INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(0, '', '', '', '', '', ''),
(1, 'Siegel der Heilung', 'Ihr heilt Euch um 82..172 Lebenspunkte. Während diese Fertigkeit benutzt wird, habt Ihr -40 Rüstung.', 'Heilt Euch um 82...172 Lebenspunkte. <i>Während Ihr diese Fertigkeit benutzt, habt Ihr -40 Rüstung.</i>', '', '', ''),
(2, 'Siegel der Wiederbelebung', 'Das als Ziel ausgewählte Gruppenmitglied wird wiederbelebt. Dieses Gruppenmitglied kehrt mit 100% seiner Lebenspunkte und 25% seiner Energiepunkte ins Leben zurück. Dieses Siegel wird nur dann wieder aufgeladen, wenn Ihr einen Moralschub erhaltet.', 'Das Gruppenmitglied wird wiederbelebt (100% Lebenspunkte, 25% Energiepunkte). <i>Dieses Siegel wird nur dann wieder aufgeladen, wenn Ihr einen Moralschub erhaltet.</i>', '', '', ''),
(3, 'Siegel der Erbeutung', 'Wählt eine Fertigkeit von einem toten Boss Eurer Klasse in der Nähe. Siegel der Erbeutung wird dauerhaft mit dieser Fertigkeit ersetzt. Wenn es sich dabei um eine Elite-Fertigkeit handelt, erhaltet Ihr pro Stufe, die Ihr erlangt habt, 250 EP.', 'Wählt eine Fertigkeit von einem toten Boss Eurer Klasse in der Nähe. Siegel der Erbeutung wird dauerhaft durch diese Fertigkeit ersetzt. Handelt es sich um eine Elite-Fertigkeit, erhaltet Ihr pro Stufe, die Ihr erlangt habt, 250 EP.', '', '', ''),
(5, 'Kraftblock', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird dieser unterbrochen und diese Fertigkeit und alle Fertigkeiten desselben Attributs 1..12 Sekunden lang deaktiviert.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Diese Fertigkeit und alle Fertigkeiten ihres Attributs werden für den Gegner 1...12 Sekunden lang deaktiviert.', '', '', ''),
(6, 'Mantra der Erde', 'Wann immer Ihr Erdschaden erleidet, ist der Schaden für einen Zeitraum von 30..90 Sekunden um 26..50% reduziert und Ihr erhaltet 2 Energiepunkte.', '30...90 Sekunden: Reduziert den von Euch erlittenen Erdschaden um 26...50%. Ihr erhaltet 2 Energiepunkte, wenn Ihr Erdschaden erleidet.', '', '', ''),
(7, 'Mantra des Feuers', 'Wann immer Ihr Feuerschaden erleidet, ist der Schaden für einen Zeitraum von 30..90 Sekunden um 26..50% reduziert und Ihr erhaltet 2 Energiepunkte.', '30...90 Sekunden: Reduziert den von Euch erlittenen Feuerschaden um 26...50%. Ihr erhaltet 2 Energiepunkte, wenn Ihr Feuerschaden erleidet.', '', '', ''),
(8, 'Mantra des Frosts', 'Wann immer Ihr Kälteschaden erleidet, ist der Schaden für einen Zeitraum von 30..90 Sekunden um 26..50% reduziert und Ihr erhaltet 2 Energiepunkte.', '30...90 Sekunden: Reduziert den von Euch erlittenen Kälteschaden um 26...50%. Ihr erhaltet 2 Energiepunkte, wenn Ihr Kälteschaden erleidet.', '', '', ''),
(9, 'Mantra des Blitzes', 'Wann immer Ihr Blitzschaden erleidet, ist der Schaden für einen Zeitraum von 30..90 Sekunden um 26..50% reduziert und Ihr erhaltet 2 Energiepunkte.', '30...90 Sekunden: Reduziert den von Euch erlittenen Blitzschaden um 26...50%. Ihr erhaltet 2 Energiepunkte, wenn Ihr Blitzschaden erleidet.', '', '', ''),
(10, 'Gebrochene Verhexung', 'Wenn Ihr innerhalb der nächsten 5..80 Sekunden das nächste Mal Ziel einer Verhexung seid, wird deren Verhexungseffekt nicht aufgetragen, der Wirker erleidet 10..46 Punkte Schaden und Gebrochene Verhexung endet. Gebrochene Verhexung verhindert keine Anfangswirkungen von Verhexungen.', '5...80 Sekunden: Die nächste Verhexung gegen Euch misslingt und der Wirker erleidet 10...46 Punkte Schaden.', '', '', ''),
(11, 'Verzerrung', 'Ihr habt 1..5 Sekunden lang eine 75%ige Chance, Angriffe zu blocken. Immer, wenn Ihr auf diese Weise einen Angriff blockt, verliert Ihr 2 Energiepunkte oder Verzerrung endet.', '1...5 Sekunden: Ihr habt eine 75%ige Chance zu blocken. <i>Blockkosten: Ihr verliert 2 Energiepunkte oder Verzerrung endet.</i>', '', '', ''),
(13, 'Mantra der Erholung', 'Die von Euch gewirkten Zauber werden 5..20 Sekunden lang um 33% schneller wieder aufgeladen.', '5...20 Sekunden: Die von Euch gewirkten Zauber werden um 33% schneller aufgeladen.', '', '', ''),
(14, 'Mantra des Bestehens', '5..25 Sekunden lang halten sämtliche von Euch gewirkten Illusionsmagie-Verhexungen um 10..40% länger an.', '5...25 Sekunden: Von Euch gewirkte Illusions-Verhexungen halten um 10...40% länger an.', '', '', ''),
(15, 'Mantra der Inschriften', '5..25 Sekunden lang werden Siegel, die Ihr erfolgreich aktiviert, während Ihr Euch in dieser Haltung befindet, um 10..40% schneller wieder aufgeladen.', '5...25 Sekunden: Erfolgreich in dieser Haltung aktivierte Siegel werden um 10...40% schneller aufgeladen.', '', '', ''),
(16, 'Mantra der Konzentration', '1..38 Sekunden lang werdet Ihr das nächste Mal, wenn Ihr sonst unterbrochen würdet, nicht unterbrochen.', '1...38 Sekunden: Das nächste Mal, wenn Ihr sonst unterbrochen würdet, werdet Ihr nicht unterbrochen.', '', '', ''),
(17, 'Mantra des Abschlusses', 'Ihr könnt 30..90 Sekunden lang nicht unterbrochen werden, aber jedes Mal, wenn Ihr normalerweise unterbrochen würdet, verliert Ihr 10..4 Punkte Energie oder Mantra des Abschlusses endet.', '30...90 Sekunden: Verhindert Unterbrechungen gegen Euch. <i>Verhinderungskosten: Ihr verliert 10...4 Energiepunkte oder Mantra des Abschlusses endet.</i>', 'Mantra des Abschlusses (PvP)', 'Ihr könnt 5 Sekunden lang nicht unterbrochen werden, aber jedes Mal, wenn Ihr normalerweise unterbrochen würdet, verliert Ihr 10..4 Energiepunkte  oder Mantra des Abschlusses endet.', '5 Sekunden: Verhindert Unterbrechungen gegen Euch. <i>Verhinderungskosten: Ihr verliert 10...4 Energiepunkte oder Mantra des Abschlusses endet.</i>'),
(18, 'Mantra der Siegel', '10..40 Sekunden lang habt Ihr +3 Rüstung für jedes ausgerüstete Siegel. Wenn Ihr ein Siegel einsetzt, heilt Ihr Euch um 5..60 Lebenspunkte.', '(10...40 Sekunden.) Ihr habt +3 Rüstung für jedes Siegel. Ihr heilt Euch jedes Mal, wenn Ihr ein Siegel einsetzt, um 5...60 Lebenspunkte.', 'Mantra der Siegel (PvP)', 'Während der nächsten 10..40 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr ein Siegel einsetzt, 5..60 Lebenspunkte.', '(10...25 Sekunden.) Ihr erhaltet jedes Mal, wenn Ihr ein Siegel einsetzt 5...60 Lebenspunkte.'),
(19, 'Zerbrechlichkeit', '8..20 Sekunden lang erleiden der Gegner und umstehende Feinde jedes Mal, wenn er unter einem neuen Zustand leidet oder sich davon erholt, 5..20 Punkte Schaden.', 'Verhext außerdem den Gegner und umstehende Feinde (8...20 Sekunden). Diese Gegner erleiden jedes Mal, wenn sie unter einem neuen Zustand leiden oder sich davon erholen, 5...20 Punkte Schaden.', 'Zerbrechlichkeit (PvP)', '8..20 Sekunden lang erleidet der Gegner jedes Mal, wenn er unter einem neuen Zustand leidet oder sich davon erholt, 5..20 Punkte Schaden.', '(8...20 Sekunden) Der Gegner erleidet jedes Mal, wenn er unter einem neuen Zustand leidet oder sich davon erholt, 5...20 Punkte Schaden.'),
(21, 'Inspirierte Verzauberung', 'Eine Verzauberung wird vom Gegner entfernt und Ihr erhaltet 3..15 Energiepunkte. Inspirierte Verzauberung wird 20 Sekunden lang durch die dem Gegner genommene Verzauberung ersetzt.', 'Entfernt beim Gegner eine Verzauberung. Entfernungswirkung: Ihr erhaltet 3...15 Energiepunkte; dieser Zauber wird durch die entfernte Verzauberung ersetzt (20 Sekunden).', '', '', ''),
(22, 'Inspirierte Verhexung', 'Eine Verhexung wird von einem Verbündeten (bzw. Euch selbst) entfernt und Ihr erhaltet 4..10 Energiepunkte. Inspirierte Verhexung wird 20 Sekunden lang durch die entfernte Verhexung ersetzt.', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung. Entfernungswirkung: Ihr erhaltet 4...10 Energiepunkte; dieser Zauber wird durch die entfernte Verhexung ersetzt (20 Sekunden).', '', '', ''),
(23, 'Kraftdorn', 'Wenn der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird die Fertigkeit unterbrochen und der Zielgegner erleidet 30..120 Punkte Schaden.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Richtet 30...120 Punkte Schaden an.', '', '', ''),
(24, 'Kraftleck', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird dieser unterbrochen und der Gegner verliert 3..17 Energiepunkte.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Lässt 3...17 Energiepunkte verlieren.', '', '', ''),
(25, 'Kraftentzug', 'Wenn der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird dieser unterbrochen und Ihr erhaltet 1..31 Energiepunkte.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Ihr erhaltet 1...31 Energiepunkte.', '', '', ''),
(26, 'Empathie', '5..15 Sekunden lang erleidet der Gegner bei jedem seiner Angriffe 10..55 Punkte Schaden und verursacht 1..15 Punkte weniger Schaden.', '5...15 Sekunden: Gegner erleidet bei jedem seiner Angriffe 10...55 Punkte Schaden und fügt 1...15 Punkte weniger Schaden zu.', 'Empathie (PvP)', '5..15 Sekunden lang erleidet der Gegner bei jedem seiner Angriffe 15..45 Punkte Schaden.', '5...15 Sekunden: Gegner erleidet bei jedem seiner Angriffe 15...45 Punkte Schaden.'),
(27, 'Täuschungen brechen', 'Entfernt eine Mesmer-Verhexung vom Gegner. Wird eine Verhexung entfernt, nehmen dieser Gegner und alle umstehenden Gegner 15..75 Punkte Schaden.', 'Entfernt beim Gegner eine Mesmer-Verhexung. Entfernungswirkung: Richtet beim Ziel und allen umstehenden Gegnern 15...75 Punkte Schaden an.', 'Täuschungen brechen (PvP)', 'Entfernt eine Mesmer-Verhexung vom Gegner. Wird eine Verhexung entfernt, nehmen dieser Gegner und alle umstehenden Gegner 15..75 Punkte Schaden.', 'Entfernt beim Gegner eine Mesmer-Verhexung. Entfernungseffekt: richtet<sub>sic</sub> beim Gegner und allen umstehenden Feinden 15...75 Punkte Schaden an.'),
(28, 'Rückschlag', '10 Sekunden lang erleidet der Gegner für jeden gewirkten Zauber 35..140 Schaden.', '10 Sekunden: Gegner erleidet bei jedem seiner Zauber 35...140 Punkte Schaden.', '', '', ''),
(29, 'Blackout', '2..6 Sekunden lang werden alle Fertigkeiten des berührten Gegners deaktiviert und alle Eure eigenen Fertigkeiten werden 5 Sekunden lang deaktiviert.', '2...6 Sekunden: Deaktiviert Fertigkeiten. <i>Eure Fertigkeiten werden deaktiviert (5 Sekunden).</i>', '', '', ''),
(30, 'Ablenkung', 'Wenn der Gegner innerhalb der nächsten 6 Sekunden eine Fertigkeit verwendet, benötigt sie zur Wiederaufladung 10..56 Sekunden länger.', '6 Sekunden: Nächste benutzte Fertigkeit des Gegners benötigt +10...56 Sekunden zum Wiederaufladen.', '', '', ''),
(31, 'Trugbild beschwören', '2..16 Sekunden lang unterliegt der Gegner einer Lebenspunktedegeneration von -5.', '2...16 Sekunden: Verursacht -5 Lebenspunktdegeneration.', '', '', ''),
(32, 'Illusion der Schwäche', 'Ihr verliert 51..241 Lebenspunkte. Illusion der Schwäche endet, wenn der Schaden Eure Lebenspunkte unter 25% Eurer maximalen Lebenspunkte bringt. Wenn Illusion der Schwäche endet, heilt Ihr um 50..240 Lebenspunkte.', 'Ihr verliert 51...241 Lebenspunkte. Wirkung am Ende: Ihr heilt um 50...240 Lebenspunkte. <i>Endet, wenn Eure Lebenspunkte durch Schaden unter 25% Eurer maximalen Lebenspunkte gebracht werden.</i>', '', '', ''),
(33, 'Illusion der Waffen', '30 Sekunden lang sind Eure Nahkampfangriffe weder erfolgreich noch erfolglos. Stattdessen verursacht Illusion der Waffen bei jedem Nahkampfangriff 8..40 Punkte Schaden an Euren Gegnern. Ihr habt +5 Rüstung pro ausgerüsteter Illusionsmagie-Fertigkeit.', '(30 Sekunden.) Richtet anstatt von Schaden oder Effekten aus Nahkampfangriffen 8...40 Punkte Schaden an. Ihr habt +5 Rüstung pro ausgerüsteter Illusionsmagie-Fertigkeit. <i>Eure Nahkampfangriffe sind weder erfolgreich noch erfolglos.</i>', 'Illusion der Waffen (PvP)', 'Illusion der Waffen verursacht 30 Sekunden bei jedem Nahkampfangriff 8..40 Schaden an Gegnern. Während diese Fertigkeit aktiv ist, können Eure Nahkampfangriffe weder treffen noch fehlschlagen oder nicht treffen, sie verursachen keinen Schaden und die Effekte von Waffen oder Angriffsfertigkeiten werden nicht auf Gegner angewendet.', '(30 Sekunden.) Illusion der Waffen verursacht bei jedem Nahkampfangriff 8...40 Schaden an den Gegnern. <i>Eure Nahkampfangriffe können weder treffen noch fehlschlagen oder nicht treffen. Eure Nahkampfangriffe verursachen keinen Schaden. Effekte Eurer Waffen oder Angriffsfertigkeiten werden nicht auf Gegner angewendet.</i>'),
(34, 'Sympathisches Gesicht', '4..10 Sekunden lang verlieren alle umstehenden Feinde jedes Mal, wenn der Verbündete (bzw. Ihr selbst) im Nahkampf getroffen wird, sämtliches Adrenalin sowie 3 Energiepunkte.', '4...10 Sekunden: Alle umstehenden Feinde verlieren jedes Mal sämtliches Adrenalin und 3 Energiepunkte, wenn ein Nahkampfangriff den Verbündeten (bzw. Euch selbst) trifft.', '', '', ''),
(35, 'Ignoranz', '8..20 Sekunden lang kann der Zielgegner keine Siegel benutzen.', '8...20 Sekunden: Gegner kann keine Siegel benutzen.', '', '', ''),
(36, 'Arkanes Rätsel', '5..15 Sekunden lang brauchen der Gegner und alle umstehenden Feinde doppelt so lange, um ihre Zauber zu wirken. Wenn diese Verhexung endet, erhaltet Ihr 1..7 Energiepunkte.', 'Verhext den Gegner und umstehende Feinde (5...15 Sekunden). Verdoppelt Wirkzeit von Zaubern. Wirkung am Ende: Ihr erhaltet 1...7 Energiepunkte', '', '', ''),
(37, 'Illusion der Eile', '5..11 Sekunden lang seid Ihr nicht mehr verkrüppelt und bewegt Euch um 33% schneller. Endet Illusion der Eile, werdet Ihr 3 Sekunden lang in den Zustand Verkrüppelung versetzt.', '(5...11 Sekunden.) Ihr bewegt Euch um 33% schneller. Anfangswirkung: Beseitigt Zustand der Verkrüppelung. <i>Wirkung am Ende: Ihr werdet (3 Sekunden) lang in den Zustand der Verkrüppelung versetzt.</i>', 'Illusion der Eile (PvP)', 'Ihr bewegt euch für 5..11 Sekunden 33% schneller als normal. Wenn Illusion der Eile endet, werdet Ihr 3 Sekunden lang in den Zustand Verkrüppelung versetzt.', '(5...11 Sekunden.) Ihr bewegt Euch um 33% schneller. <i>Wirkung am Ende: Ihr werdet (3 Sekunden) lang in den Zustand der Verkrüppelung versetzt.</i>'),
(38, 'Kanalisieren', '8..56 Sekunden lang erhaltet Ihr für jeden Gegner in dem Bereich jeweils einen Energiepunkt, wann immer Ihr einen Zauber wirkt.', '8...56 Sekunden: Ihr erhaltet jedes Mal 1 Energiepunkt pro Gegner im Bereich, wenn Ihr einen Zauber wirkt.', '', '', ''),
(39, 'Energiewelle', 'Der Zielgegner verliert 1..10 Punkte Energie. Für jeden verlorenen Energiepunkt erleiden dieser und alle Feinde in der Nähe 9 Punkte Schaden.', 'Lässt 1...10 Energiepunkt verlieren. Richtet beim Gegner und Feinden in der Nähe pro verlorenem Energiepunkt 9 Punkte Schaden an.', '', '', ''),
(40, 'Ätherschmaus', 'Der Gegner verliert 3 Punkte Energie. Für jeden verlorenen Punkt Energie heilt Ihr Euch um 20..65 Lebenspunkte.', 'Lässt 3 Energiepunkte verlieren. Ihr heilt Euch pro verlorenem Energiepunkt um 20...65 Lebenspunkte.', '', '', ''),
(41, 'Herr über den Äther', 'Ihr verliert Eure gesamte Energie. 5..10 Sekunden lang unterliegt der Gegner einer Energiedegeneration von -1..3 und Ihr erfreut Euch einer Energieregeneration von 1..3.', '<i>Ihr verliert alle Energiepunkte.</i> Gegner hat -1...3 Energiedegeneration und Ihr habt +1...3 Energieregeneration. (5...10 Sekunden).', '', '', ''),
(42, 'Energiebrand', 'Der Zielgegner verliert 1..10 Punkte Energie. Für jeden verlorenen Energiepunkt erleidet dieser jeweils 9 Punkte Schaden.', 'Lässt 1...10 Energiepunkte verlieren. Richtet pro verlorenem Energiepunkt 9 Punkte Schaden an.', '', '', ''),
(43, 'Ungeschick', '4 Sekunden lang werden der Gegner und umstehende Feinde mit Ungeschick verhext. Beim nächsten Angriff jedes Feindes wird der Angriff unterbrochen und der Feind erleidet 10..92 Punkte Schaden.', '4 Sekunden: Verhext auch umstehende Feinde. Unterbricht nächsten Angriff. Unterbrechungswirkung: Richtet 10...92 Punkte Schaden an.', '', '', ''),
(44, 'Phantomschmerz', '10 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..4. Wenn Phantomschmerz endet, erhält dieser Gegner eine Tiefe Wunde, die seine maximalen Lebenspunkte 5..20 Sekunden lang um 20% reduziert.', '10 Sekunden: Verursacht -1...4 Lebenspunktdegeneration. Wirkung am Ende: Verursacht Zustand Tiefe Wunde (5...20 Sekunden).', '', '', ''),
(45, 'Ätherische Bürde', '10 Sekunden lang bewegt sich der Zielgegner um 50% langsamer. Wenn Ätherische Bürde endet, erhaltet Ihr 10..18 Energiepunkte.', '10 Sekunden: Gegner bewegt sich um 50% langsamer. Wirkung am Ende: Ihr erhaltet 10...18 Energiepunkte.', '', '', ''),
(46, 'Schuld', '6 Sekunden lang schlägt der nächste Zauber des Gegners auf einen seiner Feinde fehl und Ihr entzieht dem Widersacher bis zu 5..14 Energiepunkte.', '6 Sekunden: Nächster Zauber des Gegners schlägt fehl und Ihr entzieht ihm 5...14 Energiepunkte. <i>Zauber des Gegners muss auf einen seiner Feinde gerichtet sein, sonst keine Wirkung.</i>', '', '', ''),
(47, 'Unbeholfenheit', '4 Sekunden lang erleidet der Gegner und alle umstehenden Gegner bei ihrem nächsten Angriff 30..135 Punkte Schaden und werden 10 Sekunden lang geblendet.', '(4 Sekunden.) Außerdem werden alle umstehenden Feinde verhext. Richtet 30...135 Punkte Schaden an. Verursacht Zustand Blindheit (10 Sekunden). <i>Kein Effekt, wenn der verhexte Gegner nicht angreift.</i>', '', '', ''),
(48, 'Geist des Versagens', '30 Sekunden lang besteht eine 25%ige Wahrscheinlichkeit, dass der Gegner bei seinen Angriffen nicht trifft. Ihr erhaltet für jeden misslungenen Angriff 1..3 Energiepunkte.', '30 sekunden: Die Angriffe des Gegners gehen mit 25%iger Wahrscheinlichkeit daneben. Ihr erhaltet für jeden misslungenen Angriff des Gegners 1...3 Energiepunkte.', '', '', ''),
(49, 'Geistiges Wrack', 'Der Gegner wird 5..40 Sekunden mit Geistiges Wrack verhext. Ist der Gegner das Ziel einer Eurer Mesmer-Fertigkeiten, die keine Verhexung ist, verliert er 1 Energiepunkt und erleidet 5..25 Schadenspunkte pro verlorenem Energiepunkt. Sinken die Energiepunkte des Gegners auf 0, erleidet er 15..100 Punkte Schaden und Geistiges Wrack endet.', '5...40 Sekunden: Der Gegner verliert 1 Energiepunkt, wenn er Ziel Eurer Mesmer-Fertigkeiten (keine Verhexung) ist. Fügt 5...25 Punkte Schaden pro verlorenem Energiepunkt zu. Sinken die Energiepunkte des Gegners auf 0, erleidet er 15...100 Punkte Schaden und Geistiges Wrack endet.', 'Geistiges Wrack (PvP)', 'Der Gegner wird 5..40 Sekunden mit Geistiges Wrack verhext. Ist der Gegner das Ziel einer Eurer Mesmer-Fertigkeiten, die keine Verhexung ist, verliert er 1 Energiepunkt. Sinken die Energiepunkte des Gegners auf 0, erleidet er 15..100 Punkte Schaden und Geistiges Wrack endet.', '(5...40 Sekunden.) Der Gegner verliert 1 Energiepunkt, wenn er Ziel Eurer Mesmer-Fertigkeiten (keine Verhexung) ist. Sinken die Energiepunkte des Gegners auf 0, erleidet er 15...100 Punkte Schaden und Geistiges Wrack endet.'),
(50, 'Verschwendung', 'Nach 3 Sekunden erleidet der Gegner und alle umstehende Feinden 20..100 Schaden. Setzt der Gegner erfolgreich eine Fertigkeit ein, endet Verschwendung vorzeitig und verursacht keinen Schaden.', '(3 Sekunden.) Wirkung am Ende: richtet beim Gegner und umstehenden Feinden 20...100 Punkte Schaden an. <i>Kein Effekt und endet vorzeitig, wenn der Gegner eine Fertigkeit einsetzt.</i>', '', '', ''),
(51, 'Schande', '6 Sekunden lang schlägt der nächste Zauber des Gegners auf einen seiner Verbündeten fehl und Ihr entzieht dem Widersacher bis zu 5..14 Energiepunkte.', '6 Sekunden: Nächster Zauber des Gegners schlägt fehl und Ihr entzieht ihm 5...14 Energiepunkte. <i>Zauber des Gegners muss auf einen seiner Verbündeten gerichtet sein, sonst keine Wirkung.</i>', '', '', ''),
(52, 'Panik', '1..10 Sekunden lang wirkt die Verhexung Panik auf den Gegner und alle Gegner in der Nähe. Verwendet ein mit Panik verhexter Gegner eine Fertigkeit erfolgreich, unterbricht Panik die Aktionen aller Feinde in der Nähe.', 'Verhext außerdem Feinde in der Nähe des Gegners (1...10 Sekunden). Unterbricht die Aktion aller Gegner in der Nähe, wenn ein verhexter Gegner eine Fertigkeit erfolgreich aktiviert.', '', '', ''),
(53, 'Migräne', '5..20 Sekunden lang unterliegt der Gegner einer Lebenspunktedegeneration von 1..8 und benötigt 100% länger, um eine Fertigkeit zu aktivieren.', '(5...20 Sekunden.) Verursacht -1...8 Lebenspunktdegeneration und verdoppelt Aktivierungszeit von Fertigkeiten.', 'Migräne (PvP)', '3..12 Sekunden lang unterliegt der Gegner einer Lebenspunktedegeneration von 1..8 und braucht 100% länger, um Fertigkeiten zu aktivieren.', '(3...12 Sekunden.) Verursacht -1...8 Lebenspunktdegeneration und verdoppelt die Aktivierungszeit von Fertigkeiten.'),
(54, 'Verkrüppelnde Qual', '5..20 Sekunden lang sind Bewegung und Angriffe des Ziels um 50% langsamer. Der Gegner unterliegt außerdem einer Lebenspunktdegeneration von -1..8.', '(5...20 Sekunden.) Bewegungen und Angriffe des Gegners sind um 50% langsamer und er hat -1...8 Lebenspunktdegeneration.', 'Verkrüppelnde Qual (PvP)', '5..20 Sekunden lang bewegt sich das Ziel um 50% langsamer und unterliegt einer Lebenspunktdegeneration von -1..5.', '5...20 Sekunden: Bewegungen des Gegners sind um 50% langsamer und er hat -1...5 Lebenspunktdegeneration.'),
(55, 'Fieberträume', '10..25 Sekunden lang leiden jedes Mal, wenn der Gegner unter einem neuen Zustand leidet, alle Feinde in dem Bereich ebenfalls unter diesem. Wenn der Gegner mit zwei oder mehr Zuständen belegt ist, wird er für 1..3 Sekunden benommen sein.', '10...25 Sekunden: Alle neuen Zustände, die der Gegner erhält, gehen auch auf Feinde im Bereich über. Wenn der Gegner mit zwei oder mehr Zuständen belegt ist, wird er für 1...3 Sekunden benommen sein.', 'Fieberträume (PvP)', '5..15 Sekunden lang leiden jedes Mal, wenn der Gegner unter einem Zustand leidet, alle Feinde in dem Bereich ebenfalls unter diesem.', '5...15 Sekunden: Alle neuen Zustände, die der Gegner erhält, gehen auch auf Feinde im Bereich über.'),
(56, 'Beruhigende Bilder', '8..20 Sekunden lang können der Zielgegner und alle umstehenden Gegner kein Adrenalin bekommen.', 'Verhext den Gegner und umstehende Feinde (8...20 Sekunden). Die Betroffenen können kein Adrenalin erhalten.', '', '', ''),
(57, 'Schrei der Frustration', 'Falls der Gegner eine Fertigkeit benutzt, werden er und alle Feinde in dem Bereich bei ihrer Aktion unterbrochen und erleiden 15..75 Punkte Schaden.', 'Benutzt der Gegner eine Fertigkeit, werden er und alle Feinde im Bereich bei ihrer Aktion unterbrochen und erleiden 15...75 Punkte Schaden.', '', '', ''),
(58, 'Siegel der Mitternacht', 'Ihr werdet gemeinsam mit dem berührten Gegner 15 Sekunden lang geblendet.', '15 Sekunden: Verursacht Zustand Blindheit. <i>Ihr leidet an Blindheit (15 Sekunden).</i>', '', '', ''),
(59, 'Siegel der Müdigkeit', 'Der Gegner und alle Feinde in der Nähe verlieren 3..8 Energiepunkte und leiden 1..12 Sekunden lang an Schwäche.', 'Wirkt auch auf Feinde in der Nähe des Gegners. Verursacht einen Verlust von 3...8 Energiepunkten und den Zustand Schwäche (1...12 Sekunden).', '', '', ''),
(61, 'Blutsauger-Siegel', 'Unterbricht die Aktion des Gegners. Falls diese Aktion ein Zauber war, bekommt Ihr 3..15 Energiepunkte.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Ihr erhaltet 3...15 Energiepunkte, falls die Aktion ein Zauber war.', '', '', ''),
(62, 'Siegel der Demut', 'Die Elite-Fertigkeit des Gegners wird 1..16 Sekunden lang deaktiviert. Eure Nicht-Mesmer-Fertigkeiten werden 10 Sekunden lang deaktiviert.', 'Deaktiviert Elite-Fertigkeit (1...16 Sekunde). <i>Deaktiviert Eure Nicht-Mesmer-Fertigkeiten (10 Sekunden).</i>', '', '', ''),
(63, 'Schlussstein-Siegel', 'Alle Eure Siegel außer dem Schlussstein-Siegel werden wieder aufgeladen. Wenn Ihr in den nächsten 20 Sekunden die nächsten 0..6 Mal ein Siegel gegen einen Gegner einsetzt, erleiden alle anderen umstehenden Feinde 15..60 Punkte Schaden und werden unterbrochen.', '(20 Sekunden.) Eure nächsten 0...6 Siegel unterbrechen die den Gegner umstehenden Feinde und fügen ihnen 15...60 Punkte Schaden zu. Anfangswirkung: lädt alle anderen Siegel wieder auf.', '', '', ''),
(65, 'Arkane Mimikry', '20 Sekunden lang wird Arkane Mimikry die Elite-Fertigkeit des Verbündeten, mit der er sich nicht verwandelt.', 'Diese Fertigkeit wird zur Elite-Fertigkeit des Verbündeten (20 Sekunden). <i>Sich selbst als Ziel wählen nicht möglich. Keine Wirkung, wenn Elite-Fertigkeit des Verbündeten eine Verwandlung ist.</i>', '', '', ''),
(66, 'Geistfesseln', '5..20 Sekunden lang verliert der Gegner bei jedem Angriff 5 Energiepunkte.', '5...20 Sekunden: Gegner verliert bei jedem seiner Angriffe 5 Energiepunkte.', '', '', ''),
(67, 'Verhexung brechen', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). Beim Entfernen einer Verhexung erleiden alle Gegner in der Nähe dieses Verbündeten 30..120 Punkte Schaden.', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung. Entfernungswirkung: Richtet bei Feinden in der Nähe 30...120 Punkte Schaden an.', '', '', ''),
(68, 'Verzauberung entziehen', 'Entfernt eine Verzauberung vom Gegner. Beim Entfernen einer Verzauberung bekommt Ihr 8..17 Energiepunkte und heilt Euch um 40..120 Lebenspunkte.', 'Entfernt beim Gegner eine Verzauberung. Entfernungswirkung: Ihr erhaltet 8...17 Energiepunkte und heilt Euch um 40...120 Lebenspunkte.', '', '', ''),
(69, 'Verzauberung brechen', 'Entfernt eine Verzauberung vom Gegner. Beim Entfernen einer Verzauberung erleidet dieser 14..100 Punkte Schaden.', 'Entfernt beim Gegner eine Verzauberung. Entfernungswirkung: Richtet 14...100 Punkte Schaden an.', '', '', ''),
(72, 'Elementarwiderstand', '30..90 Sekunden lang bekommt Ihr +40 Rüstung gegen Elementarschaden, verliert jedoch 24..12 Rüstung gegen physischen Schaden.', '30...90 Sekunden: Ihr habt +40 Rüstung gegen Elementarschaden. <i>Ihr habt -24...12 Rüstung gegen physischen Schaden.</i>', '', '', ''),
(73, 'Körperlicher Widerstand', '30..90 Sekunden lang bekommt Ihr +40 Rüstung gegen physischen Schaden, verliert jedoch 24..12 Rüstung gegen Elementarschaden.', '30...90 Sekunden: Ihr habt +40 Rüstung gegen physischen Schaden. <i>Ihr habt -24...12 Rüstung gegen Elementarschaden.</i>', '', '', ''),
(74, 'Echo', '30 Sekunden lang wird Echo durch die nächste von Euch benutzte Fertigkeit ersetzt. Echo wirkt 30 Sekunden lang wie diese Fertigkeit.', '30 Sekunden: Echo wird zur nächsten von Euch benutzten Fertigkeit (30 Sekunden).', '', '', ''),
(75, 'Arkanes Echo', 'Falls Ihr in den nächsten 20 Sekunden einen Zauber wirkt, wird Arkanes Echo 20 Sekunden lang durch diesen Zauber ersetzt. Arkanes Echo endet vorzeitig, wenn Ihr eine Fertigkeit benutzt, die kein Zauber ist.', '20 Sekunden: Arkanes Echo wird zum nächsten von Euch benutzten Zauber (20 Sekunden). <i>Diese Verzauberung endet, wenn Ihr eine Fertigkeit benutzt, die kein Zauber ist.</i>', '', '', ''),
(76, 'Eingebildete Bürde', '8..20 Sekunden lang bewegt sich der Zielgegner um 50% langsamer als normal.', '8...20 Sekunden: Gegner bewegt sich um 50% langsamer.', '', '', ''),
(77, 'Chaossturm', 'An der Position des Gegners wird ein Chaossturm erzeugt, der 10 Sekunden lang anhält. Umstehende Feinde erleiden pro Sekunde 5..25 Punkte Schaden und verlieren 0..2 Energiepunkte.', 'Fügt pro Sekunde 5...25 Punkte Schaden zu und verursacht einen Verlust von 0...2 Energiepunkten (10 Sekunden). Trifft umstehende Feinde um anfängliche Position des Ziels.', '', '', ''),
(78, 'Epidemie', 'Verteilt alle negativen Zustände und ihre verbleibende Dauer vom Gegner auf alle ihn umstehenden Feinde.', 'Zustände des Gegners übertragen sich auf umstehende Feinde.', '', '', ''),
(79, 'Energieentzug', 'Der Gegner verliert 2..9 Punkte Energie. Ihr erhaltet für jeden entzogenen Energiepunkt 3 Punkte Energie.', 'Lässt 2...9 Energiepunkte verlieren. Ihr erhaltet pro verlorenem Energiepunkt 3 Energiepunkte.', '', '', ''),
(80, 'Energie abzapfen', 'Der Gegner verliert 4..7 Energiepunkte. Ihr erhaltet für jeden verlorenen Energiepunkt 2 Energiepunkte.', 'Lässt 4...7 Energiepunkte verlieren. Ihr erhaltet pro verlorenem Energiepunkt 2 Energiepunkte.', '', '', ''),
(81, 'Arkaner Diebstahl', '5..35 Sekunden lang wird ein zufällig gewählter Zauber des Gegners deaktiviert und Arkaner Diebstahl wird durch diesen Zauber ersetzt.', '5...35 Sekunden: Deaktiviert einen willkürlichen Zauber. Diese Fertigkeit wird zum betreffenden Zauber.', '', '', ''),
(82, 'Mantra der Zurückrufung', '20 Sekunden lang erhaltet Ihr daraus keinen Vorteil. Allerdings erhaltet Ihr, wenn Mantra der Zurückrufung endet, 10..25 Energiepunkte.', '20 Sekunden: Wirkung am Ende: Ihr erhaltet 10...25 Energiepunkte.', '', '', ''),
(83, 'Knochenschreck beleben', 'Der nächste Kadaver wird als Knochenschreck der Stufe 1..17 belebt.', 'Erstellt einen Knochenschreck der Stufe 1...17. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(84, 'Knochenteufel beleben', 'Der nächste Kadaver wird als Knochenteufel der Stufe 1..17 belebt. Knochenteufel können aus der Ferne angreifen.', 'Erstellt einen Knochenteufel der Stufe 1...17, der aus der Ferne angreifen kann. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(85, 'Knochendiener beleben', 'Der nächste Kadaver wird als zwei Knochendiener der Stufe 0..12 belebt.', 'Erstellt zwei Knochendiener der Stufe 0...12. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(86, 'Grenths Ausgleich', 'Wenn der Gegner mehr Lebenspunkte als Ihr hat, erhaltet Ihr die Hälfte der Differenz (bis zu Euren maximalen Lebenspunkten) und der Gegner verliert die gleiche Punktzahl. Falls dieser Gegner weniger Lebenspunkte hat als Ihr, verliert Ihr die Hälfte der Differenz und er erhält die gleiche Menge.', 'Ihr erhaltet die Hälfte der Differenz zwischen den Lebenspunkten des Gegners und Euren Lebenspunkten. Der Gegner verliert dieselbe Punktzahl. <i>Falls der Gegner weniger Lebenspunkte hat als Ihr, verliert Ihr die Hälfte der Differenz und er erhält die gleiche Menge.</i>', '', '', ''),
(87, 'Veratas Starren', 'Hat der als Ziel ausgewählte feindliche Untote einen Meister, wird seine Verbindung zu diesem Meister aufgehoben, womit er alle anderen Kreaturen als Feind betrachtet. Falls der Untote keinen Meister hatte, werdet Ihr zu seinem Meister. (Bei einer Todesmagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', 'Macht das Ziel zum herrenlosen untoten Diener. Falls es schon herrenlos ist, werdet Ihr sein Meister. <i>Mindestens Todesmagie 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(88, 'Veratas Aura', 'Alle belebten feindlichen Untoten in dem Gebiet werden an Euch gebunden. Nach 120..300 Sekunden verliert Veratas Aura seine Wirkung. Wenn Veratas Aura endet, verliert Ihr Eure Verbindung zu allen an Euch gebundenen Untoten. (Bei einer Todesmagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', '120...300 Sekunden: Ihr werdet zum Meister aller feindlichen untoten Diener im Bereich. <i>Wirkung am Ende: Eure untoten Diener werden herrenlos. Mindestens Todesmagie Stufe 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(89, 'Tödliche Kühlung', 'Der Gegner erleidet 5..50 Punkte Kälteschaden. Sind seine Lebenspunkte über 50%, verursacht Ihr zusätzlich 5..50 Punkte Schattenschaden.', 'Richtet 5...50 Punkte Kälteschaden an. Richtet zusätzlich 5...50 Punkte Schaden an, falls der Gegner mehr als 50% seiner maximalen Lebenspunkte hatte.', '', '', ''),
(90, 'Veratas Opfer', '5..10 Sekunden lang erfahren alle Eure untoten Diener Lebenspunktregeneration von +10. Alle Zustände werden von diesen Dienern entfernt und auf Euch übertragen. Wenn dieser Zauber erfolgreich ist und Ihr und Eure Verbündeten zusammen 3 oder weniger Diener kontrolliert, wird Veratas Opfer sofort wieder aufgeladen.', '5...10 Sekunden: Von Euch kontrollierte untote Diener erhalten +10 Lebenspunktregeneration. Wird sofort wieder aufgeladen, falls Ihr und Eure Verbündeten zusammen 3 oder weniger Diener kontrollieren. <i>Überträgt alle Zustände der erstgenannten Diener auf Euch.</i>', '', '', ''),
(91, 'Brunnen der Kraft', 'Der nächste Kadaver zum ausgewählten Ziel wird an dieser Position in einen Brunnen der Kraft verwandelt. 8..20 Sekunden erhalten Verbündete, die sich im Bereich des Brunnens der Kraft aufhalten, eine Lebenspunktregeneration von 1..6 und eine zusätzliche Energieregeneration von 2 Punkten.', '(8...20 Sekunden.) Verbündete in diesem Brunnen erhalten eine Lebenspunktregeneration in Höhe von +1...6 und eine Energieregeneration von +2. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(92, 'Brunnen des Blutes', 'Der nächste Kadaver wird an seiner Position in einen Brunnen des Blutes verwandelt. 8..20 Sekunden lang erhalten Verbündete, die sich in dem Bereich aufhalten, eine Lebenspunktregeneration in Höhe von 1..6 Punkten.', '8...20 Sekunden: Verbündete in diesem Brunnen haben +1...6 Lebenspunktregeneration. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(93, 'Brunnen des Leidens', 'Der nächste Kadaver wird an seiner Position in einen Brunnen des Leidens verwandelt. 10..30 Sekunden lang erleiden alle Feinde, die sich in dem Bereich aufhalten, eine Lebenspunktdegeneration von -1..6.', '(10...30 Sekunden.) Gegner in diesem Brunnen erhalten eine Lebenspunktdegeneration in Höhe von -1...6. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(94, 'Gottloser Brunnen', 'Der nächste Kadaver wird an seiner Position in einen Gottlosen Brunnen verwandelt. 8..20 Sekunden lang werden alle Feinde, die sich in dem Bereich aufhalten, von sämtlichen Verzauberungen befreit. Außerdem können sie nicht das Ziel weiterer Verzauberungen sein. (Bei einer Todesmagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit eines Misserfolgs.)', '(8...20 Sekunden.) Gegner in diesem Brunnen verlieren alle Verzauberungen und können nicht Ziel von weiteren Verzauberungen sein. <i>Nutzt einen frischen Kadaver. Es besteht eine 50%ige Wahrscheinlichkeit eines Misserfolgs, es sei denn, Todesmagie ist 5 oder höher.</i>', '', '', ''),
(95, 'Explosion der Verwesung', 'Der Kadaver, der Eurem Ziel am nächsten ist, explodiert und sendet eine Schockwelle aus, die allen in der Nähe befindlichen Gegnern 24..120 Punkte Schaden zufügt.', 'Lässt einen Kadaver explodieren und richtet bei Gegnern in der Nähe 24...120 Punkte Schaden an. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(96, 'Seelenschmaus', 'Benutzt den Kadaver, der Euch am nächsten ist, um 50..280 Lebenspunkte zu erhalten.', 'Ihr erhaltet 50...280 Lebenspunkte. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(97, 'Nekrotische Verwechslung', 'Benutzt einen willkürlichen Kadaver, um einen Schattenschritt an seine Position zu unternehmen, und alle Feinde in dessen Nähe werden 5..20 Sekunden lang vergiftet.', 'Unternehmt einen Schattenschritt zur Position eines Kadavers. Verursacht Zustand Gift (5...20 Sekunden). Betrifft alle Feinde in der Nähe. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(98, 'Kadaver verzehren', 'Benutzt einen willkürlichen Kadaver, um einen Schattenschritt dahin zu unternehmen, und Ihr erhaltet 25..100 Lebenspunkte und 5..20 Punkte Energie.', 'Unternehmt einen Schattenschritt zur Position eines Kadavers. Ihr erhaltet 25...100 Lebenspunkte und 5...20 Energiepunkte. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(99, 'Parasitäre Fesseln', '20 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1. Wenn Parasitäre Fesseln endet, wird der Wirker um 30..120 Lebenspunkte geheilt.', '20 Sekunden: Verursacht -1 Lebenspunktdegeneration. Wirkung am Ende: Ihr erhaltet 30...120 Lebenspunkte.', '', '', ''),
(100, 'Seelenwiderhaken', '30 Sekunden lang erleidet der Gegner jedes Mal 15..30 Punkte Schaden, wenn er verzaubert oder verhext wird.', '30 Sekunden: Richtet jedes Mal 15...30 Punkte Schaden an, wenn der Gegner verzaubert oder verhext wird.', '', '', ''),
(101, 'Widerhaken', 'Der Gegner erleidet 30 Sekunden lang 1..15 Punkte mehr Schaden, wenn ihm physischer Schaden zugefügt wird.', '30 Sekunden: Gegner erleidet jedes Mal 1...15 Punkte Schaden, wenn er physischen Schaden erhält.', '', '', ''),
(102, 'Schattenschlag', 'Der Gegner erleidet 12..48 Schattenschaden. Sind seine Lebenspunkte über 50%, entzieht Ihr ihm bis zu 12..48 Lebenspunkte.', 'Richtet 12...48 Punkte Schaden an. Entzieht bis zu 12...48 Lebensunkte, falls der Gegner mehr als 50% seiner maximalen Lebenspunkte hatte.', '', '', ''),
(103, 'Preis des Versagens', '30 Sekunden lang besteht eine 25%ige Wahrscheinlichkeit, dass die Angriffe des Gegners fehlschlagen. Für jeden Fehlschlag im Kampf erleidet er 1..46 Punkte Schaden.', '30 Sekunden: Die Angriffe des Gegners gehen mit 25%iger Wahrscheinlichkeit daneben. Gegner erleidet für jeden misslungenen Angriff 1...46 Punkte Schaden.', '', '', ''),
(104, 'Todesnova', 'Falls der Verbündete (bzw. Ihr selbst) innerhalb der nächsten 30 Sekunden stirbt, erleiden alle umstehenden Feinde 26..100 Punkte Schaden und werden 15 Sekunden lang vergiftet.', '30 Sekunden: Falls der Verbündete (bzw. Ihr selbst) stirbt, erhalten umstehende Feinde 26...100 Punkte Schaden und Zustand Gift (15 Sekunden).', '', '', ''),
(105, 'Tödlicher Schwarm', 'Tödlicher Schwarm breitet sich langsam aus und fügt bis zu drei Zielen in dem Bereich 30..90 Punkte Kälteschaden zu.', 'Tödlicher Schwarm breitet sich langsam aus und richtet 30...90 Punkte Kälteschaden an. Trifft zwei weitere Feinde im Bereich.', '', '', ''),
(106, 'Verrottendes Fleisch', 'Die als Ziel ausgewählte Kreatur aus Fleisch und Blut erkrankt 10..25 Sekunden lang und verliert allmählich Lebenspunkte.', 'Verursacht Zustand Krankheit (10...25 Sekunden).', '', '', ''),
(107, 'Ansteckung', 'Falls der Gegner bereits an einem Zustand leidet, leidet er 3..15 Sekunden lang unter Krankheit, Gift und Schwäche.', 'Verursacht Zustände Krankheit, Gift und Schwäche (3...15 Sekunden). <i>Gegner muss bereits einen Zustand haben, sonst keine Wirkung.</i>', '', '', ''),
(108, 'Leiden', '6..30 Sekunden lang unterliegen der Gegner und alle in der Nähe befindlichen Feinde einer Lebenspunktdegeneration von -0..3.', 'Verhext den Gegner und Feinde in der Nähe (6...30 Sekunden). Die Betroffenen haben -0...3 Lebenspunktdegeneration.', '', '', ''),
(109, 'Leben abzapfen', '12..25 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von 1..3 und Ihr selbst erfreut Euch einer Lebenspunktregeneration von 1..3.', '12...25 Sekunden: Gegner hat -1...3 Lebenspunktdegeneration. Ihr habt 1...3 Lebenspunktregeneration.', '', '', ''),
(110, 'Unheiliger Schmaus', 'Entzieht bis zu 1..4 Gegnern in dem Bereich bis zu 10..65 Lebenspunkte.', 'Entzieht 1...4 Feinden im Bereich um Euch 10...65 Lebenspunkte.', 'Unheiliger Schmaus (PvP)', 'Entzieht bis zu 1..4 Gegnern in dem Bereich bis zu 10..65 Lebenspunkte.', 'Entzieht 1...4 Feinden im Bereich um Euch 10...65 Lebenspunkte.'),
(111, 'Das Blut erwecken', '20..44 Sekunden lang erhöht sich Euer Blutmagie- und Flücheattribut um 2, aber jedes Mal, wenn Ihr Lebenspunkte opfert, opfert Ihr 50% mehr als normal.', '20...44 Sekunden: Ihr habt +2 Blutmagie und Flüche. <i>Ihr opfert 50% mehr Lebenspunkte als normal.</i>', '', '', ''),
(112, 'Verzauberungsschändung', 'Der Zielgegner und alle in seiner Nähe befindlichen Gegner erleiden 6..60 Punkte Schattenschaden sowie zusätzlich 4..20 Punkte Schattenschaden für jede auf ihnen liegende Verzauberung.', 'Richtet beim Gegner und Feinden in der Nähe 6...60 Punkte Schaden an. Richtet zusätzlich 4...20 Punkte Schaden für jede auf ihnen liegende Verzauberung an.', '', '', ''),
(113, 'Verdorbenes Fleisch', '20..44 Sekunden lang ist der Verbündete (bzw. Ihr selbst) immun gegen Krankheit, und jeder, der ihn im Nahkampf trifft, erkrankt für 3..15 Sekunden.', '20...44 Sekunden: Feinde, die den Verbündeten (bzw. Euch selbst) im Nahkampf treffen, erkranken (3...15 Sekunden); der Verbündete (bzw. Ihr selbst) ist immun gegen Krankheit.', '', '', ''),
(114, 'Aura des Lich', 'Alle Kadaver in Hörweite werden benutzt und Ihr belebt einen Knochenschreck der Stufe 1..17 sowie einen für jeden auf diese Weise benutzten Kadaver. 5..45 Sekunden lang ist Euer Todesmagie-Attribut um +1 erhöht.', 'Benutzt alle Kadaver in Hörweite. Belebt einen Knochenschreck der Stufe 1...17 sowie einen für jeden benutzten Kadaver. Ihr habt +1 Todesmagie (5...45 Sekunden).', '', '', ''),
(115, 'Bluterneuerung', '7 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration von +3..6. Wenn Bluterneuerung endet, werdet Ihr um 40..190 Lebenspunkte geheilt.', '(7 Sekunden.) Ihr habt +3...6 Lebenspunktregeneration. Wirkung am Ende: Werdet um 40...190 Lebenspunkte geheilt.', '', '', ''),
(116, 'Dunkle Aura', '30 Sekunden lang fügt Dunkle Aura jedes Mal, wenn der Verbündete (bzw. Ihr selbst) Lebenspunkte opfert, allen umstehenden Feinden 5..50 Punkte Schattenschaden zu. Außerdem verliert Ihr 5..20 Lebenspunkte.', '30 Sekunden: Richtet bei umstehenden Gegnern jedes Mal 5...50 Punkte Schaden an, wenn der Verbündete (bzw. Ihr selbst) Lebenspunkte opfert. <i>Schadenskosten: Ihr verliert 5...20 Lebenspunkte.</i>', '', '', ''),
(117, 'Schwächen', 'Das ausgewählte Ziel leidet 10..30 Sekunden lang unter Schwäche.', 'Verursacht Zustand Schwäche (10...30 Sekunden).', 'Schwächen (PvP)', 'Das ausgewählte Ziel leidet 10..30 Sekunden lang unter Schwäche.', 'Verursacht Zustand Schwäche (10...30 Sekunden).'),
(118, 'Schwächendes Blut', 'Der Gegner und alle Feinde in der Nähe leiden 5..20 Sekunden lang unter Schwäche.', 'Verursacht beim Gegner und Feinden in der Nähe Zustand Schwäche (5...20 Sekunden).', 'Schwächendes Blut (PvP)', 'Der Gegner und alle Feinde in der Nähe leiden 1..16 Sekunden lang unter Schwäche.', 'Verursacht beim Gegner und Feinden in der Nähe Zustand Schwäche (1...16 Sekunden).'),
(119, 'Blut ist Kraft', '10 Sekunden bekommt der Verbündete eine Energieregeneration von +3..6.', '10 Sekunden: +3...6 Energieregeneration. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(120, 'Blut des Meisters', 'Alle Eure untoten Verbündeten werden um 30..116 Lebenspunkte geheilt. Ihr opfert pro Diener, der auf diese Weise geheilt wird, zusätzlich 2% maximale Lebenspunkte.', 'Heilt Eure untoten Diener um 30...116 Lebenspunkte. <i>Heilkosten: +2% Lebenspunktopfer pro geheiltem Diener.</i>', '', '', ''),
(121, 'Boshafter Geist', '8..20 Sekunden lang fügt Boshafter Geist jedes Mal, wenn der Gegner angreift oder eine Fertigkeit benutzt, diesem und allen ihn umstehenden Verbündeten 5..35 Punkte Schattenschaden zu.', '8...20 Sekunden: Richtet beim Gegner und umstehenden Feinden jedes Mal 5...35 Punkte Schaden an, wenn der Gegner angreift oder eine Fertigkeit benutzt.', '', '', ''),
(122, 'Unheilvoller Eingriff', '5..20 Sekunden lang verschafft Heilung dem Gegner 20% weniger Vorteile. Wenn der Gegner stirbt, während er mit Unheilvoller Eingriff verhext ist, wird ein herrenloser Knochenschreck der Stufe 1..17 belebt.', '5...20 Sekunden: Verringert die Wirkung von Heilung beim Gegner um 20%. Stirbt der Gegner, während er verhext ist, wird ein herrenloser Knochenschreck der Stufe 1...17 belebt.', '', '', ''),
(123, 'Heimtückischer Parasit', '5..15 Sekunden lang entzieht Ihr dem Gegner jedes Mal, wenn sein Angriff trifft, bis zu 15..45 Lebenspunkte.', '5...15 Sekunden: Entzieht jedes Mal 15...45 Lebenspunkte, wenn ein Angriff des Gegners trifft.', '', '', ''),
(124, 'Rückenschauer', '10..40 Sekunden lang wird der Gegner jedes Mal, wenn er beim Anwenden einer Fertigkeit mit Kälteschaden getroffen wird, unterbrochen und Ihr verliert 10..5 Energiepunkte oder Rückenschauer endet.', '10...40 Sekunden: Kälteschaden unterbricht die Fertigkeiten des Gegners. <i>Unterbrechungskosten: Ihr verliert 10...5 Energiepunkte oder Rückenschauer endet.</i>', '', '', ''),
(125, 'Verwelken', '5..35 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -2..4 und einer Energiedegeneration von -1. Wenn die Energie des Gegners auf 0 gefallen ist, nimmt dieser Gegner 15..75 Punkte Schaden und Verwelken endet.', '5...35 Sekunden: Verursacht -2...4 Lebenspunktdegeneration und -1 Energiedegeneration. Richtet 15...75 Punkte Schaden an, wenn die Energiepunkte des Gegners auf 0 sinken. <i>Endet, wenn die Energiepunkte des Gegners auf 0 sinken.</i>', '', '', ''),
(126, 'Lebensübertragung', '6..12 Sekunden lang unterliegen der Gegner und ihn umstehende Feinde einer Lebenspunktdegeneration von -3..8, die Euch als Lebenspunktregeneration zugute kommt.', 'Verhext auch den Gegner umstehende Feinde (6...12 Sekunden). Verursacht -3...8 Lebenspunktdegeneration. Ihr habt +3...8 Lebenspunktregeneration.', '', '', ''),
(127, 'Zeichen der Subversion', '6 Sekunden lang schlägt der nächste Zauber des Gegners auf einen seiner Verbündeten fehl und Ihr entzieht dem Widersacher bis zu 10..92 Lebenspunkte.', '6 Sekunden: Nächster Zauber des Gegners schlägt fehl und Ihr entzieht ihm 10...92 Lebenspunkte. <i>Zauber des Gegners muss auf einen seiner Verbündeten gerichtet sein, sonst keine Wirkung.</i>', '', '', ''),
(128, 'Seelenfresser', '10 Sekunden lang entzieht Ihr dem Gegner jedes Mal, wenn er einen Zauber wirkt, bis zu 16..80 Lebenspunkte.', '10 Sekunden: Entzieht jedes Mal 16...80 Lebenspunkte, wenn der Gegner einen Zauber wirkt.', '', '', ''),
(129, 'Fleisch schänden', '5..35 Sekunden lang erhält der Gegner bei einer Heilung nur zwei Drittel der Lebenspunkte.', '5...35 Sekunden: Verringert die Wirkung von Heilung beim Gegner um 33%. <i>Nur Fertigkeiten mit dem Wort "heilen" in der Beschreibung sind betroffen.</i>', '', '', ''),
(130, 'Dämonenfleisch', 'Während der nächsten 30..60 Sekunden erleiden jedes Mal, wenn Ihr eine Fertigkeit auf einen Feind anwendet, alle anderen Euch umstehenden Feinde 5..20 Punkte Schattenschaden.', '(30...60 Sekunden.) Jedes Mal, wenn Ihr eine Fertigkeit auf einen Feind anwendet, erleiden alle anderen umstehenden Feinde 5...20 Punkte Schattenschaden.', '', '', ''),
(131, 'Siegel der Widerhaken', 'Der Gegner und alle umstehenden Feinde leiden 3..15 Sekunden lang an einer Blutung.', 'Der Gegner und umstehende Feinde leiden an einer Blutung (3...15 Sekunden).', '', '', ''),
(132, 'Pest-Siegel', 'Alle negativen Zustände und ihre verbleibende Dauer werden um 100..200% verlängert und werden von Euch auf den Gegner übertragen. (Bei Flüchen von 4 oder weniger besteht eine 50%ige Chance auf einen Misserfolg.)', 'Überträgt alle Zustände und ihre verbleibende Dauer, die um 100...200% verlängert wurde, von Euch auf den Gegner. <i>Mindestens Flüche 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(133, 'Dunkler Pakt', 'Fügt dem Gegner Schattenschaden in Höhe von 10..48 zu.', 'Richtet 10...48 Punkte Schaden an.', '', '', ''),
(134, 'Orden der Schmerzen', '5 Sekunden lang fügt ein Gruppenmitglied jedes Mal, wenn es einem Gegner physischen Schaden zufügt, +3..16 Punkte Schaden zu.', 'Verzaubert alle Gruppenmitglieder (5 Sekunden). +3...16 Punkte Schaden, wenn diese Gruppenmitglieder physischen Schaden anrichten.', '', '', ''),
(135, 'Mutlosigkeit', 'Während der nächsten 3..16 Sekunden greift der Gegner um 50% langsamer an und unterliegt einer Lebenspunktdegeneration von -0..3.', '3...16 Sekunden: Gegner greift um 50% langsamer an und hat -0...3 Lebenspunktdegeneration.', '', '', ''),
(136, 'Schatten der Angst', 'Der Zielgegner und alle umstehenden Gegner greifen während der nächsten 5..30 Sekunden 50% langsamer an.', 'Verhext den Gegner und umstehende Feinde (5...30 Sekunden). Sie greifen um 50% langsamer an.', '', '', ''),
(137, 'Totenstarre', '8..20 Sekunden lang kann der Gegner nicht blocken.', '8...20 Sekunden: Gegner kann nicht blocken.', '', '', ''),
(138, 'Dunkles Band', 'Während der nächsten 30..60 Sekunden erleidet Euer Diener, der Euch am nächsten ist, jedes Mal, wenn Ihr Schaden erleidet, 75% dieses Schadens an Eurer Stelle.', '30...60 Sekunden: Überträgt 75% des erlittenen Schadens von Euch auf Euren am nächsten befindlichen Diener.', '', '', ''),
(139, 'Zustand einflößen', 'Während der nächsten 15..60 Sekunden wird jeder Zustand, den Ihr erhaltet, stattdessen auf Euren nächsten Diener übertragen.', '15...60 Sekunden: Überträgt jeden Zustand, den Ihr erhaltet, von Euch auf Euren am nächsten befindlichen untoten Diener.', '', '', ''),
(140, 'Unwohlsein', '5..35 Sekunden lang unterliegt der Gegner einer Energiedegeneration von -1 und Ihr erleidet eine Lebenspunktdegeneration von -1. Wenn die Energie des Gegners auf 0 gefallen ist, erleidet er 5..50 Punkte Schaden und Unwohlsein endet.', '5...35 Sekunden: Verursacht -1 Energiedegeneration. Richtet 5...50 Punkte Schaden an, wenn die Energiepunkte des Gegners auf 0 sinken. <i>Ihr habt -1 Lebenspunktdegeneration. Endet, wenn die Energiepunkte des Gegners auf 0 sinken.</i>', '', '', ''),
(141, 'Verzauberungen entreißen', 'Entfernt 5..9 Verzauberungen vom Gegner. Für jede entfernte Mönch-Verzauberung verliert Ihr 55..25 Lebenspunkte.', 'Entfernt 5...9 Verzauberungen beim Gegner. <i>Entfernungskosten: Ihr verliert für jede entfernte Mönch-Verzauberung 55...25 Lebenspunkte.</i>', '', '', ''),
(142, 'Bleibender Fluch', '6..30 Sekunden lang unterliegen der Gegner und alle in der Nähe befindlichen Feinde einer Lebenspunktdegeneration von -0..3 und Heilung verschafft ihnen um 20% weniger Vorteile.', '6...30 Sekunden: Gegner und Feinde in der Nähe haben -0...3 Lebenspunktdegeneration. Heilung verschafft ihnen um 20% weniger Vorteile.', '', '', ''),
(143, 'Verzauberung rauben', 'Entfernt 0..2 Verzauberungen vom Gegner. Beim Entfernen einer Verzauberung entzieht Ihr 5..65 Lebenspunkte.', 'Entfernt 0...2 Verzauberungen beim Gegner. Entfernungswirkung: Ihr entzieht 5...65 Lebenspunkte.', '', '', ''),
(144, 'Frostbeulen', 'Ihr werdet 10 Sekunden lang vergiftet. Gegner in dem Bereich Eures Gegners erleiden 10..44 Kälteschaden und verlieren 1..2 Verzauberungen.', 'Richtet bei Feinden im Bereich um das Ziel 10...44 Punkte Kälteschaden an; entfernt 1...2 Verzauberungen bei den Betroffenen. <i>Ihr werdet vergiftet (10 Sekunden).</i>', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(145, 'Siegel der Qual', 'Ihr leidet 25 Sekunden lang unter einer Blutung. Alle in der Nähe befindlichen Gegner erleiden 10..70 Punkte Schaden.', 'Richtet bei Feinden in Eurer Nähe 10...70 Punkte Schaden an. <i>Ihr beginnt zu bluten (25 Sekunden).</i>', 'Siegel der Qual (PvP)', 'Ihr leidet 25 Sekunden lang unter einer Blutung. Alle in der Nähe befindlichen Gegner erleiden 10..70 Punkte Schaden.', 'Richtet bei Feinden in Eurer Nähe 10...70 Punkte Schaden an. <i>Ihr beginnt zu bluten (25 Sekunden).</i>'),
(146, 'Blutopfer', 'Ihr bekommt 8..20 Energiepunkte.', 'Ihr erhaltet 8...20 Energiepunkte.', '', '', ''),
(147, 'Dunkle Wut', '5 Sekunden erhält jedes Gruppenmitglied für jeden erfolgreichen Angriff einen Adrenalinstoß. (Bei Blutmagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', 'Verzaubert Gruppenmitglieder (5 Sekunden). Die Betroffenen erhalten jedes Mal einen Adrenalinstoß, wenn sie mit einem Angriff einen Treffer landen. <i>Mindestens Blutmagie 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(148, 'Orden des Vampirs', '5 Sekunden lang entzieht ein Gruppenmitglied, das nicht mit einer anderen Nekromanten-Verzauberung belegt ist, jedes Mal, wenn es einem Gegner physischen Schaden zufügt, bis zu 3..16 Lebenspunkte.', 'Verzaubert alle Gruppenmitglieder (5 Sekunden). Die Betroffenen entziehen mit jedem Angriff, der physischen Schaden anrichtet, 3...16  Lebenspunkte. <i>Gruppenmitglieder, die unter dem Einfluss einer anderen Nekromanten-Verzauberung stehen, sind nicht betroffen.</i>', '', '', ''),
(149, 'Pest aussenden', 'Überträgt 1..3 negative Zustände und ihre verbleibende Dauer von Euch auf den Gegner und alle umstehenden Feinde.', 'Überträgt 1...3 Zustände und ihre verbleibende Dauer von Euch auf den Gegner und alle umstehenden Feinde.', '', '', ''),
(150, 'Zeichen des Schmerzes', '30 Sekunden lang fügt "Zeichen des Schmerzes" immer dann, wenn der Zielgegner physischen Schaden nimmt, umstehenden Feinden 10..40 Punkte Schattenschaden zu.', '30 Sekunden: Richtet bei umstehenden Feinden jedes Mal 10...40 Punkte Schaden an, wenn der Gegner physischen Schaden erleidet.', '', '', ''),
(151, 'Schmaus der Verdorbenheit', 'Der Gegner und alle umstehenden Feinde erleiden 16..80 Punkte Schattenschaden. Ihr entzieht allen getroffenen Gegnern, die unter einer Verhexung leiden, bis zu 8..40 Lebenspunkte.', 'Richtet beim Gegner und umstehenden Feinden 16...80 Punkte Schaden an. Entzieht jedem verhexten Feind 8...40 Lebenspunkte.', '', '', ''),
(152, 'Geschmack des Todes', 'Ihr entzieht dem angewählten bzw. Euch am nächsten stehenden verbündeten Diener 100..400 Lebenspunkte.', 'Entzieht verbündetem untotem Diener 100...400 Lebenspunkte.', '', '', ''),
(153, 'Vampir-Starren', 'Entzieht dem Gegner bis zu 18..60 Lebenspunkte.', 'Entzieht 18...60 Lebenspunkte.', '', '', ''),
(154, 'Pest-Berührung', 'Übertragt 1..3 negative Zustände und ihre verbleibende Dauer von Euch auf den berührten Gegner.', 'Überträgt 1...3 Zustände von Euch auf den Gegner.', '', '', ''),
(155, 'Abscheuliche Berührung', 'Der Gegner erleidet durch die Berührung 20..65 Punkte Schaden.', 'Richtet 20...65 Punkte Schaden an.', '', '', ''),
(156, 'Vampir-Berührung', 'Entzieht dem Gegner durch die Berührung 29..74 Lebenspunkte.', 'Entzieht 29...74 Lebenspunkte.', '', '', ''),
(157, 'Blutritual', '8..14 Sekunden lang erhält der berührte Verbündete zusätzlich 3 Energieregeneration. Blutritual kann nicht auf den Wirker angewendet werden.', '8...14 Sekunden: +3 Energieregeneration. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(158, 'Berührung der Qual', 'Der berührte Gegner erleidet 20..58 Punkte Schattenschaden.', 'Richtet 20...58 Punkte Schaden an.', '', '', ''),
(159, 'Rüstung schwächen', 'Der Gegner und ihn umstehende Feinde leiden 5..20 Sekunden lang an Beschädigter Rüstung.', 'Betrifft auch umstehende Feinde. Verursacht Zustand Beschädigte Rüstung (5...20 Sekunden).', '', '', ''),
(160, 'Windgeschwindigkeit', '5..13 Sekunden lang bewegt sich der Verbündete (bzw. Ihr selbst) um 33% schneller.', '5...13 Sekunden: Der Verbündete (bzw. Ihr selbst) bewegt sich um 33% schneller.', '', '', ''),
(162, 'Orkan', 'Der Gegner wird 2 Sekunden zu Boden geworfen. Dieser Zauber ruft Erschöpfung hervor. Bei Luftmagie von 4 oder weniger besteht eine 50% Wahrscheinlichkeit für einen Misserfolg.', 'Wirft zu Boden. <i>Ihr leidet an Erschöpfung. Mindestens Luftmagie 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(163, 'Wirbelsturm', 'Alle umstehenden Gegner erleiden 15..75 Punkte Kälteschaden. Angreifende Gegner werden von "Wirbelsturm" erfasst und zu Boden geworfen. Wenn Ihr Überzaubert seid, trifft dieser Zauber Feinde in der Nähe, anstatt umstehende Feinde.', 'Trifft umstehende Feinde. Richtet 15...75 Punkte Kälteschaden an. Wirft angreifende Feinde zu Boden. Trifft Feinde in der Nähe, anstatt umstehende Feinde, wenn Überzaubert.', '', '', ''),
(164, 'Elementareinstimmung', '25..60 Sekunden seid Ihr auf Luft, Feuer, Wasser und Erde eingestimmt und erhaltet +1..2 auf diese Attribute. Jedes Mal, wenn Ihr Magie in Verbindung mit einem dieser Elemente verwendet, erhaltet Ihr 50% der Basisenergiekosten der Fertigkeit zurück.', '25...60 Sekunden: Eure Elementarattribute sind um +1...2 erhöht. Ihr erhaltet 50% der Energiekosten für Luft-, Feuer-, Wasser- und Erdmagie-Fertigkeiten zurück, die Ihr benutzt.', '', '', ''),
(165, 'Erdrüstung', '30 Sekunden lang erhaltet Ihr +24..60 Rüstung, bewegt Euch jedoch um 50..14% langsamer.', '30 Sekunden: Ihr habt +24...60 Rüstung. <i>Ihr bewegt Euch um \'\'\'50...21...14%\'\'\' langsamer.</i>', '', '', ''),
(166, 'Kinetik-Rüstung', '8 Sekunden lang erhaltet Ihr +20..80 Punkte Rüstung. Jedes Mal, wenn Ihr einen Zauber wirkt, wird Kinetik-Rüstung 8 Sekunden lang erneuert.', '8 Sekunden: Ihr habt +20...80 Rüstung. Erneuerungsbonus: Wirkt einen Zauber.', '', '', ''),
(167, 'Eruption', 'An der Position des Gegners wird eine Eruption erzeugt. 5 Sekunden lang erleiden Feinde in der Nähe dieser Position pro Sekunde 10..40 Punkte Erdschaden und werden 10 Sekunden lang geblendet.', 'Richtet jede Sekunde 10...40 Punkte Erdschaden an (5 Sekunden). Trifft Feinde in der Nähe der anfänglichen Position des Ziels. Verursacht Zustand Blindheit (10 Sekunden).', '', '', ''),
(168, 'Magnet-Aura', 'Für 1..5 Sekunden blockt Ihr den nächsten gegen Euch gerichteten Angriff und Euer Angreifer erleidet 10..50 Punkte Schaden. Wenn Ihr überzaubert seid, werden alle Gruppenmitglieder in Hörweite ebenfalls verzaubert.', '(1...5 Sekunden.) Blockt den nächsten gegen Euch gerichteten Angriff und reflektiert 10...50 Schaden zum Angreifer. Wenn Ihr überzaubert seid, werden alle Gruppenmitglieder in Hörweite ebenfalls verzaubert.', '', '', ''),
(169, 'Erdeinstimmung', '36..60 Sekunden lang seid Ihr auf die Erde eingestimmt. Jedes Mal, wenn Ihr Erdmagie verwendet, erhaltet Ihr 1 Energiepunkt sowie 30% der Basisenergiekosten der Fertigkeit.', '36...60 Sekunden: Ihr erhaltet 1 Energiepunkt plus 30% der Energiekosten zurück, wenn Ihr Erdmagie-Fertigkeiten benutzt.', '', '', ''),
(170, 'Erdbeben', 'An der Position des Gegners wird ein Erdbeben erzeugt. Alle Gegner in diesem Gebiet werden zu Boden geworfen und erleiden 26..100 Punkte Erdschaden.', 'Richtet 26...100 Punkte Erdschaden an. Tifft auch Feinde in der Nähe des Ziels. Wirft zu Boden.', '', '', ''),
(171, 'Steinigung', 'Sendet einen großen Stein aus, der dem Gegner 45..105 Punkte Erdschaden zufügt, wenn er trifft. Wenn Steinigung einen Gegner trifft, der unter Schwäche leidet, wird er zu Boden geworfen.', 'Geschoss: Richtet 45...105 Punkte Erdschaden an. Wirft Gegner zu Boden, falls dieser geschwächt ist.', '', '', ''),
(172, 'Steindolche', 'Sendet zwei Steindolche aus. Jeder Steindolch fügt dem Gegner 8..33 Punkte Erdschaden zu, wenn er trifft. Wenn Ihr überzaubert seid, verursacht jedes Geschoss für 1..5 Sekunden Blutung.', 'Zwei Geschosse: Richtet je 8...33 Punkte Erdschaden an. Wenn Ihr überzaubert seid, leidet der Gegner 1...5 Sekunden lang an einer Blutung.', '', '', ''),
(173, 'Zupackende Erde', '5..20 Sekunden lang bewegen sich alle Feinde in der Nähe um 50% langsamer.', 'Verhext Feinde in Eurer Nähe (5...20 Sekunden). Die Betroffenen bewegen sich um 50% langsamer.', '', '', ''),
(174, 'Nachbeben', 'Feinde, die in der Nähe von Euch stehen, erleiden 26..100 Punkte Erdschaden. Umgestoßene Gegner erleiden 10..68 Punkte zusätzlichen Erdschaden.', 'Richtet bei in der Nähe befindlichen Feinden 26...100 Punkte Erdschaden an. Zu Boden geworfene Feinde erleiden zusätzlich 10...68 Punkte Erdschaden.', '', '', ''),
(175, 'Abwehr gegen Elemente', 'Ihr erzeugt an Eurer aktuellen Position eine Abwehr gegen Elemente. 8..20 Sekunden lang erhalten Gruppenmitglieder, die keine Geister sind, in dem Bereich +24 Rüstung gegen Elementarschaden.', '8...20 Sekunden: Gruppenmitglieder in dieser Abwehr haben +24 Rüstung gegen Elementarschaden. <i>Geister sind nicht betroffen.</i>', '', '', ''),
(176, 'Abwehr gegen Nahkampf', 'Ihr erzeugt an Eurer aktuellen Position eine Abwehr gegen Nahkampf. 5..20 Sekunden lang haben Gruppenmitglieder, die keine Geister sind, in diesem Bereich eine 50%ige Chance, Nahkampfangriffe zu blocken.', '5...20 Sekunden: Gruppenmitglieder in dieser Abwehr können Nahkampfangriffe mit 50%iger Wahrscheinlichkeit blocken. <i>Verbündete Geister sind nicht betroffen.</i>', '', '', ''),
(177, 'Abwehr gegen Feinde', 'Ihr erzeugt an Eurer aktuellen Position eine Abwehr gegen Feinde. 8..20 Sekunden lang bewegen sich Gegner, die keine Geister sind, in diesem Bereich um 50% langsamer.', '8...20 Sekunden: Feinde in dieser Abwehr bewegen sich um 50% langsamer.', '', '', ''),
(178, 'Ätherwunder', 'Alle Verzauberungen gehen verloren. 8..20 Sekunden erhaltet Ihr eine Energieregeneration von +6. Wenn Ätherwunder endet, verliert Ihr für jeden Energiepunkt, den Ihr habt, 2 Lebenspunkte. Dieser Zauber ruft Erschöpfung hervor.', '8...20 Sekunden: Ihr habt +6 Energieregeneration. Wirkung am Ende: Ihr verliert 2 Lebenspunkte für jeden Eurer Energiepunkte. <i>Ihr verliert alle Verzauberungen. Ihr leidet an Erschöpfung.</i>', '', '', ''),
(179, 'Brandbänder', 'Nach 3 Sekunden erleiden der Gegner und alle Feinde in der Nähe 20..80 Punkte Feuerschaden und werden 1..3 Sekunde(n) lang in Brand gesetzt. Wird früher aktiviert, wenn der anvisierte Gegner stirbt.', '(3 Sekunden.) Richtet bei Feinden in der Nähe des Ziels 20...80 Punkte Feuerschaden an und verursacht Zustand Brennen (1...3 Sekunden). Wird auch aktiviert, wenn der anvisierte Gegner stirbt.', '', '', ''),
(180, 'Wiederherstellungs-Aura', '60 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr einen Zauber wirkt, 0..1 Energie und heilt um 200..500% der jeweiligen Energiekosten.', '(60 Sekunden.) Ihr erhaltet jedes Mal, wenn Ihr einen Zauber wirkt, 0...1 Energie und heilt um 200...500% der jeweiligen Energiekosten.', 'Wiederherstellungs-Aura (PvP)', '60 Sekunden lang gewinnt Ihr jedes Mal, wenn Ihr einen Zauber wirkt, 0..1 Energie und Heilung in Höhe von 200..400% der jeweiligen Energiekosten.', '(60 Sekunden.) Ihr gewinnt jedes Mal wenn Ihr einen Zauber wirkt, 0...1 Energie und Heilung in Höhe von 200...400% der jeweiligen Energiekosten.'),
(181, 'Äthererneuerung', '5..20 Sekunden lang bekommt Ihr jedes Mal, wenn Ihr einen Zauber wirkt, 1..4 Energiepunkte und heilt um 5..20 Lebenspunkte. Beides gilt für jede Verzauberung, die auf Euch liegt.', '5...20 Sekunden: Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr für jede Verzauberung auf Euch 1...4 Energiepunkte und heilt um 5...20 Lebenspunkte.', 'Äthererneuerung (PvP)', '7 Sekunden lang, bekommt Ihr jedes Mal, wenn Ihr einen Zauber wirkt, 1..3 Energiepunkte und 5..20 Lebenspunkte für jede Verzauberung, die auf Euch liegt.', '7 Sekunden: Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr für jede Verzauberung auf Euch 1...3 Energiepunkte und 5...20 Lebenspunkte.'),
(182, 'Flammenbeschwörung', '60 Sekunden lang richten Eure Angriffe 5..20 Punkte zusätzlichen Feuerschaden an, wenn Ihr eine Feuerwaffe führt.', '60 Sekunden: Eure Angriffe richten +5...20 Punkte Feuerschaden an. <i>Keine Wirkung, falls Eure Waffe keinen Feuerschaden verursacht.</i>', '', '', ''),
(183, 'Inferno', 'Alle umstehenden Feinde erleiden 30..135 Punkte Feuerschaden.', 'Richtet bei umstehenden Feinden 30...135 Punkte Feuerschaden an.', '', '', ''),
(184, 'Feuereinstimmung', '36..60 Sekunden lang seid Ihr auf das Feuer eingestimmt. Jedes Mal, wenn Ihr Feuermagie verwendet, erhaltet Ihr 1 Energiepunkt und 30% der Basisenergiekosten der Fertigkeit.', '36...60 Sekunden: Ihr erhaltet 1 Energiepunkt plus 30% der Energiekosten zurück, wenn Ihr Feuermagie-Fertigkeiten benutzt.', '', '', ''),
(185, 'Geistige Verbrennung', 'Gegner und umstehende Feinde erleiden 15..60 Punkte Feuerschaden. Falls Ihr mehr Energie als der Gegner habt, erleiden er und alle umstehenden Feinde zusätzlich 15..60 Punkte Feuerschaden und werden 1..10 Sekunden lang in Brand gesetzt.', 'Richtet 15...60 Punkte Feuerschaden an. Falls Ihr mehr Energie habt als der Gegner, erhält er zusätzlich 15...60 Punkte Feuerschaden und Zustand Brennen (1...10 Sekunden). Trifft den Gegner und umstehende Feinde.', '', '', ''),
(186, 'Feuerball', 'Sendet einen Feuerball aus, der dem Gegner und alle umstehenden Feinden 7..112 Punkte Feuerschaden zufügt.', 'Geschoss: Richtet beim Gegner und umstehenden Feinden 7...112 Punkte Feuerschaden an.', '', '', ''),
(187, 'Meteor', 'Der Gegner und alle umstehenden Feinde erleiden 7..112 Punkte Feuerschaden und werden zu Boden geworfen.', 'Richtet 7...112 Punkte Feuerschaden an und wirft zu Boden. Trifft auch umstehende Feinde um den Gegner.', '', '', ''),
(188, 'Flammenstoß', 'Alle Feinde in der Nähe erleiden 15..120 Punkte Feuerschaden.', 'Richtet bei allen Feinden in Eurer Nähe 15...120 Punkte Feuerschaden an.', '', '', ''),
(189, 'Anrufung von Rodgort', 'Der Gegner und alle Feinde in der Nähe erleiden 15..120 Punkte Feuerschaden und werden 1..3 Sekunden lang in Brand gesetzt.', 'Richtet 15...120 Punkte Feuerschaden an. Wirkt auch auf Feinde in der Nähe des Gegners. Verursacht Zustand Brennen (1...3 Sekunden).', '', '', ''),
(190, 'Zeichen des Rodgort', 'Ein feindliches Ziel und alle Gegner in der Nähe werden durch Zeichen des Rodgort verhext. 10..35 Sekunden lang wird jeder Gegner jedes Mal, wenn er Feuerschaden erleidet, 1..4 Sekunden in Brand gesetzt.', 'Verhext den Gegner und Feinde in der Nähe (10...35 Sekunden). Verursacht Zustand Brennen (1...4 Sekunden), wenn die Betroffenen Feuerschaden erleiden.', '', '', ''),
(191, 'Feueropfer', 'Der Gegner erleidet 20..75 Punkte Feuerschaden und wird für 1..3 Sekunden in Brand gesetzt.', 'Richtet 20...75 Punkte Feuerschaden an. Verursacht Zustand Brennen (1...3 Sekunden).', '', '', ''),
(192, 'Meteorschauer', 'An der Position des Gegners wird ein Meteorschauer erzeugt. Während der nächsten 9 Sekunden erleiden umstehende Gegner alle 3 Sekunden 7..112 Punkte Feuerschaden und werden zu Boden geworfen.', 'Richtet 7...112 Punkte Feuerschaden an und wirft alle 3 Sekunden zu Boden (9 Sekunden). Trifft umstehende Feinde um anfängliche Position des Ziels.', '', '', ''),
(193, 'Phönix', 'An Eurer Position steigt ein feuriger Phönix empor, fliegt zu Eurem Ziel und explodiert, sobald er dort aufschlägt. Diese Explosion richtet beim Gegner und umstehenden Feinden 10..60 Punkte Feuerschaden an. Wenn Ihr Überzaubert seid, werden Verbündete im Explosionsradius für 20..80 Lebenspunkte geheilt.', 'Geschoss: Richtet beim Gegner und umstehenden Feinden um den Gegner 10...60 Punkte Feuerschaden an. Heilt umstehende Verbündete am Zielort für 20...80 Lebenspunkte, wenn Ihr Überzaubert seid.', '', '', ''),
(194, 'Feuergeschoss', 'Sendet ein Feuergeschoss aus, das dem Gegner 20..65 Punkte Feuerschaden zufügt, falls es trifft. Wenn Ihr Überzaubert seid, trifft dieser Zauber umstehende Feinde ebenfalls.', 'Geschoss: Richtet 20...65 Punkte Feuerschaden an. Wenn überzaubert, werden auch umstehende Gegner getroffen.', '', '', ''),
(195, 'Lavabecken', '5 Sekunden lang erleiden umstehende Gegner am Wirkungsort dieses Zaubers 5..50 Punkte Feuerschaden pro Sekunde. Wenn Ihr Überzaubert seid, trifft dieser Zauber Feinde in der Nähe, anstatt umstehende Feinde.', 'Richtet jede Sekunde 5...50 Punkte Feuerschaden an (5 Sekunden). Trifft umstehende Feinde an Eurer ursprünglichen Position. Wenn Überzaubert, dann wird die Reichweite zu In der Nähe erhöht.', '', '', ''),
(196, 'Sengende Hitze', 'An der Position des Gegners wird Sengende Hitze erzeugt. 5 Sekunden lang erleiden Feinde in der Nähe pro Sekunde je 10..40 Punkte Feuerschaden. Wenn Sengende Hitze endet, werden die im Wirkungsbereich befindlichen Feinde 3 Sekunden lang in Brand gesetzt.', 'Richtet jede Sekunde 10...40 Punkte Feuerschaden an (5 Sekunden). Trifft Feinde in der Nähe der anfänglichen Position des Ziels. Wirkung am Ende: Verursacht Zustand Brennen (3 Sekunden).', '', '', ''),
(197, 'Feuersturm', 'An der Position des Gegners wird ein Feuersturm erzeugt. Während der nächsten 10 Sekunden erleiden umstehende Gegner 5..35 Punkte Feuerschaden pro Sekunde.', 'Richtet jede Sekunde 5...35 Punkte Feuerschaden an (10 Sekunden). Trifft umstehende Feinde um anfängliche Position des Ziels.', '', '', ''),
(198, 'Glyphe der Elementarkraft', '25 Sekunden lang sind Eure Elementar-Attribute um 2 erhöht. Diese Glyphe endet, wenn Ihr 10 Zauber benutzt habt.', '25 Sekunden: Erhöht Eure Elementar-Attribute für Euren nächsten 10 Zauber um +2.', '', '', ''),
(199, 'Glyphe der Energie', 'Euer nächster 1..3 Zauber verursachen kein Überzaubern und kosten 10..25 Energiepunkte weniger. Eure Elementarattribute sind um 1..2 erhöht.', 'Euer nächster<sub>sic</sub> 1...3 Zauber verursachen kein Überzaubern<sub>sic</sub> und kosten 10...25 Energiepunkte weniger. Ihr erhaltet 1...2 aus<sub>sic</sub> alle Elementarattribute.', '', '', ''),
(200, 'Glyphe der geringeren Energie', 'Während der nächsten 15 Sekunden kostet das Wirken Eures nächsten 2 Zauber 10..18 Energiepunkte weniger.', '15 Sekunden: Euer nächster<sub>sic</sub> 2 Zauber kosten 10...18 Energiepunkte weniger.', '', '', ''),
(201, 'Glyphe der Konzentration', '15 Sekunden lang kann Euer nächster 1 Zauber nicht unterbrochen werden und berücksichtigt die Auswirkungen von Benommenheit nicht.', '15 Sekunden: Euer nächster 1 Zauber kann nicht unterbrochen werden. Sie bleiben vom Zustand Benommenheit unbeeinflusst.', '', '', ''),
(202, 'Glyphe der Opferung', '15 Sekunden lang benötigt Eure nächste Fertigkeit für die Wiederaufladung 30 Sekunden länger, aber wird, wenn sie ein Zauber ist, sofort gewirkt.', '(15 Sekunden.) Eure nächste Fertigkeit benötigt für die Wiederaufladung 30 Sekunden länger. Ist sie ein Zauber, wird sie sofort gewirkt.', '', '', ''),
(203, 'Glyphe der Erneuerung', '15 Sekunden lang wird Euer nächster Zauber sofort wieder aufgeladen.', '15 Sekunden: Euer nächster Zauber wird sofort wieder aufgeladen.', '', '', ''),
(204, 'Rost', 'Richtet beim Gegner und allen umstehenden Feinden 10..70 Punkte Kälteschaden an. 5..20 Sekunden lang benötigen der Gegner und alle umstehenden Feinden doppelt so lange, um Siegel zu aktivieren. Wenn Ihr Überzaubert seid, werden Siegel von Gegnern, die von Rost getroffen sind, unterbrochen und für 1..10 Sekunden deaktiviert.', 'Richtet beim Gegner und umstehenden Feinden 10...70 Punkte Kälteschaden an. Verhext den Gegner und umstehende Feinde (5...20 Sekunden). Verdoppelt die Aktivierungszeit von Siegeln. Unterbricht und deaktiviert Siegel für 1...10 Sekunden, wenn Ihr Überzaubert seid.', '', '', ''),
(205, 'Blitzwelle', 'Nach 3 Sekunden wird der Zielgegner zu Boden geworfen, erleidet 14..100 Punkte Blitzschaden und leidet 5..20 Sekunden lang an Beschädigter Rüstung. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', '3 Sekunden: Wirkung am Ende: Richtet 14...100 Punkte Blitzschaden an, wirft zu Boden und verursacht Beschädigte Rüstung (5...20 Sekunden). 25% Rüstungsdurchdringung.', '', '', ''),
(206, 'Frostrüstung', '10..34 Sekunden lang erhaltet Ihr +40 Rüstung gegen physischen Schaden. Eure Wassermagie ist um +1 erhöht.', '10...34 Sekunden: Ihr habt +40 Rüstung gegen physischen Schaden. Eure Wassermagie ist um +1 erhöht.', '', '', ''),
(207, 'Frost beschwören', '60 Sekunden lang richten Eure Angriffe 5..20 Punkte zusätzlichen Kälteschaden an, wenn Ihr eine Kältewaffe führt.', '60 Sekunden: Eure Angriffe richten +5...20 Punkte Kälteschaden an. <i>Keine Wirkung, falls Eure Waffe keinen Kälteschaden verursacht.</i>', '', '', ''),
(208, 'Wassereinstimmung', '36..60 Sekunden lang seid Ihr auf das Wasser eingestimmt. Jedes Mal, wenn Ihr Wassermagie verwendet, erhaltet Ihr 1 Energiepunkt und 30% der Basisenergiekosten der Fertigkeit.', '36...60 Sekunden: Ihr erhaltet 1 Energiepunkt plus 30% der Energiekosten zurück, wenn Ihr Wassermagie-Fertigkeiten benutzt.', '', '', ''),
(209, 'Geistige Erfrierung', 'Der Gegner erleidet 10..60 Punkte Kälteschaden. Wenn Ihr mehr Energie als der Gegner habt, erleidet er zusätzlich 10..60 Punkte Kälteschaden und bewegt sich 1..5 Sekunden lang um 90% langsamer. Dieser Zauber ruft Erschöpfung hervor.', 'Richtet 10...60 Punkte Kälteschaden an. Falls Ihr mehr Energie habt als der Gegner, erhält er zusätzlich 10...60 Punkte Kälteschaden und bewegt sich um 90% langsamer (1...5 Sekunden).', 'Geistige Erfrierung (PvP)', 'Der Gegner erleidet 10..40 Punkte Kälteschaden. Wenn Ihr mehr Energie als der Gegner habt, erleidet er zusätzlich 10..40 Punkte Kälteschaden und bewegt sich 1..5 Sekunden lang um 90% langsamer. Dieser Zauber ruft Erschöpfung hervor.', 'Richtet 10...40 Punkte Kälteschaden an. Falls Ihr mehr Energie habt als der Gegner, erhält er zusätzlich 10...40 Punkte Kälteschaden und bewegt sich um 90% langsamer (1...5 Sekunden).'),
(210, 'Eisgefängnis', '8..20 Sekunden lang werden die Beine des Gegners von Eis umschlossen, wodurch seine Bewegungen um 66% langsamer werden.', '8...20 Sekunden: Gegner bewegt sich um 66% langsamer.', '', '', ''),
(211, 'Eisdornen', 'Der Gegner sowie umstehende Feinde erleiden 20..80 Punkte Kälteschaden und bewegen sich 2..6 Sekunden lang um 66% langsamer.', 'Verhext den Gegner und umstehende Feinde (2...6 Sekunden). Die Betroffenen bewegen sich um 66% langsamer. Anfangswirkung: Richtet 20...80 Punkte Kälteschaden an.', '', '', ''),
(212, 'Frostexplosion', 'Alle in der Nähe befindlichen Gegner erleiden 10..85 Punkte Kälteschaden und bewegen sich 3..8 Sekunden lang um 66% langsamer.', 'Verhext Feinde in Eurer Nähe. Die Betroffenen bewegen sich um 66% langsamer (3...8 Sekunden). Anfangswirkung: Richtet bei Feinden in Eurer Nähe 10...85 Punkte Kälteschaden an.', '', '', ''),
(213, 'Scherbensturm', 'Sendet eine Eisscherbe aus, die dem Gegner 10..85 Punkte Kälteschaden zufügt, wenn sie trifft und dazu führt, dass er sich 2..6 Sekunden lang um 66% langsamer bewegt.', 'Geschoss: Richtet 10...85 Punkte Kälteschaden an. Gegner bewegt sich um 66% langsamer (2...6 Sekunden).', '', '', ''),
(214, 'Eisspeer', 'Sendet einen Eisspeer aus, der dem Gegner 10..60 Punkte Kälteschaden zufügt, wenn er trifft. Wenn Ihr Überzaubert seid, habt Ihr 5 Sekunden lang +1..4 Lebenspunktregeneration.', 'Geschoss: Richtet 10...60 Punkte Kälteschaden an. Wenn Ihr Überzaubert seid, erhaltet Ihr für 5 Sekunden +1...4 Lebenspunktregeneration.', '', '', ''),
(215, 'Strudel', 'An der Position des Gegners wird ein Strudel erzeugt. 10 Sekunden lang erleiden umstehende Feinde in diesem Bereich pro Sekunde 10..25 Punkte Kälteschaden. Wenn Strudel trifft, wird dadurch das Wirken von Zaubern unterbrochen.', 'Richtet jede Sekunde 10...25 Punkte Kälteschaden an und unterbricht Zauber (10 Sekunden). Trifft umstehende Feinde um anfängliche Position des Ziels.', '', '', ''),
(216, 'Eisenbeschlag', '8..15 Sekunden lang habt ihr +15 Rüstung. Eure Luftmagie-Zauber, die einen Gegner anvisieren, werden 25% schneller aktiviert und wiederaufgeladen, Ihr seid jedoch um 3 Punkte überzaubert.', '8...15 Sekunden: Ihr erhaltet +15 Rüstung. Luftmagie-Zauber, die einen Gegner anvisieren, werden 25% schneller aktiviert und wiederaufgeladen, Ihr seid jedoch um 3 Punkte überzaubert.', '', '', ''),
(217, 'Kristallwelle', 'Umstehende Gegner erleiden 10..70 Punkte Schaden, werden jedoch von sämtlichen negativen Zuständen geheilt. Jeder entfernte Zustand verursacht 5..15 Schaden.', 'Richtet bei allen umstehenden Feinden 10...70 Punkte Schaden an. Diese Gegner verlieren alle Zustände und erleiden für jeden entfernten Zustand 5...15 Schaden.', '', '', ''),
(218, 'Obsidianfleisch', '8..20 Sekunden lang erhaltet Ihr +20 Rüstung gegen physischen Schaden und könnt nicht zum Ziel von feindlichen Zaubern werden. Dafür könnt Ihr nicht angreifen und erleidet -2 Energiedegeneration.', '(8...20 Sekunden.) Ihr erhaltet +20 Rüstung gegen physischen Schaden und könnt nicht zum Ziel von feindlichen Zaubern werden. <i>Ihr könnt nicht angreifen und erleidet -2 Energiedegeneration.</i>', '', '', ''),
(219, 'Obsidianflamme', 'Fügt dem Gegner 22..112 Punkte Schaden zu. Dieser Zauber ignoriert Rüstung.', 'Richtet 22...112 Punkte Schaden an.', 'Obsidianflamme (PvP)', 'Fügt dem Gegner 22..92 Punkte Schaden zu. Dieser Zauber ignoriert Rüstung.', 'Richtet 22...92 Punkte Schaden an.'),
(220, 'Blitzblendung', 'Der als Ziel ausgewählte Feind wird 3..8 Sekunden lang geblendet.', 'Verursacht Zustand Blindheit (3...8 Sekunden).', '', '', ''),
(221, 'Blitzbeschwörung', '60 Sekunden lang richten Eure Angriffe 5..20 Punkte zusätzlichen Blitzschaden an, wenn Ihr eine Blitzwaffe führt.', '60 Sekunden: Eure Angriffe richten +5...20 Punkte Blitzschaden an. <i>Keine Wirkung, falls Eure Waffe keinen Blitzschaden verursacht.</i>', '', '', ''),
(222, 'Blitzschlag', 'Fügt dem Gegner 5..50 Punkte Blitzschaden zu. Dieser Zauber hat eine Rüstungsdurchdringung von 25%. Wenn Ihr Überzaubert seid, wir dieser Gegner für 3 Sekunden mit Blitzschlag verhext. Wenn diese Verhexung endet, wird diesem Gegner noch einmal 5..50 Punkte Blitzschaden zugefügt.', 'Richtet 5...50 Punkte Blitzschaden an. 25% Rüstungsdurchdringung. Verhext für 3 Sekunden, wenn Überzaubert. Wirkung am Ende: Richtet 5...50 Punkte Blitzschaden an.', '', '', ''),
(223, 'Kettenblitz', 'Der Gegner, ein Feind in seiner Nähe und ein weiterer Feind in der Nähe von letzterem erleiden 10..85 Punkte Blitzschaden. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...85 Punkte Blitzschaden an. Trifft den Gegner und zwei Feinde in seiner Nähe. 25% Rüstungsdurchdringung.', '', '', ''),
(224, 'Schwächende Aufladung', 'Der Gegner wird getroffen, erleidet 25..50 Punkte Blitzschaden und leidet 5..20 Sekunden lang an Schwäche. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 25...50 Punkte Blitzschaden an. Verursacht Zustand Schwäche (5...20 Sekunden). 25% Rüstungsdurchdringung.', '', '', ''),
(225, 'Lufteinstimmung', '36..60 Sekunden lang seid Ihr auf die Luft eingestimmt. Jedes Mal, wenn Ihr Luftmagie verwendet, erhaltet Ihr 1 Energiepunkt und 30% der Basisenergiekosten der Fertigkeit.', '36...60 Sekunden: Ihr erhaltet 1 Energiepunkt plus 30% der Energiekosten zurück, wenn Ihr Luftmagie-Fertigkeiten benutzt.', '', '', ''),
(226, 'Geistiger Schock', 'Der Gegner erleidet 10..50 Punkte Blitzschaden. Wenn Ihr mehr Energie als der Gegner habt, erleidet er 10..50 Punkte zusätzlichen Blitzschaden und wird zu Boden geworfen. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...50 Punkte Blitzschaden an. Wenn Ihr mehr Energie als der Gegner habt, erleidet er +10...50 Punkte Blitzschaden und wird zu Boden geworfen. 25% Rüstungsdurchdringung.', 'Geistiger Schock (PvP)', 'Der Gegner erleidet 10..50 Punkte Blitzschaden. Wenn Ihr mehr Energie als der Gegner habt, erleidet er 10..50 Punkte zusätzlichen Blitzschaden und wird zu Boden geworfen. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...50 Punkte Blitzschaden an. Wenn Ihr mehr Energie als der Gegner habt, erleidet er +10...50 Punkte Blitzschaden und wird zu Boden geworfen. 25% Rüstungsdurchdringung.'),
(227, 'Schimmerndes Zeichen', '10 Sekunden lang wird der Gegner mit Schimmerndes Zeichen verhext. Er und alle umstehenden Feinde erleiden pro Sekunde 5..25 Punkte Schaden. Feinde, die sich in der Wirk- oder Nachwirkzeit von einer Angriffsfertigkeit befinden, während sie von Schimmerndes Zeichen Schaden erleiden, werden 3 Sekunden lang geblendet. Diese Verhexung endet, wenn Ihr eine Fertigkeit mit diesem Gegner als Ziel verwendet.', '10 Sekunden: Richtet beim Gegner und umstehenden Feinden pro Sekunde 5...25 Punkte Schaden an. Blendet Gegner, die Angriffsfertigkeiten verwenden. <i>Endet, wenn Ihr eine Fertigkeit mit diesem Gegner als Ziel verwendet.</i>', '', '', ''),
(228, 'Donnerschlag', 'An der Position des Gegners wird eine gewaltige Stoßwelle erzeugt. Richtet beim Gegner und umstehenden Feinden 10..50 Punkte Blitzschaden an. Getroffene Feinde werden unterbrochen und leiden 5..20 Sekunden lang an Beschädigter Rüstung und Schwäche. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...50 Punkte Blitzschaden an. Verursacht Beschädigte Rüstung und Schwäche (5...20 Sekunden). Unterbricht. 25% Rüstungsdurchdringung.', '', '', ''),
(229, 'Kugelblitz', 'Sendet einen Kugelblitz aus, der dem Gegner 10..100 Punkte Blitzschaden zufügt. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Geschoss: Richtet 10...100 Punkte Blitzschaden an. 25% Rüstungsdurchdringung.', '', '', ''),
(230, 'Blitzspeer', 'Schleudert einen Blitzspeer, der, falls er trifft, 15..50 Punkte Blitzschaden zufügt. Blitzspeer unterbricht angreifende Gegner. Dieser Zauber hat eine Rüstungsdurchdringung von 25% und trifft alle Feinde zwischen Euch und Eurem Ziel.', 'Geschoss: Richtet 15...50 Punkte Blitzschaden an. Unterbricht, wenn Euer Ziel angreift. 25% Rüstungsdurchdringung. Trifft alle Feinde zwischen Euch und Eurem Ziel.', '', '', ''),
(231, 'Schock', 'Der berührte Gegner wird zu Boden geworfen und erleidet 10..60 Punkte Blitz-Schaden. Diese Fertigkeit hat eine Rüstungsdurchdringung von 25% und verursacht Erschöpfung.', 'Richtet 10...60 Punkte Blitzschaden an. Wirft zu Boden. 25% Rüstungsdurchdringung. <i>Ihr leidet an Erschöpfung.</i>', '', '', ''),
(232, 'Blitz-Berührung', 'Der berührte Gegner und alle umstehenden Feinden erleiden 10..60 Punkte Blitzschaden, sind für 1..4 Sekunden geblendet und erleiden 1..8 Sekunden Beschädigte Rüstung. Diese Fertigkeit hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...60 Punkte Blitzschaden an. Trifft auch umstehende Feinde um den Gegner. Verursacht für 1...4 Sekunden Blind<sub>sic</sub> und für 1...8 Sekunden Beschädigte Rüstung bei allen getroffenen Gegnern. 25% Rüstungsdurchdringung.', '', '', ''),
(233, 'Wirbelnde Aura', '8..20 Sekunden lang besteht eine 50%ige Wahrscheinlichkeit, dass Wirbelnde Aura Geschossangriffe blockt.', '5 Sekunden: Ihr habt +1...6 Lebenspunktregeneration und eine 50%ige Chance, Geschosse zu blocken. Wenn Überzaubert, werden alle Gruppenmitglieder  in Hörweite ebenfalls verzaubert.', '', '', ''),
(234, 'Tiefgefrieren', 'Die Position des Gegners wird tiefgefroren. Alle Feinde in diesem Bereich erleiden 10..85 Punkte Kälteschaden und bewegen sich 10 Sekunden lang um 66% langsamer.', 'Verhext auch Feinde im Bereich Eures Gegners (10 Sekunden). Die Betroffenen bewegen sich um 66% langsamer. Anfangswirkung: Richtet 10...85 Punkte Kälteschaden an.', '', '', ''),
(235, 'Verschwommene Sicht', '4..10 Sekunden lang werden der Gegner und alle umstehenden Feinde mit Verschwommener Sicht verhext. Solange diese Verhexung wirkt, besteht eine 50%ige Chance, dass die Angriffe dieser Gegner fehlschlagen.', 'Verhext den Gegner umstehende Feinde (4...10 Sekunden). Die Angriffe der Betroffenen gehen mit 50%iger Wahrscheinlichkeit daneben.', '', '', ''),
(236, 'Nebelform', '10..45 Sekunden lang erleidet Ihr 33% weniger Schaden von Feinden, die an den Auswirkungen einer Wassermagie-Verhexung leiden. Immer wenn Ihr einen Elementarzauber wirkt, werden alle Verbündete in Hörweite, die keine Geister sind, um 50..250% der Energiekosten dieses Zaubers geheilt.', '(10...45 Sekunden.) Ihr erleidet 33% weniger Schaden von Feinden, die an den Auswirkungen einer Wassermagie-Verhexung leiden. Verbündete in Hörweite, die keine Geister sind, werden um 50...250% der Energiekosten Eurer Elementarzauber geheilt.', 'Nebelform (PvP)', '10..45 Sekunden lang erleidet Ihr 33% weniger Schaden von Feinden, die an den Auswirkungen einer Wassermagie-Verhexung leiden. Immer wenn Ihr einen Elementarzauber wirkt, werden alle Verbündete in Hörweite, die keine Geister sind, um 50..250% der Energiekosten dieses Zaubers geheilt. Dieser Zauber heilt keine Verbündeten mit mehr als 80% Gesundheit.', '(10...45 Sekunden.) Ihr erleidet 33% weniger Schaden von Feinden, die an den Auswirkungen einer Wassermagie-Verhexung leiden. Verbündete in Hörweite, die keine Geister sind, werden um 50...250% der Energiekosten Eurer Elementarzauber geheilt. <i>Heilt keine Verbündeten mit mehr als 80% Gesundheit.</i>'),
(237, 'Wasserdreizack', 'Sendet einen schnellen Wasserdreizack aus, der dem Gegner und bis zu 2 umstehenden Feinden 10..90 Punkte Kälteschaden zufügt, wenn er trifft. Trifft er einen Gegner, der sich bewegt, wird dieser zu Boden geworfen.', 'Schnelles Geschoss: Richtet 10...90 Punkte Kälteschaden an und wirft Feinde, die sich bewegen, zu Boden. Schleudert zwei zusätzliche Geschosse auf umstehende Feinde.', '', '', ''),
(238, 'Nebelrüstung', '8..20 Sekunden lang erhaltet Ihr +10..40 Rüstung und bewegt Euch um 33% schneller.', '8...20 Sekunden: Ihr habt +10...40 Rüstung und bewegt Euch um 33% schneller.', '', '', ''),
(239, 'Abwehr gegen Schaden', 'Errichtet an dieser Position eine Abwehr gegen Schaden. 5..15 Sekunden lang erhalten in diesem Bereich befindliche Verbündete, die keine Geister sind, +1..3 Lebenspunktregeneration. Gruppenmitglieder erhalten außerdem +12..24 Rüstung und zusätzlich +12..24 Rüstung gegen Elementarschaden. Dieser Zauber wird 20 Sekunden lang deaktiviert.', '5...15 Sekunden: Verbündete in dieser Abwehr haben +1...3 Lebenspunktregeneration. Gruppenmitglieder haben +12...24 Rüstung und zusätzlich +12...24 Rüstung gegen Elementarschaden. <i>Geister sind nicht betroffen. Dieser Zauber wird 20 Sekunden lang deaktiviert.</i>', 'Abwehr gegen Schaden (PvP)', 'Errichtet an dieser Position eine Abwehr gegen Schaden. 5..15 Sekunden lang erhalten in diesem Bereich befindliche Verbündete, die keine Geister sind, +1..3 Lebenspunktregeneration, +12..24 Rüstung und zusätzlich +12..24 Rüstung gegen Elementarschaden. Dieser Zauber wird 30 Sekunden lang deaktiviert.', '5...15 Sekunden: Verbündete in dieser Abwehr haben +1...3 Lebenspunktregeneration, +12...24 Rüstung  und zusätzlich +12...24 Rüstung gegen Elementarschaden. <i>Geister sind nicht betroffen. Dieser Zauber wird 30 Sekunden lang deaktiviert.</i>'),
(240, 'Peinigung', 'Dieser Angriff fügt 10..55 Punkte heiligen Schaden zu. Wenn Eurer Ziel angreift erleidet es zusätzlich 10..35 heiligen Schaden.', 'Richtet 10...55 Punkte heiligen Schaden an. Richtet zusätzlich 10...35 Punkte heiligen Schaden an, falls der Gegner gerade angreift.', '', '', ''),
(241, 'Lebensbindung', 'Solange Ihr diese Verzauberung aufrechterhaltet, wird immer dann, wenn der Verbündete bei einem Angriff Schaden erleidet, die Hälfte dieses Schadens auf Euch weitergeleitet. Der dadurch von Euch erlittene Schaden wird um 3..30 Punkte reduziert.', 'Die Hälfte des Schadens, den der Verbündete durch Angriffe erleidet, wird auf Euch weitergeleitet. Der weitergeleitete Schaden wird um 3...30 Punkte reduziert. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(242, 'Balthasars Geist', 'Solange Ihr diese Verzauberung aufrecht erhaltet, erhält der Verbündete (bzw. Ihr selbst), nachdem er Schaden erlitten hat, 2..5 Fünfundzwanzigstel eines Adrenalinstoßes und 1 Energiepunkt.', 'Der Verbündete (bzw. Ihr selbst) erhält Adrenalin und 1 Energiepunkt, wenn er Schaden erleidet.', '', '', ''),
(243, 'Stärke der Ehre', 'Solange Ihr diese Verzauberung aufrechterhaltet, fügt der Verbündete (bzw. Ihr selbst) im Nahkampf zusätzlich 5..25 Punkte Schaden zu.', 'Der Verbündete (bzw. Ihr selbst) richtet im Nahkampf zusätzlich 5...25 Punkte Schaden an.', 'Stärke der Ehre (PvP)', 'Solange Ihr diese Verzauberung aufrechterhaltet, fügt der Verbündete (bzw. Ihr selbst) im Nahkampf zusätzlich 1..5 Punkte Schaden zu.', 'Der Verbündete (bzw. Ihr selbst) richtet im Nahkampf zusätzlich 1...5 Punkte Schaden an.'),
(244, 'Lebenseinstimmung', 'Solange Ihr diese Verzauberung aufrechterhaltet, fügt der Verbündete (bzw. Ihr selbst) mit seinen Angriffen 30% weniger Schaden zu, erhält aber bei der Heilung 14..50% mehr Lebenspunkte.', 'Verbündeter (bzw. Ihr selbst) erhält bei Heilung um 14...50% mehr Lebenspunkte. <i>Er richtet mit Angriffen 30% weniger Schaden an.</i>', '', '', ''),
(245, 'Schutzgeist', '5..23 Sekunden lang verliert der Verbündete (bzw. Ihr selbst) bei einem Schaden aus einem einzelnen Angriff oder Zauber höchstens 10% der maximalen Lebenspunkte.', '5...23 Sekunden: Erlittener Schaden wird auf 10% der maximalen Lebenspunkte des Verbündeten (bzw. von Euch selbst) reduziert.', '', '', ''),
(246, 'Göttliche Intervention', '10 Sekunden lang wird das nächste Mal, wenn der Verbündete (oder Ihr selbst) Schaden erleidet, der tödlich wäre, dieser Schaden aufgehoben und der Verbündete stattdessen für 26..240 Lebenspunkte  geheilt.', '10 Sekunden: Hebt den nächsten Schaden auf, der zum Tod des Verbündeten (bzw. von Euch selbst) führen würde. Aufhebungswirkung: Heilt um 26...240 Lebenspunkte.', '', '', ''),
(247, 'Symbol des Zorns', '5 Sekunden lang erleiden umstehende Gegner am Wirkungsort des Zaubers 8..32 Punkte heiligen Schaden pro Sekunde.', 'Richtet jede Sekunde 8...32 Punkte heiligen Schaden an (5 Sekunden). Trifft umstehende Feinde um Eure anfängliche Position.', '', '', ''),
(248, 'Vergeltung', 'Solange Ihr diese Verzauberung aufrechterhaltet, fügt Vergeltung jedes Mal, wenn der Verbündete (bzw. Ihr selbst) Angriffsschaden erleidet, dem Angreifer ebenfalls Schaden in Höhe von 33% des Schadens als Heiligen Schaden zu (maximal 5..20 Schadenspunkte).', 'Wirft 33% des Schadens durch Angriffe (maximal 5...20 Punkte) auf den Verursacher zurück.', '', '', ''),
(249, 'Heiliger Zorn', 'Während der folgenden 10..30 Sekunden fügt dieser Zauber die nächsten 1..10 Mal, wenn der Verbündete Angriffsschaden erleidet, dem Angreifer 66% des Schadens in Form von heiligem Schaden zu (maximal 5..50 Schadenspunkte).', '(10...30 Sekunden.) Fügt 66% des Schadens durch Angriffe (maximal 5...50 Punkte) dem Verursacher als heiligen Schaden zu. <i>Endet nach 1...10 Schadenszufügungen. Sich selbst als Ziel wählen ist nicht möglich.</i>', '', '', ''),
(250, 'Essenzbindung', 'Solange Ihr diese Verzauberung aufrechterhaltet, bekommt Ihr jedes Mal, wenn Euer Verbündeter (oder Ihr selbst) physischen oder Elementarschaden erleidet, 1 Energiepunkt.', 'Ihr erhaltet jedes Mal 1 Energiepunkt, wenn der Verbündete (bzw. Ihr selbst) physischen Schaden oder Elementarschaden erleidet.', '', '', ''),
(251, 'Geißelheilung', '30 Sekunden lang erleidet der Heiler jedes Mal, wenn er den Zielgegner heilt, 15..80 Punkte heiligen Schaden.', '30 Sekunden: Wenn der Gegner geheilt wird, erleidet der Heiler 15...80 Punkte heiligen Schaden.', '', '', ''),
(252, 'Verbannung', 'Der Gegner erleidet 20..56 Punkte heiligen Schaden. Dieser Zauber fügt herbeigerufenen Kreaturen doppelten Schaden zu.', 'Richtet 20...56 Punkte heiligen Schaden an. Fügt herbeigerufenen Kreaturen doppelten Schaden zu.', '', '', ''),
(253, 'Geißelopfer', '8..20 Sekunden lang opfern der Gegner und umstehende Feinde jedes Mal, wenn sie Leben opfern, das doppelte der Prozentzahl.', 'Verhext den Gegner und umstehende Feinde (8...20 Sekunden). Verdoppelt Lebenspunkteopfer.', '', '', ''),
(254, 'Energischer Geist', '30 Sekunden lang wird der Verbündete (bzw. Ihr selbst) jedes Mal, wenn er angreift oder einen Zauber wirkt, um 5..20 Punkte geheilt.', '30 Sekunden: Heilt jedes Mal um 5...20 Lebenspunkte, wenn der Verbündete (bzw. Ihr selbst) angreift oder einen Zauber wirkt.', '', '', ''),
(255, 'Wachsamer Geist', 'Solange Ihr diese Verzauberung aufrechterhaltet, erfreut sich der Verbündete (bzw. Ihr selbst) einer zusätzlichen Lebenspunktregeneration von 2. Wenn Wachsamer Geist endet, wird er für 30..180 Lebenspunkte geheilt.', '+2 Lebenspunktregeneration. Wirkung am Ende: Heilt um 30...180 Lebenspunkte.', '', '', ''),
(256, 'Gesegnete Aura', 'Solange Ihr diese Verzauberung aufrechterhaltet, halten von Euch gewirkte Mönch-Verzauberungen um 10..35% länger.', 'Von Euch gewirkte Mönch-Verzauberungen halten um 10...35% länger an.', '', '', ''),
(257, 'Aegis', '5..11 Sekunden lang erhalten alle Gruppenmitglieder in Hörweite eine 50%ige Chance, Angriffe zu blocken.', 'Verzaubert alle Gruppenmitglieder in Hörweite (5...11 Sekunden). 50%ige Chance zu blocken.', 'Aegis (PvP)', 'Das andere Gruppenmitglied kann 1..3 Sekunden lang nicht zum Ziel von feindlichen Zaubern werden und Angriffe gegen dieses Gruppenmitglied versagen.', '(1...3 Sekunden) Das andere Gruppenmitglied kann nicht Ziel von feindlichen Zaubern werden. Angriffe gegen dieses Gruppenmitglied versagen. <i>Sich selbst als Ziel wählen nicht möglich.</i>'),
(258, 'Wächter', '2..7 Sekunden lang bekommt der Verbündete (bzw. Ihr selbst) eine 50%ige Chance, Angriffe zu blocken.', '2...7 Sekunden: 50%ige Chance, Angriffe zu blocken.', '', '', ''),
(259, 'Ablenkungsschild', '3..10 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine 75%ige Chance Angriffe zu blocken und erhält 15..30 Rüstung.', '3...10 Sekunden: 75%ige Chance zu blocken. +15...30 Rüstung.', '', '', ''),
(260, 'Glaubensaura', '3 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) bei seiner Heilung 50..100% mehr Lebenspunkte und erleidet 5..50% weniger Schaden.', '3 Sekunden: Der Verbündete (bzw. Ihr selbst) erhält bei seiner Heilung 50...100% mehr Lebenspunkte und erleidet 5...50% weniger Schaden.', '', '', ''),
(261, 'Schild der Regeneration', '5..13 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) eine Lebenspunktregeneration in Höhe von 3..10 und 40 Rüstung.', '5...13 Sekunden: +3...10 Lebenspunktregeneration und +40 Rüstung.', '', '', ''),
(262, 'Schild des Urteils', '8..20 Sekunden lang wird jeder, der den Verbündeten (bzw. Euch selbst) angreift, zu Boden geworfen und erleidet 5..50 Punkte heiligen Schaden.', '8...20 Sekunden: Richtet bei den Verbündeten (bzw. Euch selbst) angreifenden Feinden 5...50 Punkte heiligen Schaden an. Wirft zu Boden.', '', '', ''),
(263, 'Band des Schutzes', 'Solange Ihr diese Verzauberung aufrechterhaltet, kann der Verbündete (bzw. Ihr selbst) immer dann, wenn ihm Schaden zugefügt wird, dadurch nur 5% seiner maximalen Lebenspunkte an Schaden erleiden. Wenn Band des Schutzes Schaden verhindert, verliert Ihr 6..3 Energiepunkte oder der Zauber endet.', 'Verbündeter (bzw. Ihr selbst) kann durch einen einzelnen Angriff oder Zauber nicht mehr als 5% seiner maximalen Lebenspunkte verlieren. <i>Jedes Mal, wenn Schaden reduziert wird, verliert Ihr 6...3 Energiepunkte oder dieser Zauber endet.</i>', '', '', ''),
(264, 'Pazifismus', '8..20 Sekunden lang kann der Gegner nicht angreifen. Diese Wirkung endet, wenn er Schaden erleidet.', '8...20 Sekunden: Gegner kann nicht angreifen. <i>Endet, wenn der Gegner Schaden erleidet.</i>', '', '', ''),
(265, 'Freundschaft', '8..20 Sekunden lang können umstehende Gegner nicht angreifen. Freundschaft endet für jeden Gegner, sobald er Schaden erleidet.', '8...20 Sekunden: Umstehende Feinde können nicht angreifen. <i>Endet bei Feinden, die Schaden erleiden.</i>', '', '', ''),
(266, 'Frieden und Harmonie', 'Der Verbündete (bzw. Ihr selbst) verliert 0..9 Zustände und Verhexungen. 1..3 Sekunden lang enden Zustände und Verhexungen bei diesem Verbündeten um 90% schneller. All Eure Peinigungsgebete werden 20 Sekunden lang deaktiviert.', 'Der Verbündete (bzw. Ihr selbst) verliert 0...9 Zustände und Verhexungen. Zustände und Verhexungen beim Verbündeten enden um 90% schneller (1...3 Sekunden). <i>Deaktiviert Eure Peinigungsgebete (20 Sekunde<sub>sic</sub>).</i>', '', '', ''),
(267, 'Einblick des Richters', '8..20 Sekunden lang fügen Angriffe des Verbündeten (bzw. Eure eigenen) Sakral-Schaden zu und haben eine um 20% erhöhte Rüstungsdurchdringung.', '8...20 Sekunden: Wandelt Angriffe des Verbündeten (bzw. von Euch selbst) in Sakral-Schaden um und fügt +20% Rüstungsdurchdringung hinzu.', '', '', ''),
(268, 'Unnachgiebige Aura', 'Während Ihr diese Verzauberung aufrecht erhaltet, heilen Eure Mönch-Zauber zusätzlich um +15..60% Lebenspunkte. Wenn diese Verzauberung endet, wird nach dem Zufallsprinzip ein anderes Gruppenmitglied mit kompletten Lebens- und Energiepunkten wiederbelebt und unternimmt einen Schattenschritt an Eure Position.', 'Eure Mönch-Zauber heilen zusätzlich um +15...60% Lebenspunkte. Wirkung am Ende: Ein nach dem Zufallsprinzip mit kompletten Lebens- und Energiepunkten wiederbelebtes anderes Gruppenmitglied unternimmt einen Schattenschritt an Eure Position.', 'Unnachgiebige Aura (PvP)', 'Holt ein totes Gruppenmitglied mit vollen Lebens- und Energiepunkten ins Leben zurück. Es stirbt und lässt einen benutzten Kadaver zurück, sobald Ihr die Verzauberung aufhebt oder wenn sie entfernt wird. Beim Tod durch Unnachgiebige Aura gibt es keinen Sterbemalus. (Bei einer Gunst der Götter von 4 oder weniger besteht eine 50%ige Chance für einen Misserfolg.)', 'Das Gruppenmitglied wird wiederbelebt (volle Lebenspunkte, volle Energiepunkte). Wirkung am Ende: Der Verbündete stirbt und hinterlässt einen benutzten Kadaver. Hat keinen Sterbemalus zur Folge. <i>Mindestens Gunst der Götter 5, sonst 50%ige Chance auf Misserfolg.</i>'),
(269, 'Schutzzeichen', '10 Sekunden lang wird der Verbündete (oder Ihr selbst) immer dann, wenn er Schaden erleiden müsste, stattdessen um diese Punktzahl, höchstens jedoch um 6..60 Punkte geheilt. Alle Eure Schutzgebete werden 5 Sekunden lang deaktiviert.', '10 Sekunden: Wandelt erlittenen Schaden in Heilung um (maximal 6...60 Lebenspunkte). <i>Alle Eure Schutzgebete werden deaktiviert (5 Sekunden).</i>', '', '', ''),
(270, 'Lebenssperre', 'Solange Ihr diese Verzauberung aufrechterhaltet, wird dem Verbündeten zugefügter Schaden um 20..50% reduziert. Sind Eure Lebenspunkte unter 50%, wenn der Verbündete Schaden erleidet, endet Lebenssperre.', 'Reduziert Schaden um 20...50%. <i>Sich selbst als Ziel wählen nicht möglich. Habt Ihr weniger als 50% Eurer maximalen Lebenspunkte, wenn der Verbündete Schaden erleidet, endet Lebenssperre.</i>', '', '', ''),
(271, 'Eifererfeuer', 'Während der nächsten 60 Sekunden erleiden jedes Mal, wenn Ihr eine Fertigkeit auf einen Verbündeten (bzw. Euch selbst) anwendet, alle diesen Verbündeten (bzw. Euch selbst) umstehenden Feinde Feuerschaden in Höhe von 5..35 Punkten und Ihr verliert einen Energiepunkt.', '60 Sekunden: Jedes Mal, wenn Ihr eine Fertigkeit auf einen Verbündeten anwendet, erleiden alle umstehenden Feinde um diesen Verbündeten 5...35 Punkte Feuerschaden. <i>Schadenskosten: Ihr verliert 1 Energiepunkte.<sub>sic</sub></i>', '', '', ''),
(272, 'Balthasars Aura', '8 Sekunden lang erleiden umstehende Gegner um den Verbündeten (bzw. Euch selbst) herum pro Sekunde 10..25 Punkte heiligen Schaden.', '8 Sekunden: Richtet pro Sekunde 10...25 Punkte heiligen Schaden bei umstehenden Feinden um den Verbündeten (bzw. Euch selbst) an.', '', '', ''),
(273, 'Zauberbrecher', '5..17 Sekunden lang kann der Verbündete (bzw. Ihr selbst) nicht zum Ziel von feindlichen Zaubern werden.', '(5...17 Sekunden.) Der Verbündete kann nicht zum Ziel von feindlichen Zaubern werden.', '', '', ''),
(274, 'Heilender Samen', '10 Sekunden lang erhalten der anvisierte Verbündete und alle umstehenden Verbündeten 3..30 Lebenspunkte, wenn der Verbündete Schaden erleidet.', '10 Sekunden: Der Verbündete und umstehende Verbündete erhalten jedes Mal 3...30 Lebenspunkte, wenn dieser Verbündete Schaden erleidet. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(275, 'Zustand bessern', 'Entfernt einen Zustand (Gift, Krankheit, Blindheit, Benommenheit, Blutung, Verkrüppelung, Brennen, Schwäche, Beschädigte Rüstung oder Tiefe Wunde) von einem Verbündeten. Wenn ein Zustand entfernt wurde, wird der Verbündete um 5..70 Lebenspunkte geheilt.', 'Entfernt einen Zustand. Entfernungswirkung: Heilt um 5...70 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(276, 'Zustand wiederherstellen', 'Entfernt alle Zustände (Gift, Krankheit, Blindheit, Benommenheit, Blutung, Verkrüppelung, Brennen, Schwäche, Beschädigte Rüstung und Tiefe Wunde) vom Verbündeten. Dieser Verbündete wird für jeden entfernten Zustand für 10..70 Punkte geheilt.', 'Entfernt alle Zustände. Entfernungswirkung: Heilt für jeden entfernten Zustand um 10...70 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(277, 'Leiden heilen', 'Entfernt einen Zustand (Gift, Krankheit, Blindheit, Benommenheit, Blutung, Verkrüppelung, Brennen, Schwäche, Beschädigte Rüstung oder Tiefe Wunde) vom Verbündeten (bzw. Euch selbst). Für jeden verbleibenden Zustand wird dieser Verbündete um 5..70 Lebenspunkte geheilt.', 'Entfernt einen Zustand. Entfernungswirkung: Heilt um 5...70 Lebenspunkte pro verbleibendem Zustand.', '', '', ''),
(278, 'Zustände reinigen', 'Der Verbündete (bzw. Ihr selbst) verliert alle Zustände Zustände (Gift, Krankheit, Blindheit, Benommenheit, Blutung, Verkrüppelung, Brennen, Schwäche, Beschädigte Rüstung und Tiefe Wunde).', 'Entfernt alle Zustände.', '', '', ''),
(279, 'Göttliche Heilung', 'Heilt Euch selbst und Gruppenmitglieder in Hörweite um 15..60 Punkte.', 'Heilt Euch selbst und Gruppenmitglieder in Hörweite um 15...60 Punkte.', '', '', ''),
(280, 'Gebiet heilen', 'Heilt Euch selbst und alle umstehenden Geschöpfe um 30..180 Punkte.', 'Heilt Euch und umstehende Verbündete und Feinde um 30...180 Lebenspunkte.', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(281, 'Fürbitte der Heilung', 'Heilt Verbündeten (bzw. Euch selbst) um 20..70 Punkte.', 'Heilt um 20...70 Lebenspunkte.', '', '', ''),
(282, 'Wort der Heilung', 'Heilt Verbündeten (bzw. Euch selbst) um 5..100 Punkte. Er wird für zusätzliche 30..115 Lebenspunkte geheilt, wenn seine Lebenspunkte unter 50% liegen.', 'Heilt um 5...100 Lebenspunkte. Heilt um weitere 30...115 Lebenspunkte, falls der Verbündete (bzw. Ihr selbst) weniger als 50% seiner maximalen Lebenspunkte hat.', '', '', ''),
(283, 'Dwaynas Kuss', 'Heilt Verbündeten um 15..60 Punkte und um zusätzlich 10..35 Punkte für jede Verzauberung oder Verhexung, die auf diesem Verbündeten liegt.', 'Heilt um 15...60 Lebenspunkte. Heilt für jede Verzauberung und Verhexung auf dem Verbündeten um weitere 10...35 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(284, 'Göttlicher Segen', 'Solange Ihr diese Verzauberung aufrechterhaltet, wird jedes Mal, wenn Ihr ein Schutzgebete- oder Gunst der Götter-Zauber mit Ziel auf einen Verbündeten (bzw. Euch selbst) wirkt, das Ziel um 15..60 Lebenspunkte geheilt und Ihr verliert 1 Energiepunkt.', 'Wenn Ihr eine Schutzgebet oder Gunst der Götter auf einen Verbündeten wirkt, wird dieser um 15...60 Lebenspunkte geheilt. <i>Heilkosten: Ihr verliert 1 Energiepunkt.</i>', '', '', ''),
(285, 'Heilende Hände', 'Während der nächsten 10 Sekunden wird der Verbündete (bzw. Ihr selbst) jedes Mal, wenn er Schaden erleidet, um 5..35 Lebenspunkte geheilt.', '10 Sekunden: Heilt jedes Mal um 5...35 Lebenspunkte, wenn das Ziel Schaden erleidet.', '', '', ''),
(286, 'Anderen heilen', 'Heilt Verbündeten für 35..180 Lebenspunkte.', 'Heilt um 35...180 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(287, 'Gruppe heilen', 'Heilt die ganze Gruppe um 30..75 Punkte.', 'Heilt die gesamte Gruppe um 30...75 Lebenspunkte.', 'Gruppe heilen (PvP)', 'Heilt die ganze Gruppe um 30..75 Punkte.', 'Heilt die gesamte Gruppe um 30...75 Lebenspunkte.'),
(288, 'Heilender Hauch', '15 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) eine zusätzliche Lebenspunktregeneration von 4..9.', '15 Sekunden: +4...9 Lebenspunktregeneration.', '', '', ''),
(289, 'Lebendige Segnung', 'Solange Ihr diese Verzauberung aufrechterhaltet, erhält der Verbündete (bzw. Ihr selbst) +40..200 maximale Lebenspunkte.', '+40...200 maximale Lebenspunkte.', '', '', ''),
(290, 'Genesung', 'Solange Ihr diese Verzauberung aufrechterhaltet, erfreut sich der Verbündete (bzw. Ihr selbst) einer zusätzlichen Lebenspunktregeneration von 1..4.', '+1...4 Lebenspunktregeneration.', '', '', ''),
(291, 'Ersatzleben', 'Solange Ihr diese Verzauberung aufrecht erhaltet, heilt Ihr Euch jedes Mal, wenn der Verbündete (bzw. Ihr selbst) einen Gegner trifft, um 2..17 Lebenspunkte.', 'Ihr heilt Euch jedes Mal um 2...17 Lebenspunkte, wenn der Verbündete (bzw. Ihr selbst) einen Gegner trifft.', '', '', ''),
(292, 'Leben einflößen', 'Ihr verliert die Hälfte Eurer aktuellen Lebenspunkte. Der Verbündete wird um 100..136% der Punkte geheilt, die Ihr verloren habt.', 'Heilt um 100...136% der Hälfte Eurer aktuellen Lebenspunkte. <i>Ihr verliert die Hälfte Eurer aktuellen Lebenspunkte. Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(293, 'Siegel der Hingabe', 'Heilt Verbündeten (bzw. Euch selbst) um 14..100 Lebenspunkte.', 'Heilt um 14...100 Lebenspunkte.', '', '', ''),
(294, 'Siegel des Urteils', 'Der Gegner wird zu Boden geworfen. Er und alle umstehenden Feinde erleiden 15..75 Punkte heiligen Schaden.', 'Wirft Gegner zu Boden<sub>sic</sub>. Richtet beim Gegner und umstehenden Feinden 15...75 Punkte heiligen Schaden an.', 'Siegel des Urteils (PvP)', 'Der Gegner wird zu Boden geworfen. Er und alle umstehenden Feinde erleiden 5..50 Punkte heiligen Schaden. Dieses Siegel hat nur halbe Reichweite.', 'Wirft Gegner zu Boden<sub>sic</sub>. Richtet beim Gegner und umstehenden Feinden 5...50 Punkte heiligen Schaden an.'),
(295, 'Siegel der Reinigung', 'Entfernt alle Verhexungen und Zustände vom Verbündeten (bzw. Euch selbst). Für jede entfernte Verhexung und jeden entfernten Zustand verliert Ihr jeweils 10 Energiepunkte.', 'Entfernt alle Verhexungen und Zustände. <i>Entfernungskosten: Je 10 Energiepunkte.</i>', '', '', ''),
(296, 'Siegel des Ruins', 'Gegner erleidet 26..56 Punkte heiligen Schaden. Falls er gerade angreift, wird er zu Boden geworfen.', 'Richtet 26...56 Punkte heiligen Schaden an. Wirft den Gegner zu Boden, falls dieser gerade angreift.', '', '', ''),
(297, 'Gesegnetes Siegel', 'Für jede Verzauberung, die Ihr aufrechterhaltet, bekommt Ihr 3 Energiepunkte, höchstens aber 3..24 Punkte.', 'Ihr bekommt 3 Energiepunkte für jede Verzauberung, die Ihr aufrechterhaltet. <i>Auf diese Weise bekommt Ihr höchstens 3...24 Energiepunkte.</i>', '', '', ''),
(298, 'Märtyrer', 'Übertragt alle Zustände und ihre verbleibende Dauer von Euren Verbündeten auf Euch selbst.', 'Überträgt alle Zustände aller Verbündeten auf Euch.', '', '', ''),
(299, 'Schützende Hände', '8 Sekunden lang werden der Schaden, den der Verbündete (bzw. Ihr selbst) erleidet, und die Lebenspunkte, die diesem durch Fertigkeiten entzogen werden, um 3..18 Punkte reduziert. Wenn Schützende Hände endet, wird der Verbündete um 5..50 Lebenspunkte geheilt.', '(8 Sekunden.) Reduziert erlittenen Schaden und entzogene Lebenspunkte um 3...18 Punkte. Wirkung am Ende: Heilt um 5...50 Lebenspunkte.', '', '', ''),
(300, 'Gebot der Reinheit', 'Alle Verzauberungen gehen verloren. Für jede verlorene Verzauberung erhaltet Ihr 0..80 Lebenspunkte und verliert eine Verhexung sowie einen Zustand (maximal 1..8 Verhexungen und Zustände).', 'Für jede Verzauberung auf Euch erhaltet Ihr 0...80 Lebenspunkte und verliert eine Verhexung sowie einen Zustand (maximal 1...8 Verhexung und Zustand). <i>Ihr verliert alle Verzauberungen.</i>', '', '', ''),
(301, 'Verhexung entfernen', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst).', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung.', '', '', ''),
(302, 'Verhexung zerschlagen', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). Beim Entfernen einer Verhexung erleiden Gegner in dem Bereich 10..85 Punkte heiligen Schaden.', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung. Entfernungswirkung: Richtet bei Feinden im Bereich des Verbündeten (bzw. von Euch selbst) 10...85 Punkte heiligen Schaden an.', '', '', ''),
(303, 'Verhexungen umwandeln', 'Entfernt alle Verhexungen vom Verbündeten. Er erhält 8..20 Sekunden lang +10 Rüstung, falls eine Nekromanten-Verhexung entfernt wurde.', 'Entfernt alle Verhexungen; +10 Rüstung bei entfernter Nekromanten-Verhexung (8...20 Sekunden). <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(304, 'Licht von Dwayna', 'Alle toten Gruppenmitglieder in dem Bereich werden wieder belebt und kehren mit 25% ihrer Lebenspunkte und 0 Energiepunkten ins Leben zurück.', 'Alle toten Gruppenmitglieder im Bereich werden wiederbelebt (25% Lebenspunkte, 0 Energiepunkte).', '', '', ''),
(305, 'Wiederbeleben', 'Das als Ziel ausgewählte Gruppenmitglied wird wiederbelebt und kehrt mit 25% seiner Lebenspunkte und 0 Energiepunkten ins Leben zurück.', 'Das Gruppenmitglied wird wiederbelebt (25% Lebenspunkte, 0 Energiepunkte).', '', '', ''),
(306, 'Wiedergeburt', 'Das als Ziel ausgewählte Gruppenmitglied wird wieder belebt, kehrt mit 25% seiner Lebenspunkte und 0 Energiepunkten ins Leben zurück und unternimmt einen Schattenschritt zu Eurer aktuellen Position. Alle seine Fertigkeiten werden 10..3 Sekunden lang deaktiviert. Dieser Zauber verbraucht Eure gesamten verbliebenen Energiepunkte.', 'Das Gruppenmitglied wird wiederbelebt (25% Lebenspunkte, 0 Energiepunkte) und unternimmt einen Schattenschritt zu Euch. <i>Deaktiviert die Fertigkeiten des Ziels (10...3 Sekunden). Ihr verliert alle Energiepunkte.</i>', '', '', ''),
(307, 'Umkehrung des Schicksals', '8 Sekunden lang wird der Verbündete (bzw. Ihr selbst), wenn er das nächste Mal Schaden erleiden oder Lebenspunkte entzogen bekommen würde, stattdessen um den entsprechenden Wert geheilt (maximal 15..80).', '(8 Sekunden.) Wandelt den nächsten erlittenen Schaden bzw. die nächsten entzogenen Lebenspunkte (maximal 15...80 Punkte) in Heilung um.', '', '', ''),
(308, 'Beistand', 'Solange Ihr diese Verzauberung aufrechterhaltet, erfreut sich der Verbündete einer Lebenspunkt- und Energieregeneration von +1. Ihr verliert jedoch 1 Energiepunkt für jeden Zauber, den er wirkt.', '+1 Lebenspunktregeneration und +1 Energieregeneration. <i>Sich selbst als Ziel wählen nicht möglich. Ihr verliert jedes Mal 1 Energiepunkt, wenn der Verbündete einen Zauber wirkt.</i>', '', '', ''),
(309, 'Heiliger Schleier', 'Solange Ihr diese Verzauberung aufrechterhaltet, dauert das Wirken von Verhexungen auf einen Verbündeten (bzw. Euch selbst) doppelt so lange. Wenn Heiliger Schleier endet, wird eine Verhexung vom entsprechenden Verbündeten entfernt.', 'Verdoppelt die Wirkzeit von Verhexungen, die auf den Verbündeten (bzw. Euch selbst) angewendet werden. Wirkung am Ende: Entfernt eine Verhexung.', '', '', ''),
(310, 'Göttlicher Geist', '1..14 Sekunden lang kostet Euch das Wirken von Mönch-Zaubern 5 Energiepunkte weniger. (Mindestkosten: 1 Energiepunkt).', '1...14 Sekunden: Mönch-Zauber kosten Euch 5 Energiepunkte weniger. <i>Mindestkosten: 1 Energiepunkt.</i>', '', '', ''),
(311, 'Zustände entziehen', 'Alle negativen Zustände werden vom Verbündeten auf Euch übertragen. Für jeden übertragenen Zustand werdet Ihr um 6..26 Lebenspunkte geheilt.', 'Überträgt alle Zustände des Verbündeten auf Euch. Übertragungswirkung: Ihr heilt Euch für jeden Zustand um 6...26 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(312, 'Heiliger Schlag', 'Der berührte Gegner erleidet 10..55 Punkte heiligen Schaden. Wenn Euer Ziel am Boden liegt, erleidet es zusätzlich 10..55 heiligen Schaden.', 'Richtet 10...55 Punkte heiligen Schaden an. Richtet zusätzlich 10...55 heiligen Schaden an, falls der Gegner zu Boden geworfen ist.', '', '', ''),
(313, 'Heilende Berührung', 'Berührt einen Verbündeten und heilt ihn für 16..60 Lebenspunkte. Der Gunst-der-Götter-Bonus wird bei diesem Zauber verdoppelt.', 'Heilt um 16...60 Lebenspunkte. Gunst-der-Götter-Bonus wird bei diesem Zauber verdoppelt.', '', '', ''),
(314, 'Leben erneuern', 'Berührt den Körper eines gefallenen Gruppenmitglieds. Dieses wird mit 20..65% seines Lebens und 42..90% seiner Energie wiederbelebt.', 'Das Gruppenmitglied wird wiederbelebt (20...65% Lebenspunkte, 42...90% Energiepunkte).', '', '', ''),
(315, 'Rache', 'Das tote Gruppenmitglied wird mit allen Lebenspunkten und voller Energie wiederbelebt. 30 Sekunden lang fügt dieses Gruppenmitglied 25% mehr Schaden zu. Wenn diese Verzauberung endet, stirbt es. Beim Tod unter dem Einfluss dieser Verzauberung gibt es keinen Sterbemalus.', '30 Sekunden: Das Gruppenmitglied wird wiederbelebt (volle Lebenspunkte, volle Energiepunkte). Der Verbündete richtet 25% mehr Schaden an und erhält keinen Sterbemalus. <i>Wirkung am Ende: Der Verbündete stirbt.</i>', '', '', ''),
(316, 'Bis ans Limit!', 'Für jeden Gegner in Hörweite (höchstens 1..6) erhaltet Ihr einen Adrenalinstoß. 10..20 Sekunden lang werden Eure maximalen Lebenspunkte um 10..60 erhöht.', '(10...20 Sekunden.) Eure maximalen Lebenspunkte werden um 10...60 erhöht. Anfangswirkung: Ihr erhaltet für jeden Feind in Hörweite einen Adrenalinstoß (maximal 1...6).', '', '', ''),
(317, 'Kampfwut', '5..20 Sekunden lang bewegt Ihr Euch um 33% schneller und Ihr erhaltet die doppelte Menge Adrenalin aus Angriffen. Kampfwut endet, wenn Ihr eine Nicht-Adrenalinfertigkeit verwendet.', '5...20 Sekunden: Ihr bewegt Euch um 33% schneller und erhaltet die doppelte Menge Adrenalin aus Euren Angriffen. <i>Endet, wenn Ihr eine Nicht-Angriffsfertigkeit verwendet.</i>', '', '', ''),
(318, 'Schmerz trotzen', '20 Sekunden lang habt Ihr zusätzlich 90..300 Lebenspunkte, zusätzliche 20 Punkte Rüstung und Ihr erleidet 1..10 Punkte weniger Schaden.', '20 Sekunden: Ihr habt +90...300 maximale Lebenspunkte, +20 Rüstung und erleidet -1...10 Punkte weniger Schaden.', 'Schmerz trotzen (PvP)', '20 Sekunden lang habt Ihr zusätzlich 90..300 Lebenspunkte und zusätzlich 20 Punkte Rüstung.', '20 Sekunden: Ihr habt +90...300 maximale Lebenspunkte und +20 Rüstung.'),
(319, 'Ansturm', '8..20 Sekunden lang bewegt Ihr Euch um 25% schneller als normal.', '8...20 Sekunden: Ihr bewegt Euch um 25% schneller.', '', '', ''),
(320, 'Lähmung', 'Wenn dieser Angriff trifft, erleidet Euer Ziel 3..15 Sekunden lang eine Verkrüppelung.', 'Verursacht Zustand Verkrüppelung 3...15 Sekunden).', '', '', ''),
(321, 'Rundumschlag', 'Wenn dieser Angriff erfolgreich ist, dann erfolgt ein kritischer Treffer und jegliche Haltung, die von Eurem Gegner eingenommen wird, endet. Ein Blocken ist bei diesem Angriff nicht möglich. Nach dem Verwenden dieser Fertigkeit geht alles Adrenalin verloren.', 'Immer ein kritischer Treffer. Entfernt eine Haltung. Kann nicht geblockt werden. <i>Ihr verliert alles Adrenalin.</i>', '', '', ''),
(322, 'Kraftangriff', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 10..40 Punkte zusätzlichen Schaden zu.', 'Richtet +10...40 Punkte Schaden an.', '', '', ''),
(323, 'Verzweiflungsschlag', 'Wenn dieser Angriff trifft, fügt Ihr 10..40 Punkte zusätzlichen Schaden zu und Euer Ziel leidet unter einem der folgenden Zustände: Tiefe Wunde (20 Sekunden lang), Schwäche (20 Sekunden lang), Blutung (25 Sekunden lang) oder Verkrüppelung (15 Sekunden lang). Nachdem Ihr einen Verzweiflungschlag durchgeführt habt, werdet Ihr zu Boden geworfen.', 'Richtet +10...40 Punkte Schaden an. Verursacht willkürlich einen der folgenden Zustände: Tiefe Wunde (20 Sekunden), Schwäche (20 Sekunden), Blutung (25 Sekunden) oder Verkrüppelung (15 Sekunden). <i>Ihr werdet zu Boden geworfen.</i>', '', '', ''),
(324, 'Rausch des Sieges', 'Wenn dieser Schlag trifft, verursacht Ihr +20..40 Punkte Schaden. Wenn Ihr mehr Lebenspunkte als der Gegner habt, erhaltet Ihr 1..2 Adrenalinstöße.', 'Richtet +20...40 Punkte Schaden an. Wenn Ihr mehr Lebenspunkte als der Gegner habt, erhaltet Ihr 1...2 Punkte Adrenalin.', '', '', ''),
(325, 'Ablenkungsschlag', 'Richtet Eure Waffen auf das Ziel. Dadurch fügt Ihr keinen Schaden zu, unterbrecht aber die aktuelle Aktion des Ziels (und die Aktionen von umstehenden Gegnern bei Eurem Ziel).', 'Greift den Gegner und umstehende Feinde an. Unterbricht eine Aktion. <i>Treffer verursachen keinen Schaden.</i>', '', '', ''),
(326, 'Schlag des Beschützers', 'Trifft dieser Schlag einen Gegner in der Bewegung, fügt Ihr 10..40 Punkte zusätzlichen Schaden zu.', 'Richtet +10...40 Punkte Schaden an, falls der Gegner sich bewegt.', '', '', ''),
(327, 'Greifenschlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff geblockt wird, wird Euer Ziel zu Boden geworfen und erleidet 10..34 Punkte Schaden.', 'Richtet +5...20 Punkte Schaden an. Wirft zu Boden und richtet 10...34 Punkte Schaden an, falls geblockt.', '', '', ''),
(328, 'Reiner Schlag', 'Wenn Reiner Schlag trifft, fügt Ihr +1..30 Punkte Schaden zu. Wenn Ihr keine Haltung einnehmt, ist ein Blocken bei Reinem Schlag nicht möglich.', 'Richtet +1...30 Punkte Schaden an. Kann nicht geblockt werden, es sei denn, Ihr nehmt eine Haltung ein.', '', '', ''),
(329, 'Schädel spalten', 'Wenn dieser Angriff erfolgreich ist, unterbricht er die aktuelle Aktion des Ziels. Wenn der Gegner einen Zauber wirkt, leidet er 10 Sekunden lang unter Benommenheit.', 'Unterbricht eine Aktion. Verursacht Zustand Benommenheit (10 Sekunden), falls der Gegner einen Zauber wirkt.', '', '', ''),
(330, 'Zyklonaxt', 'Führt mit kreisender Axt einen Angriff gegen alle umstehenden Gegner für 4..12 Punkte zusätzlichen Schaden aus.', 'Richtet bei allen umstehenden Feinden +4...12 Punkte Schaden an.', '', '', ''),
(331, 'Hammerstreich', 'Alles Adrenalin geht verloren. Trifft Hammerstreich, wird Euer Ziel zu Boden geworfen.', 'Wirft zu Boden. <i>Ihr verliert alles Adrenalin.</i>', '', '', ''),
(332, 'Bullenstoß', 'Trifft dieser Angriff einen sich bewegenden Gegner, fügt Ihr 5..30 Punkte zusätzlichen Schaden zu und Euer Ziel wird zu Boden geworfen.', 'Richtet 5...30 Punkte Schaden an. Wirft zu Boden. <i>Gegner muss sich bewegen, sonst keine Wirkung.</i>', '', '', ''),
(333, 'Ich werde Euch rächen!', 'Für jeden toten Verbündeten auf dem Kompass erhaltet Ihr 10 Sekunden lang eine Lebenspunktregeneration von +3..7 und der Abstand zwischen Euren Angriffen sinkt um 25%.', 'Ihr habt +3...7 Lebenspunktregeneration und greift um 25% schneller an (10 Sekunden pro totem Verbündetem).', '', '', ''),
(334, 'Harkenaxt', 'Wenn dieser Angriff einen Gegner trifft, der an einer tiefen Wunde leidet, fügt Ihr 1..10 Punkte zusätzlichen Schaden zu und dieser Gegner erleidet 15 Sekunden lang eine Verkrüppelung.', 'Richtet +1...10 Punkte Schaden an und verursacht Zustand Verkrüppelung (15 Sekunden), falls Gegner eine Tiefe Wunde hat.', '', '', ''),
(335, 'Spalten', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 10..30 Punkte zusätzlichen Schaden zu.', 'Richtet +10...30 Punkte Schaden an.', '', '', ''),
(336, 'Henkerschlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 10..40 Punkte zusätzlichen Schaden zu.', 'Richtet +10...40 Punkte Schaden an.', '', '', ''),
(337, 'Zerstückeln', 'Wenn dieser Axtschlag trifft, fügt er dem Gegner eine Tiefe Wunde zu, die seine maximalen Lebenspunkte 5..20 Sekunden lang um 20% verringert.', 'Verursacht Zustand Tiefe Wunde (5...20 Sekunden).', '', '', ''),
(338, 'Ausweiden', 'Wenn Ausweiden trifft, fügt Ihr 1..31 Punkte zusätzlichen Schaden und eine Tiefe Wunde zu. Dadurch werden die maximalen Lebenspunkte Eures Ziels 5..20 Sekunden lang um 20% verringert.', 'Richtet +1...31 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden).', '', '', ''),
(339, 'Durchdringender Schlag', 'Wenn dieser Angriff erfolgreich ist, trefft Ihr für 5..20 Punkte zusätzlichen Schaden. Dieser Axtangriff hat eine Rüstungsdurchdringung von 20%.', 'Richtet +5...20 Punkte Schaden an. 20% Rüstungsdurchdringung.', '', '', ''),
(340, 'Unterbrechender Hieb', 'Wenn dieser Angriff erfolgreich ist, unterbricht er die aktuelle Aktion des Ziels. Beruhte diese Aktion auf einer Fertigkeit, wird diese Fertigkeit für weitere 20 Sekunden deaktiviert.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Unterbrochene Fertigkeit wird +20 Sekunden lang deaktiviert.', '', '', ''),
(341, 'Schneller Hieb', 'Wenn dieser Angriff trifft, fügt Ihr 1..20 Punkte zusätzlichen Schaden zu. Wird Schneller Hieb geblockt, erleidet Euer Ziel 20 Sekunden lang eine Tiefe Wunde und 1..20 Punkte zusätzlichen Schaden.', 'Richtet +1...20 Punkte Schaden an. Richtet 1...20 Punkte Schaden an und verursacht Zustand Tiefe Wunde (20 Sekunden), falls geblockt.', '', '', ''),
(342, 'Axtdrehung', 'Wenn dieser Angriff einen Gegner trifft, der an einer tiefen Wunde leidet, fügt Ihr 1..20 Punkte mehr Schaden zu und dieser Gegner leidet 20 Sekunden lang unter Schwäche.', 'Richtet +1...20 Punkte Schaden an und verursacht Zustand Schwäche (20 Sekunden), falls Gegner eine Tiefe Wunde hat.', '', '', ''),
(343, 'Für höhere Gerechtigkeit!', '20 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr Adrenalin bekommt, um 100% mehr.', '20 Sekunden: Ihr erhaltet 100% mehr Adrenalin.', '"Für höhere Gerechtigkeit!" (PvP)', '8 Sekunden erhaltet Ihr für jeden Treffer, den Ihr landet, 1 zusätzlichen Adrenalinstoß.', ''),
(344, 'Schauer', '5 Sekunden lang ist der Abstand zwischen Euren Angriffen um 33% niedriger, aber Ihr fügt mit Eurer Waffe 25% weniger Basis-Schaden zu.', '5 Sekunden: Ihr greift um 33% schneller an. <i>Ihr richtet 25% weniger Schaden an.</i>', '', '', ''),
(345, 'Verteidigungshaltung', '1..5 Sekunden lang habt Ihr eine 75%ige Chance, Nahkampf- und Geschossangriffe zu blocken. Wenn Verteidigungshaltung endet, erhaltet Ihr für jede Nahkampfangriffsfertigkeit, die Ihr besitzt, einen Adrenalinstoß (maximal 0..4).', '(1...5 Sekunden.) Ihr habt eine 75%ige Chance zu blocken. Wirkung am Ende: Ihr erhaltet für jede Nahkampfangriffsfertigkeit, die Ihr besitzt, einen Adrenalinstoß (maximal 0...4).', '', '', ''),
(346, 'Raserei', '8 Sekunden lang greift Ihr um 33% schneller an, erleidet aber doppelten Schaden.', '8 Sekunden: Ihr greift um 33% schneller an. <i>Ihr erleidet doppelten Schaden.</i>', '', '', ''),
(347, 'Schmerz ertragen', '7..18 Sekunden lang habt Ihr zusätzliche 90..300 Lebenspunkte.', '7...18 Sekunden: Ihr habt +90...300 maximale Lebenspunkte.', '', '', ''),
(348, 'Passt auf Euch auf!', 'Gruppenmitglieder in Hörweite erhalten 10 Sekunden lang +5..25 Rüstung. Dieser Schrei endet nach 10 Angriffen.', '10 Sekunden: Gruppenmitglieder in Hörweite haben +5...25 Rüstung. <i>Endet nach 10 erhaltenen Angriffen.</i>', '"Passt auf Euch auf!" (PvP)', 'Gruppenmitglieder in Hörweite erhalten 10 Sekunden lang +5..25 Rüstung. Dieser Schrei endet nach 1..3 Angriffen.', ''),
(349, 'Sprinten', '8..14 Sekunden lang bewegt Ihr Euch um 25% schneller als normal.', '8...14 Sekunden: Ihr bewegt Euch um 25% schneller.', '', '', ''),
(350, 'Bauchklatscher', 'Trifft dieser Angriff einen am Boden liegenden Gegner, führt die entstehende Staubwolke bei umstehenden Gegnern 3..7 Sekunden lang zu Blindheit.', 'Verursacht bei umstehenden Feinden Zustand Blindheit (3...7 Sekunden), falls Gegner zu Boden geworfen ist.', '', '', ''),
(351, 'Mächtiger Schlag', 'Bei einem Treffer fügt dieser Angriff 10..40 Punkte zusätzlichen Schaden zu.', 'Richtet +10...40 Punkte Schaden an.', '', '', ''),
(352, 'Vernichtender Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 1..20 Punkte zusätzlichen Schaden zu. Wenn Ihr einen Feind am Boden trefft, fügt Ihr eine Tiefe Wunde zu. Dadurch werden die maximalen Lebenspunkte Eures Ziels 5..20 Sekunden lang um 20% verringert.', 'Richtet +1...20 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls Gegner zu Boden geworfen ist.', '', '', ''),
(353, 'Grober Schwung', 'Greift alle umstehenden Feinde an. Jeder Feind, den Ihr trefft, erleidet +1..20 Schaden.', 'Greift alle umstehenden Feinde an und richtet +1...20 Punkte Schaden an.', '', '', ''),
(354, 'Erdrüttler', 'Wenn Erdrüttler trifft, werden der Gegner und alle ihn umstehenden Feinde zu Boden geworfen. (Bei einer Hammerbeherrschung von 4 oder weniger besteht eine 50%ige Chance für einen Misserfolg.)', 'Wirft Gegner und umstehende Feinde zu Boden. <i>Mindestens Hammerbeherrschung 5, sonst 50%ige Chance auf Misserfolg.</i>', '', '', ''),
(355, 'Verheerender Hammer', 'Trifft Verheerender Hammer, wird Euer Ziel zu Boden geworfen und leidet 5..20 Sekunden lang unter Schwäche.', 'Wirft zu Boden. Verursacht Zustand Schwäche (5...20 Sekunden).', '', '', ''),
(356, 'Brutaler Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 5..20 Punkte zusätzlichen Schaden zu. Wenn Brutaler Schlag geblockt wird, wird Euer Ziel zu Boden geworfen und erleidet 5..20 Punkte Schaden.', 'Richtet +5...20 Punkte Schaden an. Richtet 5...20 Punkte Schaden an und wirft zu Boden, falls geblockt.', '', '', ''),
(357, 'Gegenschlag', 'Trifft dieser Angriff einen angreifenden Gegner, wird dieser Gegner zu Boden geworfen.', 'Wirft den Gegner zu Boden, falls dieser gerade angreift.', '', '', ''),
(358, 'Rückenbrecher', 'Wenn Rückenbrecher trifft, verursacht Ihr +1..20 Punkte Schaden und Euer Ziel wird zu Boden geworfen. Wenn Ihr mindestens 8 Stärke habt, dauert dieser Effekt 4 Sekunden.', 'Richtet +1...20 Punkte Schaden an. Wirft den Gegner zu Boden. Dieser Effekt wirkt bei Stärke 8 oder höher 4 Sekunden lang.', '', '', ''),
(359, 'Heftiger Schlag', 'Alles Adrenalin geht verloren. Trifft dieser Angriff einen Gegner, der unter Schwäche leidet, wird er zu Boden geworfen und Ihr fügt 1..30 Punkte zusätzlichen Schaden zu.', 'Richtet +1...30 Punkte Schaden an und wirft den Gegner zu Boden, falls dieser geschwächt ist. <i>Ihr verliert alles Adrenalin.</i>', '', '', ''),
(360, 'Umwerfender Schlag', 'Wenn dieser Hammerschlag trifft, leidet Euer Ziel 5..20 Sekunden lang unter Schwäche.', 'Verursacht Zustand Schwäche (5...20 Sekunden).', '', '', ''),
(361, 'Dolyak-Siegel', '8..20 Sekunden lang habt Ihr +10..40 Rüstung und könnt nicht zu Boden geworfen werden, aber Eure Bewegungen sind um 75% verlangsamt.', '8...20 Sekunden: Ihr habt +10...40 Rüstung und könnt nicht zu Boden geworfen werden. <i>Ihr bewegt Euch um 75% langsamer.</i>', '', '', ''),
(362, 'Verschlagenheit des Kriegers', '5..11 Sekunden lang ist ein Blocken bei Euren Nahkampfangriffen nicht möglich.', '5...11 Sekunden: Eure Nahkampfangriffe können nicht geblockt werden.', '', '', ''),
(363, 'Heftiger Schildschlag', '5..11 Sekunden lang wird, wenn Ihr einen Schild benutzt, die nächste Angriffsfertigkeit gegen Euch geblockt. Falls es sich dabei um eine Nahkampffertigkeit gehandelt hat, wird Euer Angreifer zu Boden geworfen und diese Fertigkeit wird für weitere 15 Sekunden deaktiviert.', '5...11 Sekunden: Ihr blockt die nächste Angriffsfertigkeit. Wirft den Angreifer im Fall einer Nahkampffertigkeit zu Boden und fügt +15 Sekunden zur Aufladezeit hinzu. <i>Keine Wirkung, falls Ihr keinen Schild benutzt.</i>', '', '', ''),
(364, 'Angriff!', 'Alle Verbündeten in Hörweite verlieren den Zustand Verkrüppelung. 5..13 Sekunden lang bewegen sich diese Verbündeten um 33% schneller als normal.', '(5...13 Sekunden.) Verbündete in Hörweite bewegen sich um 33% schneller. Anfangswirkung: Diese Verbündeten verlieren den Zustand Verkrüppelung.', '', '', ''),
(365, 'Der Sieg ist mein!', 'Ihr erhaltet für jeden Zustand, unter dem der Gegner leidet, 10..68 Lebenspunkte und 3..7 Energiepunkte.', 'Ihr erhaltet für jeden Zustand des Gegners 10...68 Lebenspunkte und 3...7 Energiepunkte.', '', '', ''),
(366, 'Fürchtet mich!', 'Alle umstehenden Gegner verlieren 1..4 Energiepunkte. 1..15 Sekunden lang erhöht sich Eure Chance auf kritische Treffer durch Nahkampfangriffe auf stationäre Feinde um +5..30%.', '(1...15 Sekunden.) Erhöht die Chance auf kritische Treffer durch Nahkampfangriffe auf stationäre Feinde um +5...30%. Anfangswirkung: Feinde in der Nähe verlieren 1...4 Energiepunkte.', '', '', ''),
(367, 'Schilde hoch!', '5..11 Sekunden lang erhaltet Ihr und Eure Gruppenmitglieder in Hörweite +60 Punkte Rüstung gegen Geschosse.', '5...11 Sekunden: Gruppenmitglieder in Hörweite haben +60 Rüstung gegen Geschossangriffe.', '', '', ''),
(368, 'Ich überlebe!', 'Ihr erfreut Euch einer Lebenspunktregeneration von 3 für jeden Zustand, unter dem Ihr leidet. Diese Regeneration endet nach 5..11 Sekunden.', '5...11 Sekunden: Ihr habt für jeden Zustand auf Euch +3 Lebenspunktregeneration.', '', '', ''),
(370, 'Berserkerhaltung', '5..11 Sekunden lang greift Ihr um 33% schneller als normal an und erhaltet 50% mehr Adrenalin für jeden Eurer Treffer im Nahkampf. Berserkerhaltung endet, sobald Ihr eine Fertigkeit verwendet.', '5...11 Sekunden: Ihr greift um 33% schneller an und erhaltet 50% mehr Adrenalin. <i>Endet, sobald Ihr eine Fertigkeit verwendet.</i>', '', '', ''),
(371, 'Ausgeglichene Haltung', '8..20 Sekunden lang könnt Ihr nicht zu Boden geworfen werden und erleidet keinen zusätzlichen Schaden durch kritische Angriffe.', '8...20 Sekunden: Ihr könnt nicht zu Boden geworfen werden und erleidet keinen zusätzlichen Schaden durch kritische Treffer.', '', '', ''),
(372, 'Gladiatoren-Verteidigung', '5..11 Sekunden lang habt Ihr eine 75%ige Chance, Angriffe zu blocken. Immer, wenn Ihr einen Nahkampfangriff auf diese Weise blockt, erleidet der Angreifer 5..35 Punkte Schaden.', '5...11 Sekunden: Ihr habt eine 75%ige Chance zu blocken. Angreifer erleidet jedes Mal 5...35 Punkte Schaden, wenn Ihr einen Nahkampfangriff auf diese Weise blockt.', '', '', ''),
(373, 'Pfeilablenkung', '1..6 Sekunden lang habt Ihr eine 75%ige Chance, Angriffe zu blocken. Wenn Ihr einen Geschossangriff blockt, leiden umstehende Feinde 5..15 Sekunden lang unter einer Blutung.', '(1...6 Sekunden.) Ihr habt eine 75%ige Chance, Angriffe zu blocken. Wenn Ihr einen Geschossangriff blockt, erleiden umstehende Feinde eine Blutung (5...15 Sekunden).', '', '', ''),
(374, 'Ausdauer des Kriegers', '5..35 Sekunden lang erhaltet Ihr 3 Energiepunkte für jeden Treffer den Ihr mit einem Nahkampfangriff erzielt. Ausdauer des Kriegers kann Eure Energiepunkte höchstens auf 10..25 erhöhen.', '5...35 Sekunden: Ihr erhaltet jedes Mal 3 Energiepunkte, wenn Ihr mit einem Nahkampfangriff trefft. <i>Kein Energieerhalt wenn Ihr mehr als 10...25 Energiepunkte habt.</i>', 'Ausdauer des Kriegers (PvP)', '5..35 Sekunden lang erhaltet Ihr jedes Mal 3 Energiepunkte, wenn Ihr mit einem Nahangriff einen Treffer erzielt. Ausdauer des Kriegers kann Eure Energiepunkte nicht auf mehr als 10..25 ansteigen lassen.', '(5...35 Sekunden) Ihr erhaltet jedes Mal 3 Energiepunkte, wenn Ihr mit einem Nahangriff<sub>sic</sub> einen Treffer erzielt. <i>Kein Energieerhalt wenn Ihr mehr als 10...25 Energiepunkte habt.</i>'),
(375, 'Zwergen-Kampfhaltung', '5..11 Sekunden lang gilt Folgendes, falls Ihr einen Hammer schwingt: Ihr greift um 33% schneller an, Ihr erhaltet +40 Rüstung, und wenn Ihr einen Gegner mit einer Angriffsfertigkeit trefft, wird dessen Aktion unterbrochen.', '(5...11 Sekunden.) Ihr greift um 33% schneller an, Ihr erhaltet +40 Rüstung und Eure Angriffsfertigkeiten unterbrechen Aktionen. <i>Keine Wirkung, falls Ihr keinen Hammer besitzt.</i>', '', '', ''),
(376, 'Disziplinierte Haltung', '1..4 Sekunden lang erhaltet Ihr +10 Punkte Rüstung und habt eine 75%ige Chance, Angriffe zu blocken. Disziplinierte Haltung endet, sobald Ihr eine Adrenalinfertigkeit verwendet.', '1...4 Sekunden: Ihr habt eine 75%ige Chance zu blocken und +10 Rüstung. <i>Endet, sobald Ihr eine Adrenalinfertigkeit verwendet.</i>', '', '', ''),
(377, 'Wachsame Haltung', '1..6 Sekunden lang blockt Ihr jede gegen Euch angewendete Angriffsfertigkeit. Für jedes erfolgreiche Blocken erhaltet Ihr Adrenalin und 5 Energiepunkte. Wachsame Haltung endet, sobald Ihr eine Fertigkeit verwendet.', '1...6 Sekunden: Ihr blockt Angriffsfertigkeiten. Für jedes Blocken erhaltet Ihr Adrenalin und 5 Energiepunkte. <i>Endet, sobald Ihr eine Fertigkeit verwendet.</i>', '', '', ''),
(378, 'Schildhaltung', '1..6 Sekunden lang habt Ihr, solange Ihr einen Schild benutzt, eine 75%ige Chance, Angriffe zu blocken. Für jeden Punkt Stärke wird der erlittene Schaden um 2 Punkte reduziert (maximal 15 Lebenspunkte Schadenreduzierung).', '(1...6 Sekunden.) Ihr habt eine 75%ige Chance zu blocken. Für jeden Punkt Stärke wird der erlittene Schaden um 2 Punkte reduziert (maximal 15 Lebenspunkte Schadenreduzierung). <i>Keine Wirkung, falls Ihr keinen Schild mitführt.</i>', '', '', ''),
(379, 'Angriff des Bullen', '5..11 Sekunden lang bewegt Ihr Euch um 33% schneller, und wenn Ihr einen sich bewegenden Gegner im Nahkampf trefft, wird dieser Gegner zu Boden geworfen. Angriff des Bullen endet, sobald Ihr eine Fertigkeit verwendet.', '5...11 Sekunden: Ihr bewegt Euch um 33% schneller. Nahkampftreffer werfen Feinde, die sich bewegen, zu Boden. <i>Endet, sobald Ihr eine Fertigkeit verwendet.</i>', '', '', ''),
(380, 'Bonettis Verteidigung', 'Für die nächsten 5..11 Sekunden habt Ihr eine 75%-ige Chance Angriffe zu blocken. Ihr bekommt 5 Energie für jeden geblockten Nahkampfangriff. Bonettis Verteidigung endet, sobald Ihr eine Fertigkeit verwendet.', '5...11 Sekunden: Ihr habt eine 75%ige Chance zu blocken. Für jeden geblockten Nahkampfangriff erhaltet Ihr 5 Energiepunkte. <i>Endet, sobald Ihr eine Fertigkeit verwendet.</i>', '', '', ''),
(381, 'Hundert Klingen', '15 Sekunden lang erleiden alle umstehenden Feinde jedes Mal, wenn Ihr mit einem Schwert angreift, 10..25 Punkte Hiebschaden.', '15 Sekunden: Richtet jedes Mal, wenn Ihr mit einem Schwert angreift, bei allen umstehenden Feinden 10...25 Punkte Schaden an.', '', '', ''),
(382, 'Arterienabtrennung', 'Wenn dieser Angriff erfolgreich ist, leidet der Gegner 5..25 Sekunden lang unter einer Blutung und verliert Lebenspunkte über Zeit.', 'Verursacht Zustand Blutung (5...25 Sekunden).', '', '', ''),
(383, 'Galrath-Hieb', 'Bei einem Treffer fügt dieser Angriff +1..40 Punkte Schaden zu.', 'Richtet +1...40 Punkte Schaden an.', '', '', ''),
(384, 'Schnittwunde', 'Wenn dieser Angriff einen Gegner mit einer Blutung trifft, fügt Ihr 5..20 Punkte zusätzlichen Schaden zu und dieser Gegner erleidet eine Tiefe Wunde, die seine maximalen Lebenspunkte und seine Heilung 5..20 Sekunden lang um 20% verringert.', 'Richtet +5...20 Punkte Schaden an und verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner blutet.', '', '', ''),
(385, 'Endstoß', 'Alles Adrenalin geht verloren. Wenn Endstoß trifft, fügt Ihr 1..40 Punkte mehr Schaden zu. Dieser Schaden wird verdoppelt, wenn die Lebenspunkte Eures Ziels unter 50% lagen.', 'Richtet +1...40 Punkte Schaden an. Richtet zusätzlich +1...40 Punkt Schaden an, falls der Gegner weniger als 50% seiner maximalen Lebenspunkte hat. <i>Ihr verliert alles Adrenalin.</i>', '', '', ''),
(386, 'Suchende Klinge', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +1..20 Punkte Schaden zu. Wenn Suchende Klinge geblockt wird, blutet das Ziel 25 Sekunden lang und erleidet 1..20 Punkte Schaden.', 'Richtet 1...20 Punkte Schaden an. Richtet 1...20 Punkte Schaden an und verursacht Zustand Blutung (25 Sekunden), falls geblockt.', '', '', ''),
(387, 'Entgegnung', '8 Sekunden lang blockt Ihr, solange Ihr ein Schwert führt, den nächsten gegen Euch gerichteten Nahkampfangriff und Euer Angreifer wird mit 20..80 Punkten Schaden getroffen.', '8 Sekunden: Ihr blockt den nächsten Nahkampfangriff; der Angreifer erleidet 20...80 Punkte Schaden. <i>Keine Wirkung, falls Ihr kein Schwert benutzt.</i>', '', '', ''),
(388, 'Tödliche Entgegnung', '8 Sekunden lang blockt Ihr, solange Ihr ein Schwert führt, den nächsten gegen Euch gerichteten Nahkampfangriff, Euer Angreifer wird mit 15..90 Punkten Schaden getroffen und blutet 3..26 Sekunden lang.', '8 Sekunden: Ihr blockt den nächsten Nahkampfangriff; der Angreifer erleidet 15...90 Punkte Schaden. Verursacht Zustand Blutung (3...26 Sekunden). <i>Keine Wirkung, falls Ihr kein Schwert benutzt.</i>', '', '', ''),
(389, 'Blütezeit', 'Alle Eure Angriffsfertigkeiten werden wieder aufgeladen. Für jede mit Hilfe von Blütezeit wieder aufgeladene Fertigkeit erhaltet Ihr 2..7 Energiepunkte.', 'Lädt Eure Angriffsfertigkeiten wieder auf. Ihr erhaltet für jede wieder aufgeladene Fertigkeit 2...7 Energiepunkte.', '', '', ''),
(390, 'Wilder Hieb', 'Wenn dieser Angriff trifft, unterbricht er die Aktion des Gegners. Wenn diese Aktion ein Zauber war, fügt Ihr 1..40 Punkte zusätzlichen Schaden zu.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Richtet +1...40 Punkte Schaden an, falls die Aktion ein Zauber war.', '', '', ''),
(391, 'Jägerschuss', 'Wenn der Jägerschuss trifft, leidet Euer Gegner 3..25 Sekunden lang an einer Blutung.', 'Verursacht Zustand Blutung (3...25 Sekunden).', '', '', ''),
(392, 'Festnageln', 'Wenn Festnageln trifft, erleidet Euer Ziel 3..15 Sekunden lang eine Verkrüppelung.', 'Verursacht Zustand Verkrüppelung (3...15 Sekunden).', '', '', ''),
(393, 'Verkrüppelungs-Schuss', 'Wenn Verkrüppelungs-Schuss trifft, leidet Euer Ziel 1..11 Sekunden lang an einer Verkrüppelung. Dieser Angriff kann nicht geblockt werden.', 'Kann nicht geblockt werden. Verursacht Zustand Verkrüppelung (1...11 Sekunden).', '', '', ''),
(394, 'Kraftschuss', 'Wenn Kraftschuss trifft, erleidet der Gegner 25..50 Schaden.', 'Der Gegner erleidet 25...50 Punkte Schaden.', '', '', ''),
(395, 'Sperrfeuer', 'Entfernt alle Eure Vorbereitungen. Schießt Pfeile auf Euren Zielgegner und bis zu 5 um ihn herum stehende Feinde. Bei Treffern verursachen diese Pfeile +5..20 Schaden.', 'Verursacht +5...20 Punkte Schaden. Trifft 5 um Euren Zielgegner herum stehende Feinde. <i>Entfernt alle Eure Vorbereitungen.</i>', '', '', ''),
(396, 'Doppelschuss', 'Zwei Pfeile werden gleichzeitig auf den Gegner abgefeuert. Diese Pfeile fügen 25% weniger Schaden zu.', 'Ihr feuert zwei Pfeile gleichzeitig auf den Gegner ab. <i>Diese Pfeile richten 25% weniger Schaden an.</i>', '', '', ''),
(397, 'Schnellschuss', 'Schießt einen Pfeil ab, der sich doppelt so schnell bewegt.', 'Ihr feuert einen Pfeil ab, der mit doppelter Geschwindigkeit fliegt.', '', '', ''),
(398, 'Durchbohr-Angriff', 'Wenn Durchbohr-Angriff trifft, fügt Ihr fügt Ihr +5..25 Punkte Schaden zu. Dieser Angriff hat eine Rüstungsdurchdringung von 10%.', 'Richtet +5...25 Punkte Schaden an. 10% Rüstungsdurchdringung.', 'Durchbohr-Angriff (PvP)', 'Wenn Durchbohr-Angriff trifft, fügt Ihr +3..10 Punkte Schaden zu. Dieser Angriff hat eine Rüstungsdurchdringung von 10%.', 'Richtet +3...10 Punkte Schaden an. 10% Rüstungsdurchdringung.'),
(399, 'Ablenk-Schuss', 'Wenn "Ablenk-Schuss" trifft, unterbricht er die Aktion des Gegners, fügt aber nur 1..16 Punkte Schaden zu. Beruhte die unterbrochene Aktion auf einer Fertigkeit, wird diese Fertigkeit für weitere 20 Sekunden deaktiviert.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Unterbrochene Fertigeit<sub>sic</sub> wird +20 Sekunden lang deaktiviert. <i>Treffer richten nur 1...16 Punkte Schaden an.</i>', '', '', ''),
(400, 'Präzisionsschuss', 'Wenn Präzisionsschuss trifft, fügt Ihr +3..10 Punkte Schaden zu. Beim Präzisionsschuss ist ein Blocken nicht möglich. Diese Aktion kann leicht unterbrochen werden.', 'Richtet +3...10 Punkte Schaden an. Kann nicht geblockt werden. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(402, 'Entschlossener Schuss', 'Wenn Entschlossener Schuss trifft, fügt Ihr 5..20 Punkte zusätzlichen Schaden zu. Wenn dieser Angriff fehlschlägt, werden alle Eure Angriffsfertigkeiten wieder aufgeladen.', 'Richtet +5...20 Punkte Schaden an. Lädt Eure Angriffsfertigkeiten auf, falls der Angriff nicht trifft.', '', '', ''),
(403, 'Ankündigungs-Schuss', 'Schießt einen Pfeil ab, der sich dreimal so schnell bewegt und nicht geblockt werden kann.', 'Ihr feuert einen Pfeil ab, der mit dreifacher Geschwindigkeit fliegt. Kann nicht geblockt werden.', '', '', ''),
(404, 'Giftpfeil', 'Wenn Giftpfeil trifft, wird Euer Ziel 5..20 Sekunden lang vergiftet.', 'Verursacht Zustand Gift (5...20 Sekunden).', '', '', ''),
(405, 'Eidschuss', 'Wenn Eidschuss trifft, werden alle Eure Fertigkeiten außer Eidschuss wieder aufgeladen. Schlägt er fehl, werden alle Eure Fertigkeiten 10..4 Sekunden lang deaktiviert. (Bei einer Fachkenntnis von 7 oder weniger besteht eine 50%ige Chance für einen Misserfolg.)', 'Lädt bei einem Treffer alle Fertigkeiten außer Eidschuss wieder auf. <i>Deaktiviert alle Fertigkeiten (10...4 Sekunden), falls der Angriff verfehlt. Mindestens Fachkenntnis 8, sonst 50%ige Chance auf Fehlschuss.</i>', '', '', ''),
(406, 'Entkräftungs-Schuss', 'Wenn Entkräftungs-Schuss trifft, verliert Euer Ziel 1..10 Energiepunkte.', 'Lässt 1...10 Energiepunkte verlieren.', '', '', ''),
(407, 'Kurzdistanzschuss', 'Ein Pfeil wird abgeschossen, der nur die halbe Reichweite zurücklegt, aber dafür +10..40 Schaden zufügt.', 'Richtet +10...40 Punkte Schaden an.', '', '', ''),
(408, 'Erschütterungsschuss', 'Trifft Erschütterungsschuss, während der Gegner einen Zauber wirkt, leidet er 5..20 Sekunden lang an Benommenheit. Dieser Angriff fügt nur 1..16 Punkte Schaden zu.', 'Verursacht Zustand Benommenheit (5...20 Sekunden). <i>Treffer richten nur 1...16 Punkte Schaden an. Gegner muss einen Zauber wirken, sonst keine Wirkung.</i>', '', '', ''),
(409, 'Bestrafungs-Schuss', 'Trifft Bestrafungs-Schuss, fügt Ihr +10..20 Punkte Schaden zu und Euer Ziel wird unterbrochen.', 'Richtet +10...20 Punkte Schaden an. Unterbricht eine Aktion.', '', '', ''),
(411, 'Tier zähmen', 'Das Tier wird gezähmt. Einmal gezähmt, reist euer Tiergefährte mit Euch, wann immer Ihr Tier zähmen benutzt. Tiere, die mehr als 4 Stufen höher als Ihr stehen, können nicht gezähmt werden.', 'Das Tier wird gezähmt. Einmal gezähmt, reist Euer Tiergefährte mit Euch, wann immer Ihr "Tier zähmen" ausgerüstet habt. <i>Tiere, die mehr als 4 Stufen höher als Ihr stehen, können nicht gezähmt werden.</i>', '', '', ''),
(412, 'Ruf des Schutzes', '120 Sekunden erhält Euer Tiergefährte eine Schadensreduzierung von 5..20.', '120 Sekunden: Euer Tiergefährte hat 5...20 Punkte Schadensreduzierung.', '', '', ''),
(415, 'Ruf der Eile', '30 Sekunden lang erhöht sich die Angriffsgeschwindigkeit Eures Tiergefährten um 33% und er bewegt sich um 33% schneller.', '30 Sekunden: Euer Tiergefährte bewegt sich um 33% schneller und greift um 33% schneller an.', 'Ruf der Eile (PvP)', '30 Sekunden lang erhöht sich die Angriffsgeschwindigkeit Eures Tiergefährten um 25% und er bewegt sich um 25% schneller.', '30 Sekunden: Euer Tiergefährte bewegt sich um 25% schneller und greift um 25% schneller an.'),
(422, 'Tier wiederbeleben', 'Alle verbündeten Tiergefährten in der Nähe werden wiederbelebt und kehren mit 10..94% Lebenspunkten ins Leben zurück.', 'Alle verbündeten Tiergefährten in der Nähe werden wiederbelebt (10...94% Lebenspunkte).', '', '', ''),
(423, 'Symbiotisches Band', '120..300 Sekunden lang erfreut sich Euer Tiergefährte einer Lebenspunkteregeneration von 1..3 und die Hälfte jeglichen Schadens, der Eurem Tiergefährten zugefügt wird, wird auf Euch umgeleitet.', '120...300 Sekunden: Euer Tiergefährte hat +1...3 Lebenspunktregeneration. Die Hälfte des Eurem Tiergefährten zugefügten Schadens wird auf Euch umgeleitet.', '', '', ''),
(424, 'Dreck werfen', 'Der berührte Gegner und umstehende Feinde werden 3..15 Sekunden lang geblendet.', 'Verursacht Zustand Blindheit (3...15 Sekunden). Betrifft den Gegner und umstehende Feinde.', '', '', ''),
(425, 'Abducken', '5..11 Sekunden lang bewegt Ihr Euch um 33% schneller und habt eine 27..75%ige Chance, Geschosse zu blocken. Abducken endet, wenn Ihr angreift.', '5...11 Sekunden: Ihr bewegt Euch um 33% schneller und habt eine 27...75%ige Chance, Geschossangriffe zu blocken. <i>Endet, sobald Ihr angreift.</i>', '', '', ''),
(426, 'Wilder Schuss', 'Trifft Wilder Schuss, wird die Aktion Eures Ziels unterbrochen. Wenn diese Aktion ein Zauber war, fügt Ihr zusätzlich 13..28 Punkte Schaden zu.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Richtet 13...28 Punkte Schaden an, falls die Aktion ein Zauber war.', '', '', ''),
(427, 'Gegengift-Siegel', 'Reinigt Euch von Gift, Krankheit, Blindheit und einem weiteren Zustand.', 'Entfernt Gift, Krankheit, Blindheit und einen weiteren Zustand von Euch.', '', '', ''),
(428, 'Flammenpfeile', 'Schießt Pfeile auf den Gegner und bis zu 2 Feinde in seiner Nähe. Diese Feinde werden 1..3 Sekunden lang in Brand gesetzt.', 'Trifft 2 Feinde in der Nähe Eures Gegners und verursacht Brennen (1...3 Sekunden).', '', '', ''),
(429, 'Melandrus Pfeile', 'Für die nächsten 18 Sekunden verursachen Eure Pfeiltreffer eine Blutung, die 3..25 Sekunden lang anhält, und fügen, sofern sie einen Feind treffen, der einer Verzauberung unterliegt, 8..28 Punkte zusätzlichen Schaden zu.', '18 Sekunden: Eure Pfeile verursachen den Zustand Blutung (3...25 Sekunden) und richten bei verzauberten Feinden +8...28 Punkte Schaden an.', '', '', ''),
(430, 'Schützenwette', '18 Sekunden lang erhaltet Ihr für jeden Eurer Pfeiltreffer 5..10 Energiepunkte, aber Ihr verliert jedes Mal 10 Energiepunkte, wenn Eure Pfeile das Ziel verfehlen.', '18 Sekunden: Ihr erhaltet jedes Mal 5...10 Energiepunkte, wenn einer eurer Pfeile trifft. <i>Ihr verliert bei Fehlschüssen 10 Energiepunkte.</i>', '', '', ''),
(431, 'Brandpfeile', '24 Sekunden lang explodieren Eure Pfeile beim Aufschlag und fügen dem Gegner und allen umstehenden Feinden 3..18 Punkte Feuerschaden zu.', '24 Sekunden: Eure Pfeile richten beim Gegner und umstehenden Feinden 3...18 Punkte Feuerschaden an.', '', '', ''),
(432, 'Wind lesen', '24 Sekunden lang bewegen sich Eure Pfeile doppelt so schnell wie normal und fügen zusätzlich 3..10 Punkte Schaden zu.', '24 Sekunden: 3...10 Punkte Schaden. Eure Pfeile fliegen mit doppelter Geschwindigkeit.', 'Wind lesen (PvP)', '24 Sekunden lang bewegen sich Eure Pfeile doppelt so schnell.', '24 Sekunden: Eure Pfeile fliegen doppelt so schnell.'),
(433, 'Zündelpfeile', '24 Sekunden lang richten Eure Pfeile Feuerschaden an und fügen zusätzlich 3..24 Punkte Feuerschaden zu.', '24 Sekunden: +3...24 Punkte Feuerschaden. Eure Pfeile richten Feuerschaden an.', '', '', ''),
(434, 'Würgegas', '1..12 Sekunden lang richten Eure Pfeile 1..8 Punkte mehr Schaden an und verbreiten beim Aufschlag Würgegas unter allen umstehenden Gegnern. Würgegas unterbricht Gegner beim Wirken von Zaubern.', '1...12 Sekunden: +1...8 Schaden. Verbreitet Würgegas unter umstehenden Feinden um den Gegner. Würgegas unterbricht Zauber.', '', '', ''),
(435, 'Gift auftragen', '24 Sekunden lang werden Gegner, die von Eurem physischen Angriff getroffen werden, 3..15 Sekunden lang vergiftet.', '24 Sekunden: Eure physischen Angriffe verursachen den Zustand Gift (3...15 Sekunden).', '', '', ''),
(436, 'Tier erquicken', 'Ihr heilt Euren Tiergefährten um 20..104 Lebenspunkte. Ist Euer Gefährte tot, wird er mit 10..58% der Lebenspunkte wiederbelebt. Wenn Ihr die Fertigkeit Tier erquicken benutzt, reist Euer Tiergefährte mit Euch.', 'Heilt Euren Tiergefährten um 20...104 Lebenspunkte. Euer Tiergefährte wird wiederbelebt (10...58% Lebenspunkte). Wenn Ihr die Fertigkeit Tier erquicken benutzt, reist Euer Tiergefährte mit Euch.', 'Tier erquicken (PvP)', 'Ihr heilt Euren Tiergefährten um 20..104 Punkte. Ist Euer Gefährte tot, wird er mit 10..58% der Lebenspunkte wiederbelebt.', 'Euer Tiergefährte gewinnt 20...104 Lebenspunkte. Euer Tiergefährte wird wiederbelebt (10...58% Lebenspunkte).'),
(437, 'Bestiensprung', 'Euer Tiergefährte führt einen Bestiensprung aus, der 5..20 Punkte zusätzlichen Schaden zufügt. Trifft dieser Angriff einen Gegner, der einen Zauber wirkt, wird er zu Boden geworfen.', 'Richtet +5...20 Punkte Schaden an. Wirft zu Boden, falls der Gegner einen Zauber wirkt.', '', '', ''),
(438, 'Verkrüppelungs-Schlag', 'Euer Tiergefährte führt einen Verkrüppelungs-Schlag aus, der 5..20 Punkte zusätzlichen Schaden zufügt. Wenn dieser Angriff einen sich bewegenden Gegner trifft, wird dieser 3..15 Sekunden lang verkrüppelt.', 'Richtet +5...20 Punkte Schaden an. Verursacht Zustand Verkrüppelung (3...15 Sekunden), falls der Gegner sich bewegt.', '', '', ''),
(439, 'Wilder Ausfall', 'Euer Tiergefährte greift mit einem Wilden Ausfall an, der +5..35 Punkte Schaden zufügt. Trifft dieser Angriff einen angreifenden Gegner, leidet dieser 5..25 Sekunden lang an einer Blutung.', 'Richtet +5...35 Punkte Schaden an. Verursacht Zustand Blutung (5...25 Sekunden), falls der Gegner gerade angreift.', '', '', ''),
(440, 'Aasfresser-Schlag', 'Euer Tiergefährte greift mit Aasfresser-Schlag an und fügt +10..25 Punkte Schaden zu. Wenn dieser Angriff einen Gegner trifft, der unter einem Zustand leidet, erhaltet Ihr 3..15 Energiepunkte.', 'Richtet +10...25 Punkte Schaden an. Ihr erhaltet 3...15 Energiepunkte, falls Gegner einen Zustand hat.', '', '', ''),
(441, 'Melandrus Angriff', 'Euer Tiergefährte greift mit Melandrus Angriff an und fügt allen Feinden in der Nähe +5..20 Punkte Schaden zu.', 'Richtet bei Feinden in der Nähe +5...20 Punkte Schaden an.', 'Melandrus Angriff (PvP)', 'Euer Tiergefährte greift mit Melandrus Angriff an und fügt +5..20 Punkte Schaden zu. Wenn dieser Angriff einen Gegner trifft, der unter einer Verzauberung leidet, erleiden er und alle umstehenden Gegner zusätzlich 5..35 Punkte Schaden.', 'Richtet +5...20 Punkte Schaden an. Richtet beim Gegner und umstehenden Feinden zusätzlich +5...35 Punkte Schaden an, falls der Gegner verzaubert ist.'),
(442, 'Wilder Schlag', 'Euer Tiergefährte greift mit Wilder Schlag an und fügt +13..28 Punkte Schaden zu. Wenn dieser Angriff trifft, erhaltet Ihr Adrenalin und 3..10 Energiepunkte.', 'Richtet +13...28 Punkte Schaden an. Ihr erhaltet Adrenalin und 3...10 Energiepunkte.', '', '', ''),
(443, 'Raubtier-Sprung', 'Euer Tiergefährte greift mit einem Raubtier-Sprung an und fügt +5..35 Punkte Schaden zu. Wenn dieser Angriff trifft, erhält Euer Tiergefährte 5..50 Lebenspunkte.', 'Richtet +5...35 Punkte Schaden an. Euer Tiergefährte erhält 5...50 Lebenspunkte, falls dieser Angriff trifft.', '', '', ''),
(444, 'Schonungsloser Schlag', 'Euer Tiergefährte greift mit Schonungsloser Schlag an und fügt +5..35 Punkte Schaden zu. Wenn dieser Angriff einen Gegner trifft, dessen Lebenspunkte unter 50% liegen, erleidet er zusätzlich 5..35 Punkte Schaden.', 'Richtet +5...35 Punkte Schaden an. Richtet zusätzlich +5...35 Punkte Schaden an, falls der Gegner weniger als 50% seiner maximalen Lebenspunkte hat.', '', '', ''),
(445, 'Unterbrechender Ausfall', 'Euer Tiergefährte greift mit Unterbrechender Ausfall an und fügt zusätzlich 1..12 Punkte Schaden zu. Wenn dieser Angriff einen Gegner trifft, der eine Fertigkeit verwendet, wird diese unterbrochen und für weitere 20 Sekunden deaktiviert.', 'Richtet +1...12 Punkte Schaden an. Unterbricht eine Aktion. Unterbrechungswirkung: Unterbrochene Fertigkeit wird 20 Sekunden lang deaktiviert.', '', '', ''),
(446, 'Trollsalbe', '13 Sekunden erhaltet Ihr eine Lebensregeneration von +3..10.', '13 Sekunden: Ihr habt +3...10 Lebenspunktregeneration.', '', '', ''),
(447, 'Otyughs Schrei', '10..25 Sekunden lang hat euer Tiergefährte +24 Rüstung und kann nicht geblockt werden.', '10...25 Sekunden: Euer Tiergefährte hat +24 Rüstung und seine Angriffe können nicht geblockt werden.', '', '', ''),
(448, 'Flucht', '1..8 Sekunden lang bewegt Ihr Euch 33% schneller und habt eine 75%ige Chance Angriffe zu blocken.', '1...8 Sekunden: Ihr bewegt Euch um 33% schneller und habt eine 75%ige Chance zu blocken.', 'Flucht (PvP)', '1..8 Sekunden lang bewegt Ihr Euch 33% schneller und habt eine 75%ige Chance Angriffe zu blocken. Flucht endet, sobald Ihr einen Nahkampfangriff ausführt.', '(1...8 Sekunden.) Ihr bewegt Euch um 33% schneller und habt eine 75%ige Chance zu blocken. <i>Endet, sobald Ihr einen Nahkampfangriff ausführt.</i>'),
(449, 'Geübte Haltung', '20..35 Sekunden lang laden sich Eure Vorbereitungen um 50% schneller auf und halten 30..150% länger an.', '20...35 Sekunden: Eure Vorbereitungen laden sich um 50% schneller auf und halten um 30...150% länger an.', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(450, 'Wirbelverteidigung', '8..20 Sekunden lang habt Ihr eine 75%ige Chance, Angriffe zu blocken. Beim Blocken eines Geschosses erleiden umstehende Gegner 5..11 Punkte Schaden.', '8...20 Sekunden: Ihr habt eine 75%ige Chance zu blocken. Richtet jedes Mal 5...11 Punkte Schaden bei umstehenden Feinden an, wenn ihr einen \'\'\'Geschossangriff\'\'\' blockt.', '', '', ''),
(451, 'Melandrus Belastbarkeit', '8..20 Sekunden lang erfreut Ihr Euch für jeden Zustand und jede Verhexung, unter denen Ihr leidet, einer Lebenspunktregeneration von +4 und einer Energiepunktregeneration von +1.', '8...20 Sekunden: Ihr habt für jeden Zustand und jede Verhexung auf Euch +4 Lebenspunktregeneration und +1 Energieregeneration.', '', '', ''),
(452, 'Dryders Verteidigungen', '5..11 Sekunden lang habt Ihr eine 75%ige Chance Angriffe zu blocken und erhaltet 34..60 Rüstung gegen Elementarschaden.', '5...11 Sekunden: Ihr habt eine 75%ige Chance zu blocken und +34...60 Rüstung gegen Elementarschaden.', '', '', ''),
(453, 'Blitzreflexe', '5..11 Sekunden lang habt Ihr eine 75%ige Chance, Nahkampf- und Geschossangriffe zu blocken, außerdem ist Eure Angriffsgeschwindigkeit um 33% höher.', '5...11 Sekunden: Ihr greift um 33% schneller an und habt eine 75%ige Chance zu blocken.', 'Blitzreflexe (PvP)', '5..11 Sekunden lang habt Ihr eine 75%ige Chance, Nahkampf- und Geschossangriffe zu blocken, außerdem ist Eure Angriffsgeschwindigkeit um 33% höher.', '5...11 Sekunden: Ihr greift um 33% schneller an und habt eine 75%ige Chance zu blocken.'),
(454, 'Wut des Tigers', 'Alle Eure Nicht-Angriffsfertigkeiten werden 5 Sekunden lang deaktiviert. 5..11 Sekunden lang greift Ihr um 25% schneller an.', '5...11 Sekunden: Ihr greift um 25% schneller an. <i>Deaktiviert Eure Nicht-Angriffsfertigkeiten (5 Sekunden).</i>', '', '', ''),
(455, 'Sturmjäger', '8..20 Sekunden lang bewegt Ihr Euch um 25% schneller und Ihr erhaltet jedes Mal, wenn Ihr Elementarschaden erleidet, 1..5 Energiepunkte.', '8...20 Sekunden: Ihr bewegt Euch um 25% schneller und gewinnt jedes Mal 1...5 Energiepunkt, wenn Ihr Elementarschaden erleidet.', '', '', ''),
(456, 'Schlangen-Schnelligkeit', '15..30 Sekunden lang ist die Wiederaufladezeit für Eure Fertigkeiten um 33% reduziert. Schlangen-Schnelligkeit endet, wenn Eure Lebenspunkte unter 50% sinken.', '15...30 Sekunden: Eure Fertigkeiten laden sich um 33% schneller auf. <i>Endet, wenn Eure Lebenspunkte unter 50% Eurer maximalen Lebenspunkte sinken.</i>', '', '', ''),
(457, 'Staubfalle', 'Wird Staubfalle aktiviert, fügt sie 5 Sekunden lang jede Sekunde allen Gegnern in der Nähe 3..8 Sekunden Blindheit und 10..25 Punkte rüstungsignorierenden Erd-Schaden zu. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden. Staubfalle endet nach 90 Sekunden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet jede Sekunde 10...25 Punkte Erdschaden an (5 Sekunden). Verursacht Zustand Blindheit (3...8 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(458, 'Stachelfalle', 'Wird Stachelfalle aktiviert, erleiden alle Gegner in der Nähe 7..23 Punkte Stichschaden, werden verkrüppelt und leiden 3..25 Sekunden lang unter einer Blutung. Stachelfalle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 7...23 Punkte Stichschaden an. Verursacht Zustände Verkrüppelung und Blutung (3...25 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(459, 'Flammenfalle', 'Wird Flammenfalle aktiviert, erleiden alle Gegner in der Nähe pro Sekunde (insgesamt 3 Sekunden lang) 5..20 Punkte Flammenschaden und werden 1..3 Sekunden lang in Brand gesetzt. Flammenfalle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet jede Sekunde 5...20 Punkte Feuerschaden an (3 Sekunden). Verursacht Zustand Brennen (1...3 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(460, 'Heilquelle', '10 Sekunden lang werden alle umstehenden Verbündeten alle 2 Sekunden um 15..60 Punkte geheilt. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '10 Sekunden: Betrifft umstehende Verbündete. Heilt alle 2 Sekunden um 15...60 Lebenspunkte. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(461, 'Dornenfalle', 'Wird Dornenfalle aktiviert, erleiden alle Gegner in der Nähe pro Sekunde (insgesamt 2 Sekunden lang) 10..40 Punkte Stichschaden, werden 3..25 Sekunden lang verkrüppelt und zu Boden geworfen. Dornenfalle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 10...40 Punkte Stichschaden an. Wirft zu Boden. Verursacht Zustand Verkrüppelung (3...25 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(462, 'Winter', 'Erstellt einen Geist der Stufe 1..10. Alle Kreaturen in seiner Reichweite erleiden Kälteschaden anstelle von Elementarschaden. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Wandelt Elementarschaden bei allen Kreaturen in Reichweite in Kälteschaden um. Keine Wirkung auf Geister.', '', '', ''),
(463, 'Trennung', 'Erstellt einen Geist der Stufe 1..10. Kreaturen in Reichweite, die keine Geister sind, erleiden, wenn sie physischen Schaden nehmen, 4 Punkte zusätzlichen Schaden. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Erhöht physischen Schaden bei Kreaturen in Reichweite um +4 Punkte. Keine Wirkung auf Geister.', '', '', ''),
(464, 'Rand der Auslöschung', 'Erstellt einen Geist der Stufe 1..10. Wenn eine Kreatur in Reichweite, die kein Geist ist, stirbt, fügt "Rand der Auslöschung" allen Kreaturen derselben Gattung, deren Lebenspunkte unter 90% liegen und die sich in Reichweite des Geistes befinden, 14..50 Punkte Schaden zu. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Richtet bei Kreaturen in Reichweite jedes Mal 14...50 Punkte Schaden an, wenn eine Kreatur derselben Gattung stirbt. Keine Wirkung auf Geister. <i>Kein Schaden bei Kreaturen, die mehr als 90% ihrer maximalen Lebenspunkte haben.</i>', '', '', ''),
(465, 'Großflächenbrand', 'Erstellt einen Geist der Stufe 1..10. Alle Kreaturen in seiner Reichweite, die keine Geister sind, erleiden durch Angriffe Feuerschaden anstelle von physischem Schaden. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Wandelt physischen Angriffsschaden bei allen Kreaturen in Reichweite in Feuerschaden um. Keine Wirkung auf Geister.', '', '', ''),
(466, 'Feuersbrunst', 'Erstellt einen Geist der Stufe 1..10. Alle Pfeiltreffer fügen Kreaturen in seiner Reichweite, die keine Geister sind, Feuerschaden zu. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Für Kreaturen in Reichweite richten alle Pfeile Feuerschaden an. Keine Wirkung auf Geister.', '', '', ''),
(467, 'Fruchtbare Jahreszeit', 'Erstellt einen Geist der Stufe 1..10. Kreaturen in Reichweite, die keine Geister sind, haben +50..150 maximale Lebenspunkte und erhalten +8 Rüstung. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...45 Sekunden Lebensdauer). Kreaturen in Reichweite haben +50...150 maximale Lebenspunkte und +8 Rüstung. Keine Wirkung auf Geister.', '', '', ''),
(468, 'Symbiose', 'Erstellt einen Geist der Stufe 1..10. Jede Kreatur in Reichweite, die kein Geist ist, erhält +27..150 maximale Lebenspunkte für jede Verzauberung auf ihr. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Kreaturen in Reichweite haben für jede Verzauberung auf ihnen +27...150 maximale Lebenspunkte. Keine Wirkung auf Geister.', '', '', ''),
(469, 'Urechos', 'Erstellt einen Geist der Stufe 1..10. Die Verwendung von Siegeln kostet Kreaturen in seiner Reichweite, die keine Geister sind, 10 Energiepunkte. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Siegel kosten Kreaturen in Reichweite 10 Energiepunkte. Keine Wirkung auf Geister.', '', '', ''),
(470, 'Jagdsaison', 'Erstellt einen Geist der Stufe 1..10 . Die Heilung aller Kreaturen in seiner Reichweite, die keine Geister sind, ist um 20% reduziert. Wenn euer Angriff trifft, erhaltet Ihr 5 Lebenspunkte. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Verringert die Wirkung von Heilungen bei Kreaturen in Reichweite um 20%. Kreaturen erhalten für jeden Treffer, den sie mit einem Angriff landen, 5 Lebenspunkte. Keine Wirkung auf Geister.', '', '', ''),
(471, 'Eisboden', 'Erstellt einen Geist der Stufe 1..10. Kreaturen in seiner Reichweite, die keine Geister sind, können keine Wiederbelebungsfertigkeiten aktivieren. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Kreaturen in Reichweite können keine Wiederbelebungsfertigkeiten aktivieren. Keine Wirkung auf Geister.', '', '', ''),
(472, 'Günstige Winde', 'Erstellt einen Geist der Stufe 1..10.Für Kreaturen in seiner Reichweite, die keine Geister sind, bewegen sich Pfeile doppelt so schnell und fügen +6 Punkte Schaden zu. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Bei Kreaturen in Reichweite fliegen Pfeile mit doppelter Geschwindigkeit und richten bei Treffern +6 Punkte Schaden an. Keine Wirkung auf Geister.', '', '', ''),
(474, 'Treibwind', 'Erstellt einen Geist der Stufe 1..6. Für Kreaturen in seiner Reichweite, die keine Geister sind, kosten alle Fertigkeiten höchstens 10 Energiepunkte und die Wiederaufladezeit ist um 25% langsamer. Dieser Geist stirbt nach 1..31 Sekunden.', 'Erstellt einen Geist der Stufe 1...6 (1...31 Sekunden Lebensdauer). Fertigkeiten kosten Kreaturen in Reichweite höchstens 10 Energie und laden sich um 25% langsamer auf. Keine Wirkung auf Geister.', '', '', ''),
(475, 'Beschleunigender Zephyr', 'Erstellt einen Geist der Stufe 1..10. Für Kreaturen in seiner Reichweite, die keine Geister sind, werden alle Fertigkeiten doppelt so schnell wieder aufgeladen und benötigen 30% mehr Basisenergie beim Wirken. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...45 Sekunden Lebensdauer). Fertigkeiten kosten Kreaturen in Reichweite 30% Energiepunkte mehr und laden sich doppelt so schnell auf. Keine Wirkung auf Geister.', '', '', ''),
(476, 'Erneuerung der Natur', 'Erstellt einen Geist der Stufe 1..10. 30..150 Sekunden lang dauert das Wirken von Verzauberungen und Verhexungen von Kreaturen, die keine Geister sind, doppelt so lange und das Aufrechterhalten von Verzauberungen benötigt doppelt soviel Energie. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Die Wirkzeit von Verzauberungen und Verhexungen wird bei Kreaturen in Reichweite verdoppelt und es kostet doppelt so viele Energiepunkte, Verzauberungen aufrechtzuerhalten. Keine Wirkung auf Geister.', '', '', ''),
(477, 'Schlammiges Terrain', 'Erstellt einen Geist der Stufe 1..10. Kreaturen in seiner Reichweite, die keine Geister sind, bewegen sich um 10% langsamer und Geschwindigkeitsschübe bleiben wirkungslos. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Kreaturen in Reichweite bewegen sich um 10% langsamer; Geschwindigkeitsschübe bleiben wirkungslos. Keine Wirkung auf Geister.', '', '', ''),
(570, 'Zeichen der Unsicherheit', '5..25 Sekunden lang erleidet der Gegner eine Lebenspunktdegeneration von -1..3 und enden Verzauberungen, mit denen der Verhexte belegt wird, und Haltungen, die er aktiviert, um 30..80% schneller. Eure gesamten Nicht-Assassinen-Fertigkeiten sind 10 Sekunden lang deaktiviert.', '(5...25 Sekunden.) Bewirkt -1...3 Lebenspunktdegeneration. Verzauberungen, die auf den Gegner gewirkt werden,  und Haltungen, die er aktiviert, enden um 30...80% schneller. <i>Deaktiviert Eure gesamten Nicht-Assassinen-Fertigkeiten (10 Sek.).</i>', '', '', ''),
(571, 'Unterbrechender Dolch', 'Ein unterbrechender Dolch fliegt auf den Gegner zu und fügt 10..35 Punkte Erdschaden zu. Wenn dieser Gegner eine Fertigkeit aktiviert hat, wird diese unterbrochen. Dieser Zauber hat nur die halbe Reichweite.', 'Geschoss: Richtet 10...35 Punkte Erdschaden an. Unterbricht eine Fertigkeit.', '', '', ''),
(572, 'Tödliches Paradoxon', 'Alle Eure Angriffsfertigkeiten werden 10 Sekunden lang deaktiviert. 5..15 Sekunden lang werden Eure Assassinen-Fertigkeiten um 33% schneller aktiviert und aufgeladen.', '5...15 Sekunden: Eure Assassinen-Fertigkeiten werden um 33% schneller aktiviert und aufgeladen. <i>Deaktiviert Eure Angriffsfertigkeiten (10 Sekunden).</i>', '', '', ''),
(763, 'Neidvoller Blick', 'Entfernt eine Verzauberung vom Gegner. Beim Entfernen einer Verzauberung wird 1..15 Sekunden lang Eure nächste Verzauberung 0..1 Sekunden schneller gewirkt und kostet 1..10 Energiepunkte weniger.', 'Entfernt eine Verzauberung. Beim Entfernen einer Verzauberung wird 1...15 Sekunden lang Eure nächste Verzauberung 0...1 Sekunden schneller gewirkt und kostet 1...10 Energiepunkte weniger.', '', '', ''),
(764, 'Schicksalsklage', '1..4 Sekunde(n) lang werden alle Attribute des Gegners auf 0 gesetzt.', '1...4 Sekunden: Attribute des Gegners sind 0.', '', '', ''),
(766, 'Verächtlicher Blick', 'Wenn der Gegner mehr als 50% seiner Lebenspunkte hat, verliert er alle Verzauberungen.', 'Entfernt Verzauberungen des Gegners. <i>Gegner muss mehr als 50% seiner maximalen Lebenspunkte haben, sonst keine Wirkung.</i>', '', '', ''),
(769, 'Verteidigung der Viper', 'Alle umstehenden Feinde leiden 5..20 Sekunden lang an Vergiftung. Unternehmt einen Schattenschritt mit in-dem-Bereich-Distanz, direkt weg vom ausgewählten Ziel. Habt Ihr Euch selbst oder kein Ziel gewählt, ist die Richtung zufällig und die Distanz schwankend.', 'Verursacht Zustand Gift (5...20 Sekunden) bei allen umstehenden Feinden und unternehmt einen Schattenschritt mit in-dem-Bereich-Distanz, weg vom ausgewählten Ziel. Zufällige Richtung mit variabler Distanz, wenn kein oder sich selbst als Ziel.', '', '', ''),
(770, 'Rückkehr', 'Alle umstehenden Gegner werden 3..8 Sekunden lang verkrüppelt. Unternehmt Schattenschritt zur Position des ausgewählten Verbündeten.', 'Verursacht bei allen umstehenden Feinden um Euch Zustand Verkrüppelung (3...8 Sekunden). Ihr unternehmt einen Schattenschritt zur Position des Verbündeten. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(771, 'Aura der Verschiebung', 'Wenn Ihr Aura der Verschiebung wirkt, macht Ihr einen Schattenschritt zum anvisierten Gegner. Wenn Ihr Aura der Verschiebung nicht länger aufrechterhaltet, kehrt Ihr an Euren ursprünglichen Ort zurück.', 'Unternehmt einen Schattenschritt zum Gegner. Wirkung am Ende: Ihr kehrt zur anfänglichen Position zurück.', '', '', ''),
(772, 'Großzügig war Tsungrai', 'Haltet Tsungrais Asche bis zu 15..60 Sekunden lang und erhaltet +50..140 Punkte maximale Lebenspunkte. Wenn Ihr seine Asche fallen lasst, erhaltet Ihr 100..280 Lebenspunkte.', '15...60 Sekunden: Ihr habt +50...140 maximale Lebenspunkte. Fallwirkung: Ihr erhaltet 100...280 Lebenspunkte.', '', '', ''),
(773, 'Mächtig war Vorizun', 'Haltet Vorizuns Asche bis zu 15..60 Sekunden lang. Während Ihr seine Asche haltet, erhaltet Ihr +15 Rüstung und +30 Punkte maximale Energie.', '15...60 Sekunden: Ihr habt +15 Rüstung und +30 Punkte maximale Energiepunkte', '', '', ''),
(775, 'Todesblüte', 'Muss einem Begleithandangriff folgen. Wenn Todesblüte erfolgreich ist, erleidet der Gegner +20..45 Punkte Schaden und alle umstehenden Feinde 20..45 Punkte Schaden.', 'Richtet beim Gegner +20...45 Punkte und bei umstehenden Feinden 20...45 Punkte Schaden an. <i>Muss einem Begleithandangriff folgen.</i>', 'Todesblüte (PvP)', 'Muss einem Begleithandangriff folgen. Wenn Todesblüte erfolgreich ist, erleidet der Gegner +20..45 Punkte Schaden und alle umstehenden Feinde 20..45 Punkte Schaden.', 'Richtet +20...45 Punkte Schaden an. Außerdem werden alle umstehende Feinde betroffen. <i>Muss einem Begleithandangriff folgen.</i>'),
(776, 'Schlangenzähne', 'Muss einem Begleithandangriff folgen. Wenn Schlangenzähne erfolgreich ist, fügt dieser Angriff +10..20 Punkte Schaden zu und getroffene Feinde leiden 5..20 Sekunden lang unter Blutung und Tiefer Wunde.', 'Richtet +10...20 Punkte Schaden an. Verursacht Zustände Blutung und Tiefe Wunde (5...20 Sekunden). <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(777, 'Hörner des Ochsen', 'Muss einem Begleithandangriff folgen. Wenn Hörner des Ochsen erfolgreich ist, fügt dieser Angriff +1..11 Punkte Schaden zu. Falls keine Verbündeten um den getroffenen Feind herumstehen, wird der Feind zu Boden geworfen.', 'Richtet +1...11 Punkte Schaden an. wirft zu Boden, falls kein Verbündeter des Gegners um ihn herumsteht. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(778, 'Fallende Spinne', 'Muss einen zu Boden geworfenen Feind treffen. Wenn Fallende Spinne trifft, fügt dieser Angriff +15..35 Schaden zu und der Feind wird 5..20 Sekunden lang vergiftet.', 'Richtet +15...35 Punkte Schaden an. Verursacht Zustand Gift (5...20 Sekunden). <i>Gegner muss zu Boden geworfen sein, sonst keine Wirkung.</i>', '', '', ''),
(779, 'Schwarzer Lotus-Stoß', 'Wenn Schwarzer Lotus-Stoß erfolgreich ist, fügt er +10..31 Punkte Schaden zu. Wenn der Gegner unter einer Verhexung leidet, erhaltet Ihr 5..13 Energiepunkte.', 'Richtet +10...31 Punkte Schaden an. Ihr erhaltet 5...13 Energiepunkte, falls der Gegner verhext ist.', '', '', ''),
(780, 'Reißzähne des Fuchses', 'Muss einem Leithandangriff folgen. Reißzähne des Fuchses kann nicht geblockt werden und fügt bei einem Treffer +10..35 Punkte Schaden zu.', 'Richtet +10...35 Punkte Schaden an. Kann nicht geblockt werden. <i>Muss einem Leithandangriff folgen.</i>', 'Reißzähne des Fuchses (PvP)', 'Muss einem Leithandangriff folgen. Reißzähne des Fuchses kann nicht geblockt werden und fügt bei einem Treffer +10..25 Punkte Schaden zu.', 'Richtet +10...25 Punkte Schaden an. Kann nicht geblockt werden. <i>Muss einem Leithandangriff folgen.</i>'),
(781, 'Möbiusstoß', 'Muss einem Doppelangriff folgen. Wenn Möbiusstoß erfolgreich ist, fügt dieser Angriff +10..35 Punkte Schaden zu. Falls Ihr einen Feind trefft, dessen Lebenspunkte unter 50% sind, werden alle Eure anderen Angriffsfertigkeiten wieder aufgeladen.', 'Richtet +10...35 Punkte Schaden an. Lädt Eure anderen Angriffsfertigkeiten auf, falls der Gegner weniger als 50% seiner maximalen Lebenspunkte hat. <i>Muss einem Doppelangriff folgen.</i>', '', '', ''),
(782, 'Gezackter Stoß', 'Wenn Gezackter Stoß erfolgreich ist, leidet Euer Ziel 5..20 Sekunden lang unter einer Blutung.', 'Verursacht Zustand Blutung (5...20 Sekunden).', '', '', ''),
(783, 'Unvermuteter Stoß', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +19..31 Punkte Schaden zu. Falls die Lebenspunkte Eures Ziels höher als 90% sind, fügt Ihr zusätzlich 15..75 Punkte Schaden zu.', 'Richtet +19...31 Punkte Schaden an. Richtet zusätzlich 15...75 Punkte Schaden an, falls der Gegner mehr als 90% seiner Lebenspunkte hat.', '', '', ''),
(784, 'Schlingnatter', 'Schlingnatter muss einem Leithandangriff folgen. Der Gegner wird zu Boden geworfen und leidet 5..20 Sekunden lang unter Vergiftung.', 'Wirft zu Boden. Verursacht Zustand Gift (5...20 Sekunden). <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(785, 'Zeichen des Todes', '4..10 Sekunden lang erfährt der Gegner um 33% weniger Heilung.', '4...10 Sekunden: Verringert die Wirkung von Heilungen beim Gegner um 33%.', '', '', ''),
(786, 'Eiserne Handfläche', 'Der berührte Gegner erleidet 5..50 Punkte Schaden und wird zu Boden geworfen, wenn er unter einer Verhexung oder einem Zustand leidet. Eiserne Handfläche zählt als Leithandangriff.', 'Richtet 5...50 Punkte Schaden an. Wirft den Gegner zu Boden, falls er verhext ist oder einen Zustand hat. Zählt als Leithandangriff.', '', '', ''),
(787, 'Unverwüstliche Waffe', '3..12 Sekunden lang bekommt der Verbündete (bzw. Ihr selbst) eine Unverwüstliche Waffe. Während er unter einer Verhexung oder einem Zustand leidet, erhält er eine Lebenspunktregeneration von +1..6 und +24 Rüstung.', '3...12 Sekunden: +1...6 Lebenspunktregeneration und +24 Rüstung. <i>Der Verbündete (bzw. Ihr selbst) muss verhext sein oder einen Zustand haben, sonst keine Wirkung.</i>', '', '', ''),
(788, 'Blind war Mingson', 'Haltet seine Asche 15..60 Sekunden lang. Wenn Ihr Mingsons Asche fallen lasst, werden alle Feinde in der Nähe 3..8 Sekunden lang geblendet.', '15...60 Sekunden: Fallwirkung: Verursacht bei allen Feinden in der Nähe Zustand Blindheit (3...8 Sekunden).', '', '', ''),
(789, 'Erfassend war Kuurong', 'Haltet Kurongs Asche bis zu 15..60 Sekunden lang. Wenn Ihr seine Asche fallen lasst, erleiden alle Feinde in der Nähe 15..75 Punkte Schaden und werden zu Boden geworfen.', '15...60 Sekunden: Fallwirkung: Richtet bei allen Feinden in der Nähe 15...75 Punkte Schaden an und wirft sie zu Boden.', '', '', ''),
(790, 'Nachtragend war Khanhei', 'Haltet Khanheis Asche bis zu 5..11 Sekunden lang. Jedes Mal, wenn Euch ein Feind im Kampf trifft, während Ihr Khanheis Asche haltet, entzieht Ihr diesem Feind 5..35 Lebenspunkte.', '5...11 Sekunden: Ihr entzieht jedem Feind, der Euch mit einem Angriff trifft, 5...35 Lebenspunkte.', '', '', ''),
(791, 'Fleisch von meinem Fleisch', 'Ihr verliert die Hälfte Eurer Lebenspunkte. Belebt das Gruppenmitglied mit Euren aktuellen Lebenspunkten und 5..20% der Energiepunkte wieder.', 'Das Gruppenmitglied wird wiederbelebt (Hälfte Eurer aktuellen Lebenspunkte, 5...20% Energiepunkte). <i>Ihr verliert die Hälfte Eurer Lebenspunkte.</i>', 'Fleisch von meinem Fleisch (PvP)', 'Verliert die Hälfte Eurer Lebenspunkte. Belebt das Gruppenmitglied mit Euren aktuellen Lebenspunkten und 5..20% der Energiepunkte wieder.', 'Das Gruppenmitglied wird wiederbelebt (Hälfte Eurer aktuellen Lebenspunkte, 5...20% Energiepunkte). <i>Ihr verliert die Hälfte Eurer Lebenspunkte.</i>'),
(792, 'Splitterwaffe', 'Während der nächsten 20 Sekunden hat der Verbündete (bzw. Ihr selbst) eine Splitterwaffe. Die nächsten 1..5 Angriffe des Verbündeten (bzw. Eure eigenen) fügen bis zu 4 umstehenden Feinden 5..50 Punkte Schaden zu.', '20 Sekunden: Angriffe richten bei 4 umstehenden Feinden 5...50 Punkte Schaden an. <i>Endet nach 1...5 Angriffen.</i>', 'Splitterwaffe (PvP)', 'Während der nächsten 20 Sekunden hat der Verbündete (bzw. Ihr selbst) eine Splitterwaffe. Die nächsten 1..5 Angriffe des Verbündeten (bzw. Eure eigenen) fügen bis zu 3 umstehenden Feinden 5..30 Punkte Schaden zu.', '20 Sekunden: Angriffe richten bei 3 umstehenden Feinden 5...30 Punkte Schaden an. <i>Endet nach 1...5 Angriffen.</i>'),
(793, 'Waffe der Abwehr', '3..8 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine Waffe der Abwehr, die ihm eine Lebenspunktregeneration von +2..4 und eine 50%ige Chance zum Blocken gewährt.', '(3...8 Sekunden.) +2...4 Lebenspunktregeneration. 50% Chance zu blocken.', 'Waffe der Abwehr (PvP)', '3..8 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine Waffe der Abwehr, die ihm eine Lebenspunktregeneration von +2..4 und eine 50% Chance zum Blocken gewährt. Waffe der Abwehr endet, wenn der betreffende Verbündete mit einem Angriff trifft.', '(3...8 Sekunden.) +2...4 Lebenspunktregeneration. 50% Chance zu blocken. <i>Endet, wenn der Verbündete angreift.</i>'),
(794, 'Klagende Waffe', '3..9 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine Klagende Waffe. Jedes Mal, wenn diese einen angreifenden Feind trifft, wird dieser unterbrochen.', '3...9 Sekunden: Angriffe unterbrechen angreifende Feinde.', '', '', ''),
(795, 'Alptraumwaffe', 'Während der nächsten 12 Sekunden hat der Verbündete (bzw. Ihr selbst) eine Alptraumwaffe. Die nächsten 3 Angriffe des Verbündeten (bzw. Eure eigenen) werden um 10..50 Schadenspunkte reduziert und entziehen bis zu 10..50 Lebenspunkte.', '12 Sekunden: Angriffe des Verbündeten (bzw. von Euch selbst) entziehen 10...50 Lebenspunkte, richten aber 10...50 Punkte weniger Schaden an. <i>Endet nach 3 Angriffen.</i>', '', '', ''),
(799, 'Verführerischer Dunst', 'Ihr unternehmt einen Schattenschritt zum Gegner hin. Dieser leidet 3..9 Sekunden lang unter Benommenheit.', 'Ihr unternehmt einen Schattenschritt zum Gegner. Verursacht Zustand Benommenheit (3...9 Sekunden).', '', '', ''),
(800, 'Dauerhaftes Toxin', '5 Sekunden lang leidet der Gegner unter einer Lebenspunktdegeneration von -1..5. Falls sich der Gegner bewegt, wenn Dauerhaftes Toxin enden sollte, wird es um 5 zusätzliche Sekunden verlängert.', '5 Sekunden: Verursacht -1...5 Lebenspunkdegeneration. Erneuerung: Falls der Gegner sich bewegt, wenn diese Verhexung endet.', '', '', ''),
(801, 'Schleier des Schweigens', 'Alle Eure Zauber werden 15 Sekunden lang deaktiviert. Der berührte Gegner kann 1..3 Sekunden lang keine Zauber wirken.', '1...3 Sekunden: Gegner kann keine Zauber wirken. <i>Eure Zauber werden deaktiviert (15 Sekunden).</i>', '', '', ''),
(802, 'Verteidigungen aufdecken', '1..11 Sekunden lang ist für den Gegner ein Blocken bei Euren Angriffen nicht möglich.', '1...11 Sekunden: Der Gegner kann Eure Angriffe nicht blocken.', '', '', ''),
(803, 'Kraftfresser', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird diese Fertigkeit unterbrochen und Ihr entzieht dem Gegner immer, wenn er innerhalb der nächsten 10 Sekunden einen Zauber wirkt, bis zu 5..15 Energiepunkte.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Entzieht 5...15 Energiepunkte, wenn der Gegner einen Zauber wirkt (10 Sekunden).', '', '', ''),
(804, 'Arkane Schwäche', '1..10 Sekunden lang verursachen alle vom Gegner gewirkten Zauber 10 Überzaubern.', '(1...10 Sekunden.) Die vom anvisierten Gegner gewirkte Zauber verursachen 10 Überzaubern.', '', '', ''),
(805, 'Vampirschreck beleben', 'Der nächste Kadaver wird als Vampirschreck der Stufe 1..17 belebt. Jedes Mal, wenn ein Vampirschreck, den Ihr kontrolliert, Schaden zufügt, erhaltet Ihr dieselbe Menge Lebenspunkte.', 'Erstellt einen Vampirschreck der Stufe 1...17. Ihr erhaltet Lebenspunkte in Höhe des von ihm angerichteten Schadens. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(806, 'Eifer des Sektierers', 'Für 5 Sekunden plus 3 Sekunden pro Rang in Seelensammlung kostet das Wirken Eurer Nekromanten-Zauber -1..6 Energiepunkte weniger, aber Ihr leidet beim Wirken jedes Nekromanten-Zaubers 10 Sekunden lang an einer Blutung.', '(5 Sekunden plus 3 Sekunden pro Rang in Seelensammlung.) Eure Nekromanten-Zauber kosten 1...6 weniger Energie. <i>Ihr leidet beim Wirken jedes Nekromanten-Zaubers an einer Blutung (10 Sekunden).</i>', '', '', ''),
(808, 'Zeichen des Schnitters', '30 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..5. Wenn er stirbt, während er mit Zeichen des Schnitters verhext ist, erhaltet Ihr 5..15 Energiepunkte.', '30 Sekunden: Verursacht -1...5 Lebenspunktdegeneration. Ihr erhaltet 5...15 Energiepunkte, falls der Gegner an dieser Verhexung stirbt.', '', '', ''),
(809, 'Trümmerstein', 'Der Gegner erleidet 25..100 Punkte Kälteschaden und wird 3 Sekunden lang mit Trümmerstein verhext. Wenn Trümmerstein endet, erleiden der Gegner und alle Feinde in der Nähe 25..100 Punkte Kälteschaden.', '3 Sekunden: Anfangswirkung: Richtet 25...100 Punkte Kälteschaden an. Wirkung am Ende: Richtet beim Gegner und allen Feinden in der Nähe 25...100 Punkte Kälteschaden an.', '', '', ''),
(810, 'Verteidigung des Beschützers', '5..11 Sekunden lang habt Ihr selbst und alle Euch umstehenden Verbündeten eine 75%ige Chance, Angriffe zu blocken. Verteidigung des Beschützers endet, wenn Ihr Euch bewegt.', '5...11 Sekunden: Umstehende Verbündete haben eine 75%ige Chance zu blocken. <i>Endet, wenn Ihr Euch bewegt.</i>', '', '', ''),
(811, 'Gemeinsam rennen', '5..15 Sekunden lang rennt Ihr und Euer Tiergefährte 25% schneller.', '5...15 Sekunden: Ihr und Euer Tiergefährte bewegt Euch um 25% schneller.', '', '', ''),
(812, 'Trotzig war Xinrae', 'Haltet Xinraes Asche bis zu 15..60 Sekunden lang. Während Ihr ihre Asche haltet, könnt Ihr durch einen einzelnen Treffer nicht mehr als 20% Eurer maximalen Lebenspunkte verlieren. Wenn Ihr ihre Asche fallen lasst, entzieht Ihr allen Feinden in der Nähe 5..50 Lebenspunkte.', '15...60 Sekunden: Ihr könnt nicht mehr als 20% Eurer maximalen Lebenspunkte durch einen einzelnen Treffer verlieren. Fallwirkung: Ihr entzieht Feinden in der Nähe 5...50 Lebenspunkte.', '', '', ''),
(813, 'Lyssas Aura', '10 Sekunden lang habt Ihr +0..5 Energieregeneration. Diese Verzauberung wird jedes Mal, wenn Ihr einen Zauber auf einen Feind wirkt, neu aufgetragen.', '10 Sekunden: Ihr habt +0...5 Energieregeneration. Erneuerung: Jedes Mal, wenn Ihr einen Zauber auf einen Feind wirkt.', '', '', ''),
(814, 'Schattenzuflucht', '6 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration von 5..10. Wenn Schattenzuflucht endet, heilt Ihr um 40..100 Lebenspunkte, wenn Ihr angreift.', '6 Sekunden: Ihr habt +5...10 Lebenspunktregeneration. Wirkung am Ende: Heilt um 40...100 Lebenspunkte, falls Ihr gerade angreift.', '', '', ''),
(815, 'Skorpiondraht', 'Während der nächsten 8..20 Sekunden werdet Ihr das nächste Mal, wenn Ihr mehr als 30 Meter vom Gegner entfernt steht, zu diesem teleportiert, und der Gegner wird zu Boden geschlagen. Die Reichweite dieses Zaubers ist nur halb so groß wie gewöhnlich.', '8...20 Sekunden: Beim nächsten Mal, wenn Ihr mehr als 30 Meter vom Gegner entfernt seid, unternehmt Ihr einen Schattenschritt zu ihm und werft ihn zu Boden.', '', '', ''),
(816, 'Gespiegelte Haltung', 'Während der nächsten 10..35 Sekunden nehmt Ihr jedes Mal, wenn der Gegner eine Haltung einnimmt, die gleiche Haltung ein.', '10...35 Sekunden: Ihr nehmt immer die gleiche Haltung ein wie der Gegner.', '', '', ''),
(817, 'Zwietracht', 'Wenn der Gegner unter einem Zustand und den Auswirkungen einer Verhexung oder einer Verzauberung steht, erleidet er 30..110 Punkte Schaden.', 'Richtet 30...110 Punkte Schaden an. <i>Gegner muss einen Zustand haben und verhext oder verzaubert sein, sonst keine Wirkung.</i>', 'Zwietracht (PvP)', 'Wenn der Gegner unter einem Zustand und den Auswirkungen einer Verhexung oder einer Verzauberung leidet, erleidet er 30..110 Punkte Schaden.', 'Richtet 30...110 Punkte Schaden an. <i>Gegner muss einen Zustand haben und verhext oder verzaubert sein, sonst keine Wirkung.</i>'),
(818, 'Brunnen der Müdigkeit', 'Der nächste Kadaver wird an seiner Position 10..55 Sekunden lang in einen Brunnen der Müdigkeit verwandelt. Feinde in dem Brunnen der Müdigkeit erleiden eine Energiepunktdegeneration in Höhe von -1.', '(10...55 Sekunden.) Feinde in diesem Brunnen erhalten eine Energiepunktdegeneration von -1. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(819, 'Vampirgeist', 'Entzieht dem Gegner bis zu 5..50 Lebenspunkte. 10 Sekunden lang habt Ihr +5..10 Lebenspunktregeneration.', 'Entzieht dem Gegner 5...50 Lebenspunkte: Ihr habt 5...10 Lebenspunktregeneration (10 Sekunden)', '', '', ''),
(820, 'Verderbtheit', 'Während der nächsten 5..20 Sekunden verlieren immer, wenn der Gegner einen Zauber wirkt, dieser und ein Feind in der Nähe 1..5 Energiepunkte.', '5...20 Sekunden: Lässt jedes Mal 1...5 Energiepunkte verlieren, wenn der Gegner einen Zauber wirkt. Auch ein Feinde<sub>sic</sub> in Eurer Nähe verliert Energie.', '', '', ''),
(821, 'Eisadern', 'Der Gegner erleidet 10..90 Punkte Kälteschaden. Wenn er innerhalb der nächsten 10..35 Sekunden stirbt, erleiden alle Feinde in der Nähe 20..110 Punkte Kälteschaden.', '10...35 Sekunden: Richtet bei Feinden in der Nähe 20...110 Punkte Kälteschaden an, wenn der Gegner stirbt. Anfangswirkung: Richtet 10...90 Punkte Kälteschaden an.', '', '', ''),
(822, 'Weiche Knie', '1..16 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..4 und nimmt 5..10 Punkte Schaden, während er in Bewegung ist.', '1...16 Sekunden: Der Gegner unterliegt einer Lebenspunktdegeneration von -1...4 und nimmt 5...10 Punkte Schaden, während er in Bewegung ist.', '', '', ''),
(823, 'Brennende Eile', '7 Sekunden lang werdet Ihr in Brand gesetzt und bewegt Euch um 30..45% schneller. Wenn Brennende Eile endet, werden alle umstehenden Gegner 3..9 Sekunden lang in Brand gesetzt.', '7 Sekunden: Ihr bewegt Euch um 30...45% schneller. <i>Ihr beginnt zu brennen (7 Sekunden).</i> Wirkung am Ende: Verursacht Zustand Brennen (3...9 Sekunden bei umstehenden Feinden).', '', '', ''),
(824, 'Lavapfeile', 'Lavapfeile fliegen auf den Gegner und bis zu zwei Feinde in seiner Nähe zu und fügen bei einem Treffer 20..65 Punkte Feuerschaden zu.', 'Geschoss: Richtet 20...65 Punkte Feuerschaden an. Bonuswirkung: Schickt Geschosse zu 2 weiteren Feinden in der Nähe des Ziels.', '', '', ''),
(825, 'Kohlenbett', 'An der Position des Gegners wird ein Kohlenbett erzeugt. Während der nächsten 5 Sekunden erleiden umstehende Gegner 5..29 Punkte Feuerschaden pro Sekunde. Alle Feinde, die auf dem Kohlenbett zu Boden geworfen werden, werden 3..7 Sekunden lang in Brand gesetzt.', 'Richtet jede Sekunde 5...29 Punkte Feuerschaden an der Position des anvisierten Gegners an (5 Sekunden). Trifft um euren Zielgegner herum stehende Feinde. Verursacht bei zu Boden geworfenen Gegnern Zustand Brennen (3...7 Sekunden).', '', '', ''),
(826, 'Schattenform', '5..21 Sekunden lang könnt Ihr nicht zum Ziel von feindlichen Zaubern werden, und Ihr erhaltet eine Schadensreduzierung von 5 Lebenspunkten für jede Assassinen-Verzauberung, die auf Euch liegt. Mit einem einzigen Angriff bzw. einer einzigen Fertigkeit könnt Ihr nicht mehr als 5..25 Punkte Schaden anrichten.', '(5...21 Sekunden.) Ihr könnt nicht zum Ziel von feindlichen Zaubern werden. Ihr erhaltet eine Schadensreduzierung von 5 Lebenspunkten für jede Assassinen-Verzauberung, die auf Euch liegt. <i>Mit einem einzigen Angriff bzw. einer einzigen Fertigkeit könnt Ihr nicht mehr als 5...25 Punkte Schaden anrichten.</i>', 'Schattenform (PvP)', '5..15 Sekunden lang könnt Ihr nicht zum Ziel feindlicher Zauber werden und alle gegen Euch gerichteten Angriffe schlagen fehl. Wenn Schattenform endet, verliert Ihr alle Lebenspunkte bis auf 5..50.', '5...15 Sekunden: Ihr könnt nicht zum Ziel feindlicher Zauber werden und Angriffe gegen Euch schlagen fehl. <i>Wirkung am Ende: Ihr verliert alle Lebenspunkte bis auf 5...50.</i>'),
(827, 'Kraft abzapfen', '5..20 Sekunden lang verursacht der Gegner mit seinen Angriffen -5..50 Schaden und alle Eure Angriffe auf diesen Gegner haben zusätzlich eine 33%ige Wahrscheinlichkeit, kritische Treffer zu sein.', '5...20 Sekunden: Gegner richtet -5...50 Punkte Angriffsschaden an. Ihr habt bei diesem Gegner +33% zur Chance auf kritische Treffer.', '', '', ''),
(828, 'Fauler Gestank', 'Der Gegner erleidet 10..65 Punkte Kälteschaden. Wenn er an einem Zustand leidet, wird er mit Faulem Gestank verhext und leidet 10 Sekunden lang unter einer Lebenspunktdegeneration von -1..5.', 'Verursacht -1...5 Lebenspunktdegeneration (10 Sekunden). Anfangswirkung: Richtet 10...65 Punkte Kälteschaden an. <i>Verhexung wird nur auferlegt, wenn der Gegner einen Zustand hat.</i>', '', '', ''),
(830, 'Strahl des Urteils', 'An der aktuellen Position des Gegners wird ein Strahl des Urteils erzeugt, der 5 Sekunden lang jede Sekunde umstehenden Feinden 5..45 Punkte heiligen Schaden und 1..3 Sekunden brennen zufügt.', 'Richtet jede Sekunde 5...45 Punkte heiligen Schaden an und verursacht Brennen (1...3 Sekunden) jede Sekunde (5 Sekunden). Trifft umstehende Feinde um anfängliche Position des Ziels.', '', '', ''),
(831, 'Urzorn', '1..15 Sekunden lang sind Eure Angriffe um 33% und Bewegungen um 25% schneller, aber Ihr erleidet die doppelte Schadensmenge.', '1...15 Sekunde: Eure Angriffe sind um 33% und Eure Bewegungen um 25% schneller. <i>Ihr erleidet die doppelte Schadensmenge.</i>', '', '', ''),
(832, 'Fleischgolem beleben', 'Der nächste Kadaver wird als Fleischgolem der Stufe 3..25 wieder belebt. Der Fleischgolem hinterlässt einen verwendbaren Leichnam. Ihr könnt jeweils nur einen Fleischgolem haben.', 'Erstellt einen Fleischgolem der Stufe 3...25, welcher einen frischen Kadaver zurücklässt, wenn er stirbt. <i>Benutzt einen frischen Kadaver. Ihr könnt immer nur einen Fleischgolem haben.</i>', '', '', ''),
(834, 'Rücksichtslose Hast', '6..12 Sekunden lang werden der Gegner und alle umstehenden Feinde mit Rücksichtsloser Hast verhext. Während dieser Zeit greifen sie um 25% schneller an, es besteht jedoch eine 50%ige Chance, dass ihre Angriffe nicht treffen.', 'Verhext den Gegner und umstehende Feinde (6...12 Sekunden). Die Angriffe der Gegner gehen mit 50%iger Wahrscheinlichkeit daneben, sie greifen jedoch um 25% schneller an.', '', '', ''),
(835, 'Blutsbande', '3..12 Sekunden lang werden der Gegner und alle umstehenden Feinde mit Blutsbande verhext. Wenn ein Verbündeter einen dieser Feinde mit einem Angriff trifft, erhält dieser Verbündete 5..20 Lebenspunkte. Wenn ein mit Blutsbande verhexter Feind stirbt, werden alle umstehenden Verbündeten für 20..100 Lebenspunkte geheilt.', 'Verhext den Gegner und umstehende Feinde (3...12 Sekunden). Verbündete, die diese Feinde treffen, erhalten 5...20 Lebenspunkte. Wenn einer dieser verhexten Feinde stirbt, werden umstehende Verbündete für 20...100 Lebenspunkte geheilt.', '', '', ''),
(836, 'Den Blitz reiten', 'Unternehmt einen Schattenschritt zum Ziel. Alle umstehenden Feinde werden 1..5 Sekunden lang geblendet. Wenn Euer Ziel ein Gegner ist, erleidet er 10..70 Punkte Blitzschaden. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...70 Punkte Blitzschaden an. 25% Rüstungsdurchdringung. Blendet alle umstehenden Feinde. (1...5 Sekunden). Schattenschritt zum Ziel. Das Ziel kann ein Verbündeter sein.', 'Den Blitz reiten (PvP)', 'Unternehmt einen Schattenschritt zum Ziel. Alle umstehenden Feinde werden 1..5 Sekunden lang geblendet. Wenn Euer Ziel ein Gegner ist, erleidet er 10..70 Punkte Blitzschaden. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...70 Punkte Blitzschaden an. 25% Rüstungsdurchdringung. Blendet alle umstehenden Feinde. (1...5 Sekunden). Schattenschritt zum Ziel. Das Ziel kann ein Verbündeter sein.'),
(837, 'Energiesegen', '36..60 Sekunden lang erhöht Energiesegen Eure maximale Gesundheit und die Eures verbündeten Ziels um 1..3 für jeden Punkt Eurer entsprechenden maximalen Energie. Beim Anwenden dieser Verzauberung erhaltet Ihr und Euer Ziel 1..12 Energiepunkte. Ihr erhaltet zudem 1 Energiepunkt pro 2 Punkte, die Ihr in Energiespeicherung habt.', '(36...60 Sekunden.) Eure maximale Gesundheit und die Eures verbündeten Ziels ist für jeden Punkt Eurer maximalen Energie um 1...3 erhöht. Anfangswirkung: Ihr erhaltet beide 1...12 Energiepunkte. Ihr erhaltet +1 Energiepunkt pro 2 Punkte in Energiespeicherung.', '', '', ''),
(838, 'Dwaynas Leid', 'Während der nächsten 30 Sekunden werden der Verbündete (bzw. Ihr selbst) und alle Verbündeten in der Nähe mit Dwaynas Leid verzaubert. Wenn ein Verbündeter stirbt, während er mit Dwaynas Leid verzaubert ist, wird Eure Gruppe um 5..50 Punkte geheilt.', 'Verzaubert Verbündete in der Nähe des Ziels (30 Sekunden). Eure Gruppe wird jedes Mal um 5...50 Lebenspunkte geheilt, wenn einer dieser Verbündeten stirbt.', '', '', ''),
(839, '"Rückzug!"', 'Wenn sich in Hörweite tote Verbündete befinden, bewegt sich Eure Gruppe 5..11 Sekunden lang um 33% schneller.', '5...11 Sekunden: Gruppenmitglieder in Hörweite bewegen sich um 33% schneller. <i>Bleibt wirkungslos, wenn sich in Hörweite keine toten Verbündeten befinden.</i>', '', '', ''),
(840, 'Vergiftetes Herz', 'Ihr und alle umstehenden Feinde leiden 5..15 Sekunden lang an Vergiftung.', 'Verursacht bei umstehenden Feinden Zustand Gift (5...15 Sekunden). <i>Ihr werdet auch vergiftet.</i>', '', '', ''),
(841, 'Stinkender Boden', 'Der Gegner erleidet 15..65 Punkte Kälteschaden. Wenn er am Boden liegt, wird er außerdem 5..20 Sekunden lang vergiftet.', 'Richtet 15...65 Punkte Kälteschaden an. Verursacht Zustand Gift (5...20 Sekunden), falls Gegner zu Boden geworfen ist.', '', '', ''),
(842, 'Blitzbogen', 'Der Gegner erleidet 5..40 Punkte Blitzschaden. Wenn Ihr Überzaubert seid, erleiden bis zu zwei andere in der Nähe befindliche Feinde 15..85 Punkte Blitzschaden. Durch Blitzbogen zugefügter Schaden hat eine Rüstungsdurchdringung von 25%.', 'Richtet 5...40 Punkte Blitzschaden an. Verursacht 15...85 Punkte Blitzschaden bei zwei Gegnern in der Nähe, wenn Ihr Überzaubert seid. 25% Rüstungsdurchdringung.', '', '', ''),
(843, 'Böe', 'Während der nächsten 5..11 Sekunden bewegen Ihr und Euer Verbündeter sich um 33% schneller. Wenn Ihr diesen Zauber wirkt, erleiden alle Feinde in Eurer Nähe und in der Nähe Eures Ziels 15..70 Punkte Kälteschaden. Von Böe getroffene angreifende oder sich bewegende Feinde werden zu Boden geworfen.', '(5...11 Sekunden.) Ihr und Euer Verbündeter bewegen sich um 33% schneller. Anfangswirkung: Feinde in der Nähe Eures Ziel erleiden 15...70 Punkte Kälteschaden. Angreifende oder sich bewegende Feinde werden zu Boden geworfen.', '', '', ''),
(844, 'Aufgeworfene Erde', 'Am Standort des Feindes wird Aufgeworfene Erde erzeugt. In den nächsten 5 Sekunden bewirkt Aufgeworfene Erde bei Feinden in der Nähe des Ortes pro Sekunde 10..40 Punkte Erdschaden. Jeder Feind, der sich schneller als normal bewegt, wenn er von Aufgeworfene Erde getroffen wird, wird zu Boden geworfen.', 'Richtet jede Sekunde 10...40 Punkte Erdschaden an (5 Sekunden). Trifft Feinde in der Nähe der anfänglichen Position des Ziels. Wirft Feinde zu Boden, die sich schneller bewegen als normal.', '', '', ''),
(845, 'Flüssige Flamme', 'Der Gegner erleidet 7..112 Punkte Feuerschaden. Wenn er angreift oder einen Zauber wirkt, erleiden Feinde in der Nähe ebenfalls 7..112 Punkte Feuerschaden.', 'Richtet 7...112 Punkte Feuerschaden an. Richtet bei Feinden in der Nähe des Gegners 7...112 Punkte Feuerschaden an, falls der Gegner gerade angreift oder Zauber wirkt.', '', '', ''),
(846, 'Dampf', 'Der Gegner erleidet 20..60 Punkte Kälteschaden. Brennt der Gegner, wird er 5..10 Sekunden lang durch den Dampf geblendet.', 'Richtet 20...60 Punkte Kälteschaden an. Verursacht Zustand Blindheit (5...10 Sekunden), falls der Gegner brennt.', '', '', ''),
(847, 'Siegel des Segens', 'Heilt den Verbündeten (bzw. Euch selbst) um 20..80 Lebenspunkte. Euer nächster auf einen Verbündeten (bzw. Euch selbst) gerichteter Heil- oder Schutzgebet-Zauber heilt zusätzlich um 20..80 Lebenspunkte.', 'Heilt um 20...80. Euer nächster auf einen Verbündeten gerichteter Heil- oder Schutzgebet-Zauber heilt um +20...80 Lebenspunkte.', '', '', ''),
(848, 'Verhexung umkehren', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). 5..10 Sekunden lang wird der nächste Schaden, den der Verbündete (bzw. Ihr selbst) erleiden würde, um 5..50 Punkte verringert.', '5...10 Sekunden: Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). Der nächste Schaden, den der Verbündete (bzw. Ihr selbst) erleidet, wird um 5...50 Punkte reduziert. <i>Der Verbündete muss verhext sein, sonst keine Wirkung.</i>', '', '', ''),
(849, 'Zerfetzender Hieb', 'Wenn Zerfetzender Hieb erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr einen Feind am Boden trefft, leidet er 5..20 Sekunden lang unter einer Blutung.', 'Richtet +5...20 Punkte Schaden an. Verursacht Zustand Blutung (5...20 Sekunden, falls Gegner zu Boden geworfen ist.', '', '', ''),
(850, 'Erbitterter Schlag', 'Wenn Erbitterter Schlag trifft, fügt Ihr +5..20 Punkte zu. Wenn der Gegner an Schwäche leidet, erleidet er außerdem 1..8 Sekunden lang eine Tiefe Wunde.', 'Richtet +5...20 Punkte Schaden an. Verursacht Tiefe Wunde (1...8 Sekunden), falls der Gegner geschwächt ist.', '', '', ''),
(851, 'Sonne-Mond-Hieb', 'Ihr greift den Gegner zweimal an. Diese Angriffe können nicht geblockt werden.', 'Ihr greift den Gegner zweimal an. Kann nicht geblockt werden.', '', '', ''),
(852, 'Splitterschuss', 'Wenn Splitterschuss trifft, fügt Ihr dem Gegner +3..15 Punkte Schaden zu. Wenn Splitterschuss geblockt wird, erleiden alle Feinde um Euren Gegner 5..65 Punkte Schaden.', 'Richtet +3...15 Punkte Schaden an. Richtet bei umstehenden Feinden 5...65 Punkte Schaden an, falls geblockt.', '', '', ''),
(853, 'Melandrus Schuss', 'Wenn Melandrus Schuss trifft, leidet Euer Gegner 5..25 Sekunden lang an einer Blutung. Wenn er einen Gegner trifft, der sich bewegt oder zu Boden geworfen wurde, erleidet dieser +10..25 Punkte Schaden und leidet 5..15 Sekunden lang an Verkrüppelung.', 'Verursacht Blutung (5...25 Sekunden). Richtet +10...25 Punkte Schaden an und verursacht Verkrüppelung (5...15 Sekunden), falls der Gegner sich bewegt oder zu Boden geworfen wurde.', '', '', ''),
(854, 'Schlinge', 'Wird Schlinge aktiviert, werden alle Gegner in der Nähe 3..15 Sekunden lang verkrüppelt. Schlinge endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Verursacht Zustand Verkrüppelung (3...15 Sekunden). <i>Leicht zu unterbrechen</i>.', '', '', ''),
(858, 'Tanzende Dolche', 'Drei Tanzende Dolche fliegen auf den Gegner zu. Jeder einzelne fügt bei einem Treffer 5..35 Punkte Erdschaden zu. Tanzende Dolche hat nur die halbe Reichweite. Diese Fertigkeit zählt als Leithandangriff.', 'Drei Geschosse: Richten je 5...35 Punkte Erdschaden an. Zählt als Leithandangriff.', '', '', ''),
(859, 'Alptraum beschwören', '2..16 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -8.', '2...16 Sekunden: Verursacht -8 Lebenspunktdegeneration.', '', '', ''),
(860, 'Siegel der Unterbrechung', 'Wenn der Gegner einen Zauber wirkt, wird dieser unterbrochen und der Gegner erleidet 10..51 Punkte Schaden. Wenn dieser Gegner verhext ist, kann Siegel der Unterbrechung alle Fertigkeiten, die nicht auf Zaubern beruhen, unterbrechen.', 'Unterbricht einen Zauber. Kann jede Fertigkeit unterbrechen, falls der Gegner verhext ist. Unterbrechungswirkung: Richtet 10...51 Punkte Schaden an.', '', '', ''),
(862, 'Heißhungriger Blick', 'Fügt dem Gegner und in der Nähe befindlichen Feinden 15..30 Punkte Schaden zu und entzieht diesen 15..30 Lebenspunkte.', 'Fügt dem Gegner und in der Nähe befindlichen Feinden 15...30 Punkte Schaden zu und entzieht diesen 15...30 Lebenspunkte.', '', '', ''),
(863, 'Orden der Abtrünnigkeit', '5 Sekunden lang verliert der Gegner jedes Mal, wenn er von einem Gruppenmitglied mit physischem Schaden getroffen wird, eine Verzauberung. Für jede entfernte Mönchs-Verzauberung verliert Ihr 25..15% Eurer maximalen Lebenspunkte.', 'Verzaubert alle Gruppenmitglieder (5 Sekunden). Die Betroffenen entfernen eine Verzauberung, wenn sie physischen Schaden anrichten. <i>Entfernungskosten: Für jede entfernte Mönch-Verzauberung verliert Ihr 25...15% Eurer maximalen Lebenspunkte.</i>', '', '', ''),
(864, 'Repressiver Blick', 'Gegner und umstehende Feinde erleiden +10..30 Punkte Schattenschaden. Feinde, die bereits unter einem Zustand leiden, werden 3..12 Sekunden lang vergiftet und geschwächt.', 'Richtet beim Gegner und umstehenden Feinden 10...30 Punkte Schaden an. Verursacht bei unter einem Zustand leidenden Feinden die Zustäde Gift und Schwäche (3...12 Sekunden).', '', '', ''),
(865, 'Blitzhammer', 'Der Gegner erleidet 10..100 Punkte Blitzschaden und leidet 5..20 Sekunden lang an Beschädigter Rüstung. Blitzhammer hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...100 Punkte Blitzschaden an. Verursacht Beschädigte Rüstung (5...20 Sekunden) 25% Rüstungsdurchdringung.', 'Blitzhammer (PvP)', 'Der Gegner erleidet 10..100 Punkte Blitzschaden. Blitzhammer hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...100 Punkte Blitzschaden an. 25% Rüstungsdurchdringung.'),
(866, 'Dunstklinge', 'Der Gegner erleidet 15..135 Punkte Kälteschaden. Dunstklinge verursacht nur die halbe Schadensmenge, wenn dieser Gegner mit Verzauberungen belegt ist.', 'Richtet 15...135 Punkte Kälteschaden an. Halber Schaden, falls Gegner verzaubert ist.', '', '', ''),
(867, 'Heilendes Licht', 'Heilt den Verbündeten (bzw. Euch selbst) um 40..100 Lebenspunkte. Wenn Euer Ziel einer Verzauberung unterliegt, erhaltet Ihr 1..3 Energiepunkte.', 'Heilt um 40...100 Lebenspunkte. Ihr erhaltet 1...3 Energiepunkte, falls der Verbündete (bzw. Ihr selbst) verzaubert ist.', '', '', ''),
(869, '"Feigling!"', 'Wenn sich der Gegner bewegt, wird er zu Boden geworfen.', 'Wirft zu Boden, falls der Gegner sich bewegt.', '', '', ''),
(870, 'Pestilenz', 'Erstellt einen Geist der Stufe 1..10. Wenn eine Kreatur, die kein Geist ist, in seiner Reichweite stirbt, werden alle Zustände, mit denen sie belegt ist, auf alle Kreaturen in dem Bereich verteilt, die bereits an einem Zustand leiden. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Wenn eine Kreatur in Reichweite stirbt, werden die Zustände dieser Kreatur auf alle anderen Kreaturen im Bereich übertragen, die einen Zustand haben. Geister sind nicht betroffen.', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(871, 'Schattengesang', 'Erstellt einen Geist der Stufe 1..6. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu und führen 1..6 Sekunden lang zu Blindheit. Dieser Geist stirbt nach 30 Sekunden.', 'Erstellt einen Geist der Stufe 1...6 (30 Sekunden Lebensdauer). Seine Angriffe fügen 5...20 Punkte Schaden zu und verursachen den Zustand Blindheit (1...6 Sekunde).', 'Schattengesang (PvP)', 'Erstellt einen Geist der Stufe 1..6. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu und führen 1..6 Sekunden lang zu Blindheit. Dieser Geist stirbt nach 30 Sekunden.', 'Erstellt einen Geist der Stufe 1...6 (30 Sekunden Lebensdauer). Seine Angriffe fügen 5...20 Punkte Schaden zu und verursachen den Zustand Blindheit (1...6 Sekunde).'),
(876, 'Siegel der Schatten', 'Der Gegner erleidet 5..35 Punkte Schaden. Wenn Euer Ziel geblendet wurde, erleidet es zusätzlich 15..60 Punkte Schaden.', 'Richtet 5...35 Punkte Schaden an. Richtet zusätzlich 15...60 Punkte Schaden an, falls der Gegner geblendet ist.', '', '', ''),
(877, 'Lyssas Ausgleich', 'Der ausgewählte Feind verliert eine Verzauberung. Wenn Ihr über mehr Verzauberungen als der ausgewählte Feind verfügt, hat diese Fertigkeit keinen Effekt.', 'Entfernt beim Gegner eine Verzauberung. <i>Keine Wirkung, falls Ihr mehr Verzauberungen habt als der Gegner.</i>', '', '', ''),
(878, 'Visionen des Bedauerns', '10 Sekunden lang erleiden der Gegner und umstehende Feinde jedes Mal, wenn sie eine Fertigkeit verwenden, 15..45 Punkte Schaden und 5..50 Schaden, sofern sie unter keiner weiteren Mesmer-Verhexung leiden.', 'Verhext außerdem den Gegner und umstehende Feinde (10 Sekunden). Diese Feinde erleiden 15...45 Punkte Schaden, sobald sie eine Fertigkeit verwenden und 5...50 Punkte zusätzlichen Schaden, wenn sie nicht eine Mesmer-Verhexung erlitten haben.', 'Visionen des Bedauerns (PvP)', '10 Sekunden lang erleiden der Gegner und umstehende Feinde jedes Mal, wenn sie eine Fertigkeit verwenden, 10..30 Punkte Schaden und 5..40 Schaden, sofern sie unter keiner weiteren Mesmer-Verhexung leiden.', 'Verhext außerdem den Gegner und umstehende Feinde (10 Sekunden). Diese Feinde erleiden 10...30 Punkte Schaden, sobald sie eine Fertigkeit verwenden und 5...40 Punkte zusätzlichen Schaden, wenn sie nicht eine Mesmer-Verhexung erlitten haben.'),
(879, 'Illusion der Schmerzen', '8 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -3..10 und nimmt jede Sekunde 3..10 Punkte Schaden. Wenn "Illusion der Schmerzen" endet, wird der Gegner um 36..120 Lebenspunkte geheilt.', '(8 Sekunden.) Verursacht eine Lebenspunktdegeneration von -3...10 und der Gegner nimmt jede Sekunde 3...10 Punkte Schaden. Wirkung am Ende: Der Gegner wird um 36...120 Punkte geheilt.', 'Illusion der Schmerzen (PvP)', 'Der Feind unterliegt 8 Sekunden lang einer Lebenspunktdegeneration von -3..10 und nimmt jede Sekunde 3..10 Punkte Schaden. Wenn "Illusion der Schmerzen" endet oder wieder einsetzt, wird der Feind um 36..120 Lebenspunkte geheilt.', '(8 Sekunden.) Verursacht eine Lebenspunktdegeneration von -3...10 und der Gegner nimmt jede Sekunde 3...10 Punkte Schaden. Wirkung am Ende: Der Gegner wird um 36...120 Punkte geheilt. <i>Endet bei erneuter Anwendung</i>'),
(880, 'Gestohlene Eile', '1..10 Sekunden lang brauchen der Gegner und alle umstehenden Feinde um 100% länger, um ihre Zauber zu wirken. Das Wirken von Zaubern auf diese Gegner durch Euch oder Eure Verbündeten benötigt um 50% weniger Zeit.', 'Verhext außerdem umstehende Feinde (1...10 Sekunden). Verdoppelt die Wirkzeit von Zaubern. Die Wirkzeit von Zaubern, die Ihr oder Eure Verbündeten auf den Gegner wirkt, wird um 50% gesenkt.', 'Gestohlene Eile (PvP)', '1..10 Sekunden lang brauchen der Gegner und alle umstehenden Feinde 100% länger, um ihre Zauber zu wirken. Von Euch gegen diese Feinde gewirkte Zauber benötigen 50% weniger Zeit.', 'Verhext umstehende Feinde (1...10 Sekunden). Verdoppelt die Wirkzeit von Zaubern. Die Wirkzeit von Zaubern, die Ihr auf verhexte Gegner wirkt, wird um 50% gesenkt.'),
(881, 'Äther-Siegel', 'Wenn Eure Energie unter 5..10 Punkten liegt, erhaltet Ihr 10..20 Energiepunkte.', 'Ihr erhaltet 10...20 Energiepunkte. <i>Ihr müsst weniger als 5...10 Energiepunkte haben, sonst keine Wirkung.</i>', '', '', ''),
(882, 'Siegel der Entzauberung', 'Ihr verliert Eure gesamte Energie. Der Gegner verliert eine Verzauberung.', 'Entfernt eine Verzauberung. <i>Ihr verliert alle Energiepunkte.</i>', '', '', ''),
(883, 'Stimmminderheit', '5..20 Sekunden lang können der Gegner und alle Feinde in der Nähe keine Schreie oder Anfeuerungsrufe verwenden.', 'Verhext den Gegner und Feinde in der Nähe (5...20 Sekunden). Die Betroffenen können keine Schreie und Anfeuerungsrufe benutzen.', '', '', ''),
(884, 'Versengende Flammen', 'Der Gegner und alle Feinde in der Nähe werden von Versengenden Flammen getroffen. Feinde, die bereits brennen, wenn diese Fertigkeit gewirkt wird, erleiden 10..100 Punkte Feuerschaden. Feinde, die noch nicht in Brand gesetzt sind, brennen 1..7 Sekunden lang.', 'Trifft Feinde in der Nähe des Ziels. Richtet bei bereits brennenden Feinden 10...100 Punkte Feuerschaden an. Verursacht bei nicht brennenden Feinden Zustand Brennen (1...7 Sekunden).', '', '', ''),
(885, 'Schildwächter', '1..4 Sekunden lang erhalten alle Gruppenmitglieder in Hörweite eine 75%ige Chance, Angriffe zu blocken. Jedes Mal, wenn ein Angriff geblockt wird, werden alle Verbündeten in Hörweite um 10..40 Lebenspunkte geheilt und Schildwächter endet.', '(1...4 Sekunden.) Gruppenmitglieder in Hörweite haben eine 75%ige Chance, Angriffe zu blocken. Blockwirkung: Verbündete in Hörweite werden um 10...40 geheilt und Schildwächter endet.', '', '', ''),
(886, 'Beruhigender Hauch', '8..18 Sekunden lang erfreut sich der Verbündete (bzw. Ihr selbst) einer Lebenspunktregeneration von +10. Diese Verzauberung endet, wenn der Verbündete angreift oder eine Fertigkeit benutzt.', '8...18 Sekunden: +10 Lebenspunktregeneration. <i>Endet, wenn der Verbündete (bzw. Ihr selbst) angreift oder eine Fertigkeit benutzt.</i>', '', '', ''),
(887, 'Siegel der Verjüngung', 'Heilt Verbündeten (bzw. Euch selbst) um 15..75 Punkte. Wenn der Verbündete (bzw. Ihr selbst) einen Zauber wirkt oder angreift, wird er um zusätzlich 15..75 Lebenspunkte geheilt.', 'Heilt um 15...75 Lebenspunkte. Heilt um weitere 15...75 Lebenspunkte, falls der Verbündete (bzw. Ihr selbst) gerade einen Zauber wirkt oder angreift.', '', '', ''),
(888, 'Wirbelnde Axt', 'Wenn Wirbelnde Axt erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu und jegliche Haltung, die von Eurem Gegner eingenommen wird, endet. Dieser Angriff kann nicht geblockt werden.', 'Richtet +5...20 Punkte Schaden an und entfernt eine Haltung. Kann nicht geblockt werden.', '', '', ''),
(889, 'Energischer Schlag', 'Wenn Energischer Schlag trifft, fügt er +10..30 Punkte Schaden zu und jede Haltung, die der Gegner einsetzt, endet. Der Gegner leidet 5..20 Sekunden lang  an Schwäche. Dieser Angriff kann nicht geblockt werden.', 'Richtet +10...30 Punkte Schaden an. Entfernt jede Haltung des Gegners. Verursacht Zustand Schwäche (5...20 Sekunden). Kann nicht geblockt werden.', '', '', ''),
(891, '"Niemand darf vorbei!"', 'Alle Gegner in der Nähe, die sich bewegen, werden zu Boden geworfen. Diese Fertigkeit wird für jeden zu Boden geworfenen Feind um 1..8 Sekunden schneller wieder aufgeladen (maximal 25 Sekunden).', 'Wirft alle Feinde in der Nähe zu Boden, die sich bewegen. Wird für jeden betroffenen Feind um 1...8 Sekunden schneller wieder aufgeladen (maximal 25 Sekunden).', '', '', ''),
(892, 'Zitternde Klinge', 'Wenn Zitternde Klinge erfolgreich ist, fügt Ihr +10..40 Punkte Schaden zu. Wenn dieser Angriff einen sich bewegenden Feind trifft, leidet dieser 5 Sekunden lang an Benommenheit.', 'Richtet +10...40 Punkte Schaden an. Verursacht Zustand Benommenheit (5 Sekunden), falls der Gegner sich bewegt.', '', '', ''),
(893, 'Suchende Pfeile', '3..14 Sekunden lang ist ein Blocken bei Euren Pfeilen nicht möglich. Suchende Pfeile endet, wenn Ihr nicht trefft.', '3...14 Sekunden: Eure Pfeile können nicht geblockt werden. <i>Endet, sobald Ihr nicht trefft.</i>', '', '', ''),
(898, 'Überlastung', '5 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von 1..3. Falls dieser Gegner gerade eine Fertigkeit wirkt, fügt Ihr diesem und den umstehenden Feinden 15..75 Punkte Schaden zu.', '(5 Sekunden.) Verursacht 1...3 Lebenspunktdegeneration. Falls dieser Gegner gerade eine Fertigkeit wirkt, erleiden dieser und die umstehenden Feinde 15...75 Punkte Schaden.', '', '', ''),
(899, 'Bilder der Reue', '5..10 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..3. Wenn er mitten im Angriff war, erleidet er 10..52 Punkte Schaden.', '5...10 Sekunden: Verursacht -1...3 Lebenspunktdegeneration. Anfangswirkung: 10...52 Punkte Schaden, falls der Gegner gerade angreift.', '', '', ''),
(900, 'Geteilte Bürde', '5..20 Sekunden lang sind die Angriffe, gewirkten Zauber und Bewegungen des Gegners und aller Feinde in der Nähe um 50% langsamer.', 'Verhext auch Feinde in der Nähe Eures Gegners (5...20 Sekunden). Die Angriffe, gewirkten Zauber und Bewegungen dieser Feinde sind um 50% langsamer.', 'Geteilte Bürde (PvP)', '5..20 Sekunden lang sind die Angriffe, gewirkten Zauber und Bewegungen des Gegners und aller umstehenden Feinde um 33% langsamer.', 'Verhext auch Feinde in der Nähe Eures Gegners (5...20 Sekunden). Die Angriffe, gewirkten Zauber und Bewegungen dieser Feinde sind um 33% langsamer.'),
(901, 'Seelenfessel', '30 Sekunden lang erleidet der Heiler des Gegners jedes Mal, wenn er den Gegner heilt, 20..80 Punkte Schaden. Diese Verhexung endet, wenn der Gegner mit Peinigungsgebeten belegt ist.', '30 Sekunden: Der Heiler des Gegners erleidet jedes Mal, wenn er den Gegner heilt, 20...80 Punkte Schaden. <i>Endet, wenn der Gegner mit Peinigungsgebeten belegt ist.</i>', '', '', ''),
(902, 'Blut des Aggressoren', 'Entzieht dem Gegner bis zu 5..45 Lebenspunkte. Wenn der Gegner angegriffen hat, leidet er 3..12 Sekunden lang unter Schwäche.', 'Entzieht 5...45 Lebenspunkte. Verursacht Schwäche (3...12 Sekunden), wenn der Gegner angegriffen hat.', '', '', ''),
(903, 'Eisprisma', 'Der Gegner erleidet 15..75 Punkte Kälteschaden. Wenn der Gegner unter eine Wassermagie-Verhexung leidet, fügt Eisprisma allen Feinden in der Nähe +15..75 Kälteschaden zu.', 'Richtet 10...75 Punkte Kälteschaden an. Verursacht 15...75 Punkte Kälteschaden bei allen Feinden in der Nähe, wenn der Gegner unter einer Wassermagie-Verhexung leidet.', '', '', ''),
(904, 'Zornige Axt', 'Wenn Zornige Axt erfolgreich ist, fügt Ihr +5..35 Punkte Schaden zu. Wenn dieser Angriff geblockt wird, erhaltet Ihr 3 Adrenalinstöße.', 'Richtet +5...35 Punkte Schaden an. Ihr erhaltet 3 Adrenalinstöße, falls geblockt.', '', '', ''),
(905, 'Günstiger Schlag', 'Wenn Günstiger Schlag trifft, fügt Ihr +5..20 Punkte Schaden zu und erhaltet 3..8 Energiepunkte. Wenn der Gegner unter Schwäche leidet, kann dieser Angriff nicht geblockt werden.', 'Richtet +5...20 Punkte Schaden an. Ihr erhaltet 3...8 Energiepunkte. Kann nicht geblockt werden, wenn der Gegner geschwächt ist.', '', '', ''),
(906, '"Auf Eure Knie!"', 'Alles Adrenalin geht verloren. Wenn ein umstehender Gegner zu Boden geworfen ist, werden alle Eure Haltungen wieder aufgeladen.', 'Lädt alle Eure Haltungen wieder auf, wenn Ihr einen zu Boden geworfenen Feind umsteht. <i>Ihr verliert alles Adrenalin.</i>', '', '', ''),
(907, 'Drachenhieb', 'Wenn Drachenhieb erfolgreich ist, fügt Ihr  +10..40 Punkte Schaden zu und erhaltet 1..5 Adrenalinstöße.', 'Richtet +10...40 Punkte Schaden an. Ihr erhaltet 1...5 Adrenalinstöße, falls Ihr trefft.', '', '', ''),
(908, 'Plünderer-Schuss', 'Wenn Plünderer-Schuss erfolgreich ist, fügt Ihr +10..35 Punkte Schaden zu, und alle Eure Nichtangriffs-Fertigkeiten werden 10 Sekunden lang deaktiviert.', 'Richtet +10...35 Punkte Schaden an. <i>Eure Nicht-Angriffsfertigkeiten werden deaktiviert (10 Sekunden), falls dieser Angriff trifft.</i>', '', '', ''),
(909, 'Scharfschuss', 'Wenn Scharfschuss erfolgreich ist, fügt Ihr +10..25 Punkte Schaden zu, aber alle Eure anderen Angriffs-Fertigkeiten werden 5..3 Sekunden lang deaktiviert.', 'Richtet +10...25 Punkte Schaden an. <i>Eure anderen Angriffsfertigkeiten werden deaktiviert (5...3 Sekunden), falls dieser Angriff trifft.</i>', '', '', ''),
(910, 'Geisterriss', 'An der Position des Gegners wird ein Geisterriss erzeugt. Nach 3 Sekunden erleiden alle umstehenden Feinde 25..125 Punkte Blitzschaden. Wenn Geisterriss endet, leiden die im Wirkungsbereich befindlichen Feinde 1..20 Sekunden lang an Beschädigter Rüstung.', '(3 Sekunden.) Trifft Feinde in der Nähe der ursprünglichen Position des Gegners. Richtet nach 3 Sekunden 25...125 Punkte Blitzschaden an. Wirkung am Ende: Verursacht 1...20 Sekunden lang Beschädigte Rüstung.', '', '', ''),
(911, 'Vereinigung', 'Erstellt einen Geist der Stufe 1..12. Jedes Mal, wenn ein Verbündeter in seiner Reichweite, der kein Geist ist, Schaden erleidet oder Lebenspunkte entzogen bekommt, wird dies um 15 Punkte reduziert und der Geist erleidet 15 Punkte Schaden. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (30...60 Sekunden Lebensdauer). Jedes Mal, wenn ein Verbündeter in seiner Reichweite, der kein Geist ist, Schaden erleidet oder Lebenspunkte entzogen bekommt, wird dies um 15 Punkte reduziert und der Geist erleidet 15 Punkte Schaden.', 'Vereinigung (PvP)', 'Erstellt einen Geist der Stufe 1..8. Jedes Mal, wenn ein Verbündeter in seiner Reichweite, der kein Geist ist, Schaden erleidet oder Lebenspunkte entzogen bekommt, wird dies um 15 Punkte reduziert und der Geist erleidet 15 Punkte Schaden. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...8 (30...60 Sekunden Lebensdauer). Jedes Mal, wenn ein Verbündeter in seiner Reichweite, der kein Geist ist, Schaden erleidet oder Lebenspunkte entzogen bekommt, wird dies um 15 Punkte reduziert und der Geist erleidet 15 Punkte Schaden.'),
(913, 'Ruhig war Tanasen', 'Haltet Tanasens Asche bis zu 5..20 Sekunden lang. Während Ihr seine Asche haltet, habt Ihr +10..25 Rüstung und könnt nicht unterbrochen werden.', '5...20 Sekunden: Ihr habt +10...25 Rüstung. Ihr könnt nicht unterbrochen werden.', '', '', ''),
(914, 'Seele verzehren', 'Ihr entzieht dem Gegner 5..60 Lebenspunkte. Alle feindlichen herbeigerufenen Kreaturen in dem Bereich erleiden 25..125 Punkte Schaden.', 'Entzieht dem Gegner 5...60 Lebenspunkte. Richtet 25...125 Punkte Schaden an herbeigerufenen Kreaturen in dem Bereich an.', '', '', ''),
(915, 'Geisterlicht', 'Der Verbündete (bzw. Ihr selbst) wird um 60..180 Punkte geheilt. Wenn sich Geister in Hörweite befinden, opfert Ihr keine Lebenspunkte.', 'Heilt um 60...180 Lebenspunkte. Ihr opfert keine Lebenspunkte, falls Ihr Euch in Hörweite von Geistern befindet.', '', '', ''),
(916, 'Wehklage', '5..20 Sekunden lang unterliegen der Gegner und alle in der Nähe befindlichen Feinde einer Lebenspunktdegeneration von -0..3. Wenn Ihr Euch beim Wirken dieser Verhexung in Hörweite eines Geistes oder Kadavers befindet, erleiden betroffene Feinde 10..50 Punkte Schaden.', '(5...20 Sekunden.) Verhext auch umstehende Feinde. Verursacht -0...3 Lebenspunktedegeneration. Anfangswirkung: Verursacht 10...50 Punkte Schaden, wenn Ihr Euch in Hörweite eines Geistes oder Kadavers befindet.', '', '', ''),
(917, 'Seele zerreißen', 'Der anvisierte verbündete Geist wird vernichtet. Alle Feinde in dessen Nähe erleiden 50..140 Punkte Blitzschaden und werden 3..12 Sekunden lang geblendet.', 'Vernichtet den verbündeten Geist. Richtet bei Feinden in der Nähe 50...140 Punkte Blitzschaden an und verursacht Zustand Blindheit (3...12 Sekunden).', '', '', ''),
(918, 'Geist zu Fleisch', 'Der berührte Geistverbündete wird vernichtet. Alle Verbündeten in der Nähe werden um 30..240 Lebenspunkte geheilt.', 'Vernichtet den berührten Geist. Heilt alle Verbündeten in der Nähe um 30...240 Lebenspunkte.', '', '', ''),
(919, 'Geisterbrand', 'Der Gegner erleidet 5..50 Punkte Blitzschaden. Wenn sich Geister in Hörweite befinden, führt Geisterbrand 1..5 Sekunden lang zu Brennen.', 'Richtet 5...50 Punkte Blitzschaden an. Verursacht Zustand Brennen (1...5 Sekunden), falls Ihr Euch in Hörweite eines Geistes befindet.', '', '', ''),
(920, 'Vernichtung', 'Erstellt einen Geist der Stufe 1..14, der nach 30 Sekunden stirbt. Wenn dieser Geist stirbt, erleiden alle in dem Bereich befindlichen Gegner für jede Sekunde, die der Geist am Leben war, 5..25 Punkte Schaden (maximal 150).', 'Erstellt einen Geist der Stufe 1...14 (30 Sekunden Lebensdauer). Wenn dieser Geist stirbt, verursacht er bei in dem Bereich befindlichen Gegner<sub>sic</sub> für jede Sekunde, die er am Leben war, 5...25 Punkte Schaden (maximal 150).', 'Vernichtung (PvP)', 'Erstellt einen Geist der Stufe 1..10, der nach 30 Sekunden stirbt. Wenn dieser Geist stirbt, erleiden alle in dem Bereich befindlichen Gegner für jede Sekunde, die der Geist am Leben war, 1..10 Punkte Schaden (maximal 150).', 'Erstellt einen Geist der Stufe 1...10 (30 Sekunden Lebensdauer). Wenn dieser Geist stirbt, verursacht er bei in dem Bereich befindlichen Gegner<sub>sic</sub> für jede Sekunde, die er am Leben war, 1...10 Punkte Schaden (maximal 150).'),
(921, 'Missklang', 'Erstellt einen Geist der Stufe 1..12. Dieser Geist fügt 5..20 Punkte Schaden zu und all jene, die von einem seiner Angriffe getroffen werden, werden unterbrochen. Dieser Geist stirbt nach 10..25 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (10...25 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an und unterbrechen Aktionen.', 'Missklang (PvP)', 'Erstellt einen Geist der Stufe 1..8. Dieser Geist fügt 5..20 Punkte Schaden zu und all jene, die von einem seiner Angriffe getroffen werden, werden unterbrochen. Dieser Geist stirbt nach 10..25 Sekunden.', 'Erstellt einen Geist der Stufe 1...8 (10...25 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an und unterbrechen Aktionen.'),
(923, 'Entzauberung', 'Erstellt einen Geist der Stufe 1..12. Dieser Geist fügt 5..20 Punkte Schaden zu und all jene, die von einem seiner Angriffe getroffen werden, verlieren eine Verzauberung. Dieser Geist stirbt nach 10..35 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (10...35 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an und entfernen eine Verzauberung.', 'Entzauberung (PvP)', 'Erstellt einen Geist der Stufe 1..8. Dieser Geist fügt 5..20 Punkte Schaden zu und all jene, die von einem seiner Angriffe getroffen werden, verlieren eine Verzauberung. Dieser Geist stirbt nach 10..35 Sekunden.', 'Erstellt einen Geist der Stufe 1...8 (10...35 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an und entfernen eine Verzauberung.'),
(925, 'Rückruf', 'Solange Rückruf aufrecht erhalten wird, passiert nichts. Wenn Rückruf endet, unternehmt Ihr einen Schattenschritt zu dem Verbündeten, den Ihr beim Aktivieren dieser Fertigkeit ausgewählt hattet, und alle Eure Fertigkeiten sind 10 Sekunden lang deaktiviert.', 'Wirkung am Ende: Unternehmt einen Schattenschritt zum Verbündeten. <i>Sich selbst als Ziel wählen nicht möglich. Deaktiviert am Ende alle Eure Fertigkeiten (10 Sekunden).</i>', '', '', ''),
(926, 'Dolche wetzen', 'Während der nächsten 5..30 Sekunden führen Eure Dolchangriffe zu einer 5..15 Sekunden langen Blutung.', '(5...30 Sekunden.) Eure Dolchangriffe verursachen den Zustand Blutung (5...15 Sekunden).', '', '', ''),
(927, 'Schändliche Angst', '10 Sekunden lang bewegt sich der Gegner um 10% schneller als normal. Pro Sekunde, die sich der Gegner bewegt, erleidet er 5..20 Punkte Schaden.', '10 Sekunden: Gegner erleidet jede Sekunde 5...20 Punkte Schaden, wenn er sich bewegt, allerdings bewegt er sich um 10% schneller.', '', '', ''),
(928, 'Schattenschleier', '3..9 Sekunden lang kann der Gegner nicht zum Ziel von Verzauberungen werden.', '3...9 Sekunden: Der Gegner kann nicht Ziel von Verzauberungen sein.', '', '', ''),
(929, 'Schatten der Eile', '10..40 Sekunden lang bewegt Ihr Euch um 15% schneller als normal. Wenn Schatten der Eile endet, kehrt Ihr zu der Position zurück, an der Ihr diese Fertigkeit aktiviert habt.', '10...40 Sekunden: Ihr bewegt Euch um 15% schneller. Wirkung am Ende: Ihr kehrt zur anfänglichen Position zurück.', '', '', ''),
(930, 'Günstige Beschwörung', 'Während der nächsten 20 Sekunden wird der nächste von Euch verwendete Zauber zusätzlich 10..5 Sekunden lang deaktiviert und Ihr erhaltet Energie im Ausmaß von 110..200% der Energiekosten dieses Zaubers.', '20 Sekunden: Ihr erhaltet bei Eurem nächsten Zauber 110...200% der Energiekosten zurück. <i>+10...5 Sekunden zur Aufladezeit des Zaubers.</i>', '', '', ''),
(931, 'Kraftrevanche', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird dieser unterbrochen und der Gegner erhält 10..5 Energiepunkte.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Gegner erhält 10...5 Energiepunkte.', '', '', ''),
(932, 'Erschweren', 'Falls der Gegner eine Fertigkeit aktiviert hat, wird diese unterbrochen und zusätzlich bei diesem und allen Feinden im Bereich 5..12 Sekunden lang deaktiviert.', 'Unterbricht eine Fertigkeit. Unterbrechungswirkung: Deaktiviert unterbrochene Fertigkeit (+5...12 Sekunden) beim Gegner und allen Feinden im Bereich.', '', '', ''),
(933, 'Sturm zerschlagen', 'Der Gegner verliert alle Verzauberungen. Für jede Verzauberung, die auf diese Art entfernt wird, wird Sturm zerschlagen zusätzlich 7 Sekunden lang deaktiviert.', 'Entfernt alle Verzauberungen. <i>Entfernungskosten: Sturm zerschlagen wird für jede entfernte Verzauberung für +7 Sekunden deaktiviert.</i>', '', '', ''),
(934, 'Unnatürliches Siegel', 'Der Gegner erleidet 15..75 Punkte Schaden. Unterliegt der Gegner einer Verzauberung oder Verhexung, erleiden umstehende Feinde 5..50 Punkte Schaden.', 'Richtet 15...75 Punkte Schaden an. Richtet bei anderen umstehenden Feinden 5...50 Punkte Schaden an, falls der Gegner verhext oder verzaubert ist.', 'Unnatürliches Siegel (PvP)', 'Der Gegner erleidet 10..60 Punkte Schaden. Unterliegt der Gegner einer Verzauberung oder Verhexung, erleiden umstehende Feinde 10..60 Punkte Schaden. Eure Nicht-Mesmer-Fertigkeiten (außer Siegel) sind 10 Sekunden lang deaktiviert.', 'Richtet 10...60 Punkte Schaden an. Richtet bei anderen umstehenden Feinden 10...60 Punkte Schaden an, falls der Gegner verhext oder verzaubert ist. <i>Eure Nicht-Mesmer-Fertigkeiten (außer Siegel) sind 10 Sekunden lang deaktiviert.</i>'),
(935, 'Hochkommende Galle', '20 Sekunden lang bewirkt diese Verhexung gar nichts. Wenn Hochkommende Galle endet, erleiden der Gegner und alle Feinde in dem Bereich für jede Sekunde, die Hochkommende Galle aktiv war, 1..6 Punkte Schaden.', '20 Sekunden: Wirkung am Ende: Richtet für jede Sekunde, die Hochkommende Galle aktiv war, 1...6 Punkte Schaden an. Schädigt den Gegner und andere Feinde im Bereich.', '', '', ''),
(936, 'Verzauberungen vergiften', 'Der Gegner verliert eine Verzauberung. Für jede verbleibende Verzauberung wird der Gegner 3..10 Sekunden lang vergiftet.', 'Entfernt eine Verzauberung vom Gegner. Verursacht Zustand Gift (3...10 Sekunden pro verbleibender Verzauberung auf dem Gegner).', '', '', ''),
(937, 'Stoßwelle', 'Alle Feinde in der Nähe leiden 1..10 Sekunden lang an Beschädigter Rüstung, alle Feinde in dem Bereich sind 1..10 Sekunden lang geschwächt und umstehende Feinde werden 1..10 Sekunden lang geblendet. Alle umstehenden Feinde erleiden 15..60 Punkte Erdschaden, alle Feinde in der Nähe erleiden 15..60 Punkte Erdschaden und alle Feinde in dem Bereich erleiden 15..60 Punkte Erdschaden.', 'Feinde in der Nähe leiden an Beschädigter Rüstung (1...10 Sekunden), Feinde im Bereich leiden unter Schwäche (1...10 Sekunden), umstehende Feinde leiden an Blindheit (1...10 Sekunden). Umstehende Feinde erleiden 15...60 Erdschaden, Feinde in der Nähe erleiden 15...60 Erdschaden und Feinde in dem Bereich erleiden 15...60 Erdschaden.', '', '', ''),
(938, 'Kreis der Stabilität', 'Erzeugt an Eurer aktuellen Position einen Kreis der Stabilität. 10..25 Sekunden lang können Verbündete, die keine Geister sind, nicht zu Boden geworfen werden.', '10...25 Sekunden: Verbündete in dieser Abwehr können nicht zu Boden geworfen werden. <i>Verbündete Geister sind nicht betroffen.</i>', '', '', ''),
(939, 'Eisfesseln', '1..10 Sekunden lang ist die Bewegungsgeschwindigkeit des Gegners um 66% reduziert. Wenn er unter dem Einfluss einer Verzauberung steht, sind die Bewegungen dieses Gegners um 90% reduziert.', '1...10 Sekunden: Gegner bewegt sich um 66% langsamer. Gegner bewegt sich um 90% langsamer, falls er verzaubert ist.', '', '', ''),
(941, 'Gesegnetes Licht', 'Heilt den Verbündeten (bzw. Euch selbst) um 10..140 Lebenspunkte und entfernt einen Zustand und eine Verhexung.', 'Heilt um 10...140 Lebenspunkte. Entfernt einen Zustand und eine Verhexung.', '', '', ''),
(942, 'Verhexungen zurücknehmen', 'Entfernt alle Verhexungen vom Verbündeten (bzw. Euch selbst) und allen umstehenden Verbündeten. Das Wiederaufladen dieses Zaubers benötigt für jede Verhexung, die auf diese Art entfernt wird, zusätzlich 20..5 Sekunden.', 'Entfernt alle Verhexungen. Betrifft auch umstehende Verbündete. <i>Entfernungskosten: Pro entfernter Verhexung +20...5 Sekunden zur Aufladezeit.</i>', '', '', ''),
(943, 'Löschen', 'Entfernt einen Zustand von jedem Gruppenmitglied. Gruppenmitglieder, von denen Brennen entfernt wurde, werden um 10..100 Lebenspunkte geheilt.', 'Wirkt auf die gesamte Gruppe. Entfernt einen Zustand. Gruppenmitglieder, von denen Brennen entfernt wurde, werden um 10...100 Lebenspunkte geheilt.', '', '', ''),
(944, 'Siegel der Stärke', 'Eure nächsten 1..16 Angriffe fügen +5 Punkte Schaden zu.', 'Eure Angriffe richten +5 Punkte Schaden an. Endet nach 1...16 Angriffen.', '', '', ''),
(946, 'Fokus des Fallenstellers', '12..24 Sekunden lang können Eure Fallen-Fertigkeiten nicht leicht unterbrochen werden und Euer Attribut Überleben in der Wildnis ist um 0..2 erhöht.', '12...24 Sekunden: Eure Fallen-Fertigkeiten können nicht länger leicht unterbrochen werden. Ihr erhaltet +0...2 bei Eurem Attribut Überleben in der Wildnis.', '', '', ''),
(947, 'Dornenranken', 'Erstellt einen Geist der Stufe 1..10. Kreaturen, die keine Geister sind und in seiner Reichweite zu Boden geworfen werden, erleiden 5 Schadenspunkte und leiden 5..20 Sekunden lang an einer Blutung. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Zu Boden geworfene Kreaturen erleiden 5 Punkte Schaden und beginnen zu bluten (5...20 Sekunden). Keine Wirkung auf Geister.', '', '', ''),
(948, 'Verzweifelter Stoß', 'Wenn Ihr weniger als 50..80% Eurer Lebenspunkte habt, fügt Ihr +15..60 Punkte Schaden zu.', 'Richtet +15...60 Punkte Schaden an, falls Ihr weniger als 50...80% Eurer maximalen Lebenspunkte habt.', '', '', ''),
(949, 'Weg des Fuchses', '10..35 Sekunden lang ist ein Blocken bei Euren nächsten 1..6 Angriffen nicht möglich.', '10...35 Sekunden: Eure Angriffe können nicht geblockt werden. <i>Endet nach 1...6 Angriffen.</i>', '', '', ''),
(950, 'Schattenbürde', '3..15 Sekunden lang bewegt sich der Gegner um 25% langsamer und während er keine anderen Verhexungen hat, hat er 20 weniger Rüstung gegen Eure Angriffe.', '3...15 Sekunden: Gegner bewegt sich um 25% langsamer und besitzt -20 Rüstung gegen Eure Angriffe. <i>Rüstungsreduzierung betrifft den Gegner nur, solange er keine anderen Verhexungen hat.</i>', '', '', ''),
(951, 'Geschwindigkeit abzapfen', '5..15 Sekunden lang bewegt sich der Gegner um 33% langsamer und bewegt Ihr Euch um 33% schneller. Dieser Zauber hat nur die halbe Reichweite. Dieser Zauber wird um 50% schneller wieder aufgeladen, wenn er auf einen sich bewegenden Feind gewirkt wird.', '5...15 Sekunden: Gegner bewegt sich um 33% langsamer und ihr bewegt Euch um 33% schneller. Wird um 50% schneller wieder aufgeladen, wenn auf einen sich bewegenden Feind gewirkt.', '', '', ''),
(952, 'Angriff des Todes', 'Schattenschritt zum Gegner. Wenn dieser Gegner mehr Lebenspunkte als Ihr hat, werdet Ihr um 65..200 Punkte geheilt.', 'Ihr unternehmt einen Schattenschritt zum Gegner. Ihr werdet um 65...200 Lebenspunkte geheilt, falls der Gegner mehr Lebenspunkte hat als Ihr.', '', '', ''),
(953, 'Kraftfluss', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird diese Fertigkeit unterbrochen und der Gegner unterliegt 4..10 Sekunden lang einer Energiedegeneration von -2.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: -2 Energiedegeneration (4...10 Sekunden).', '', '', ''),
(954, 'Verhexungen vertreiben', 'Entfernt bis zu 2 Verhexungen vom Verbündeten (bzw. Euch selbst).', 'Entfernt beim Verbündeten (bzw. Euch selbst) 2 Verhexungen.', '', '', ''),
(955, 'Verzauberung zerreißen', 'Entfernt 1 Verzauberung vom Gegner. Wenn eine Verzauberung entfernt wurde, leidet er 5..25 Sekunden an einer Blutung.', 'Entfernt 1 Verzauberung. Entfernungswirkung: Verursacht Blutung (5...25 Sekunden).', '', '', ''),
(957, 'Zauberschild', '5..20 Sekunden lang könnt Ihr beim Wirken von Zaubern nicht zum Ziel von Zaubern werden. Wenn Zauberschild endet, werden alle Eure Fertigkeiten 10..5 Sekunden lang deaktiviert.', '5...20 Sekunden: Während Ihr Zauber wirkt, könnt Ihr nicht zum Ziel von Zaubern werden. <i>Wirkung am Ende: Eure Fertigkeiten werden deaktiviert (10...5 Sekunden).</i>', '', '', ''),
(958, 'Heilendes Geflüster', 'Der Verbündete wird um 40..100 Punkte geheilt. Dieser Zauber hat nur die Hälfte der normalen Reichweite.', 'Heilt um 40...100 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(959, 'Ätherisches Licht', 'Der Verbündete (bzw. Ihr selbst) wird um 25..100 Punkte geheilt. Dieser Zauber kann leicht unterbrochen werden.', 'Heilt um 25...100 Lebenspunkte. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(960, 'Verzauberungen freisetzen', 'Alle Verzauberungen gehen verloren. Jedes Gruppenmitglied wird pro verlorene Mönchs-Verzauberung um 5..35 Lebenspunkte geheilt.', 'Entfernt alle Eure Verzauberungen. Heilt alle Gruppenmitglieder für jede entfernte Mönch-Verzauberung um 5...35 Lebenspunkte.', '', '', ''),
(961, 'Zerfetzen', 'Erstellt einen Geist der Stufe 1..10. Blutende Kreaturen in seiner Reichweite erleiden eine Lebenspunktdegeneration von -2. Wenn dieser Geist stirbt, leiden alle Kreaturen in seiner Reichweite, die keine Geister sind, und deren Lebenspunkte unter 90% liegen, 5..25 Sekunden lang an einer Blutung. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Blutende Kreaturen in Reichweite haben -2 Lebenspunktdegeneration. Wirkung am Ende: Verursacht bei Kreaturen in Reichweite, die weniger als 90% ihrer maximalen Lebenspunkte haben, Zustand Blutung (5...25 Sekunden). Keine Wirkung auf Geister.', '', '', ''),
(962, 'Geistübertragung', 'Der Geist, der Euch am nächsten ist, verliert 5..50 Lebenspunkte. Der Verbündete (bzw. Ihr selbst) wird für jeden verlorenen Lebenspunkt um 5 Punkte geheilt.', 'Der am nächsten zu Euch befindliche Geist verliert 5...50 Lebenspunkte. Heilt den Verbündeten (bzw. Euch selbst) für jeden verlorenen Lebenspunkt um 5 Lebenspunkte.', '', '', ''),
(963, 'Wiederherstellung', 'Erstellt einen Geist der Stufe 1..14. Wenn dieser Geist stirbt, werden alle Gruppenmitglieder in dem Bereich mit 5..50% ihrer Lebenspunkte und 0 Energiepunkten wieder belebt. Dieser Geist stirbt nach 30 Sekunden', 'Erstellt einen Geist der Stufe 1...14 (30 Sekunden Lebensdauer). Wirkung am Ende: Gruppenmitglieder im Bereich werden wiederbelebt (5...50% Lebenspunkte, 0 Energiepunkte).', 'Wiederherstellung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Wenn dieser Geist stirbt, werden alle Gruppenmitglieder in dem Bereich mit 5..50% ihrer Lebenspunkte und 0 Energiepunkten wieder belebt. Dieser Geist stirbt nach 30 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30 Sekunden Lebensdauer). Wirkung am Ende: Im Bereich befindliche Gruppenmitglieder werden wiederbelebt (5...50% Lebenspunkte, 0 Energiepunkte).'),
(964, 'Rachsüchtige Waffe', 'Wenn der Verbündete (bzw. Ihr selbst) innerhalb der nächsten 8 Sekunden das nächste Mal durch einen Gegner Schaden erleidet oder Lebenspunkte entzogen bekommt, entzieht er diesem Gegner bis zu 15..60 Lebenspunkte.', '(8 Sekunden.) Entzieht dem nächsten Feind, der Schaden beim Verbündeten anrichtet, 15...60 Lebenspunkte.', '', '', ''),
(973, 'Blendpulver', 'Muss einem Begleithandangriff folgen. Der Gegner und alle umstehenden Feinde werden 3..15 Sekunden lang geblendet.', 'Verursacht beim Gegner und umstehenden Feinden Zustand Blindheit (3...15 Sekunden). <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(974, 'Berührung der Mantis', 'Muss einem Leithandangriff folgen. Der Gegner wird 5..20 Sekunden lang verkrüppelt. Diese Fertigkeit zählt als Begleithandangriff.', 'Verursacht Zustand Verkrüppelung (5...20 Sekunden). Diese Fertigkeit zählt als Begleithandangriff. <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(975, 'Ermüdender Angriff', 'Muss einem Leithandangriff folgen. Unterbricht die Aktion des Gegners. Wenn es sich dabei um einen Zauber gehandelt hat, erleidet der Gegner 10 Überzaubern.', 'Unterbricht eine Aktion. Fügt 10 Überzaubern zu, wenn die unterbrochene Aktion ein Zauber war. <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(976, 'Wiederholender Schlag', 'Muss einem Begleithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er +10..30 Punkte Schaden zu. Wenn er sein Ziel verfehlt, benötigt er zusätzlich 15 Sekunden zum Wiederaufladen.', 'Richtet +10...30 Punkte Schaden an. <i>+15 Sekunden zur Aufladezeit dieser Fertigkeit, falls Ihr verfehlt. Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(977, 'Weg des Lotus', 'Wenn Ihr innerhalb der nächsten 20 Sekunden das nächste Mal mit einer Doppelangriff-Fertigkeit trefft, erhaltet Ihr 5..20 Energiepunkte.', '20 Sekunden: Ihr erhaltet 5...20 Energiepunkte, wenn Ihr das nächste Mal mit einem Doppelangriff trefft.', '', '', ''),
(978, 'Zeichen der Unbeständigkeit', 'Während der nächsten 20 Sekunden wird der Feind das nächste Mal, wenn Ihr ihn mit einer Doppelangriffs-Fertigkeit trefft, zu Boden geworfen.', '20 Sekunden: Wirft den Gegner zu Boden, wenn Ihr ihn das nächste Mal mit einem Doppelangriff trefft.', '', '', ''),
(979, 'Argwohn', 'Während der nächsten 6 Sekunden scheitert der nächste Zauber, den der Gegner auf einen seiner Feinde richtet, und verursacht beim Gegner und allen Feinden in der Nähe 30..100 Punkte Schaden.', '6 Sekunden: Der nächste Zauber, den der Gegner auf einen seiner Feinde richtet, scheitert und richtet beim Gegner und Feinden in der Nähe 30...100 Punkte Schaden an.', 'Argwohn (PvP)', 'Während der nächsten 6 Sekunden scheitert der nächste Zauber, den der Gegner auf einen Eurer Verbündeten richtet und richtet beim Gegner und allen Feinden in der Nähe 10..75 Punkte Schaden an.', '6 Sekunden: Der nächste Zauber, den der Gegner auf einen Eurer Verbündeten richtet, scheitert und richtet beim Gegner und Feinden in er<sub>sic</sub> Nähe 10...75 Punkte Schaden an.'),
(980, 'Schmaus der Seelen', 'Vernichtet die Geister aller Verbündeten in der Nähe. Für jeden Geist, der auf diese Art vernichtet wurde, werden alle Gruppenmitglieder um 50..100 Lebenspunkte geheilt.', 'Heilt alle Gruppenmitglieder für jeden verbündeten Geist in der Nähe um 50...100 Lebenspunkte. <i>Alle verbündeten Geister in der Nähe werden vernichtet.</i>', '', '', ''),
(981, 'Erholung', 'Erstellt einen Geist der Stufe 1..14. Verbündete in seiner Reichweite, die keine Geister sind, erfreuen sich einer Lebenspunktregeneration von 1..3. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (15...45 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, haben +1...3 Lebenspunktregeneration.', 'Erholung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Verbündete in seiner Reichweite, die keine Geister sind, erfreuen sich einer Lebenspunktregeneration von +1..3. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...45 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, haben +1...3 Lebenspunktregeneration.'),
(982, 'Unterschlupf', 'Erstellt einen Geist der Stufe 1..12. Verbündete in seiner Reichweite, die keine Geister sind, können nicht mehr als 10% ihrer maximalen Lebenspunkte auf einmal verlieren. Wenn dieser Geist Schaden verhindert, verliert er 75..45 Lebenspunkte. Dieser Geist besteht 30..60 Sekunden lang.', 'Erstellt einen Geist der Stufe 1...12 (30...60 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, können nicht mehr als 10% ihrer maximalen Lebenspunkte auf einmal verlieren. <i>Schadensverhinderungskosten: Der Geist verliert 75...45 Lebenspunkte.</i>', 'Unterschlupf (PvP)', 'Erstellt einen Geist der Stufe 1..8. Verbündete in seiner Reichweite, die keine Geister sind, können nicht mehr als 10% ihrer maximalen Lebenspunkte auf einmal verlieren. Wenn dieser Geist Schaden verhindert, verliert er 75..45 Lebenspunkte. Dieser Geist besteht 30..60 Sekunden lang.', 'Erstellt einen Geist der Stufe 1...8 (30...60 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, können nicht mehr als 10% ihrer maximalen Lebenspunkte auf einmal verlieren. <i>Schadensverhinderungskosten: Der Geist verliert 75...45 Lebenspunkte.</i>'),
(983, 'Schattenwaffe', 'Der Verbündete (bzw. Ihr selbst) erhält für eine Dauer von 1..7 Sekunden eine Schattenwaffe. Jedes Mal, wenn der Verbündete getroffen wird, wird der Angreifer 5 Sekunden lang geblendet. Die nächsten 1..3 Mal, wenn ein Angriff dieses Verbündeten trifft, wird sein Ziel 5 Sekunden lang geblendet.', '(1...7 Sekunden.) Verursacht bei jedem, der den Verbündeten (bzw. Euch selbst) angreift, den Zustand Blindheit (5 Sekunden). Die nächsten 1...3 Angriffe des Ziels verursachen Blindheit (5 Sekunden), wenn sie treffen.', '', '', ''),
(985, 'Trittnägel', 'Der Feind und alle umstehenden Gegner werden 5..15 Sekunden lang verkrüppelt. Trittnägel haben nur die halbe Reichweite.', 'Verursacht beim Gegner und umstehenden Feinden Zustand Verkrüppelung (5...15 Sekunden).', '', '', ''),
(986, 'Neunschwänziger Stoß', 'Muss einem Begleithandangriff folgen. Neunschwänziger Stoß kann nicht geblockt werden und fügt bei einem Treffer +15..40 Punkte Schaden zu.', 'Richtet +15...40 Punkte Schaden an. Kann nicht geblockt werden. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(987, 'Weg der leeren Handfläche', '5..20 Sekunden lang kosten Begleithand- und Doppelangriffe keine Energiepunkte.', '5...20 Sekunden: Eure Begleithand- und Doppelangriffe kosten keine Energiepunkte.', '', '', ''),
(988, 'Schläfenstoß', 'Muss einem Leithandangriff folgen. Wenn dieser Angriff erfolgreich ist, leidet der Gegner 1..10 Sekunden lang unter Benommenheit und Blindheit und wenn er einen Zauber wirkt, wird er dabei unterbrochen.', 'Unterbricht einen Zauber. Verursacht Zustände Benommenheit und Blindheit (1...10 Sekunden). <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(989, 'Goldener Phönix-Stoß', 'Wenn Ihr nicht verzaubert seid, trifft diese Fertigkeit nicht. Wenn Goldener Phönix-Stoß erfolgreich ist, fügt er dem Gegner +10..30 Punkte Schaden und allen umstehenden Feinden 10..30 Punkte Schaden zu.', 'Fügt dem Gegner +10...30 und umstehenden Feinden 10...30 Punkte Schaden zu. <i>Schlägt fehl, falls Ihr nicht verzaubert seid.</i>', '', '', ''),
(990, 'Verzauberungen ausmerzen', 'Der Gegner verliert 1 Verzauberung. Alle anderen Nicht-Angriffsfertigkeiten werden 10..5 Sekunden lang deaktiviert. Für jede Fertigkeit, die auf diese Weise deaktiviert wird, verliert der berührte Gegner 1 weitere Verzauberung.', 'Entfernt für jede Eurer Nicht-Angriffsfertigkeiten eine Verzauberung. <i>Alle Eure Nicht-Angriffsfertigkeiten werden deaktiviert (10...5 Sekunden).</i>', '', '', ''),
(991, 'Verhexungen zurückweisen', 'Entfernt für jede wieder aufladende Gunst-der-Götter-Fertigkeit, die Ihr einschließlich Verhexungen zurückweisen habt, eine Verhexung vom Verbündeten (bzw. Euch selbst).', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst) und eine weitere für jede wieder aufladende Gunst-der-Götter-Fertigkeit, die Ihr habt.', '', '', ''),
(992, 'Dreifachhieb', 'Greift den Gegner und umstehende Feinde an. Jeder erfolgreiche Angriff fügt +10..40 Punkte Schaden zu.', 'Richtet +10...40 Punkte Schaden an. Trifft den Gegner und umstehende Feinde.', '', '', ''),
(993, 'Erzürnter Schlag', 'Wenn Erzürnter Schlag erfolgreich ist, erhaltet Ihr 1..4 Adrenalinstöße. Wenn Ihr einen sich bewegenden Feind trefft, erleidet dieser +10..40 Schaden und wird zu Boden geworfen.', 'Gibt Euch 1...4 Adrenalinstöße, wenn Ihr trefft. Richtet +10...40 Punkte Schaden und wirft den Gegner zu Boden, wenn er sich bewegt hat.', 'Erzürnter Schlag (PvP)', 'Wenn Erzürnter Schlag erfolgreich ist, erhaltet Ihr 1..4 Adrenalinstöße. Wenn Ihr einen sich bewegenden Feind trefft, erleidet dieser +10..40 Schaden und wird zu Boden geworfen.', 'Gibt Euch 1...4 Adrenalinstöße, wenn Ihr trefft. Richtet +10...40 Punkte Schaden und wirft den Gegner zu Boden, wenn er sich bewegt hat.'),
(994, 'Erneuernder Schlag', 'Wenn Erneuernder Schlag trifft, fügt er +10..40 Punkte Schaden zu. Wenn Ihr einen zu Boden geworfenen Feind trefft, erhaltet Ihr 3 Energiepunkte und dieser Angriff wird sofort wieder aufgeladen.', 'Richtet +10...40 Punkte Schaden an. Ihr erhaltet 3 Energiepunkte und dieser Angriff wird sofort wieder aufgeladen, wenn der Gegner zu Boden geworfen wurde.', 'Erneuernder Schlag (PvP)', 'Wenn Erneuernder Schlag trifft, fügt er +5..20 Punkte Schaden zu. Wenn Ihr einen zu Boden geworfenen Feind trefft, erhaltet Ihr 3 Energiepunkte und dieser Angriff wird sofort wieder aufgeladen.', 'Richtet +5...20 Punkte Schaden an. Ihr erhaltet 3 Energiepunkte und dieser Angriff wird sofort wieder aufgeladen, wenn der Gegner zu Boden geworfen wurde.'),
(995, 'Tigerhaltung', '4..10 Sekunden lang sind Eure Angriffe um 33% schneller. Tigerhaltung endet, sobald einer Eurer Angriffe nicht trifft.', '4...10 Sekunden: Ihr greift um 33% schneller an. <i>Endet, sobald Ihr nicht trefft.</i>', '', '', ''),
(996, 'Stehender Hieb', 'Wenn Stehender Hieb erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu sowie zusätzlich 5..20 Punkte Schaden, wenn Ihr eine Haltung einnehmt.', 'Richtet +5...20 Punkte Schaden an. Richtet zusätzlich 5...20 Punkte Schaden an, falls Ihr eine Haltung einnehmt.', '', '', ''),
(997, 'Hungersnot', 'Erstellt einen Geist der Stufe 1..10. Jedes Mal, wenn die Energiepunkte einer Kreatur in seiner Reichweite auf 0 sinken, erleidet diese Kreatur 10..35 Punkte Schaden. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Richtet bei Kreaturen in Reichweite, deren Energiepunkte auf 0 sinken, 10...35 Punkte Schaden an. Keine Wirkung auf Geister.', '', '', ''),
(1018, 'Kritisches Auge', '10..35 Sekunden lang habt Ihr eine 3..15%ige Chance, bei einem Angriff einen kritischen Treffer zu erzielen. Ihr erhaltet 1 Energiepunkt für jeden kritischen Treffer.', '10...35 Sekunden: Ihr habt + 3...15% zur Chance auf kritische Treffer. Ihr erhaltet jedes Mal 1 Energiepunkt, wenn Ihr einen kritischen Treffer landet.', '', '', ''),
(1019, 'Kritischer Stoß', 'Muss einem Begleithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er +10..30 Punkte Schaden zu, führt zu einem kritischen Treffer und Ihr erhaltet 1..3 Energiepunkte.', 'Richtet +10...30 Punkte Schaden an. Immer ein kritischer Treffer. Ihr erhaltet 1...3 Energiepunkte. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(1020, 'Stahlklingen', 'Muss einem Begleithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er für jeden wieder aufzuladenen Dolchangriff +5..16 Punkte Schaden (maximaler Bonus 60) zu.', 'Richtet für jeden sich aufladenden Dolchangriff +5...16 Punkte Schaden an (maximal 60). <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(1021, 'Dschungelschlag', 'Muss einem Leithandangriff folgen. Wenn Dschungelschlag erfolgreich ist, fügt er dem Gegner +10..25 Punkte Schaden zu. Trifft er einen Gegner mit einer Verkrüppelung, nimmt der Gegner und alle umstehenden Gegner +1..31 zusätzlichen Schaden.', 'Richtet +10...25 Punkte Schaden an. Richtet beim Gegner und umstehenden Feinden +1...31 Punkte Schaden an, falls der Gegner verkrüppelt ist. <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(1022, 'Wilder Stoß', 'Muss einem Leithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er +10..35 Punkte Schaden zu und jede Haltung, die der Gegner einsetzt, endet. Dieser Angriff kann nicht geblockt werden.', 'Richtet +10...35 Punkte Schaden an. Entfernt Haltung des Gegners. Kann nicht geblockt werden. <i>Muss einem Leithandangriff folgen.</i>', 'Wilder Stoß (PvP)', 'Muss einem Leithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er +10..35 Punkte Schaden zu und jede Haltung, die der Gegner einsetzt, endet. Dieser Angriff kann nicht geblockt werden.', 'Richtet +10...35 Punkte Schaden an. Entfernt Haltung des Gegners. Kann nicht geblockt werden. <i>Muss einem Leithandangriff folgen.</i>'),
(1023, 'Stich der springenden Mantis', 'Wenn Stich der Mantis erfolgreich ist, erleidet der Gegner +5..15 Punkte Schaden. Wenn dieser Angriff einen sich bewegenden Gegner trifft, wird dieser 3..15 Sekunden lang verkrüppelt.', 'Richtet +5...15 Punkte Schaden an. Verursacht Zustand Verkrüppelung (3...15 Sekunden), falls der Gegner sich bewegt.', '', '', ''),
(1024, 'Stoß der schwarzen Mantis', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +8..20 Punkte Schaden zu. Wenn Euer Gegner unter einer Verhexung leidet, wird er 3..15 Sekunden lang verkrüppelt.', 'Richtet +8...20 Punkte Schaden an. Verursacht Zustand Verkrüppelung (3...15 Sekunden), falls der Gegner verhext ist.', '', '', ''),
(1025, 'Unterbrechender Dolchstoß', 'Wenn dieser Angriff erfolgreich ist, unterbricht er die Aktion des Gegners. Wenn diese Aktion ein Zauber war, wird er 3..10 Sekundenn lang deaktiviert.', 'Unterbricht eine Aktion. Falls die unterbrochene Aktion ein Zauber war, wird er deaktiviert (3...10 Sekunden).', '', '', ''),
(1026, 'Goldener Lotus-Stoß', 'Wenn dieser Angriff erfolgreich ist, fügt er +5..20 Punkte Schaden zu. Wenn Ihr unter den Auswirkungen einer Verzauberung leidet, erhaltet Ihr 5..8 Energiepunkte.', 'Richtet +5...20 Punkte Schaden an. Ihr erhaltet 5...8 Energiepunkte, falls Ihr verzaubert seid.', '', '', ''),
(1027, 'Kritische Verteidigungen', '4..10 Sekunden lang habt Ihr eine 75%ige Chance zum Blocken. Kritische Verteidigungen wird bei jedem kritischen Treffer, den Ihr landet, aktualisiert.', '4...10 Sekunden: Ihr habt eine 75%ige Chance zu blocken. Erneuerung: Mit jedem Eurer kritischen Treffer.', '', '', ''),
(1028, 'Weg der Perfektion', 'Während der nächsten 60 Sekunden erhaltet Ihr für alle Eure erfolgreichen kritischen Treffer 10..40 Lebenspunkte.', '60 Sekunden: Erhaltet durch kritische Treffer 10...40 Lebenspunkte.', '', '', ''),
(1029, 'Dunkle Abtrünnigkeit', 'Während der nächsten 3..17 Sekunden entfernt Ihr mit jedem erfolgreichen kritischen Treffer eine Verzauberung von Eurem Ziel. Wenn Ihr eine Verzauberung auf diese Weise entfernt, verliert Ihr 10..4 Energiepunkte oder Dunkle Abtrünnigkeit endet.', '3...17 Sekunden: Eure kritischen Treffer entfernen eine Verzauberung. <i>Entfernungskosten: Ihr verliert 10...4 Energiepunkte oder Dunkle Abtrünnigkeit endet.</i>', '', '', ''),
(1030, 'Wut der Heuschrecke', '10..35 Sekunden lang habt Ihr zusätzlich eine 50%ige Chance auf einen Doppeltreffer, wenn Ihr Dolche verwendet.', '10...35 Sekunden: Ihr habt +50% zur Chance auf Doppeltreffer. <i>Keine Wirkung, falls Ihr keine Dolche benutzt.</i>', '', '', ''),
(1031, 'Schleier des Kummers', '30..60 Sekunden lang habt Ihr +3..8 Lebenspunktregeneration und eine 75%ige Chance Angriffe zu blocken, wenn Eure Lebenspunkte unter 50% liegen.', '(30...60 Sekunden.) Ihr habt +3...8 Lebenspunktregeneration und eine 75%ige Chance, zu blocken. <i>Eure Lebenspunkte müssen unter 50% liegen, sonst keine Wirkung.</i>', 'Schleier des Kummers (PvP)', '30..60 Sekunden lang habt Ihr eine 75%ige Chance Angriffe zu blocken, wenn Eure Lebenspunkte unter 50% liegen.', '30...60 Sekunden: Ihr habt eine 75%ige Chance zu blocken. <i>Ihr müsst weniger als 50% Eurer maximalen Lebenspunkte haben, sonst keine Wirkung.</i>'),
(1032, 'Herz der Schatten', 'Ihr werdet um 30..150 Lebenspunkte geheilt. Unternehmt einen Schattenschritt mit in-dem-Bereich-Distanz, direkt weg vom ausgewählten Ziel. Habt Ihr Euch selbst oder kein Ziel gewählt, ist die Richtung zufällig und die Distanz schwankend.', 'Ihr werdet um 30...150 Lebenspunkte geheilt und unternehmt einen Schattenschritt mit in-dem-Bereich-Distanz, weg vom ausgewählten Ziel. Zufällige Richtung mit variabler Distanz, wenn kein oder sich selbst als Ziel.', '', '', ''),
(1033, 'Aufspießen', 'Muss einem Doppelangriff folgen. Der Gegner erleidet 25..100 Punkte Erdschaden und leidet 5..20 Sekunden lang an einer Tiefe Wunde.', 'Richtet 25...100 Punkte Erdschaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden). <i>Muss einem Doppelangriff folgen.</i>', '', '', ''),
(1034, 'Eiternde Wunde', '1..7 Sekunden lang bewegt sich der Gegner um 33% langsamer. Wenn der Gegner unter einem Zustand leidet, erleidet er 5..25 Punkte Schaden pro Sekunde. Die Reichweite dieses Zaubers ist nur halb so groß wie gewöhnlich.', '(1...7 Sekunden.) Gegner bewegt sich um 33% langsamer. Verursacht an Feinden, die an einem Zustand leiden, 5...25 Punkte Schaden pro Sekunde.', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1035, 'Versprechen des Assassinen', 'Wenn der Gegner innerhalb der nächsten 5..15 Sekunden stirbt, erhaltet Ihr 5..20 Energiepunkte, und alle Eure Fertigkeiten werden wieder aufgeladen.', '5...15 Sekunden: Wenn der Gegner stirbt, erhaltet Ihr 5...20 Energiepunkte und alle Eure Fertigkeiten werden aufgeladen.', '', '', ''),
(1036, 'Siegel der Bosheit', 'Für jeden Zustand unter dem der Gegner leidet, verliert Ihr einen Zustand.', 'Für jeden Zustand des Gegners verliert Ihr einen Zustand.', '', '', ''),
(1037, 'Dunkle Flucht', '5..15 Sekunden lang bewegt Ihr Euch um 25% schneller und erleidet nur den halben Schaden. Dunkle Flucht endet, wenn Ihr mit einem Angriff erfolgreich trefft.', '5...15 Sekunden: Ihr bewegt Euch um 25% schneller und nehmt nur halben Schaden. <i>Endet, wenn Ihr mit einem Angriff trefft.</i>', '', '', ''),
(1038, 'Verkrüppelnder Dolch', 'Ein Verkrüppelnder Dolch fliegt auf den Gegner zu. Verkrüppelnder Dolch fügt bei einem Treffer 15..60 Punkte Erdschaden zu und verkrüppelt sich bewegende Feinde 3..15 Sekunden lang. Dieser Zauber hat nur die halbe Reichweite.', 'Geschoss: Richtet 15...60 Punkte Erdschaden an. Verursacht Zustand Verkrüppelung (3...15 Sekunden), falls der Gegner sich bewegt.', '', '', ''),
(1040, 'Geistermarsch', 'Schattenschritt zu Zielgeist.', 'Schattenschritt zum ausgewählten Geist.', '', '', ''),
(1041, 'Unsichtbare Wut', 'Alle umstehenden Feinde werden 3..10 Sekunden lang geblendet. Während der nächsten 15..60 Sekunden kann der geblendete Gegner nicht blocken.', 'Verursacht bei umstehenden Feinden Zustand Blindheit (3...10 Sekunden). Geblendete Feinde können Eure Angriffe nicht blocken (15...60 Sekunden).', 'Unsichtbare Wut (PvP)', 'Innerhalb der nächsten 15..60 Sekunden können Euch geblendete Gegner nicht blocken.', '15...60 Sekunden: Geblendete Feinde können Eure Angriffe nicht blocken.'),
(1042, 'Blitzende Klingen', '5..30 Sekunden lang habt Ihr eine 75%ige Chance, Angriffe zu blocken, während Ihr selbst angreift. Wenn Ihr einen Angriff auf diese Weise blockt, erleidet Euer Angreifer 5..20 Punkte Schaden.', '5...30 Sekunden: 75%ige Chance beim Angreifen zu blocken. Blockwirkung: 5...20 Punkte Schaden für Euren Angreifer.', '', '', ''),
(1043, 'Preschen', '3 Sekunden lang lauft Ihr um 50% schneller.', '3 Sekunden: Ihr bewegt Euch um 50% schneller.', '', '', ''),
(1044, 'Dunkles Gefängnis', 'Schattenschritt zum Gegner. 1..6 Sekunden lang bewegt sich dieser Gegner um 33% langsamer.', 'Unternehmt einen Schattenschritt zum Gegner. Der Gegner bewegt sich um 33% langsamer (1...6 Sekunden).', '', '', ''),
(1045, 'Handflächenschlag', 'Der berührte Gegner erleidet 10..65 Punkte Schaden und wird 1..5 Sekunden lang verkrüppelt. Diese Fertigkeit zählt als Begleithandangriff.', 'Richtet +10...65 Punkte Schaden an und verursacht Zustand Verkrüppelung (1...5 Sekunden). Dieser Angriff zählt als Begleithandangriff.', '', '', ''),
(1048, 'Enthüllte Verzauberung', 'Eine Verzauberung wird vom Gegner entfernt und Ihr erhaltet 3..15 Energiepunkte. Enthüllte Verzauberung wird 20 Sekunden lang durch die dem Gegner genommene Verzauberung ersetzt.', 'Entfernt beim Gegner eine Verzauberung. Entfernungswirkung: Ihr erhaltet 3...15 Energiepunkte; dieser Zauber wird durch die entfernte Verzauberung ersetzt (20 Sekunden).', '', '', ''),
(1049, 'Enthüllte Verhexung', 'Eine Verhexung wird von einem Verbündeten (bzw. Euch selbst) entfernt und Ihr erhaltet 4..10 Energiepunkte. Enthüllte Verhexung wird 20 Sekunden lang durch die entfernte Verhexung ersetzt.', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung. Entfernungswirkung: Ihr erhaltet 4...10 Energiepunkte; dieser Zauber wird durch die entfernte Verhexung ersetzt (20 Sekunden).', '', '', ''),
(1052, 'Angehäufter Schmerz', 'Der Gegner erleidet 15..75 Punkte Schaden. Wenn der Gegner unter 2 oder mehr Verhexungen leidet, erleidet er 5..20 Sekunden lang eine Tiefe Wunde.', 'Richtet 15...75 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner 2 oder mehr Verhexungen hat.', 'Angehäufter Schmerz (PvP)', 'Der Gegner erleidet 10..35 Punkte Schaden. Wenn der Gegner unter 2 oder mehr Verhexungen leidet, erleidet er 5..20 Sekunden lang eine Tiefe Wunde.', 'Richtet 10...35 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner 2 oder mehr Verhexungen hat.'),
(1053, 'Übernatürliche Ablenkung', 'Alle Eure anderen Fertigkeiten werden 8 Sekunden lang deaktiviert. Wenn der Gegner eine Fertigkeit verwendet, wird diese unterbrochen und zusätzlich 5..12 Sekunden lang deaktiviert.', 'Unterbricht eine Fertigkeit. Unterbrechungswirkung: Deaktiviert die unterbrochene Fertigkeit (+5...12 Sekunden). <i>Eure anderen Fertigkeiten werden deaktiviert (8 Sekunden).</i>', '', '', ''),
(1054, 'Antlitz des Ahnen', '4..10 Sekunden lang verlieren alle umstehenden Feinde jedes Mal, wenn der Verbündete (bzw. Ihr selbst) im Nahkampf getroffen wird, sämtliches Adrenalin sowie drei Energiepunkte.', '4...10 Sekunden: Alle umstehenden Feinde verlieren jedes Mal alles Adrenalin und 3 Energiepunkte, wenn ein Nahkampfangriff den Verbündeten (bzw. Euch selbst) trifft.', '', '', ''),
(1055, 'Wiederkehrende Unsicherheit', '10 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..5. Wenn dieser Gegner eine weitere Verhexung hat, wenn Wiederkehrende Unsicherheit enden würde, wird es wieder angewendet.', '10 Sekunden: Verursacht -1...5 Lebenspunkdegeneration. Erneuerung: Falls der Gegner eine andere Verhexung hat, wenn Wiederkehrende Unsicherheit enden würde.', '', '', ''),
(1056, 'Kitahs Bürde', '10 Sekunden lang bewegt sich der Gegner um 50% langsamer. Wenn Kitahs Bürde endet, erhaltet Ihr 10..18 Energiepunkte.', '10 Sekunden: Gegner bewegt sich um 50% langsamer. Wirkung am Ende: Ihr erhaltet 10...18 Energiepunkte.', '', '', ''),
(1057, 'Übernatürliche Labilität', 'Unterbricht die Aktion des Gegners. Handelt es sich bei der Aktion um eine Fertigkeit, werden der Gegner und alle Gegner in der Nähe 2..4 Sekunden lang zu Boden geworfen. (Bei Schnellwirkung von 4 oder weniger besteht eine 50%ige Chance auf Misserfolg.)', 'Unterbricht die Aktion des gegners<sub>sic</sub>. Handelt es sich bei der Aktion um eine Fertigkeit, werden der Gegner und und<sub>sic</sub> alle Feinde in der Nähe 2...4 lang zu Boden geworfen. <i>Bei Schnellwirkung von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.</i>', 'Übernatürliche Labilität (PvP)', 'Unterbricht die Aktion des Gegners. Handelt es sich bei der Aktion um eine Fertigkeit, werden der Gegner und alle umstehenden Gegner für 2..3 Sekunden zu Boden geworfen. (Bei Schnellwirkung von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', 'Unterbricht die Aktion des Gegners. Handelt es sich bei der Aktion um eine Fertigkeit, werden der Gegner und alle ihn umstehenden Feinde 2...3 Sekunden lang zu Boden geworfen. <i>Bei Schnellwirkung von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.</i>'),
(1059, 'Verhexungsfresser-Siegel', 'Berührt den Verbünden (bzw. Euch selbst) und nichts geschieht. Wenn Euch das erste Gruppenmitglied umstehend und verhext ist, verliert es eine Verhexung, wenn Euch das zweite Gruppenmitglied umstehend und verhext ist, verliert es eine Verhexung, und so weiter, bis 2..5 Verhexungen entfernt wurden oder keine Gruppenmitglieder mehr übrig sind. Ihr erhaltet für jede Verhexung, die auf diese Art entfernt wird, 1..4 Energiepunkte.', 'Nichts geschieht.  Wenn Euch das erste Gruppenmitglied umstehend und verhext ist, verliert es eine Verhexung, wenn euch das zweite Gruppenmitglied umstehend und verhext ist, verliert es eine Verhexung, und so weiter. Entfernungswirkung: Ihr erhaltet für jede entfernte Verhexung 1...4 Energiepunkte. <i>Es können höchstens 2...5 Verhexungen entfernt werden.</i>', '', '', ''),
(1061, 'Reaktion', 'Der Gegner verliert eine Verzauberung. Wenn eine Verzauberung auf diese Art entfernt wird, verliert der Gegner 4..10 Energiepunkte.', 'Entfernt eine Verzauberung. Entfernungswirkung: Gegner verliert 4...10 Energiepunkte.', '', '', ''),
(1062, 'Arkane Entwendung', '5..35 Sekunden lang wird ein zufällig gewählter Zauber des Gegners deaktiviert und Arkane Entwendung wird durch diesen Zauber ersetzt.', '5...35 Sekunden: Deaktiviert einen willkürlichen Zauber. Diese Fertigkeit wird zum betreffenden Zauber.', '', '', ''),
(1066, 'Sieger plündern', 'Während der nächsten 3..15 Sekunden verliert der Gegner jedes Mal, wenn er eine Kreatur mit weniger Lebenspunkten angreift oder einen Zauber auf sie wirkt, 25..100 Lebenspunkte.', '3...15 Sekunden: Lässt den Gegner jedes Mal 25...100 verlieren, wenn er eine Kreatur mit weniger Lebenspunkte angreift oder einen Zauber auf eine solche Kreatur wirkt.', 'Sieger plündern (PvP)', 'Während der nächsten 3..15 Sekunden verliert der Gegner jedes Mal, wenn er eine Kreatur mit weniger Lebenspunkten angreift oder einen Zauber auf sie wirkt, 15..75 Lebenspunkte.', '3...15 Sekunden: Lässt den Gegner jedes Mal 15...75 verlieren, wenn er eine Kreatur mit weniger Lebenspunkte angreift oder einen Zauber auf eine solche Kreatur wirkt.'),
(1067, 'Lebensfluchstoß', 'Der Gegner erleidet 12..48 Punkte Schattenschaden. Sind seine Lebenspunkte über 50%, entzieht Ihr ihm bis zu 12..48 Lebenspunkte.', 'Richtet 12...48 Punkte Schaden an. Entzieht 12...48 Lebensunkte, falls der Gegner mehr als 50% seiner maximalen Lebenspunkte hat.', '', '', ''),
(1068, 'Bitterkalt', 'Der Gegner erleidet 15..60 Punkte Kälteschaden. Wenn dieser Gegner mehr Lebenspunkte als Ihr hat, wird Bitterkalt sofort wieder aufgeladen.', 'Richtet 15...60 Punkte Kälteschaden an. Lädt sich sofort wieder auf, falls der Gegner mehr Lebenspunkte hatte als Ihr.', '', '', ''),
(1069, 'Geschmack der Schmerzen', 'Wenn die Lebenspunkte des Gegners unter 50% liegen, erhaltet Ihr 30..150 Lebenspunkte.', 'Erhaltet 30...150 Lebenspunkte. <i>Der Gegner muss weniger als 50% seiner maximalen Lebenspunkte haben, sonst keine Wirkung.</i>', '', '', ''),
(1070, 'Verzauberungen schänden', 'Der Zielgegner und alle in seiner Nähe befindlichen Gegner erleiden 6..60 Punkte Schattenschaden sowie zusätzlich 4..20 Punkte Schattenschaden für jede auf ihnen liegende Verzauberung.', 'Richtet beim Gegner und Feinden in der Nähe 6...60 Punkte Schaden an. Richtet zusätzlich 4...20 Punkte Schaden für jede auf ihnen liegende Verzauberung an.', '', '', ''),
(1071, 'Angstschauer', '10..40 Sekunden lang wird der Gegner jedes Mal, wenn er beim Anwenden einer Fertigkeit mit Kälteschaden getroffen wird, unterbrochen und Ihr verliert 10..5 Energiepunkte oder Angstschauer endet.', '10...40 Sekunden: Kälteschaden unterbricht die Fertigkeiten des Gegners. <i>Unterbrechungskosten: Ihr verliert 10...5 Energiepunkte oder diese Verhexung endet.</i>', '', '', ''),
(1075, 'Vampirschwarm', 'Vampirschwarm entzieht bis zu drei Gegnern in dem Bereich bis zu 15..60 Lebenspunkte.', 'Entzieht 15...60 Lebenspunkte. Trifft zwei weitere Gegner in dem Bereich.', '', '', ''),
(1076, 'Bluttrinker', 'Wenn Eure Lebenspunkte über 50% liegen, leidet Ihr 10 Sekunden lang an einer Blutung. Entzieht dem Gegner bis zu 20..65 Lebenspunkte.', 'Entzieht 20...65 Lebenspunkte. <i>Ihr beginnt zu bluten (10 Sekunden), falls Ihr mehr als 50% Eurer maximalen Lebenspunkte habt.</i>', '', '', ''),
(1077, 'Vampirbiss', 'Entzieht dem Gegner durch die Berührung 29..74 Lebenspunkte.', 'Entzieht 29...74 Lebenspunkte.', '', '', ''),
(1078, 'Suhlerbiss', 'Der berührte Gegner erleidet 20..58 Punkte Schattenschaden.', 'Richtet 20...58 Punkte Schaden an.', '', '', ''),
(1079, 'Schwächende Berührung', 'Der berührte Gegner verliert 5..50 Lebenspunkte und leidet 5..20 Sekunden lang an Schwäche.', 'Lässt 5...50 Lebenspunkte verlieren. Verursacht Zustand Schwäche (5...20 Sekunden).', '', '', ''),
(1081, 'Teinais Wind', 'Gegner und alle umstehenden Feinde erleiden 10..40 Punkte Kälteschaden. Brennende Feinde, die durch Teinais Wind getroffen werden, erleiden zusätzlich 40..80 Punkte Schaden und werden unterbrochen.', 'Richtet 10...40 Punkte Kälteschaden an. Brennende Feinde erleiden +40...80 Punkte Schaden und werden unterbrochen. Trifft auch umstehende Gegner.', '', '', ''),
(1082, 'Schockpfeil', 'Sendet einen Schockpfeil aus, der schnell auf den Gegner zufliegt und ihm 5..50 Punkte Blitzschaden zufügt. Wenn Schockpfeil einen Gegner mit Beschädigter Rüstung trifft, erhaltet Ihr 5 Energiepunkte sowie 1 Punkt für alle 2 Ränge in Energiespeicherung. Schockpfeil hat eine Rüstungsdurchdringung von 25%.', 'Rasantes Geschoss: Richtet 5...50 Punkte Blitzschaden an. Erhaltet 5 Energiepunkte sowie 1 Energiepunkt pro 2 Ränge in Energiespeicherung, wenn Ihr einen Feind mit Beschädigter Rüstung trefft. 25% Rüstungsdurchdringung.', '', '', ''),
(1083, 'Schwankender Boden', 'Am Standort des Feindes wird Schwankender Boden erzeugt. In den nächsten 5 Sekunden erleiden Gegner in der Nähe 10..40 Punkte Erdschaden pro Sekunde. Angreifende Gegner, die von Schwankender Boden getroffen werden, werden zu Boden geworfen.', 'Richtet jede Sekunde 10...40 Punkte Erdschaden an (5 Sekunden) und wirft angreifende Gegner zu Boden. Trifft Feinde in der Nähe der anfänglichen Position des Ziels.', '', '', ''),
(1084, 'Splitter-Rüstung', '5..11 Sekunden lang habt Ihr eine 25..50%ige Chance, Angriffe zu blocken, und jedes Mal, wenn Ihr das Ziel eines feindlichen Zaubers oder Angriffs seid, erleidet ein zufällig gewählter Gegner in der Nähe 5..35 Punkte Erdschaden.', '5...11 Sekunden: Ihr habt eine 25...50%ige Chance zu blocken. Richtet jedes Mal bei einem Gegner in der Nähe 5...35 Punkte Erdschaden an, wenn Ihr Ziel eines feindlichen Zaubers oder Angriffs seid.', '', '', ''),
(1085, 'Aschenexplosion', 'Umstehende Feinde erleiden 35..65 Punkte Erdschaden. Brennende Feinde, die durch Aschenexplosion getroffen werden, werden für 5 Sekunden verhext und haben eine 20..75%ige Wahrscheinlichkeit, ihr Ziel zu verfehlen.', 'Richtet 35...65 Punkte Erdschaden an. Brennende Feinde sind 5 Sekunden verhext und verfehlen 20...75% ihrer Angriffe. Trifft auch umstehende Gegner.', '', '', ''),
(1086, 'Drachenstampfer', 'Ihr beschwört an der Position des Zielgegners einen Drachenstampfer. Alle Gegner in diesem Gebiet werden zu Boden geworfen und erleiden 26..100 Punkte Erdschaden.', 'Richtet 26...100 Punkte Erdschaden an. Trifft auch Feinde in der Nähe des Gegners.', '', '', ''),
(1088, 'Zweite Chance', 'Ihr erhaltet für jeden Eurer Energiepunkte, der durch Überzaubern eingeschränkt ist, 1 Energiepunkt und 5 Punkte Gesundheit. Ihr verliert alle Verzauberungen.', 'Ihr erhaltet für jeden Punkt Überzaubern<sub>sic</sub> 1 Energiepunkt und 5 Punkte Gesundheit. Ihr verliert alle Verzauberungen.', '', '', ''),
(1090, 'Glühende Kohle', 'Der Gegner erleidet 10..70 Punkte Feuerschaden. Wenn Ihr Überzaubert seid, wird dieser Gegner für 3 Sekunden mit Glühenden Kohlen verhext und erleidet zusätzlich jede Sekunde 5..25 Feuerschaden.', 'Richtet 10...70 Punkte Feuerschaden an. Wenn Ihr Überzaubert seid, wird dieser Gegner für 3 Sekunden verhext und erleidet jede Sekunde 5...25 Feuerschaden.', '', '', ''),
(1091, 'Doppeldrache', 'Ihr beschwört die Macht des Drachen. 8 Sekunden lang werden ein Verbündeter und Ihr mit Doppeldrache verzaubert. Umstehende Feinde erleiden pro Sekunde 5..30 Punkte Feuerschaden. Wenn Ihr oder Euer Verbündeter Fertigkeiten auf einen Feind anwenden, wird dieser Feind zudem 0..3 Sekunden lang in Brand gesetzt.', '(8 Sekunden.) Verzaubert Euch und einen Verbündeten. Umstehende Feinde erleiden pro Sekunde 5...30 Punkte Feuerschaden. Fertigkeiten gegen einen Feind verursachen zustätzlich Brennen (0...3 Sekunden).', '', '', ''),
(1093, 'Teinais Hitze', 'Erstellt für 10..15 Sekunden eine Abwehr von Teinais Hitze an Eurer Position  Feinde im Bereich haben 2..5 Lebenspunktedegeneration. Die Angriffe geschwächter Feinde sind um 33% verlangsamt. Diese Fertigkeit wird 20 Sekunden lang deaktiviert.', '(10...15 Sekunden) Verursacht -2...5 Lebenspunktdegeneration. Angriffe geschwächter Feinde im Bereich sind um 33% verlangsamt. Für 20 Sekunden deaktiviert.', '', '', ''),
(1094, 'Feueratem', 'An der aktuellen Position des Gegners wird Feueratem erzeugt. Während der nächsten 5 Sekunden erleiden umstehende Feinde 10..40 Punkte Feuerschaden pro Sekunde.', 'Richtet jede Sekunde 10...40 Punkte Feuerschaden an (5 Sekunden). Trifft umstehende Feinde um die anfängliche Position des Ziels.', '', '', ''),
(1095, 'Sternenexplosion', 'Der berührte Gegner und alle Feinde im Bereich erleiden 7..112 Punkte Feuerschaden und werden 1..4 Sekunden lang in Brand gesetzt. Ihr erhaltet für jeden getroffenen Feind 2 Energiepunkte.', 'Richtet 7...112 Punkte Feuerschaden an. Verursacht Zustand Brennen (1...4 Sekunden). Ihr erhaltet für jeden getroffenen Feind 2 Energiepunkte. Trifft auch Feinde im Bereich.', '', '', ''),
(1096, 'Glyphe der Essenz', '15 Sekunden lang wird Euer nächster Zauber sofort gewirkt, führt aber dazu, dass Ihr Eure gesamte Energie verliert.', '15 Sekunden: Euer nächster Zauber kann augenblicklich gewirkt werden. <i>Ihr verliert alle Energiepunkte.</i>', '', '', ''),
(1097, 'Teinais Gefängnis', '1..6 Sekunden lang werden die Beine des Gegners von Eis umschlossen, wodurch seine Bewegungen um 66% langsamer werden. Wenn der Gegner an Beschädigter Rüstung leidet, sickert die Kälte durch und verursacht 5..9 Lebenspunktdegeneration.', '(1...6 Sekunden.) Gegner bewegt sich um 66% langsamer. Gegner mit Beschädigter Rüstung haben 5...9 Lebenspunktdegeneration.', '', '', ''),
(1098, 'Eisspiegel', 'Zerbrecht einen Eisspiegel. Alle Feinde in Eurer Nähe und in der Nähe des verbündeten Ziels (bzw. Euch selbst) erleiden 15..70 Punkte Kälteschaden und bewegen sich 2..6 Sekunden lang um 66% langsamer. Wenn Ihr einen Gegner trefft, der mit Wassermagie verhext ist, beträgt die Wiederaufladezeit unveränderlich 6 Sekunden.', 'Richtet 15...70 Punkte Kälteschaden an und verlangsamt Feinde um 66% (2...6 Sekunden). Trifft Feinde in Eurer Nähe und in der Nähe Eures Verbündeten. Unveränderliche Wiederaufladezeit von 6 Sekunden, falls ein getroffener Gegner mit Wassermagie verhext ist.', '', '', ''),
(1099, 'Teinais Kristalle', 'Der Gegner erleidet 20..40 Punkte Schaden. Wenn der Gegner unter einer Wassermagie-Verhexung leidet, richtet dieser Zauber beim Gegner und Feinden in der Nähe zusätzlich 20..40 Punkte Schaden an, und Feinde in der Nähe erleiden Beschädigte Rüstung für 5..15 Sekunden.', 'Fügt dem Gegner 20...40 Punkte Schaden zu. Verursacht +20...40 mehr Schaden und verursacht Beschädigte Rüstung (5...15 Sekunden) bei Feinden in der Nähe, die unter einer Wasserverhexung leiden.', '', '', ''),
(1113, 'Kirins Zorn', '5 Sekunden lang erleiden umstehende Gegner am Wirkungsort des Zaubers 8..32 Punkte heiligen Schaden pro Sekunde.', 'Richtet jede Sekunde 8...32 Punkte heiligen Schaden an (5 Sekunden). Trifft umstehende Feinde um Eure anfängliche Position.', '', '', ''),
(1114, 'Geisterbindung', 'Während der nächsten 8 Sekunden wird der Verbündete (bzw. Ihr selbst) die nächsten 10 Male vor dem Erleiden von mehr als 50 Punkten Schaden um 30..90 Lebenspunkte geheilt.', '8 Sekunden: Heilt jedes Mal um 30...90 Lebenspunkte, bevor der Verbündete (bzw. Ihr selbst) mehr als 50 Punkte Schaden erleidet. <i>Endet nach 10 Wirkungen.</i>', 'Geisterbindung (PvP)', 'Während der nächsten 8 Sekunden wird der Verbündete (bzw. Ihr selbst) die nächsten 10 Male vor dem Erleiden von mehr als 50 Punkten Schaden um 30..90 Lebenspunkte geheilt.', '8 Sekunden: Heilt jedes Mal um 30...90 Lebenspunkte, bevor der Verbündete (bzw. Ihr selbst) mehr als 50 Punkte Schaden erleidet. <i>Endet nach 10 Wirkungen.</i>'),
(1115, 'Flair der Verzauberung', '4..10 Sekunden lang kosten Verzauberungen, die auf diesen Verbündeten gewirkt werden, 5 Energiepunkte weniger (mindestens 1 Energie).', '4...10 Sekunden: Auf den Verbündeten gewirkte Verzauberungen kosten 5 Energiepunkte weniger (mindestens 1 Energiepunkt). <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1117, 'Himmlische Wonne', 'Heilt Euch und Gruppenmitglieder in Hörweite um 15..60 Punkte.', 'Heilt Euch selbst und Gruppenmitglieder in Hörweite um 15...60 Punkte.', '', '', ''),
(1118, 'Heilsalve', 'Der Verbündete (bzw. Ihr selbst) wird um 10..160 Lebenspunkte geheilt. Alle Gruppenmitglieder in Hörweite Eures Ziels erhalten Lebenspunkte entsprechend des mit diesem Zauber verbundenen Gunst-der-Götter-Bonus. Eure Peinigungsgebete werden 20 Sekunden lang deaktiviert.', 'Heilt um 10...160 Lebenspunkte. Gruppenmitglieder in Hörweite Eures Ziels erhalten Lebenspunkte entsprechend des Gunst-der-Götter-Bonus. Deaktiviert Eure Peinigungsgebete (20 Sekunden).', '', '', ''),
(1119, 'Kareis Heilkreis', 'Heilt Euch selbst und alle umstehenden Geschöpfe um 30..180 Punkte.', 'Heilt Euch um 30...180 Lebenspunkte. Heilt auch umstehende Kreaturen.', '', '', ''),
(1120, 'Jameis Starren', 'Heilt Verbündeten für 35..180 Lebenspunkte.', 'Heilt um 35...180 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1121, 'Geschenk des Lebens', 'Alle Eure anderen Heilgebete werden 10..5 Sekunden lang deaktiviert. Der Verbündete wird um 15..150 Lebenspunkte geheilt.', 'Heilt um 15...150 Lebenspunkte. <i>Eure anderen Heilgebete-Fertigkeiten werden deaktiviert (10...5 Sekunden). Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1123, 'Lebensmantel', 'Entfernt beim Verbündeten (bzw. Euch selbst) 0..2 Zustände. 8 Sekunden lang wird der Verbündete (bzw. Ihr selbst), wenn er das nächste Mal Schaden erleiden oder Lebenspunkte entzogen bekommen würde, stattdessen um den entsprechenden Wert geheilt (maximal 20..100).', '(8 Sekunden.) Wandelt den nächsten erlittenen Schaden bzw. die nächsten entzogenen Lebenspunkte (maximal 20...100 Punkte) in Heilung um. Anfangswirkung: Entfernt 0...2 Zustände.', '', '', ''),
(1126, 'Ausdrückliche Entfernung', 'Ihr und der Verbündete verlieren 1 Zustand und 1 Verhexung und werden um 50 Lebenspunkte geheilt.', 'Entfernt einen Zustand und eine Verhexung vom Verbündeten und Euch selbst und heilt um 50 Punkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1128, 'Gesang der Wiederbelebung', 'Ein Gruppenmitglied wird mit höchstens der Anzahl Eurer Lebenspunkte und 5..35% der Energiepunkte wiederbelebt. Die Reichweite dieses Zaubers ist nur halb so groß wie gewöhnlich.', 'Das Gruppenmitglied wird wiederbelebt (Eure aktuellen Lebenspunkte, 5...35% Energiepunkte).', '', '', ''),
(1129, 'Wort des Tadels', 'Der Gegner erleidet 15..75 Punkte heiligen Schaden. Waren seine Lebenspunkte Beim Anwenden dieses Zaubers unter 33%, benötigt Wort des Tadels 20 Sekunden zusätzliche Wiederaufladezeit.', 'Richtet 15...75 Punkte heiligen Schaden an. <i>+20 Sekunden zur Aufladezeit, falls der Gegner weniger als 33% seiner maximalen Lebenspunkte hat.</i>', '', '', ''),
(1130, 'Speer des Lichts', 'Speer des Lichts fliegt auf den Gegner zu und fügt ihm, falls er trifft, 26..56 Punkte heiligen Schaden zu. Speer des Lichts fügt +15..60 Punkte Schaden zu, wenn er einen angreifenden Gegner trifft.', 'Geschoss: Richtet 26...56 Punkte heiligen Schaden an. Richtet zusätzlich 15...60 Punkte Schaden an, falls der Gegner gerade angreift.', '', '', ''),
(1131, 'Steinseelenschlag', 'Der berührte Gegner erleidet 10..55 Punkte heiligen Schaden. Wenn Euer Ziel zu Boden geworfen wurde, erleidet es zusätzlich 10..55 heiligen Schaden.', 'Richtet 10...55 Punkte heiligen Schaden an. Richtet zusätzlich 10...55 Punkte heiligen Schaden an, falls der Gegner zu Boden geworfen ist.', '', '', ''),
(1133, 'Betrunkener Schlag', 'Wenn dieser Angriff trifft, fügt Ihr +10..40 Punkte Schaden zu und Euer Ziel leidet unter einem der folgenden Zustände: Tiefe Wunde (20 Sekunden lang), Schwäche (20 Sekunden lang), Blutung (25 Sekunden lang) oder Verkrüppelung (15 Sekunden lang). Nachdem Ihr einen Betrunkenen Schlag ausgeführt habt, werdet Ihr zu Boden geworfen.', 'Richtet +10...40 Punkte Schaden an. Verursacht willkürlich einen der folgenden Zustände: Tiefe Wunde (20 Sekunden), Schwäche (20 Sekunden), Blutung (25 Sekunden) oder Verkrüppelung (15 Sekunden). <i>Ihr werdet zu Boden geworfen.</i>', '', '', ''),
(1134, 'Leviathanschlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff geblockt wird, wird Euer Ziel zu Boden geworfen und erleidet 10..34 Punkte Schaden.', 'Richtet +5...20 Punkte Schaden an. Wirft zu Boden und richtet 10...34 Punkte Schaden an, falls geblockt.', '', '', ''),
(1135, 'Jaizhenjuschlag', 'Wenn Jaizhenjuschlag trifft, fügt Ihr +1..30 Punkte Schaden zu. Wenn Ihr keine Haltung verwendet, ist ein Blocken nicht möglich.', 'Richtet +1...30 Punkt Schaden an. Kann nicht geblockt werden, es sei denn, Ihr nehmt eine Haltung ein.', '', '', ''),
(1136, 'Durchdringender Hieb', 'Wenn dieser Angriff erfolgreich ist, trefft Ihr für 5..20 Punkte zusätzlichen Schaden. Dieser Axtangriff hat eine Rüstungsdurchdringung von 20%.', 'Richtet +5...20 Punkte Schaden an. 20% Rüstungsdurchdringung.', '', '', ''),
(1137, 'Yetischlag', 'Alles Adrenalin geht verloren. Greift alle umstehenden Feinde an. Wenn dieser Angriff einen Gegner trifft, der unter einem Zustand leidet, wird dieser zu Boden geworfen. (Bei einer Hammerbeherrschung von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', 'Greift alle umstehenden Feinde an. Wenn der angegriffene Gegner unter einem Zustand leidet, wird er zu Boden geworfen. <i>Alles Adrenalin geht verloren. Mindestens Hammerbeherrschung 5, sonst 50%ige Chance auf Misserfolg.)</i>', '', '', ''),
(1141, '"Ihr werdet sterben!"', 'Wenn die Lebenspunkte des Gegners unter 50% liegen, erhaltet Ihr 1..3 Adrenalinstöße.', 'Ihr erhaltet 1...3 Adrenalinstöße. <i>Der Gegner muss weniger als 50% seiner maximalen Lebenspunkte haben, sonst keine Wirkung.</i>', '', '', ''),
(1142, 'Günstige Abwehr', '8 Sekunden lang wird der nächste Angriff gegen Euch geblockt und diese Haltung endet. Ihr erhaltet 1..4 Adrenalinstöße, wenn diese Haltung endet.', '(8 Sekunden.) Blockt einen Angriff. Wirkung am Ende: Ihr erhaltet 1...4 Adrenalinstöße.', '', '', ''),
(1144, 'Silberflügel-Hieb', 'Bei einem Treffer fügt dieser Angriff +1..40 Punkte Schaden zu.', 'Richtet +1...40 Punkte Schaden an.', '', '', ''),
(1146, 'Schubs', 'Der berührte Gegner wird zu Boden geworfen. Wenn dieser Gegner in Bewegung war, erleidet er 15..75 Punkte Schaden und jede eingesetzte Haltung endet, bevor er zu Boden geworfen wird.', 'Wirft den Gegner zu Boden. Feinde, die sich bewegen, erleiden 15...75 Punkte Schaden und verlieren ihre aktuelle Haltung, bevor sie zu Boden geworfen werden.', '', '', ''),
(1191, 'Trennender Angriff', 'Wenn Trennender Angriff trifft, fügt Ihr +5..25 Punkte Schaden zu. Dieser Angriff hat eine Rüstungsdurchdringung von 10%.', 'Richtet +5...25 Punkte Schaden an. 10% Rüstungsdurchdringung.', 'Trennender Angriff (PvP)', 'Wenn Trennender Angriff trifft, fügt Ihr +3..10 Punkte Schaden zu. Dieser Angriff hat eine Rüstungsdurchdringung von 10%.', 'Richtet +3...10 Punkte Schaden an. 10% Rüstungsdurchdringung.'),
(1192, 'Zojuns Schuss', 'Ein Pfeil wird abgeschossen, der nur die halbe Reichweite zurücklegt, aber dafür +10..40 Punkte Schaden zufügt.', 'Richtet +10...40 Punkte Schaden an.', '', '', ''),
(1194, 'Raubtierverbindung', 'Während der nächsten 5..20 Sekunden greift Euer Tiergefährte um 25% schneller an und Ihr werdet bei jedem erfolgreichen Angriff um 1..31 Punkte geheilt.', '(5...20 Sekunden.) Euer Tiergefährte greift um 25% schneller an und Ihr erhaltet bei jedem erfolgreichen Angriff Eures Tiergefährten 1...31 Lebenspunkte.', 'Raubtierverbindung (PvP)', 'Während der nächsten 5..20 Sekunden werdet Ihr durch Treffer Eures Tiergefährten um 1..31 Punkte geheilt.', '5...20 Sekunden: Ihr erhaltet bei jedem erfolgreichen Angriff Eures Tiergefährten 1...31 Lebenspunkte.'),
(1195, 'Gemeinsame Heilung', 'Euer Tiergefährte entzieht 15 Sekunden lang jedes Mal 1..20 Lebenpunkte, wenn er mit einem Angriff trifft. Sowohl Ihr als auch Euer Tiergefährte werden für 20..104 Lebenspunkte geheilt. Wenn Euer Gefährte tot ist, wird er mit 50% der Lebenspunkte wiederbelebt. Wenn Ihr diese Fertigkeit benutzt, reist Euer Tiergefährte mit Euch.', '(15 Sekunden.) Euer Tiergefährte einzieht mit jedem Angriff 1...20 Lebenspunkte. Anfangswirkung: Heilt Euch und Euren Tiergefährten für 20...104 Lebenspunkte. Euer Tiergefährte wird wiederbelebt (50% Lebenspunkte). Wenn Ihr diese Fertigkeit benutzt, reist Euer Tiergefährte mit Euch.', 'Gemeinsame Heilung (PvP)', 'Wenn Eure oder die Lebenspunkte Eures Tiergefährten unter 75% liegen, werdet Ihr beide für 25..145 Lebenspunkte geheilt. Falls Euer Gefährte tot ist, wird er mit 50% seiner Lebenspunkte wiederbelebt.', 'Heilt Euch und Euren Tiergefährten um 25...145 Lebenspunkte. <i>Ihr oder Euer Tiergrfährte müsst weniger als 75% der maximalen Lebenspunkte haben, sonst keine Wirkung.</i> Falls Euer Tiergefährte tot ist, wird er mit 50% seiner Lebenspunkte wiederbelebt.'),
(1196, 'Zojuns Eile', '5..11 Sekunden lang bewegt Ihr Euch um 33% schneller und habt eine 27..75%ige Chance, Geschosse zu blocken. Zojuns Eile endet, wenn Ihr angreift.', '5...11 Sekunden: Ihr bewegt Euch um 33% schneller und habt eine 27...75%ige Chance, Geschosse zu blocken. <i>Endet, sobald Ihr angreift.</i>', '', '', ''),
(1197, 'Nadelschuss', 'Wenn der Gegner unter 50% seiner Lebenspunkte hat, wird Nadelschuss sofort wieder aufgeladen. Wenn dieser Angriff erfolgreich ist, fügt er nur 10..30 Punkte Schaden zu. Der Pfeil bewegt sich schneller als normal. Eure anderen Bogenangriffsfertigkeiten werden 2 Sekunden lang deaktiviert.', 'Schneller Pfeil. Richtet 10...30 Punkte Schaden an. Lädt sich sofort wieder auf, falls der Gegner weniger als 50% seiner maximalen Lebenspunkte hat. <i>Deaktiviert Eure anderen Angriffsfertigkeiten (2 Sekunden).</i>', '', '', ''),
(1198, 'Stahlspitzenpfeil', 'Ihr schießt einen Stahlspitzenpfeil ab, der sich langsamer als normal bewegt. Wenn er trifft, leidet der Gegner 5..20 Sekunden lang unter Benommenheit und falls er einen Zauber wirkt, wird dieser unterbrochen.', 'Langsamer Pfeil. Unterbricht einen Zauber. Verursacht Zustand Benommenheit (5...20 Sekunden).', '', '', ''),
(1199, 'Glaspfeile', 'Während der nächsten 10..35 Sekunden fügen Eure Pfeile bei einem Treffer +5..20 Punkte Schaden zu und führen 10..20 Sekunden lang zu einer Blutung, wenn sie geblockt werden.', '10...35 Sekunden: Eure Pfeile richten +5...20 Punkte Schaden an. Verursacht Zustand Blutung (10...20 Sekunden), falls geblockt.', 'Glaspfeile (PvP)', 'Während der nächsten 10..35 Sekunden fügen Eure Pfeile bei einem Treffer +5..15 Punkte Schaden zu und führen 10..20 Sekunden lang zu einer Blutung, wenn sie geblockt werden.', '10...35 Sekunden: Eure Pfeile richten +5...15 Punkte Schaden an. Verursacht Zustand Blutung (10...20 Sekunden), falls geblockt.'),
(1200, 'Siegel des Bogenschützen', '0..24 Sekunden lang dauern alle von Euch zugefügten Zustände, während Ihr einen Bogen führt, um 100% länger an.', '0...24 Sekunden: Von Euch zugefügte Zustände, während Ihr einen Bogen führt, dauern um 100% länger an.', '', '', ''),
(1201, 'Wilder Sprung', 'Euer Tiergefährte führt einen Wilden Sprung aus, der +5..20 Punkte Schaden zufügt. Trifft dieser Angriff einen Gegner, der einen Zauber wirkt, wird er zu Boden geworfen.', 'Richtet ++5...20 Punkte Schaden an. Wirft zu Boden, falls der Gegner einen Zauber wirkt.', '', '', ''),
(1202, 'Erzürnter Ausfall', 'Euer Tiergefährte greift mit einem Erzürnten Ausfall an, der dem Gegner 5..20 Sekunden lang eine Tiefe Wunde und +10..50 Punkte Schaden zufügt.', 'Verursacht Zustand Tiefe Wunde (5...20 Sekunden) und fügt +10...50 Punkte Schaden zu.', 'Erzürnter Ausfall (PvP)', 'Euer Tiergefährte greift mit einem Erzürnten Ausfall an, der für jede Tierbeherrschungs-Fertigkeit, die wieder aufgeladen wird, +3..23 Punkte Schaden (maximaler Bonus 80) zufügt.', 'Richtet für jede Eurer sich aufladenden Tierbeherrschungs-Fertigkeiten +3...23 Punkte Schaden an (maximal 80).'),
(1203, 'Bestienattacke', 'Euer Tiergefährte greift mit einer Bestienattacke an, die +5..20 Punkte Schaden zufügt. Trifft dieser Angriff einen Gegner am Boden, leidet dieser 4..10 Sekunden an Benommenheit.', 'Richtet +5...20 Punkte Schaden an. Verursacht Zustand Benommenheit (4...10 Sekunden), falls der Gegner zu Boden geworfen ist.', '', '', ''),
(1205, 'Giftbiss', 'Euer Tiergefährte greift mit einem Giftbiss an, der den Gegner 5..20 Sekunden lang vergiftet.', 'Verursacht Zustand Gift (5...20 Sekunden).', '', '', ''),
(1206, 'Sprung', 'Der nächste Angriff Eures Tiergefährten ist ein Sprung, der +5..20 Punkte Schaden zufügt. Trifft dieser Angriff einen Gegner, der sich bewegt, wird dieser zu Boden geworfen.', 'Richtet +5...20 Punkte Schaden an. Wirft zu Boden, falls der Gegner sich bewegt.', '', '', ''),
(1209, 'Bestienwut', 'Alle Eure Nicht-Angriffsfertigkeiten werden 5 Sekunden lang deaktiviert. 5..11 Sekunden lang greift Ihr um 25% schneller an.', '5...11 Sekunden: Ihr greift um 25% schneller an. <i>Eure Nicht-Angriffsfertigkeiten werden deaktiviert (5 Sekunden).</i>', '', '', ''),
(1211, 'Vipernnest', 'Erzeugt ein Vipernnest. Wenn es ausgelöst wird, erleiden alle getroffenen Gegner in der Nähe 5..35 Punkte Stichschaden und werden 5..20 Sekunden lang vergiftet. Vipernnest endet nach 90 Sekunden. Diese Falle kann leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 5...35 Punkte Stichschaden an. Verursacht Zustand Gift (5...20 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(1212, 'Tagundnachtgleiche', 'Erstellt einen Geist der Stufe 1..10. In seiner Reichweite gewirkte Zauber, die Überzaubern hervorrufen, verursachen zusätzlich 10 Überzaubern. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...150 Sekunden Lebensdauer). Überzaubern verursachende Zauber, die in seiner Reichweite gewirkt werden, verursachen zusätzlich 10 Überzaubern.', '', '', ''),
(1213, 'Ruhe', 'Erstellt einen Geist der Stufe 1..10. Von Kreaturen in seiner Reichweite, die keine Geister sind, gewirkte Verzauberungen enden 20..50% schneller . Dieser Geist stirbt nach 15..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...60 Sekunden Lebensdauer). Verzauberungen enden bei Kreaturen in Reichweite um 20...50% schneller. Keine Wirkung auf Geister.', '', '', ''),
(1215, 'Gezeter der Seelen', 'Der Gegner und alle Feinde in der Nähe erleiden 10..65 Punkte Blitzschaden. Wenn Ihr Euch in Hörweite eines Geistes befindet oder einen gebündelten Gegenstand haltet, erhaltet Ihr 10 Punkte Energie.', 'Richtet beim Gegner und bei Feinden in der Nähe 10...65 Blitzschaden an. Ihr erhaltet 10 Energie, wenn Ihr in Hörweite eines Geistes seid oder einen gebündelten Gegenstand haltet.', '', '', ''),
(1217, 'Herr der Rituale', '5..35 Sekunden lang erhalten Eure Ritualisten-Attribute einen Schub von 2..4 für Eure nächste Fertigkeit. Wenn diese Fertigkeit ein Binderitual ist, wird sie um 10..60% schneller wieder aufgeladen und Herr der Rituale lädt sich sofort wieder auf.', '(5...35 Sekunden.) Ihr erhaltet +2...4 auf alle Ritualisten-Attribute für Eure nächste Fertigkeit. Eure Binderrituale werden um 10...60% schneller wieder aufgeladen und bewirken, dass sich diese Fertigkeit sofort wieder auflädt.', '', '', ''),
(1218, 'Grausam war Daoshen', 'Haltet Daoshens Asche bis zu 15..60 Sekunden lang. Während Ihr seine Asche haltet, haben alle Ritualisten-Fertigkeiten eine Rüstungsdurchdringung von 10%. Wenn Ihr seine Asche fallen lasst, erleiden alle Feinde in der Nähe 15..85 Punkte Blitzschaden.', '(15...60 Sekunden.) Eure Ritualisten-Fertigkeiten haben eine Rüstungsdurchdringung von 10%. Fallwirkung: Richtet bei allen Feinden in der Nähe 15...85 Punkte Blitzschaden an.', '', '', ''),
(1219, 'Schützend war Kaolai', 'Haltet Kaolais Asche bis zu 15..60 Sekunden lang. Während Ihr seine Asche haltet, erhaltet Ihr 10 Rüstung. Wenn ihr seine Asche fallen lasst, werden alle Gruppenmitglieder um 10..85 Lebenspunkte geheilt.', '15...60 Sekunden: Ihr habt +10 Rüstung. Fallwirkung: Alle Gruppenmitglieder werden um 10...85 Lebenspunkte geheilt.', '', '', ''),
(1220, 'Eingestimmt war Songkai', 'Haltet Songkais Asche bis zu 45 Sekunden lang. Während Ihr ihre Asche haltet, kostet das Wirken Eurer Zauber und Binderituale bis zu -5..50% weniger Basisenergie.', '45 Sekunden: Das Wirken Eurer Zauber und Binderituale kostet 5...50% der Basis-Energiekosten weniger.', '', '', ''),
(1221, 'Unverwüstlich war Xiko', 'Haltet Xikos Asche bis zu 5..20 Sekunden lang. Für jede Verhexung und jeden Zustand, unter der bzw. dem Ihr leidet, solange Ihr die Asche haltet, erhaltet Ihr +3 Lebenspunktregeneration. Wenn Ihr die Asche fallen lasst, verliert Ihr 1..4 Zustände.', '5...20 Sekunden: Für jede Verhexung und jeden Zustand auf Euch habt Ihr +3 Lebenspunktregeneration. Fallwirkung: Ihr verliert 1...4 Zustände.', '', '', ''),
(1222, 'Lebhaft war Naomei', 'Haltet ihre Asche bis zu 45 Sekunden lang. Wenn Ihr sie fallen lasst, werden alle Gruppenmitglieder in dem Bereich mit 15..75% ihrer Lebenspunkte und 0 Energie wiederbelebt.', '45 Sekunden: Fallwirkung: Gruppenmitglieder im Bereich werden wiederbelebt (15...75% Lebenspunkte, 0 Energiepunkte).', '', '', ''),
(1223, 'Gequält war Lingwah', 'Haltet Lingwahs Asche bis zu 10..60 Sekunden lang. Während Ihr ihre Asche haltet, kosten Eure Ritualisten-Verhexungen 1..5 weniger Energiepunkte und halten um 33% länger an. Wenn Ihr ihre Asche fallen lasst, werden alle Eure Ritualisten-Verhexungen wieder aufgeladen.', '(10...60 Sekunden.) Eure Ritualisten-Verhexungen kosten 1...5 weniger Energiepunkte und halten um 33% länger an. Fallwirkung: Alle Eure Ritualisten-Verhexungen werden wieder aufgeladen.', '', '', ''),
(1224, 'Geist entziehen', 'Teleportiert den verbündeten Geist an Eure Position.', 'Teleportiert den verbündeten Geist an Eure Position.', '', '', ''),
(1225, 'Gelenkter Schlag', 'Der Gegner erleidet 5..95 Punkte Blitzschaden. Dieser Gegner erleidet zusätzlich 5..35 Punkte Blitzschaden, wenn Ihr einen Gegenstand haltet.', 'Richtet 5...95 Blitzschaden an. Richtet zusätzlich 5...35 Punkte Blitzschaden an, falls Ihr einen Gegenstand haltet.', '', '', ''),
(1226, 'Geistersegenstoß', 'Der Gegner erleidet 20..65 Punkte Blitzschaden und alle von Euch kontrollierten Geister in Hörweite erhalten 20..65 Lebenspunkte.', 'Richtet 20...65 Punkte Blitzschaden an. Von Euch kontrollierte Geister in Hörweite erhalten 20...65 Lebenspunkte.', '', '', ''),
(1227, 'Essenzschlag', 'Der Gegner erleidet 15..60 Punkte Blitzschaden. Wenn sich Geister in Hörweite befinden, erhaltet Ihr 1..9 Energiepunkte.', 'Richtet 15...60 Punkte Blitzschaden an. Ihr erhaltet 1...9 Energiepunkte, falls sich Geister in Hörweite befinden.', '', '', ''),
(1228, 'Geist abzapfen', 'Der Euch am nächsten stehende Geist verliert seine gesamte Energie. Ihr erhaltet 15..50% dieser Energie.', 'Der Euch am nächsten stehende Geist verliert seine gesamte Energie. Ihr erhaltet 15...50% dieser Energie.', '', '', ''),
(1229, 'Explosives Wachstum', 'Während der nächsten 15..60 Sekunden erleiden jedes Mal, wenn Ihr eine Kreatur erschafft, bis zu 5 Gegner neben dieser Kreatur 20..65 Punkte Blitzschaden.', '15...60 Sekunden: Richtet jedes Mal, wenn Ihr eine Kreatur erstellt, bei 5 Feinden in deren Nähe 20...65 Punkte Blitzschaden an.', '', '', ''),
(1230, 'Segen der Schöpfung', '15..60 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr eine Kreatur erschafft, 1..6 Energiepunkte und heilt um 5..50 Lebenspunkte.', '15...60 Sekunden: Ihr erhaltet jedes Mal 1...6 Energiepunkte und heilt um 5...50 Lebenspunkte, wenn Ihr eine Kreatur erstellt.', '', '', ''),
(1231, 'Geisterlenkung', '12 Sekunden lang habt Ihr +1..6 Energieregeneration. Wenn Ihr Euch beim Wirken dieses Zaubers in Hörweite eines Geistes befindet, erhaltet Ihr 3..12 Energiepunkte.', '(12 Sekunden.) Ihr erhaltet +1...6 Energieregeneration. Anfangswirkung: Wenn Ihr Euch in Hörweite eines Geistes befindet, erhaltet Ihr 3...12 Energiepunkte.', '', '', ''),
(1232, 'Rüstung der Gefühllosigkeit', '10..35 Sekunden lang verringert sich der von Euren Geistern in Hörweite erlittene Schaden um 50% und sie sind gegen kritische Treffer gefeit.', '(10...35 Sekunden.) Eure Geister in Hörweite erleiden 50% weniger Schaden und sind gegen kritische Treffer gefeit.', 'Rüstung der Gefühllosigkeit (PvP)', '10..35 Sekunden lang habt Ihr beim Wirken von Binderitualen eine Basisschadens-Reduzierung von 10.', '(10...35 Sekunden.) Ihr habt eine Basisschadens-Reduzierung von 10 beim Wirken von Binderitualen.'),
(1233, 'Beruhigende Erinnerungen', 'Der Verbündete (bzw. Ihr selbst) wird um 10..100 Lebenspunkte geheilt. Wenn Ihr einen Gegenstand haltet, erhaltet Ihr 3 Energiepunkte.', 'Heilt um 10...100 Lebenspunkte. Ihr erhaltet 3 Energiepunkte, falls Ihr einen Gegenstand haltet.', '', '', ''),
(1234, 'Körper und Seele heilen', 'Der Verbündete (bzw. Ihr selbst) wird um 20..115 Lebenspunkte geheilt. Der betreffende Verbündete verliert für jeden Geist in Hörweite einen Zustand.', 'Heilt um 20...115 Lebenspunkte. Entfernt für jeden Geist in Hörweite einen Zustand.', '', '', ''),
(1235, 'Stumpfe Waffe', '5..15 Sekunden lang können der Gegner und alle umstehenden Feinde in der Nähe keine kritischen Treffer landen und verursachen 1..15 Punkte weniger Schaden.', 'Verhext den Gegner sowie umstehende Feinde (5...15 Sekunden). Entfernt die Fähigkeit, kritische Treffer zu landen. Reduziert den Schaden um 1...15 Punkte.', '', '', ''),
(1236, 'Fesselnde Ketten', 'Während der nächsten 3 Sekunden bewegen sich der Gegner und alle umstehenden Feinde um 90% langsamer und und erleiden 1..30 Punkte Schaden pro Sekunde, solange sie in Bewegung sind.', '(3 Sekunden.) Der Gegner und alle umstehenden Feinde bewegen sich um 90% langsamer und erleiden 1...30 Punkte Schaden pro Sekunde, solange sie in Bewegung sind.', '', '', ''),
(1237, 'Schmerzhafte Bindung', '10..20 Sekunden lang werden der Gegner und alle Feinde in der Nähe mit Schmerzhafte Bindung verhext und erleiden jedes Mal, wenn sie von einem Geisterangriff getroffen werden, 8..20 Punkte Schaden.', 'Verhext den Gegner und Feinde in der Nähe (10...20 Sekunden). Geister richten bei den Betroffenen +8...20 Schaden an.', '', '', ''),
(1238, 'Siegel der Schöpfung', 'Ihr erhaltet 4 Energiepunkte für jede von Euch kontrollierte herbeigerufene Kreatur in Hörweite (maximal 3..12 Energiepunkte).', 'Ihr erhaltet 4 Energiepunkte für jede von Euch kontrollierte herbeigerufene Kreatur in Hörweite (maximal 3...12 Energiepunkte).', '', '', ''),
(1239, 'Siegel der Geister', 'Erstellt 3 Geister der Stufe 1..12. Diese Geister fügen mit Angriffen 5..20 Punkte Schaden zu und sterben nach 60 Sekunden.', 'Erstellt 3 Geister der Stufe 1...12 (60 Sekunden Lebensdauer). Diese Geister fügen mit Angriffen 5...20 Punkte Schaden zu.', 'Siegel der Geister (PvP)', 'Ihr erhaltet  3..12 Energiepunkte, wenn Ihr Euch in Hörweite eines Geistes befindet.', 'Ihr erhaltet 3...12 Energiepunkte. <i>Ihr müsst Euch in Hörweite eines Geistes befinden, sonst keine Wirkung.</i>'),
(1240, 'Seelenverflechtung', '5..45 Sekunden lang kosten die nächsten 1..3 Binderituale, die Ihr ausführt, 15 weniger Energiepunkte (mindestens 10) und laden sich sofort wieder auf.', '(5...45 Sekunden.) Die nächsten 1...3 Binderituale, die Ihr ausführt, kosten 15 weniger Energiepunkte (mindestens 10) und laden sich sofort wieder auf.', '', '', ''),
(1244, 'Geisterhafte Eile', 'Die Zauber, die Ihr wirkt, während Ihr Euch in dem Bereich eines Geistes befindet, werden 5..20 Sekunden lang um 25% schneller wieder aufgeladen.', '5...20 Sekunden: Von Euch gewirkte Zauber laden sich um 25% schneller wieder auf. <i>Ihr müsst Euch in Hörweite eines Geistes befinden, sonst keine Wirkung.</i>', '', '', ''),
(1245, 'Blick aus dem Jenseits', 'Der Gegner erleidet 20..65 Punkte Blitzschaden. Wenn Ihr Euch in Hörweite eines Geistes befindet, wird der Gegner 2..6 Sekunden lang geblendet.', 'Richtet 20...65 Punkte Blitzschaden an. Verursacht den Zustand Blindheit (2...6 Sekunden), falls Ihr Euch in Hörweite eines Geistes befindet.', '', '', ''),
(1246, 'Wut der Vorfahren', '1 Sekunde lang geschieht nichts. Wenn diese Fertigkeit endet erleiden alle den Verbündeten (bzw. Euch selbst) umstehenden Feinde 5..110 Blitzschaden.', '1 Sekunde: Wirkung am Ende: Richtet bei umstehenden Feinden um den Verbündeten (bzw. Euch selbst) 5...110 Punkte Blitzschaden an.', 'Wut der Vorfahren (PvP)', '3 Sekunden lang erleiden alle den Verbündeten (bzw. Euch selbst) umstehenden Feinde pro Sekunde 1..41 Punkte Blitzschaden.', '3 Sekunden: Richtet bei den Verbündeten (bzw. Euch selbst) umstehenden Feinden pro Sekunde 1...41 Punkte Blitzschaden an.'),
(1247, 'Schmerzen', 'Erstellt einen Geist der Stufe 1..12. Die Angriffe dieses Geistes fügen 5..30 Punkte Schaden zu. Dieser Geist stirbt nach 30..150 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (30...150 Sekunden Lebensdauer). Seine Angriffe richten 5...30 Punkte Schaden an.', 'Schmerz (PvP)', 'Erstellt einen Geist der Stufe 1..8. Die Angriffe dieses Geistes fügen 5..30 Punkte Schaden zu. Dieser Geist stirbt nach 30..150 Sekunden.', ''),
(1249, 'Verschiebung', 'Erstellt einen Geist der Stufe 1..14. Alle Verbündeten in seiner Reichweite, die keine Geister sind, haben eine 75%ige Chance, Angriffe zu blocken. Jedes Mal, wenn ein Angriff auf diese Weise geblockt wird, erleidet dieser Geist 60 Punkte Schaden. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (30...60 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, haben eine 75%ige Chance zu blocken. <i>Blockwirkung: Der Geist erleidet 60 Punkte Schaden.</i>', 'Verschiebung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Alle Nicht-Geist-Verbündeten in seiner Reichweite haben eine 75%ige Chance, Angriffe zu blocken. Jedes Mal, wenn ein Angriff auf diese Art geblockt wird, erleidet dieser Geist 60 Punkte Schaden. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...60 Sekunden Lebensdauer). Verbündete in Reichweite, die keine Geister sind, haben eine 75%ige Chance zu blocken. <i>Blockwirkung: Der Geist erleidet 60 Punkte Schaden.</i>'),
(1250, 'Erhaltung', 'Erstellt einen Geist der Stufe 1..14. Dieser Geist heilt alle 4 Sekunden einen Verbündeten in dem Bereich um 10..115 Lebenspunkte. Dieser Geist stirbt nach 90 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (90 Sekunden Lebensdauer). Alle 4 Sekunden heilt der Geist einen Verbündeten, der kein Geist ist, um 10...115 Lebenspunkte.', 'Erhaltung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Dieser Geist heilt alle 4 Sekunden einen Verbündeten in dem Bereich um 10..115 Lebenspunkte. Dieser Geist stirbt nach 90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (90 Sekunden Lebensdauer). Alle 4 Sekunden heilt der Geist einen Verbündeten, der kein Geist ist, um 10...115 Lebenspunkte.'),
(1251, 'Leben', 'Erstellt einen Geist der Stufe 1..14. Wenn dieser Geist stirbt, werden alle Verbündeten in seiner Reichweite, die keine Geister sind, pro Sekunde, die der Geist am Leben war, um 1..7 Lebenspunkte geheilt. Dieser Geist stirbt nach 20 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (20 Sekunden Lebensdauer). Betrifft Verbündete in Reichweite, die keine Geister sind. Wirkung am Ende: Heilt pro Sekunde, die der Geist am Leben war, um 1...7 Lebenspunkte.', 'Leben (PvP)', 'Erstellt einen Geist der Stufe 1..10. Wenn dieser Geist stirbt, werden alle Verbündeten in seiner Reichweite, die keine Geister sind, pro Sekunde, die der Geist am Leben war, um 1..7 Lebenspunkte geheilt. Dieser Geist stirbt nach 20 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (20 Sekunden Lebensdauer). Betrifft Verbündete in Reichweite, die keine Geister sind. Wirkung am Ende: Heilt pro Sekunde, die der Geist am Leben war, um 1...7 Lebenspunkte.'),
(1252, 'Erdbindung', 'Erstellt einen Geist der Stufe 1..14. Alle Gegner, die keine Geister sind, die in seiner Reichweite zu Boden geworfen werden, bleiben mindestens 3 Sekunden lang am Boden. Jedes Mal, wenn dies geschieht, verliert der Geist 50..25 Lebenspunkte. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (15...45 Sekunden Lebensdauer). Feinde in Reichweite, die keine Geister sind und zu Boden geworfen werden, bleiben mindestens 3 Sekunden lang am Boden. <i>Bodenwurfkosten: Der Geist verliert 50...25 Lebenspunkte.</i>', 'Erdbindung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Alle Gegner, die keine Geister sind, die in seiner Reichweite zu Boden geworfen werden, bleiben mindestens 3 Sekunden lang am Boden. Jedes Mal, wenn dies geschieht, verliert der Geist 50..25 Lebenspunkte. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...45 Sekunden Lebensdauer). Feinde in Reichweite, die keine Geister sind und zu Boden geworfen werden, bleiben mindestens 3 Sekunden lang am Boden. <i>Bodenwurfkosten: Der Geist verliert 50...25 Lebenspunkte.</i>'),
(1253, 'Blutgesang', 'Erstellt einen Geist der Stufe 1..12, der nach 30..150 Sekunden stirbt. Angriffe durch diesen Geist entziehen bis zu 5..25 Lebenspunkte und fügen 0 Schaden zu.', 'Erstellt einen Geist der Stufe 1...12 (30...150 Sekunden Lebensdauer). Seine Angriffe entziehen 5...25 Lebenspunkte und fügen 0 Schaden zu.', 'Blutgesang (PvP)', 'Erstellt einen Geist der Stufe 1..8, der nach 30..150 Sekunden stirbt. Angriffe durch diesen Geist entziehen bis zu 5..25 Lebenspunkte und fügen 0 Schaden zu.', 'Erstellt einen Geist der Stufe 1...8 (30...150 Sekunden Lebensdauer). Seine Angriffe entziehen 5...25 Lebenspunkte und fügen 0 Schaden zu.');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1255, 'Fernweh', 'Erstellt einen Geist der Stufe 1..12. Jedes Mal, wenn ein Angriff dieses Geistes einen Gegner trifft, der sich nicht bewegt, wird dieser zu Boden geworfen und der Geist verliert 70..50 Lebenspunkte. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (30...60 Sekunden Lebensdauer). Seine Angriffe werfen Feinde, die sich nicht bewegen, zu Boden. <i>Bodenwurfkosten: Der Geist verliert 70...50 Lebenspunkte.</i>', 'Fernweh (PvP)', 'Erstellt einen Geist der Stufe 1..8. Jedes Mal, wenn ein Angriff dieses Geistes einen Gegner trifft, der sich nicht bewegt, wird dieser zu Boden geworfen und der Geist verliert 70..50 Lebenspunkte. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...8 (30...60 Sekunden Lebensdauer). Seine Angriffe werfen Feinde, die sich nicht bewegen, zu Boden. <i>Bodenwurfkosten: Der Geist verliert 70...50 Lebenspunkte.</i>'),
(1257, 'Geisterlichtwaffe', 'Während der nächsten 10 Sekunden erhält der Verbündete (bzw. Ihr selbst) pro Sekunde 1..15 Lebenspunkte sowie zusätzlich 1..15 Lebenspunkte pro Sekunde, wenn dieser Verbündete sich in Hörweite eines Geistes befindet.', '10 Sekunden: Heilt jede Sekunde um 1...15 Lebenspunkte. In Hörweite eines Geistes erhöht sich die Heilung um 1...15 Lebenspunkte pro Sekunde.', '', '', ''),
(1258, 'Grausame Waffe', 'Gebt dem Verbündeten (bzw. Euch selbst) 10..40 Sekunden lang eine Grausame Waffe. Solange der Träger mit keiner Verzauberung belegt ist, fügt die Waffe +5..15 Punkte Schaden zu.', '10...40 Sekunden: Angriffe richten +5...15 Punkte Schaden an. <i>Keine Wirkung, während der Verbündete (bzw. Ihr selbst) verzaubert ist.</i>', '', '', ''),
(1259, 'Gelenkte Waffe', '4..10 Sekunden lang können die Angriffe des Verbündeten (bzw. Eure eigenen) nicht geblockt werden.', '4...10 Sekunden: Angriffe können nicht geblockt werden', '', '', ''),
(1260, 'Sanftmut', 'Während der nächsten 5..30 Sekunden greifen der Gegner und alle Feinde in dem Bereich um 50% langsamer an.', 'Verhext Feinde im Bereich des Gegners (5...30 Sekunden). Die Betroffenen greifen um 50% langsamer an.', '', '', ''),
(1261, 'Kalte Rüstung', '10..25 Sekunden lang habt Ihr +10..40 Rüstung gegen physischen Schaden und könnt nicht in Brand gesetzt werden.', '10...25 Sekunden: Ihr habt +10...40 Rüstung gegen physischen Schaden und seid immun gegen Brennen.', '', '', ''),
(1262, 'Heilring', 'Heilt umstehende Kreaturen um 30..180 Lebenspunkte. Der Wirker wird nicht geheilt.', 'Heilt umstehende Verbündete und Feinde um 30...180 Lebenspunkte. <i>Der Wirker wird nicht geheilt.</i>', '', '', ''),
(1263, 'Leben verlängern', 'Belebt das berührte tote Gruppenmitglied mit 50% seiner Lebenspunkte und 5..20% seiner Energie wieder. Das Gruppenmitglied und alle Verbündeten in Hörweite werden um 55..130 Lebenspunkte geheilt.', 'Das Gruppenmitglied wird wiederbelebt (50% Lebenspunkte, 5...20% Energiepunkte). Heilt Verbündete in Hörweite um 55...130 Lebenspunkte.', '', '', ''),
(1264, 'Untergang', 'Der Gegner erleidet für jedes Binderitual, das Ihr habt und das wieder aufgeladen wird, 10..40 Punkte Blitzschaden (maximal 135).', 'Richtet für jedes Eurer sich aufladenden Binderituale 10...40 Punkte Blitzschaden an (maximal 135).', '', '', ''),
(1265, 'Segen des Ausübers', 'Heilt Verbündeten (bzw. Euch selbst) um 15..60 Punkte. Wenn dieser Verbündete unter den Auswirkungen eines Waffenzaubers leidet, heilt ihn Segen des Ausübers zusätzlich für 15..75 Lebenspunkte.', 'Heilt um 15...60 Lebenspunkte. Heilt um weitere 15...75 Lebenspunkte, falls der Verbündete (bzw. Ihr selbst) unter dem Einfluss eines Waffenzaubers steht.', '', '', ''),
(1266, 'Beruhigung', 'Erstellt einen Geist der Stufe 1..12. Alle Feinde in seiner Reichweite benötigen doppelt so lange, um Adrenalin aufzuladen. Dieser Geist stirbt nach 15..45 Sekunden.', 'Binderitual. Erstellt einen Geist der Stufe  1...8 (15...45 Sekunden Lebensdauer). Das Aufladen von Adrenalin dauert bei Feinden in Reichweite doppelt so lange.', 'Beruhigung (PvP)', 'Erstellt einen Geist der Stufe 1..8. Alle Feinde in seiner Reichweite benötigen doppelt so lange, um Adrenalin aufzuladen. Dieser Geist stirbt nach 15..45 Sekunden.', 'Binderitual. Erstellt einen Geist der Stufe  1...8 (15...45 Sekunden Lebensdauer). Das Aufladen von Adrenalin dauert bei Feinden in Reichweite doppelt so lange.'),
(1267, 'Wesentliche Waffe', '5..30 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine Wesentliche Waffe und +40..175 maximale Lebenspunkte.', '5...30 Sekunden: +40...175 maximale Lebenspunkte.', '', '', ''),
(1268, 'Waffe der Beschleunigung', '5..25 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine Waffe der Beschleunigung und Zauber und Binderituale werden um 33% schneller wieder aufgeladen.', '5...25 Sekunden: Zauber und Binderituale laden sich um 33% schneller wieder auf.', '', '', ''),
(1269, 'Siegel der Wut', 'Der Gegner erleidet 5..50 Punkte heiligen Schaden und +5..10 Punkte heiligen Schaden für jede Adrenalinfertigkeit des Gegners.', 'Richtet 5...50 Punkte heiligen Schaden an. Richtet für jede Adrenalinfertigkeit des Gegners zusätzlich 5...10 Punkte heiligen Schaden an.', '', '', ''),
(1333, 'Zustände ausweiten', 'Verteilt alle Zustände vom Gegner auf alle Feinde in der Nähe Eures Gegners. Die Dauer dieser Zustände ist um 5..100% verlängert (maximal 30 Sekunden).', 'Verteilt alle Zustände vom Gegner auf alle Feinde in der Nähe Eures Gegners. Die Dauer dieser Zustände ist um 5...100% verlängert (maximal 30 Sekunden).', '', '', ''),
(1334, 'Hypochondrie', 'Alle Zustände von allen Gegnern im Bereich werden auf den Zielgegner übertragen.', 'Alle Zustände von Gegnern im Bereich werden auf den Zielgegner übertragen.', '', '', ''),
(1335, 'Ableben des Verschwenders', '5 Sekunden lang wird der Gegner mit Ableben des Verschwenders verhext. Jede Sekunde fügt die Verhexung für jede Sekunde, die sie aktiv ist, dem Gegner und allen umstehenden Feinden 1..10 Punkte Schaden zu. Diese Verhexung endet vorzeitig, wenn der Gegner eine Fertigkeit einsetzt.', '(5 Sekunden.) Fügt jede Sekunde für jede Sekunde der Wirkungszeit dieser Verhexung dem Gegner und umstehenden Feinden 1...10 Punkte Schaden zu. <i>Endet vorzeitig, wenn der Gegner eine Fertigkeit einsetzt.</i>', '', '', ''),
(1336, 'Geisterschmerz', 'Der Gegner erleidet 15..75 Punkte Schaden. Alle gegnerischen herbeigerufenen Kreaturen im Bereich des Gegners erleiden 25..125 Punkte Schaden.', 'Richtet 15...75 Punkte Schaden an. Richtet bei allen gegnerischen herbeigerufenen Kreaturen im Bereich des Gegners 25...125 Punkte Schaden an.', 'Geisterschmerz (PvP)', 'Der Gegner erleidet 5..40 Punkte Schaden. Alle gegnerischen herbeigerufenen Kreaturen im Bereich des Gegners erleiden 25..125 Punkte Schaden.', 'Richtet 5...40 Punkte Schaden an. Richtet bei allen gegnerischen herbeigerufenen Kreaturen im Bereich des Gegners 25...125 Punkte Schaden an.'),
(1337, 'Täuschungen entziehen', 'Entfernt eine Mesmer-Verhexung vom Gegner. Wenn eine Verhexung auf diese Weise entfernt wurde, verliert der Gegner 1..5 Energiepunkte und Ihr erhaltet für jeden verlorenen Punkt 4 Energiepunkte.', 'Entfernt beim Gegner eine Mesmer-Verhexung. Lässt 1...5 Energiepunkte verlieren. Ihr erhaltet für jeden verlorenen Energiepunkt 4 Energiepunkte. <i>Keine Wirkung, falls keine Verhexung entfernt wird.</i>', '', '', ''),
(1338, 'Beharrliche Erinnerungen', 'Während der nächsten 5..20 Sekunden wird jeder von Euch gewirkte Zauber, der unterbrochen wird, sofort wieder aufgeladen.', '5...20 Sekunden: Eure unterbrochenen Zauber laden sich sofort wieder auf.', '', '', ''),
(1339, 'Symbole der Inspiration', '1..31 Sekunden lang wird diese Fertigkeit zur Elite-Fertigkeit des Gegners. Elite-Zauber, die Ihr wirkt, verwenden anstelle Eures normalen Attributs das Schnellwirkungs-Attribut.', '1...31 Sekunden: Diese Fertigkeit wird zur Elite-Fertigkeit des Gegners. Elite-Zauber, die Ihr wirkt, verwenden anstelle Eures normalen Attributs das Schnellwirkungs-Attribut.', '', '', ''),
(1340, 'Symbolische Schnelligkeit', '36..60 Sekunden lang verwenden alle Eure Siegel Euer Attribut Schnellwirkung statt der normalen Attribute.', '(36...60 Sekunden.) Eure Siegel verwenden das Attribut Schnellwirkung.', '', '', ''),
(1341, 'Frustration', 'Während der nächsten 5..20 Sekunden wirkt der Gegner seine Zauber um 50% langsamer und erleidet jedes Mal, wenn er unterbrochen wird, 5..50 Punkte Schaden. Wird eine Fertigkeit unterbrochen, richtet Frustration doppelten Schaden an.', '5...20 Sekunden: +50% zur Wirkzeit von Zaubern. Gegner erleidet jedes Mal 5...50 Punkte Schaden, wenn er unterbrochen wird. Richtet doppelten Schaden bei unterbrochenen Fertigkeiten an.', 'Frustration (PvP)', 'Während der nächsten 5..20 Sekunden wirkt der Gegner seine Zauber um 50% langsamer und erleidet jedes Mal, wenn er unterbrochen wird, 5..50 Punkte Schaden.', '5...20 Sekunden: +50% zur Wirkzeit von Zaubern. Gegner erleidet jedes Mal 5...50 Punkte Schaden, wenn er unterbrochen wird.'),
(1342, 'Reizen', 'Falls der Gegner eine Fertigkeit verwendet, werden er selbst sowie alle Gegner im Bereich unterbrochen und Ihr entzieht allen Gegnern im Bereich 0..5 Energiepunkte.', 'Unterbricht eine Fertigkeit. Unterbrechungswirkung: Unterbricht außerdem andere Gegner im Bereich und Ihr entzieht allen Gegnern im Bereich 0...5 Energiepunkte.', '', '', ''),
(1343, 'Äther-Phantom', '10 Sekunden lang unterliegt der Gegner einer Energiedegeneration von -1. Wenn diese Verhexung vorzeitig entfernt wird, verliert dieser Gegner 1..5 Energiepunkte.', '10 Sekunden: Verursacht -1 Energiedegeneration. Lässt 1...5 Energiepunkte verlieren, falls diese Verhexung vorzeitig endet.', '', '', ''),
(1344, 'Netz der Störung', 'Wenn der Gegner eine Fertigkeit verwendet, wird er dabei unterbrochen. 10 Sekunden lang ist der Gegner mit Netz der Störung verhext. Wenn diese Verhexung endet und der Gegner eine Fertigkeit verwendet, wird er dabei unterbrochen.', '10 Sekunden: Anfangswirkung: Unterbricht eine Fertigkeit. Wirkung am Ende: Unterbricht eine Fertigkeit.', 'Netz der Störung (PvP)', 'Wenn der Gegner eine Fertigkeit verwendet, wird er dabei unterbrochen. 10 Sekunden lang ist der Gegner mit Netz der Störung verhext. Wenn diese Verhexung endet und der Gegner eine Fertigkeit verwendet, wird er dabei unterbrochen.', '10 Sekunden: Anfangswirkung: Unterbricht eine Fertigkeit. Wirkung am Ende: Unterbricht eine Fertigkeit.'),
(1345, 'Rätsel des Verzauberers', '10 Sekunden lang ist die Wirkgeschwindigkeit von Verzauberungen des Gegners um 100..200% langsamer. Ist der Gegner beim Anwenden der Verhexung nicht verzaubert, erleiden er und alle umstehenden Feinde 10..100 Punkte Schaden.', 'Verlangsamt die Wirkungszeit von Verzauberungen um 100...200% (10 Sekunden). Anfangswirkung: Richtet beim Gegner und allen umstehenden Feinden 10...100 Punkte Schaden an, falls der Gegner nicht verzaubert ist.', 'Rätsel des Verzauberers (PvP)', '10 Sekunden lang ist die Wirkgeschwindigkeit von Verzauberungen des Gegners um 100..200% langsamer. Ist der Gegner beim Anwenden der Verhexung nicht verzaubert, erleiden er und alle umstehenden Feinde 10..100 Punkte Schaden.', 'Verlangsamt die Wirkungszeit von Verzauberungen um 100...200% (10 Sekunden). Anfangswirkung: Richtet beim Gegner und allen umstehenden Feinden 10...100 Punkte Schaden an, falls der Gegner nicht verzaubert ist.'),
(1346, 'Siegel der Illusionen', 'Euer nächster 1..3 Zauber verwenden anstelle ihrer normalen Attribute Euer Illusions-Attribut.', 'Euer nächster 1...3 Zauber verwenden Euer Attribut Illusionsmagie statt seines normalen Attributs.', '', '', ''),
(1347, 'Verzauberung entlassen', 'Entfernt eine Verzauberung vom Gegner. Wenn der Gegner verhext ist, wird diese Fertigkeit um 20..50% schneller wieder aufgeladen.', 'Entfernt eine Verzauberung vom Gegner. Um 20...50% schnellere Aufladung, falls der Gegner verhext ist.', '', '', ''),
(1348, 'Verhexungsfresser-Strudel', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). Beim Entfernen einer Verhexung auf diese Art erleiden Gegner in der Nähe dieses Verbündeten 30..90 Punkte Schaden und verlieren eine Verzauberung.', 'Entfernt beim Verbündeten (bzw. Euch selbst) eine Verhexung. Entfernungswirkung: Richtet 30...90 Punkte Schaden an und entfernt bei Feinden in der Nähe des Verbündeten (bzw. von Euch selbst) eine Verzauberung.', '', '', ''),
(1349, 'Spiegel der Entzauberung', 'Entfernt eine Verzauberung vom Gegner. Alle Mitglieder in der Gruppe dieses Gegners verlieren ebenfalls dieselbe Verzauberung.', 'Entfernt eine Verzauberung vom Gegner. Gruppenmitglieder des Gegners verlieren diese Verzauberung ebenfalls.', 'Spiegel der Entzauberung (PvP)', 'Entfernt eine Verzauberung vom Gegner. Gruppenmitglieder des Gegners verlieren diese Verzauberung ebenfalls.', 'Entfernt eine Verzauberung vom Gegner. Gruppenmitglieder des Gegners verlieren diese Verzauberung ebenfalls.'),
(1350, 'Simpler Diebstahl', 'Unterbricht die Aktion des Gegners. Falls diese Aktion eine Fertigkeit war, wird diese 5..20 Sekunden lang deaktiviert und Simpler Diebstahl durch die Fertigkeit ersetzt.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Falls eine Fertigkeit unterbrochen wurde, wird diese deaktiviert und Simpler Diebstahl wird zu dieser Fertigkeit (5...20 Sekunden).', '', '', ''),
(1351, 'Schlurfschreck beleben', 'Der nächste Kadaver wird als Schlurfschreck der Stufe 1..17 belebt. Wenn der Schlurfschreck stirbt, wird er durch einen Splitterschreck der Stufe 0..15 ersetzt, der bei jedem seiner Angriffe eine Blutung hervorruft.', 'Erstellt einen Schlurfschreck der Stufe 1...17. Wenn der Schlurfschreck stirbt, wird er durch einen Splitterschreck der Stufe 0...15 ersetzt, der bei jedem seiner Angriffe eine Blutung hervorruft. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(1352, 'Orden der Untoten', '5 Sekunden lang richten Eure Diener +3..16 Punkte Schaden an, Ihr verliert jedoch jedes Mal, wenn einer Eurer Diener einen erfolgreichen Angriff durchführt, 2% Eurer maximalen Lebenspunkte.', '5 Sekunden: Eure Diener richten +3...16 Schaden an. <i>Diener-Trefferwirkung: Ihr verliert 2% Eurer maximalen Lebenspunkte.</i>', '', '', ''),
(1353, 'Verwesendes Fleisch', 'Vernichtet den anvisierten belebten untoten Verbündeten. Alle Feinde in der Nähe dieser Kreatur erkranken 5..15 Sekunden lang.', 'Vernichtet einen Eurer untoten Diener. Verursacht bei Feinden in der Nähe des Dieners Zustand Krankheit (5...15 Sekunden).', '', '', ''),
(1354, 'Fest für die Toten', 'Vernichtet den anvisierten belebten untoten Verbündeten. Alle Eure anderen belebten untoten Verbündeten werden um 10..100 Lebenspunkte geheilt.', 'Vernichtet einen Eurer untoten Diener. Heilt Eure Diener um 10...100 Lebenspunkte.', '', '', ''),
(1355, 'Versplitterte Knochen', '30 Sekunden lang wird, wenn der von Euch anvisierte Diener stirbt, dieser durch einen Splitterschreck der Stufe 0..15 ersetzt, der bei jedem seiner Angriffe eine Blutung hervorruft.', '30 Sekunden: Wenn der untote Diener stirbt, wird er durch einen Splitterschreck der Stufe 0...15 ersetzt, der bei jedem seiner Angriffe eine Blutung hervorruft.', '', '', ''),
(1356, 'Erkrankung', '60 Sekunden lang leiden jedes Mal, wenn Ihr unter einem neuen Zustand leidet, alle Gegner in dem Bereich unter demselben Zustand und Ihr opfert 10..5% maximale Lebenspunkte.', '60 Sekunden: Immer wenn Ihr einen Zustand erhaltet, erhalten ihn auch alle Feinde in dem Bereich. <i>Ihr opfert jedes Mal 10...5% Eurer maximalen Lebenspunkte, wenn dies geschieht.</i>', '', '', ''),
(1358, 'Geschwürige Lungen', '10..25 Sekunden lang unterliegt der Gegner und alle in der Nähe befindlichen Feinde einer Lebenspunktdegeneration von -4, wenn sie bluten und erleiden jedes Mal, wenn sie einen Schrei oder Anfeuerungsruf anwenden, 3..15 Sekunden lang eine Blutung.', 'Verhext den Gegner und Feinde in der Nähe (10...25 Sekunden). Verursacht bei allen Betroffenen, die bluten, -4 Lebenspunktdegeneration. Verursacht jedes mal eine Blutung (3...15 Sekunden), wenn einer der Betroffenen einen Schrei oder Anfeuerungsruf benutzt.', '', '', ''),
(1359, 'Schmerz der Entzauberung', 'Der Gegner verliert 1..3 Verzauberungen. Wenn mindestens eine Verzauberung auf diese Weise entfernt wird, verlieren der Gegner und alle umstehenden Feinde 10..100 Lebenspunkte.', 'Der Gegner verliert 1...3 Verzauberungen. Entfernungswirkung: Der Gegner und alle umstehenden Feinde verlieren 10...100 Lebenspunkte.', '', '', ''),
(1360, 'Zeichen der Wut', 'Wann immer der Gegner in den nächsten 5 Sekunden von einem Verbündeten (bzw. Euch selbst) getroffen wird, erhält dieser Verbündete (bzw. Ihr selbst) 0..2 Adrenalinstöße. Wenn diese Verhexung endet, leidet der Gegner 1..15 Sekunden lang an Beschädigter Rüstung.', '(5 Sekunden.) Wann immer der Gegner von einem Verbündeten (bzw. Euch selbst) getroffen wird, erhält der Verbündete (bzw. Ihr selbst) 0...2 Adrenalinstöße. Wirkung am Ende: Verursacht Beschädigte Rüstung (1...15 Sekunden).', '', '', ''),
(1362, 'Verzauberung verderben', 'Entfernt eine Verzauberung vom Gegner. Beim Entfernen einer Verzauberung auf diese Weise erleidet der Gegner 10 Sekunden lang eine Lebenspunktdegeneration von -1..8.', 'Entfernt eine Verzauberung vom Gegner. Entfernungswirkung: -1...8 Lebenspunktdegeneration (10 Sekunden).', '', '', ''),
(1363, 'Siegel des Kummers', 'Der Gegner erleidet 15..75 Punkte Schaden. Wenn dieser Gegner sich in der Nähe eines Kadavers befindet oder einen toten Tiergefährten hat, wird diese Fertigkeit sofort aufgeladen.', 'Richtet 15...75 Punkte Schaden an. Lädt sich sofort wieder auf, falls der Gegner in der Nähe eines Kadavers ist oder einen toten Tiergefährten hat.', '', '', ''),
(1364, 'Siegel des Leidens', 'Ihr leidet 6 Sekunden lang unter einer Blutung. Die nächste Nekromanten-Fertigkeit, die einen Gegner trifft, verursacht 2..16 Sekunden lang eine Blutung.', 'Ihr leidet unter dem Zustand Blutung (6 Sekunden). Die nächste Nekromanten-Fertigkeit, die einen Gegner trifft, verursacht 2...16 Sekunden lang eine Blutung.', '', '', ''),
(1365, 'Siegel der verlorenen Seelen', 'Wenn die Lebenspunkte des Gegners unter 50% liegen, erhaltet Ihr 10..100 Lebenspunkte und 1..10 Energiepunkte.', 'Ihr erhaltet 10...100 Lebenspunkte und 1...10 Energiepunkte, falls der Gegner weniger als 50% seiner maximalen Lebenspunkte hat.', '', '', ''),
(1366, 'Brunnen der Dunkelheit', 'Der nächste Kadaver wird an seiner Position 5..50 Sekunden lang in einen Brunnen der Dunkelheit verwandelt. Verhexte Feinde innerhalb des Brunnens der Dunkelheit verfehlen 50% der Zeit ihr Ziel.', '(5...50 Sekunden.) Angriffe verhexter Feinde in diesem Brunnen haben eine 50%ige Wahrscheinlichkeit für einen Misserfolg. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(1367, 'Blendende Welle', 'Der Gegner erleidet 5..50 Punkte Blitzschaden. Der Gegner und alle umstehenden Feinde werden 3..8 Sekunden lang geblendet. Dieser Zauber hat eine Rüstungsdurchdringung von 25%. Wenn dieser Zauber einen angreifenden Gegner trifft, werden alle umstehenden Feinde ebenfalls getroffen und dieser Zauber richtet 50% mehr Schaden an.', 'Richtet 5...50 Punkte Blitzschaden an. Verursacht Blindheit beim Gegner und allen umstehenden Feinden (3...8 Sekunden). 25% Rüstungsdurchdringung. Falls der Gegner gerade angreift, werden auch umstehende Feinde getroffen und dieser Zauber richtet 50% mehr Schaden an.', '', '', ''),
(1368, 'Kühle Winde', 'Der anvisierte Gegner erleidet gemeinsam mit allen umstehenden Feinden 30..60 Punkte Kälteschaden. Für 10 Sekunden hält die nächste auf die verhexten Gegner gerichtete Wasser-Verhexung 25..100% länger an.', '(10 Sekunden.) Die nächste Wasser-Verhexung auf dem Gegner hält um 25...100% länger an. Anfangswirkung: 30...60 Punkte Kälteschaden. Trifft auch umstehende Gegner.', '', '', ''),
(1369, 'Blitzstrahl', 'Ein Blitzstrahl wird hinausgeschickt, der, falls er trifft, 5..50 Punkte Blitzschaden zufügt. Falls Blitzstrahl einen sich bewegenden Gegner trifft, erleidet dieser 5..50 Punkte zusätzlichen Blitzschaden. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Geschoss: Richtet 5...50 Punkte Blitzschaden an. Richtet zusätzlich 5...50 Punkte Blitzschaden an, falls der Gegner sich bewegt. 25% Rüstungsdurchdringung.', '', '', ''),
(1370, 'Eile des Sturm-Dschinn', '10..25 Sekunden lang bewegt Ihr Euch um 25% schneller. Ihr verliert pro Sekunde, die Ihr Euch bewegt, 1 Energiepunkt.', '10...25 Sekunden: Ihr bewegt Euch um 25% schneller. <i>Ihr verliert jede Sekunde 1 Energiepunkt, wenn Ihr Euch bewegt.</i>', '', '', ''),
(1371, 'Steinschläger', 'Während der nächsten 5..30 Sekunden wird sämtlicher Elementarschaden oder physische Schaden, den Ihr nehmt oder verursacht, in Erdschaden umgewandelt.', '5...30 Sekunden: Wandelt von Euch erlittenen oder angerichteten physischen und Elementarschaden in Erdschaden um.', '', '', ''),
(1372, 'Sandsturm', 'Am Standort des Gegners wird ein Sandsturm erzeugt. Während der nächsten 10 Sekunden erleiden Gegner in der Nähe 10..30 Punkte Erdschaden pro Sekunde. Angreifende Feinde erleiden pro Sekunde zusätzlich 10..30 Punkte Erdschaden.', 'Richtet jede Sekunde 10...30 Punkte Erdschaden an (10 Sekunden). Trifft Feinde in der Nähe der anfänglichen Position des Gegners. Richtet bei angreifenden Feinden zusätzlich jede Sekunde 10...30 Punkte Erdschaden an.', '', '', ''),
(1373, 'Steinmantel', '5..20 Sekunden lang habt Ihr und ein Verbündeter +1..30 Rüstung und seid gegen kritische Treffer immun. Wenn Ihr diesen Zauber wirkt, erleiden alle Feinde in Eurer Nähe und in der Nähe Eures Ziels 15..70 Punkte Erdschaden und sind 5..20 Sekunden lang geschwächt.', '(5...20 Sekunden). Ihr und ein Verbündeter habt +1...30 Rüstung und seid gegen kritische Treffer immun. Anfangswirkung: Feinde in Eurer Nähe und in der Nähe Eures Ziels erleiden 15...70 Punkte Erdschaden und leiden unter Schwäche (5...20 Sekunden).', '', '', ''),
(1374, 'Ebonfalke', 'Sendet ein Geschoss, das dem Gegner 10..85 Punkte Erdschaden zufügt und 5..15 Sekunden lang Schwäche verursacht, wenn es trifft.', 'Geschoss: Richtet 10...85 Punkte Erdschaden an und verursacht Zustand Schwäche (5...15 Sekunden).', '', '', ''),
(1375, 'Steinfleisch-Aura', '5..15 Sekunden lang wird der Schaden, den Ihr erleidet, um 1..31 Punkte reduziert und Ihr seid immun gegen kritische Angriffe.', '5...15 Sekunden: Reduziert von Euch erlittenen Schaden um 1...31 Punkte und Ihr seid gegen kritische Treffer immun.', '', '', ''),
(1376, 'Glyphe der Wiederherstellung', '15 Sekunden lang heilen Euch Eure nächsten 2 Zauber um 30..105 Lebenspunkte und Ihr erfahrt Heilung in Höhe von 150..400% der Energiekosten dieser Zauber.', '15 Sekunden: Eure nächsten 2 Zauber heilen Euch um 30...105 Lebenspunkte. Zusätzlich werdet Ihr um 150...400% der Energiekosten dieser Zauber geheilt.', '', '', ''),
(1377, 'Ätherprisma', '3 Sekunden lang wird der Schaden, den Ihr erleidet, um 75% reduziert. Wenn Ätherprisma endet, erhaltet Ihr 5..20 Energiepunkte.', '(3 Sekunden): Sämtlicher Schaden, den Ihr erleidet, wird um 75% reduziert. Wirkung am Ende: Erhaltet 5...20 Energiepunkte.', '', '', ''),
(1378, 'Meister der Magie', '1..61 Sekunden lang betragen alle Eure Elementarattribute 8..14 und Eure Elementarzauber geben Euch 30% ihrer Energiekosten zurück.', '1...61 Sekunden: Eure Elementarattribute werden betragen 8...14 und Elementarzauber geben Euch 30% ihrer Energiekosten zurück.', '', '', ''),
(1379, 'Glühender Blick', 'Der Gegner erleidet 5..50 Punkte Feuerschaden. Wenn er in Flammen steht, erhaltet Ihr 5 Energiepunkte sowie 1 Energiepunkt pro 2 Ränge in Energiespeicherung.', 'Geschoss: Fügt 5...50 Punkte Feuerschaden zu. Ihr erhaltet 5 Energiepunkte sowie 1 Energiepunkt pro 2 Ränge in Energiespeicherung, wenn der Gegner brennt.', '', '', ''),
(1380, 'Hitze der Savanne', 'An der Position des Gegners wird Hitze der Savanne erzeugt. In den nächsten 5 Sekunden erleiden alle Gegner in der Nähe 5..20 Punkte Feuerschaden pro Sekunde der Wirkzeit des Zaubers.', 'Richtet jede Sekunde ab dem Wirken des Spruchs (5 Sekunden) 5...20 Punkte Feuerschaden an. Trifft Feinde in der Nähe der ursprünglichen Position des Ziels.', 'Hitze der Savanne (PvP)', 'An der Position des Gegners wird Hitze der Savanne erzeugt. In den nächsten 5 Sekunden erleiden alle Gegner in der Nähe 5..20 Punkte Feuerschaden pro Sekunde der Wirkzeit des Zaubers.', 'Richtet jede Sekunde ab dem Wirken des Spruchs (5 Sekunden) 5...20 Punkte Feuerschaden an. Trifft Feinde in der Nähe der ursprünglichen Position des Ziels.'),
(1381, 'Eile des Flammen-Dschinn', 'Alle umstehenden Feinde erleiden 15..120 Punkte Feuerschaden. Ihr bewegt Euch 8..14 Sekunden lang um 25% schneller. Eile des Flammen-Dschinn wird um 50% schneller wieder aufgeladen, wenn ein Gegner von diesem Zauber getroffen wird.', '8...14 Sekunden: Ihr bewegt Euch um 25% schneller. Anfangswirkung: Richtet bei umstehenden Gegnern 15...120 Punkte Feuerschaden an. Falls Ihr einen Feind mit diesem Zauber schädigt, wird er um 50% schneller wieder aufgeladen.', '', '', ''),
(1382, 'Gefrierende Böe', 'Wenn der Gegner an den Auswirkungen einer Wassermagie-Verhexung leidet, erleidet er 20..80 Punkte Kälteschaden. Ansonsten bewegt er sich 1..5 Sekunden lang um 66% langsamer.', 'Richtet 20...80 Punkte Kälteschaden an, falls der Gegner mit Wassermagie verhext ist. Andernfalls bewegt sich der Gegner um 66% langsamer (1...5 Sekunden).', '', '', ''),
(1390, 'Intervention des Richters', '10 Sekunden lang wird das nächste Mal, wenn der Verbündete (oder Ihr selbst) Schaden erleidet, der tödlich wäre, dieser Schaden aufgehoben und ein Feind in der Nähe erleidet 30..180 Punkte heiligen Schaden.', '10 Sekunden: Hebt den nächsten Schaden auf, der zum Tod führen würde. Aufhebungswirkung: Richtet bei einem Feind in der Nähe des Verbündeten (bzw. Euch selbst) 30...180 Punkte heiligen Schaden an.', '', '', ''),
(1391, 'Unterstützender Geist', '5..23 Sekunden lang wird der Verbündete (bzw. Ihr selbst) jedes Mal, wenn er Schaden erleidet, während er am Boden liegt, um 5..35 Lebenspunkte geheilt.', '5...23 Sekunden: Heilt jedes Mal um 5...35 Lebenspunkte, wenn der Verbündete (bzw. Ihr selbst) Schaden erleidet, während er zu Boden geworfen ist.', '', '', ''),
(1392, 'Wachsame Heilung', '10 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) eine Lebenspunktregeneration von +1..4. Wenn diese Fertigkeit vorzeitig endet, wird dieser Verbündete um 30..120 Lebenspunkte geheilt.', '10 Sekunden: Der Verbündete (bzw. Ihr selbst) hat +1...4 Lebenspunktregeneration und heilt um 30...120 Lebenspunkte, falls diese Verzauberung vorzeitig endet.', '', '', ''),
(1393, 'Segen des Heilers', '10..55 Sekunden lang werden Heilgebet-Zauber um 50% schneller gewirkt und heilen um 50% mehr Lebenspunkte.', '10...55 Sekunden: Ihr könnt Eure Heilgebet-Zauber um 50% schneller wirken. Erhöht ihre Heilwirkung um 50%.', '', '', ''),
(1394, 'Verpflichtung des Heilers', 'Während Ihr diese Verzauberung aufrechterhaltet, heilen Eure Heilgebete-Zauber um 25% weniger Lebenspunkte, kosten dafür aber -1..3 Energiepunkte.', 'Eure Heilgebet-Zauber kosten 1...3 Energiepunkte weniger. <i>Verringert ihre Heilwirkung um 25%.</i>', '', '', ''),
(1395, 'Balthasars Pendel', '5..10 Sekunden lang wird das nächste Mal, wenn der Verbündete (bzw. Ihr selbst) durch einen Gegner zu Boden geworfen würde, stattdessen dieser Gegner zu Boden geworfen.', '5...10 Sekunden: Wirft den nächsten Feind zu Boden, der versucht, den Verbündeten (bzw. Euch selbst) zu Boden zu werfen.', '', '', ''),
(1396, 'Tröstende Worte', 'Der Verbündete (bzw. Ihr selbst) wird um 15..60 Lebenspunkte geheilt und zusätzlich um 15..45 Lebenspunkte, wenn er unter einem Zustand leidet.', 'Heilt um 15...60 Lebenspunkte. Heilt um weitere 15...45 Lebenspunkte, falls der Verbündete (bzw. Ihr selbst) einen Zustand hat.', '', '', ''),
(1397, 'Licht der Erlösung', 'Alle Gruppenmitglieder werden um 5..70 Lebenspunkte geheilt.', 'Heilt die gesamte Gruppe um 5...70 Lebenspunkte.', 'Licht der Erlösung (PvP)', 'Alle Gruppenmitglieder werden um 5..70 Lebenspunkte geheilt. All Eure Peinigungsgebete werden 20 Sekunden lang deaktiviert.', 'Heilt die gesamte Gruppe um 5...70 Lebenspunkte. <i>Deaktiviert Eure Peinigungsgebete (20 Sekunden).</i>'),
(1398, 'Geißelverzauberung', '30 Sekunden lang nimmt jedes Mal, wenn der Gegner das Ziel einer Verzauberung ist, der Wirker dieser Verzauberung 15..75 Punkte heiligen Schaden.', '30 Sekunden: Richtet bei jedem, der eine Verzauberung auf den Gegner wirkt, 15...75 Punkte Schaden an.', '', '', ''),
(1399, 'Schild der Absorption', '3..7 Sekunden lang ist Schaden, den der Verbündete (bzw. Ihr selbst) erleidet, für jeden Treffer, den dieser Verbündete erleidet, während er unter dieser Verzauberung steht, um 5 Punkte reduziert.', '3...7 Sekunden: Reduziert erlittenen Schaden jedes Mal um 5 Punkte, wenn der Verbündete (bzw. Ihr selbst) Schaden erleidet.', '', '', ''),
(1400, 'Umkehrung des Schadens', '8 Sekunden lang wird, wenn der Verbündete (bzw. Ihr selbst) das nächste Mal Schaden erleiden würde, dieser Schaden um 5..75 Punkte reduziert. Wenn der Schaden von einem Feind zugefügt wurde, wird ihm der verhinderte Schaden als heiliger Schaden zugefügt.', '8 Sekunden: Hebt den nächsten Schaden auf und trifft den Verursacher für dieselbe Punktzahl (maximal 5...75).', '', '', ''),
(1401, 'Genesungsberührung', 'Der berührte Verbündete (bzw. Ihr selbst) verliert zwei Zustände und wird für jeden Zustand, der auf diese Weise entfernt wird, um 15..60 Lebenspunkte geheilt.', 'Entfernt zwei Zustände. Heilt für jeden entfernten Zustand um 15...60 Lebenspunkte.', '', '', ''),
(1402, 'Kritischer Hieb', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff zu einem kritischen Treffer führt, wird der Gegner unterbrochen.', 'Richtet +5...20 Punkte Schaden an. Unterbricht eine Aktion, falls Ihr einen kritischen Treffer landet.', '', '', ''),
(1403, 'Qualvoller Hieb', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Euer Gegner an einer Tiefen Wunde leidet, unterbrecht Ihr seine Aktion.', 'Richtet +5...20 Punkte Schaden an. Unterbricht eine Aktion, falls Gegner eine Tiefe Wunde hat.', '', '', ''),
(1404, 'Dreschen', '1..15 Sekunden lang greift Ihr um 33% schneller an, bewegt Euch aber um 33% langsamer.', '1...15 Sekunden: Ihr greift um 33% schneller an. <i>Ihr bewegt Euch um 33% langsamer</i>', '', '', ''),
(1405, 'Angreifender Schlag', '1..10 Sekunden lang lauft Ihr um 33% schneller. Euer nächster erfolgreicher Nahkampftreffer fügt +10..40 Punkte Schaden zu und diese Haltung endet. Diese Haltung endet, wenn Ihr eine Fertigkeit verwendet.', '1...10 Sekunden: Ihr bewegt Euch um 33% schneller und richtet mit Eurem nächsten Nahkampftreffer +10...40 Punkte Schaden an. <i>Endet, wenn Ihr trefft oder eine Fertigkeit benutzt.</i>', '', '', ''),
(1406, 'Kopfstoß', 'Der berührte Gegner erleidet 40..100 Punkte Schaden. Ihr leidet 5..20 Sekunden lang unter Benommenheit.', 'Richtet 40...100 Punkte Schaden an. Ihr seid benommen (5...20 Sekunden).', '', '', ''),
(1407, 'Trost des Löwen', 'Alle Eure Siegel werden 12 Sekunden lang deaktiviert. Ihr werdet um 50..110 Lebenspunkte geheilt und erhaltet 0..3 Adrenalinstöße.', 'Ihr werdet um 50...110 Lebenspunkte geheilt und erhaltet 0...3 Adrenalinstöße <i>Deaktiviert Eure Siegel (12 Sekunden).</i>', '', '', ''),
(1408, 'Wut des Ntouka', 'Erhaltet 1..7 Adrenalinstöße. Während der nächsten 10 Sekunden wird jedes Mal, wenn Ihr eine Adrenalinfertigkeit verwendet, diese 5 Sekunden lang wieder aufgeladen.', 'Ihr erhaltet 1...7 Adrenalinstöße. <i>Adrenalinfertigkeiten laden sich nach Benutzung innerhalb von 5 Sekunden wieder auf (10 Sekunden).</i>', '', '', ''),
(1409, 'Mokele-Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu und Ihr erhaltet 2 Adrenalinstöße.', 'Richtet +5...20 Punkte Schaden an und Ihr erhaltet 2 Adrenalinstöße.', '', '', ''),
(1410, 'Anmaßender Schlag', 'Wenn dieser Angriff erfolgreich ist, verursacht Ihr +1..20 Punkte Schaden. Wenn der Gegner zu Boden geworfen wurde, leidet er 1..8 Sekunden an Benommenheit.', 'Richtet +1...20 Punkte Schaden an. Wenn der Gegner zu Boden geworfen wurde, leidet er 1...8 Sekunden an Benommenheit.', '', '', ''),
(1411, 'Siegel der Ausdauer', 'Ihr verfügt über +50..300 Lebenspunkte. Dieses Siegel endet, wenn Ihr einen erfolgreichen Angriff durchführt.', 'Ihr habt +50...300 maximale Lebenspunkte. <i>Endet, wenn ihr mit einem Angriff trefft.</i>', '', '', ''),
(1412, '"Ihr seid ganz allein!"', 'Wenn sich der Gegner nicht in der Nähe eines Verbündeten befindet, leidet er 8 Sekunden lang an einer Verkrüppelung und an Schwäche.', 'Verursacht Zustände Verkrüppelung und Schwäche (8 Sekunden). <i>Keine Wirkung, falls der Gegner in der Nähe eines seiner Verbündeten ist.</i>', '', '', ''),
(1413, 'Aggressiver Ausbruch', '2..10 Sekunden lang greift Ihr um 33% schneller an. Wenn diese Haltung endet, verliert Ihr Euer gesamtes Adrenalin.', '2...10 Sekunden: Ihr greift um 33% schneller an. <i>Wirkung am Ende: Ihr verliert alles Adrenalin.</i>', '', '', ''),
(1414, 'Wütender Angriff', '5..15 Sekunden lang bewegt Ihr Euch um 25% schneller. Wütender Angriff endet, wenn Ihr ein Ziel erfolgreich trefft. Dabei erhaltet Ihr 0..3 Adrenalinstöße, sofern Ihr mit einem Nahkampfangriff trefft.', '5...15 Sekunden: Ihr bewegt Euch um 25% schneller. Erhaltet bei Eurem nächsten Nahkampfangriff +0...3 Adrenalin. Endet nach Eurem nächsten Treffer.', '', '', ''),
(1415, 'Verkrüppelnder Hieb', 'Wenn dieser Angriff erfolgreich ist, wird der Gegner 5..15 Sekunden lang verkrüppelt und leidet 10..25 Sekunden lang an einer Blutung.', 'Verursacht Zustand Verkrüppelung (5...15 Sekunden) und Zustand Blutung (10...25 Sekunden).', '', '', ''),
(1416, 'Barbarischer Schnitt', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..30 Punkte Schaden zu. Wenn Ihr gegenwärtig keine Haltung einnehmt, fügt Ihr auch 5..15 Sekunden lang eine Blutung zu.', 'Richtet +5...30 Punkte Schaden an. Verursacht Zustand Blutung (5...15 Sekunden), falls Ihr keine Haltung einnehmt.', '', '', ''),
(1465, 'Vorbereiteter Schuss', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..25 Punkte Schaden zu. Wenn Ihr unter den Auswirkungen einer Vorbereitung steht, erhaltet Ihr 1..9 Energiepunkte.', 'Richtet +10...25 Punkte Schaden an. Ihr erhaltet 1...9 Energiepunkte, falls Ihr eine aktive Vorbereitung habt.', '', '', ''),
(1466, 'Brennender Pfeil', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..30 Punkte Schaden zu und verursacht 1..7 Sekunden lang Brennen.', 'Richtet +10...30 Punkte Schaden an. Verursacht Zustand Brennen (1...7 Sekunden).', '', '', ''),
(1467, 'Bogenschuss', 'Wenn dieser Pfeil trifft, fügt er +10..25 Punkte Schaden zu. Dieser Pfeil kann nicht geblockt werden, doch er bewegt sich um 50% langsamer.', 'Richtet +10...25 Punkte Schaden an. Kann nicht geblockt werden. <i>Dieser Pfeil bewegt sich um 50% langsamer.</i>', '', '', ''),
(1468, 'Gemeinsam zuschlagen', 'Euer Tiergefährte unternimmt einen Schattenschritt zur ausgewählten Kreatur. Wenn Ihr während der nächsten 30 Sekunden das nächste Mal mit einem Eurer Angriffe trefft, wird Euer Gegner 5..15 Sekunden lang verkrüppelt. Euer Tiergefährte verursacht während der nächsten 30 Sekunden mit seinem nächsten Angriff 5..15 Sekunden lang eine Blutung.', 'Euer Tiergefährte bewegt sich sofort zu Eurem Ziel hin und verursacht mit seinem nächsten Angriff Blutung (5...15 Sekunden). Das nächste Mal, wenn einer Eurer Angriffe trifft, verursacht Ihr Zustand Verkrüppelung (5...15 Sekunden).', '', '', ''),
(1469, 'Kreuzfeuer', 'Wenn dieser Angriff den Gegner trifft, fügt er +5..20 Punkte Schaden zu. Wenn dieser Gegner sich in der Nähe Eurer Verbündeten befindet, ist ein Blocken bei dem Angriff nicht möglich.', 'Richtet +10...20 Punkte Schaden an. Kann nicht geblockt werden, falls der Gegner in der Nähe eines Verbündeten ist.', '', '', ''),
(1470, 'Stachelpfeile', '24 Sekunden lang führen Eure Pfeile zu einer Blutung, die 3..15 Sekunden lang anhält. Beim Aktivieren dieser Fertigkeit habt Ihr -40 Rüstung.', '24 Sekunden: Eure Pfeile verursachen den Zustand Blutung (3...15 Sekunden). <i>Beim Aktivieren dieser Fertigkeit habt ihr -40 Rüstung.</i>', '', '', ''),
(1471, 'Fokus des Plünderers', '10 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr einen Feind trefft, der an einem Zustand leidet, 3..12 Energiepunkte.', '10 Sekunden: Ihr erhaltet 3...12 Energiepunkte, wenn ihr einen Feind trefft, der an einem Zustand leidet.', '', '', ''),
(1472, 'Giftigkeit', 'Erstellt einen Geist der Stufe 1..10. Vergiftete oder erkrankte Kreaturen in seiner Reichweite erleiden eine Lebenspunktdegeneration von -2. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Vergiftete und kranke Kreaturen in Reichweite haben -2 Lebenspunktdegeneration.', '', '', ''),
(1473, 'Treibsand', 'Erstellt einen Geist der Stufe 1..10. Alle Kreaturen in seiner Reichweite, die keine Geister sind, verlieren jedes Mal, wenn sie angreifen oder eine Fertigkeit verwenden, 1 Energiepunkt. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...90 Sekunden Lebensdauer). Kreaturen verlieren jedes Mal 1 Energiepunkt, wenn sie angreifen oder eine Fertigkeit einsetzen. Keine Wirkung auf Geister.', '', '', ''),
(1474, 'Umarmung des Sturms', '10 Sekunden lang bewegt Ihr Euch um 25% schneller. Diese Haltung wird jedes Mal, wenn Ihr Elementarschaden erleidet, erneuert.', '10 Sekunden: Ihr bewegt Euch um 25% schneller. Erneuerung: Jedes Mal, wenn Ihr Elementarschaden erleidet.', '', '', ''),
(1475, 'Geschwindigkeit des Fallenstellers', '5..30 Sekunden lang werden Eure Fallen um 25% schneller aufgeladen und um 25% schneller aktiviert. Diese Haltung endet, wenn Ihr mit einem Angriff trefft.', '5...30 Sekunden: Eure Fallen laden such um 25% schneller auf und werden um 25% schneller aktiviert. <i>Endet, wenn Ihr mit einem Angriff trefft.</i>', '', '', ''),
(1476, 'Stolperdraht', 'Wird Stolperdraht aktiviert, erleiden alle Feinde in der Nähe 5..20 Punkte Stichschaden. Alle verkrüppelten Feinde werden zu Boden geworfen. Stolperdraht endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Richtet bei Feinden in der Nähe 5...20 Punkte Stichschaden an. Wirft verkrüppelte Feinde zu Boden. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(1478, 'Erneuernde Welle', '8 Sekunden lang erleidet der Gegner 2..12 Punkte Schaden pro Sekunde. Wenn diese Verhexung endet, erhaltet Ihr 1..8 Energiepunkte.', '(8 Sekunden.) Richtet 2...12 Punkte Schaden pro Sekunde an. Wirkung am Ende: Erhaltet 1...8 Energiepunkte.', '', '', ''),
(1479, 'Opfer des Geistes', 'Ihr erhaltet 8..17 Energiepunkte. Wenn sich Geister in Hörweite befinden, opfert Ihr keine Lebenspunkte.', 'Ihr erhaltet 8...17 Energiepunkte. Ihr opfert keine Lebenspunkte, falls Geister in Hörweite sind.', '', '', ''),
(1480, 'Geschenk des Geistes', 'Jedes Mal, wenn Ihr während der nächsten 60 Sekunden eine Kreatur erschafft, erhalten alle Verbündeten in der Nähe dieser Kreatur 5..50 Lebenspunkte und verlieren 1 Zustand.', '60 Sekunden: Jedes Mal, wenn Ihr eine Kreatur erstellt, erhalten alle Verbündeten in der Nähe 5...50 Lebenspunkte und verlieren einen Zustand.', '', '', ''),
(1481, 'Todespakt-Siegel', 'Belebt das Gruppenmitglied mit Euren aktuellen Lebenspunkten und 15..100% maximaler Energie wieder. Wenn dieser Verbündete das nächste mal innerhalb von 120 Sekunden stirbt, sterbt Ihr auch.', 'Das Gruppenmitglied wird wiederbelebt (Eure aktuellen Lebenspunkte, 15...100% maximale Energie). <i>120 Sekunden: Ihr sterbt, wenn dieses Gruppenmitglied das nächste Mal stirbt.</i>', 'Todespakt-Siegel (PvP)', 'Belebt das Gruppenmitglied mit Euren aktuellen Lebenspunkten und 15..100% maximaler Energie wieder. Wenn dieser Verbündete das nächste mal innerhalb von 120 Sekunden stirbt, sterbt Ihr auch.', 'Das Gruppenmitglied wird wiederbelebt (Eure aktuellen Lebenspunkte, 15...100% maximale Energie). <i>120 Sekunden: Ihr sterbt, wenn dieses Gruppenmitglied das nächste Mal stirbt.</i>'),
(1482, 'Essenz zurückfordern', 'Alle Eure Geister sterben. Ihr erhaltet 5..20 Energiepunkte und all Eure Binderituale werden wieder aufgeladen, wenn ein Geist auf diese Weise stirbt.', 'All Eure Geister sterben. Wenn ein Geist auf diese Weise stirbt, erhaltet Ihr 5...20 Energiepunkte und all Eure Binderituale werden wieder aufgeladen.', '', '', ''),
(1483, 'Bannstoß', 'Wenn dieser Angriff erfolgreich ist, fügt er 10..60 Punkte heiligen Schaden zu. Fügt herbeigerufenen Kreaturen doppelten Schaden zu.', 'Richtet 10...60 Punkte heiligen Schaden an. Fügt herbeigerufenen Kreaturen doppelten Schaden zu.', 'Bannstoß (PvP)', 'Wenn dieser Angriff erfolgreich ist, fügt er 10..60 Punkte heiligen Schaden zu. Fügt herbeigerufenen Kreaturen doppelten Schaden zu.', 'Richtet 10...60 Punkte heiligen Schaden an. Fügt herbeigerufenen Kreaturen doppelten Schaden zu.'),
(1484, 'Mystischer Schwung', 'Wenn dieser Angriff erfolgreich ist, verursacht Ihr +3..12 Punkte Schaden. Wenn ihr verzaubert seid, fügt dieser Angriff zusätzlich +3..12 Punkte Schaden zu.', 'Richtet +3...12 Punkte Schaden an. Richtet zusätzlich +3...12 Punkte Schaden an, wenn Ihr verzaubert seid.', '', '', ''),
(1485, 'Angriff des Eremiten', 'Entfernt eine Derwisch-Verzauberung. Wenn dieser Angriff erfolgreich ist, fügt er +1..10 Punkte Schaden zu. Wenn eine Verzauberung entfernt wird, fügt Ihr zusätzlich +1..10 Schaden zu und trefft, anstatt nur den Gegner, alle Euch umstehenden Feinde.', 'Richtet +1...10 Punkte Schaden an und entfernt eine Derwisch-Verzauberung. Entfernungswirkung: Richtet +1...10 Punkte Schaden an und trifft alle umstehenden Feinde.', '', '', ''),
(1486, 'Unreinheiten ausnutzen', 'Wenn dieser Angriff erfolgreich ist, verursacht Ihr +3..15 Punkte Schaden. Jeder getroffene Feind verliert 1 Zustand. Für jeden Feind, der einen Zustand verliert, erleiden alle in der Nähe des Gegners befindlichen Feinde 10..40 Punkte heiligen Schaden.', 'Richtet +3...15 Punkte Schaden an. Getroffene Feinde verlieren 1 Zustand. Entfernungseffekt: In der Nähe der getroffenen Gegner befindliche Feinde erleiden 10...40 Punkte heiligen Schaden.', '', '', ''),
(1487, 'Doppelter Mondschwung', 'Ihr verliert 1 Derwisch-Verzauberung und erhaltet 10..50 Lebenspunkte. Wenn eine Verzauberung auf diese Weise entfernt wird, könnt ihr nicht geblockt werden, trefft zweimal und erhaltet zusätzlich 10..70 Lebenspunkte.', 'Entfernt 1 Eurer Derwisch-Verzauberung. Ihr erhaltet 10...50 Lebenspunkte. Entfernungseffekt: Unblockbar, trifft zweimal und gibt 10...70 Lebenspunkte zusätzlich.', 'Doppelter Mondschwung (PvP)', 'Ihr verliert 1 Derwisch-Verzauberung und erhaltet 10..20 Lebenspunkte. Wenn eine Verzauberung auf diese Weise entfernt wird, könnt ihr nicht geblockt werden, trefft zweimal, wobei jeder Treffer 25% weniger Schaden als sonst zufügt, und erhaltet zusätzlich 10..30 Lebenspunkte.', 'Entfernt 1 Eurer Derwisch-Verzauberung. Ihr erhaltet 10...20 Lebenspunkte. Entfernungseffekt: Unblockbar, zwei Angriffe mit je 25% weniger Schaden pro Treffer und 10...30 Lebenspunkte zusätzlich.'),
(1488, 'Siegreicher Schwung', 'Für jeden Gegner, den Ihr mit diesem Angriff trefft und dessen Lebenspunkte niedriger als Eure eigenen sind, erhaltet Ihr 30..80 Lebenspunkte. Dieser Angriff fügt danach +5..25 Punkte Schaden zu.', 'Richtet +5...25 Punkte Schaden an. Ihr erhaltet 30...80 Lebenspunkte für jeden getroffenen Gegner, der weniger Lebenspunkte hat als Ihr.', '', '', ''),
(1489, 'Unwiderstehlicher Schwung', 'Richtet +3..15 Punkte Schaden an und verliert 1 Derwisch-Verzauberung. Wenn Ihr eine Verzauberung auf diese Weise verliert, kann Unwiderstehlicher Schwung nicht geblockt werden, eine Haltung wird entfernt und der Angriff richtet zusätzlich +3..15 Punkte Schaden an.', 'Richtet +3...15 Punkte Schaden an. Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Unblockbar, entfernt eine Haltung, richtet zusätzlich +3...15 Punkte Schaden an.', 'Unwiderstehlicher Schwung (PvP)', 'Richtet +3..15 Punkte Schaden an und verliert 1 Derwisch-Verzauberung. Wenn Ihr eine Verzauberung auf diese Weise verliert, kann Unwiderstehlicher Schwung nicht geblockt werden und entfernt eine Haltung.', 'Richtet +3...15 Punkte Schaden an. Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Unblockbar, entfernt eine Haltung.'),
(1490, 'Frommer Angriff', 'Entfernt 1 Derwisch-Verzauberung. Wenn der Angriff erfolgreich ist, fügt er +10..20 Punkte Schaden zu. Wenn eine Derwisch-Verzauberung entfernt wurde, lädt diese Fertigkeit 75% schneller wieder auf und alle Euch umstehenden Feinde erleiden 10..30 Punkte Schaden.', 'Richtet +10...20 Punkte Schaden an. Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Fertigkeit lädt 75% schneller wieder auf und alle umstehenden Feinde erleiden 10...30 Punkte Schaden.', 'Frommer Angriff (PvP)', 'Ihr entfernt 1 Derwisch-Verzauberung. Wenn eine Derwisch-Verzauberung entfernt wurde, lädt diese Fertigkeit 75% schneller wieder auf und alle Euch umstehenden Feinde erleiden 10..30 Punkte Schaden.', ''),
(1491, 'Mystischer Wirbelsturm', 'Richtet bei allen Feinden in der Nähe 10..60 Punkte Kälteschaden an. Wenn Ihr verzaubert seid, verursacht dieser Zauber zusätzlich 10..60 Punkte Kälteschaden.', 'Richtet bei allen Feinden in der Nähe 10...60 Punkte Kälteschaden an. Richtet 10...60 Punkte Kälteschaden zusätzlich an, wenn Ihr verzaubert seid.', '', '', ''),
(1493, 'Grenths Finger', 'Alle in der Nähe befindlichen Feinde erleiden 10..40 Punkte Kälteschaden. 30 Sekunden lang fügen Eure Angriffe Kälteschaden zu. Wenn diese Verzauberung endet, übertragt ihr 1..2 Zustände an alle umstehenden Feinde.', '(30 Sekunden.) Eure Angriffe fügen Kälteschaden zu. Anfangswirkung: In der Nähe befindliche Feinde erleiden 10...40 Punkte Kälteschaden. Wirkung am Ende: Übertragt 1...2 Zustände auf alle umstehenden Feinde.', '', '', ''),
(1495, 'Dornenaura', 'Alle Feinde in der Nähe leiden 5..15 Sekunden an einer Blutung. 30 Sekunden lang bewirkt diese Verzauberung nichts. Wenn diese Verzauberung endet, werden alle in der Nähe befindliche Feinde 3..8 Sekunden lang verkrüppelt.', '(30 Sekunden.) Anfangswirkung: Verursacht bei in der Nähe befindlichen Feinden den Zustand Blutung (5...15 Sekunden). Wirkung am Ende: Verursacht bei in der Nähe befindlichen Feinden den Zustand Verkrüppelung (3...8 Sekunden).', 'Dornenaura (PvP)', 'Alle Feinde in der Nähe leiden 5..15 Sekunden an einer Blutung. 30 Sekunden lang bewirkt diese Verzauberung nichts. Wenn diese Verzauberung endet, werden alle in der Nähe befindliche Feinde 3..8 Sekunden lang verkrüppelt.', '(30 Sekunden.) Anfangswirkung: Verursacht bei in der Nähe befindlichen Feinden den Zustand Blutung (5...15 Sekunden). Wirkung am Ende: Verursacht bei in der Nähe befindlichen Feinden den Zustand Verkrüppelung (3...8 Sekunden).'),
(1496, 'Balthasars Wut', 'Alle in der Nähe befindlichen Feinde werden 1..3 Sekunden lang in Brand gesetzt und ihr werdet 20 Sekunden lang verzaubert. Wenn diese Verzauberung endet, erhaltet Ihr 1..2 Adrenalinstöße, wenn sich Feinde in Hörweite befinden.', '(20 Sekunden.) Anfangswirkung: Verursacht bei in der Nähe befindlichen Feinden Brennen (1...3 Sekunden). Wirkung am Ende: Ihr erhaltet 1...2 Adrenalinstöße, wenn sich Feinde in Hörweite befinden.', '', '', ''),
(1497, 'Staubdecke', 'Alle in der Nähe befindlichen Feinde erleiden 10..40 Punkte Erdschaden. 30 Sekunden lang fügen Eure Angriffe Erdschaden zu. Wenn diese Verzauberung endet, werden alle in der Nähe befindlichen Feinde 1..4 Sekunden lang geblendet.', '(30 Sekunden.) Eure Angriffe fügen Erdschaden zu. Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 10...40 Punkte Erdschaden an. Wirkung am Ende: Verursacht bei in der Nähe befindlichen Feinden Zustand Blindheit (1...4 Sekunden).', 'Staubdecke (PvP)', 'Alle in der Nähe befindlichen Feinde erleiden 10..40 Punkte Erdschaden. 30 Sekunden lang fügen Eure Angriffe Erdschaden zu. Wenn diese Verzauberung endet, werden alle in der Nähe befindlichen Feinde 1..4 Sekunden lang geblendet.', '(30 Sekunden.) Eure Angriffe fügen Erdschaden zu. Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 10...40 Punkte Erdschaden an. Wirkung am Ende: Verursacht bei in der Nähe befindlichen Feinden Zustand Blindheit (1...4 Sekunden).'),
(1498, 'Umwerfende Gewalt', 'Alle in der Nähe befindlichen Feinde erleiden 10..40 Punkte Erdschaden. 30 Sekunden lang fügen Eure Angriffe Erdschaden zu. Wenn diese Verzauberung endet, leiden alle in der Nähe befindlichen Feinde 1..10 Sekunden lang unter "Beschädigte Rüstung".', '(30 Sekunden.) Eure Angriffe fügen Erdschaden zu. Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 10...40 Punkte Erdschaden an. Wirkung am Ende: Fügt in der Nähe befindlichen Feinden "Beschädigte Rüstung" (1...10 Sekunden) zu.', '', '', ''),
(1499, 'Fromme Erneuerung', '8 Sekunden lang geschieht nichts. Wenn diese Verzauberung endet, wird Fromme Erneuerung wieder aufgeladen und Ihr erhaltet 0..5 Energiepunkte und 0..30 Lebenspunkte.', '(8 Sekunden.) Wirkung am Ende: Lädt wieder auf und Ihr erhaltet 0...5 Energie- und 0...30 Lebenspunkte.', '', '', ''),
(1500, 'Trugbild-Umhang', '1..7 Sekunden lang habt Ihr eine 40..80%ige Chance Angriffe zu blocken. Wenn Ihr Verzauberung wirkt, erleiden alle Feinde in der Nähe 10..40 Erdschaden.', '(1...7 Sekunden.) Ihr habt eine 40...80%ige Chance zu blocken. Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 10...40 Punkte Erdschaden an.', '', '', ''),
(1502, 'Arkaner Eifer', '10 Sekunden lang bekommt Ihr jedes Mal, wenn Ihr einen Zauber wirkt, für jede Verzauberung, die auf Euch liegt, 1 Energiepunkt (maximal 1..7 Energiepunkte).', '(10 Sekunden.) Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr für jede Verzauberung auf Euch 1 Energiepunkt (maximal 1...7).', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1503, 'Mystische Vitalität', '20 Sekunden lang erhaltet Ihr jedes Mal, wenn einer Eurer Angriffe erfolgreich ist, für jede Verzauberung, die auf Euch liegt, 3..15 Lebenspunkte (maximal 25 Lebenspunkte).', '20 Sekunden: Jedes Mal, wenn ein Angriff von Euch trifft, erhaltet Ihr für jede Verzauberung auf Euch 3...15 Lebenspunkte (maximal 25).', '', '', ''),
(1504, 'Wachsame Intervention', '60 Sekunden lang wird der Verbündete (bzw. Ihr selbst) das nächste Mal, wenn er Schaden erleidet und seine Lebenspunkte dadurch unter 25% sinken, um 50..200 Lebenspunkte geheilt.', '60 Sekunden: Heilt um 50...200 Lebenspunkte, wenn Schaden die Lebenspunkte des Verbündeten (bzw. von Euch selbst) das nächste Mal unter 25% seiner maximalen Lebenspunkte senkt.', '', '', ''),
(1505, 'Gelübde der Frömmigkeit', '20 Sekunden lang habt Ihr +24 Rüstung und eine Lebenspunktregeneration von +1..4. Das Gelübde der Frömmigkeit erneuert sich jedes Mal, wenn eine Verzauberung, mit der Ihr belegt seid, endet.', '(20 Sekunden.) +24 Rüstung und +1...4 Lebenspunktregeneration. Erneuerung: jedes Mal, wenn eine Verzauberung, mit der Ihr belegt seid, endet.', '', '', ''),
(1506, 'Wesentliche Wohltat', '20 Sekunden lang habt Ihr +40..100 maximale Lebenspunkte. Wenn diese Verzauberung endet, erhaltet Ihr 75..200 Lebenspunkte.', '20 Sekunden: Ihr habt +40...100 maximale Lebenspunkte. Wirkung am Ende: Erhaltet 75...200 Lebenspunkte.', '', '', ''),
(1507, 'Herz der heiligen Flamme', 'Alle in der Nähe befindliche Gegner erleiden 5..30 Punkte heiligen Schaden. 30 Sekunden lang fügen Eure Angriffe Sakral-Schaden zu. Wenn diese Verzauberung endet, werden alle in der Nähe befindlichen Feinde 2..5 Sekunden lang in Brand gesetzt.', '(30 Sekunden.) Eure Angriffe fügen Sakral-Schaden zu. Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 5...30 Punkte heiligen Schaden an. Wirkung am Ende: Verursacht bei in der Nähe befindlichen Feinden Zustand Brennen (2...5 Sekunden).', '', '', ''),
(1508, 'Verzauberungen ausweiten', '10 Sekunden lang wirkt Eure nächste Derwisch-Verzauberung 10..150% länger.', '(10 Sekunden<sub>sic</sub>) Eure nächste Derwisch-Verzauberung wirkt 10...150% länger.', '', '', ''),
(1509, 'Vertrauensvolle Intervention', 'Wenn Schaden Eure Lebenspunkte auf unter 50% fallen lässt, endet Vertrauensvolle Intervention. Wenn Vertrauensvolle Intervention endet, heilt Ihr Euch um 30..150 Lebenspunkte.', 'Ihr heilt Euch um 30...150 Lebenspunkte, wenn Eure Lebenspunkte das nächste Mal durch Schaden unter 50% Eurer maximalen Lebenspunkte gebracht werden.', '', '', ''),
(1510, 'Sandscherben', '30 Sekunden lang fügen Eure nächsten 1..5 Treffer mit einer Sense allen umstehenden Feinden 10..60 Punkte Erdschaden zu.', '(30 Sekunden.) Richtet jedes Mal, wenn Ihr mit einer Sense trefft, bei allen umstehenden Feinden 10...60 Punkte Erdschaden an. Endet nach 1...5 Treffern.', '', '', ''),
(1512, 'Lyssas Eile', '3..15 Sekunden lang werden Eure Derwisch-Verzauberungen um 33% schneller wieder aufgeladen. Wenn Ihr diese Verzauberung aktiviert, unterbrecht Ihr die Aktion aller umstehenden Feinde. Wenn diese Verzauberung endet, unterbrecht Ihr die Aktion aller umstehenden Feinde. Bei Windgebeten von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.', '(3...15 Sekunden.) Eure Derwisch-Verzauberungen werden um 33% schneller wieder aufgeladen. Anfangswirkung: Unterbricht die Aktion aller umstehenden Feinde. Wirkung am Ende: Unterbricht die Aktion aller umstehenden Feinde. <i>(Windgebete von 5 oder höher, sonst 50%ige Wahrscheinlichkeit für einen Misserfolg.)</i>', 'Lyssas Eile (PvP)', '3..15 Sekunden lang werden Eure Derwisch-Verzauberungen um 33% schneller wieder aufgeladen. Wenn Ihr diese Verzauberung aktiviert, unterbrecht Ihr die Aktion aller umstehenden Feinde. Wenn diese Verzauberung endet, unterbrecht Ihr die Aktion aller umstehenden Feinde. Bei Windgebeten von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.', '(3...15 Sekunden.) Eure Derwisch-Verzauberungen werden um 33% schneller wieder aufgeladen. Anfangswirkung: Unterbricht die Aktion aller umstehenden Feinde. Wirkung am Ende: Unterbricht die Aktion aller umstehenden Feinde. <i>(Windgebete von 5 oder höher, sonst 50%ige Wahrscheinlichkeit für einen Misserfolg.)</i>'),
(1513, 'Lenkende Hände', '20 Sekunden lang ist ein Blocken bei Euren nächsten 0..3 Angriffen nicht möglich. Bei Aktivierung entfernt diese Fertigkeit den Zustand "Blindheit".', '(20 Sekunden.) Eure nächsten 0...3 Angriffe können nicht geblockt werden. Anfangswirkung: Entfernt "Blindheit".', 'Lenkende Hände (PvP)', '20 Sekunden lang ist ein Blocken bei Euren nächsten 0..3 Angriffe nicht möglich. Bei Aktivierung entfernt diese Fertigkeit den Zustand "Blindheit".', '(20 Sekunden.) Eure nächsten 0...3 Angriffe können nicht geblockt werden. Anfangswirkung: Entfernt "Blindheit".'),
(1514, 'Flüchtige Stabilität', 'Während der nächsten 2..6 Sekunden könnt Ihr nicht zu Boden geworfen werden und bewegt Euch um 25% schneller. Diese Verzauberung endet vorzeitig, wenn sie verhindert, dass Ihr zu Boden geworfen werdet.', '(2...6 Sekunden.) Ihr könnt nicht zu Boden geworfen werden und bewegt Euch um 25% schneller. Endet, wenn sie verhindert, dass Ihr zu Boden geworfen werdet.', '', '', ''),
(1515, 'Rüstung der Heiligkeit', 'Alle umstehenden Feinde leiden 5..15 Sekunden lang unter Schwäche. 15 Sekunden lang erleidet Ihr 5..20 weniger Schaden von Feinden, die an einem Zustand leiden.', 'Verursacht Zustand Schwäche bei allen umstehenden Feinden (5...15 Sekunden). Feinde mit einem Zustand richten bei Euch 5...20 Punkte weniger Schaden an (15 Sekunden).', '', '', ''),
(1516, 'Mystische Regeneration', '5..20 Sekunden lang habt Ihr für jede Verzauberung auf Euch (maximal 8), +1..4 Lebenspunktregeneration.', '5...20 Sekunden: Ihr habt für jede Verzauberung auf Euch (maximal 8) +1...4 Lebenspunktregeneration.', 'Mystische Regeneration (PvP)', '5..20 Sekunden lang habt Ihr für jede Verzauberung auf Euch (maximal 3), +1..4 Lebenspunktregeneration.', '5...20 Sekunden: Ihr habt für jede Verzauberung auf Euch (maximal 3) +1...4 Lebenspunktregeneration.'),
(1517, 'Schweigegelübde', '5..10 Sekunden lang könnt Ihr nicht zum Ziel von Zaubern werden und selbst keine Zauber wirken.', '(5...10 Sekunden.) Ihr könnt nicht Ziel von Zaubern werden. <i>Ihr könnt keine Zauber wirken.</i>', '', '', ''),
(1518, 'Balthasars Avatar', '10..90 Sekunden lang erhaltet Ihr +20 Rüstung gegen körperlichen Schaden, gewinnt Adrenalin um 25% schneller, Eure Angriffe fügen Sakral-Schaden zu und immer wenn Ihr eine Derwisch-Verzauberung verliert, werden Feinde in der Nähe 1..3 Sekunden lang in Brand gesetzt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Ihr erhaltet +20 Rüstung gegen körperlichen Schaden, gewinnt Adrenalin um 25% schneller, Eure Angriffe fügen Sakral-Schaden zu und immer wenn Ihr eine Derwisch-Verzauberung verliert, werden Feinde in der Nähe 1...3 Sekunden lang in Brand gesetzt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '', '', ''),
(1519, 'Dwaynas Avatar', '10..90 Sekunden lang fügen Eure Angriffe Sakral-Schaden zu. Immer wenn Ihr eine Derwisch-Angriffsfertigkeit einsetzt, verliert Ihr 1 Verhexung. Immer wenn Ihr eine Derwisch-Verzauberung verliert, werden alle Verbündeten in Hörweite um 5..50 Lebenspunkte geheilt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Ihr richtet heiligen Schaden an. Immer wenn Ihr eine Derwisch-Verzauberung wirkt, verliert Ihr 1 Verhexung. Immer wenn Ihr eine Derwisch-Verzauberung verliert, werden Verbündete in Hörweite um 5...50 Lebenspunkte geheilt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', 'Dwaynas Avatar (PvP)', '10..90 Sekunden lang fügen Eure Angriffe Sakral-Schaden zu. Immer wenn Ihr eine Derwisch-Angriffsfertigkeit einsetzt, verliert Ihr 1 Verhexung. Immer wenn Ihr eine Derwisch-Verzauberung verliert, werdet Ihr um 5..50 Lebenspunkte geheilt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Ihr richtet heiligen Schaden an. Immer wenn Ihr eine Derwisch-Verzauberung wirkt, verliert Ihr 1 Verhexung. Immer wenn Ihr eine Derwisch-Verzauberung verliert, werdet Ihr um 5...50 Lebenspunkte geheilt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.'),
(1520, 'Grenths Avatar', '10..90 Sekunden lang fügen Eure Sensenangriffe Dunkelschaden zu und stehlen 0..12 Lebenspunkte. Ihr seid immun gegen Krankheit und fügt allen umstehenden Feinden 3 Sekunden lang Krankheit zu, wenn Ihr eine Derwisch-Verzauberung verliert. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Eure Sensenangriffe fügen Dunkelschaden zu und stehlen 0...12 Lebenspunkte. Ihr seid immun gegen Krankheit. Ihr fügt allen umstehenden Feinden Krankheit zu (3 Sekunden lang), wenn Ihr eine Derwisch-Verzauberung verliert. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '', '', ''),
(1521, 'Lyssas Avatar', '10..90 Sekunden lang werden Eure Derwisch-Verzauberungen um 50% schneller wieder aufgeladen und Eure Angriffe fügen Chaosschaden zu. Immer wenn Ihr eine Derwisch-Verzauberung verliert, entzieht ihr allen Feinden in der Nähe 1 Energiepunkt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Eure Derwisch-Verzauberungen laden 50% schneller wieder auf und Ihr fügt bei Angriffen Chaosschaden zu. Wenn Ihr eine Derwisch-Verzauberung verliert, entzieht ihr Feinden in der Nähe 1 Energiepunkt. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '', '', ''),
(1522, 'Melandrus Avatar', '10..90 Sekunden lang habt Ihr +150 Lebenspunkte, +30 Elementarrüstung und Eure Angriffe fügen Erdschaden zu. Immer wenn Ihr eine Derwisch-Verzauberung verliert, verlieren alle Gruppenmitglieder in Hörweite 1 Zustand. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Ihr habt +150 maximale Lebenspunkte, +30 Elementarrüstung und Eure Angriffe fügen Erdschaden zu. Immer wenn Ihr eine Derwisch-Verzauberung verliert, verlieren alle Gruppenmitglieder in Hörweite 1 Zustand. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', 'Melandrus Avatar (PvP)', '10..90 Sekunden lang habt Ihr +100 Lebenspunkte, +30 Elementarrüstung und Eure Angriffe fügen Erdschaden zu. Immer wenn Ihr eine Derwisch-Verzauberung verliert, verliert Ihr 2 Zustände. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.', '(10...90 Sekunden.) Ihr habt +100 maximale Lebenspunkte, +30 Elementarrüstung und Eure Angriffe fügen Erdschaden zu. Immer wenn Ihr eine Derwisch-Verzauberung verliert, verliert Ihr 2 Zustände. Diese Fertigkeit wird 45 Sekunden lang deaktiviert.'),
(1523, 'Meditation', 'Alles Adrenalin geht verloren. 20 Sekunden lang erhaltet Ihr jedes Mal, wenn eine auf Euch wirkende Verzauberung endet, 1..4 Energiepunkte.', '(20 Sekunden.) Alles Adrenalin geht verloren. Ihr erhaltet jedes Mal 1...4 Energiepunkte, wenn eine auf Euch wirkende Verzauberung endet.', '', '', ''),
(1524, 'Eifer des Eremiten', 'Ihr erhaltet für jeden Feind in Hörweite 1..3 Energiepunkte (maximal 8 Energiepunkte). 10 Sekunden lang bewirkt diese Verzauberung nichts. Wenn diese Verzauberung endet, erhaltet Ihr 1..3 Energiepunkte für jeden Feind in Hörweite (maximal 8 Energiepunkte).', '(10 Sekunden.) Anfangswirkung: Ihr erhaltet für jeden Feind in Hörweite 1...3 Energiepunkte (maximal 8). Wirkung am Ende: Ihr erhaltet für jeden Feind in Hörweite 1...3 Energiepunkte (maximal 8).', '', '', ''),
(1525, 'Natürliche Heilung', 'Ihr werdet um 50..170 Lebenspunkte geheilt. Wenn Ihr nicht verzaubert seid, wird dieser Zauber 50% schneller aktiviert.', 'Heilt Euch um 50...170 Lebenspunkte. Wenn Ihr nicht verzaubert seid, wird dieser Zauber 50% schneller aktiviert.', '', '', ''),
(1526, 'Mit Leben durchdringen', 'Der Verbündete wird um 5..50% Eurer aktuellen Lebenspunkte (maximal 300 Lebenspunkte) geheilt.', 'Heilt um 5...50% Eurer aktuellen Lebenspunkte (maximal 300 Punkte). <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1527, 'Mystische Heilung', 'Ihr werdet um 5..65 Lebenspunkte geheilt. Heilt zudem alle verzauberten Gruppenmitglieder um 5..65 Lebenspunkte.', 'Heilt Euch um 5...65 Lebenspunkte. Heilt alle verzauberten Gruppenmitglieder um 5...65 Lebenspunkte.', 'Mystische Heilung (PvP)', 'Ihr werdet um 5..65 Lebenspunkte geheilt. Heilt zudem alle verzauberten Gruppenmitglieder um 5..65 Lebenspunkte.', 'Heilt Euch um 5...65 Lebenspunkte. Heilt alle verzauberten Gruppenmitglieder um 5...65 Lebenspunkte.'),
(1528, 'Dwaynas Berührung', 'Der berührte Verbündete (bzw. Ihr selbst) wird pro Verzauberung, mit der Ihr belegt seid, um 15..60 (maximal 150) Lebenspunkte geheilt.', 'Heilt für jede Verzauberung auf Euch um 15...60 Lebenspunkte (maximal 150).', '', '', ''),
(1529, 'Fromme Wiederherstellung', 'Ihr erhaltet 80..150 Lebenspunkte und entfernt 1 Derwisch-Verzauberung. Wenn eine Verzauberung auf diese Weise entfernt wurde, verliert Ihr außerdem 1..2 Verhexungen.', 'Ihr erhaltet 80...150 Lebenspunkte und entfernt 1 Derwisch-Verzauberung. Entfernungseffekt: Ihr verliert 1...2 Verhexungen.', '', '', ''),
(1530, 'Siegel des frommen Lichts', 'Entfernt 1 Derwisch-Verzauberung. Heilt Verbündeten (bzw. Euch selbst) um 30..150 Lebenspunkte. Wenn eine Verzauberung auf diese Weise entfernt wird, lädt dieses Siegel 75% schneller wieder auf.', 'Heilt 30...150 Lebenspunkte. Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungswirkung: Lädt 75% schneller wieder auf.', '', '', ''),
(1531, 'Einschüchternde Aura', '60 Sekunden lang habt Ihr zusätzlich +40..100 Lebenspunkte und erleidet 1..10 Punkte weniger Schaden von Feinden mit weniger Lebenspunkten als Ihr.', '(60 Sekunden.) Ihr habt zusätzlich 40...100 Lebenspunkte und erleidet -1...10 Punkte Schaden von Feinden mit weniger Lebenspunkten als Ihr.', '', '', ''),
(1532, 'Mystischer Sandsturm', 'An der Eurer Position wird ein Sandsturm erzeugt, der 3 Sekunden lang anhält. Feinde in der Nähe Eurer ursprünglichen Position erleiden pro Sekunde 10..20 Punkte Erdschaden. Angreifende Feinde erleiden zusätzlich 10..20 Punkte Schaden. Wenn Ihr beim Wirken dieses Zaubers verzaubert seid, hält die Wirkung doppelt so lange an.', '(3 Sekunden.) Richtet 10...20 Punkte Erdschaden pro Sekunde an. Richtet bei angreifenden Feinden zusätzlich 10...20 Punkte Schaden an. Trifft Feinde in der Nähe Eurer ursprünglichen Position. Die Wirkung hält doppelt so lange an, wenn Ihr verzaubert seid.', '', '', ''),
(1533, 'Wind der Entzauberung', 'Entfernt 1 Eurer Derwisch-Verzauberungen. Wird eine Verzauberung entfernt, verlieren alle Feinde in der Nähe 1 Verzauberung und erleiden 20..80 Punkte Kälteschaden.', 'Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Alle Feinde in der Nähe verlieren 1 Verzauberung und erleiden 20...80 Punkte Kälteschaden.', '', '', ''),
(1534, 'Zerreißende Berührung', 'Fügt dem berührten Gegner 15..65 Punkte rüstungsignorierenden Kälteschaden zu. Ihr verliert 1 Derwisch-Verzauberung. Wird eine Verzauberung entfernt, verliert der Gegner 1 Verzauberung und Ihr erhaltet 1 Adrenalinstoß.', 'Richtet 15...65 Punkte rüstungsignorierenden Kälteschaden an. Ihr verliert 1 Derwisch-Verzauberung. Entfernungseffekt: Der Gegner verliert 1 Verzauberung und Ihr erhaltet 1 Adrenalinstoß.', '', '', ''),
(1535, 'Verkrüppelnder Schwung', 'Trifft dieser Angriff einen Gegner, wird dieser 3..12 Sekunden lang verkrüppelt. Diese Fertigkeit verursacht +3..15 Punkte Schaden zusätzlich, wenn der Feind sich bewegt.', '(3...12 Sekunden.) Verursacht Verkrüppelung. Richtet bei sich bewegenden Feinden +3...15 Punkte Schaden an.', '', '', ''),
(1536, 'Verwundender Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu, der Gegner leidet 5..20 Sekunden lang an einer Blutung und Ihr verliert 1 Derwisch-Verzauberung. Wenn eine Verzauberung entfernt wurde, erleidet der Gegner außerdem 5..20 Sekunden lang eine Tiefe Wunde.', 'Richtet +5...20 Punkte Schaden an und verursacht Zustand Blutung (5...20 Sekunden). Entfernt 1 Derwisch-Verzauberung. Entfernungseffekt: Verursacht Zustand Tiefe Wunde (5...20 Sekunden).', 'Verwundender Schlag (PvP)', 'Wenn dieser Angriff erfolgreich ist, leidet der Gegner 5..20 Sekunden lang an einer Blutung und Ihr verliert 1 Derwisch-Verzauberung. Wenn eine Verzauberung entfernt wurde, erleidet der Gegner außerdem 5..20 Sekunden lang eine Tiefe Wunde.', 'Verursacht Zustand Blutung (5...20 Sekunden). Entfernt 1 Derwisch-Verzauberung. Entfernungseffekt: Verursacht Zustand Tiefe Wunde (5...20 Sekunden).'),
(1537, 'Ermüdender Schlag', 'Ihr entfernt 1 Derwisch-Verzauberung. Wenn eine Verzauberung entfernt wurde, erleidet der Gegner 3..10 Sekunden lang eine Tiefe Wunde. Wenn keine Verzauberung entfernt, leidet Ihr 10 Sekunden lang unter "Schwäche".', 'Entfernt 1 Derwisch-Verzauberung. Entfernungseffekt: Verursacht Zustand Tiefe Wunde (3...10 Sekunden). <i>Wenn keine Verzauberung entfernt wird, leidet Ihr unter "Schwäche" (10 Sekunden).</i>', '', '', ''),
(1538, 'Lyssas Angriff', 'Wenn dieser Angriff trifft, unterbricht er eine Aktion. Falls Ihr verzaubert seid, wird jede von Euch unterbrochene Fertigkeit zudem 1..10 Sekunden lang deaktiviert. Dieser Angriff fügt nur 50% des normalen Schadens zu.', 'Unterbricht eine Aktion. Falls Ihr verzaubert seid, werden die unterbrochenen Fertigkeiten zudem 1...10 Sekunden lang deaktiviert. Dieser Angriff fügt nur 50% des normalen Schadens zu.', '', '', ''),
(1539, 'Kühler Sieg', 'Wenn dieser Angriff erfolgreich ist, werden dem Gegner +3..15 Punkte Schaden zugefügt. Wenn Ihr einen Feind trefft, dessen Lebenspunkte niedriger als Eure eigenen sind, erleiden der Gegner und alle umstehenden Feinde 10..30 Punkte Kälteschaden.', 'Richtet +3...15 Punkte Schaden an. Fügt jedem Feind, dessen Lebenspunkte niedriger als Eure eigenen sind, und allen umstehenden Feinden 10...30 Punkte Kälteschaden zu.', '', '', ''),
(1540, 'Überzeugung', 'Wenn Ihr unter einem Zustand leidet, erhaltet Ihr 10 Sekunden lang +10 Rüstung und 1..3 Lebenspunktregeneration für jeden Zustand, unter dem Ihr leidet. Wenn diese Verzauberung endet, werdet Ihr von 1..2 Zuständen geheilt.', '(10 Sekunden.) Ihr erhaltet +10 Rüstung, wenn Ihr unter einem Zustand leidet. Ihr erhaltet 1...3 Lebenspunktregeneration für jeden Zustand, unter dem Ihr leidet. Wirkung am Ende: Entfernt 1...2 Zustände.', '', '', ''),
(1541, 'Verzauberte Eile', '7 Sekunden lang bewegt Ihr Euch um 25% schneller. Wenn diese Verzauberung vorzeitig endet, verliert Ihr 1 Zustand.', '(7 Sekunden.) Ihr bewegt Euch um 25% schneller. Wenn dieser Verzauberung vorzeitig endet, verliert Ihr 1 Zustand.', '', '', ''),
(1542, 'Fromme Konzentration', 'Ihr könnt 5..20 Sekunden lang nicht unterbrochen werden, aber jedes Mal, wenn Ihr normalerweise unterbrochen würdet, verliert Ihr 1 Derwisch-Verzauberung oder Fromme Konzentration endet.', '(5...20 Sekunden.) Verhindert Unterbrechungen. <i>Verhinderungskosten: Ihr verliert 1 Derwisch-Verzauberung oder Fromme Konzentration endet.</i>', '', '', ''),
(1543, 'Fromme Eile', 'Ihr entfernt 1 Derwisch-Verzauberung und bewegt Euch 1..7 Sekunden lang 25% schneller. Wenn eine Verzauberung entfernt wird, rennt Ihr stattdessen 50% schneller.', '(1...7 Sekunden.) Ihr verliert 1 Derwisch-Verzauberung und bewegt Euch 25% schneller. Entferungseffekt: Rennt stattdessen 50% schneller.', '', '', ''),
(1544, 'Wirbelangriff', '1..6 Sekunden lang bewegt Ihr Euch um 33% schneller als gewöhnlich. Das nächste Mal, wenn Ihr einen Feind trefft, erleiden alle anderen Feinde in der Nähe 10..60 Kälteschaden und diese Verzauberung endet.', '(1...6 Sekunden.) Ihr bewegt Euch um 33% schneller. Fügt das nächste Mal, wenn Ihr einen Feind trefft, allen anderen Feinden in der Nähe 10...60 Kälteschaden zu und die Verzauberung endet.', '', '', ''),
(1545, 'Glaubensprüfung', 'Fügt dem berührten Gegner 15..65 Punkte Kälteschaden zu und entfernt 1 Verzauberung vom diesem. Wenn der Gegner nicht verzaubert war, leidet er 1..4 Sekunden lang unter Benommenheit.', 'Fügt 15...65 Punkte Kälteschaden zu und entfernt 1 Verzauberung vom Gegner. Wenn der Gegner nicht verzaubert war, leidet er 1...4 Sekunden lang unter Benommenheit.', '', '', ''),
(1546, 'Lodernder Speer', 'Wenn dieser Angriff erfolgreich ist, fügt er +5..25 Punkte Schaden zu und setzt den Gegner 1..3 Sekunden lang in Brand.', 'Richtet +5...25 Punkte Schaden an. Verursacht Zustand Brennen (1...3 Sekunden).', '', '', ''),
(1547, 'Mächtiger Wurf', 'Euer Speer bewegt sich dreimal so schnell. Wenn er trifft, fügt Ihr +10..40 Punkte Schaden zu.', 'Richtet +10...40 Punkte Schaden an. Dieser Speer bewegt sich mit dreifacher Geschwindigkeit.', '', '', ''),
(1548, 'Grausamer Speer', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +1..31 Punkte Schaden zu. Wenn Ihr ein Ziel trefft, das sich nicht bewegt, fügt Ihr 5..20 Sekunden lang eine Tiefe Wunde zu.', 'Richtet ++1...31 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner sich nicht bewegt.', '', '', ''),
(1549, 'Wurf des Läufers', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff einen sich bewegenden Feind trifft, fügt Ihr zusätzlich 5..30 Punkte Schaden zu.', 'Richtet +5...20 Punkte Schaden an. Richtet zusätzlich +5...30 Punkte Schaden an, falls der Gegner sich bewegt.', 'Wurf des Läufers (PvP)', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff einen sich bewegenden Feind trifft, fügt Ihr zusätzlich +5..30 Punkte Schaden zu.', 'Richtet +5...20 Punkte Schaden an. Richtet zusätzlich +5...30 Punkte Schaden an, falls der Gegner sich bewegt.'),
(1550, 'Unblockbarer Wurf', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..40 Punkte Schaden zu. Dieser Angriff kann nicht geblockt werden.', 'Richtet +10...40 Punkte Schaden an. Kann nicht geblockt werden.', '', '', ''),
(1551, 'Speer des Blitzes', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +8..20 Punkte Blitzschaden zu. Dieser Angriff hat eine Rüstungsdurchdringung von 25%.', 'Richtet +8...20 Punkte Blitzschaden an. 25% Rüstungsdurchdringung.', '', '', ''),
(1552, 'Ermüdender Speer', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..40 Punkte Schaden zu. Ihr seid 5 Sekunden lang geschwächt.', 'Richtet +10...40 Punkte Schaden an. <i>Ihr seid geschwächt (5 Sekunden).</i>', '', '', ''),
(1553, 'Hymne der Wut', '10 Sekunden erhalten alle Gruppenmitglieder in Hörweite bei der nächsten Verwendung einer Angriffsfertigkeit 1..4 Adrenalinstöße.', '10 Sekunden: Gruppenmitglieder in Hörweite erhalten bei Benutzung ihrer nächsten Angriffsfertigkeit 1...4 Adrenalin.', '', '', ''),
(1554, 'Verkrüppelungs-Hymne', '10 Sekunden lang führt die nächste Angriffsfertigkeit von jedem Verbündetem in Hörweite 5..15 Sekunden lang zu einer Verkrüppelung.', '10 Sekunden: Verbündete in Hörweite verursachen mit ihrer nächsten Angriffsfertigkeit den Zustand Verkrüppelung (5...15 Sekunden).', '', '', ''),
(1555, 'Verteidigungshymne', '4..10 Sekunden lang hat jedes Gruppenmitglied in Hörweite eine 50%ige Chance, Angriffe zu blocken. Dieser Anfeuerungsruf endet, wenn das Gruppenmitglied mit einer Angriffsfertigkeit trifft.', '4...10 Sekunden: Gruppenmitglieder in Hörweite haben eine 50%ige Chance zu blocken. <i>Endet bei einem Treffer mit einer Angriffsfertigkeit</i>', 'Verteidigungshymne (PvP)', '4..10 Sekunden lang hat jedes Gruppenmitglied in Hörweite eine 50%ige Chance, Angriffe zu blocken. Dieser Anfeuerungsruf endet, wenn das Gruppenmitglied mit einer Angriffsfertigkeit trifft.', '4...10 Sekunden: Gruppenmitglieder in Hörweite haben eine 50%ige Chance zu blocken. <i>Endet bei einem Treffer mit einer Angriffsfertigkeit</i>'),
(1556, 'Gute Reise', '5..20 Sekunden bewegen sich alle Verbündeten in Hörweite um 25% schneller, wenn sie verzaubert sind.', '5...20 Sekunden: Verbündete in Hörweite bewegen sich um 25% schneller, solange sie verzaubert sind.', '', '', ''),
(1557, 'Hymne der Flamme', 'Jedes Gruppenmitglied in Hörweite fügt, wenn es in den nächsten 10 Sekunden das nächste Mal mit einer Angriffsfertigkeit einen Treffer landet, vor dem Treffer auch 1..3 Sekunden lang Brennen zu.', '10 Sekunden: Gruppenmitglieder in Hörweite verursachen mit ihrer nächsten Angriffsfertigkeit den Zustand Brennen (1...3 Sekunden).', '', '', ''),
(1558, '"Zielt auf die Augen!"', '10 Sekunden lang hat der nächste Angriff jedes Verbündeten in Hörweite zusätzlich eine 30..100%ige Chance, ein kritischen Treffer zu sein.', '10 Sekunden: Verbündete in Hörweite haben mit ihrem nächsten Angriff +30...100% zur Chance auf kritische Treffer.', '"Zielt auf die Augen!" (PvP)', '10 Sekunden lang hat der nächste Angriff jedes Verbündeten in Hörweite eine 30..75%ige Chance, ein kritischen Treffer zu sein.', '10 Sekunden: Verbündete in Hörweite haben mit ihrem nächsten Angriff eine um +30...75% größere Chance für einen kritischen Treffer.'),
(1559, 'Hymne des Neids', '10 Sekunden lang führt die nächste Angriffsfertigkeit von jedem Verbündeten in Hörweite bei Feinden mit mehr als 50% Lebenspunkten zu +10..25 Punkten Schaden.', '10 Sekunden: Verbündete in Hörweite richten mit ihrer nächsten Angriffsfertigkeit +10...25 Schaden an. <i>Schadensbonus gilt nur bei Feinden mit mehr als 50% ihrer maximalen Lebenspunkte.</i>', 'Hymne des Neids (PvP)', '10 Sekunden lang führt die nächste Angriffsfertigkeit von jedem Verbündeten in Hörweite bei Feinden mit mehr als 50% Lebenspunkten zu +10..20 Punkten Schaden.', '10 Sekunden: Verbündete in Hörweite richten mit ihrer nächsten Angriffsfertigkeit +10...20 Schaden an. <i>Schadensbonus gilt nur bei Feinden mit mehr als 50% ihrer maximalen Lebenspunkte.</i>'),
(1560, 'Machtgesang', '5..20 Sekunden lang erfreut sich jeder Verbündete in Hörweite einer Energieregeneration von 4, bis er eine Fertigkeit benutzt.', '5...20 Sekunden: Verbündete in Hörweite erhalten +4 Energieregeneration. <i>Endet bei Benutzung einer Fertigkeit.</i>', '', '', ''),
(1561, 'Eifrige Hymne', '10 Sekunden lang erhält jeder Verbündete in Hörweite, wenn er das nächste Mal eine Angriffsfertigkeit verwendet, 1..8 Energiepunkte.', '10 Sekunden: Verbündete in Hörweite erhalten bei Benutzung ihrer nächsten Angriffsfertigkeit 1...8 Energiepunkte.', '', '', ''),
(1562, 'Arie des Eifers', '10 Sekunden lang erhält jeder Verbündete in Hörweite bei der nächsten Verwendung eines Zaubers 1..6 Energiepunkte.', '10 Sekunden: Verbündete in Hörweite erhalten bei Benutzung ihres nächsten Zaubers 1...6 Energiepunkte.', '', '', ''),
(1563, 'Lyrik des Eifers', '10 Sekunden lang erhält jeder Verbündete in Hörweite bei der nächsten Verwendung eines Siegels 1..8 Energiepunkte.', '10 Sekunden: Verbündete in Hörweite erhalten bei Benutzung Ihres nächsten Siegels 1...8 Energiepunkte.', '', '', ''),
(1564, 'Ballade der Wiederherstellung', '10 Sekunden lang wird jedes Gruppenmitglied in Hörweite, wenn es das nächste Mal Schaden erleidet, um 15..75 Lebenspunkte geheilt.', '10 Sekunden: Gruppenmitglieder in Hörweite werden um 15...75 Lebenspunkte geheilt, wenn sie das nächste Mal Schaden erleiden.', 'Ballade der Wiederherstellung (PvP)', '10 Sekunden lang erhält jedes Gruppenmitglied in Hörweite, wenn es das nächste Mal Schaden erleidet, 15..75 Lebenspunkte.', '10 Sekunden: Gruppenmitglieder in Hörweite erhalten 15...75 Lebenspunkte, wenn sie das nächste Mal Schaden erleiden.'),
(1565, 'Chor der Wiederherstellung', '10 Sekunden lang wird jeder Verbündete in Hörweite, wenn er das nächste Mal einen Schrei oder Anfeuerungsruf verwendet, um 30..90 Lebenspunkte geheilt.', '10 Sekunden: Verbündete in Hörweite werden bei Benutzung ihres nächsten Schreis oder Anfeuerungsrufs um 30...90 Lebenspunkte geheilt.', '', '', ''),
(1566, 'Arie der Wiederherstellung', '10 Sekunden lang wird jedes Gruppenmitglied in Hörweite, wenn es das nächste Mal einen Zauber wirkt, um 30..90 Lebenspunkte geheilt.', '10 Sekunden: Gruppenmitglieder in Hörweite werden bei Benutzung ihres nächsten Zaubers um 30...90 Lebenspunkte geheilt.', '', '', ''),
(1567, 'Lied der Konzentration', '10 Sekunden lang kann die nächste Fertigkeit, die jeder Verbündete in Hörweite verwendet, nicht unterbrochen werden.', '10 Sekunden: Verbündete in Hörweite können bei ihrer nächsten Fertigkeit nicht unterbrochen werden.', '', '', ''),
(1568, 'Hymne der Führung', '10 Sekunden lang kann der nächste theoretische Treffer mit einer Angriffsfertigkeit, die jedes Gruppenmitglied in Hörweite verwendet, nicht geblockt werden.', '10 Sekunden: Die nächste Angriffsfertigkeit von Gruppenmitgliedern in Hörweite kann nicht geblockt werden.', '', '', ''),
(1569, 'Energie bringender Chor', '10 Sekunden lang kostet der nächste Schrei oder Anfeuerungsruf von jedem Verbündeten in Hörweite 3..7 Energiepunkte weniger.', '10 Sekunden: Der nächste Schrei oder Anfeuerungsruf kostet Verbündete in Hörweite 3...7 Energiepunkte weniger.', '', '', ''),
(1570, 'Lied der Läuterung', '20 Sekunden lang entfernen die nächsten 1..3 Fertigkeiten, die jeder Verbündete in Hörweite verwendet, 1 Zustand von diesem Verbündeten.', '20 Sekunden: Verbündete in Hörweite verlieren bei Benutzung ihrer nächsten 1...3 Fertigkeiten einen Zustand.', '', '', ''),
(1571, 'Verhexungsbrech-Arie', '10 Sekunden lang verliert jeder Verbündete in Hörweite beim nächsten Wirken eines Zaubers 1 Verhexung.', '10 Sekunden: Verbündete in Hörweite verlieren bei Benutzung ihres nächsten Zaubers eine Verhexung.', '', '', ''),
(1572, '"Seid vorbereitet!"', '5..15 Sekunden lang erleiden das nächste Mal, wenn der Verbündete zu Boden geworfen würde, alle Feinde in der Nähe stattdessen 15..75 Punkte Schaden.', '(5...15 Sekunden.) Verhindert den nächsten Bodenwurf und richtet bei allen Feinden in der Nähe des Verbündeten 15...75 Punkte Schaden an. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '"Seid vorbereitet!" (PvP)', '5..15 Sekunden lang erleidet das nächste Mal, wenn der Verbündete zu Boden geworfen würde, 1 Feind in der Nähe stattdessen 15..75 Punkte Schaden.', '5...15 Sekunden: Verhindert den nächsten Bodenwurf und richtet bei einem Feind in der Nähe des Verbündeten 15...75 Schaden an. <i>Sich selbst als Ziel wählen nicht möglich</i>'),
(1573, 'Ehrfurcht', 'Wenn diese Fertigkeit einen zu Boden geworfenen Feind trifft, leidet dieser 5..15 Sekunden lang an Benommenheit. Ehrfurcht hat nur die halbe Reichweite.', 'Verursacht Zustand Benommenheit (5...15 Sekunden). <i>Ziel muss zu Boden geworfen sein, sonst keine Wirkung</i>', '', '', ''),
(1574, 'Dauerhafte Harmonie', 'Anfeuerungsrufe und Schreie, die in den nächsten 10..35 Sekunden auf den Nicht-Geister-Verbündeten (bzw. Euch selbst) gewirkt werden, dauern um 50% länger.', '10...35 Sekunden: Schreie und Anfeuerungsrufe halten beim Verbündeten (bzw. Euch selbst) um 50% länger an. <i>Nicht auf Geister anwendbar.</i>', '', '', ''),
(1575, 'Loderndes Finale', '10..35 Sekunden lang werden jedes Mal, wenn ein auf den Nicht-Geister-Verbündeten (bzw. Euch selbst) gerichteter Anfeuerungsruf oder Schrei endet, alle diesen Verbündeten umstehenden Feinde 1..7 Sekunden lang in Brand gesetzt.', '10...35 Sekunden: Verursacht bei umstehenden Feinden jedes Mal Zustand Brennen (1...7 Sekunden), wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten (bzw. Euch selbst) endet. <i>Nicht auf Geister anwendbar.</i>', 'Loderndes Finale (PvP)', '10..35 Sekunden lang werden jedes Mal, wenn ein auf einen Nicht-Geister-Verbündeten (bzw. Euch selbst) gerichteter Anfeuerungsruf oder Schrei endet, alle diesen Verbündeten umstehenden Feinde 1..3 Sekunden lang in Brand gesetzt.', '10...35 Sekunden: Verursacht bei umstehenden Feinden jedes Mal Zustand Brennen (1...3 Sekunden), wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten (bzw. Euch selbst) endet. <i>Nicht auf Geister anwendbar.</i>'),
(1576, 'Brennender Refrain', 'Wann immer der Nicht-Geister-Verbündete (bzw. Ihr selbst) während der nächsten 20 Sekunden einen Feind trifft, der über mehr Lebenspunkte als er selbst verfügt, wird dieser Feind 1..3 Sekunden lang in Brand gesetzt. Dieses Echo wird jedes Mal wiederholt, wenn ein auf diesen Verbündeten gerichteter Anfeuerungsruf oder Schrei endet.', '20 Sekunden: Verursacht Zustand Brennen (1...3 Sekunden), wenn der Verbündete (bzw. Ihr selbst) einen Feind mit mehr Lebenspunkten trifft. Erneuerung: Jedes Mal, wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten endet. <i>Nicht auf Geister anwendbar.</i>', '', '', ''),
(1577, 'Finale der Wiederherstellung', '10..35 Sekunden lang wird der Nicht-Geister-Verbündete (bzw. Ihr selbst) jedes Mal, wenn ein auf ihn gerichteter Anfeuerungsruf oder Schrei endet, um 15..75 Lebenspunkte geheilt.', '10...35 Sekunden: Der Verbündete (bzw. Ihr selbst) wird jedes Mal um 15...75 Lebenspunkte geheilt, wenn ein Schrei oder Anfeuerungsruf auf ihm endet. <i>Nicht auf Geister anwendbar.</i>', 'Finale der Wiederherstellung (PvP)', '10..35 Sekunden lang wird der Nicht-Geister-Verbündete (bzw. Ihr selbst) bei den nächsten 5 Malen, wenn ein auf ihn gerichteter Anfeuerungsruf oder Schrei endet, um 15..75 Lebenspunkte geheilt.', '(10...35 Sekunden.) Verbündeter (bzw. Ihr selbst) erhält 15...75 Lebenspunkte die nächsten 5 Male, wenn ein auf diesen Verbündeten Anfeuerungsruf oder Schrei endet. <i>Nicht auf Geister anwendbar.</i>'),
(1578, 'Genesungsrefrain', '15 Sekunden erfreut sich der Nicht-Geister-Verbündete (bzw. Ihr selbst) einer Lebenspunkteregeneration von +2..3. Dieses Echo wird jedes Mal wiederholt, wenn ein auf diesen Verbündeten gerichteter Anfeuerungsruf oder Schrei endet.', '15 Sekunden: Der Verbündete (bzw. Ihr selbst) hat +2...3 Lebenspunktregeneration. Erneuerung: Jedes Mal, wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten endet. <i>Nicht auf Geister anwendbar.</i>', 'Genesungsrefrain (PvP)', '15 Sekunden erfreut Ihr Euch einer Lebenspunkteregeneration von +2..3. Dieses Echo wird jedes Mal wiederholt, wenn ein auf Euchgerichteter Anfeuerungsruf oder Schrei endet.', '15 Sekunden: Ihr habt +2...3 Lebenspunktregeneration. Erneuerung: Jedes Mal, wenn ein Schrei oder Anfeuerungsruf auf Euch endet.'),
(1579, 'Reinigendes Finale', '10..35 Sekunden lang verliert der Nicht-Geister-Verbündete (bzw. Ihr selbst) jedes Mal, wenn ein auf ihn gerichteter Anfeuerungsruf oder Schrei endet, einen Zustand.', '10...35 Sekunden: Der Verbündete (bzw. Ihr selbst) verliert jedes Mal einen Zustand, wenn ein Schrei oder Anfeuerungsruf auf ihm endet. <i>Nicht auf Geister anwendbar.</i>', '', '', ''),
(1580, 'Klingenumkehr-Refrain', '20 Sekunden lang hat der Verbündete (bzw. Ihr selbst), der kein Geist ist, eine 5..20%ige Chance Angriffe zu blocken. Dieses Echo wird jedes Mal wiederholt, wenn ein auf diesen Verbündeten gerichteter Anfeuerungsruf oder Schrei endet.', '(20 Sekunden) Der Verbündete (bzw. Ihr selbst) hat eine 5...20%ige Chance, Angriffe zu Blocken. Erneuerung: Jedes mal, wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten endet. <i>Nicht auf Geister anwendbar.</i>', 'Klingenumkehr-Refrain (PvP)', '20 Sekunden lang hat der Nicht-Geister-Verbündete (bzw. Ihr selbst) +10..40 Rüstung gegen Hiebschaden. Dieses Echo wird jedes Mal wiederholt, wenn ein auf diesen Verbündeten gerichteter Anfeuerungsruf oder Schrei endet.', '20 Sekunden: Der Verbündete (bzw. Ihr selbst) hat +10...40 Rüstung gegen Hiebschaden. Erneuerung: Jedes mal, wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten endet. <i>Nicht auf Geister anwendbar.</i>'),
(1581, 'Glühendes Siegel', 'Wenn der Gegner brennt, erhaltet Ihr 5..15 Energiepunkte.', 'Ihr erhaltet 5...15 Energiepunkte, falls der Gegner brennt.', '', '', ''),
(1583, 'Eifer des Anführers', 'Ihr erhaltet für jeden Verbündeten in der Nähe 2 Energiepunkte (maximal 8..12 Energiepunkte).', 'Ihr erhaltet für jeden Verbündeten in der Nähe 2 Energiepunkte (maximal 8...12).', '', '', ''),
(1584, 'Trost des Anführers', 'Ihr heilt Euch um 30..75 Lebenspunkte. Ihr heilt für jeden Verbündeten in Hörweite um 10..20 Lebenspunkte mehr (zusammen maximal 140 Punkte).', 'Ihr heilt Euch um 30...75 Lebenspunkte. Zusätzlich heilt Ihr für jeden Verbündeten in Hörweite 10...20 Lebenspunkte (maximal 140).', '', '', ''),
(1585, 'Siegel der Synergie', 'Der Verbündete wird um 40..100 Lebenspunkte geheilt. Wenn Ihr nicht unter den Auswirkungen einer Verzauberung leidet, werdet Ihr um 40..100 Lebenspunkte geheilt.', 'Heilt den Verbündeten um 40...100 Lebenspunkte. Außerdem werdet Ihr um 40...100 Lebenspunkte geheilt, falls Ihr nicht verzaubert seid. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1586, 'Engelsschutz', '10 Sekunden lang wird der Verbündete jedes Mal, wenn er mehr als 250..100 Punkte Schaden pro Sekunde erleidet, um die Schadensmenge über diesem Betrag geheilt.', '10 Sekunden: In jeder Sekunde, in welcher der Verbündete (bzw. Ihr selbst) Schaden von mehr als 250...100 Punkten erleidet, wird bei ihm jeglicher Schaden über dieser Menge geheilt. <i>Sich selbst als Ziel wählen ist nicht möglich</i>', '', '', ''),
(1587, 'Engelsbindung', '10 Sekunden lang wird das nächste Mal, wenn ein Verbündeter in Hörweite tödlichen Schaden erleiden würde, dieser Schaden aufgehoben und der Verbündete um 20..200 Lebenspunkte geheilt. Engelsbindung endet bei allen anderen Verbündeten.', '10 Sekunden: Das nächste Mal, wenn ein Verbündeter in Hörweite tödlichen Schaden erleiden würde, wird dieser Schaden aufgehoben und der Verbündete um 20...200 geheilt. <i>Endet bei allen anderen Verbündeten.</i>', '', '', ''),
(1588, 'Siegel der Verätzung', 'Alle Gruppenmitglieder verlieren alle Zustände. Ihr werdet für jeden Zustand, der auf diese Weise entfernt wird, 1 Sekunde lang in Brand gesetzt.', 'Alle Gruppenmitglieder verlieren alle Zustände. <i>Ihr beginnt zu brennen (1 Sekunde pro entferntem Zustand).</i>', '', '', ''),
(1589, '"Haltet Eure Stellung!"', '5..20 Sekunden lang erhalten alle Gruppenmitglieder in Hörweite +24 Rüstung, wenn sie sich nicht bewegen.', '5...20 Sekunden: Gruppenmitglieder in Hörweite erhalten +24 Rüstung, wenn sie sich nicht bewegen.', '"Haltet Eure Stellung!" (PvP)', '5..20 Sekunden lang erhalten alle Gruppenmitglieder in Hörweite +24 Rüstung, wenn sie sich nicht bewegen. Dieser Effekt endet bei einem Verbündeten, der von diesem Schrei betroffen ist, wenn er erfolgreich angreift.', '5...20 Sekunden: Gruppenmitglieder in Hörweite erhalten +24 Rüstung, wenn sie sich nicht bewegen. <i>Endet für einen Verbündeten, wenn er angreift.</i>'),
(1590, '"Geht voran!"', 'Der Verbündete (bzw. Ihr selbst) bewegt sich pro Verbündeten in Hörweite 1..5 Sekunden lang um 25% schneller (maximal 20 Sekunden lang).', '1...5 Sekunden pro Verbündetem in Hörweite (maximal 20 Sekunden): Der Verbündete (bzw. Ihr selbst) bewegt sich um 25% schneller.', '', '', ''),
(1591, '"Beeilt Euch!"', '5..20 Sekunden lang bewegt sich der Verbündete um 33% schneller. Diese Fertigkeit endet, wenn der Verbündete erfolgreich angreift.', '5...20 Sekunden: Der Verbündete bewegt sich um 33% schneller. <i>Endet sobald der Verbündete mit einem Angriff trifft. Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1592, '"Wir kommen wieder!"', 'Alle Gruppenmitglieder in Hörweite werden mit 25..50% Lebenspunkten und 5..20% Energiepunkten wiederbelebt.', 'Alle Gruppenmitglieder in Hörweite werden wiederbelebt (25...50% Lebenspunkte, 5...20% Energiepunkte).', '"Wir kommen wieder!" (PvP)', '10 Sekunden lang werden jedes Mal, wenn Gruppenmitglieder in Hörweite eine Wiederbelebungsfertigkeit einsetzen, ihre gefallenen Verbündeten mit 10..75 Prozentpunkten mehr Lebenspunkten und 5..50 Prozentpunkten mehr Energie wieder ins Leben gerufen.', '10 Sekunden: Wenn Gruppenmitglieder in Hörweite Wiederbelebungsfertigkeiten einsetzen, werden ihre gefallenen Verbündeten mit 10...75% mehr Lebenspunkten und 5...50% mehr Energiepunkten ins Leben zurückgerufen.'),
(1593, '"Gebt nicht auf!"', 'alle Verbündeten in Hörweite, deren Lebenspunkte unter 75% liegen, erhalten 1..10 Energiepunkte.', 'Verbündete in Hörweite erhalten 1...10 Energiepunkte. <i>Betrifft nur Verbündete, die weniger als 75% ihrer maximalen Lebenspunkte haben</i>', '"Gebt niemals auf!" (PvP)', 'Alle Verbündeten in Hörweite, deren Lebenspunkte unter 75% liegen, erhalten 1..10 Energiepunkte.', ''),
(1594, '"Helft mir!"', 'Ihr heilt um  15..90 Lebenspunkte. 1..10 Sekunden lang werden von Verbündeten auf Euch gerichtete Zauber um 50% schneller gewirkt.', '(1...10 Sekunden.) Verbündete können ihre Fertigkeiten auf Euch um 50% schneller wirken. Heilt 15...90 Lebenspunkte.', '"Helft mir!" (PvP)', 'Ihr erhaltet 5..50 Lebenspunkte. 1..10 Sekunden lang werden von Verbündeten auf Euch gerichtete Zauber um 50% schneller gewirkt.', '(1...10 Sekunden.) Verbündete können ihre Zauber auf Euch um 50% schneller wirken. Ihr erhaltet 5...50 Lebenspunkte.'),
(1595, '"Zieht Euch zurück!"', '4..10 Sekunden erhalten alle Verbündeten in Hörweite 5..15 Lebenspunkte pro Sekunde, wenn sie sich bewegen und bewegen sich um 33% schneller. "Zurückweichen!" endet bei einem Verbündeten, der von diesem Schrei betroffen ist, wenn er erfolgreich angreift.', '4...10 Sekunden: Verbündete in Hörweite erhalten 5...15 Lebenspunkte pro Sekunde, wenn sie sich bewegen. Sie bewegen sich um 33% schneller. <i>Endet für einen Verbündeten, wenn er mit einem Angriff trifft.</i>', '"Zurückweichen!" (PvP)', '4..10 Sekunden erhalten alle Verbündeten in Hörweite 5..15 Lebenspunkte pro Sekunde, wenn sie sich bewegen und bewegen sich um 33% schneller. "Zurückweichen!" endet bei einem Verbündeten, der von diesem Schrei betroffen ist, wenn er erfolgreich angreift.', ''),
(1596, '"Schon unterwegs!"', '4..10 Sekunden lang bewegen sich alle Verbündeten in Hörweite um 33% schneller und gewinnen 5..15 Lebenspunkte pro Sekunde, während sie in Bewegung sind.', '4...10 Sekunden: Verbündete in Hörweite bewegen sich 33% schneller und gewinnen 5...15 Lebenspunkte pro Sekunde, solange sie in Bewegung sind.', '"Schon unterwegs!" (PvP)', '4..10 Sekunden lang bewegen sich alle Verbündeten in Hörweite um 33% schneller und gewinnen 5..15 Lebenspunkte pro Sekunde, während sie in Bewegung sind.', '(4...10 Sekunden.) Verbündete in Hörweite bewegen sich 33% schneller und gewinnen 5...15 Lebenspunkte pro Sekunde, solange sie in Bewegung sind.'),
(1597, '"Sie brennen!"', '10 Sekunden lang erleiden Gruppenmitglieder in Hörweite 5..35% weniger Schaden von Feinden, die brennen.', '10 Sekunden: Gruppenmitglieder in Hörweite erleiden von brennenden Feinden 5...35% weniger Schaden.', '', '', ''),
(1598, '"Ergebt Euch nicht!"', '15 Sekunden lang erfreuen sich alle Gruppenmitglieder in Hörweite, deren Lebenspunkte unter 75% liegen, einer Lebenspunktregeneration von +1..5.', '15 Sekunden: Gruppenmitglieder in Hörweite erhalten +1...5 Lebenspunktregeneration. <i>Betrifft nur Gruppenmitglieder, die weniger als 75% ihrer maximalen Lebenspunkte haben.</i>', '"Ergebt Euch nicht!" (PvP)', '10 Sekunden lang erfreuen sich alle Gruppenmitglieder in Hörweite, deren Lebenspunkte unter 75% liegen, einer Lebenspunktregeneration von +1..5.', '10 Sekunden: Gruppenmitglieder in Hörweite erhalten +1...5 Lebenspunktregeneration. <i>Betrifft nur Gruppenmitglieder, die weniger als 75% ihrer maximalen Lebenspunkte haben.</i>'),
(1599, 'Es ist nur eine Fleischwunde.', 'Der Verbündete verliert alle Zustände. Wenn ein Zustand auf diese Weise entfernt wird, bewegt sich dieser Verbündete 1..10 Sekunden lang um 25% schneller.', 'Entfernt alle Zustände vom Verbündeten. Dieser Verbündete bewegt sich um 25% schneller (1...10 Sekunden), wenn ein Zustand entfernt wurde. <i>Sich selbst als Ziel auswählen ist nicht möglich.</i>', '', '', ''),
(1600, 'Stachelspeer', 'Wenn dieser Angriff erfolgreich ist, leidet der Gegner 5..20 Sekunden lang an einer Blutung.', 'Verursacht Zustand Blutung (5...20 Sekunden).', '', '', ''),
(1601, 'Bösartiger Angriff', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr mit diesem Angriff einen kritischen Treffer landet, leidet der Gegner 5..15 Sekunden lang an einer Tiefen Wunde.', 'Richtet +5...20 Punkte Schaden an. Verursacht mit einem kritischen Treffer Zustand Tiefe Wunde (5...15 Sekunden).', '', '', ''),
(1602, 'Betäubender Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..30 Punkte Schaden zu. Wenn er einen Feind trifft, der unter einem Zustand leidet, wird dieser 4..10 Sekunden lang betäubt.', 'Richtet +5...30 Punkte Schaden an. Verursacht Zustand Benommenheit (4...10 Sekunden), falls der Gegner einen Zustand hat.', '', '', ''),
(1603, 'Unbarmherziger Speer', 'Wenn dieser Angriff einen Gegner trifft, dessen Lebenspunkte unter 50% liegen, leidet dieser 5..20 Sekunden lang an einer Tiefen Wunde.', 'Verursacht Zustand Tiefe Wunde (5...20 Sekunden). <i>Gegner muss weniger als 50% seiner maximalen Lebenspunkte haben, sonst keine Wirkung.</i>', '', '', ''),
(1604, 'Unterbrechender Wurf', 'Wenn dieser Angriff einen Gegner trifft, der unter einem Zustand leidet, wird er unterbrochen.', 'Unterbricht Aktionen. <i>Gegner muss einen Zustand haben, sonst keine Wirkung.</i>', '', '', ''),
(1605, 'Wilder Wurf', 'Wenn dieser Angriff erfolgreich ist, fügt er +5..20 Punkte Schaden zu und jegliche Haltung, die von Eurem Gegner eingenommen wird, endet. Dieser Angriff kann nicht geblockt werden. Alle Eure Angriffsfertigkeiten, die nicht auf Speeren beruhen, werden 3 Sekunden lang deaktiviert.', 'Richtet +5...20 Punkte Schaden an. Kann nicht geblockt werden. Beendet die Haltung des Gegners.<i>Eure<sub>sic</sub> Angriffsfertigkeiten, die nicht auf Speeren beruhen, werden 3 Sekunden lang deaktiviert.</i>', '', '', ''),
(1633, 'Böswilliger Stoß', 'Wenn dieser Angriff einen Gegner trifft, der an einem Zustand leidet, fügt Ihr +10..30 Punkte Schaden zu und der Angriff führt zu einem kritischen Treffer.', 'Dieser Angriff richtet +10...30 Punkte Schaden an und ist ein kritischer Treffer, falls der Gegner einen Zustand hat.', '', '', ''),
(1634, 'Vernichtender Angriff', 'Muss einem Begleithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt Ihr 5..50 Punkte Schaden vom Typ der geführten Waffe zu und der Gegner verliert eine Verzauberung. Dieser Angriff kann nicht geblockt werden.', 'Richtet 5...50 Punkte Schaden vom Typ der geführten Waffe an. Entfernt eine Verzauberung. Kann nicht geblockt werden. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(1635, 'Goldener Schädel-Stoß', 'Wenn Ihr mit einer Verzauberung belegt seid und dieser Angriff trifft, leidet der Gegner 1..5 Sekunden lang an Benommenheit.', 'Verursacht Zustand Benommenheit (1...5 Sekunden). <i>Ihr müsst verzaubert sein, sonst keine Wirkung.</i>', '', '', ''),
(1636, 'Schwarzer Spinnen-Stoß', 'Muss einen verhexten Feind treffen. Wenn er trifft, fügt dieser Angriff +5..20 Punkte Schaden zu und der Feind wird 5..20 Sekunden lang vergiftet.', 'Richtet +5...20 Punkte Schaden an. Verursacht Zustand Gift (5...20 Sekunden). <i>Muss einen verhexten Feind treffen.</i>', '', '', ''),
(1637, 'Goldener Fuchs-Stoß', 'Wenn dieser Angriff erfolgreich ist, erleidet der Gegner +10..30 Punkte Schaden. Wenn Ihr unter dem Einfluss einer Verzauberung steht, kann der Angriff nicht geblockt werden.', 'Richtet +10...30 Punkte Schaden an. Kann nicht geblockt werden, falls Ihr verzaubert seid.', '', '', ''),
(1638, 'Tödliche Eile', '10..35 Sekunden lang werden Zauber mit halber Reichweite um 5..50% schneller gewirkt und um 5..50% schneller wieder aufgeladen.', '10...35 Sekunden: Ihr könnt Eure Zauber mit halber Reichweite um 5...50% schneller wirken und sie laden sich um 5...50% schneller auf.', '', '', ''),
(1639, 'Mittel des Assassinen', 'Während der nächsten 30 Sekunden entfernen Eure nächsten 1..10 Angriffsfertigkeiten einen Zustand.', '30 Sekunden: Eure nächsten 1...10 Angriffsfertigkeiten entfernen je einen Zustand.', 'Mittel des Assassinen (PvP)', 'Während der nächsten 30 Sekunden entfernen Eure nächsten 1..10 Angriffsfertigkeiten einen Zustand.', '30 Sekunden: Eure nächsten 1...10 Angriffsfertigkeiten entfernen je einen Zustand.'),
(1640, 'Versprechen des Fuchses', '5..20 Sekunden lang können Eure Dolchangriffe nicht geblockt werden. Diese Verzauberung endet, wenn Ihr das nächste Mal nicht trefft.', '5...20 Sekunden: Eure Dolchangriffe können nicht geblockt werden. <i>Endet, sobald Ihr nicht trefft.</i>', '', '', ''),
(1641, 'Vorgetäuschte Neutralität', '4..10 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration von +7 und +80 Rüstung. Diese Verzauberung endet, wenn Ihr mit einem Angriff erfolgreich trefft oder eine Fertigkeit verwendet.', '4...10 Sekunden: Ihr habt +7 Lebenspunktregeneration und +80 Rüstung. <i>Endet, wenn Ihr mit einem Angriff trefft oder eine Fertigkeit benutzt.</i>', '', '', ''),
(1642, 'Versteckte Trittnägel', 'Eure Nicht-Assassinen-Fertigkeiten sind 10 Sekunden lang deaktiviert. 1..10 Sekunden lang bewegt sich der Gegner um 50% langsamer. Wenn diese Verhexung endet, ist der Gegner 1..15 Sekunden lang verkrüppelt.', '(1...10 Sekunden.) Bewirkt 50%ige Verlangsamung der Bewegung. Wirkung am Ende: Verursacht Zustand Verkrüppelung (1...15 Sekunden). <i>Eure Nicht-Assassinen-Fertigkeiten sind 10 Sekunden lang deaktiviert.</i>', '', '', ''),
(1643, 'Verzauberungen angreifen', 'Muss auf einen Doppelangriff folgen. Das feindliche Ziel verliert alle Verzauberungen.', 'Entfernt alle Verzauberungen. <i>Muss einem Doppelangriff folgen.</i>', '', '', ''),
(1644, 'Verfall des Verschwenders', 'Ihr unternehmt einen Schattenschritt zum Gegner hin. Wenn dieser keine Fertigkeit benutzt, wird er zu Boden geworfen. Alle Eure Angriffsfertigkeiten, die nicht auf Dolchen beruhen, sind 10 Sekunden lang deaktiviert.', 'Unternehmt einen Schattenschritt zum Gegner. Gegner wird zu Boden geworfen, wenn er keine Fertigkeit benutzt. <i>Deaktiviert Eure Angriffsfertigkeiten, die nicht auf Dolchen beruhen.</i>', '', '', ''),
(1645, 'Verzauberung lösen', 'Wenn der berührte Gegner am Boden liegt, verliert er eine Verzauberung.', 'Entfernt eine Verzauberung. <i>Gegner muss zu Boden geworfen sein, sonst keine Wirkung.</i>', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1646, 'Todesomen', 'Während den nächsten 5..35 Sekunden fügt Ihr das nächste Mal, wenn die Lebenspunkte des Gegners nach dem Hinzufügen von Schaden unter 50% fallen, 5..20 Sekunden lang eine Tiefe Wunde zu und unternehmt einen Schattenschritt zu diesem Gegner hin. Dieser Zauber hat nur die halbe Reichweite.', '5...35 Sekunden: Verursacht Zustand Tiefe Wunde (5...20 Sekunden) und unternimmt einen Schattenschritt zum Gegner, wenn Schaden seine Lebenspunkte das nächste Mal unter 50% seiner maximalen Lebenspunkte bringen würde.', '', '', ''),
(1647, 'Siegel des toxischen Schocks', 'Wenn der Gegner vergiftet ist, erleidet er 10..100 Punkte Schaden.', 'Richtet 10...100 Punkte Schaden an. <i>Gegner muss vergiftet sein, sonst keine Wirkung.</i>', '', '', ''),
(1648, 'Siegel des Zwielichts', 'Pro Verhexung, mit der der Gegner belegt ist, verliert dieser eine Verzauberung.', 'Entfernt eine Verzauberung pro Verhexung auf dem Gegner.', '', '', ''),
(1649, 'Weg des Assassinen', 'Während Ihr Dolche verwendet, sinkt 20 Sekunden lang der Abstand zwischen Euren Angriffen um 5..20% und Ihr habt bei der Benutzung von Dolchen eine +5..35%ige Chance, einen kritischen Treffer zu erzielen.', '20 Sekunden: Ihr greift, während Ihr Dolche führt, Ihr<sub>sic</sub> um 5...20% schneller an und habt eine +5...35%ige Chance, ein<sub>sic</sub> kritischen Treffer zu erzielen.', '', '', ''),
(1650, 'Schattenmarsch', 'Schattenschritt zum Gegner. 15 Sekunden lang geschieht nichts. Eure Angriffsfertigkeiten sind 1 Sekunde lang deaktiviert und Eure Haltungen und Verzauberungen sind 10 Sekunden lang deaktiviert. Wenn diese Haltung endet, kehrt Ihr zu Eurem Ausgangsort zurück.', '(15 Sekunden.) Schattenschritt zum Gegner. Wirkung am Ende: Ihr kehrt zu Eurem Ausgangsort zurück. <i>Deaktiviert Eure Angriffsfertigkeiten 1 Sekunde lang. Deaktiviert Eure Verzauberungen und Haltungen 10 Sekunden lang.</i>', '', '', ''),
(1651, 'Rückzug des Todes', 'Schattenschritt zum Verbündeten. Wenn Ihr weniger Lebenspunkte als dieser Verbündete habt, heilt Ihr Euch um 40..130 Lebenspunkte.', 'Ihr heilt Euch um 40...130 Lebenspunkte, falls Ihr weniger Lebenspunkte habt als der Verbündete. Anfangswirkung: Unternehmt einen Schattenschritt zum Verbündeten.', '', '', ''),
(1652, 'Schattengefängnis', 'Schattenschritt zum Gegner. 1..7 Sekunden lang bewegt sich dieser Gegner um 66% langsamer.', 'Unternehmt einen Schattenschritt zum Gegner. Der Gegner bewegt sich um 66% langsamer (1...7 Sekunden).', '', '', ''),
(1653, 'Tauschen', 'Ihr und die herbeigerufene Kreatur macht einen Schattenschritt zum Standort des jeweils anderen.', 'Ihr und die herbeigerufene Kreatur macht einen Schattenschritt zum Standort des jeweils anderen.', '', '', ''),
(1654, 'Schattenverschmelzung', 'Ihr unternehmt einen Schattenschritt zum Verbündeten hin. Wenn Ihr diese Verzauberung nicht länger aufrechterhaltet, kehrt Ihr an Euren ursprünglichen Ort zurück.', 'Unternehmt einen Schattenschritt zum Verbündeten. Wirkung am Ende: Ihr kehrt zur anfänglichen Position zurück. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1655, 'Preis des Stolzes', 'Während der nächsten 5..20 Sekunden verliert der Gegner bei der nächsten Verwendung einer Elite-Fertigkeit 1..7 Energiepunkte.', '5...20 Sekunden: Lässt den Gegner 1...7 Energiepunkte verlieren, wenn er das nächste Mal eine Elite-Fertigkeit benutzt.', '', '', ''),
(1656, 'Flair der Entzauberung', 'Entfernt eine Verzauberung vom Gegner und allen Feinden in der Nähe. 5..20 Sekunden lang enden Verzauberungen auf diesen Feinden mit 150..300%iger Geschwindigkeit.', 'Verhext auch Feinde in der Nähe Eures Gegners (5...20 Sekunden). Entfernt eine Verzauberung vom Gegner und allen Feinden in der Nähe. Verzauberungen auf diesen Feinden enden mit 150...300%iger Geschwindigkeit.', '', '', ''),
(1657, 'Siegel der Unbeholfenheit', 'Wenn der Gegner angreift, werden er und alle umstehenden Feinde unterbrochen und erleiden 15..60 Punkte Schaden. Gegner, die Angriffsfertigkeiten verwenden, werden zu Boden geworfen.', 'Unterbricht einen Angriff des Gegners und aller umstehenden Feinde. Unterbrechungswirkung: Richtet 15...60 Punkte Schaden an und wirft Gegner, die Angriffsfertigkeiten verwenden, zu Boden.', 'Siegel der Unbeholfenheit (PvP)', 'Wenn der Gegner angreift, werden er und alle umstehenden Feinde unterbrochen und erleiden 15..60 Punkte Schaden. Gegner, die Angriffsfertigkeiten verwenden, werden zu Boden geworfen.', 'Unterbricht einen Angriff des Gegners und aller umstehenden Feinde. Unterbrechungswirkung: Richtet 15...60 Punkte Schaden an und wirft Gegner, die Angriffsfertigkeiten verwenden, zu Boden.'),
(1658, 'Symbolische Haltung', '5..20 Sekunden lang wird das nächste Siegel, das Ihr aktiviert, um 20..80% schneller aufgeladen.', '5...20 Sekunden: Euer nächstes Siegel lädt sich um 20...80% schneller wieder auf.', '', '', ''),
(1659, 'Toxische Kälte', 'Der Gegner erleidet 15..75 Punkte Kälteschaden. Falls er mit einer Verhexung oder Verzauberung belegt ist, leidet er 10..25 Sekunden lang an einer Vergiftung.', 'Richtet 15...75 Punkte Kälteschaden an. Verursacht Zustand Gift (10...25 Sekunden), falls der Gegner verhext oder verzaubert ist.', '', '', ''),
(1660, 'Brunnen der Stille', 'Der nächste Kadaver wird an seiner Position 10..30 Sekunden lang in einen Brunnen der Stille verwandelt. Feinde innerhalb des Brunnens können keine Schreie oder Anfeuerungsrufe verwenden und leiden unter einer Lebenspunktdegeneration von -1..4.', '(10...30 Sekunden.) Feinde innerhalb des Brunnen können keine Schreie oder Anfeuerungsrufe verwenden und leiden unter einer Lebenspunktdegeneration von -1...4. <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(1661, 'Leuchtstein', 'Sendet ein Geschoss aus, das dem Gegner 5..50 Punkte Erdschaden zufügt, wenn es trifft. Wenn dieser Zauber einen geschwächten Gegner trifft, erhaltet Ihr 5 Energiepunkte sowie 1 Punkt für alle 2 Ränge in Energiespeicherung.', 'Geschoss: Richtet 5...50 Punkte Erdschaden an. Ihr erhaltet 5 Energiepunkte sowie 1 Energiepunkt pro 2 Ränge in Energiespeicherung, wenn der Gegner unter Schwäche  leidet.', '', '', ''),
(1662, 'Geistige Explosion', 'Der Gegner erleidet 15..60 Punkte Feuerschaden. Wenn Ihr mehr Energie als dieser Gegner habt, erhaltet Ihr 1..8 Energiepunkte.', 'Richtet 15...60 Punkte Feuerschaden an. Ihr erhaltet 1...8 Energiepunkte, falls Ihr mehr Energie habt als der Gegner.', '', '', ''),
(1663, 'Elementare Flamme', 'Während der nächsten 10..35 Sekunden wird der Gegner jedes Mal, wenn auf ihn eine Elementar-Verhexung gerichtet wird, 1..5 Sekunden lang in Brand gesetzt.', '(10...35 Sekunden.) Verursacht jedes Mal Zustand Brennen (1...5 Sekunden), wenn eine Elementar-Verhexung auf den Gegner gerichtet wird.', 'Elementare Flamme (PvP)', 'Während der nächsten 5..20 Sekunden wird der Gegner jedes Mal, wenn auf ihn eine Elementar-Verhexung gerichtet wird, 1..2 Sekunden lang in Brand gesetzt.', '(5...20 Sekunden.) Verursacht jedes Mal Zustand Brennen (1...2 Sekunden), wenn eine Elementar-Verhexung auf den Gegner gerichtet wird.'),
(1664, 'Blitz herbeirufen', 'Der Gegner, ein Feind in seiner Nähe und ein weiterer Feind in der Nähe von letzterem erleiden 10..90 Punkte Blitzschaden. Dieser Zauber hat eine Rüstungsdurchdringung von 25%.', 'Richtet 10...90 Punkte Blitzschaden an. Trifft zwei Feinde in der Nähe des Ziels. 25% Rüstungsdurchdringung.', '', '', ''),
(1683, 'Nachdenklicher Wächter', '5..11 Sekunden lang hat der Verbündete (bzw. Ihr selbst) eine 50%ige Chance Angriffe von verzauberten Gegnern zu blocken.', '5...11 Sekunden: 50%ige Chance, Angriffe verzauberter Gegner zu blocken.', '', '', ''),
(1684, 'Einblick des Schreibers', 'Während der nächsten 10..35 Sekunden erhaltet Ihr jedes Mal, wenn Ihr ein Siegel benutzt, 3 Energiepunkte.', '10...35 Sekunden: Ihr erhaltet jedes Mal 3 Energiepunkte, wenn Ihr ein Siegel benutzt.', '', '', ''),
(1685, 'Heilige Eile', '1..60 Sekunden lang werden Eure Heilgebete-Zauber um 50% schneller gewirkt. Diese Verzauberung endet, wenn Ihr eine andere Verzauberung wirkt.', '1...60 Sekunden: Ihr könnt Eure Heilgebet-Zauber um 50% schneller wirken. <i>Endet, wenn Ihr eine Verzauberung wirkt.</i>', '', '', ''),
(1686, 'Lichtschimmer', 'Heilt Verbündeten (bzw. Euch selbst) um 10..115 Lebenspunkte.', 'Heilt um 10...115 Lebenspunkte.', '', '', ''),
(1687, 'Eifrige Segnung', 'Heilt den Verbündeten (bzw. Euch selbst) um 30..180 Lebenspunkte. Wenn seine Lebenspunkte unter 50% lagen, erhaltet Ihr 7 Energiepunkte.', 'Heilt um 30...180 Lebenspunkte. Ihr erhaltet 7 Energiepunkte, falls der Verbündete (bzw. Ihr selbst) weniger als 50% seiner maximalen Lebenspunkte hatte.', '', '', ''),
(1688, 'Eifer des Verteidigers', '5..25 Sekunden lang erhaltet Ihr jedes Mal, wenn der Gegner einen erfolgreichen Angriff landet, 2 Energiepunkte.', '5...25 Sekunden: Ihr erhaltet jedes Mal 2 Energiepunkte, wenn ein Angriff des Gegners trifft.', '', '', ''),
(1689, 'Siegel des mystischen Zorns', 'Der Gegner erleidet für jede Verzauberung, mit der Ihr belegt seid, 5..35 Punkte heiligen Schaden (maximal 100 Punkte heiligen Schaden).', 'Richtet für jede Verzauberung auf Euch 5...35 Punkte heiligen Schaden an (maximal 100).', '', '', ''),
(1690, 'Siegel der Entfernung', 'Wenn der Verbündete (bzw. Ihr selbst) unter dem Einfluss einer Verzauberung steht, verliert er eine Verhexung und einen Zustand.', 'Entfernt eine Verhexung und einen Zustand. <i>Der Verbündete (bzw. Ihr selbst) muss verzaubert sein, sonst keine Wirkung.</i>', '', '', ''),
(1691, 'Zustand aufheben', 'Entfernt einen Zustand vom Verbündeten (bzw. Euch selbst). Wenn dieser Verbündete unter den Auswirkungen einer Verzauberung leidet, wird er um 15..75 Lebenspunkte geheilt.', 'Entfernt einen Zustand. Heilt um 15...75 Lebenspunkte, falls der Verbündete (bzw. Ihr selbst) verzaubert ist.', '', '', ''),
(1692, 'Verhexungen umleiten', 'Entfernt bis zu 1..3 Verhexung(en) vom Verbündeten (bzw. Euch selbst). Für jede Verhexung, die auf diese Weise entfernt wird, wird der Verbündete um 15..75 Lebenspunkte geheilt und verliert einen Zustand.', 'Entfernt 1...3 Verhexung(en). Für jede entfernte Verhexung verliert der Verbündete (bzw. Ihr selbst) einen Zustand und wird um 15...75 Lebenspunkte geheilt.', '', '', ''),
(1693, 'Gegenangriff', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..35 Schaden zu. Wenn Ihr einen angreifenden Gegner trefft erhaltet Ihr 2..6 Energiepunkte.', 'Richtet +5...35 Punkte Schaden an. Ihr erhaltet 2...6 Energiepunkte, falls der Gegner gerade angreift.', '', '', ''),
(1694, 'Magierschlag', 'Wenn dieser Angriff erfolgreich ist, fügt er +5..20 Punkte Schaden zu. Wenn Euer Gegner unter dem Einfluss einer Verzauberung steht, ist ein Blocken bei dem Angriff nicht möglich.', 'Richtet +5...20 Punkte Schaden an. Kann nicht geblockt werden, falls Gegner verzaubert ist.', '', '', ''),
(1695, 'Soldatenstoß', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..40 Punkte mehr Schaden zu. Wenn Ihr mit einem Anfeuerungsruf oder Schrei belegt seid, ist ein Blocken bei dem Angriff nicht möglich.', 'Richtet +10...40 Punkte Schaden an. Kann nicht geblockt werden, wenn Ihr unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.', '', '', ''),
(1696, 'Köpfen', 'Ihr verliert Euer gesamtes Adrenalin und alle Energie. Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..50 Punkte Schaden und 5..20 Sekunden lang eine Tiefe Wunde zu. Dieser Angriff führt zu einem kritischen Treffer.', 'Richtet +5...50 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden). Immer ein kritischer Treffer. <i>Ihr verliert alles Adrenalin und alle Energiepunkte.</i>', '', '', ''),
(1697, 'Magiejägerschmetterer', 'Wenn dieser Angriff erfolgreich ist, wird der Gegner zu Boden geworfen. Wenn Euer Gegner unter dem Einfluss einer Verzauberung steht, kann der Angriff nicht geblockt werden.', 'Wirft zu Boden. Kann nicht geblockt werden, wenn Gegner verzaubert ist.', '', '', ''),
(1698, 'Soldaten-Haltung', '5..15 Sekunden habt Ihr eine 75%ige Chance zu blocken. Ihr greift um 33% schneller an, wenn Ihr unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.', '(5...15 Sekunden.) Ihr habt eine 75%ige Chance zu blocken. Ihr greift um 33% schneller an, wenn Ihr unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.', 'Soldaten-Haltung (PvP)', '4..10 Sekunden lang greift Ihr um 33% schneller an, wenn Ihr mit einem Schrei oder Anfeuerungsruf belegt seid.', '4...10 Sekunden: Ihr greift um 33% schneller an, wenn Ihr unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.'),
(1699, 'Soldaten-Verteidigung', '1..6 Sekunden lang habt Ihr eine 75%ige Chance, Angriffe zu blocken, wenn Ihr mit einem Schrei oder Anfeuerungsruf belegt seid. Für jeden geblockten Angriff erhaltet Ihr 1 Punkt Adrenalin.', '(1...6 Sekunden.) Ihr habt eine 75%ige Chance zu blocken, wenn Ihr unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht. Für jeden Block erhaltet Ihr 1 Punkt Adrenalin.', '', '', ''),
(1700, 'Fieberhafte Verteidigung', '8 Sekunden lang habt Ihr eine 75%ige Chance Angriffe zu blocken, dafür erleidet Ihr aber doppelten Schaden.', '8 Sekunden: Ihr habt eine 75%ige Chance zu blocken. <i>Ihr erleidet doppelten Schaden.</i>', '', '', ''),
(1701, 'Ruhige Haltung', '10 Sekunden lang erhaltet Ihr das nächste Mal, wenn Ihr zu Boden geworfen würdet, stattdessen 1..3 Adrenalinstöße und 1..7 Energiepunkte.', '10 Sekunden: Das nächste Mal. wenn Ihr zu Boden geworfen würdet, erhaltet Ihr stattdessen 1...3 Adrenalinstöße und 1...7 Energiepunkte.', '', '', ''),
(1702, 'Hieb des stählernen Fangzahns', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +1..31 Punkte Schaden zu. Wenn Ihr einen Gegner trefft, der zu Boden geworfen wurde, erhaltet Ihr 1..5 Adrenalin.', 'Richtet +1...31 Punkte Schaden an. Ihr erhaltet 1...5 Adrenalinstöße, falls Gegner zu Boden geworfen ist.', '', '', ''),
(1719, 'Schreischuss', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +10..25 Punkte Schaden zu. Wenn Euer Gegner in Hörweite ist, leidet er 5..20 Sekunden lang an einer Blutung.', 'Richtet +10...25 Punkte Schaden an. Verursacht Zustand Blutung (5...20 Sekunden), falls der Gegner in Hörweite ist.', '', '', ''),
(1720, 'Scharfer Pfeil', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Falls Ihr einen kritischen Treffer landet, fügt Ihr zusätzlich 5..25 Punkte Schaden zu.', 'Richtet +5...20 Punkte Schaden an. Richtet zusätzlich +5...25 Punkte Schaden an, falls Ihr einen kritischen Treffer landet.', 'Scharfer Pfeil (PvP)', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +1..5 Punkte Schaden zu. Falls Ihr einen kritischen Treffer landet, fügt Ihr zusätzlich +5..30 Punkte Schaden zu.', 'Richtet +1...5 Punkte Schaden an. Richtet zusätzlich +5...30 Punkte Schaden an, falls Ihr einen kritischen Treffer landet.'),
(1721, 'Gemeinsam toben', '3..15 Sekunden lang greift Ihr und Euer Tiergefährte um 33% schneller an und lauft um 25% schneller.', '3...15 Sekunden: Ihr und euer Tiergefährte greift um 33% schneller an und bewegt Euch um 25% schneller. <i>Euer Tiergefährte muss am Leben sein, sonst keine Wirkung.</i>', '', '', ''),
(1722, 'Gegabelter Pfeil', 'Schießt zwei Pfeile gleichzeitig auf den Gegner ab. Wenn Ihr mit einer Verzauberung oder Verhexung belegt seid, schießt Ihr nur einen Pfeil ab.', 'Ihr feuert zwei Pfeile gleichzeitig ab. <i>Ihr feuert nur einen Pfeil ab, falls Ihr verzaubert oder verhext seid.</i>', '', '', ''),
(1723, 'Unterbrechende Genauigkeit', 'Während der nächsten 36 Sekunden unterbrechen Eure Pfeile bei jedem kritischen Treffer auch Euer Ziel.', '36 Sekunden: Unterbricht jedes Mal eine Aktion, wenn Ihr mit einem Pfeil einen kritischen Treffer landet.', '', '', ''),
(1724, 'Gewandtheit des Profis', '1..20 Sekunden lang greift Ihr um 33% schneller an und Euer Attribut Treffsicherheit ist um 2 erhöht.', '1...20 Sekunden: Ihr greift um 33% schneller an und Euer Attribut Treffsicherheit wird um +2 erhöht.', 'Gewandtheit des Profis (PvP)', '1..20 Sekunden lang greift Ihr um 15% schneller an und Euer Attribut Treffsicherheit ist um 1 erhöht.', '1...20 Sekunden: Ihr greift um 15% schneller an und Euer Attribut Treffsicherheit wird um +1 erhöht.'),
(1725, 'Tosender Wind', 'Erstellt einen Geist der Stufe 1..10. Anfeuerungsrufe und Schreie kosten 1..5 Energiepunkte mehr. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...60 Sekunden Lebensdauer). Schreie und Anfeuerungsrufe kosten Kraturen in Reichweite +1...5 Energiepunkte.', '', '', ''),
(1726, 'Verderben des Magiers-Schuss', 'Wenn dieser Angriff erfolgreich ist, unterbricht er die Aktion des Gegners. Wenn diese Aktion ein Zauber war, wird er zusätzlich 10 Sekundenn lang deaktiviert. Dieser Angriff kann nicht geblockt werden.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Unterbrochener Zauber ist +10 Sekunden lang deaktiviert. Kann nicht geblockt werden.', '', '', ''),
(1727, 'Natürlicher Schwung', '1..8 Sekunden lang lauft Ihr um 33% schneller und habt eine 50%ige Chance, Angriffe zu blocken. Natürlicher Schwung endet, wenn Ihr verhext oder verzaubert werdet.', '1...8 Sekunden: Ihr bewegt Euch um 33% schneller und habt eine 50%ige Chance zu blocken. <i>Endet, sobald Ihr verhext oder verzaubert werdet.</i>', '', '', ''),
(1728, 'Heket-Toben', '5..11 Sekunden lang sind Eure Angriffe um 33% schneller. Diese Haltung endet, sobald Ihr eine Angriffsfertigkeit verwendet.', '5...11 Sekunden: Ihr greift um 33% schneller an. <i>Endet, sobald Ihr eine Angriffsfertigkeit verwendet.</i>', '', '', ''),
(1729, 'Rauchfalle', 'Wird Rauchfalle aktiviert, werden alle Gegner in der Nähe 5..10 Sekunden lang geblendet und benommen. Rauchfalle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Verursacht bei Feinden in der Nähe die Zustände Blindheit und Benommenheit (5...10 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(1730, 'Irritierende Hitze', 'Erstellt einen Geist der Stufe 1..10. Kreaturen in seiner Reichweite, die keine Geister sind, erhalten Adrenalin doppelt so schnell. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...60 Sekunden Lebensdauer). Verdoppelt für Kreaturen in Reichweite die erhaltene Adrenalinmenge.', '', '', ''),
(1731, 'Lautstark war Sogolon', '60 Sekunden lang dauern alle von Euch verwendeten Schreie und Anfeuerungsrufe um 20..50% länger an.', '60 Sekunden: Eure Schreie und Anfeuerungsrufe halten um 20...50% länger an.', '', '', ''),
(1732, 'Vernichtend war Glaive', 'Haltet Glaives Asche bis zu 30..60 Sekunden lang. Während Ihr ihre Asche haltet, haben alle Ritualisten-Fertigkeiten eine Rüstungsdurchdringung von 20%. Wenn Ihr ihre Asche fallen lasst, erleiden alle Feinde in dem Bereich 15..85 Punkte Blitzschaden.', '(30...60 Sekunden). Eure Ritualisten-Fertigkeiten haben eine Rüstungsdurchdringung von 20%. Fallwirkung: Richtet bei allen Feinden im Bereich 15...85 Punkte Schaden an.', 'Vernichtend war Glaive (PvP)', 'Haltet Glaives Asche bis zu 30..60 Sekunden lang. Während Ihr ihre Asche haltet, haben alle Ritualisten-Fertigkeiten eine Rüstungsdurchdringung von 10%. Wenn Ihr ihre Asche fallen lasst, erleiden alle Feinde in dem Bereich 15..85 Punkte Blitzschaden.', '(30...60 Sekunden). Eure Ritualisten-Fertigkeiten haben eine Rüstungsdurchdringung von 10%. Fallwirkung: Richtet bei allen Feinden im Bereich 15...85 Punkte Schaden an.'),
(1733, 'Stoß des Ausübers', 'Der Gegner erleidet 5..50 Punkte Blitzschaden. Wenn Ihr unter dem Einfluss eines Waffenzaubers steht, fügt Ihr zusätzlich 10..40 Punkte Blitzschaden zu.', 'Richtet 5...50 Punkte Blitzschaden an. Richtet zusätzlich 10...40 Punkte Blitzschaden an, falls Ihr unter dem Einfluss eines Waffenzaubers steht.', '', '', ''),
(1734, 'Wütendes Starren', 'Vernichtet den anvisierten Geist und erstellt einen Geist der Wut der Stufe 1..14. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu. Dieser Geist stirbt nach 30..60 Sekunden.', 'Vernichtet einen Geist. Erstellt einen Geist der Stufe 1...14 (30...60 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an.', 'Wütendes Starren (PvP)', 'Vernichtet den anvisierten verbündeten Geist und erstellt einen Geist der Wut der Stufe 1..10. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu. Dieser Geist stirbt nach 30..60 Sekunden.', 'Zerstört einen verbündeten Geist. Erstellt einen Geist der Stufe 1...10 (30...60 Sekunden Lebensdauer.) Seine Angriffe richten 5...20 Punkte Schaden an.'),
(1736, 'Geistesstärke', '15..60 Sekunden lang fügen Eure Angriffe 5..35 Punkte mehr Schaden zu, wenn Ihr unter den Auswirkungen eines Waffenzaubers steht.', '15...60 Sekunden: Eure Angriffe richten +5...35 Punkte Schaden an, falls Ihr unter dem Einfluss eines Waffenzaubers steht.', '', '', ''),
(1737, 'Eifer des Ausübers', '10..30 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr einen Waffenzauber wirkt, 1..5 Energiepunkte.', '10...30 Sekunden: Ihr erhaltet jedes Mal 1...5 Energiepunkte, wenn Ihr einen Waffenzauber wirkt.', '', '', ''),
(1738, 'Sicht jenseits der Sicht', 'Ihr könnt 8..20 Sekunden lang nicht geblendet sein.', '8...20 Sekunden: Ihr seid immun gegen Blindheit.', '', '', ''),
(1739, 'Erneuernde Erinnerungen', '5..20 Sekunden lang kostet Euch beim Halten eines Gegenstandes das Wirken aller Waffen- und Gegenstandszauber 5..35% weniger Energie.', '5...20 Sekunden: Eure Waffen- und Gegenstandszauber kosten 5...35% weniger Energie. <i>Ihr müsst einen Gegenstand halten, sonst keine Wirkung.</i>', '', '', ''),
(1740, 'Mittel des Ausübers', 'Wann immer Ihr innerhalb der nächsten 10..30 Sekunden einen Waffenzauber auf einen Verbündeten (bzw. Euch selbst) wirkt, verliert dieser Verbündete einen Zustand.', '10...30 Sekunden: Jedes Mal, wenn Ihr einen Waffenzauber wirkt, verliert der betreffende Verbündete einen Zustand.', '', '', ''),
(1741, 'Geisterspiegel-Licht', 'Der Verbündete wird um 15..90 Lebenspunkte geheilt. Wenn Ihr Euch in der Nähe eines Geistes befindet, werdet Ihr ebenfalls um 15..90 Lebenspunkte geheilt.', 'Heilt um 15...90 Lebenspunkte. Ihr erhaltet 15...90 Lebenspunkte, falls Ihr Euch in Hörweite eines Geistes befindet. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(1742, 'Siegel der Macht der Geister', '5..20 Sekunden greifen alle von Euch kontrollierten Geister in Hörweite um 33% schneller an und richten um +5..10 Punkte mehr Schaden an.', '5...20 Sekunden: Alle von Euch kontrollierten Geister in Hörweite greifen um 33% schneller an und richten um +5...10 Punkte mehr Schaden an.', 'Siegel der Macht der Geister (PvP)', 'Die Angriffe der verbündeten herbeigerufenen Kreatur fügen 5..35 Punkte mehr Schaden zu. Diese Kreatur wird nach 10 Sekunden vernichtet.', 'Die verbündete herbeigerufene Kreatur richtet +5...35 Punkte Schaden an. <i>Diese Kreatur wird nach 10 Sekunden vernichtet</i>'),
(1743, 'Siegel der Bindung', 'Ihr verliert 200..50 Lebenspunkte und übernehmt die Kontrolle über den vom Gegner kontrollierten Geist. (Bei einer Macht des Herbeirufens von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)', 'Ihr verliert 200...50 Lebenspunkte. Übernehmt die Kontrolle über den vom Gegner kontrollierten Geist. Mindestens Macht des Herbeirufens 5, sonst 50%ige Chance auf Misserfolg.', '', '', ''),
(1744, 'Angriff des Verwalters', 'Der Gegner erleidet 20..75 Punkte Blitzschaden. Wenn Ihr einen Gegenstand haltet, erhaltet Ihr 5 Energiepunkte und 5..50 Lebenspunkte.', 'Richtet 20...75 Punkte Blitzschaden an. Ihr erhaltet 5 Energiepunkte und 5...50 Lebenspunkte, falls Ihr einen Gegenstand haltet.', '', '', ''),
(1745, 'Qual', 'Erstellt einen Geist der Stufe 1..11. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu und verursachen bei verhexten Feinden doppelten Schaden. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...11 (15...45 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an. Doppelter Schaden bei verhexten Feinden.', 'Qual (PvP)', 'Erstellt einen Geist der Stufe 1..7. Die Angriffe dieses Geistes fügen 5..20 Punkte Schaden zu und verursachen bei verhexten Feinden doppelten Schaden. Dieser Geist stirbt nach 15..45 Sekunden.', 'Erstellt einen Geist der Stufe 1...7 (15...45 Sekunden Lebensdauer). Seine Angriffe richten 5...20 Punkte Schaden an. Doppelter Schaden bei verhexten Feinden.'),
(1747, 'Ermächtigung', 'Erstellt einen Geist der Stufe 1..14. Alle Verbündeten in seiner Reichweite, die einen Gegenstand halten, erhalten +15..45 maximale Lebenspunkte und +10 maximale Energiepunkte. Dieser Geist stirbt nach 15..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (15...60 Sekunden Lebensdauer). Verbündete in Reichweite erhalten +15...45 maximale Lebenspunkte und +10 maximale Energiepunkte, falls sie einen Gegenstand halten.', 'Ermächtigung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Alle Verbündeten in seiner Reichweite, die einen Gegenstand halten, erhalten +15..45 maximale Lebenspunkte und +10 maximale Energiepunkte. Dieser Geist stirbt nach 15..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (15...60 Sekunden Lebensdauer). Verbündete in Reichweite erhalten +15...45 maximale Lebenspunkte und +10 maximale Energiepunkte, falls sie einen Gegenstand halten.'),
(1748, 'Gesundung', 'Erstellt einen Geist der Stufe 1..14. Zustände, mit denen Verbündete in seiner Reichweite belegt sind, enden um 20..50% schneller. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...14 (30...60 Sekunden Lebensdauer). Zustände auf Verbündeten in Reichweite enden um 20...50% früher.', 'Gesundung (PvP)', 'Erstellt einen Geist der Stufe 1..10. Zustände, mit denen Verbündete in seiner Reichweite belegt sind, enden um 20..50% schneller. Dieser Geist stirbt nach 30..60 Sekunden.', 'Erstellt einen Geist der Stufe 1...10 (30...60 Sekunden Lebensdauer). Zustände auf Verbündeten in Reichweite enden um 20...50% früher.'),
(1749, 'Waffe der Wut', '5..20 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) bei jedem erfolgreichen Treffer mit einem Angriff um 5..50% mehr Adrenalin und 1 Energiepunkt.', '5...20 Sekunden: +5...50% zur erhaltenen Adrenalinmenge und +1 Energiepunkt, wenn ein Angriff des Verbündeten (bzw. von Euch selbst) trifft.', '', '', ''),
(1750, 'Xinraes Waffe', 'Wenn der Verbündete (bzw. Ihr selbst) innerhalb der nächsten 8 Sekunden das nächste Mal Schaden durch einen Feind erleidet, ist dieser Schaden auf 5% der maximalen Lebenspunkte des Verbündeten begrenzt und er entzieht dem Gegner bis zu 20..80 Lebenspunkte.', '8 Sekunden: Wenn der Verbündete (bzw. Ihr selbst) das nächste Mal Schaden durch einen Feind erleidet, ist dieser Schaden auf 5% der maximalen Lebenspunkte des Verbündeten begrenzt und er entzieht dem Feind bis zu 20...80 Lebenspunkte.', '', '', ''),
(1751, 'Waffe des Kriegshetzers', 'Während der nächsten 3..13 Sekunden wird jedes Mal, wenn der Verbündete (bzw. Ihr selbst) einen Feind angreift, der selbst nicht angreift, dieser Feind unterbrochen.', '3...13 Sekunden: Angriffe unterbrechen eine Aktion.', '', '', ''),
(1752, 'Waffe der Behebung', 'Wenn der Verbündete (bzw. Ihr selbst) innerhalb der nächsten 8 Sekunden das nächste Mal durch einen Gegner Schaden erleidet oder Lebenspunkte entzogen bekommt, entzieht er diesem Gegner bis zu 15..75 Lebenspunkte und verliert 1 Zustand.', '(8 Sekunden.) Wenn der Verbündete (bzw. Ihr selbst) das nächste Mal durch einen Gegner Schaden erleidet oder Lebenspunkte entzogen bekommt, entzieht er diesem Gegner 15...75 Lebenspunkte und verliert 1 Zustand.', '', '', ''),
(1753, 'Zerreißender Schwung', 'Ihr richtet +5..20 Punkte Schaden an und verliert 1 Derwisch-Verzauberung. Wenn eine Verzauberung entfernt wird, entfernt Ihr eine Verzauberung von jedem Feind, den Ihr trefft.', 'Richtet +5...20 Punkte Schaden an. Ihr verliert 1 Derwisch-Verzauberung. Entfernungseffekt: Getroffene Feinde verlieren eine Verzauberung.', '', '', ''),
(1754, 'Attacke', '3..15 Sekunden lang erfolgen Eure Angriffe, Bewegungen und Euer Adrenalingewinn um 25% schneller.', '(3...15 Sekunden.) Eure Angriffe, Bewegungen und Euer Adrenalingewinn erfolgen um 25% schneller.', 'Attacke (PvP)', '2..8 Sekunden lang erfolgen Eure Angriffe und Euer Adrenalingewinn um 25% schneller.', '(2...8 Sekunden.) Eure Angriffe und Euer Adrenalinweginn erfolgen um 25% schneller.'),
(1755, 'Mystische Verdorbenheit', 'Alle umstehenden Feinde leiden 1..2 Sekunden lang an Krankheit. 20 Sekunden lang geschieht nichts. Wenn Ihr verzaubert seid, während Ihr diese Fertigkeit aktiviert, wird die Dauer von Krankheit verdoppelt. Wenn diese Verzauberung endet, werden alle Gruppenmitglieder in Hörweite von Krankheit geheilt.', '(20 Sekunden.) Anfangswirkung: Alle umstehenden Feinde leiden unter Krankheit.<sub>sic</sub> (1...2 Sekunden.) Doppelte Dauer, wenn Ihr bereits verzaubert seid. Wirkung am Ende: Gruppenmitglieder in Hörweite werden von Krankheit geheilt.', '', '', ''),
(1756, 'Grenths Griff', '20 Sekunden lang verkrüppeln Eure Angriffsfertigkeiten den Gegner außerdem 1..11 Sekunden lang, wenn Ihr eine Kältewaffe führt, und Ihr übertragt 1 Zustand von Euch auf diesen Gegner.', '(20 Sekunden.) Eure Angriffsfertigkeiten verursachen Verkrüppelung (1...11 Sekunden) und übertragen 1 Zustand. <i>Keine Wirkung, falls Ihr keine Kältewaffe benutzt.</i>', '', '', ''),
(1757, 'Dornenschleier', 'Wenn Ihr diese Verzauberung wirkt, erleiden alle Feinde in der Nähe 5..50 Stichschaden. 5..25 Sekunden lang erleidet Ihr von Zaubern 5..35% weniger Schaden.', '(5...25 Sekunden.) Zauberschaden um 5...35% verringert. Anfangswirkung: In der Nähe befindliche Feinde erleiden 5...50 Punkte Stichschaden.', '', '', ''),
(1758, 'Griff des Läufers', 'Während der nächsten 5..20 Sekunden führen Angriffe gegen Feinde, die sich bewegen, 2..9 Sekunden lang zu Verkrüppelung. Wenn Ihr diese Verzauberung wirkt, werden Verkrüppelung und 1 weiterer Zustand von Euch entfernt. Diese Verzauberung endet, wenn Ihr 1..3 Mal Verküppelung zugefügt habt.', '(5...20 Sekunden.) Angriffe auf sich bewegende Feinde fügen zusätzlich "Verkrüppelung" (2...9 Sekunden) zu. Anfangswirkung: Ihr verliert "Verkrüppelung" und 1 weiteren Zustand. Endet, wenn Ihr 1...3 Mal Verküppelung zugefügt habt.', '', '', ''),
(1759, 'Gelübde der Stärke', '15 Sekunden lang fügt Ihr allen Euch umstehenden Gegnern 10..25 Punkte Hiebschaden zu, wenn Ihr einen Gegner mit Eurer Sense angreift.', '(15 Sekunden.) Angriffe mit einer Sense fügen allen Euch umstehenden Gegnern 10...25 Punkte Hiebschaden zu.', '', '', ''),
(1760, 'Ebon-Staubaura', 'Wenn Ihr diese Verzauberung wirkt, werden alle Feinde in der Nähe 1..7 Sekunden lang geblendet. 30 Sekunden lang fügen Eure Nahkampfangriffe +3..15 Punkte Erdschaden zu, wenn Ihr eine Erdwaffe führt. Wenn diese Verzauberung endet, werdet Ihr von Blindheit geheilt.', '(30 Sekunden.) Eure Nahkampfangriffe fügen +3...15 Punkte Erdschaden zu. Anfangswirkung: Blendet Feine<sub>sic</sub> in der Nähe 1...7 Sekunden lang. Wirkung am Ende: Entfernt Blindheit. <i>Keine Wirkung, falls ihr keine Erdwaffe benutzt.</i>', '', '', ''),
(1761, 'Eifriges Gelübde', '20 Sekunden lang beträgt Eure Energieregeneration -3 und Ihr bekommt jedes Mal, wenn einer Eurer Angriffe trifft, 1..6 Energiepunkte.', '20 Sekunden: Ihr erhaltet jedes Mal 1...6 Energiepunkte, wenn ein Angriff von Euch trifft. <i>Ihr habt -3 Energieregeneration.</i>', '', '', ''),
(1762, 'Herz der Wut', '2..10 Sekunden lang sind Eure Angriffe um 25% schneller.', '(2...10 Sekunden.) Ihr greift um 25% schneller an.', 'Herz der Wut (PvP)', '2..10 Sekunden lang sind Eure Angriffe um 25% schneller.', '(2...10 Sekunden.) Ihr greift um 25% schneller an.'),
(1763, 'Eifrige Erneuerung', 'Alle in der Nähe befindlichen Gegner erleiden 5..30 Punkte heiligen Schaden. 5..25 Sekunden lang beträgt Eure Energieregeneration -1 und ihr bekommt jedes Mal, wenn einer Euer Angriffe trifft, 1 Energiepunkt. Wenn diese Verzauberung vorzeitig endet, erhaltet ihr 1..5 Energiepunkte.', '(5...25 Sekunden.) Anfangswirkung: Richtet bei in der Nähe befindlichen Feinden 5...30 Punkte heiligen Schaden an. Eure Energieregeneration beträgt -1 und Ihr bekommt jedes Mal, wenn einer Eurer Angriffe trifft, 1 Energiepunkt. Wenn diese Verzauberung vorzeitig endet, erhaltet ihr 1...5 Energiepunkte.', '', '', ''),
(1764, 'Einblick des Angreifers', '3..10 Sekunden lang habt Ihr eine Chance von 50%, beim Angreifen zu blocken. Wenn diese Verzauberung endet, fügt Ihr allen umstehenden Feinden 3..15 Sekunden lang Schwäche zu.', '(3...10 Sekunden.) Gewährt eine Chance von 50%, beim Angreifen zu blocken. Wirkung am Ende: Fügt umstehenden Feinden 3...15 Sekunden lang Schwäche zu.', '', '', ''),
(1765, 'Zerreißende Aura', 'Wenn Ihr diese Verzauberung wirkt, erleiden alle Feinde in der Nähe 10..40 Punkte Kälteschaden. 30 Sekunden lang entfernen Eure Angriffsfertigkeiten Verzauberungen von zu Boden geworfenen Feinden. Wenn diese Verzauberung endet, leiden in der Nähe befindliche Feinde 1..10 Sekunden unter "Beschädigte Rüstung".', '(30 Sekunden.) Eure Angriffsfertigkeiten entfernen Verzauberungen von zu Boden geworfenen Feinden. Anfangswirkung: Richtet bei allen Feinden in der Nähe 10...40 Punkte Kälteschaden an. Wirkung am Ende: In der Nähe befindliche Feinde leiden 1...10 Sekunden lang an Beschädigter Rüstung.', '', '', ''),
(1766, 'Anmut des Federfußes', '5..20 Sekunden lang bewegt Ihr Euch um 25% schneller und Zustände enden um 25% schneller.', '5...20 Sekunden: Ihr bewegt Euch um 25% schneller und Zustände auf Euch enden um 25% früher.', '', '', ''),
(1767, 'Schwung des Schnitters', 'Wenn dieser Angriff erfolgreich ist, leidet Euer Gegner 3..15 Sekunden lang an Verkrüppelung. Ihr verliert 1 Derwisch-Verzauberung. Wenn eine Verzauberung entfernt wurde, wirft dieser Angriff 2..3 Sekunden zu Boden.', 'Verursacht Verkrüppelung (3...15 Sekunden.). Ihr verliert 1 Derwisch-Verzauberung. Entfernungseffekt: Wirft 2...3 Sekunden lang zu Boden.', '', '', ''),
(1768, 'Eile des Läufers', '2..8 Sekunden lang bewegt Ihr Euch um 25% schneller und fügt Feinden, die sich bewegen, +3..12 Punkte mehr Schaden zu.', '(2...8 Sekunden.) Ihr bewegt Euch um 25% schneller und richtet bei Feinden, die sich bewegen, +3...12 Punkte Schaden an.', '', '', ''),
(1769, 'Konzentrierter Zorn', '45 Sekunden lang erhaltet Ihr um 0..150% mehr Adrenalin.', '45 Sekunden: Ihr erhaltet 0...150% mehr Adrenalin.', '', '', ''),
(1770, 'Natürliche Laune', '4..10 Sekunden lang erhaltet Ihr um 33% mehr Adrenalin, wenn Ihr nicht mit einer Verzauberung belegt seid.', '4...10 Sekunden: Ihr erhaltet 33% mehr Adrenalin. <i>Keine Wirkung, falls ihr verzaubert seid.</i>', '', '', ''),
(1771, 'Gesang der Wiederherstellung', '10 Sekunden lang wird jedes Gruppenmitglied in Hörweite, wenn es das nächste Mal eine Fertigkeit einsetzt, um 45..110 Lebenspunkte geheilt.', '10 Sekunden: Gruppenmitglieder in Hörweite werden bei Benutzung ihrer nächsten Fertigkeit um 45...110 Lebenspunkte geheilt.', 'Gesang der Wiederherstellung (PvP)', '10 Sekunden lang erhält jedes Gruppenmitglied in Hörweite, wenn es das nächste Mal eine Fertigkeit einsetzt, 45..110 Lebenspunkte.', '10 Sekunden: Gruppenmitglieder in Hörweite erhalten bei Benutzung ihrer nächsten Fertigkeit 45...110 Lebenspunkte.'),
(1772, 'Lyrik der Läuterung', 'Während der nächsten 5..20 Sekunden lang verliert jeder Verbündete in Hörweite bei der nächsten Verwendung eines Siegels 1 Zustand.', '5...20 Sekunden: Verbündete in Hörweite verlieren bei Benutzung Ihres nächsten Siegels einen Zustand.', '', '', ''),
(1773, 'Wut des Soldaten', '10..35 Sekunden lang greift Ihr um 33% schneller an und erhaltet um 33% mehr Adrenalin, wenn Ihr mit einem Anfeuerungsruf oder Schrei belegt seid, allerdings habt Ihr -20 Rüstung.', '(10...35 Sekunden.) Ihr greift um 33% schneller an und erhaltet um 33% mehr Adrenalin, falls Ihr unter dem Einfluss eines Schreis oder Anfeuerungsrufs steht. <i>Ihr habt -20 Rüstung.</i>', '', '', ''),
(1774, 'Aggressiver Refrain', '5..25 Sekunden lang sind Eure Angriffe um 25% schneller, aber Ihr habt -20 Rüstung. Dieses Echo wird jedes Mal wiederholt, wenn ein auf Euch gerichteter Anfeuerungsruf oder Schrei endet.', '(5...25 Sekunden.) Ihr greift um 25% schneller an. Erneuerung: Jedes Mal, wenn ein Schrei oder ein Anfeuerungsruf auf Euch endet. <i>Ihr habt -20 Rüstung.</i>', '', '', ''),
(1775, 'Energie bringendes Finale', '10..35 Sekunden lang erhält der Nicht-Geister-Verbündete (bzw. Ihr selbst) jedes Mal, wenn ein auf ihn gerichteter Anfeuerungsruf oder Schrei endet, 1 Energiepunkt.', '10...35 Sekunden: Der Verbündete (bzw. Ihr selbst) erhält jedes Mal 1 Energiepunkt, wenn ein Schrei oder Anfeuerungsruf auf ihm endet. <i>Nicht auf Geister anwendbar.</i>', '', '', ''),
(1776, 'Siegel der Aggression', 'Wenn Ihr mit einem Schrei oder Anfeuerungsruf belegt seid, erhaltet Ihr 2 Adrenalinstöße.', 'Ihr erhaltet 2 Adrenalinstöße, falls Ihr unter dem Einfluss eines Schreis oder Anfeuerungsrufs steht.', '', '', ''),
(1777, 'Heilmittel-Siegel', 'Ihr verliert 1 Zustand.', 'Ihr verliert einen Zustand.', '', '', ''),
(1778, 'Siegel der Wiederkehr', 'Belebt das ausgewählte Gruppenmitglied mit 5..15% seiner Lebens- und 1..4% seiner Energiepunkte pro Gruppenmitglied in Hörweite.', 'Das Gruppenmitglied wird wiederbelebt (5...15% Lebenspunkte und 1...4% Energiepunkte pro Gruppenmitglied in Hörweite).', 'Siegel der Wiederkehr (PvP)', 'Belebt das ausgewählte Gruppenmitglied mit 5..15% seiner Lebens- und 1..4% seiner Energiepunkte pro Gruppenmitglied in Hörweite.', 'Das Gruppenmitglied wird wiederbelebt (5...15% Lebenspunkte und 1...4% Energiepunkte pro Gruppenmitglied in Hörweite).'),
(1779, '"Nehmt Euch Zeit!"', 'Ihr erhaltet pro Gruppenmitglied in Hörweite 1 Adrenalinstoß (maximal 1..5 Adrenalin).', 'Ihr erhaltet für jedes Gruppenmitglied in Hörweite 1 Adrenalinstoß (maximal 1...5).', '', '', ''),
(1780, '"Can\'t touch this!"', 'Während der nächsten 20 Sekunden scheitern die nächsten gegen Gruppenmitglieder in in Hörweite gerichteten 1..5 Berührungs-Fertigkeiten.', '20 Sekunden: Die nächsten 1...5 gegen Gruppenmitglieder in Hörweite gerichteten Berührungs-Fertigkeiten scheitern.', '"Darf nicht angerührt werden!" (PvP)', 'Während der nächsten 20 Sekunden scheitern die nächsten gegen Euch gerichteten 1..5 Berührungs-Fertigkeiten.', ''),
(1781, '"Findet ihren Schwachpunkt!"', 'Während der nächsten 5..20 Sekunden richtet der Verbündete (bzw. Ihr selbst) mit dem nächsten Treffer +5..50 Schaden an und verursacht damit 5..20 Sekunden lang eine Tiefe Wunde.', '(5...20 Sekunden.) Dieser Verbündete verursacht mit dem nächsten Angriff +5...50 Schaden sowie (5...20 Sekunden) den Zustand Tiefe Wunde.', '"Findet ihren Schwachpunkt!" (PvP)', '5..20 Sekundenn lang verursacht der nächste kritische Treffer des Verbündeten (bzw. Euer eigener) auch 5..20 Sekunden lang eine Tiefe Wunde.', '5...20 Sekunden: Verbündeter (bzw. Ihr selbst) verursacht mit dem nächsten kritischen Treffer den Zustand Tiefe Wunde (5...20 Sekunden).'),
(1782, '"Die Macht ist Euer!"', '3 Sekunden lang erhalten alle Verbündeten in Hörweite 0..1 Energieregeneration.', '3 Sekunden: Verbündete in Hörweite erhalten 0...1 Energieregeneration.', '', '', ''),
(1783, 'Speer des Würgers', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..25 Punkte Schaden zu. Wenn der Gegner mehr Lebenspunkte als Ihr hat, leidet er 5..20 Sekunden lang an einer Tiefen Wunde.', 'Richtet +5...25 Punkte Schaden an. Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner mehr Lebenspunkte hat als Ihr.', '', '', ''),
(1784, 'Schneller Speer', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr mit einer Verzauberung belegt seid, fliegt dieser Speer doppelt so schnell und kann nicht geblockt werden.', 'Richtet +5...20 Punkte Schaden an. Dieser Speer fliegt mit doppelter Geschwindigkeit und kann nicht geblockt werden, falls Ihr verzaubert seid.', '', '', ''),
(1814, 'Blick des Lichtbringers', 'Der ausgewählte dämonische Diener Abaddons erleidet 100 Punkte Sakral-Schaden und wird unterbrochen. Diese Fertigkeit fügt pro Lichtbringertitelrang, den Ihr erreicht habt, einem zusätzlichen Feind in dem Bereich 100 Punkte Sakral-Schaden zu.', 'Der ausgewählte dämonische Diener Abaddons erleidet 100 Punkte Sakral-Schaden und wird unterbrochen. Pro Lichtbringertitelrang erleidet ein zusätzlicher Feind im Bereich ebenfalls 100 Punkte Sakral-Schaden.', '', '', ''),
(1815, 'Lichtbringer-Siegel', 'Wenn Ihr Euch im Bereich eines dämonischen Dieners Abaddons befindet, erhaltet Ihr 3..5 Adrenalinstöße und 16..24 Energiepunkte.', 'Ihr erhaltet 3...5 Adrenalinstöße und 16...24 Energiepunkte, wenn Ihr Euch im Bereich eines dämonischen Dieners Abaddons befindet.', '', '', ''),
(1816, 'Sonnenspeer-Wiedergeburtssiegel', 'Das tote Gruppenmitglied wird mit vollen Lebenspunkten und 10% Energiepunkte pro Sonnenspeer-Rang, den Ihr erreicht habt, wiederbelebt und unternimmt einen Schattenschritt an Eure Position. Dieses Siegel kann nur einmal pro Mission verwendet werden, es sei denn, es wird durch einen Moralschub wieder aufgeladen.', 'Das tote Gruppenmitglied wird wiederbelebt (volle Lebenspunkte, 10% Energiepunkte pro Sonnenspeer-Rang, den Ihr erreicht habt) und unternimmt einen Schattenschritt an Eure Position. <i>Dieses Siegel wird nur dann wieder aufgeladen, wenn Ihr einen Moralschub erhaltet.</i>', '', '', ''),
(1948, 'Schattenzuflucht (Luxon)', 'Ihr seid 5 Sekunden lang geblendet. 10 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration in Höhe von 7..10 und +40 Rüstung.', '10 Sekunden: Ihr erhaltet +7...10 Lebenspunktregeneration und +40 Rüstung. <i>Ihr seid geblendet (5 Sekunden)</i>', '', '', ''),
(1949, 'Äther-Alptraum (Luxon)', 'Der Gegner verliert 5..8 Energiepunkte. Der Gegner und alle Feinde in dem Bereich erleiden 10 Sekunden lang eine Lebenspunktdegeneration in Höhe der verlorenen Energiepunkte.', 'Lässt 5...8 Energiepunkte verlieren. Der Gegner und Feinde im Gebiet<sub>sic</sub> haben -1 Lebenspunktdegeneration pro verlorenem Energiepunkt (10 Sekunden).', '', '', ''),
(1950, 'Siegel der Verdorbenheit (Luxon)', 'Der Gegner und alle Feinde in der Nähe erleiden 20..30 Punkte Schaden. Pro betroffenen Feind, der unter einem Zustand oder einer Verhexung leidet, erhaltet Ihr 2 Punkte Energie (maximal 12..20 Punkte).', 'Richtet beim Gegner und Feinden in der Nähe 20...30 Punkte Schaden an. Ihr erhaltet für jeden dieser Gegner mit einem Zustand oder einer Verhexung 2 Energiepunkte (maximal 12...20).', '', '', ''),
(1951, 'Elementarlord (Luxon)', '40..60 Sekunden lang erhalten Eure Elementarattribute einen Schub von 1. Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr 1 Energiepunkt pro jeweils 10 Ränge Energiespeicherung und werdet um 100..300% der Energiekosten dieses Zaubers geheilt.', '(40...60 Sekunden.) Eure Elementarattribute erhalten einen Schub von 1. Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr 1 Energiepunkt pro 10 Ränge in Energiespeicherung und werdet um 100...300% der Energiekosten dieses Zaubers geheilt.', '', '', ''),
(1952, 'Selbstloser Geist (Luxon)', '15..20 Sekunden lang kosten von Euch auf einen anderen Verbündeten gewirkte Zauber 3 Punkte weniger Energie.', '(15...20 Sekunden.) Zauber, die Ihr auf einen weiteren Verbündeten wirkt, kosten 3 Punkte weniger Energie.', '', '', ''),
(1953, 'Dreifachschuss (Luxon)', '3 Pfeile werden gleichzeitig auf den Gegner abgefeuert. Diese Pfeile fügen 40..25% weniger Schaden zu.', 'Ihr feuert drei Pfeile gleichzeitig auf den Gegner ab. <i>Diese Pfeile richten 40...25% weniger Schaden an.</i>', '', '', ''),
(1954, '"Rettet Euch selbst!" (Luxon)', '4..6 Sekunden lang erhalten alle anderen Gruppenmitglieder +100 Rüstung.', '4...6 Sekunden: Alle anderen Gruppenmitglieder erhalten +100 Rüstung.', '', '', ''),
(1955, 'Aura der Heiligen Macht (Luxon)', '45 Sekunden lang fügt Ihr, wann immer Ihr eine Derwisch-Verzauberung verliert, allen umstehenden Feinden 20..25 Punkte heiligen Schaden zu.', '(45 Sekunden.) Immer wenn Ihr eine Derwisch-Verzauberung verliert, erleiden alle umstehenden Feinde 20...25 Punkte heiligen Schaden.', '', '', ''),
(1957, 'Speer der Wut (Luxon)', 'Der Angriff fügt +30..40 Punkte Schaden zu. Wenn er einen Feind trifft, der an einem Zustand leidet, erhaltet Ihr 3..6 Adrenalinstöße.', 'Richtet +30...40 Punkte Schaden an. Ihr erhaltet 3...6 Adrenalinstöße, falls Ihr einen Feind mit einem Zustand trefft.', '', '', ''),
(1986, 'Vampirangriff', 'Muss einem Begleithandangriff folgen. Wenn dieser Angriff erfolgreich ist, entzieht Ihr 10..40 Lebenspunkte.', 'Entzieht 10...40 Lebenspunkte, falls dieser Angriff trifft. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(1987, 'Lotus-Stoß', 'Muss einem Leithandangriff folgen. Wenn dieser Angriff erfolgreich ist, fügt er +10..25 Punkte Schaden zu und Ihr erhaltet 5..20 Energiepunkte.', 'Richtet +10...25 Punkte Schaden an; Ihr erhaltet 5...20 Energiepunkte. <i>Muss einem Leithandangriff folgen.</i>', '', '', ''),
(1988, 'Goldfangstoß', 'Muss einem Leithandangriff folgen. Wenn Ihr verzaubert seid und dieser Angriff einen Treffer landet, leidet der Gegner 5..20 Sekunden lang an einer Tiefen Wunde.', 'Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls Ihr verzaubert seid.', '', '', ''),
(1990, 'Fallender Lotus-Stoß', 'Muss einen zu Boden geworfenen Gegner treffen. Wenn Ihr erfolgreich seid, fügt Ihr +15..35 Punkte Schaden zu und erhaltet 1..12 Energiepunkte.', 'Richtet +15...35 Punkte Schaden an; Ihr erhaltet 1...12 Energiepunkte. <i>Gegner muss zu Boden geworfen sein, sonst keine Wirkung.</i>', '', '', ''),
(1991, 'Siegel des Sadisten', 'Ihr heilt Euch pro Zustand auf dem Gegner 10..40 um Lebenspunkte.', 'Ihr heilt Euch pro Zustand auf dem Gegner 10...40 um Lebenspunkte.', '', '', ''),
(1992, 'Siegel der Ablenkung', 'Falls der Gegner einen Zauber wirkt, wird dieser unterbrochen und pro Siegel, das Ihr ausgerüstet habt, 1..5 Sekunden lang deaktiviert.', 'Unterbricht einen Zauber. Unterbrechungswirkung: Der Zauber des Gegners wird deaktiviert (1...5 Sekunden für jedes Siegel, das Ihr ausgerüstet habt).', '', '', ''),
(1993, 'Siegel der Zurückrufung', '10 Sekunden lang beträgt Eure Energieregeneration -4. Wenn dieser Effekt endet, erhaltet Ihr 13..20 Energiepunkte.', '10 Sekunden: Ihr habt -4 Energieregeneration. Wirkung am Ende: Ihr erhaltet 13...20 Energiepunkte.', '', '', ''),
(1994, 'Kraftsperre', 'Falls der Gegner einen Zauber oder Anfeuerungsruf wirkt, wird dieser unterbrochen und zusätzlich 5..13 Sekunden lang deaktiviert.', 'Unterbricht einen Zauber oder Anfeuerungsruf. Unterbrechungswirkung: Unterbrochener Zauber oder Anfeuerungsruf wird +5...13 Sekunden lang deaktiviert.', '', '', ''),
(1995, 'Spare in der Zeit ...', 'Wenn der Gegner keinen Zauber wirkt oder angreift, erhaltet Ihr 8..13 Energiepunkte.', 'Ihr erhaltet 8...13 Energiepunkte, falls der Gegner gerade nicht angreift oder einen Zauber wirkt.', '', '', ''),
(1996, 'Summe aller Ängste', '1..10 Sekunden lang sind die Bewegungen, Angriffe und Wirkgeschwindigkeit von Zaubern des Gegners um 33% langsamer.', '1...10 Sekunden: Bewegung, Angriffe und das Wirken von Zaubern sind beim Gegner um 33% verlangsamt.', '', '', ''),
(1997, 'Verkümmernde Aura', '5..20 Sekunden lang verursachen die Nahkampfangriffe des Verbündeten (bzw. Eure eigenen) 5..20 Sekunden lang Schwäche.', '5...20 Sekunden: Die Nahkampfangriffe des Verbündeten (bzw. von Euch selbst) verursachen den Zustand Schwäche (5...20 Sekunden).', '', '', ''),
(1998, 'Kakophonie', '10 Sekunden lang erleidet der Gegner jedes Mal, wenn er einen Schrei oder Anfeuerungsruf benutzt, 30..105 Punkte Schaden.', '10 Sekunden: Richtet jedes Mal 30...105 Punkte Schaden an, wenn der Gegner einen Schrei oder Anfeuerungsruf benutzt.', '', '', ''),
(1999, 'Umarmung des Winters', 'Während der nächsten 2..6 Sekunden bewegt sich der Gegner um 66% langsamer und erleidet 5..15 Punkte Schaden pro Sekunde, während er sich bewegt.', '(2...6 Sekunden.) Der Gegner bewegt sich um 66% langsamer und erleidet 5...15 Punkte Schaden pro Sekunde, während er sich bewegt.', '', '', ''),
(2000, 'Irdene Fesseln', '3 Sekunden lang bewegen sich der Gegner und alle Feinde in der Nähe um 90% langsamer. Wenn Irdene Fesseln endet, verursacht es Schwäche für 5..20 Sekunden.', '(3 Sekunden.) Der Gegner und alle umstehenden Feinde bewegen sich um 90% langsamer. Verursacht Schwäche für 5...20 Sekunden, wenn es endet.', '', '', ''),
(2001, 'Kreis der Schwäche', 'Erzeugt an Eurer gegenwärtigen Position einen Kreis der Schwäche. 5..20 Sekunden lang werden Feinde in diesem Bereich jedes Mal, wenn sie Elementarschaden erleiden, für 5..20 Sekunden geschwächt.', '5...20 Sekunden: Verursacht bei allen Feinden in dieser Abwehr, die Elementarschaden erleiden, den Zustand Schwäche (5...20 Sekunden).', '', '', ''),
(2002, 'Glyphe der Schnelligkeit', '15 Sekunden lang werden Eure nächsten 1..5 Zauber um 25% schneller wieder aufgeladen und Geschosse von ihnen bewegen sich um 200% schneller.', '15 Sekunden: Eure nächsten 1...5 Zauber laden sich um 25% schneller auf und mit ihnen abgefeuerte Geschosse bewegen sich um 200% schneller.', '', '', ''),
(2003, 'Verhexung heilen', 'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). Wenn eine Verhexung entfernt wurde, wird der Verbündete um 30..120 Lebenspunkte geheilt.', 'Entfernt eine Verhexung. Entfernungswirkung: Heilt um 30...120 Lebenspunkte.', '', '', ''),
(2004, 'Zustand zerschlagen', 'Entfernt einen Zustand vom Verbündeten (bzw. Euch selbst). Wenn ein Zustand entfernt wurde, erleiden Gegner in der Nähe 10..60 Punkte heiligen Schaden.', 'Entfernt einen Zustand. Entfernungswirkung: Richtet bei Feinden in der Nähe des Verbündeten (bzw. von Euch selbst) 10...60 Punkte heiligen Schaden an.', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(2005, 'Segen des Peinigers', '30 Sekunden lang haben Eure Peinigungsgebete den doppelten Gunst der Götter-Bonus.', '30 Sekunden: Eure Peinigungsgebete haben den doppelten Gunst-der-Götter-Heilbonus.', 'Segen des Peinigers (PvP)', '5 Sekunden lang haben Eure Peinigungsgebete den doppelten Gunst der Götter-Bonus.', '5 Sekunden: Eure Peinigungsgebete haben den doppelten Gunst-der-Götter-Heilbonus.'),
(2006, 'Geißelungs-Siegel', 'Der Gegner erleidet 26..56 Punkte heiligen Schaden. Falls er angegriffen hat, erhaltet Ihr 1..10 Energiepunkte.', 'Richtet 26...56 Punkte heiligen Schaden an. Ihr erhaltet 1...10 Energiepunkte, falls der Gegner gerade angreift.', '', '', ''),
(2007, 'Reinigender Schleier', 'Während Ihr diese Verzauberung aufrechterhaltet, enden Zustände auf den Verbündeten (bzw. Euch selbst) um 5..50% schneller, mindestens jedoch 1 Sekunde. Wenn diese Verzauberung endet, wird ein Zustand vom Verbündeten entfernt.', 'Zustände auf dem Verbündeten (bzw. Euch selbst) enden um 5...50% früher, mindestens jedoch 1 Sekunde. Wirkung am Ende: Entfernt einen Zustand.', '', '', ''),
(2008, 'Zermalmender Schlag', 'Wenn Ihr einen Gegner trefft, der zu Boden geworfen wurde, leidet dieser 5..20 Sekunden lang an Schwäche und an einer Tiefen Wunde.', 'Verursacht die Zustände Schwäche und Tiefe Wunde (5...20 Sekunden), falls Gegner zu Boden geworfen ist.', '', '', ''),
(2009, 'Verwegener Hieb', 'Wenn er erfolgreich ist, führt dieser Angriff immer zu einem kritischen Treffer.', 'Immer ein kritischer Treffer.', '', '', ''),
(2010, 'Knieschneider', 'Wenn dieser Angriff einen verkrüppelten Feind trifft, erhaltet Ihr 2..7 Energiepunkte und 1..3 Adrenalinstöße.', 'Ihr erhaltet 2...7 Energiepunkte und 1...3 Adrenalinstöße, falls der Gegner verkrüppelt ist.', '', '', ''),
(2011, 'Raufen', 'Der berührte Gegner und Ihr werden zu Boden geworfen. Ihr verliert Eure aktuelle Haltung.', 'Ihr und der Gegner werdet zu Boden geworfen. <i>Ihr verliert Eure aktuelle Haltung.</i>', '', '', ''),
(2012, 'Strahlende Sense', 'Dieser Angriff verursacht pro Energiepunkt, den Ihr momentan habt, +1 Punkt Schaden (maximal 5..30 Punkte Schaden). Ihr erhaltet 1..7 Energiepunkte.', 'Richtet für jeden Eurer Energiepunkte +1 Punkt Schaden an (maximal 5...30). Ihr erhaltet 1...7 Energiepunkte.', '', '', ''),
(2013, 'Grenths Aura', '20 Sekunden lang fügt Ihr 5..25 Punkte weniger Schaden zu und stehlt 5..25 Lebenspunkte, wenn Ihr mit einer Sense trefft. Wenn Ihr diese Verzauberung wirkt, stehlt Ihr 5..25 Lebenspunkte von allen umstehenden Feinden.', '(20 Sekunden.) Ihr fügt 5...25 Punkte weniger Schaden zu und stehlt 5...25 Lebenspunkte, wenn Ihr mit einer Sense trefft. Anfangswirkung: Stiehlt 25 Lebenspunkte von allen umstehenden Feinden.', '', '', ''),
(2014, 'Siegel der frommen Zurückhaltung', 'Entfernt 1 Derwisch-Verzauberung. Der Gegner leidet 5..15 Sekunden lang an Verkrüppelung. Wenn eine Verzauberung entfernt wurde, leiden alle in der Nähe des Gegners befindlichen Feinde ebenfalls an Verkrüppelung und das Siegel lädt 75% schneller wieder auf.', 'Verursacht Zustand Verkrüppelung (5...15 Sekunden). Entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungswirkung: Verursacht bei allen in der Nähe des Gegners befindlichen Feinde ebenfalls Verkrüppelung und lädt 75% schneller wieder auf.', 'Siegel der frommen Zurückhaltung (PvP)', 'Ihr verliert 1 Derwisch-Verzauberung und fügt Eurem Gegner 5..15 Sekunden lang "Verkrüppelung" zu. Wenn eine Verzauberung entfernt wurde, lädt diese Fertigkeit 75% schneller wieder auf.', 'Verkrüppelt den Gegner (5...15 Sekunden) und entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Lädt 75% schneller wieder auf.'),
(2015, 'Sense des Bauern', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr 5..20 Punkte Schaden zu. Wenn Ihr mehr als einen Feind trefft, wird dieser Angriff sofort wieder aufgeladen.', 'Richtet +5...20 Punkte Schaden an. Wird sofort wieder aufgeladen, falls Ihr mehr als einen Feind angreift.', '', '', ''),
(2016, 'Energisch war Lee Sa', 'Haltet Lee Sas Asche 5..15 Sekunden lang. Während Ihr ihre Asche haltet, erhaltet Ihr +2 Energieregeneration. Wenn Ihr ihre Asche fallen lasst, erhaltet Ihr +1..10 Energiepunkte.', '5...15 Sekunden: Ihr habt +2 Energieregeneration. Fallwirkung: Ihr erhaltet +1...10 Energiepunkte.', '', '', ''),
(2017, 'Hymne der Müdigkeit', '8 Sekunden lang führt die nächste Angriffsfertigkeit von jedem Verbündeten in Hörweite auch 1..16 Sekunden lang zu Schwäche.', '8 Sekunden: Verbündete in Hörweite verursachen bei Benutzung ihrer nächsten Angriffsfertigkeit Schwäche (1...16 Sekunden)', '', '', ''),
(2018, 'Hymne der Störung', '1..10 Sekunden lang unterbricht die nächste Angriffsfertigkeit jedes Verbündeten in Hörweite außerdem eine Aktion.', '1...10 Sekunden: Verbündete in Hörweite unterbrechen bei Benutzung ihrer nächsten Angriffsfertigkeit eine Aktion.', 'Hymne der Störung (PvP)', '1..10 Sekunden lang unterbricht die nächste Angriffsfertigkeit jedes Verbündeten in Hörweite außerdem eine Aktion.', '1...10 Sekunden: Verbündete in Hörweite unterbrechen bei Benutzung ihrer nächsten Angriffsfertigkeit eine Aktion.'),
(2051, 'Geister herbeirufen (Luxon)', 'Alle von Euch kontrollierten Geister unternehmen einen Schattenschritt zu Eurer Position und erhalten 60..100 Lebenspunkte.', 'Von Euch kontrollierte Geister machen einen Schattenschritt zu Eurer Position und erhalten 60...100 Lebenspunkte.', '', '', ''),
(2052, 'Schattenfangzahn', 'Ihr unternehmt einen Schattenschritt zum Gegner hin. 10 Sekunden lang macht diese Verhexung nichts. Wenn sie endet, kehrt Ihr zu Eurer ursprünglichen Position zurück und der Gegner leidet 5..20 Sekunden lang an einer Tiefen Wunde.', 'Unternehmt einen Schattenschritt zum Gegner. Wirkung am Ende (nach 10 Sekunden): Verursacht Zustand Tiefe Wunde (5...20 Sekunden); Ihr kehrt zur anfänglichen Position zurück.', '', '', ''),
(2053, 'Kalkuliertes Risiko', '3..24 Sekunden lang fügen Angriffe des Gegners +10 Schaden zu, aber es gibt bei jedem Angriff eine Chance von 50%, dass der Schaden stattdessen dem Gegner zugefügt wird. (Maximal 15..100 Punkte Schaden)', '(3...24 Sekunden.) Angriffe des Gegners richten +10 Punkte Schaden an. Bei jedem Angriff besteht eine Chance von 50%, dass der Schaden (maximal 15...100 Punkte) stattdessen dem Gegner zugefügt wird.', 'Kalkuliertes Risiko (PvP)', '3..12 Sekunden lang fügen Angriffe des Gegners +10 Punkte Schaden zu, aber es gibt bei jedem Angriff eine Chance von 50%, dass der Schaden stattdessen dem Gegner zugefügt wird. (Maximal 15..60 Punkte Schaden)', 'Angriffe des Gegners richten +10 Punkte Schaden an (1...12 Sekunden). Bei jedem Angriff besteht eine Chance von 50%, dass der Schaden (Maximal 15...60 Punkte) stattdessen dem Gegner zugefügt wird.'),
(2054, 'Schrumpfende Rüstung', '10 Sekunden lang leidet der Gegner an einer Lebenspunktdegeneration von -1..4. Wenn diese Verhexung endet, hat der Gegner 5..20 Sekunden lang eine Beschädigte Rüstung.', '10 Sekunden: Verursacht -1...4 Lebenspunktdegeneration. Wirkung am Ende: Verursacht Zustand Beschädigte Rüstung (5...20 Sekunden).', '', '', ''),
(2055, 'Aneurysma', 'Der Gegner erhält alle Energiepunkte zurück, höchstens jedoch Energie in Höhe seiner maximalen Energie. Pro Energiepunkt, der auf diese Weise gewonnen wurde, erleidet der Gegner 1..3 Punkte Schaden und alle umstehenden Gegner verlieren einen Energiepunkt. (Maximalverlust: 1..30 Energiepunkte)', 'Gegner erhält alle Energiepunkte zurück, höchstens jedoch Energie in Höhe seiner maximalen Energie. Pro Energiepunkt, der auf diese Weise gewonnen wurde. Pro gewonnenem Energiepunkt erleidet der Gegner 1...3 Punkte Schaden und alle umstehenden Gegner verlieren einen Energiepunkt (maximal 1...30).', '', '', ''),
(2056, 'Wanderndes Auge', '4 Sekunden lang wird der nächste Angriff des Gegners, falls dieser anzugreifen versucht, unterbrochen und alle Feinde in der Nähe erleiden 30..110 Punkte Schaden.', '4 Sekunden: Unterbricht den nächsten Angriff des Gegners. Unterbrechungswirkung: 30...110 Punkte Schaden bei Feinden in der Nähe.', 'Wanderndes Auge (PvP)', '4 Sekunden lang wird der nächste Angriff des Gegners, falls dieser anzugreifen versucht, unterbrochen und alle Feinde in der Nähe erleiden 10..92 Punkte Schaden.', '4 Sekunden: Unterbricht den nächsten Angriff des Gegners. Unterbrechungswirkung: 10...92 Punkte Schaden bei Feinden in der Nähe.'),
(2057, 'Widerliches Gelage', 'Alle Zustände werden vom Verbündeten auf Euch übertragen. Für jeden auf diese Weise erhaltenen Zustand werdet Ihr um 0..45 Lebenspunkte geheilt und erhaltet 0..2 Energiepunkte. Wenn Ihr "Krankheit" von Eurem Ziel entfernt, beträgt die Wiederaufladezeit unveränderlich 2 Sekunden.', 'Alle Zustände werden vom Verbündeten auf Euch übertragen. Ihr werdet für jeden übertragenen Zustand um 0...45 Lebenspunkte geheilt und erhaltet 0...2 Energiepunkte. Unveränderliche Wiederaufladezeit von 2 Sekunden, wenn Ihr "Krankheit" entfernt. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2058, 'Verwesende Galle', '5..20 Sekunden lang unterliegt der Gegner einer Lebenspunktdegeneration von -1..4. Wenn er stirbt, während er mit dieser Verhexung belegt ist, erleiden alle Feinde in der Nähe 25..85 Punkte Schaden.', '5...20 Sekunden: Verursacht -1...4 Lebenspunktdegeneration. Richtet bei allen Feinden in der Nähe 25...85 Punkte Schaden an, falls der Gegner stirbt.', '', '', ''),
(2059, 'Kriegsneurose', 'Der Gegner erleidet 10..30 Punkte Blitzschaden und leidet 5..20 Sekunden lang an Beschädigter Rüstung. Dieser Zauber hat eine Rüstungsdurchdringung von 25%. Wenn Ihr Überzaubert seid, trifft dieser Zauber umstehende Feinde.', 'Richtet 10...30 Punkte Blitzschaden an. Verursacht den Zustand Beschädigte Rüstung (5...20 Sekunden). 25% Rüstungsdurchdringung. Wenn Überzaubert, werden auch umstehende Gegner getroffen.', '', '', ''),
(2060, 'Glyphe der Aufopferung', '15 Sekunden lang führen Eure nächsten 1..4 Zauber, die einen Gegner anvisieren, außerdem 1..4 Sekunden lang zu Brennen.', '15 Sekunden: Eure nächsten 1...4 auf Feinde gerichteten Zauber verursachen zusätzlich 1...4 Sekunden lang den Zustand Brennen.', '', '', ''),
(2061, 'Geduldiger Geist', '2 Sekunden lang wird der Verbündete (bzw. Ihr selbst) mit Geduldiger Geist verzaubert. Sofern diese Verzauberung nicht vorzeitig beendet wird, wird dieser Verbündete um 30..120 Lebenspunkte geheilt, wenn diese Verzauberung endet.', '(2 Sekunden.) Wirkung am Ende: Heilt 30...120 Lebenspunkte. <i>Keine Wirkung bei vorzeitiger Beendigung.</i>', '', '', ''),
(2062, 'Heilendes Band', 'Der Verbündete wird um 20..110 Lebenspunkte geheilt. Bis zu zwei zusätzliche Verbündete in der Nähe des Ziels werden um 10..100 Lebenspunkte geheilt.', 'Heilt um 20...110 Lebenspunkte. Heilt zwei weitere Verbündete in der Nähe des Verbündeten um 10...100 Lebenspunkte. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2063, 'Aura der Stabilität', '3..8 Sekunden lang kann der Verbündete nicht zu Boden geworfen werden.', '3...8 Sekunden: Der Verbündete kann nicht zu Boden geworfen werden. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2064, 'Makelloser Verstand', '1..15 Sekunden lang verliert der Verbündete alle 5 Sekunden lang eine Verhexung.', '1...15 Sekunden: Entfernt nach je 5 Sekunden eine Verhexung. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2065, 'Makellose Seele', '1..15 Sekunden lang verliert der Verbündete alle 3 Sekunden lang einen Zustand.', '1...15 Sekunden: Entfernt nach je 3 Sekunden einen Zustand. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2066, 'Entwaffnen', 'Wenn dieser Angriff einen angreifenden Gegner trifft, wird dieser unterbrochen und alle seine Angriffsfertigkeiten werden 0..3 Sekunden deaktiviert.', 'Unterbricht den Gegner. Unterbrechungswirkung: Deaktiviert die Angriffsfertigkeiten des Gegners (0...3 Sekunden), falls die unterbrochene Aktion ein Angriff war.', '', '', ''),
(2067, '"Das war Absicht!"', 'Wenn Ihr zu Boden geworfen wurdet, erhaltet Ihr 1..5 Adrenalinstöße.', 'Ihr erhaltet 1...5 Adrenalinstöße, falls Ihr zu Boden geworfen werdet.', '', '', ''),
(2068, 'Schnellfeuer', '5..25 Sekunden lang sind Eure Angriffe um 33% schneller, wenn Ihr einen Bogen einsetzt.', '5...25 Sekunden: Eure Angriffe sind um 33% schneller, wenn Ihr einen Bogen einsetzt.', '', '', ''),
(2069, 'Schuss des Faultierjägers', 'Wenn dieser Angriff erfolgereich ist, erleidet der Gegner +10..25 Punkte Schaden. Wenn er keine Fertigkeit verwendet, fügt Schuss des Faultierjägers zusätzlich 10..35 Punkte Schaden zu.', 'Richtet +10...25 Punkte Schaden an. Richtet zusätzlich +10...35 Punkte Schaden an, falls der Gegner gerade keine Fertigkeit benutzt.', 'Schuss des Faultierjägers (PvP)', 'Wenn dieser Angriff erfolgereich ist, erleidet der Gegner +5..20 Punkte Schaden. Wenn er keine Fertigkeit verwendet, fügt Schuss des Faultierjägers zusätzlich 5..15 Punkte Schaden zu.', 'Richtet +5...20 Punkte Schaden an. Richtet zusätzlich +5...25 Punkte Schaden an, falls der Gegner gerade keine Fertigkeit benutzt.'),
(2070, 'Auraschneider', 'Wenn dieser Angriff erfolgreich ist, leidet Euer Gegner 5..15 Sekunden lang an einer Blutung. Falls Ihr verzaubert seid, fügt Ihr außerdem 1..10 Sekunde lang Beschädigte Rüstung zu.', 'Verursacht Blutung (5...15 Sekunden). Verursacht ebenfalls Beschädigte Rüstung (1...10 Sekunden), falls Ihr verzaubert seid.', '', '', ''),
(2071, 'Eifriger Schwung', 'Wenn dieser Angriff erfolgreich ist, verursacht Ihr +5..20 Punkte Schaden. Ihr erhaltet für jeden getroffenen Feind 3 Energiepunkte und 1 Adrenalinstoß.', 'Richtet +5...20 Punkte Schaden an. Ihr erhaltet für jeden getroffenen Feind 3 Energiepunkte und 1 Adrenalinstoß.', '', '', ''),
(2072, 'Rein war Li Ming', 'Haltet Li Mings Asche 5..20 Sekunden lang. Während Ihr ihre Asche haltet, enden Zustände, mit denen Ihr belegt seid, um 10..50% schneller. Wenn Ihr ihre Asche fallen lasst, verlieren alle Verbündeten in Hörweite 1..4 Zustände.', '5...20 Sekunden: Zustände auf Euch enden um 10...50% früher. Fallwirkung: Entfernt bei Verbündeten in Hörweite 1...4 Zustände.', '', '', ''),
(2073, 'Waffe der Aggression', '5..15 Sekunden lang sind Eure Angriffe um 25% schneller.', '5...15 Sekunden: Ihr greift um 25% schneller an.', '', '', ''),
(2074, 'Brusttrommler', 'Wenn dieser Angriff einen Feind mit Beschädigter Rüstung trifft, leidet dieser 5..20 Sekunden lang an einer Tiefen Wunde.', 'Verursacht Zustand Tiefe Wunde (5...20 Sekunden), falls der Gegner Beschädigte Rüstung hat.', '', '', ''),
(2075, 'Hastiger Refrain', '3..11 Sekunden lang bewegt sich der Verbündete (bzw. Ihr selbst) um 25% schneller. Dieses Echo wird jedes Mal wiederholt, wenn ein auf diesen Verbündeten gerichteter Anfeuerungsruf oder Schrei endet.', '3...11 Sekunden: Der Verbündete (bzw. Ihr selbst) bewegt sich um 25% schneller. Erneuerung: Jedes Mal, wenn ein Schrei oder Anfeuerungsruf auf dem Verbündeten (bzw. Euch selbst) endet.', '', '', ''),
(2091, 'Schattenzuflucht (Kurzick)', 'Ihr seid 5 Sekunden lang geblendet. 10 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration in Höhe von 7..10 und +40 Rüstung.', '10 Sekunden: Ihr erhaltet +7...10 Lebenspunktregeneration und +40 Rüstung. <i>Ihr seid geblendet (5 Sekunden)</i>', '', '', ''),
(2092, 'Äther-Alptraum (Kurzick)', 'Der Gegner verliert 5..8 Energiepunkte. Der Gegner und alle Feinde in dem Bereich erleiden 10 Sekunden lang eine Lebenspunktdegeneration in Höhe der verlorenen Energiepunkte.', 'Lässt 5...8 Energiepunkte verlieren. Der Gegner und Feinde im Gebiet<sub>sic</sub> haben -1 Lebenspunktdegeneration pro verlorenem Energiepunkt (10 Sekunden).', '', '', ''),
(2093, 'Siegel der Verdorbenheit (Kurzick)', 'Der Gegner und alle Feinde in der Nähe erleiden 20..30 Punkte Schaden. Pro betroffenen Feind, der unter einem Zustand oder einer Verhexung leidet, erhaltet Ihr 2 Punkte Energie (maximal 12..20 Punkte).', 'Richtet beim Gegner und Feinden in der Nähe 20...30 Punkte Schaden an. Ihr erhaltet für jeden dieser Gegner mit einem Zustand oder einer Verhexung 2 Energiepunkte (maximal 12...20).', '', '', ''),
(2094, 'Elementarlord (Kurzick)', '40..60 Sekunden lang erhalten Eure Elementarattribute einen Schub von 1. Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr 1 Energiepunkt pro jeweils 10 Ränge Energiespeicherung und werdet um 100..300% der Energiekosten dieses Zaubers geheilt.', '(40...60 Sekunden.) Eure Elementarattribute erhalten einen Schub von 1. Jedes Mal, wenn Ihr einen Zauber wirkt, erhaltet Ihr 1 Energiepunkt pro 10 Ränge in Energiespeicherung und werdet um 100...300% der Energiekosten dieses Zaubers geheilt.', '', '', ''),
(2095, 'Selbstloser Geist (Kurzick)', '15..20 Sekunden lang kosten von Euch auf einen anderen Verbündeten gewirkte Zauber 3 Punkte weniger Energie.', '(15...20 Sekunden.) Zauber, die Ihr auf einen weiteren Verbündeten wirkt, kosten 3 Punkte weniger Energie.', '', '', ''),
(2096, 'Dreifachschuss (Kurzick)', '3 Pfeile werden gleichzeitig auf den Gegner abgefeuert. Diese Pfeile fügen 40..25% weniger Schaden zu.', 'Ihr feuert drei Pfeile gleichzeitig auf den Gegner ab. <i>Diese Pfeile richten 40...25% weniger Schaden an.</i>', '', '', ''),
(2097, '"Rettet Euch selbst!" (Kurzick)', '4..6 Sekunden lang erhalten alle anderen Gruppenmitglieder +100 Rüstung.', '4...6 Sekunden: Alle anderen Gruppenmitglieder erhalten +100 Rüstung.', '', '', ''),
(2098, 'Aura der Heiligen Macht (Kurzick)', '45 Sekunden lang fügt Ihr, wann immer Ihr eine Derwisch-Verzauberung verliert, allen umstehenden Feinden 20..25 Punkte heiligen Schaden zu.', '(45 Sekunden.) Immer wenn Ihr eine Derwisch-Verzauberung verliert, erleiden alle umstehenden Feinde 20...25 Punkte heiligen Schaden.', '', '', ''),
(2099, 'Speer der Wut (Kurzick)', 'Der Angriff fügt +30..40 Punkte Schaden zu. Wenn er einen Feind trifft, der an einem Zustand leidet, erhaltet Ihr 3..6 Adrenalinstöße.', 'Richtet +30...40 Punkte Schaden an. Ihr erhaltet 3...6 Adrenalinstöße, falls Ihr einen Feind mit einem Zustand trefft.', '', '', ''),
(2100, 'Geister herbeirufen (Kurzick)', 'Alle von Euch kontrollierten Geister unternehmen einen Schattenschritt zu Eurer Position und erhalten 60..100 Lebenspunkte.', 'Von Euch kontrollierte Geister machen einen Schattenschritt zu Eurer Position und erhalten 60...100 Lebenspunkte.', '', '', ''),
(2101, 'Kritische Beweglichkeit', '4 Sekunden und 1 extra Sekunde pro Rang bei Kritischen Stößen lang greift Ihr um 33% schneller an und erhaltet +15..25 Rüstung. Diese Fertigkeit wiederholt sich jedes Mal, wenn Ihr einen kritischen Treffer landet.', '(4 Sekunden plus 1 Sekunde für jeden Rang bei Kritischen Stößen.) Ihr greift um 33% schneller an und erhaltet +15...25 Rüstung. Erneuerung: Jedes Mal, wenn Ihr einen kritischen Treffer landet.', '', '', ''),
(2102, 'Schmerzensschrei', 'Unterbricht die Fertigkeit des Gegners. Wenn dieser Gegner unter einer Mesmer-Verhexung gelitten hat, nehmen dieser Gegner und alle Feinde in dem Bereich 25..50 Punkte Schaden und 10 Sekunden lang eine Lebenspunktdegeneration von 3..5.', 'Unterbricht eine Fähigkeit. Wenn Gegner eine Mesmer-Verhexung erlitten hat, werden 25...50 Punkte Schaden beim Gegner und Feinden in dem Bereich angerichtet und 10 Sekunden lang ist eine Lebenspunktdegeneration von -3...5 wirksam.', '', '', ''),
(2103, 'Nekrose', 'Wenn der Gegner unter einem Zustand oder einer Verhexung leidet, erleidet er 60..90 Punkte Schaden.', 'Richtet 60...90 Punkte Schaden an, falls Gegner einen Zustand oder eine Verhexung hat.', '', '', ''),
(2104, 'Intensität', '10 Sekunden lang fügt Ihr das nächste Mal, wenn Ihr einem Gegner mit einem Zauber Elementarschaden zufügt, 60..70% dieses Schadens allen Feinden in dem Bereich zu.', '(10 Sekunden.) Das nächste Mal, wenn Ihr Elementarschaden mit einem Zauber zufügt, erleiden andere Feinde in dem Bereich 60...70% dieses Schadens.', '', '', ''),
(2105, 'Lebenssaat', 'Während der nächsten 2..5 Sekunden werden jedes Mal, wenn der Verbündete Schaden erleidet, alle Gruppenmitglieder pro Rang in Gunst der Götter um 2 Lebenspunkte geheilt.', '(2...5 Sekunden.) Heilt alle Gruppenmitglieder jedes Mal, wenn das Ziel Schaden erleidet, um 2 Lebenspunkte pro Rang in Gunst der Götter. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2107, 'Wirbelsturmangriff', 'Greift den Gegner und umstehende Feinde an. Jeder erfolgreiche Angriff fügt +13..20 Punkte Schaden zu.', 'Greift Gegner und umstehende Feinde an und richtet +13...20 Punkte Schaden an.', '', '', ''),
(2108, 'Tobt niemals allein', '18..25 Sekunden lang greifen Ihr und Euer Tiergefährte um 25% schneller und erfreuen sich einer Lebenspunktregeneration von +1..3.', '18...25 Sekunden: Ihr und Euer Tiergefährte greift um 25% schneller an und habt +1...3 Lebenspunktregeneration.', '', '', ''),
(2109, 'Ewige Aura', 'Ihr habt +100 maximale Lebenspunkte. Wenn diese Verzauberung endet, werden alle Gruppenmitglieder (bzw. Ihr selbst) im Bereich mit 40..50% Lebenspunkten und 20..30% Energiepunkten wiederbelebt.', 'Ihr habt +100 maximale Lebenspunkte. Wirkung am Ende: Alle Gruppenmitglieder (bzw. Ihr selbst) im Bereich werden mit 40...50% Lebenspunkten und 20...30% Energiepunkten wiederbelebt.', '', '', ''),
(2110, 'Vampirismus', 'Erstellt einen Geist der Stufe 4..14, der nach 75..150 Sekunden stirbt. Angriffe durch diesen Geist entziehen bis zu 10..20 Lebenspunkte und Ihr werdet um 10..20 Lebenspunkte geheilt.', 'Erstellt einen Geist der Stufe 4...14 (75...150 Sekunden Lebensdauer). Seine Angriffe entziehen 10...20 Lebenspunkte und Ihr werdet um 10...20 Lebenspunkte geheilt.', '', '', ''),
(2112, '"Es gibt nichts zu befürchten!"', '4 Sekunden und 1 Sekunde pro 2 Ränge bei Führung lang erleiden alle Gruppenmitglieder in Hörweite um 20..35% weniger Schaden. Betroffene Gruppenmitglieder werden um 35..60 Lebenspunkte geheilt, wenn dieser Schrei endet.', '(4 Sekunden plus 1 Sekunde für alle 2 Ränge bei Führung.) Reduziert Schaden für Gruppenmitglieder in Hörweite um  20...35%. Wirkung am Ende: Heilt um 35...60 Lebenspunkte.', '', '', ''),
(2116, 'Überraschungsangriff', 'Wenn dieser Angriff erfolgreich ist, wird der Gegner 5..8 Sekunden lang geblendet. Dieser Angriff zählt als Leithandangriff.', 'Verursacht Blindheit (5...8 Sekunden). Zählt als Leithandangriff.', '', '', ''),
(2135, 'Trampelnder Ochse', 'Muss einem Begleithandangriff folgen. Wenn Ihr erfolgreich seid, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr einen verkrüppelten Feind trefft, wird dieser zu Boden geworfen.', 'Richtet +5...20 Punkte Schaden an; wirft den Gegner zu Boden, falls er verkrüppelt ist. <i>Muss einem Begleithandangriff folgen.</i>', '', '', ''),
(2136, 'Rauchpulver-Verteidigung', '8 Sekunden lang erleidet Ihr beim nächsten Treffer nur den halben Schaden und umstehende Feinde werden 2..6 Sekunden lang geblendet.', '8 Sekunden: Wenn Ihr das nächste Mal getroffen werdet, erleidet Ihr nur halben Schaden und verursacht bei umstehenden Feinden den Zustand Blindheit (2...6 Sekunden).', '', '', ''),
(2137, 'Verwirrende Bilder', '2..10 Sekunden lang benötigt der Gegner doppelt so lange, um Nicht-Angriffsfertigkeiten zu aktivieren.', '(2...10 Sekunden.) Der Gegner benötigt doppelt so lange, um Nicht-Angriffsfertigkeiten zu aktivieren.', '', '', ''),
(2138, 'Lebenskraft des Verhexers', '10 Sekunden lang habt Ihr +1..8 Lebenspunktregeneration. Lebenskraft des Verhexers endet, wenn Ihr eine Fertigkeit benutzt, die keine Verhexung ist.', '10 Sekunden: Ihr habt +1...8 Lebenspunktregeneration. <i>Endet, wenn Ihr eine Fertigkeit verwendet, die keine Verhexung ist.</i>', '', '', ''),
(2139, 'Masochismus', '10..40 Sekunden lang sind Eure Todesmagie- und Seelensammlungsattribute um +2 erhöht und Ihr opfert zusätzlich 5..3% Eurer maximalen Lebenspunkte, wenn Ihr einen Zauber wirkt.', '(10...40 Sekunden.) Ihr habt +2 Todesmagie und Seelensammlung. <i>Ihr opfert jedes Mal, wenn Ihr einen Zauber wirkt, zusätzlich 5...3% Eurer maximalen Lebenspunkte.</i>', 'Masochismus (PvP)', '30 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr Lebenspunkte opfert, 1..3 Energiepunkte.', '30 Sekunden: Ihr erhaltet jedes Mal 1...3 Energiepunkte, wenn Ihr Lebenspunkte opfert.'),
(2140, 'Durchbohrende Falle', 'Wird Durchbohrende Falle aktiviert, erleiden alle Feinde in der Nähe 5..50 Punkte Stichschaden. Alle Feinde mit Beschädigter Rüstung erleiden zusätzlich 15..60 Punkte Schaden. Durchbohrende Falle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 5...50 Punkte Stichschaden an. Richtet bei Feinden mit Beschädigter Rüstung zusätzlich 15...60 Punkte Schaden an. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(2141, 'Kameradschaft', 'Wenn Eure Lebenspunkte niedriger als die Eures Tiergefährten sind, erhaltet Ihr 30..120 Lebenspunkte geheilt. Wenn die Lebenspunkte Eures Tiergefährten niedriger als Eure eigenen sind, erhält er 30..120 Lebenspunkte.', 'Erhaltet 30...120 Lebenspunkte, falls Ihr weniger Lebenspunkte habt als Euer Tiergefährte. Euer Tiergefährte erhält 30...120 Lebenspunkte, falls er weniger Lebenspunkte hat als Ihr.', '', '', ''),
(2142, 'Wilde Aggression', '5..20 Sekunden lang greift Euer Tiergefährte um 33% schneller an und fügt 3..10 Punkte zusätzlichen Schaden zu.', '5...20 Sekunden: Euer Tiergefährte greift um 33% schneller and <sub>sic</sub> und richtet +3...10 Punkte Schaden an.', '', '', ''),
(2143, 'Unterbrechender Schuss', 'Wenn dieser Angriff erfolgreich ist, wird die Aktion des Gegners unterbrochen. Wenn diese Aktion eine Fertigkeit war, fügt Ihr +10..40 Punkte Schaden zu.', 'Unterbricht eine Aktion. Unterbrechungswirkung: +10...40 Punkte Schaden, falls Ihr eine Fertigkeit unterbrecht.', '', '', ''),
(2144, 'Salve', 'Entfernt alle Eure Vorbereitungen und Glyphen. Schießt Pfeile auf Euren Zielgegner und bis zu 3 um ihn herum stehende Feinde. Diese Pfeile fügen +1..10 Punkte Schaden zu, wenn sie treffen.', 'Richtet +1...10 Punkte Schaden an. Trifft bis zu 3 umstehende Feinde um den Gegner. <i>Entfernt alle Eure Vorbereitungen und Glyphen.</i>', '', '', ''),
(2145, 'Expertenfokus', '24 Sekunden lang kosten Eure Bogenangriffe 1..2 weniger Energiepunkte und fügen 1..10 Punkte zusätzlichen Schaden zu.', '24 Sekunden: Eure Bogenangriffe kosten 1...2 Energiepunkte weniger und richten +1...10 Punkte Schaden an.', '', '', ''),
(2146, 'Frommer Zorn', 'Entfernt 1 Derwisch-Verzauberung. 1..7 Sekunden lang sind Eure Angriffe um 25% schneller. Wenn eine Verzauberung entfernt wurde, dauert diese Haltung doppelt so lange an.', '(1...7 Sekunden.) Ihr greift 25% schneller an und entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Diese Haltung dauert doppelt so lange an.', 'Frommer Zorn (PvP)', 'Entfernt 1 Derwisch-Verzauberung. 1..5 Sekunden lang sind Eure Angriffe um 25% schneller. Wenn eine Verzauberung entfernt wurde, dauert diese Haltung doppelt so lange an.', '(1...5 Sekunden.) Ihr greift 25% schneller an und entfernt 1 Eurer Derwisch-Verzauberungen. Entfernungseffekt: Diese Haltung dauert doppelt so lange an.'),
(2147, 'Verkrüppelnder Sieg', 'Trifft dieser Angriff einen Gegner, wird dieser 3..8 Sekunden lang verkrüppelt. Falls Ihr mehr Lebenspunkte habt als der Gegner, werden erleiden alle umstehenden Feinde 10..30 Punkte Erdschaden.', '(3...8 Sekunden.) Verkrüppelt den Gegner. Falls Ihr mehr Lebenspunkte habt als der Gegner, werden<sub>sic</sub> erleiden alle umstehenden Feinde 10...30 Punkte Erdschaden.', '', '', ''),
(2148, 'Trennende Waffe', '4..10 Sekunden lang haben die nächsten 3 Angriffe des Verbündeten (bzw. Eure eigenen) eine Rüstungsdurchdringung von 10% und führen 5..20 Sekunden lang zu Beschädigter Rüstung.', '4...10 Sekunden: Die nächsten 3 Angriff des Verbündeten (bzw. von Euch selbst) verursachen Zustand Beschädigte Rüstung (5...20 Sekunden) und haben 10% Rüstungsdurchdringung.', '', '', ''),
(2149, 'Waffe der Erneuerung', '4..10 Sekunden lang erhält der Verbündete (bzw. Ihr selbst) das nächste Mal, wenn er mit einer Angriffsfertigkeit trifft, 1..7 Energiepunkte.', '4...10 Sekunden: Der Verbündete (bzw. Ihr selbst) erhält 1...7 Energiepunkte, wenn er das nächste Mal mit einer Angriffsfertigkeit trifft.', '', '', ''),
(2150, 'Verkrüppelungs-Speer', 'Wenn Euer Angriff einen blutenden Feind trifft, wird dieser 5..20 Sekunden lang verkrüppelt.', 'Verursacht Zustand Verkrüppelung (5...20 Sekunden), falls der Gegner blutet.', '', '', ''),
(2186, 'Siegel der tödlichen Verdorbenheit', 'Dieser Angriff fügt pro Zustand, mit dem der Gegner belegt ist, 5..35 Punkte Schaden zu (maximal 130 Punkte).', 'Richtet für jeden Zustand des Gegner 5...35 Punkte Schaden an (maximal 130).', 'Siegel der tödlichen Verdorbenheit (PvP)', 'Muss einem Doppelangriff folgen. Dieser Angriff fügt pro Zustand, mit dem der Gegner belegt ist, 5..35 Punkte Schaden zu (maximal 130 Punkte).', 'Richtet für jeden Zustand des Gegner 5...35 Punkte Schaden an (maximal 130). <i>Muss einem Doppelangriff folgen.</i>'),
(2187, 'Weg des Meisters', '60 Sekunden lang habt Ihr zusätzlich eine 3..33%ige Chance, einen kritischen Treffer zu erzielen, während Ihr eine Waffe führt, die kein Dolch ist.', '60 Sekunden: Wenn Ihr eine Waffe führt, die kein Dolch ist, habt Ihr +3...33% zur Chance auf kritische Treffer.', '', '', ''),
(2188, 'Verteidigung entweihen', '5..20 Sekunden lang erleidet der Gegner, wenn er das nächste Mal blockt, 30..120 Punkte Schaden.', '5...20 Sekunden: Richtet 30...120 Punkte Schaden an, wenn der Gegner das nächste Mal blockt.', '', '', ''),
(2189, 'Angorodons Starren', 'Entzieht dem Gegner 10..50 Lebenspunkte. Wenn Ihr an einem Zustand leidet, erhaltet Ihr 3..12 Energiepunkte.', 'Entzieht 10...50 Lebenspunkte. Ihr erhaltet 3...12 Energiepunkte, falls Ihr einen Zustand habt.', '', '', ''),
(2190, 'Magnetschub', 'Der Gegner erleidet 15..75 Punkte Schaden. Wenn Ihr überzaubert seid, verzaubert Magnetschub alle Verbündeten in Hörweite 1..5 Sekunde(n), sodass sie den nächsten gegen sie gerichteten Angriff blocken.', 'Richtet 15...75 Punkte Schaden an. Wenn Ihr überzaubert seid, werden alle Verbündeten in Hörweite 1...5 Sekunde(n) lang verzaubert, sodass sie den nächsten gegen sie gerichteten Angriff blocken.', '', '', ''),
(2191, 'Rutschiger Boden', 'Wenn der Gegner oder ein umstehender Feind geblendet ist oder sich bewegt, wird er zu Boden geworfen. Bei Wassermagie von 4 oder weniger besteht eine 50%ige Change eines Misserfolges.)', 'Wirft den Gegner zu Boden, falls er geblendet ist oder sich bewegt. Trifft um Euren Zielgegner herum stehende Feinde. <i>Bei Wassermagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.</i>', 'Rutschiger Boden (PvP)', 'Wenn der Gegner geblendet ist oder sich bewegt, wird er zu Boden geworfen. (Bei Wassermagie von 4 oder weniger besteht eine 50%ige Change eines Misserfolges.)', 'Wirft den Gegner zu Boden, falls er geblendet ist oder sich bewegt. <i>Bei Wassermagie von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.</i>'),
(2192, 'Leuchtendes Eis', 'Der Gegner erleidet 5..50 Punkte Kälteschaden. Wenn er mit einer Wassermagie-Verhexung belegt ist, erhaltet Ihr 5 Energiepunkte sowie 1 Punkt für alle 2 Ränge in Energiespeicherung.', 'Geschoss: Fügt 5...50 Punkte Kälteschaden zu. Ihr erhaltet 5 Energiepunkte sowie 1 Energiepunkt pro 2 Ränge in Energiespeicherung, wenn der Gegner mit Wassermagie verhext ist.', '', '', ''),
(2193, 'Energieexplosion', 'Der Gegner erleidet pro Energiepunkt, den Ihr habt, 1..2 Punkte Schaden (maximal 130 Punkte).', 'Richtet für jeden Eurer Energiepunkte 1...2 Punkte Schaden an (maximal 130).', '', '', ''),
(2194, 'Ablenkender Stoß', 'Wenn Ablenkender Stoß erfolgreich ist, fügt er nur 0 Punkte Schaden zu und unterbricht die Aktion des Gegners. Wenn der Gegner eine Beschädigte Rüstung hat und Ihr eine Fertigkeit unterbrochen habt, benötigt die unterbrochene Fertigkeit mindestens 20 Sekunden zur Wiederaufladung.', 'Unterbricht eine Aktion. Unterbrechungswirkung: Deaktiviert unterbrochene Fertigkeit (20 Sekunden), falls Gegner Beschädigte Rüstung aufweist. <i>Fügt keinen Schaden zu.</i>', '', '', ''),
(2195, 'Symbolischer Schlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr pro ausgerüstetem Siegel +12 Punkte Schaden zu (maximal 70 Punkte Schaden).', 'Richtet für jedes Siegel, das Ihr ausgerüstet habt, +12 Punkte Schaden an (maximal 70).', '', '', ''),
(2196, 'Geschwindigkeit des Soldaten', '3..18 Sekunden lang bewegt Ihr Euch um 15% schneller. Ihr bewegt Euch um weitere 15% schneller, wann immer Ihr in der Zeit unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.', '(3...18 Sekunden.) Ihr bewegt Euch um 15% schneller und um weitere 15%, wann immer Ihr in der Zeit unter dem Einfluss eines Anfeuerungsrufs oder Schreis steht.', '', '', ''),
(2197, 'Körperschlag', 'Wenn dieser Angriff erfolgreich ist, erleidet der Gegner +10..40 Punkte Schaden. Wenn er an Beschädigter Rüstung leidet, hat er 0..15 Sekunden lang eine Tiefe Wunde.', 'Richtet +10...40 Punkte Schaden an. Verursacht Tiefe Wunde (0...15 Sekunden), falls Gegner Beschädigte Rüstung hat.', '', '', ''),
(2198, 'Körperschuss', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr einen Gegner mit beschädigter Rüstung trefft, erhaltet Ihr 5..10 Energiepunkte.', 'Richtet +5...20 Punkte Schaden an. Ihr erhaltet 5...10 Energiepunkte, falls der Gegner Beschädigte Rüstung hat.', '', '', ''),
(2199, 'Giftspitzen-Siegel', '60 Sekunden lang führt Euer nächster Angriff außerdem 8..15 Sekunden lang zu Vergiftung.', '60 Sekunden: Euer nächster Angriff verursacht Zustand Gift (8...15 Sekunden).', '', '', ''),
(2200, 'Siegel der mystischen Geschwindigkeit', '30 Sekunden lang wirken Eure nächsten 1..3 auf Euch gewirkten Verzauberungen sofort. Blitzverzauberungen verbrauchen diese Fertigkeit nicht.', '(30 Sekunden.) Eure nächsten 1...3 auf Euch gewirkten Verzauberungen wirken sofort. Blitzverzauberungen verbrauchen diese Fertigkeit nicht.', '', '', ''),
(2201, 'Kraftschild', '1..16 Sekunden lang werden die nächsten 1 Angriffe gegen Euch geblockt.  Wenn ein Angriff geblockt wird, werden alle umstehenden angreifenden Feinde zu Boden geworfen und leiden 5..20 Sekunden lang an "Schwäche".', '(1...16 Sekunden.) Blockt die nächsten 1 Angriffe gegen Euch. Wirft beim Blocken alle umstehenden angreifenden Feinde zu Boden und fügt "Schwäche" zu (5...20 Sekunden).', '', '', ''),
(2202, 'Genesungsgriff', 'Der Verbündete (bzw. Ihr selbst) wird um 15..75 Lebenspunkte geheilt. Wenn dieser Verbündete unter den Auswirkungen eines Waffenzaubers leidet, verliert er einen Zustand.', 'Heilt um 15...75 Lebenspunkte. Entfernt einen Zustand, falls der Verbündete (bzw. Ihr selbst) unter dem Einfluss eines Waffenzaubers steht.', '', '', ''),
(2203, 'Geistsauger-Aura', 'Für eine Dauer von 5..20 Sekunden fügen alle Eure Geister in Hörweite 5..20 weniger Schaden zu, aber stehlen 5..20 Lebenspunkte, wenn sie angreifen.', '(5...20 Sekunden.) Alle Eure Geister in Hörweite fügen 5...20 weniger Schaden zu und stehlen 5...20 Lebenspunkte, wenn sie angreifen.', '', '', ''),
(2204, 'Verjüngung', 'Erstellt einen Geist der Stufe 1..20. Dieser Geist heilt alle Gruppenmitglieder in Hörweite pro Sekunde um 3..10 Lebenspunkte. Der Geist verliert pro Verbündeten, der auf diese Weise geheilt wird, 3..10 Lebenspunkte. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...20 (30...90 Sekunden Lebensdauer). Heilt Gruppenmitglieder in Hörweite jede Sekunde um 3...10 Lebenspunkte. <i>Heilkosten: Der Geist verliert 3...10 Lebenspunkte.</i>', 'Verjüngung (PvP)', 'Erstellt einen Geist der Stufe 1..16. Dieser Geist heilt alle Gruppenmitglieder in Hörweite pro Sekunde um 3..10 Lebenspunkte. Der Geist verliert pro Verbündeten, der auf diese Weise geheilt wird, 3..10 Lebenspunkte. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...16 (30...90 Sekunden Lebensdauer). Heilt Gruppenmitglieder in Hörweite jede Sekunde um 3...10 Lebenspunkte. <i>Heilkosten: Der Geist verliert 3...10 Lebenspunkte.</i>'),
(2205, 'Todesqual', 'Erstellt einen Geist der Stufe 1..12. Feinde in Hörweite verlieren pro Sekunde 3..10 Lebenspunkte. Der Geist verliert pro Feind, der auf diese Weise beeinträchtigt wird, 3..10 Lebenspunkte. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...12 (30...90 Sekunden Lebensdauer). Lässt Feinde in Hörweite jede Sekunde 3...10 Lebenspunkte verlieren. <i>Der Geist verliert für jeden betroffenen Feind 3...10 Lebenspunkte.</i>', 'Todesqual (PvP)', 'Erstellt einen Geist der Stufe 1..8. Feinde in Hörweite verlieren pro Sekunde 3..10 Lebenspunkte. Der Geist verliert pro Feind, der auf diese Weise beeinträchtigt wird, 3..10 Lebenspunkte. Dieser Geist stirbt nach 30..90 Sekunden.', 'Erstellt einen Geist der Stufe 1...8 (30...90 Sekunden Lebensdauer). Lässt Feinde in Hörweite jede Sekunde 3...10 Lebenspunkte verlieren. <i>Der Geist verliert für jeden betroffenen Feind 3...10 Lebenspunkte.</i>'),
(2206, 'Geisterwaffe', '5..20 Sekunden lang kann der nächste Angriff des Verbündeten nicht geblockt werden.', '5...20 Sekunden: Der nächste Angriff des Verbündeten kann nicht geblockt werden. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2207, 'Inspirative Rede', 'Ihr verliert Euer gesamtes Adrenalin und der Verbündete erhält 1..4 Adrenalinstöße.', 'Der Verbündete erhält 1...4 Adrenalinstöße. <i>Sich selbst als Ziel wählen nicht möglich. Ihr verliert alles Adrenalin.</i>', '', '', ''),
(2208, 'Brennender Schild', '3..9 Sekunden lang wird, wenn Ihr einen  Schild benutzt, die nächste Angriffsfertigkeit gegen Euch geblockt. Falls es sich dabei um einen Nahkampfangriff gehandelt hat, wird Euer Angreifer 1..6 Sekunden lang in Brand gesetzt.', '3...9 Sekunden: Blockt die nächste feindliche Fertigkeit gegen Euch. Verursacht Zustand Brennen (1...6 Sekunden), falls es sich um einen Nahkampfangriff handelt. <i>Keine Wirkung, falls Ihr keinen Schild benutzt</i>', '', '', ''),
(2209, 'Heiliger Speer', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn dieser Angriff eine herbeigerufene Kreatur trifft, erleiden alle Feinde in der Nähe 15..90 Punkte heiligen Schaden und werden 3 Sekunden lang in Brand gesetzt.', 'Richtet +5...20 Punkte Schaden an. Richtet bei Feinden in der Nähe 15...90 Punkte heiligen Schaden an und verursacht Zustand Brennen (3 Sekunden), falls Ihr eine herbeigerufene Kreatur trefft.', '', '', ''),
(2210, 'Speerschlag', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu und der Gegner leidet 4..10 Sekunden lang an Benommenheit. Dieser Angriff hat Nahkampfreichweite.', 'Richtet +5...20 Schaden an und verursacht Zustand Benommenheit (4...10 Sekunden). Dieser Angriff hat Nahkampfreichweite.', '', '', ''),
(2211, 'Alkars Alchemiesäure', 'Bewerft den Gegner mit Alkars Alchemiesäure. Der Gegner und umstehende Feinde erleiden 40..50 Punkte Schaden. Zerstörer erleiden zusätzlich 45..70 Punkte Schaden und haben 14..20 Sekunden lang Beschädigte Rüstung.', 'Geschoss: Richtet beim Gegner und umstehenden Feinden 40...50 Punkte Schaden an. Verursacht bei Zerstörern zusätzlich 45...70 Punkte Schaden und Zustand Beschädigte Rüstung (14...20 Sekunden).', '', '', ''),
(2212, 'Licht von Deldrimor', 'Alle Feinde in dem Bereich erleiden 55..80 Punkte heiligen Schaden. Versteckte Gegenstände werden kurz auf Eurem Kompass angezeigt. Alle versteckten Gegenstände in dem Bereich werden enthüllt.', 'Richtet bei Feinden in dem Bereich 55...80 Punkte heiligen Schaden an. Versteckte Gegenstände in Kompassreichweite werden kurz angezeigt. Versteckte Gegenstände in dem Bereich werden enthüllt.', '', '', ''),
(2213, 'Ohrenbiss', 'Der berührte Gegner erleidet 50..70 Punkte Stichschaden und leidet 15..25 Sekunden lang an einer Blutung.', 'Richtet 50...70 Punkte Stichschaden und verursacht Zustand Blutung (15...25 Sekunden).', '', '', ''),
(2214, 'Unter der Gürtellinie', 'Fügt dem berührten Gegner 45..70 Punkte Schaden zu. Liegt er am Boden, erleidet er zusätzlich 30..50 Punkte Schaden und leidet 14..20 Sekunden lang an Beschädigter Rüstung.', 'Richtet 45...70 Punkte Schaden an. Richtet +30...50 Punkte zusätzlichen Schaden an und verursacht Beschädigte Rüstung (14...20 Sekunden), falls der Gegner zu Boden geworfen wurde.', '', '', ''),
(2215, 'Rauf-Kopfstoß', 'Ihr gebt dem berührten Gegner einen Kopfstoß, fügt ihm 45..70 Punkte Schaden zu und werft ihn zu Boden, wenn Ihr trefft.', 'Richtet 45...70 Punkte Schaden an und wirft zu Boden.', '', '', ''),
(2216, '"Stolpert nicht!"', '3..5 Sekunden lang können Gruppenmitglieder in Hörweite nicht zu Boden geworfen werden.', '3...5 Sekunden: Gruppenmitglieder in Hörweite können nicht zu Boden geworfen werden.', '', '', ''),
(2217, '"Bei Urals Hammer!"', '30 Sekunden lang werden alle Gruppenmitglieder in Hörweite mit vollen Lebens- und Energiepunkten wiederbelebt. Wiederbelebte Gruppenmitglieder fügen zusätzlich 25..33% Schaden zu. Wenn dieser Effekt endet, sterben alle betroffenen Gruppenmitglieder. Diese Fertigkeit verursacht keinen Sterbemalus.', '(30 Sekunden.) Alle toten Gruppenmitglieder in Hörweite werden mit vollen Lebens- und Energiepunkten wiederbelebt. Betroffene Gruppenmitglieder fügen zusätzlich 25...33% Schaden zu. Betroffene Gruppenmitglieder sterben am Ende der Wirkung, erhalten jedoch keinen Sterbemalus.', '', '', ''),
(2218, 'Betrunkener Meister', '72..90 Sekunden lang ist Eure Bewegungsgeschwindigkeit um 10..15% erhöht und der Abstand zwischen Euren Angriffen sinkt um 10..15%. Wenn Ihr beim Aktivieren dieser Fertigkeit betrunken seid, verändern sich diese Werte stattdessen um 25..33%.', '72...90 Sekunden: Eure Bewegungs- und Angriffsgeschwindigkeit ist um 10...15% erhöht, falls ihr nicht betrunken seid. Eure Bewegungs- und Angriffsgeschwindigkeit ist um 25...33%. erhöht, falls ihr betrunken seid.', '', '', ''),
(2219, 'Waffe des Großen Zwergs', '20 Sekunden lang fügt die Waffe des Verbündeten +15..20 Punkte Schaden zu und hat eine 28..40%ige Chance, Feinde zu Boden zu werfen.', '20 Sekunden: +15...20 Punkte Waffenschaden und 28...40%ige Chance, Feinde mit Angriffen zu Boden zu werfen. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2220, 'Rüstung des Großen Zwergs', '22..40 Sekunden lang hat der Verbündete (bzw. Ihr selbst) +24 Rüstung, +60 maximale Lebenspunkte und zusätzlich +24 Rüstung gegen Zerstörer.', '22...40 Sekunden: +24 Rüstung und +60 maximale Lebenspunkte. Zusätzlich +24 Rüstung gegen Zerstörer.', '', '', ''),
(2221, 'Atem des Großen Zwergs', 'Alle Gruppenmitglieder werden von Brennen befreit und um 50..60 Lebenspunkte geheilt.', 'Entfernt Brennen und heilt um 50...60 Lebenspunkte. Betrifft Gruppenmitglieder.', '', '', ''),
(2222, 'Schneesturm', 'An der aktuellen Position des Gegners wird ein Schneesturm erzeugt. 5 Sekunden erleiden umstehende Feinde um diesen Bereich 30..40 Punkte Kälteschaden pro Sekunde.', '5 Sekunden: Richtet jede Sekunde 30...40 Punkte Kälteschaden an. Trifft umstehende Feinde um anfängliche Position des Gegners.', '', '', ''),
(2223, 'Schwarzpulvermine', 'Wird Schwarzpulvermine aktiviert, erleiden alle Gegner in der Nähe 20..30 Punkte Feuer-Schaden, werden 7..10 Sekunden lang geblendet und leiden 7..10 Sekunden lang an einer Blutung. Schwarzpulvermine endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 20...30 Punkte Feuer-Schaden an. Verursacht die Zustände Blindheit und Blutung (7...10 Sekunden). <i>Leicht zu unterbrechen.</i>', '', '', ''),
(2224, 'Mursaat herbeirufen', 'Ruft einen Mursaat der Stufe 14..20 herbei, der 40..60 Sekunden lebt und über Schwächende Aufladung verfügt. Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.', 'Ruft einen Mursaat der Stufe 14...20 herbei (40...60 Sekunden Lebensdauer), der über Schwächende Aufladung verfügt. <i>Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.</i>', '', '', ''),
(2225, 'Rubin-Dschinn herbeirufen', 'Ruft einen Rubin-Dschinn der Stufe 14..20 herbei, der 40..60 Sekunden lebt und über Feueropfer verfügt. Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.', 'Ruft einen Rubin-Dschinn der Stufe 14...20 herbei (40...60 Sekunden Lebensdauer), der über Feueropfer verfügt. <i>Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.</i>', '', '', ''),
(2226, 'Eis-Kobold herbeirufen', 'Ruft einen Eis-Kobold der Stufe 14..20 herbei, der 40..60 Sekunden lang lebt und über Eisdornen verfügt. Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.', 'Ruft einen Eis-Kobold der Stufe 14...20 herbei (40...60 Sekunden Lebensdauer), der über Eisdornen verfügt. <i>Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.</i>', '', '', ''),
(2227, 'Naga-Schamanen herbeirufen', 'Ruft einen Naga-Schamanen der Stufe 14..20 herbei, der 40..60 Sekunden lang lebt und über Steinigung verfügt. Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.', 'Ruft einen Naga-Schamanen der Stufe 14...20 herbei (40...60 Sekunden Lebensdauer), der über Steinigung verfügt. <i>Es kann jeweils nur 1 Asura-Beschwörung aktiv sein.</i>', '', '', ''),
(2228, 'Geschickter Schlag', 'Wenn dieser Angriff erfolgreich ist, erleidet der Gegner 18..30 Punkte Schaden. Wenn er an Beschädigter Rüstung leidet, blutet er 18..30 Sekunden lang.', 'Richtet 18...30 Punkte Schaden an. Verursacht Zustand Blutung (18...30 Sekunden), falls Gegner Beschädigte Rüstung hat.', '', '', ''),
(2229, 'Siegel der Infektion', 'Wenn der Gegner blutet, leidet er 13..20 Sekunden lang an Krankheit.', 'Verursacht Zustand Krankheit (13...20 Sekunden), falls der Gegner blutet.', '', '', ''),
(2230, 'Tryptophan-Siegel', '14..20 Sekunden lang sind Bewegungen und Angriffe des Gegners und aller umstehenden Feinde um 23..40% verlangsamt.', '14...20 Sekunden: Die Bewegungs- und Angriffsgeschwindigkeit des Gegners und umstehender Feinde ist um 23...40% verringert.', '', '', ''),
(2231, 'Ebon-Kampfstandarte des Mutes', 'Ihr setzt eine Ebon-Kampfstandarte des Mutes an Eurer aktuellen Position. 14..20 Sekunden lang erhalten Verbündete, die keine Geister sind, in dem Bereich +24 Rüstung und zusätzlich +24 Rüstung gegen Charr.', '14...20 Sekunden: Verbündete in dieser Abwehr haben +24 Rüstung und zusätzlich +24 Rüstung gegen Charr. <i>Geister sind nicht betroffen.</i>', '', '', ''),
(2232, 'Ebon-Kampfstandarte der Weisheit', 'Ihr setzt eine Ebon-Kampfstandarte der Weisheit an Eurer aktuellen Position. 14..20 Sekunden lang haben Verbündete, die keine Geister sind, in dem Bereich eine 44..60%ige Chance, die Wiederaufladezeit des Zaubers, den sie wirken, zu halbieren.', '14...20 Sekunden: Von Verbündeten in dieser Abwehr gewirkte Zauber haben eine 44...60%ige Chance, sich um 50% schneller aufzuladen. <i>Geister sind nicht betroffen.</i>', '', '', ''),
(2233, 'Ebon-Kampfstandarte der Ehre', 'Ihr setzt eine Ebon-Kampfstandarte der Ehre an Eurer aktuellen Position. 14..20 Sekunden lang verursachen Verbündete, die keine Geister sind, in dem Bereich +8..15 Punkte Schaden und zusätzlich +7..10 Punkte gegen Charr.', '14...20 Sekunden: Verbündete in dieser Abwehr richten +8...15 Punkte Schaden an und zusätzlich +7...10 Punkte Schaden gegen Charr. <i>Geister sind nicht betroffen.</i>', '', '', ''),
(2234, 'Ebon-Vorhut-Heckenschützen-Unterstützung', 'Der Gegner erleidet 54..90 Punkte Stichschaden und leidet 5..25 Sekunden lang an einer Blutung. Bei diesem Angriff besteht eine 10%ige Chance, zusätzlich 540..900 Punkte Stichschaden zuzufügen. Wenn dieser Angriff einen Charr trifft, besteht stattdessen eine 25%ige Chance auf zusätzlich 540..900 Punkte Stichschaden.', 'Richtet 54...90 Punkte Stichschaden und Zustand Blutung an (5...25 Sekunden). 10%ige Chance auf +540...900 Stichschaden. Stattdessen 25% Chance auf +540...900 Stichschaden, wenn der Gegner ein Charr ist.', '', '', ''),
(2235, 'Ebon-Vorhut-Assassinen-Unterstützung', 'Ruft einen Ebon-Vorhut-Assassinen der Stufe 14..20 mit Eiserne Handfläche, Reißzähne des Fuchses und Neunschwänziger Stoß herbei. Dieser herbeigerufene Assassine unternimmt einen Schattenschritt zum Gegner hin. Wenn es sich beim Gegner um einen Charr handelt, lebt der Assassine 24..30 Sekunden lang. Wenn er kein Charr ist, lebt der Assassine 12..15 Sekunden lang.', 'Ruft einen Assassinen der Stufe 14...20 herbei, der über Eiserne Handfläche, Reißzähne des Fuchses und Neunschwänziger Stoß verfügt; er unternimmt einen Schattenschritt zum Gegner. Ist der Gegner ein Charr, lebt der Assassine 24...30 Sekunden lang. Andernfalls lebt der Assassine 12...15 Sekunden lang.', '', '', ''),
(2236, 'Brunnen des Ruins', 'Der nächste Kadaver wird an seiner Position in einen Brunnen des Ruins verwandelt. 5..30 Sekunden lang leidet jeder Feind im Brunnen, der physischen Schaden erleidet, 5..20 Sekunden lang an Beschädigter Rüstung.', '(5...30 Sekunden.) Verursacht bei jedem Feind im Brunnen, der physischen Schaden erleidet, den Zustand Beschädigte Rüstung (5...20 Sekunden). <i>Benutzt frischen Kadaver.</i>', '', '', ''),
(2237, 'Schwund', '3..7 Sekunden lang ist das Primärattribut des Gegners auf 0 reduziert.', '3...7 Sekunden: Das Primärattribut des Gegners wird auf 0 gesetzt.', '', '', ''),
(2238, 'Speer der Erlösung', 'Wenn dieser Angriff erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn er nicht trifft, verliert Ihr einen Zustand.', 'Richtet +5...20 Punkte Schaden an. Falls der Angriff nicht trifft, verliert Ihr einen Zustand.', '', '', ''),
(2353, '"Erledigt ihn!"', 'Wenn die Lebenspunkte des Gegners weniger als 50% betragen, erleidet er 44..80 Punkte Schaden und leidet 12..20 Sekunden lang an Beschädigter Rüstung und Tiefer Wunde.', 'Richtet 44...80 Punkte Schaden an und verursacht die Zustände Beschädigte Rüstung und Tiefe Wunde (12...20 Sekunden). <i>Der Gegner muss weniger als 50% seiner maximalen Lebenspunkte haben, sonst keine Wirkung.</i>', '', '', '');
INSERT INTO `gw1_skilldesc_de` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(2354, '"Weicht aus!"', 'Während der nächsten 16..20 Sekunden kann Euer nächster Angriff nicht geblockt werden und fügt +14..20 Punkte Schaden zu.', '16...20 Sekunden: Euer nächster Angriff kann nicht geblockt werden und richtet +14...20 Punkte Schaden an.', '', '', ''),
(2355, 'Ich bin am stärksten!', 'Eure nächsten 5..8 Angriffe fügen zusätzlich +14..20 Punkte Schaden zu.', 'Euer nächster<sub>sic</sub> 5...8 Angriffe richten +14...20 Punkte Schaden an.', '', '', ''),
(2356, '"Ich bin unaufhaltbar!"', '16..20 Sekunden habt Ihr +24 Rüstung und könnt weder zu Boden geworfen noch verkrüppelt werden.', '20 Sekunden: Ihr habt +24 Rüstung und könnt nicht zu Boden geworfen oder verkrüppelt werden.', '', '', ''),
(2357, 'Ein Funken Arglist', 'Der berührte Gegner erleidet 44..80 Punkte Schaden. Wenn er zu Boden geworfen wurde, kann er 5..8 Sekunden lang nicht angreifen.', 'Richtet 44...80 Punkte Schaden an. Gegner kann nicht angreifen (5...8 Sekunden), falls er zu Boden geworfen ist.', '', '', ''),
(2358, '"Ihr bewegt Euch wie ein Zwerg!"', 'Der Gegner wird zu Boden geworfen und erleidet 44..80 Punkte Schaden. Der Zielgegner leidet außerdem 8..15 Sekunden lang an Verkrüppelung.', 'Richtet 44...80 Punkte Schaden an, wirft zu Boden und verursacht den Zustand Verkrüppelung (8...15 Sekunden).', '', '', ''),
(2359, '"Ihr seid alle Schwächlinge!"', 'Der Gegner und ihn umstehende Feinde werden 8..12 Sekunden lang geschwächt.', 'Verursacht Zustand Schwäche (8...12 Sekunden). Betrifft auch umstehende Feinde.', '', '', ''),
(2360, 'Den Schmerz nicht spüren', '30 Sekunden lang erfreut Ihr Euch einer Lebenspunktregeneration von +2..3. Wenn Ihr beim Aktivieren dieser Fertigkeit betrunken seid, habt Ihr außerdem +200..300 maximale Lebenspunkte.', '30 Sekunden: Ihr habt +2...3 Lebenspunktregeneration. Ihr habt +200...300 maximale Lebenspunkte falls Ihr beim Aktivieren dieser Fertigkeit betrunken seid.', '', '', ''),
(2361, 'Tausend-Bären-Prügel', 'Wenn dieser Angriff erfolgreich ist, fügt er für jeden umstehenden Feind +6..9 Punkte Schaden zu (maximal 60 Punkte). Wenn der Feind kein Mensch ist, wird er zu Boden geworfen.', 'Richtet pro umstehendem Feind +6...9 Punkte Schaden an (maximal 60 Punkte). Wirft zu Boden, falls der Gegner kein Mensch ist.', '', '', ''),
(2374, 'Segen der Bärin', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild der Bärin an. 60 Sekunden lang habt Ihr 100 Rüstung und 750..800 Lebenspunkte. Eure Attribute werden auf 0 gesetzt und Eure Fertigkeiten werden durch Bärenangriffe ersetzt.', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild der Bärin an (60 Sekunden). Eure Attribute werden auf 0 gesetzt, Eure Fertigkeiten werden durch Bärenangriffe ersetzt und Ihr habt 100 Rüstung und 750...800 Lebenspunkte.', '', '', ''),
(2379, 'Segen des Wolfes', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild des Wolfes an. 60 Sekunden lang habt Ihr 80 Rüstung, 660..700 Lebenspunkte und 2..4 Lebenspunktregeneration. Eure Attribute werden auf 0 gesetzt und Eure Fertigkeiten werden durch Wolfsangriffe ersetzt.', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild des Wolfes an (60 Sekunden). Eure Attribute werden auf 0 gesetzt, Eure Fertigkeiten werden durch Wolfsangriffe ersetzt und Ihr habt 80 Rüstung, 660...700 Lebenspunkte und 2...4 Lebenspunktregeneration.', '', '', ''),
(2384, 'Segen des Raben', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild des Raben an. 60 Sekunden lang habt Ihr 80 Rüstung, 660..700 Lebenspunkte und eine 20..30%ige Chance zu blocken. Eure Attribute werden auf 0 gesetzt und Eure Fertigkeiten werden durch Rabenangriffe ersetzt.', 'Ihr verliert sämtliches Eigentum und nehmt das Erscheinungsbild des Raben an (60 Sekunden). Eure Attribute werden auf 0 gesetzt, Eure Fertigkeiten werden durch Rabenangriffe ersetzt und Ihr habt 80 Rüstung, 660...700 Lebenspunkte und eine 20...30%ige Chance zu blocken.', '', '', ''),
(2411, 'Geistbrecher', '10..16 Sekunden lang bewegt Ihr Euch um 20..33% schneller und die Wirkzeit Eurer Zauber ist um 20% kürzer.', '(10...16 Sekunden.) Ihr bewegt Euch um 20...33% schneller und wirkt Zauber um 20% schneller.', '', '', ''),
(2412, 'Durchtriebener Krimineller', '10..20 Sekunden lang wird ein zufällig gewählter Zauber des Gegners deaktiviert und Durchtriebener Krimineller wird durch diesen Zauber ersetzt. Ihr erhaltet 5..10 Energiepunkte.', '10...20 Sekunden: Deaktiviert einen Zauber. Diese Fertigkeit wird zum betreffenden Zauber. Ihr erhaltet 5...10 Energiepunkte.', '', '', ''),
(2413, 'Technobabbel', 'Der Gegner und alle umstehenden Feinde erleiden 30..40 Punkte Schaden. Wenn er kein Boss ist, leiden der Gegner und alle umstehenden Feinde 3..5 Sekunden an Benommenheit.', 'Richtet beim Gegner und umstehenden Feinden 30...40 Punkte Schaden an. Verursacht bei Feinden Zustand Benommenheit (3...5 Sekunden), falls Gegner kein Boss ist.', '', '', ''),
(2414, 'Strahlungsfeld', '5 Sekunden lang unterliegen Feinde in dem Bereich einer Lebenspunktdegeneration von -4..6. Wenn das Portal endet, leiden alle Feinde in dem Bereich 12..20 Sekunden lang an Krankheit.', '5 Sekunden: Verursacht bei Feinden im Bereich -4...6 Lebenspunktdegeneration. Wirkung am Ende: Verursacht bei Feinden im Bereich den Zustand Krankheit (12...20 Sekunden).', '', '', ''),
(2415, 'Asurischer Scan', 'Verhext den Gegner 9..12 Sekunden lang mit "Asurischer Scan". Solange ein Feind mit dieser Verhexung belegt ist, können Eure Angriffe einen beliebigen Feind nicht verfehlen. Wenn ein mit "Asurischer Scan" verhexter Feind stirbt, entfernt dies 5% Sterbemalus von Euch.', '(9...12 Sekunden.) Der Gegner und Ihr selbst können weder Feinde verfehlen noch geblockt werden. Wenn der verhexte Gegner stirbt, verliert Ihr 5% Sterbemalus.', '', '', ''),
(2416, 'Hochnäsigkeit', '20..30 Sekunden lang erhaltet Ihr jedes Mal, wenn Ihr durch das Töten eines Feindes Erfahrung sammelt, einen willkürlichen asurischen Vorteil.', '20...30 Sekunden: Jedes Mal, wenn Ihr durch das Töten eines Feindes Erfahrung sammelt, erhaltet Ihr einen willkürlichen asurischen Vorteil.', '', '', ''),
(2417, 'Geistige Sperre', '5..11 Sekunden lang habt Ihr eine 50%ige Chance Angriffe zu blocken. Diese Verzauberung wird jedes Mal, wenn Ihr von einem Feind getroffen werdet, erneuert.', '5...11 Sekunden: Ihr habt eine 50%ige Chance Angriffe zu blocken. Erneuerung: jedes Mal, wenn ein Feind Euch trifft.', '', '', ''),
(2418, 'Schmerztauscher', '6..10 Sekunden lang erleidet der Gegner jedes Mal, wenn er Schaden zufügt, 100..140% des Schadens, den er zufügt (maximal 80 Punkte).', '6...10 Sekunden: Richtet jedes Mal, wenn er Schaden anrichtet, 100...140% des Schadens (maximal 80 Punkte) beim Gegner selbst an.', '', '', ''),
(2420, 'Ebon-Flucht', 'Unternehmt einen Schattenschritt zum ausgewählten Verbündeten hin. Ihr und der Verbündete werden um 70..110 Lebenspunkte geheilt.', 'Heilt Euch und den Verbündeten um 70...110 Lebenspunkte. Anfangswirkung: Ihr unternehmt einen Schattenschritt zum Verbündeten. <i>Sich selbst als Ziel wählen nicht möglich.</i>', '', '', ''),
(2421, 'Schwäche-Falle', 'Wird Schwäche-Falle aktiviert, werden alle Feinde in der Nähe 10..20 Sekunden lang geschwächt und erleiden 24..50 Punkte Blitzschaden. Alle Charr werden zu Boden geworfen. Schwäche-Falle endet nach 90 Sekunden. Bei der Aktivierung dieser Fertigkeit könnt Ihr leicht unterbrochen werden.', '90 Sekunden: Betrifft Feinde in der Nähe. Richtet 24...50 Punkte Blitzschaden an. Verursacht Zustand Schwäche (10...20 Sekunden). Wirft Charr zu Boden. <i>Leicht zu unterbrechen.</i>', '', '', ''),
(2422, 'Winde', 'Erstellt einen Geist der Stufe 4..10. Alle Feinde in seiner Reichweite haben eine 15%ige Chance, dass ihre Distanzangriffe fehlschlagen. Dieser Geist stirbt nach 54..90 Sekunden.', 'Erstellt einen Geist der Stufe 4...10 (54...90 Sekunden Lebensdauer). Betrifft Feinde in Reichweite. Distanzangriffe gehen mit 15%iger Wahrscheinlichkeit daneben.', '', '', ''),
(2423, 'Zwergenstabilität', '24..30 Sekunden lang dauern Eure Haltungen um 55..100% länger an. Wenn Ihr diese Fertigkeit aktiviert, während Ihr betrunken seid, könnt Ihr nicht zu Boden geworfen werden.', '24...30 Sekunden: Eure Haltungen dauern um 55...100% länger an. Ihr könnt nicht zu Boden geworfen werden, falls Ihr beim Aktivieren dieser Fertigkeit betrunken seid.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(0, '', '', '', '', '', ''),
(1, 'Healing Signet', 'You gain 82..172 Health. You have -40 armor while using this skill.', 'You gain 82...172 Health. <i>You have -40 armor while using this skill.</i>', '', '', ''),
(2, 'Resurrection Signet', 'Resurrect target party member. That party member is returned to life with 100% Health and 25% Energy. This signet only recharges when you gain a morale boost.', 'Resurrects target party member (100% Health, 25% Energy). This signet only recharges when you gain a morale boost.', '', '', ''),
(3, 'Signet of Capture', 'Choose one skill from a nearby dead Boss of your profession. Signet of Capture is permanently replaced by that skill. If that skill was elite, gain 250 XP for every level you have earned.', 'Choose one skill from a nearby dead Boss of your profession. Signet of Capture is permanently replaced by that skill. If that skill was elite, gain 250 XP for every level you have earned.', '', '', ''),
(5, 'Power Block', 'If target foe is casting a spell or chant, that skill and all skills of the same attribute are disabled for 1..12 seconds and that skill is interrupted.', 'If target foe is casting a spell or chant, that skill and all skills of the same attribute are disabled (1...12 seconds) and that skill is interrupted.', '', '', ''),
(6, 'Mantra of Earth', 'For 30..90 seconds, whenever you take earth damage, the damage is reduced by 26..50% and you gain 2 Energy.', '(30...90 seconds.) Reduces earth damage you take by 26...50%. You gain 2 Energy when you take earth damage.', '', '', ''),
(7, 'Mantra of Flame', 'For 30..90 seconds, whenever you take fire damage, the damage is reduced by 26..50% and you gain 2 Energy.', '(30...90 seconds.) Reduces fire damage you take by 26...50%. You gain 2 Energy when you take fire damage.', '', '', ''),
(8, 'Mantra of Frost', 'For 30..90 seconds, whenever you take cold damage, the damage is reduced by 26..50% and you gain 2 Energy.', '(30...90 seconds.) Reduces cold damage you take by 26...50%. You gain 2 Energy when you take cold damage.', '', '', ''),
(9, 'Mantra of Lightning', 'For 30..90 seconds, whenever you take lightning damage, the damage is reduced by 26..50% and you gain 2 Energy.', '(30...90 seconds.) Reduces lightning damage you take by 26...50%. You gain 2 Energy when you take lightning damage.', '', '', ''),
(10, 'Hex Breaker', 'For 5..80 seconds, the next time you are the target of a hex, that hex fails and the caster takes 10..46 damage.', '(5...80 seconds.) The next hex against you fails and the caster takes 10...46 damage.', '', '', ''),
(11, 'Distortion', ' For 1..5 second[s], you have a 75% chance to block attacks.  Whenever you block an attack this way, you lose 2 Energy or Distortion ends.', '(1...5 seconds.) You have 75% chance to block. <i>Block cost: you lose 2 Energy or Distortion ends.</i>', '', '', ''),
(13, 'Mantra of Recovery', 'For 5..20 seconds, spells you cast recharge 33% faster.', '(5...20 seconds.) Your spells recharge 33% faster.', '', '', ''),
(14, 'Mantra of Persistence', 'For 5..25 seconds, any Illusion Magic hex you cast lasts 10..40% longer.', '(5...25 seconds.) Illusion hexes you cast last 10...40% longer.', '', '', ''),
(15, 'Mantra of Inscriptions', 'For 5..25 seconds, signets you successfully activate while in this stance recharge 10..40% faster.', '(5...25 seconds.) Signets you successfully activate while in this stance recharge 10...40% faster.', '', '', ''),
(16, 'Mantra of Concentration', 'For 1..38 seconds, the next time you would be interrupted, you are not interrupted.', '(1...38 seconds.) The next time you would be interrupted, you are not interrupted.', '', '', ''),
(17, 'Mantra of Resolve', 'For 30..90 seconds, you cannot be interrupted, but each time you would have been interrupted, you lose 10..4 Energy or Mantra of Resolve ends.', '(30...90 seconds.) Prevents interrupts against you. <i>Prevention cost: lose 10...4 Energy or Mantra of Resolve ends.</i>', 'Mantra of Resolve (PvP)', 'For 5 seconds, you cannot be interrupted, but each time you would have been interrupted, you lose 10..4 Energy or Mantra of Resolve ends.', '(5 seconds.) Prevents interrupts against you. <i>Prevention cost: lose 10...4 Energy or Mantra of Resolve ends.</i>'),
(18, 'Mantra of Signets', 'For 10..40 seconds, you have +3 armor for each signet you have equipped. Whenever you use a signet you gain 5..60 health.', '(10...40 seconds.) You have +3 armor for each signet. You gain 5...60 health each time you use a signet.', 'Mantra of Signets (PvP)', 'For 10..40 seconds, whenever you use a signet, you gain 5..60 Health.', '(10...40 seconds.) You gain 5...60 Health each time you use a signet.'),
(19, 'Fragility', 'For 8..20 seconds, target and adjacent foes take 5..20 damage each time they suffer or recover from a new condition.', 'Also hexes foes adjacent to target (8...20 seconds). These foes take 5...20 damage each time they gain or lose a condition.', 'Fragility (PvP)', 'For 8..20 seconds, target foe takes 5..20 damage each time that foe suffers or recovers from a new condition.', '(8...20 seconds.) Target foe takes 5...20 damage each time it gains or loses a condition.'),
(21, 'Inspired Enchantment', 'Removes an enchantment from target foe and gain 3..15 Energy. For 20 seconds, Inspired Enchantment is replaced with the enchantment removed from target foe.', 'Removes an enchantment from target foe. Removal effects: you gain 3...15 Energy; this spell is replaced with that enchantment (20 seconds).', '', '', ''),
(22, 'Inspired Hex', 'Remove a hex from target ally and gain 4..10 Energy. For 20 seconds, Inspired Hex is replaced with the hex that was removed.', 'Removes a hex from target ally. Removal effects: you gain 4...10 Energy; this spell is replaced with that hex (20 seconds).', '', '', ''),
(23, 'Power Spike', 'If target foe is casting a spell or a chant, that skill is interrupted and target foe takes 30..120 damage.', 'Interrupts a spell or chant. Interruption effect: deals 30...120 damage.', '', '', ''),
(24, 'Power Leak', 'If target foe is casting a spell or chant, that skill is interrupted and target foe loses 3..17 Energy.', 'Interrupts a spell or chant. Interruption effect: causes 3...17 Energy loss.', '', '', ''),
(25, 'Power Drain', 'If target foe is casting a spell or chant, that skill is interrupted and you gain 1..31 Energy.', 'Interrupts a spell or chant. Interruption effect: you gain 1...31 Energy.', '', '', ''),
(26, 'Empathy', 'For 5..15 seconds, whenever target foe attacks, that foe takes 10..55 damage and deals 1..15 less damage.', '(5...15 seconds.) Target foe\'s attacks deal 1...15 less damage, and that foe takes 10...55 damage with each attack.', 'Empathy (PvP)', 'For 5..15 seconds, whenever target foe attacks, that foe takes 15..45 damage.', '(5...15 seconds.) Target foe takes 15...45 damage whenever it attacks.'),
(27, 'Shatter Delusions', 'Remove one Mesmer hex from target foe. If a hex was removed, that foe and all adjacent foes takes 15..75 damage.', 'Removes a Mesmer hex from target foe. Removal effect: 15...75 damage to target and all adjacent foes.', 'Shatter Delusions (PvP)', 'Remove one Mesmer hex from target foe. If a hex was removed, that foe and all adjacent foes takes 15..75 damage.', 'Removes a Mesmer hex from target foe. Removal effect: 15...75 damage to target and all adjacent foes.'),
(28, 'Backfire', 'For 10 seconds, whenever target foe casts a spell, that foe takes 35..140 damage.', '(10 seconds.) Target foe takes 35...140 damage whenever it casts a spell.', '', '', ''),
(29, 'Blackout', 'For 2..6 seconds, all of touched target foe\'s skills are disabled, and all of your skills are disabled for 5 seconds.', 'Touch Skill. (2...6 seconds.) Disables skills. <i>Your skills are disabled (5 seconds).</i>', '', '', ''),
(30, 'Diversion', 'For 6 seconds, the next time target foe uses a skill, that skill takes an additional 10..56 seconds to recharge.', '(6 seconds.) Target foe\'s next skill takes +10...56 seconds to recharge.', '', '', ''),
(31, 'Conjure Phantasm', ' For 2..16 seconds, target foe experiences -5 Health degeneration.', '(2...16 seconds.) Causes -5 Health degeneration.', '', '', ''),
(32, 'Illusion of Weakness', 'You lose 50..240 Health. Illusion of Weakness ends if damage drops your Health below 25% of your maximum. When Illusion of Weakness ends, you gain 50..240 Health.', 'Lose 50...240 Health. End effect: you gain 50...240 Health. <i>Ends if damage drops your Health below 25% of your maximum.</i>', '', '', ''),
(33, 'Illusionary Weaponry', 'For 30 seconds, your melee attacks neither hit nor fail to hit. Instead, Illusionary Weaponry deals 8..40 damage to your targets for each melee attack. You have +5 armor for each equipped Illusion Magic skill.', '(30 seconds.) Deals 8...40 damage to foes in place of other damage or effects from melee attacks. You have +5 armor for each equipped Illusion Magic skill. <i>Your melee attacks neither hit nor fail to hit.</i>', 'Illusionary Weaponry (PvP)', 'For 30 seconds, your melee attacks neither hit nor fail to hit. Instead, Illusionary Weaponry deals 8..40 damage to your targets for each melee attack.', '(30 seconds.) Deals 8...40 damage to foes in place of other damage or effects from melee attacks. <i>Your melee attacks neither hit nor fail to hit.</i>'),
(34, 'Sympathetic Visage', 'For 4..10 seconds, whenever target ally is hit by a melee attack, all adjacent foes lose all adrenaline and 3 Energy.', '(4...10 seconds.) All adjacent foes lose all adrenaline and 3 Energy whenever a melee attack hits target ally.', '', '', ''),
(35, 'Ignorance', 'For 8..20 seconds target foe cannot use signets.', '(8...20 seconds.) Target foe cannot use signets.', '', '', ''),
(36, 'Arcane Conundrum', 'For 5..15 seconds, spells cast by target foe and all adjacent foes take twice as long to cast. When this hex ends, you gain 1..7 energy. ', 'Also hexes foes adjacent to target (5...15 seconds). Doubles spell casting time. End effect: you gain 1...7 energy. <sub>sic</sub>', '', '', ''),
(37, 'Illusion of Haste', 'For 5..11 seconds you are no longer Crippled, and you move 33% faster. When Illusion of Haste ends, you become Crippled for 3 seconds.', '(5...11 seconds.) You move 33% faster. Initial effect: removes Crippled condition. <i>End effect: you are Crippled (3 seconds).</i>', 'Illusion of Haste (PvP)', 'For 5..11 seconds, you move 33% faster. When Illusion of Haste ends, you become Crippled for 3 seconds.', '(5...11 seconds.) You move 33% faster. <i>End effect: you are Crippled (3 seconds).</i>'),
(38, 'Channeling', 'For 8..56 seconds, whenever you cast a spell, you gain 1 Energy for each foe in the area.', '(8...56 seconds.) You gain 1 Energy for each foe in the area whenever you cast a spell.', '', '', ''),
(39, 'Energy Surge', 'Target foe loses 1..10 Energy. For each point of Energy lost, that foe and all nearby foes take 9 damage.', 'Causes 1...10 Energy loss. Deals 9 damage to target and nearby foes for each point of Energy lost.', '', '', ''),
(40, 'Ether Feast', 'Target foe loses 3 Energy. You are healed 20..65 for each point of Energy lost.', 'Cause 3 Energy loss. You gain 20...65 Health for each point of Energy lost.', '', '', ''),
(41, 'Ether Lord', 'You lose all Energy. For 5..10 seconds, target foe suffers -1..3 Energy degeneration, and you experience +1..3 Energy regeneration.', '<i>You lose all Energy.</i> Target foe has -1...3 Energy degeneration and you have +1...3 Energy regeneration (5...10 seconds).', '', '', ''),
(42, 'Energy Burn', 'Target foe loses 1..10 Energy and takes 9 damage for each point of Energy lost.', 'Causes 1...10 Energy loss. Deals 9 damage for each point of Energy lost.', '', '', ''),
(43, 'Clumsiness', 'For 4 seconds, target and adjacent foes are hexed with Clumsiness. The next time each foe attacks, the attack is interrupted and that foe suffers 10..92 damage.', '(4 seconds.) Also hexes adjacent foes. Interrupts next attack. Interruption effect: deals 10...92 damage.', '', '', ''),
(44, 'Phantom Pain', 'For 10 seconds, target foe suffers -1..4 Health degeneration. When Phantom Pain ends, that foe suffers a Deep Wound, lowering that foe\'s maximum Health by 20% for 5..20 seconds.', '(10 seconds.) Causes -1...4 Health degeneration. End effect: inflicts Deep Wound condition (5...20 seconds).', '', '', ''),
(45, 'Ethereal Burden', ' For 10 seconds, target foe moves 50% slower.  When Ethereal Burden ends, you gain 10..18 Energy.', '(10 seconds.) Target foe moves 50% slower. End effect: you gain 10...18 Energy.', '', '', ''),
(46, 'Guilt', 'For 6 seconds, the next time target foe casts a spell that targets a foe, the spell fails and you steal up to 5..14 Energy from that foe.', '(6 seconds.) Target foe\'s next spell fails and you steal 5...14 Energy. <i>No effect unless this foe\'s spell targets one of your allies.</i>', '', '', ''),
(47, 'Ineptitude', 'For 4 seconds, the next time target foe or any adjacent foe attacks, they take 30..135 damage and are Blinded for 10 seconds.', '(4 seconds.) Also hexes foes adjacent to target. Deals 30...135 damage. Inflicts Blindness condition (10 seconds). <i>No effect unless hexed foe attacks.</i>', '', '', ''),
(48, 'Spirit of Failure', 'For 30 seconds, target foe has a 25% chance to miss with attacks. You gain 1..3 Energy whenever that foe fails to hit in combat.', '(30 seconds.) Target foe has 25% chance to miss. You gain 1...3 Energy whenever this foe misses.', '', '', ''),
(49, 'Mind Wrack', 'For 5..40 seconds, whenever this foe is the target of any of your non-hex Mesmer skills, that foe loses 1 Energy and takes 5..25 damage per point of Energy lost. If the target foe\'s Energy drops to 0, Mind Wrack ends and that foe takes 15..100 damage.', '(5...40 seconds.) Causes 1 Energy loss each time foe is the target of your non-hex Mesmer skills. Deals 5...25 damage per point of Energy lost. If target foe\'s Energy drops to 0, it takes 15...100 damage and Mind Wrack ends.', 'Mind Wrack (PvP)', 'For 5..40 seconds, whenever this foe is the target of one of your non-hex Mesmer skills, that foe loses 1 Energy. If the target foe\'s Energy drops to 0, Mind Wrack ends and that foe takes 15..100 damage.', '(5...40 seconds.) Causes 1 Energy loss each time foe is the target of your non-hex Mesmer skills. If target foe\'s Energy drops to 0, Mind Wrack ends and that foe takes 15...100 damage.'),
(50, 'Wastrel\'s Worry', 'After 3 seconds, target foe and all adjacent foes take 20..100 damage. If that foe successfully uses a skill, Wastrel\'s Worry ends prematurely and does no damage.', '(3 seconds). End effect: causes 20...100 damage to target and adjacent foes. <i>No effect and ends early if target foe uses a skill.</i>', '', '', ''),
(51, 'Shame', 'For 6 seconds, the next time target foe casts a spell that targets an ally, the spell fails and you steal up to 5..14 Energy from that foe.', '(6 seconds.) Target foe\'s next spell fails and you steal 5...14 Energy. <i>No effect unless this foe\'s spell targeted one of its allies.</i>', '', '', ''),
(52, 'Panic', 'For 1..10 second[s], target foe and all nearby foes are hexed with Panic. When a foe hexed with Panic successfully uses a skill, all other nearby foes are interrupted.', 'Also hexes foes near your target (1...10 seconds). Interrupts all other nearby foes whenever a hexed foe successfully activates a skill.', '', '', ''),
(53, 'Migraine', 'For 5..20 seconds, target foe suffers -1..8 Health degeneration and takes 100% longer to activate skills.', '(5...20 seconds.) Causes -1...8 Health degeneration and doubles skill activation time.', 'Migraine (PvP)', 'For 3..12 seconds, target foe suffers -1..8 Health degeneration and takes 100% longer to activate skills.', '(3...12 seconds.) Causes -1...8 Health degeneration and doubles skill activation time.'),
(54, 'Crippling Anguish', 'For 5..20 seconds, target moves and attacks 50% slower and suffers -1..8 Health degeneration.', '(5...20 seconds.) Target foe moves and attacks 50% slower and has -1...8 Health degeneration.', 'Crippling Anguish (PvP)', 'For 5..20 seconds, target moves 50% slower and suffers -1..5 Health degeneration.', '(5...20 seconds.) Target foe moves 50% slower and has -1...5 Health degeneration.'),
(55, 'Fevered Dreams', 'For 10..25 seconds, whenever target foe suffers from a new condition, all foes in the area suffer from that condition as well. If target foe has two or more conditions that foe is Dazed for 1..3 second[s].', '(10...25 seconds.) Foes in the area also have any new conditions that target foe acquires. Inflicts Dazed on target foe (1...3 seconds) if that foe has two or more conditions.', 'Fevered Dreams (PvP)', 'For 5..15 seconds, whenever target foe suffers from a new condition, all foes in the area suffer from that condition as well.', '(5...15 seconds.) Foes in the area also have any new conditions that target foe acquires.'),
(56, 'Soothing Images', 'For 8..20 seconds, target foe and all adjacent foes cannot gain adrenaline.', 'Also hexes foe adjacent to target. (8...20 seconds). These foes cannot gain adrenaline.', '', '', ''),
(57, 'Cry of Frustration', 'If target foe is using a skill, that foe and all foes in the area are interrupted and suffer 15..75 damage.', 'If target foe is using a skill, that foe and all foes in the area are interrupted and take 15...75 damage.', '', '', ''),
(58, 'Signet of Midnight', 'You and target touched foe become Blinded for 15 seconds.', 'Elite Touch Signet. (15 seconds.) Inflicts Blindness condition. <i>You suffer from Blindness (15 seconds).</i>', '', '', ''),
(59, 'Signet of Weariness', 'Target foe and all nearby foes lose 3..8 Energy and suffer from Weakness for 1..12 second[s].', 'Also affects foes near your target. Causes 3...8 Energy loss and inflicts Weakness (1...12 seconds).', '', '', ''),
(61, 'Leech Signet', 'Interrupt target foe\'s action. If that action was a spell, you gain 3..15 Energy.', 'Interrupts an action. Interruption effect: you gain 3...15 Energy if the action was a spell.', '', '', ''),
(62, 'Signet of Humility', 'Target foe\'s elite skill is disabled for 1..16 second[s]. Your non-Mesmer skills are disabled for 10 seconds.', 'Disables elite skill (1...16 seconds). <i>Disables your non-Mesmer skills (10 seconds).</i>', '', '', ''),
(63, 'Keystone Signet', 'All of your signets except Keystone Signet are recharged. For 20 seconds, the next 0..6 times you use a signet that targets a foe, all other foes adjacent to your target take 15..60 damage and are interrupted.', '(20 seconds.) Your next 0...6 signets interrupt <sub>sic</sub> and deal 15...60 damage to other foes adjacent to your target. Initial Effect: <sub>sic</sub> recharges all of your other signets.', '', '', ''),
(65, 'Arcane Mimicry', 'For 20 seconds, Arcane Mimicry becomes the non-form elite skill from target other ally.', 'This skill becomes target ally\'s elite skill (20 seconds). <i>Cannot self-target. No effect if target\'s elite skill is a form.</i>', '', '', ''),
(66, 'Spirit Shackles', 'For 5..20 seconds, target foe loses 5 Energy whenever that foe attacks.', '(5...20). Target foe loses 5 Energy whenever it attacks.', '', '', ''),
(67, 'Shatter Hex', 'Remove a hex from target ally. If a hex is removed, foes near that ally take 30..120 damage.', 'Removes a hex from target ally. Removal effect: deals 30...120 damage to foes near this ally.', '', '', ''),
(68, 'Drain Enchantment', 'Remove an enchantment from target foe. If an enchantment is removed, you gain 8..17 Energy and 40..120 Health.', 'Removes an enchantment from target foe. Removal effect: you gain 8...17 Energy and 40...120 Health.', '', '', ''),
(69, 'Shatter Enchantment', 'Remove an enchantment from target foe. If an enchantment is removed, that foe takes 14..100 damage.', 'Removes an enchantment from target foe. Removal effect: deals 14...100 damage.', '', '', ''),
(72, 'Elemental Resistance', 'For 30..90 seconds, You gain +40 armor against elemental damage, but you lose 24..12 armor against physical damage.', '(30...90 seconds.) You have +40 armor against elemental damage. <i>You have -24...12 armor against physical damage.</i>', '', '', ''),
(73, 'Physical Resistance', 'For 30..90 seconds, You gain +40 armor against physical damage, but you lose 24..12 armor against elemental damage.', '(30...90 seconds.) You have +40 armor against physical damage. <i>You have -24...12 armor against elemental damage.</i>', '', '', ''),
(74, 'Echo', 'For 30 seconds, Echo is replaced with the next skill you use. Echo acts as this skill for 30 seconds.', '(30 seconds.) Echo becomes the next skill you use (30 seconds).', '', '', ''),
(75, 'Arcane Echo', 'If you cast a spell in the next 20 seconds, Arcane Echo is replaced with that spell for 20 seconds. Arcane Echo ends prematurely if you use a non-spell skill.', '(20 seconds.) Arcane Echo becomes the next spell you use (20 seconds). <i>This enchantment ends if you use any skill that is not a spell.</i>', '', '', ''),
(76, 'Imagined Burden', 'For 8..20 seconds, target foe moves 50% slower.', '(8...20 seconds.) Target foe moves 50% slower.', '', '', ''),
(77, 'Chaos Storm', 'Create a Chaos Storm at target foe\'s location that lasts for 10 seconds. Each second, foes adjacent to this location take 5..25 damage and lose 0..2 Energy.', 'Deals 5...25 damage and causes 0...2 Energy loss each second (10 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(78, 'Epidemic', 'Spread all negative conditions and their remaining durations from target foe to all foes adjacent to your target.', 'Conditions on target foe transfer to adjacent foes.', '', '', ''),
(79, 'Energy Drain', 'Target foe loses 2..9 Energy. You gain 3 Energy for each point of Energy lost.', 'Causes 2...9 Energy loss. You gain 3 Energy for each point of Energy lost.', '', '', ''),
(80, 'Energy Tap', 'Target foe loses 4..7 Energy. You gain 2 Energy for each point of Energy lost.', 'Causes 4...7 Energy loss. You gain 2 Energy for each point of Energy lost.', '', '', ''),
(81, 'Arcane Thievery', 'For 5..35 seconds, one random spell is disabled for target foe, and Arcane Thievery is replaced by that spell.', '(5...35 seconds.) Disables one random spell. This skill becomes that spell.', '', '', ''),
(82, 'Mantra of Recall', 'For 20 seconds, you gain no benefit from it. You gain 10..25 Energy when Mantra of Recall ends.', '(20 seconds.) End effect: you gain 10...25 Energy.', '', '', ''),
(83, 'Animate Bone Horror', 'Exploit nearest corpse to animate a level 1..17 bone horror.', 'Creates a level 1...17 bone horror. grey', '', '', ''),
(84, 'Animate Bone Fiend', 'Exploit nearest corpse to animate a level 1..17 bone fiend. Bone fiends can attack at range.', 'Creates a level 1...17 bone fiend that can attack at range. <i>Exploits a fresh corpse.</i>', '', '', ''),
(85, 'Animate Bone Minions', 'Exploit nearest corpse to animate two level 0..12 bone minions.', 'Creates two level 0...12 bone minions. <i>Exploits a fresh corpse.</i>', '', '', ''),
(86, 'Grenth\'s Balance', 'If target foe has more Health than you, you gain half the difference (up to your maximum Health), and that foe loses an equal amount. If this foe has less Health than you, you lose half the difference, and that foe gains an equal amount.', 'You gain Health equal to half the difference between you and target, and this foe loses an equal amount. <i>If this foe has less Health than you, you lose Health equal to half the difference.</i>', '', '', ''),
(87, 'Verata\'s Gaze', 'If target hostile animated undead has a master, its bond to its master is broken, making it hostile to all other creatures. If it had no master, you become its master. (50% failure chance with Death Magic 4 or less.)', 'Make target undead servant masterless. If it is already masterless, you become its master. <i>50% failure chance unless Death Magic 5 or more.</i>', '', '', ''),
(88, 'Verata\'s Aura', 'All hostile animated undead in the area become bound to you. Verata\'s Aura ends after 120..300 seconds. When Verata\'s Aura ends, you lose your bond with any undead bound to you. (50% failure chance with Death Magic 4 or less.)', '(120...300 seconds.) Become master of all hostile undead servants in the area. <i>End effect: your undead servants become masterless. 50% failure chance unless Death Magic 5 or more.</i>', '', '', ''),
(89, 'Deathly Chill', 'Target foe is struck for 5..50 cold damage. If that foe\'s Health is above 50%, you deal an additional 5..50 shadow damage.', 'Deals 5...50 cold damage. Deals 5...50 more damage if target foe was above 50% Health.', '', '', ''),
(90, 'Verata\'s Sacrifice', 'For 5..10 seconds, your undead allies gain +10 Health regeneration. All conditions are removed from those allies and transferred to you. If this spell is successful and you have control of 3 or fewer minions, Verata\'s Sacrifice instantly recharges.', '(5...10 seconds.) 10 undead allied servants gain +10 Health regeneration. Instantly recharges if you have control of 3 or fewer servants. <i>Transfers all conditions from those servants to you.</i>', '', '', ''),
(91, 'Well of Power', 'Exploit nearest corpse to create a Well of Power at that location. For 8..20 seconds, allies within the area of Well of Power gain +1..6 Health regeneration and +2 Energy regeneration.', '(8...20 seconds.) Allies in this well have +1...6 Health regeneration and +2 Energy regeneration. <i>Exploits a fresh corpse.</i>', '', '', ''),
(92, 'Well of Blood', 'Exploit nearest corpse to create a Well of Blood at its location. For 8..20 seconds, allies in that area receive +1..6 Health regeneration.', '(8...20 seconds.) Allies in this well have +1...6 Health regeneration. <i>Exploits a fresh corpse.</i>', '', '', ''),
(93, 'Well of Suffering', 'Exploit nearest corpse to create a Well of Suffering at its location. For 10..30 seconds, foes in that area suffer -1..6 Health degeneration.', '(10...30 seconds.) Foes in this well have -1...6 Health degeneration. <i>Exploits a fresh corpse.</i>', '', '', ''),
(94, 'Well of the Profane', 'Exploit nearest corpse to create a Well of the Profane at its location. For 8..20 seconds, foes in that area are stripped of all enchantments and cannot be the target of further enchantments. (50% failure chance with Death Magic 4 or less.)', '(8...20 seconds.) Foes in this well lose all enchantments and cannot be the target of further enchantments. <i>Exploits a fresh corpse. 50% failure chance unless Death Magic 5 or more.</i>', '', '', ''),
(95, 'Putrid Explosion', 'The corpse nearest your target explodes, sending out a shockwave that deals 24..120 damage to nearby foes.', 'Explodes a corpse, dealing 24...120 damage to foes near it. <i>Exploits a fresh corpse.</i>', '', '', ''),
(96, 'Soul Feast', 'Exploit nearest corpse to gain 50..280 Health.', 'You gain 50...280 Health. <i>Exploits a fresh corpse.</i>', '', '', ''),
(97, 'Necrotic Traversal', 'Exploit a random corpse. You teleport to that corpse\'s location and all nearby foes become Poisoned for 5..20 seconds.', 'Teleport to a corpse\'s location. Inflicts Poisoned condition (5...20 seconds). Affects all nearby foes. <i>Exploits a fresh corpse.</i>', '', '', ''),
(98, 'Consume Corpse', 'Exploit a random corpse. You teleport to that corpse\'s location and gain 25..100 Health and 5..20 Energy.', 'Teleport to a corpse\'s location. You gain 25...100 Health and 5...20 Energy. <i>Exploits a fresh corpse.</i>', '', '', ''),
(99, 'Parasitic Bond', 'For 20 seconds, target foe suffers -1 Health degeneration. The caster is healed for 30..120 when Parasitic Bond ends.', '(20 seconds.) Causes -1 Health degeneration. End effect: you are healed for 30...120 Health.', '', '', ''),
(100, 'Soul Barbs', 'For 30 seconds, target foe takes 15..30 damage when an enchantment or hex is cast on that target.', '(30 seconds.) Deals 15...30 damage when an enchantment or hex is cast on target foe.', '', '', ''),
(101, 'Barbs', 'For 30 seconds, target foe takes 1..15 more damage when hit by physical damage.', '(30 seconds.) Target foe takes 1...15 damage whenever it takes physical damage.', '', '', ''),
(102, 'Shadow Strike', 'Target foe takes 12..48 shadow damage. If that foe\'s Health is above 50%, you steal up to 12..48 Health.', 'Deals 12...48 damage. Steal up to 12...48 Health if this foe was above 50% Health.', '', '', ''),
(103, 'Price of Failure', 'For 30 seconds, target foe has a 25% chance to miss with attacks and takes 1..46 damage whenever that foe fails to hit in combat.', '(30 seconds.) 25% chance to miss. Target foe takes 1...46 damage whenever it fails to hit.', '', '', ''),
(104, 'Death Nova', 'For 30 seconds, if target ally dies, all adjacent foes take 26..100 damage and are Poisoned for 15 seconds.', '(30 seconds.) Deals 26...100 damage and inflicts Poisoned condition (15 seconds) on adjacent foes if target ally dies.', '', '', ''),
(105, 'Deathly Swarm', 'Deathly Swarm flies out slowly and strikes for 30..90 cold damage on up to three targets in the area.', 'Deathly Swarm flies out slowly and deals 30...90 cold damage. Hits two additional foes in the area.', '', '', ''),
(106, 'Rotting Flesh', 'Target fleshy foe becomes Diseased for 10..25 seconds, slowly losing Health.', 'Inflicts Diseased condition (10...25 seconds).', '', '', ''),
(107, 'Virulence', 'If target foe was already suffering from a condition, that foe suffers from Disease, Poison, and Weakness for 3..15 seconds.', 'Inflicts Disease, Poison, and Weakness conditions (3...15 seconds). <i>No effect unless this foe already had a condition.</i>', '', '', ''),
(108, 'Suffering', 'For 6..30 seconds, target foe and all nearby foes suffer -0..3 Health degeneration.', 'Also hexes foes near target (6...30 seconds). These foes have -0...3 Health degeneration.', '', '', ''),
(109, 'Life Siphon', 'For 12..24 seconds, target foe suffers -1..3 Health degeneration, and you gain +1..3 Health regeneration.', '(12...24 seconds.) Target foe has -1...3 Health degeneration. You have +1...3 Health regeneration.', '', '', ''),
(110, 'Unholy Feast', 'Steal up to 10..65 Health from up to 1..4 foes  in the area.', 'Steals 10...65 Health from 1...4 foes in the area around you.', 'Unholy Feast (PvP)', 'Steal up to 10..65 Health from up to 1..4 foes  in the area.', 'Steals 10...65 Health from 1...4 foes in the area around you.'),
(111, 'Awaken the Blood', 'For 20..44 seconds, you gain +2 Blood Magic and +2 Curses, but whenever you sacrifice Health, you sacrifice 50% more than the normal amount.', '(20...44 seconds.) You have +2 Blood Magic and Curses. <i>Sacrifice 50% more Health than normal.</i>', '', '', ''),
(112, 'Desecrate Enchantments', 'Target foe and all nearby foes take 6..60 shadow damage and 4..20 shadow damage for each enchantment on them.', 'Deals 6...60 damage to target and nearby foes. Deals 4...20 more damage for each enchantment on them.', '', '', ''),
(113, 'Tainted Flesh', 'For 20..44 seconds, target ally is immune to disease, and anyone striking that ally in melee becomes Diseased for 3..15 seconds.', '(20...44 seconds.) Foes who hit target ally in melee become Diseased (3...15 seconds); this ally is immune to Disease.', '', '', ''),
(114, 'Aura of the Lich', 'All corpses within earshot are exploited and you animate a level 1..17 bone horror plus one for each corpse exploited in this way. For 5..45 seconds, your Death Magic attribute is increased by +1.', 'Exploit all corpses in earshot. Animates a level 1...17 bone horror, plus one for each exploited corpse. You have +1 Death Magic (5...45 seconds).', '', '', ''),
(115, 'Blood Renewal', 'For 7 seconds, you gain +3..6 Health regeneration. When Blood Renewal ends, you gain 40..190 Health.', '(7 seconds.) You have +3...6 Health regeneration. End effect: heals you for 40...190.', '', '', ''),
(116, 'Dark Aura', 'For 30 seconds, whenever target ally sacrifices Health, Dark Aura deals 5..50 shadow damage to adjacent foes, and you lose 5..20 Health.', '(30 seconds). Deals 5...50 damage to adjacent foes whenever target ally sacrifices Health. <i>Damage cost: you lose 5...20 Health.</i>', '', '', ''),
(117, 'Enfeeble', 'Target suffers from Weakness for 10..30 seconds.', 'Inflicts Weakness condition (10...30 seconds).', 'Enfeeble (PvP)', 'Target suffers from Weakness for 10..30 seconds.', 'Inflicts Weakness condition (10...30 seconds).'),
(118, 'Enfeebling Blood', 'Target foe and all nearby foes suffer from Weakness for 5..20 seconds.', 'Inflicts Weakness condition (5...20 seconds) on this foe and nearby foes.', 'Enfeebling Blood (PvP)', 'Target foe and all nearby foes suffer from Weakness for 1..16 second[s].', 'Inflicts Weakness condition (1...16 seconds) on this foe and nearby foes.'),
(119, 'Blood is Power', 'For 10 seconds, target other ally gains +3..6 Energy regeneration.', '(10 seconds.) +3...6 Energy regeneration. <i>Cannot self-target.</i>', '', '', ''),
(120, 'Blood of the Master', 'All of your undead allies are healed for 30..116 Health. You sacrifice an additional 2% maximum Health per minion healed in this way.', 'Heals your undead servants for 30...116. <i>Healing cost: +2% Health sacrifice per servant healed.</i>', '', '', ''),
(121, 'Spiteful Spirit', 'For 8..20 seconds, whenever target foe attacks or uses a skill, Spiteful Spirit deals 5..35 shadow damage to that foe and all adjacent allies of that foe.', '(8...20 seconds.) Deals 5...35 damage to target and adjacent foes whenever this foe attacks or uses a skill.', '', '', ''),
(122, 'Malign Intervention', 'For 5..20 seconds, target foe receives 20% less benefit from healing. If target foe dies while hexed with Malign Intervention, a level 1..17 masterless bone horror is summoned.', '(5...20 seconds.) Target foe receives 20% less from healing. If this foe dies while suffering from this hex, a level 1...17 masterless bone horror is summoned.', '', '', ''),
(123, 'Insidious Parasite', 'For 5..15 seconds, whenever target foe hits with an attack, you steal up to 15..45 Health from that foe.', '(5...15 seconds.) Steal 15...45 Health whenever target foe hits with an attack.', '', '', ''),
(124, 'Spinal Shivers', 'For 10..40 seconds, whenever target foe is struck for cold damage while using a skill, that foe is interrupted, and you lose 10..5 Energy or Spinal Shivers ends.', '(10...40 seconds.) Cold damage interrupts target foe\'s skills. <i>Interruption cost: you lose 10...5 Energy or Spinal Shivers ends.</i>', '', '', ''),
(125, 'Wither', 'For 5..35 seconds, target foe suffers -2..4 Health degeneration and -1 Energy degeneration. If target foe\'s Energy reaches 0, that foe takes 15..75 damage and Wither ends.', '(5...35 seconds.) Causes -2...4 Health degeneration and -1 Energy degeneration. Deals 15...75 damage if target foe\'s Energy drops to 0. <i>Ends if this foe\'s Energy drops to 0.</i>', '', '', ''),
(126, 'Life Transfer', 'For 6..12 seconds, target foe and adjacent foes suffer -3..8 Health degeneration, which you gain as Health regeneration.', 'Also hexes foes adjacent to target (6...12 second). Causes -3...8 Health degeneration. You have +3...8 Health regeneration.', '', '', ''),
(127, 'Mark of Subversion', 'For 6 seconds, the next time target foe casts a spell that targets an ally of that foe, the spell fails and you steal up to 10..92 Health from that foe.', '(6 seconds.) Target foe\'s next spell fails and you steal 10...92 Health. <i>No effect unless this foe\'s spell targeted one of its allies.</i>', '', '', ''),
(128, 'Soul Leech', 'For 10 seconds, whenever target foe casts a spell, you steal up to 16..80 Health from that foe.', '(10 seconds.) Steal 16...80 Health whenever target foe casts a spell.', '', '', ''),
(129, 'Defile Flesh', 'For 5..35 seconds, target foe gains only two-thirds Health from healing.', '(5...35 seconds.) Reduces healing target foe receives by 33%. <i>Only skills with the word "heal" in the description are affected.</i>', '', '', ''),
(130, 'Demonic Flesh', 'For 30..60 seconds, whenever you use a skill that targets a foe, you deal 5..20 shadow damage to all other foes adjacent to you.', '(30...60 seconds.) When using a skill on a foe, deal 5...20 shadow damage to all other foes adjacent to you.', '', '', ''),
(131, 'Barbed Signet', 'You inflict Bleeding for 3..15 seconds on target foe and all adjacent foes.', 'Target and adjacent foes suffer from Bleeding (3...15 seconds).', '', '', ''),
(132, 'Plague Signet', 'Transfer all negative conditions with 100..200% of their remaining durations from yourself to target foe. (50% failure chance with Curses 4 or less.)', 'Transfers all conditions with 100...200% of their remaining durations from yourself to target foe. <i>50% failure chance unless Curses 5 or more.</i>', '', '', ''),
(133, 'Dark Pact', 'Deal 10..48 shadow damage to target foe.', 'Deals 10...48 damage.', '', '', ''),
(134, 'Order of Pain', 'For 5 seconds, whenever a party member hits a foe with physical damage, that party member does +3..16 damage.', 'Enchants all party members (5 seconds). 3...16 more damage whenever these party members hit with physical damage.', '', '', ''),
(135, 'Faintheartedness', 'For the next 3..16 seconds, target foe attacks 50% slower, and that foe suffers -0..3 Health degeneration.', '(3...16 seconds.) Target foe attacks 50% slower and has -0...3 Health degeneration.', '', '', ''),
(136, 'Shadow of Fear', 'Target foe and all adjacent foes attack 50% slower for the next 5..30 seconds.', 'Also hexes foes adjacent to target (5...30 seconds). They attack 50% slower.', '', '', ''),
(137, 'Rigor Mortis', 'For 8..20 seconds, target foe cannot block.', '(8...20 seconds.) Target foe cannot block.', '', '', ''),
(138, 'Dark Bond', 'For the next 30..60 seconds, whenever you receive damage, your closest minion suffers 75% of that damage for you.', '(30...60 seconds.) Transfers 75% of incoming damage from you to your nearest servant.', '', '', ''),
(139, 'Infuse Condition', 'For the next 15..60 seconds, whenever you receive a condition, that condition is transferred to your closest minion instead.', '(15...60 seconds.) Whenever you receive a condition, it transfers from you to your closest undead servant.', '', '', ''),
(140, 'Malaise', 'For 5..35 seconds, target foe suffers -1 Energy degeneration and you suffer -1 Health degeneration. If target foe\'s Energy reaches 0, that foe takes 5..50 damage and Malaise ends.', '(5...35 seconds.) Causes -1 Energy degeneration. Deals 5...50 damage if target foe\'s Energy drops to 0. <i>You have -1 Health degeneration. Ends if this foe\'s Energy drops to 0.</i>', '', '', ''),
(141, 'Rend Enchantments', 'Remove 5..9 enchantments from target foe. For each Monk enchantment removed, you lose 55..25 Health.', 'Removes 5...9 enchantments from target foe. <i>Removal cost: you lose 55...25 Health for each Monk enchantment removed.</i>', '', '', ''),
(142, 'Lingering Curse', 'For 6..30 seconds, target foe and all nearby foes suffer -0..3 Health degeneration, and gain 20% less benefit from healing.', '(6...30 seconds.) Target and nearby foes have -0...3 Health degeneration and receive 20% less benefit from healing.', '', '', ''),
(143, 'Strip Enchantment', 'Remove 0..2 enchantment[s] from target foe. If an enchantment is removed, you steal 5..65 Health.', 'Removes 0...2 enchantments from target foe. Removal effect: you steal 5...65 Health.', '', '', ''),
(144, 'Chilblains', 'You become Poisoned for 10 seconds. Foes in the area of your target are struck for 10..44 cold damage and lose 1..2 enchantment[s].', 'Deals 10...44 cold damage to foes in the area around your target; removes 1...2 enchantments from these foes. <i>You are Poisoned (10 seconds).</i>', '', '', ''),
(145, 'Signet of Agony', 'You suffer from Bleeding for 25 seconds. All nearby foes take 10..70 damage.', 'Deals 10...70 damage to foes near you. <i>You begin Bleeding (25 seconds).</i>', 'Signet of Agony (PvP)', 'You suffer from Bleeding for 25 seconds. All nearby foes take 10..70 damage.', 'Deals 10...70 damage to foes near you. <i>You begin Bleeding (25 seconds).</i>'),
(146, 'Offering of Blood', 'You gain 8..20 Energy.', 'You gain 8...20 Energy.', '', '', ''),
(147, 'Dark Fury', 'For 5 seconds, whenever any party member hits with an attack, that party member gains one hit of adrenaline. (50% failure chance with Blood Magic of 4 or less.)', 'Enchants party members (5 seconds). These party members gain one strike of adrenaline each time they hit with an attack. <i>50% failure chance unless Blood Magic 5 or more.</i>', '', '', ''),
(148, 'Order of the Vampire', 'For 5 seconds, whenever a party member who is not under the effects of another Necromancer enchantment hits a foe with physical damage, that party member steals up to 3..16 Health.', 'Enchants all party members (5 seconds.) These party members steal 3...16 Health with each physical damage attack. <i>Party members under another Necromancer enchantment are not affected.</i>', '', '', ''),
(149, 'Plague Sending', 'Transfer 1..3 negative condition[s] and [its/their] remaining duration[s] from yourself to target foe and all adjacent foes.', 'Transfer 1...3 conditions and its/their remaining durations from yourself to target foe and all adjacent foes.', '', '', ''),
(150, 'Mark of Pain', 'For 30 seconds, whenever target foe takes physical damage, Mark of Pain deals 10..40 shadow damage to adjacent foes.', '(30 seconds.) Deals 10...40 damage to adjacent foes whenever target foe takes physical damage.', '', '', ''),
(151, 'Feast of Corruption', 'Target foe and all adjacent foes are struck for 16..80 shadow damage. You steal up to 8..40 Health from each struck foe who is suffering from a hex.', 'Deals 16...80 damage to target and adjacent foes. Steal 8...40 Health from each hexed foe.', '', '', ''),
(152, 'Taste of Death', 'Steal up to 100..400 Health from target animated undead ally.', 'Steals 100...400 Health from allied undead servant.', '', '', ''),
(153, 'Vampiric Gaze', 'Steal up to 18..60 Health from target foe.', 'Steals 18...60 Health.', '', '', ''),
(154, 'Plague Touch', 'Transfer 1..3 negative condition[s] and [its/their] remaining duration[s] from yourself to target touched foe.', 'Touch Skill. Transfers 1...3 conditions from yourself to target foe.', '', '', ''),
(155, 'Vile Touch', 'Touch target foe to deal 20..65 damage.', 'Touch Skill. Deals 20...65 damage.', '', '', ''),
(156, 'Vampiric Touch', 'Touch target foe to steal up to 29..74 Health.', 'Touch Skill. Steals 29...74 Health.', '', '', ''),
(157, 'Blood Ritual', 'For 8..14 seconds, target touched ally gains +3 Energy regeneration. Blood Ritual cannot be used on the caster.', '(8...14 seconds.) +3 Energy regeneration. <i>Cannot self-target.</i>', '', '', ''),
(158, 'Touch of Agony', 'Target touched foe takes 20..58 shadow damage.', 'Touch Skill. Deals 20...58 damage.', '', '', ''),
(159, 'Weaken Armor', 'Target foe and foes adjacent to your target have Cracked Armor for 5..20 seconds.', 'Also affects adjacent foes. Inflicts Cracked Armor condition (5...20 seconds).', '', '', ''),
(160, 'Windborne Speed', 'For 5..13 seconds, target ally moves 33% faster.', '(5...13 seconds.) Target ally moves 33% faster.', '', '', ''),
(162, 'Gale', 'Knock down target foe for 2 seconds. (50% failure chance with Air Magic 4 or less.)', 'Causes knock-down. <i>50% failure chance unless Air Magic 5 or more.</i>', '', '', ''),
(163, 'Whirlwind', 'All adjacent foes take 15..75 cold damage. Attacking foes struck by Whirlwind are knocked down. If you are Overcast, this spell strikes nearby instead of adjacent.', 'Hits foes adjacent to you. Deals 15...75 cold damage. Causes knock-down to attacking foes. Strikes nearby instead of adjacent if Overcast.', '', '', ''),
(164, 'Elemental Attunement', 'For 25..60 seconds, you are attuned to Air, Fire, Water, and Earth and gain +1..2 to these attributes. You gain 50% of the base Energy cost of the skill each time you use magic associated with any of these elements.', '(25...60 seconds.) Your elemental attributes are increased by +1...2. You gain 50% of the Energy cost of any Air, Earth, Fire, and Water Magic skills you use.', '', '', ''),
(165, 'Armor of Earth', 'For 30 seconds, you gain 24..60 armor, but move 50..14% slower.', '(30 seconds.) You have +24...60 armor. You move 50...14% slower.', '', '', ''),
(166, 'Kinetic Armor', 'For 8 seconds, you gain +20..80 armor. Whenever you cast a spell, Kinetic Armor is renewed for 8 seconds.', '(8 seconds.) You have +20...80 armor. Renewal bonus: cast a spell.', '', '', ''),
(167, 'Eruption', 'Cause an Eruption at target foe\'s location. Each second for 5 seconds, foes near this location are struck for 10..40 earth damage and are Blinded for 10 seconds.', 'Deals 10...40 earth damage each second (5 seconds). Hits foes near target\'s initial location. Inflicts Blindness condition (10 seconds).', '', '', ''),
(168, 'Magnetic Aura', 'For 1..5 second[s], you block the next attack against you and deal 10..50 damage to your attacker. If you are Overcast, all party members in earshot are also enchanted.', '(1...5 seconds.) Block the next attack against you and reflect 10...50 damage to the attacker. If you are Overcast, enchant party members in earshot.', '', '', ''),
(169, 'Earth Attunement', 'For 36..60 seconds, you are attuned to Earth. You gain 1 Energy plus 30% of the base Energy cost of the skill each time you use Earth Magic.', '(36...60 seconds.) You gain 1 Energy plus 30% of the Energy cost when you use an Earth Magic skill.', '', '', ''),
(170, 'Earthquake', 'You invoke an Earthquake at target foe\'s location. All foes near this location are knocked down and are struck for 26..100 earth damage.', 'Deals 26...100 earth damage. Also hits foes near target. Causes knock-down.', '', '', ''),
(171, 'Stoning', 'Send out a large stone, striking target foe for 45..105 earth damage if it hits. If Stoning hits a foe suffering from Weakness, that foe is knocked down.', 'Projectile: deals 45...105 earth damage. Causes knock-down if target foe is Weakened.', '', '', ''),
(172, 'Stone Daggers', 'Send out two Stone Daggers. Each Stone Dagger strikes target foe for 8..33 earth damage if it hits. If you are Overcast, each projectile inflicts Bleeding for 1..5 second[s].', 'Two projectiles: each deals 8...33 earth damage. If Overcast, cause Bleeding for 1...5 seconds.', '', '', ''),
(173, 'Grasping Earth', 'For 5..20 seconds, all nearby foes move 50% slower.', 'Hexes foes near you for (5...20 seconds). These foes move 50% slower.', '', '', ''),
(174, 'Aftershock', 'Nearby foes are struck for 26..100 earth damage. Knocked down foes are struck for 10..68 additional earth damage.', 'Deals 26...100 earth damage to nearby foes. Deals 10...68 more earth damage to knocked down foes.', '', '', ''),
(175, 'Ward Against Elements', ' You create a Ward Against Elements at your current location. For 8..20 seconds, non-spirit allies in this area gain +24 armor against elemental damage.', '(8...20 seconds.) Allies in this ward have +24 armor against elemental damage. <i>Spirits are unaffected.</i>', '', '', ''),
(176, 'Ward Against Melee', 'You create a Ward Against Melee at your current location. For 5..20 seconds, non-spirit allies in this area have a 50% chance to block melee attacks.', '(5...20 seconds.) Allies in this ward have a 50% chance to block melee attacks. <i>Allied spirits are not affected.</i>', '', '', ''),
(177, 'Ward Against Foes', 'You create a Ward Against Foes at your current location. For 8..20 seconds, non-spirit foes in this area move 50% slower.', '(8...20 seconds.) Foes in this ward move 50% slower.', '', '', ''),
(178, 'Ether Prodigy', 'Lose all enchantments. For 8..20 seconds, you gain +6 Energy regeneration. When Ether Prodigy ends, you lose 2 Health for each point of Energy you have.', '(8...20 seconds.) You have +6 Energy regeneration. End effect: lose 2 Health for each point of Energy you have. <i>Lose all enchantments.</i>', '', '', ''),
(179, 'Incendiary Bonds', ' After 3 seconds, target foe and all nearby foes are struck for 20..80 fire damage and are set on fire for 1..3 second[s]. Activates early if target foe dies.', '(3 seconds.) End effect: deals 20...80 fire damage and inflicts Burning condition (1...3 seconds) to foes near your target. Also triggers if foe dies.', '', '', ''),
(180, 'Aura of Restoration', 'For 60 seconds, you gain 0..1 Energy and are healed for 200..500% of the Energy cost each time you cast a spell.', '(60 seconds.) You gain 0...1 Energy and are healed for 200...500% of the Energy cost each time you cast a spell.', 'Aura of Restoration (PvP)', 'For 60 seconds, you gain 0..1 Energy and are healed for 200..400% of the Energy cost each time you cast a spell.', '(60 seconds.) You gain 0...1 Energy and are healed for 200...400% of the Energy cost each time you cast a spell.'),
(181, 'Ether Renewal', 'For 5..20 seconds, each time you cast a spell, you gain 1..4 Energy and 5..20 Health for each enchantment on you.', '(5...20 seconds.) Each time you cast a spell, you gain 1...4 Energy and 5...20 Health for each enchantment on you.', 'Ether Renewal (PvP)', 'For 7 seconds, each time you cast a spell, you gain 1..3 Energy and 5..20 Health for each enchantment on you.', '(7 seconds.) Each time you cast a spell, you gain 1...3 Energy and 5...20 Health for each enchantment on you.'),
(182, 'Conjure Flame', 'For 60 seconds, if you\'re wielding a fire weapon, your attacks strike for an additional 5..20 fire damage.', '(60 seconds.) Your attacks hit for +5...20 fire damage. <i>No effect unless your weapon deals fire damage.</i>', '', '', ''),
(183, 'Inferno', ' All adjacent foes are struck for 30..135 fire damage.', 'Deals 30...135 fire damage to foes adjacent to you.', '', '', ''),
(184, 'Fire Attunement', 'For 36..60 seconds, you are attuned to Fire. You gain 1 Energy plus 30% of the base Energy cost of the skill each time you use Fire Magic.', '(36...60 seconds.) You gain 1 Energy plus 30% of the Energy cost when you use a Fire Magic skill.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(185, 'Mind Burn', 'Target foe and all adjacent foes take 15..60 fire damage.  If you have more Energy than target foe, that foe and all adjacent foes take an additional 15..60 fire damage and are set on fire for 1..10 second[s].', 'Deals 15...60 fire damage. If you have more energy than target foe, deals 15...60 more fire damage and inflicts Burning (1...10 seconds). Also hits adjacent foes.', '', '', ''),
(186, 'Fireball', 'Send out a ball of fire that strikes target foe and all adjacent foes for 7..112 fire damage.', 'Projectile: deals 7...112 fire damage to target and adjacent foes.', '', '', ''),
(187, 'Meteor', 'Target foe and all adjacent foes are struck for 7..112 fire damage and knocked down.', 'Deals 7...112 fire damage and causes knock-down. Also hits foes adjacent to target foe.', '', '', ''),
(188, 'Flame Burst', 'All nearby foes are struck for 15..120 fire damage.', 'Deals 15...120 fire damage to all foes near you.', '', '', ''),
(189, 'Rodgort\'s Invocation', 'Target foe and all nearby foes are struck for 15..120 fire damage and set on fire for 1..3 second[s].', 'Deals 15...120 fire damage. Also affects foes near your target. Inflicts Burning condition (1...3 seconds).', '', '', ''),
(190, 'Mark of Rodgort', 'Target foe and all nearby foes are hexed with Mark of Rodgort. For 10..35 seconds, whenever each foe is struck for fire damage, that foe is set on fire for 1..4 second[s].', 'Also hexes foes near your target (10...35 seconds). Inflicts Burning condition (1...4 seconds) when these foes take fire damage.', '', '', ''),
(191, 'Immolate', 'Target foe is struck for 20..75 fire damage and is set on fire for 1..3 second[s].', 'Deals 20...75 fire damage. Inflicts Burning condition (1...3 seconds).', '', '', ''),
(192, 'Meteor Shower', 'Create a Meteor Shower at target foe\'s location. For 9 seconds, foes adjacent to that location are struck for 7..112 fire damage and knocked down every 3 seconds.', 'Deals 7...112 fire damage and causes knock-down every 3 seconds (9 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(193, 'Phoenix', 'A fiery Phoenix rises at your location and flies out to your target, exploding on impact. This explosion strikes your target and nearby foes for 10..60 fire damage. If you are Overcast, allies in the blast radius are healed for 20..80 Health.', 'Projectile: deals 10...60 fire damage to target and nearby foes. Heals allies for 20...80 if you are Overcast.', '', '', ''),
(194, 'Flare', 'Send out a flare  that strikes target foe for 20..65 fire damage if it hits. If you are Overcast, Flare hits adjacent foes as well.', 'Projectile: deals 20...65 fire damage. If Overcast, strikes adjacent.', '', '', ''),
(195, 'Lava Font', 'For 5 seconds, foes adjacent to the location where this spell was cast are struck for 5..50 fire damage each second. If you are Overcast, this spell strikes nearby foes instead of adjacent ones.', 'Deals 5...50 fire damage each second (5 seconds). Hits foes adjacent to your initial location. If Overcast, range increased to nearby.', '', '', ''),
(196, 'Searing Heat', 'Cause Searing Heat at target foe\'s location. For 5 seconds, foes near this location are struck for 10..40 fire damage each second. When Searing Heat ends, foes in the area of effect are set on fire for 3 seconds.', 'Deals 10...40 fire damage each second (5 seconds). Hits foes near target\'s initial location. End effect: inflicts Burning condition (3 seconds).', '', '', ''),
(197, 'Fire Storm', 'Create a Fire Storm at target foe\'s location. For 10 seconds, foes adjacent to that location are struck for 5..35 fire damage each second.', 'Deals 5...35 fire damage each second (10 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(198, 'Glyph of Elemental Power', 'For 25 seconds, your elemental attributes are boosted by 2 for your next 10 spells.', '(25 seconds.) Boosts your elemental attributes by +2 for your next 10 spells.', '', '', ''),
(199, 'Glyph of Energy', 'Your next 1..3 spell[s] [does/do] not cause Overcast and cost[s]  10..25 less Energy to cast. Your elemental attributes are increased by 1..2.', 'Your next 1...3 spells does/do not cause Overcast and costs <sub>sic</sub> 10...25 less Energy. Gain 1...2 to all elemental attributes.', '', '', ''),
(200, 'Glyph of Lesser Energy', 'For the next 15 seconds, your next 2 spells cost 10..18 less Energy to cast.', '(15 seconds.) Your next 2 spells cost 10...18 less Energy.', '', '', ''),
(201, 'Glyph of Concentration', ' For 15 seconds, your next 1 spell cannot be interrupted and ignores the effects of being Dazed.', 'Glyph (15 seconds.) Your next 1 spell cannot be interrupted and is unaffected by the Dazed condition.', '', '', ''),
(202, 'Glyph of Sacrifice', 'For 15 seconds, your next spell casts instantly, but it takes an additional 30 seconds to recharge. Ends prematurely if you use a non-spell skill.', '(15 seconds.) Your next spell casts instantly but takes an additional 30 seconds to recharge. <i>Ends if you use a non-spell skill.</i>', '', '', ''),
(203, 'Glyph of Renewal', 'For 15 seconds, your next spell instantly recharges.', '(15 seconds.) Your next spell recharges instantly.', '', '', ''),
(204, 'Rust', 'Deals 10..70 cold damage to target and adjacent foes. For 5..20 seconds, target foe and all adjacent foes take twice as long to activate signets. If you are Overcast, foes struck with Rust have their signets interrupted and disabled for 1..10 second[s].', 'Deals 10...70 cold damage to target and adjacent foes. Hexes target and adjacent foes (5...20 seconds). Doubles signet activation time. Interrupts and disables signets for 1...10 seconds if you are Overcast.', '', '', ''),
(205, 'Lightning Surge', 'After 3 seconds, target foe is knocked down and struck for 14..100 lightning damage, and has Cracked Armor for 5..20 seconds. This spell has 25% armor penetration.', '(3 seconds.) End effect: deals 14...100 lightning damage, causes knock-down, and inflicts Cracked Armor (5...20 seconds). 25% armor penetration.', '', '', ''),
(206, 'Armor of Frost', ' For 10..34 seconds, you gain +40 armor against physical damage and have +1 Water Magic.', '(10...34 seconds.) You have +40 armor against physical damage and have +1 Water Magic.', '', '', ''),
(207, 'Conjure Frost', 'For 60 seconds, if you\'re wielding a cold weapon, your attacks strike for an additional 5..20 cold damage.', '(60 seconds.) Your attacks hit for +5...20 cold damage. <i>No effect unless your weapon deals cold damage.</i>', '', '', ''),
(208, 'Water Attunement', 'For 36..60 seconds, you are attuned to Water. You gain 1 Energy plus 30% of the base Energy cost of the skill each time you use Water Magic.', '(36...60 seconds.) You gain 1 Energy plus 30% of the Energy cost when you use a Water Magic skill.', '', '', ''),
(209, 'Mind Freeze', 'Target foe takes 10..60 cold damage. If you have more Energy than target foe, that foe suffers an additional 10..60 cold damage and moves 90% slower for 1..5 second[s].', 'Deals 10...60 cold damage. If you have more Energy than target foe, deals +10...60 cold damage and causes 90% slower movement (1...5 seconds).', 'Mind Freeze (PvP)', 'Target foe takes 10..40 cold damage. If you have more Energy than target foe, that foe suffers an additional 10..40 cold damage and moves 90% slower for 1..5 seconds.', 'Deals 10...40 cold damage. If you have more Energy than target foe, deals +10...40 cold damage and causes 90% slower movement (1...5 seconds).'),
(210, 'Ice Prison', ' For 8..20 seconds, target foe\'s legs are encased in ice, causing the foe to move 66% slower.', '(8...20 seconds.) Target foe moves 66% slower.', '', '', ''),
(211, 'Ice Spikes', 'Target and adjacent foes are struck for 20..80 cold damage and move 66% slower for 2..6 seconds.', 'Also hexes foes adjacent to target (2...6 seconds). These foes move 66% slower. Initial effect: deals 20...80 cold damage.', '', '', ''),
(212, 'Frozen Burst', ' All nearby foes are struck for 10..85 cold damage and move 66% slower for 3..8 seconds.', 'Hexes foes near you. These foes move 66% slower (3...8 seconds). Initial effect: deals 10...85 cold damage to foes near you.', '', '', ''),
(213, 'Shard Storm', ' Send out an ice shard that strikes target foe for 10..85 cold damage if it hits and causing target foe to move 66% slower for 2..6 seconds.', 'Projectile: deals 10...85 cold damage. Target foe moves 66% slower (2...6 seconds).', '', '', ''),
(214, 'Ice Spear', 'Send out an Ice Spear, striking target foe for 10..60 cold damage if it hits. If you are Overcast, you gain +1..4 Health regeneration for 5 seconds.', 'Projectile: deals 10...60 cold damage. Gain +1...4 Health regeneration for 5 seconds if Overcast.', '', '', ''),
(215, 'Maelstrom', 'Create a Maelstrom at target foe\'s location. For 10 seconds, foes adjacent to that area are struck for 10..25 cold damage each second. Maelstrom interrupts spell-casting when it hits.', 'Deals 10...25 cold damage and interrupts spells each second (10 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(216, 'Iron Mist', 'For 8..15 seconds, you have +15 armor. Your Air Magic spells that target a foe activate and recharge 25% faster, but you are Overcast by 3 points.', '(8...15 seconds.) Gain +15 armor. Air Magic spells that target a foe activate and recharge 25% faster, but you are Overcast by 3 points.', '', '', ''),
(217, 'Crystal Wave', 'Foes adjacent to you are struck for 10..70 damage but are cured of any negative conditions. Each condition removed deals 5..15 damage.', 'Deals 10...70 damage to all foes adjacent to you. Those foes lose all conditions and take 5...15 damage for each condition removed.', '', '', ''),
(218, 'Obsidian Flesh', 'For 8..20 seconds, you gain +20 armor and cannot be the target of enemy spells, but cannot attack and have -2 energy degeneration.', '(8...20 seconds.) You have +20 armor and enemy spells cannot target you. <i>You cannot attack and have -2 energy degeneration.</i>', '', '', ''),
(219, 'Obsidian Flame', 'Deal 22..112 damage to target foe. This spell ignores armor.', 'Deals 22...112 damage.', 'Obsidian Flame (PvP)', 'Deal 22..92 damage to target foe. This spell ignores armor.', 'Deals 22...92 damage.'),
(220, 'Blinding Flash', 'Target foe is Blinded for 3..8 seconds.', 'Inflicts Blindness condition (3...8 seconds).', '', '', ''),
(221, 'Conjure Lightning', 'For 60 seconds, if you\'re wielding a lightning weapon, your attacks strike for an additional 5..20 lightning damage.', '(60 seconds.) Your attacks hit for +5...20 lightning damage. <i>No effect unless your weapon deals lightning damage.</i>', '', '', ''),
(222, 'Lightning Strike', 'Strike target foe for 5..50 lightning damage. This spell has 25% armor penetration. If you are Overcast, that foe is hexed with Lightning Strike for 3 seconds. When this hex ends, that foe is struck again for 5..50 lightning damage.', 'Deals 5...50 lightning damage. 25% armor penetration. Hex for 3 seconds if Overcast. End effect: deals 5...50 lightning damage.', '', '', ''),
(223, 'Chain Lightning', 'Target foe and up to two other foes near your target are struck for 10..85 lightning damage. This spell has 25% armor penetration.', 'Deals 10...85 lightning damage. Also hits two foes near your target. 25% armor penetration.', '', '', ''),
(224, 'Enervating Charge', 'Target foe is struck for 25..50 lightning damage and suffers from Weakness for 5..20 seconds. This spell has 25% armor penetration.', 'Deals 25...50 lightning damage. Inflicts Weakness condition (5...20 seconds). 25% armor penetration.', '', '', ''),
(225, 'Air Attunement', 'For 36..60 seconds, you are attuned to Air. You gain 1 Energy plus 30% of the base Energy cost of the skill whenever you use Air Magic.', '(36...60 seconds.) You gain 1 Energy plus 30% of the Energy cost whenever you use an Air Magic skill.', '', '', ''),
(226, 'Mind Shock', 'Target foe suffers 10..50 lightning damage. If you have more Energy than target foe, that foe suffers 10..50 more lightning damage and is knocked down. This spell has 25% armor penetration.', 'Deals 10...50 lightning damage. If you have more Energy than target foe, deals +10...50 lightning damage and causes knockdown. 25% armor penetration.', 'Mind Shock (PvP)', 'Target foe suffers 10..50 lightning damage. If you have more Energy than target foe, that foe suffers 10..50 more lightning damage and is knocked down. This spell has 25% armor penetration.', 'Deals 10...50 lightning damage. If you have more Energy than target foe, deals +10...50 lightning damage and causes knockdown. 25% armor penetration.'),
(227, 'Glimmering Mark', 'For 10 seconds, target foe and all foes adjacent to your target take 5..25 damage each second. Foes using attack skills are Blinded for 3 seconds. This hex ends if you use a skill that targets this foe.', '(10 seconds.) Deals 5...25 damage each second to target and adjacent foes. Inflicts Blindness <sub>sic</sub> on foes using attack skills. <i>Ends if you use a skill that targets this foe.</i>', '', '', ''),
(228, 'Thunderclap', 'Create a massive shockwave at target foe\'s location. Deals 10..50 lightning damage to target and all adjacent foes. Struck foes are interrupted and suffer from Cracked Armor and Weakness for 5..20 seconds. This spell has 25% armor penetration.', 'Deals 10...50 lightning damage. Also strikes adjacent foes. Inflicts Cracked Armor and Weakness (5...20 seconds). Causes interrupt. 25% armor penetration.', '', '', ''),
(229, 'Lightning Orb', 'Send out a Lightning Orb that strikes target foe for 10..100 lightning damage. This spell has 25% armor penetration.', 'Projectile: deals 10...100 lightning damage. 25% armor penetration.', '', '', ''),
(230, 'Lightning Javelin', 'Send out a Lightning Javelin that strikes for 15..50 lightning damage if it hits. Lightning Javelin interrupts attacking foes. This spell has 25% armor penetration and strikes all foes between you and your target.', 'Projectile: Deals 15...50 lightning damage. Interrupts if your target is attacking. 25% armor penetration. Strikes all foes between you and your target.', '', '', ''),
(231, 'Shock', 'Target touched foe is knocked down and struck for 10..60 lightning damage. This skill has 25% armor penetration.', 'Touch Skill. Deals 10...60 lightning damage. Causes knock-down. 25% armor penetration.', '', '', ''),
(232, 'Lightning Touch', 'Target touched foe and all adjacent foes are struck for 10..60 lightning damage, are Blinded for 1..4 second[s], and have Cracked Armor for 1..10 second[s]. This skill has 25% armor penetration.', 'Touch Skill. Deals 10...60 lightning damage. Also hits foes adjacent to target foe. Inflicts Blind (1...4 seconds) and Cracked Armor (1...10 seconds) on all struck foes. 25% armor penetration.', '', '', ''),
(233, 'Swirling Aura', ' For 5 seconds, you are enchanted with Swirling Aura and have 1..6 Health regeneration and a 50% chance to block projectiles. If you are Overcast when you cast this spell, all party members in earshot are also enchanted.', '(5 seconds.) Gives 1...6 Health regeneration and a 50% chance to block projectiles. If Overcast, also enchants party members in earshot.', '', '', ''),
(234, 'Deep Freeze', 'You cause a Deep Freeze at target foe\'s location. All foes in this area are struck for 10..85 cold damage, and for 10 seconds, they move 66% slower.', 'Also hexes foes in the area of your target (10 seconds). These foes move 66% slower. Initial effect: deals 10...85 cold damage.', '', '', ''),
(235, 'Blurred Vision', 'For 4..10 seconds, target foe and adjacent foes are hexed with Blurred Vision. While hexed, those foes have a 50% chance to miss with attacks.', 'Also hexes foes adjacent to target (4...10 seconds). They have 50% chance to miss.', '', '', ''),
(236, 'Mist Form', 'For 10..45 seconds, you take 33% less damage from foes under the effects of Water Magic hexes.  Whenever you cast an elemental spell, all non-spirit allies in earshot are healed for 50..250% of the Energy cost of the spell.', '(10...45 seconds.) Take 33% less damage from foes hexed with Water Magic. Heals non-spirit allies in earshot for 50...250% of the energy cost of your elemental spells.', 'Mist Form (PvP)', 'For 10..45 seconds, you take 33% less damage from foes under the effects of Water Magic hexes.  Whenever you cast an elemental spell, all non-spirit allies in earshot are healed for 50..250% of the Energy cost of the spell. This spell does not heal allies above 80% Health.', '(10...45 seconds.) Take 33% less damage from foes hexed with Water Magic. Heals non-spirit allies in earshot for 50...250% of the energy cost of your elemental spells. <i>Does not heal allies above 80% Health.</i>'),
(237, 'Water Trident', 'Send out a fast-moving Water Trident, striking target foe and up to 2 adjacent foes for 10..90 cold damage if it hits. If it hits a moving foe, that foe is knocked down.', 'Fast Projectile: deals 10...90 cold damage and knocks-down moving foes. Shoots 2 additional projectiles at adjacent foes.', '', '', ''),
(238, 'Armor of Mist', 'For 8..20 seconds, you gain +10..40 armor and move 33% faster.', '(8...20 seconds.) You have +10...40 armor and move 33% faster.', '', '', ''),
(239, 'Ward Against Harm', 'Create a Ward Against Harm at this location. For 5..15 seconds, non-spirit allies in this area have +1..3 health regeneration, +12..24 armor, and an additional +12..24 armor against elemental damage. This spell is disabled for 20 seconds.', '(5...15 seconds.) Allies in this ward have +1...3 Health regeneration, +12...24 armor, and +12...24 additional armor against elemental damage. <i>Spirits are unaffected. This spell is disabled for 20 seconds.</i>', 'Ward Against Harm (PvP)', 'Create a Ward Against Harm at this location. For 5..15 seconds, non-spirit allies in this area have +1..3 health regeneration, +12..24 armor, and an additional +12..24 armor against elemental damage. This spell is disabled for 30 seconds.', '(5...15 seconds.) Allies in this ward have +1...3 Health regeneration, +12...24 armor, and +12...24 additional armor against elemental damage. <i>Spirits are unaffected. This spell is disabled for 30 seconds.</i>'),
(240, 'Smite', 'This attack deals 10..55 Holy damage. If attacking, your target takes an additional 10..35 Holy damage.', 'Deals 10...55 holy damage. Deals 10...35 more holy damage if target is attacking.', '', '', ''),
(241, 'Life Bond', 'While you maintain this enchantment, whenever target other ally takes damage from an attack, half the damage is redirected to you. The damage you receive this way is reduced by 3..30.', 'Half of the damage target ally takes from attacks is redirected to you. Redirected damage is reduced by 3...30. <i>Cannot self-target.</i>', '', '', ''),
(242, 'Balthazar\'s Spirit', 'While you maintain this enchantment, target ally gains adrenaline and 1 Energy after taking damage. (The amount of adrenaline gained increases depending on your rank in Smiting Prayers.)', 'Target ally gains adrenaline and 1 Energy when taking damage.', '', '', ''),
(243, 'Strength of Honor', 'While you maintain this enchantment, target ally deals 5..25 more damage in melee.', 'Target ally deals 5...25 more damage in melee.', 'Strength of Honor (PvP)', 'While you maintain this enchantment, target ally deals 1..5 more damage in melee.', 'Target ally deals 1...5 more damage in melee.'),
(244, 'Life Attunement', 'While you maintain this enchantment, target ally deals 30% less damage with attacks, but gains 14..50% more Health when healed.', 'Target ally gains 14...50% more Health when healed. <i>This ally deals 30% less damage with attacks.</i>', '', '', ''),
(245, 'Protective Spirit', 'For 5..23 seconds, target ally cannot lose more than 10% max Health due to damage from a single attack or spell.', '(5...23 seconds.) Incoming damage is reduced to 10% of target ally\'s maximum Health.', '', '', ''),
(246, 'Divine Intervention', 'For 10 seconds, the next time target ally receives damage that would be fatal, the damage is negated and that ally is healed for 26..240 Health.', '(10 seconds.) Negates the next fatal damage target ally takes. Negation effect: heals for 26...240.', '', '', ''),
(247, 'Symbol of Wrath', 'For 5 seconds, foes adjacent to the location in which the spell was cast take 8..32 holy damage each second.', 'Deals 8...32 holy damage each second (5 seconds). Hits foes adjacent to your initial location.', '', '', ''),
(248, 'Retribution', 'While you maintain this enchantment, whenever target ally takes attack damage, this spell deals 33% of the damage back to the source (maximum 5..20 damage).', 'Deals 33% of each attack\'s damage (maximum 5...20) back to the source.', '', '', ''),
(249, 'Holy Wrath', 'For 10..30 seconds, the next 1..10 time[s] target other ally takes attack damage, this spell deals 66% of the damage back to the source (maximum of 5..50 damage).', '(10...30 seconds). Deals 66% of each attack\'s damage (maximum 5...50) back to source. <i>Ends after dealing damage 1...10 times. Cannot self-target.</i>', '', '', ''),
(250, 'Essence Bond', 'While you maintain this enchantment, whenever target ally takes physical or elemental damage, you gain 1 Energy.', 'You gain 1 Energy whenever target ally takes physical or elemental damage.', '', '', ''),
(251, 'Scourge Healing', 'For 30 seconds, every time target foe is healed, the healer takes 15..80 holy damage.', '(30 seconds.) Whenever target foe is healed, the healer takes 15...80 holy damage.', '', '', ''),
(252, 'Banish', 'Target foe takes 20..56 holy damage. This spell does double damage to summoned creatures.', 'Deals 20...56 holy damage. Deals double damage to summoned creatures.', '', '', ''),
(253, 'Scourge Sacrifice', 'For 8..20 seconds, every time target foe and adjacent foes sacrifice life, they sacrifice twice the normal amount.', 'Also hexes foes adjacent to target (8...20 seconds). Doubles Health sacrifice.', '', '', ''),
(254, 'Vigorous Spirit', 'For 30 seconds, each time target ally attacks or casts a spell, that ally is healed for 5..20 Health.', '(30 seconds.) Heals for 5...20 each time target ally attacks or casts a spell.', '', '', ''),
(255, 'Watchful Spirit', 'While you maintain this enchantment, target ally gains +2 Health regeneration. That ally is healed for 30..180 Health when Watchful Spirit ends.', '+2 Health regeneration. End effect: heals for 30...180.', '', '', ''),
(256, 'Blessed Aura', 'While you maintain this enchantment, Monk enchantments you cast last 10..35% longer.', 'Monk enchantments you cast last 10...35% longer.', '', '', ''),
(257, 'Aegis', 'For 5..11 seconds, all party members within earshot have a 50% chance to block attacks.', 'Enchants all party members within earshot (5...11 seconds). 50% chance to block.', 'Aegis (PvP)', 'For 1..3 seconds, target other party member cannot be the target of hostile spells, and attacks against this party member fail.', '(1...3 seconds). Target party member cannot be targeted by hostile spells. Attacks against this party member fail. <i>Cannot self-target.</i>'),
(258, 'Guardian', 'For 2..7 seconds, target ally has a 50% chance to block attacks.', '(2...7 seconds.) 50% chance to block.', '', '', ''),
(259, 'Shield of Deflection', 'For 3..10 seconds, target ally has a 75% chance to block attacks and gains 15..30 armor.', '(3...10 seconds.) 75% chance to block. +15...30 armor.', '', '', ''),
(260, 'Aura of Faith', 'For 3 seconds, target ally gains 50..100% more Health when healed and takes 5..50% less damage.', '(3 seconds.) Target ally gains 50...100% more Health when healed and takes 5...50% less damage.', '', '', ''),
(261, 'Shield of Regeneration', 'For 5..13 seconds, target ally gains +3..10 Health regeneration and 40 armor.', '(5...13 seconds.) +3...10 Health regeneration and +40 armor.', '', '', ''),
(262, 'Shield of Judgment', 'For 8..20 seconds, anyone striking target ally with an attack is knocked down and suffers 5..50 holy damage.', '(8...20 seconds.) Deals 5...50 holy damage to foes attacking <sub>sic</sub> target ally. Causes knock-down.', '', '', ''),
(263, 'Protective Bond', 'While you maintain this enchantment, target ally cannot lose more than 5% max Health due to damage from a single attack or spell. When Protective Bond prevents damage, you lose 6..3 Energy or the spell ends.', 'Target ally cannot lose more than 5% max Health from a single attack or spell. <i>Each time damage is reduced you lose 6...3 Energy or this spell ends.</i>', '', '', ''),
(264, 'Pacifism', 'For 8..20 seconds, target foe cannot attack. This effect ends if the target takes damage.', '(8...20 seconds.) Target foe cannot attack. <i>Ends if this foe takes damage.</i>', '', '', ''),
(265, 'Amity', 'For 8..20 seconds, adjacent foes cannot attack. For each foe, Amity ends if that foe takes damage.', '(8...20 seconds.) Foes adjacent to you cannot attack. <i>Ends on any foes that take damage.</i>', '', '', ''),
(266, 'Peace and Harmony', 'Target ally loses 0..9 condition[s] and hex[es]. For 1..3 second[s], conditions and hexes expire 90% faster on that ally. All your Smiting Prayers are disabled for 20 seconds.', 'Target ally loses 0...9 conditions and hexes. Conditions and hexes expire 90% faster on that ally (1...3 seconds). <i>Disables your Smiting Prayers (20 seconds).</i>', '', '', ''),
(267, 'Judge\'s Insight', 'For 8..20 seconds, target ally\'s attacks deal holy damage and have +20% armor penetration.', '(8...20 seconds.) Converts target ally\'s attacks to holy damage and adds +20% armor penetration.', '', '', ''),
(268, 'Unyielding Aura', 'While you maintain this enchantment, your Monk spells heal for +15..60% more Health. When this enchantment ends one random other party member is resurrected with full Health and Energy and teleported to your location.', 'Your Monk spells heal for +15...60%. End effect: a random other party member is resurrected with full Health and Energy and teleported to your location.', 'Unyielding Aura (PvP)', 'Bring target dead party member back to life at full Health and full Energy. If you stop maintaining this enchantment or if this enchantment is removed, that party member dies and leaves an exploited corpse. Deaths while enchanted with Unyielding Aura do not incur a death penalty. (50% failure chance with Divine Favor 4 or less.)', 'Resurrects target party member (full Health, full Energy). End effect: this ally dies and leaves an exploited corpse. Does not incur a death penalty. <i>50% failure chance unless Divine Favor 5 or more.</i>'),
(269, 'Mark of Protection', 'For 10 seconds, whenever target ally would take damage, that ally is healed for that amount instead, maximum 6..60. All your Protection Prayers are disabled for 5 seconds.', '(10 seconds.) Converts incoming damage to healing (maximum 6...60). <i>All your Protection Prayers are disabled (5 seconds).</i>', '', '', ''),
(270, 'Life Barrier', 'While you maintain this enchantment, damage dealt to target other ally is reduced by 20..50%. If your Health is below 50% when that ally takes damage, Life Barrier ends.', 'Reduces damage by 20...50%. <i>Cannot self-target. If your Health is below 50% when target takes damage, Life Barrier ends.</i>', '', '', ''),
(271, 'Zealot\'s Fire', 'For 60 seconds, whenever you use a skill that targets an ally, all foes adjacent to that target are struck for 5..35 fire damage and you lose 1 Energy.', '(60 seconds.) Whenever you use a skill on an ally, all foes adjacent to that ally are hit for 5...35 fire damage. <i>Damage cost: you lose 1 Energy.</i>', '', '', ''),
(272, 'Balthazar\'s Aura', 'For 8 seconds, foes adjacent to target ally take 10..25 holy damage each second.', '(8 seconds.) Deals 10...25 holy damage each second to foes adjacent to target ally.', '', '', ''),
(273, 'Spell Breaker', 'For 5..17 seconds, target ally cannot be the target of enemy spells.', '(5...17 seconds.) Target ally cannot be the target of enemy spells.', '', '', ''),
(274, 'Healing Seed', 'For 10 seconds, whenever target other ally takes damage, that ally and all adjacent allies gain 3..30 Health.', '(10 seconds.) Target and adjacent allies gain 3...30 Health whenever this ally takes damage. <i>Cannot self-target.</i>', '', '', ''),
(275, 'Mend Condition', 'Remove one condition (Poison, Disease, Blindness, Dazed, Bleeding, Crippled, Burning, Weakness, Cracked Armor, or Deep Wound) from target other ally. If a condition is removed, that ally is healed for 5..70 Health.', 'Removes one condition. Removal effect: heals for 5...70. <i>Cannot self-target.</i>', '', '', ''),
(276, 'Restore Condition', 'Remove all conditions (Poison, Disease, Blindness, Dazed, Bleeding, Crippled, Burning, Weakness, Cracked Armor, and Deep Wound) from target other ally. For each condition removed, that ally is healed for 10..70 Health.', 'Removes all conditions. Removal effect: heals for 10...70 for each condition removed. <i>Cannot self-target.</i>', '', '', ''),
(277, 'Mend Ailment', 'Remove one condition (Poison, Disease, Blindness, Dazed, Bleeding, Crippled, Burning, Weakness, Cracked Armor, or Deep Wound) from target ally. For each remaining Condition, that ally is healed for 5..70 Health.', 'Removes a condition. Removal effect: heals for 5...70 for each remaining condition.', '', '', ''),
(278, 'Purge Conditions', 'Remove all conditions (Poison, Disease, Blindness, Dazed, Bleeding, Crippled, Burning, Weakness, Cracked Armor, and Deep Wound) from target ally.', 'Removes all conditions.', '', '', ''),
(279, 'Divine Healing', 'Heal yourself and party members within earshot for 15..60 points.', 'Heals you and party members within earshot for 15...60.', '', '', ''),
(280, 'Heal Area', 'Heal yourself and all adjacent creatures for 30..180 points.', 'Heals you and adjacent allies and foes for 30...180.', '', '', ''),
(281, 'Orison of Healing', 'Heal target ally for 20..70 Health.', 'Heals for 20...70.', '', '', ''),
(282, 'Word of Healing', 'Heal target ally for 5..100 Health. Heal for an additional 30..115 Health if that ally is below 50% Health.', 'Heals for 5...100. Heals for 30...115 more if target ally is below 50% Health.', '', '', ''),
(283, 'Dwayna\'s Kiss', 'Heal target other ally for 15..60 Health and an additional 10..35 Health for each enchantment or hex on that ally.', 'Heals for 15...60. Heals for 10...35 more for each enchantment and hex on target ally. <i>Cannot self-target.</i>', '', '', ''),
(284, 'Divine Boon', 'While you maintain this enchantment, whenever you cast a Protection Prayer  or Divine Favor spell that targets an ally, that ally is healed for 15..60 Health, and you lose 1 Energy.', 'Whenever you cast a Protection Prayer <sub>sic</sub> or Divine Favor spell on an ally, that ally is healed for 15...60. <i>Heal cost: you lose 1 Energy.</i>', '', '', ''),
(285, 'Healing Hands', 'For 10 seconds, whenever target ally takes damage, that ally is healed for 5..35 Health.', '(10 seconds.) Heals for 5...35 whenever target takes damage.', '', '', ''),
(286, 'Heal Other', 'Heal target other ally for 35..180 Health.', 'Heals for 35...180. <i>Cannot self-target.</i>', '', '', ''),
(287, 'Heal Party', 'Heal entire party for 30..75 Health.', 'Heals entire party for 30...75.', 'Heal Party (PvP)', 'Heal entire party for 30..75 Health.', 'Heals entire party for 30...75.'),
(288, 'Healing Breeze', 'For 15 seconds, target ally gains +4..9 Health regeneration.', '(15 seconds.) +4...9 Health regeneration.', '', '', ''),
(289, 'Vital Blessing', 'While you maintain this enchantment, target ally has +40..200 maximum Health.', '+40...200 maximum Health.', '', '', ''),
(290, 'Mending', 'While you maintain this enchantment, target ally gains +1..4 Health regeneration.', '+1...4 Health regeneration.', '', '', ''),
(291, 'Live Vicariously', 'While you maintain this enchantment, whenever target ally hits a foe, you gain 2..17 Health.', 'You gain 2...17 Health whenever target ally hits a foe.', '', '', ''),
(292, 'Infuse Health', 'Lose half your current Health. Target other ally is healed for 100..136% of the amount you lost.', 'Heals for 100...136% of half your current Health. <i>Lose half your current Health. Cannot self-target.</i>', '', '', ''),
(293, 'Signet of Devotion', 'Heal target ally for 14..100 Health.', 'Heals for 14...100.', '', '', ''),
(294, 'Signet of Judgment', 'Target foe is knocked down. That foe and all adjacent foes take 15..75 holy damage.', 'Knocks down target. Deals 15...75 holy damage to target and adjacent foes.', 'Signet of Judgment (PvP)', 'Target foe is knocked down. That foe and all adjacent foes take 5..50 holy damage. This signet has half the normal range.', 'Elite Half Range Signet. Knocks down target. Deals 5...50 holy damage to target and adjacent foes.'),
(295, 'Purge Signet', 'Remove all hexes and conditions from target ally. You lose 10 Energy for each hex and each condition removed.', 'Removes all hexes and conditions. <i>Removal cost: 10 Energy each.</i>', '', '', ''),
(296, 'Bane Signet', 'Target foe takes 26..56 holy damage. If target foe was attacking, that foe is knocked down.', 'Deals 26...56 holy damage. Causes knock-down if target foe is attacking.', '', '', ''),
(297, 'Blessed Signet', 'For each enchantment you are maintaining, you gain 3 Energy. You cannot gain more than 3..24 Energy in this way.', 'You gain 3 Energy for each enchantment you are maintaining. <i>You cannot gain more than 3...24 Energy in this way.</i>', '', '', ''),
(298, 'Martyr', 'Transfer all conditions and their remaining durations from your allies to you.', 'Transfer all conditions from all allies to you.', '', '', ''),
(299, 'Shielding Hands', 'For 8 seconds, damage and life steal received by target ally is reduced by 3..18. When Shielding Hands ends, that ally is healed for 5..50 Health.', '(8 seconds.) Reduces incoming damage and life steal by 3...18. End effect: heals for 5...50', '', '', ''),
(300, 'Contemplation of Purity', 'Lose all enchantments. For each one lost, you gain 0..80 Health, lose one hex, and lose one condition (maximum 1..8 hexes and conditions).', 'You gain 0...80 Health and lose one hex and one condition for each enchantment on you (maximum 1...8 hexes and conditions). <i>You lose all enchantments.</i>', '', '', ''),
(301, 'Remove Hex', 'Remove a hex from target ally.', 'Removes a hex from target ally.', '', '', ''),
(302, 'Smite Hex', 'Remove a hex from target ally. If a hex is removed, foes in the area suffer 10..85 holy damage.', 'Removes a hex from target ally. Removal effect: deals 10...85 holy damage to foes in the area of target ally.', '', '', ''),
(303, 'Convert Hexes', 'Remove all hexes from target other ally. For 8..20 seconds, that ally gains +10 armor for each Necromancer hex that was removed.', 'Removes all hexes; +10 armor for each Necromancer hex removed (8...20 seconds). <i>Cannot self-target.</i>', '', '', ''),
(304, 'Light of Dwayna', 'Resurrect all dead party members in the area. They are returned to life with 25% Health and zero Energy.', 'Resurrects all dead party members in the area. (25% Health, zero Energy).', '', '', ''),
(305, 'Resurrect', 'Resurrect target party member. Target party member is returned to life with 25% Health and zero Energy.', 'Resurrects target party member (25% Health, 0 Energy).', '', '', ''),
(306, 'Rebirth', 'Resurrect target party member. Target party member is returned to life with 25% Health and zero Energy, and is teleported to your current location. All of target\'s skills are disabled for 10..3 seconds. This spell consumes all of your remaining Energy.', 'Resurrects target party member (25% Health, 0 Energy). Teleports target to you. <i>Disables target\'s skills (10...3 seconds). You lose all Energy.</i>', '', '', ''),
(307, 'Reversal of Fortune', 'For 8 seconds, the next time target ally would take damage or life steal, that ally gains that amount of Health instead, maximum 15..80.', '(8 seconds.) Converts the next incoming damage or life steal (maximum 15...80) to healing.', '', '', ''),
(308, 'Succor', 'While you maintain this Enchantment, target other ally gains +1 Health and +1 Energy regeneration, but you lose 1 Energy each time that ally casts a Spell.', '+1 Health regeneration and +1 Energy regeneration. <i>Cannot self-target. You lose 1 Energy each time target ally casts a spell.</i>', '', '', ''),
(309, 'Holy Veil', 'While you maintain this enchantment, any hex cast on target ally takes twice as long to cast. When Holy Veil ends, one hex is removed from target ally.', 'Doubles casting time of hexes cast on target ally. End effect: removes a hex.', '', '', ''),
(310, 'Divine Spirit', 'For 1..14 second[s], Monk Spells cost you 5 less Energy to cast. (Minimum cost: 1 Energy.)', '(1...14 seconds.) Monk spells cost you 5 less Energy. <i>Minimum cost: 1 Energy.</i>', '', '', ''),
(311, 'Draw Conditions', 'All negative conditions are transferred from target other ally to yourself. For each condition acquired, you gain 6..26 Health.', 'Transfers all conditions from target ally to yourself. Transfer effect: you gain 6...26 Health for each condition. <i>Cannot self-target.</i>', '', '', ''),
(312, 'Holy Strike', 'Touched target foe takes 10..55 holy damage. If knocked down, your target takes an additional 10..55 holy damage.', 'Touch Skill. Deals 10...55 holy damage. Deals 10...55 more holy damage if target is knocked down.', '', '', ''),
(313, 'Healing Touch', 'Heal target touched ally for 16..60 Health. Health gain from Divine Favor is doubled for this spell.', 'Touch spell. Heals for 16...60. Double Health gain from Divine Favor for this spell.', '', '', ''),
(314, 'Restore Life', 'Touch the body of a fallen party member. Target party member is returned to life with 20..65% Health and 42..90% Energy.', 'Touch Spell. Resurrects target party member (20...65% Health, 42...90% Energy).', '', '', ''),
(315, 'Vengeance', 'Bring target dead party member back to life at full Health and full Energy. For 30 seconds, that party member deals 25% more damage. When this enchantment ends, target party member dies. Deaths while under the effects of this enchantment do not incur a death penalty.', '(30 seconds.) Resurrects target party member (full Health, full Energy). This ally deals 25% more damage and does not incur death penalty. <i>End effect: this ally dies.</i>', '', '', ''),
(316, 'To the Limit!', 'For each foe within earshot (maximum 1..6), you gain one strike of adrenaline.  For 10..20 seconds, your maximum Health is increased by 10..60.', '(10...20 seconds.) You have +10...60 maximum Health. Initial effect: you gain one strike of adrenaline for each foe within earshot (maximum 1...6).', '', '', ''),
(317, 'Battle Rage', 'For 5..20 seconds, you move 33% faster and gain double adrenaline from attacks. Battle Rage ends if you use any non-adrenal skills.', '(5...20 seconds.) You move 33% faster and gain double adrenaline from your attacks. <i>Ends if you use any non-adrenal skills.</i>', '', '', ''),
(318, 'Defy Pain', 'For 20 seconds you have an additional 90..300 Health, an additional 20 armor, and you take -1..10 less damage.', '(20 seconds.) You have +90...300 maximum Health, +20 armor, and take 1...10 less damage.', 'Defy Pain (PvP)', 'For 20 seconds you have an additional 90..300 Health and an additional 20 armor.', '(20 seconds.) You have +90...300 maximum Health and +20 armor.'),
(319, 'Rush', 'For 8..20 seconds, you move 25% faster.', '(8...20 seconds.) You move 25% faster.', '', '', ''),
(320, 'Hamstring', 'If this attack hits, your target is Crippled for 3..15 seconds, slowing his movement.', 'Inflicts Crippled condition (3...15 seconds).', '', '', ''),
(321, 'Wild Blow', 'Lose all adrenaline. If it hits, this attack will result in a critical hit and any stance being used by your target ends. This attack cannot be blocked.', 'Always a critical hit. Removes a stance. Unblockable. <i>Lose all adrenaline.</i>', '', '', ''),
(322, 'Power Attack', 'If this attack hits, you strike for +10..40 damage.', 'Deals +10...40 damage.', '', '', ''),
(323, 'Desperation Blow', ' If this attack hits, you strike for +10..40 damage, and your target suffers from one of the following conditions: Deep Wound (for 20 seconds), Weakness (for 20 seconds), Bleeding (for 25 seconds), or Crippled (for 15 seconds). After making a Desperation Blow, you are knocked down.', 'Deals +10...40 damage. Inflicts one of the following random conditions: Deep Wound (20 seconds), Weakness (20 seconds), Bleeding (25 seconds), or Crippled (15 seconds). <i>You are knocked-down.</i>', '', '', ''),
(324, 'Thrill of Victory', 'If this blow hits, you deal +20..40 damage. If you have more Health than target foe, you gain 1..2 strike[s] of adrenaline.', 'Deals +20...40 damage. If you have more Health than your target, you gain 1...2 adrenaline.', '', '', ''),
(325, 'Distracting Blow', 'Swipe your weapon at the target, dealing no damage but disrupting the target\'s current action (and the actions of foes adjacent to your target).', 'Also attacks foes adjacent to your target. Interrupts an action. <i>Hits for no damage.</i>', '', '', ''),
(326, 'Protector\'s Strike', ' If this attack strikes a moving foe, you strike for 10..40 more damage.', 'Deals +10...40 damage if target is moving.', '', '', ''),
(327, 'Griffon\'s Sweep', 'If this attack hits, you strike for +5..20 damage. If this attack is blocked, your target is knocked down and suffers 10..34 damage.', 'Deals +5...20 damage. Causes knock-down and deals 10...34 damage if blocked.', '', '', ''),
(328, 'Pure Strike', ' If Pure Strike hits, you strike for +1..30 damage. If you are not using a stance, Pure Strike cannot be blocked.', 'Deals +1...30 damage. Unblockable unless you are in a stance.', '', '', ''),
(329, 'Skull Crack', 'If it hits, this attack interrupts the target\'s current action. If that foe was casting a spell, that foe is Dazed for 10 seconds.', 'Interrupts an action. Inflicts Dazed condition (10 seconds) if target is casting a spell.', '', '', ''),
(330, 'Cyclone Axe', 'Perform a spinning axe attack striking for +4..12 damage to all adjacent opponents.', 'Deals +4...12 damage to all foes adjacent to you.', '', '', ''),
(331, 'Hammer Bash', 'Lose all adrenaline. If Hammer Bash hits, your target is knocked down.', 'Causes knock-down. <i>Lose all adrenaline.</i>', '', '', ''),
(332, 'Bull\'s Strike', 'If this attack hits a moving foe, you strike for +5..30 damage, and your target is knocked down.', 'Deals +5...30 damage and causes knock-down if target foe is moving.', '', '', ''),
(333, 'I Will Avenge You!', 'For each dead ally, you gain 10 seconds of +3..7 Health regeneration and your attack speed increases by 25%.', 'You have +3...7 Health regeneration and attack 25% faster (10 seconds for each dead ally).', '', '', ''),
(334, 'Axe Rake', 'If this attack hits a foe suffering from a Deep Wound, you strike for +1..10 damage, and that foe becomes Crippled for 15 seconds.', 'Deals +1...10 damage and inflicts Crippled condition (15 seconds) if target foe has a Deep Wound.', '', '', ''),
(335, 'Cleave', 'If this attack hits, you strike for +10..30 damage.', 'Deals +10...30 damage.', '', '', ''),
(336, 'Executioner\'s Strike', 'If this attack hits, you strike for +10..40 damage.', 'Deals +10...40 damage.', '', '', ''),
(337, 'Dismember', 'If it hits, this axe blow will inflict a Deep Wound on the target foe, lowering that foe\'s maximum Health by 20% for 5..20 seconds.', 'Inflicts Deep Wound condition (5...20 seconds).', '', '', ''),
(338, 'Eviscerate', 'If Eviscerate hits, you strike for +1..31 damage and inflict a Deep Wound, lowering your target\'s maximum Health by 20% for 5..20 seconds.', 'Deals +1...31 damage. Inflicts Deep Wound condition (5...20 seconds).', '', '', ''),
(339, 'Penetrating Blow', 'If this attack hits, you strike for +5..20 damage.  This axe attack has 20% armor penetration.', 'Deals +5...20 damage. 20% armor penetration.', '', '', ''),
(340, 'Disrupting Chop', 'If it hits, this attack interrupts the target\'s current action. If that action was a skill, that skill is disabled for an additional 20 seconds.', 'Interrupts an action. Interruption effect: interrupted skill is disabled for +20 seconds.', '', '', ''),
(341, 'Swift Chop', 'If this attack hits, you strike for +1..20 damage. If Swift Chop is blocked, target foe takes 1..20 damage and suffers a Deep Wound for 20 seconds.', 'Deals +1...20 damage. Deals 1...20 damage and inflicts Deep Wound condition (20 seconds) if blocked.', '', '', ''),
(342, 'Axe Twist', 'If this attack hits a foe suffering from a Deep Wound, you strike for 1..20 more damage and that foe suffers from Weakness for 20 seconds.', 'Deals +1...20 damage and inflicts Weakness condition (20 seconds) if target foe has a Deep Wound.', '', '', ''),
(343, 'For Great Justice!', 'For 20 seconds, whenever you gain adrenaline, you gain 100% more.', '(20 seconds.) You gain 100% more adrenaline.', 'For Great Justice! (PvP)', 'For 8 seconds, when you hit with an attack, you gain 1 additional strike of adrenaline.', '(8 seconds.) You gain 1 extra strike of adrenaline whenever you hit with an attack.'),
(344, 'Flurry', 'For 5 seconds, your attack rate is increased by 33%, but you deal 25% less damage.', '(5 seconds). You attack 33% faster. <i>You do 25% less damage.</i>', '', '', ''),
(345, 'Defensive Stance', 'For 1..5 second[s], you have a 75% chance to block melee and projectile attacks. When Defensive Stance ends, you gain one strike of adrenaline for each melee attack skill you have (maximum 0..4).', '(1...5 seconds.) You have 75% chance to block. <i>End effect: gain one adrenaline for each melee attack skill you have (maximum 0...4).</i>', '', '', ''),
(346, 'Frenzy', 'For 8 seconds, you attack 33% faster but take double damage.', '(8 seconds.) You attack 33% faster. <i>You take double damage.</i>', '', '', ''),
(347, 'Endure Pain', 'For 7..18 seconds you have an additional 90..300 Health.', '(7...18 seconds.) You have +90...300 maximum Health.', '', '', ''),
(348, 'Watch Yourself!', 'Party members within earshot gain +5..25 armor for 10 seconds. This shout ends after 10 incoming attacks.', '(10 seconds.) Party members in earshot have +5...25 armor. <i>Ends after 10 incoming attacks.</i>', 'Watch Yourself! (PvP)', 'Party members within earshot gain +5..25 armor for 10 seconds. This shout ends after 1..3 incoming attack[s].', '(10 seconds.) Party members in earshot have +5...25 armor. <i>Ends after 1...3 incoming attacks.</i>'),
(349, 'Sprint', 'For 8..14 seconds, you move 25% faster.', '(8...14 seconds.) You move 25% faster.', '', '', ''),
(350, 'Belly Smash', 'If this attack strikes a foe who is knocked down, the resulting dust cloud will blind adjacent foes for 3..7 seconds.', 'Inflicts Blindness condition to adjacent foes (3...7 seconds) if target foe is knocked down.', '', '', ''),
(351, 'Mighty Blow', 'If this attack hits, you strike for +10..40 damage.', 'Deals +10...40 damage.', '', '', ''),
(352, 'Crushing Blow', 'If this attack hits, you strike for +1..20 damage. If you hit a knocked-down foe you inflict a Deep Wound, lowering your target\'s maximum Health by 20% for 5..20 seconds.', 'Deals +1...20 damage. Inflicts Deep Wound condition if target foe is knocked-down (5...20 seconds).', '', '', ''),
(353, 'Crude Swing', 'Attack all adjacent foes. Each foe you hit is struck for +1..20 damage.', 'Attack all adjacent foes for +1...20 damage.', '', '', ''),
(354, 'Earth Shaker', 'Target foe and all adjacent foes are knocked down. (50% failure chance with Hammer Mastery 4 or less.)', 'Knocks down target and adjacent foes. <i>50% failure chance unless Hammer Mastery is 5 or more.</i>', '', '', ''),
(355, 'Devastating Hammer', 'If Devastating Hammer hits, your target is knocked down and suffers from Weakness for 5..20 seconds.', 'Causes knock-down. Inflicts Weakness condition (5...20 seconds).', '', '', ''),
(356, 'Irresistible Blow', 'If this attack hits, you strike for +5..20 damage. If Irresistible Blow is blocked, your target is knocked down and takes 5..20 damage.', 'Deals +5...20 damage. Deals 5...20 damage and causes knock-down if blocked.', '', '', ''),
(357, 'Counter Blow', 'If this attack hits an attacking foe, that foe is knocked down.', 'Causes knock-down if target foe is attacking.', '', '', ''),
(358, 'Backbreaker', 'If Backbreaker hits, you strike for +1..20 damage and your target is knocked down.  If you have 8 Strength or higher, this knockdown lasts 4 seconds.', 'Deals +1...20 damage. Causes knockdown. <i>Knockdown lasts 4 seconds with Strength 8 or higher.</i>', '', '', ''),
(359, 'Heavy Blow', 'Lose all adrenaline. If this attack hits a foe suffering from Weakness, that foe is knocked down and you strike for +1..30 damage.', 'Deals +1...30 damage and causes knock-down if target foe is Weakened. <i>Lose all adrenaline.</i>', '', '', ''),
(360, 'Staggering Blow', 'If this hammer blow hits, your target will suffer from Weakness for 5..20 seconds.', 'Inflicts Weakness condition (5...20 seconds).', '', '', ''),
(361, 'Dolyak Signet', 'For 8..20 seconds, you have +10..40 armor and cannot be knocked down, but your movement is slowed by 75%.', '(8...20 seconds.) You have +10...40 armor and cannot be knocked-down. <i>You move 75% slower.</i>', '', '', ''),
(362, 'Warrior\'s Cunning', 'For 5..11 seconds, your melee attacks cannot be blocked.', '(5...11 seconds.) Your melee attacks are unblockable.', '', '', ''),
(363, 'Shield Bash', 'For 5..11 seconds, while wielding a shield, the next attack skill used against you is blocked. If it was a melee skill, your attacker is knocked down and that skill is disabled for an additional 15 seconds.', '(5...11 seconds.) You block the next attack skill. Causes knock-down and +15 second recharge if it was a melee skill. <i>No effect unless you are wielding a shield.</i>', '', '', ''),
(364, 'Charge!', 'Allies in earshot lose the Crippled condition. For 5..13 seconds, these allies move 33% faster.', '(5...13 seconds.) Allies in earshot move 33% faster. Initial effect: these allies lose the Crippled condition.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(365, 'Victory Is Mine!', 'You gain 10..68 Health and 3..7 Energy for each condition suffered by target foe.', 'You gain 10...68 Health and 3...7 Energy for each condition on target foe.', '', '', ''),
(366, 'Fear Me!', 'All nearby foes lose 1..4 Energy. For 1..15 second[s], your melee attacks gain +5..30% chance of a critical hit against stationary foes.', '(1...15 seconds.) You have +5...30% chance of a critical hit with melee attacks against stationary foes. Initial effect: nearby foes lose 1...4 Energy.', '', '', ''),
(367, 'Shields Up!', 'For 5..11 seconds, you and all party members within earshot gain +60 armor against incoming projectile attacks.', '(5...11 seconds.) Party members in earshot have +60 armor against projectile attacks.', '', '', ''),
(368, 'I Will Survive!', 'You gain +3 Health regeneration for each condition you are suffering. This regeneration expires after 5..11 seconds.', '(5...11 seconds.) You have +3 Health regeneration for each condition on you.', '', '', ''),
(370, 'Berserker Stance', 'For 5..11 seconds, you attack 33% faster and gain 50% more adrenaline. Berserker Stance ends if you use a skill.', '(5...11 seconds.) You attack 33% faster and gain 50% more adrenaline. <i>Ends if you use a skill.</i>', '', '', ''),
(371, 'Balanced Stance', 'For 8..20 seconds, you cannot be knocked down and you do not suffer extra damage from a critical attack.', '(8...20 seconds.) You cannot be knocked-down and do not take extra damage from critical hits.', '', '', ''),
(372, 'Gladiator\'s Defense', 'For 5..11 seconds, you have a 75% chance to block incoming attacks. Whenever you block a melee attack this way, the attacker suffers 5..35 damage.', '(5...11 seconds.) You have 75% chance to block. Your attacker takes 5...35 damage whenever you block a melee attack this way.', '', '', ''),
(373, 'Deflect Arrows', 'For 1..6 second[s], you have a 75% chance to block attacks. If you block a projectile attack, adjacent foes suffer from Bleeding for 5..15 seconds.', '(1...6 seconds.) You have 75% chance to block attacks. Adjacent foes suffer Bleeding (5...15 seconds) when you block a projectile attack.', '', '', ''),
(374, 'Warrior\'s Endurance', 'For 5..35 seconds, you gain 3 Energy each time you hit with a melee attack. Warrior\'s Endurance cannot raise your Energy above 10..25.', '(5...35 seconds.) You gain 3 Energy each time you hit with a melee attack. <i>No Energy gain if you have more than 10...25 Energy.</i>', 'Warrior\'s Endurance (PvP)', 'For 5..35 seconds, you gain 3 Energy each time you hit with a melee attack. Warrior\'s Endurance cannot raise your Energy above 10..25.', '(5...35 seconds.) You gain 3 Energy each time you hit with a melee attack. <i>No Energy gain if you have more than 10...25 Energy.</i>'),
(375, 'Dwarven Battle Stance', 'For 5..11 seconds, if you are wielding a hammer, you attack 33% faster, you gain +40 armor, and your attack skills interrupt foes when they hit.', '(5...11 seconds.) You attack 33% faster, you gain +40 armor, and your attack skills interrupt actions. <i>No effect unless you have a hammer equipped.</i>', '', '', ''),
(376, 'Disciplined Stance', 'For 1..4 second[s], you gain +10 armor and have a 75% chance to block attacks. Disciplined Stance ends if you use an adrenal skill.', '(1...4 seconds.) You have 75% chance to block and +10 armor. <i>Ends if you use an adrenal skill.</i>', '', '', ''),
(377, 'Wary Stance', 'For 1..6 second[s], you block any attack skills used against you. For each successful block, you gain adrenaline and 5 Energy. Wary Stance ends if you use a skill.', '(1...6 seconds). You block attack skills. Gain adrenaline and 5 Energy for each block. <i>Ends if you use a skill.</i>', '', '', ''),
(378, 'Shield Stance', 'For 1..6 second[s], while wielding a shield, you have a 75% chance to block incoming attacks, and damage is reduced by 2 for each rank of Strength (maximum 15 damage reduction).', '(1...6 seconds.) You have 75% chance to block. Damage is reduced by 2 for each rank of Strength (maximum 15 damage reduction). <i>No effect unless you have a shield equipped.</i>', '', '', ''),
(379, 'Bull\'s Charge', 'For 5..11 seconds, you move 33% faster and if you strike a moving foe in melee, that foe is knocked down. Bull\'s Charge ends if you use a skill.', '(5...11 seconds.) You move 33% faster. Causes knock-down if you hit a moving foe in melee. <i>Ends if you use a skill.</i>', '', '', ''),
(380, 'Bonetti\'s Defense', 'For 5..11 seconds, you have a 75% chance to block melee and projectile attacks. You gain 5 Energy for each successful melee attack blocked. Bonetti\'s Defense ends if you use a skill.', '(5...11 seconds.) You have 75% chance to block. Gain 5 Energy for each melee attack blocked. <i>Ends if you use a skill.</i>', '', '', ''),
(381, 'Hundred Blades', 'For 15 seconds, whenever you attack with a sword, all adjacent foes take 10..25 slashing damage.', '(15 seconds.) Deals 10...25 slashing damage to all adjacent foes whenever you attack with a sword.', '', '', ''),
(382, 'Sever Artery', 'If this attack hits, the opponent begins Bleeding for 5..25 seconds, losing Health over time.', 'Inflicts Bleeding condition (5...25 seconds).', '', '', ''),
(383, 'Galrath Slash', ' This attack strikes for +1..40 damage if it hits.', 'Deals +1...40 damage.', '', '', ''),
(384, 'Gash', 'If this attack hits a Bleeding foe, you strike for 5..20 more damage and that foe suffers a Deep Wound, lowering that foe\'s maximum Health by 20% for 5..20 seconds.', 'Deals +5...20 damage and inflicts Deep Wound condition (5...20 seconds) if your target is Bleeding.', '', '', ''),
(385, 'Final Thrust', 'Lose all adrenaline. If Final Thrust hits, you deal 1..40 more damage. This damage is doubled if your target was below 50% Health.', 'Deals +1...40 damage. Deals +1...40 more damage if target foe is below 50% Health. <i>Lose all adrenaline.</i>', '', '', ''),
(386, 'Seeking Blade', 'If this attack hits you strike for +1..20 damage. If Seeking Blade is blocked, your target begins Bleeding for 25 seconds and takes 1..20 damage.', 'Deals +1...20 damage. Deals 1...20 damage and inflicts Bleeding condition (25 seconds) if blocked.', '', '', ''),
(387, 'Riposte', ' For 8 seconds, while you have a sword equipped, you block the next melee attack against you and your attacker is struck for 20..80 damage.', '(8 seconds). You block the next melee attack and your attacker takes 20...80 damage. <i>No effect unless you have a sword equipped.</i>', '', '', ''),
(388, 'Deadly Riposte', 'For 8 seconds, while you have a sword equipped, you block the next melee attack against you, and your attacker is struck for 15..90 damage and begins Bleeding for 3..25 seconds.', '(8 seconds). You block the next melee attack and your attacker takes 15...90 damage. Inflicts Bleeding condition. (3...25 seconds). <i>No effect unless you have a sword equipped.</i>', '', '', ''),
(389, 'Flourish', 'All of your attack skills become recharged. You gain 2..7 Energy for each skill recharged by Flourish.', 'Recharges your attack skills. You gain 2...7 Energy for each skill recharged.', '', '', ''),
(390, 'Savage Slash', 'If this attack hits, it interrupts target foe\'s action. If that action was a spell, you deal 1..40 extra damage.', 'Interrupts an action. Interruption effect: deals +1...40 damage if action was a spell.', '', '', ''),
(391, 'Hunter\'s Shot', 'If this attack hits, your target bleeds for 3..25 seconds.', 'Inflicts Bleeding condition (3...25 seconds) if your target is hit.', '', '', ''),
(392, 'Pin Down', 'If Pin Down hits, your target is Crippled for 3..15 seconds.', 'Inflicts Crippled condition (3...15 seconds).', '', '', ''),
(393, 'Crippling Shot', 'If Crippling Shot hits, your target becomes Crippled for 1..11 second[s]. This attack cannot be blocked.', 'Unblockable. Inflicts Crippled condition (1...11 seconds).', '', '', ''),
(394, 'Power Shot', 'If Power Shot hits, target foe takes 25..50 damage.', 'Target foe takes 25...50 damage.', '', '', ''),
(395, 'Barrage', 'All your preparations are removed. Shoot arrows at target foe and up to 5 foes adjacent to your target. These arrows strike for +5..20 damage if they hit.', 'Deals +5...20 damage. Hits 5 foes adjacent to your target. <i>All your preparations are removed.</i>', '', '', ''),
(396, 'Dual Shot', 'Shoot two arrows simultaneously at target foe.  These arrows deal 25% less damage.', 'You shoot two arrows simultaneously at target foe. <i>These arrows deal 25% less damage</i>', '', '', ''),
(397, 'Quick Shot', 'Shoot an arrow that moves twice as fast.', 'You shoot an arrow that moves twice as fast.', '', '', ''),
(398, 'Penetrating Attack', 'If Penetrating Attack hits, you strike for +5..25 damage and this attack has 10% armor penetration.', 'Deals +5...25 damage. 10% armor penetration.', 'Penetrating Attack (PvP)', 'If Penetrating Attack hits, you strike for +3..10 damage and this attack has 10% armor penetration.', 'Deals +3...10 damage. 10% armor penetration.'),
(399, 'Distracting Shot', 'If Distracting Shot hits, it interrupts target foe\'s action but deals only 1..16 damage. If the interrupted action was a skill, that skill is disabled for an additional 20 seconds.', 'Interrupts an action. Interruption effect: interrupted skill is disabled for +20 seconds. <i>Hits for only 1...16 damage.</i>', '', '', ''),
(400, 'Precision Shot', 'If Precision Shot hits, you strike for +3..10 damage. Precision Shot cannot be blocked. This action is easily interrupted.', 'Deals +3...10 damage. Unblockable. <i>Easily Interrupted.</i>', '', '', ''),
(402, 'Determined Shot', 'If Determined Shot hits, you strike for +5..20 damage. If Determined Shot fails to hit, all of your attack skills are recharged.', 'Deals +5...20 damage. Recharges your attack skills if it fails to hit.', '', '', ''),
(403, 'Called Shot', 'Shoot an arrow that moves 3 times faster and cannot be blocked.', 'You shoot an arrow that moves 3 times faster. Unblockable.', '', '', ''),
(404, 'Poison Arrow', 'If Poison Arrow hits, your target becomes Poisoned for 5..20 seconds.', 'Inflicts Poisoned condition. (5...20)', '', '', ''),
(405, 'Oath Shot', 'If Oath Shot hits, all of your skills except Oath Shot are recharged. If it misses, all of your skills are disabled for 10 seconds. (50% miss chance with Expertise 7 or less.)', 'Recharges all skills except Oath Shot if it hits. <i>Disables all skills if it misses (10...4 seconds). 50% miss chance unless Expertise 8 or higher.</i>', '', '', ''),
(406, 'Debilitating Shot', 'If Debilitating Shot hits, your target loses 1..10 Energy.', 'Causes 1...10 Energy loss.', '', '', ''),
(407, 'Point Blank Shot', 'Shoot an arrow that has half the normal range, but strikes for +10..40 damage.', 'Half Range Bow Attack. Deals +10...40 damage.', '', '', ''),
(408, 'Concussion Shot', 'If Concussion Shot hits while target foe is casting a spell, the spell is interrupted and your target is Dazed for 5..20 seconds.  This attack deals only 1..16 damage.', 'Interrupts a spell. Interruption effect: inflicts Dazed condition (5...20 seconds). <i>Hits for only 1...16 damage.</i>', '', '', ''),
(409, 'Punishing Shot', 'If Punishing Shot hits, you strike for +10..20 damage and your target is interrupted.', 'Deals +10...20 damage. Interrupts an action.', '', '', ''),
(411, 'Charm Animal', 'Charm target animal. Once charmed, your animal companion will travel with you whenever you have Charm Animal equipped. You cannot charm an animal that is more than 4 levels above you.', 'Charm target animal. Once charmed, your animal companion will travel with you whenever you have Charm Animal equipped. <i>You cannot charm an animal that is more than 4 levels above you.</i>', '', '', ''),
(412, 'Call of Protection', ' For 120 seconds, your animal companion has a 5..20 base damage reduction.', '(120 seconds.) Your pet has 5...20 damage reduction.', '', '', ''),
(415, 'Call of Haste', ' For 30 seconds, your animal companion has a 33% faster attack speed and moves 33% faster.', '(30 seconds.) Your pet moves and attacks 33% faster.', 'Call of Haste (PvP)', ' For 30 seconds, your animal companion has a 25% faster attack speed and moves 25% faster.', '(30 seconds.) Your pet moves and attacks 25% faster.'),
(422, 'Revive Animal', 'Resurrect all nearby allied animal companions. They come back to life with 10..94% Health.', 'Resurrects all nearby allied pets (10...94% Health).', '', '', ''),
(423, 'Symbiotic Bond', 'For 120..300 seconds, your animal companion gains 1..3 Health regeneration, and half of all damage dealt to your animal companion is redirected to you.', '(120...300 seconds.) Your pet has +1...3 Health regeneration. Half of all damage dealt to your pet is redirected to you.', '', '', ''),
(424, 'Throw Dirt', 'Target touched foe and foes adjacent to your target become Blinded for 3..15 seconds.', 'Touch Skill. Inflicts Blindness condition (3...15 seconds). Also affects foes adjacent to target foe.', '', '', ''),
(425, 'Dodge', 'For 5..11 seconds, you move 33% faster and have a 27..75% chance to block incoming projectiles. Dodge ends if you attack.', '(5...11 seconds.) You move 33% faster and have 27...75% chance to block projectile attacks. <i>Ends if you attack.</i>', '', '', ''),
(426, 'Savage Shot', 'If Savage Shot hits, your target\'s action is interrupted.  If that action was a spell, you strike for +13..28 damage.', 'Interrupts an action. Interruption effect: deals +13...28 damage if action was a spell.', '', '', ''),
(427, 'Antidote Signet', 'Cleanse yourself of Poison, Disease, and Blindness, and one additional condition.', 'Remove Poison, Disease, and Blindness from yourself, and one additional condition.', '', '', ''),
(428, 'Incendiary Arrows', 'Shoot arrows at target foe and up to 2 foes near your target. Those foes are set on fire for 1..3 second[s].', 'Hits 2 foes near your target and inflicts burning (1...3 seconds).', '', '', ''),
(429, 'Melandru\'s Arrows', 'For 18 seconds, whenever your arrows hit, they cause Bleeding for 3..25 seconds, and if they hit a target who is under an enchantment, they do +8..28 damage.', '(18 seconds.) Your arrows inflict Bleeding condition (3...25 seconds) and deal +8...28 damage to enchanted foes.', '', '', ''),
(430, 'Marksman\'s Wager', 'For 18 seconds, you gain 5..10 Energy whenever your arrows hit, but lose 10 Energy whenever your arrows fail to strike.', '(18 seconds.) Gain 5...10 Energy whenever your arrows hit. <i>Lose 10 Energy whenever your arrows fail to hit.</i>', '', '', ''),
(431, 'Ignite Arrows', 'For 24 seconds, your arrows explode on contact, dealing 3..18 fire damage to target and all adjacent foes.', '(24 seconds.) Your arrows deal 3...18 fire damage to target and foes adjacent to target.', '', '', ''),
(432, 'Read the Wind', 'For 24 seconds, your arrows move twice as fast and deal 3..10 extra damage.', '(24 seconds). +3...10 damage. Your arrows move twice as fast.', 'Read the Wind (PvP)', 'For 24 seconds, your arrows move twice as fast.', '(24 seconds). Your arrows move twice as fast.'),
(433, 'Kindle Arrows', 'For 24 seconds, your arrows deal fire damage and hit for an additional 3..24 fire damage.', '(24 seconds.) +3...24 fire damage. Your arrows deal fire damage.', '', '', ''),
(434, 'Choking Gas', 'For 1..12 seconds, your arrows deal 1..8 more damage and spread Choking Gas to all adjacent foes on impact.  Choking Gas interrupts foes attempting to cast spells.', '(1...12 seconds.) +1...8 damage. Spreads Choking Gas to foes adjacent to target. Choking Gas interrupts spells.', '', '', ''),
(435, 'Apply Poison', 'For 24 seconds, foes struck by your physical attacks become Poisoned for 3..15 seconds.', '(24 seconds.) Your physical attacks inflict Poisoned condition (3...15 seconds).', '', '', ''),
(436, 'Comfort Animal', ' You heal your animal companion for 20..104 Health. If your companion is dead, it is resurrected with 10..58% Health. If you have Comfort Animal equipped, your animal companion will travel with you.', 'Your pet gains 20...104 Health. Resurrects your pet (10...58% Health.) If you have Comfort Animal equipped, your animal companion will travel with you.', 'Comfort Animal (PvP)', 'You heal your animal companion for 20..104 Health. If your companion is dead, it is resurrected with 10..58% Health.', 'Your pet gains 20...104 Health. Resurrects your pet (10...58% Health.)'),
(437, 'Bestial Pounce', 'Your animal companion attempts a Bestial Pounce that deals +5..20 damage. If the attack strikes a foe who is casting a spell, that foe is knocked down.', 'Deals +5...20 damage. Causes knock-down if target foe is casting a spell.', '', '', ''),
(438, 'Maiming Strike', 'Your animal companion attempts a Maiming Strike that deals +5..20 damage. If that attack hits a moving foe that foe becomes Crippled for 3..15 seconds.', 'Deals +5...20 damage. Inflicts Crippled condition (3...15 seconds) if target foe is moving.', '', '', ''),
(439, 'Feral Lunge', 'Your animal companion attempts a Feral Lunge that deals +5..35 damage. If the attack strikes a foe who is attacking, that foe suffers from Bleeding for 5..25 seconds.', 'Deals +5...35 damage. Inflicts Bleeding condition if target foe is attacking (5...25 seconds).', '', '', ''),
(440, 'Scavenger Strike', 'Your animal companion attempts a Scavenger Strike that deals +10..25 damage. If the attack strikes a foe who is suffering a condition, you gain 3..15 Energy.', 'Deals +10...25 damage. You gain 3...15 Energy if target foe has a condition.', '', '', ''),
(441, 'Melandru\'s Assault', 'Your animal companion attempts a Melandru\'s Assault that deals +5..20 damage to all nearby foes.', 'Deals +5...20 damage to nearby foes.', 'Melandru\'s Assault (PvP)', 'Your animal companion attempts a Melandru\'s Assault that deals +5..20 damage. If that attack strikes a foe with an enchantment, that foe and all adjacent foes take +5..35 additional damage.', 'Deals +5...20 damage. Deals +5...35 more damage to target and foes adjacent to target if this foe is enchanted.'),
(442, 'Ferocious Strike', 'Your animal companion attempts a Ferocious Strike that deals  +13..28 damage. If that attack hits, you gain adrenaline and 3..10 Energy.', 'Deals +13...28 damage. You gain adrenaline and 3...10 energy.', '', '', ''),
(443, 'Predator\'s Pounce', 'Your animal companion attempts a Predator\'s Pounce that deals +5..35 damage. If that attack hits, your animal companion gains 5..50 Health.', 'Deals +5...35 damage. Your pet gains 5...50 Health if this attack hits.', '', '', ''),
(444, 'Brutal Strike', 'Your animal companion attempts a Brutal Strike that deals +5..35 damage. If that attack strikes a foe whose Health is below 50%, that foe takes an additional +5..35 damage.', 'Deals +5...35 damage. Deals +5...35 more damage if target foe is under 50% health.', '', '', ''),
(445, 'Disrupting Lunge', 'Your animal companion attempts a Disrupting Lunge that deals +1..12 damage. If that attack strikes a foe using a skill that skill is interrupted and is disabled for an additional 20 seconds.', 'Deals +1...12 damage. Interrupts a skill. Interruption effect: interrupted skill is disabled for +20 seconds.', '', '', ''),
(446, 'Troll Unguent', 'For 13 seconds, you gain +3..10 Health regeneration.', '(13 seconds.) You have +3...10 Health regeneration.', '', '', ''),
(447, 'Otyugh\'s Cry', 'For 10..25 seconds, your animal companion gains +24 armor and cannot be blocked.', '(10...25 seconds.) Your pet has +24 armor and is unblockable.', '', '', ''),
(448, 'Escape', 'For 1..8 seconds, you move 33% faster and have a 75% chance to block attacks.', '(1...8 seconds.) You move 33% faster and have 75% chance to block.', 'Escape (PvP)', 'For 1..8 seconds, you move 33% faster and have a 75% chance to block attacks. Escape ends if you make a melee attack.', '(1...8 seconds.) You move 33% faster and have 75% chance to block. <i>Ends if you make a melee attack.</i>'),
(449, 'Practiced Stance', 'For 20..35 seconds, your Preparations recharge 50% faster and last 30..150% longer.', '(20...35 seconds.) Your preparations recharge 50% faster and last 30...150% longer.', '', '', ''),
(450, 'Whirling Defense', 'For 8..20 seconds, you have 75% chance to block attacks. Whenever you block a projectile in this way, adjacent foes take 5..11 damage.', '(8...20 seconds.) You have 75% chance to block. Deals 5...11 damage to adjacent foes whenever you block a projectile attack.', '', '', ''),
(451, 'Melandru\'s Resilience', 'For 8..20 seconds, you gain +4 Health regeneration and +1 Energy regeneration for each condition and hex you are suffering.', '(8...20 seconds.) You have +4 Health regeneration and +1 Energy regeneration for each condition and hex on you.', '', '', ''),
(452, 'Dryder\'s Defenses', 'For 5..11 seconds, you gain 75% chance to block attacks and 34..60 armor against elemental damage.', '(5...11 seconds.) You have 75% chance to block and +34...60 armor against elemental damage.', '', '', ''),
(453, 'Lightning Reflexes', 'For 5..11 seconds, you have a 75% chance to block melee and projectile attacks, and you attack 33% faster.', '(5...11 seconds.) You attack 33% faster and have 75% chance to block.', 'Lightning Reflexes (PvP)', 'For 5..11 seconds, you have a 75% chance to block melee and projectile attacks, and you attack 33% faster.', '(5...11 seconds.) You attack 33% faster and have 75% chance to block.'),
(454, 'Tiger\'s Fury', 'All your non-attack skills are disabled for 5 seconds. For 5..11 seconds, you attack 25% faster.', '(5...11 seconds.) You attack 25% faster. <i>Disables your non-attack skills (5 seconds).</i>', '', '', ''),
(455, 'Storm Chaser', 'For 8..20 seconds, you move 25% faster, and you gain 1..5 Energy whenever you take elemental damage.', '(8...20 seconds.) You move 25% faster and gain 1...5 Energy whenever you take elemental damage.', '', '', ''),
(456, 'Serpent\'s Quickness', 'For 15..30 seconds, recharge times for your skills are reduced by 33%. Serpent\'s Quickness ends if your Health drops below 50%.', '(15...30 seconds.) Your skills recharge 33% faster. <i>Ends if your Health drops below 50%.</i>', '', '', ''),
(457, 'Dust Trap', 'When Dust Trap is triggered, every second (for 5 seconds total), all nearby foes are Blinded for 3..8 seconds and take 10..25 damage. While activating this skill, you are easily interrupted. Dust Trap ends after 90 seconds.', '(90 seconds.) Affects nearby foes. Deals 10...25 damage every second (5 seconds). Inflicts Blindness condition (3...8 seconds) every second (5 seconds). <i>Easily interrupted.</i>', '', '', ''),
(458, 'Barbed Trap', 'When Barbed Trap is triggered, all nearby foes are struck for 7..23 piercing damage, become Crippled, and begin Bleeding for 3..25 seconds.  Barbed Trap ends after 90 seconds.  While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 7...23 piercing damage. Inflicts Crippled and Bleeding conditions (3...25 seconds). <i>Easily interrupted.</i>', '', '', ''),
(459, 'Flame Trap', 'When Flame Trap is triggered, every second (for 3 seconds total), all nearby foes are struck for 5..20 fire damage and set on fire for 1..3 seconds. Flame Trap ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 5...20 fire damage every second (3 seconds). Inflicts Burning condition (1...3 seconds). <i>Easily interrupted.</i>', '', '', ''),
(460, 'Healing Spring', 'For 10 seconds, all adjacent allies are healed for 15..60 every 2 seconds. While activating this skill, you are easily interrupted.', '(10 seconds.) Affects adjacent allies. Heals for 15...60 every 2 seconds. <i>Easily interrupted.</i>', '', '', ''),
(461, 'Spike Trap', 'When Spike Trap is triggered, every second (for 2 seconds), all nearby foes are struck for 10..40 piercing damage, become Crippled for 3..25 seconds, and are knocked down. Spike Trap ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Every second, (for 2 seconds), this trap deals 10...40 piercing damage, causes knockdown, and inflicts Crippled condition (3...25 seconds). <i>Easily interrupted.</i>', '', '', ''),
(462, 'Winter', 'Create a level 1..10 spirit. For creatures within its range, all elemental damage is cold damage instead. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Converts elemental damage to cold damage for creatures in range. Does not affect spirits.', '', '', ''),
(463, 'Winnowing', 'Create a level 1..10 spirit.  Non-spirit creatures within range take 4 additional damage whenever they take physical damage.  This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Increases physical damage by +4 for creatures in range. Does not affect spirits.', '', '', ''),
(464, 'Edge of Extinction', 'Create a level 1..10 spirit. If a non-spirit creature within range dies, Edge of Extinction deals 14..50 damage to all creatures of the same type that are below 90% Health and within range of the spirit. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Deals 14...50 damage to creatures in its range whenever a creature of the same type dies. Does not affect spirits. <i>No damage to creatures above 90% Health.</i>', '', '', ''),
(465, 'Greater Conflagration', 'Create a level 1..10 spirit. For non-spirit creatures within its range, all physical damage is fire damage instead. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Converts physical damage to fire damage for creatures in range. Does not affect spirits.', '', '', ''),
(466, 'Conflagration', ' Create a level 1..10 spirit. For non-spirit creatures within its range, all arrows that hit strike for fire damage. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). All arrows deal fire damage for creatures in range. Does not affect spirits.', '', '', ''),
(467, 'Fertile Season', 'Create a level 1..10 spirit. Non-spirit creatures within range have +50..150 maximum Health and gain +8 armor. This spirit dies after 15..45 seconds.', 'Creates a level 1...10 spirit (15...45 second lifespan). Creatures in range have +50...150 maximum health and +8 armor. Does not affect spirits.', '', '', ''),
(468, 'Symbiosis', 'Create a level 1..10 spirit. For each enchantment on a non-spirit creature within range, that creature has +27..150 maximum Health. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Creatures in range have +27...150 maximum health for each enchantment on them. Does not affect spirits.', '', '', ''),
(469, 'Primal Echoes', 'Create a level 1..10 Spirit. For non-Spirit creatures within its range, Signets cost 10 Energy to use. This Spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Signets cost 10 energy for creatures in range. Does not affect spirits.', '', '', ''),
(470, 'Predatory Season', 'Create a level 1..10 spirit. For non-spirit creatures within its range, all healing is reduced by 20%. If any of your attacks hit, you gain 5 Health. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Creatures in range receive 20% less from healing. Creatures gain 5 health each time they hit with an attack. Does not affect spirits.', '', '', ''),
(471, 'Frozen Soil', 'Create a level 1..10 spirit. Non-spirit creatures within its range cannot activate resurrection skills. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit. (30...90 second lifespan). Creatures in range cannot activate resurrection skills. Does not affect spirits.', '', '', ''),
(472, 'Favorable Winds', 'Create a level 1..10 spirit.  For non-spirit creatures within its range, arrows move twice as fast and strike for +6 damage.  This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Arrows move twice as fast and hit for +6 damage for creatures in range. Does not affect spirits.', '', '', ''),
(474, 'Energizing Wind', 'Create a level 1..6 spirit. For non-spirit creatures within its range, all skills cost 15 less Energy (minimum cost 10 Energy), and skills recharge 25% slower. This spirit dies after 1..31 second[s].', 'Creates a level 1...6 spirit (1...31 seconds lifespan). Skills cost 15 less energy (minimum cost 10 energy) and recharge 25% slower for creatures in range. Does not affect spirits.', '', '', ''),
(475, 'Quickening Zephyr', 'Create a level 1..10 spirit. For non-spirit creatures within its range, all skills recharge twice as fast and cost 30% more of the base Energy to cast. This spirit dies after 15..45 seconds.', 'Creates a level 1...10 spirit (15...45 second lifespan). Skills cost 30% more Energy and recharge twice as fast for creatures in range. Does not affect spirits.', '', '', ''),
(476, 'Nature\'s Renewal', 'Create a level 1..10 spirit. For 30..150 seconds, enchantments and hexes cast by non-spirit creatures in range take twice as long to cast, and it costs twice as much Energy to maintain enchantments. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). For creatures in range, enchantments and Hexes <sub>sic</sub> take twice as long to cast and it costs twice as much Energy to maintain enchantments. Does not affect spirits.', '', '', ''),
(477, 'Muddy Terrain', 'Create a level 1..10 spirit. Non-spirit creatures within its range move 10% slower and speed boosts have no effect. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit (30...90 second lifespan). Creatures in range have 10% slower movement: also negates speed boosts. <i>Does not affect spirits.</i>', '', '', ''),
(570, 'Mark of Insecurity', 'For 5..25 seconds, target foe suffers from -1..3 Health degeneration, and enchantments and stances on target foe expire 30..80% faster. All of your non-Assassin skills are disabled for 10 seconds.', '(5...25 seconds.) Causes 1...3 Health degeneration. Enchantments and stances expire 30...80% faster on target foe. <i>Disables your non-Assassin skills (10 seconds).</i>', '', '', ''),
(571, 'Disrupting Dagger', 'Send out a Disrupting Dagger at target foe that strikes for 10..35 earth damage. If that foe was activating a skill, that skill is interrupted. This spell has half the normal range.', 'Half Range Spell. Projectile: deals 10...35 earth damage. Interrupts a skill.', '', '', ''),
(572, 'Deadly Paradox', 'All of your attack skills are disabled for 10 seconds. For 5..15 seconds, your Assassin skills activate and recharge 33% faster.', '(5...15 seconds.) Your Assassin skills activate and recharge 33% faster. <i>Disables your attack skills (10 seconds).</i>', '', '', ''),
(763, 'Jaundiced Gaze', 'Remove an enchantment from target foe. If an enchantment is removed, for the next 1..15 second[s], your next enchantment spell casts 0..1 second[s] faster and costs 1..10 less Energy.', 'Removes an enchantment from target foe. Removal effect: your next enchantment casts 0...1 seconds faster and costs 1...10 less Energy. (1...15 seconds)', '', '', ''),
(764, 'Wail of Doom', 'For 1..4 second[s], all of target foe\'s attributes are set to 0.', '(1...4 seconds.) Target foe\'s attributes are 0.', '', '', ''),
(766, 'Gaze of Contempt', 'If target foe has more than 50% Health, that foe loses all enchantments.', 'Removes target foe\'s enchantments. <i>No effect unless this foe has more than 50% Health.</i>', '', '', ''),
(769, 'Viper\'s Defense', 'All adjacent foes are Poisoned for 5..20 seconds. Shadow Step to a nearby location directly away from your target.', 'Inflict Poisoned condition (5...20 seconds) on all adjacent foes and Shadow Step to a nearby location directly away from your target.', '', '', ''),
(770, 'Return', 'All adjacent foes are Crippled for 3..8 seconds. Shadow Step to target other ally\'s location.', 'Inflicts Crippled condition (3...8 seconds) on all foes adjacent to you. You Shadow Step to target ally\'s location. <i>Cannot self-target.</i>', '', '', ''),
(771, 'Aura of Displacement', 'When you cast Aura of Displacement, Shadow Step to target foe. When you stop maintaining Aura of Displacement you return to your original location.', 'Shadow Step to target foe. End effect: return to your original location.', '', '', ''),
(772, 'Generous Was Tsungrai', 'Hold Tsungrai\'s ashes for up to 15..60 seconds and gain +50..140 maximum Health. When you drop his ashes, you gain 100..280 Health.', '(15...60 seconds.) You have +50...140 maximum Health. Drop effect: you gain 100...280 Health.', '', '', ''),
(773, 'Mighty Was Vorizun', 'Hold Vorizun\'s ashes for up to 15..60 seconds. While you hold his ashes, you gain +15 armor and +30 maximum Energy.', '(15...60 seconds.) You have +15 armor and +30 maximum Energy.', '', '', ''),
(775, 'Death Blossom', 'Must follow an off-hand attack. If it hits, Death Blossom strikes target foe for +20..45 damage and all adjacent foes take 20..45 damage.', 'Deals +20...45 damage. Also affects foes adjacent to target foe. <i>Must follow an off-hand attack.</i>', 'Death Blossom (PvP)', 'Must follow an off-hand attack. If it hits, Death Blossom strikes target foe for +20..45 damage and all adjacent foes take 20..45 damage.', 'Deals +20...45 damage. Also affects foes adjacent to target foe. <i>Must follow an off-hand attack.</i>'),
(776, 'Twisting Fangs', 'Must follow an off-hand attack. If it hits, Twisting Fangs strikes for +10..20 damage and struck foe suffers from Bleeding and Deep Wound for 5..20 seconds.', 'Deals +10...20 damage. Inflicts Bleeding and Deep Wound conditions (5...20 seconds). <i>Must follow an off-hand attack.</i>', '', '', ''),
(777, 'Horns of the Ox', 'Must follow an off-hand attack. If it hits, Horns of the Ox strikes for +1..11 damage. If struck foe is not adjacent to any allies, that foe is knocked down.', 'Deals +1...11 damage. Causes knock-down if the target foe is not adjacent to any of its allies. <i>Must follow an off-hand attack.</i>', '', '', ''),
(778, 'Falling Spider', ' Must strike a knocked-down foe. If it hits, Falling Spider strikes for +15..35 damage and target foe is Poisoned for 5..20 seconds.', 'Deals +15...35 damage. Inflicts Poisoned condition (5...20 seconds). <i>No effect unless target foe is knocked-down.</i>', '', '', ''),
(779, 'Black Lotus Strike', 'If it hits, Black Lotus Strike strikes for +10..31 damage. If target foe is suffering from a Hex, you gain 5..13 Energy.', 'Deals +10...31 damage. You gain 5...13 Energy if target foe is hexed.', '', '', ''),
(780, 'Fox Fangs', 'Must follow a lead attack. Fox Fangs cannot be blocked and strikes for +10..35 damage if it hits.', 'Deals +10...35 damage. Unblockable. <i>Must follow a lead attack.</i>', 'Fox Fangs (PvP)', 'Must follow a lead attack. Fox Fangs cannot be blocked and strikes for +10..25 damage if it hits.', 'Deals +10...25 damage. Unblockable. <i>Must follow a lead attack.</i>'),
(781, 'Moebius Strike', 'Must follow a Dual Attack. If it hits, Moebius Strike strikes for +10..35 damage. If you strike a foe whose Health is below 50%, all your other attack skills are recharged.', 'Deals +10...35 damage. Recharges all your other attack skills if target foe\'s Health is below 50%. <i>Must follow a dual attack.</i>', '', '', ''),
(782, 'Jagged Strike', 'If Jagged Strike hits, your target suffers from Bleeding for 5..20 seconds.', 'Inflicts Bleeding condition (5...20 seconds).', '', '', ''),
(783, 'Unsuspecting Strike', 'If this attack hits, you strike for +19..31 damage. If your target was above 90% Health you deal an additional 15..75 damage.', 'Deals +19...31 damage. Deals 15...75 more damage if target foe\'s Health is above 90%.', '', '', ''),
(784, 'Entangling Asp', 'Entangling Asp must follow a lead attack. Target foe is knocked down and becomes Poisoned for 5..20 seconds.', 'Causes knock-down. Inflicts Poisoned condition (5...20 seconds). <i>Must follow a lead attack.</i>', '', '', ''),
(785, 'Mark of Death', ' For 4..10 seconds, target foe gains 33% less benefit from healing.', '(4...10 seconds.) Target foe receives 33% less from healing.', '', '', ''),
(786, 'Iron Palm', 'Target touched foe suffers 5..50 damage, and if that foe is suffering from a hex or condition that foe is knocked down. Iron Palm counts as a lead attack.', 'Touch Skill. Deals 5...50 damage. Causes knock-down if target foe is hexed or has a condition. Counts as a lead attack.', '', '', ''),
(787, 'Resilient Weapon', 'For 3..12 seconds, target ally has a Resilient Weapon. While suffering from a hex or condition, that ally gains +1..6 Health regeneration and +24 armor.', '(3...12 seconds.) +1...6 Health regeneration and +24 armor. <i>No effect unless target ally is hexed or has a condition.</i>', '', '', ''),
(788, 'Blind Was Mingson', 'Hold Mingson\'s ashes for up to 15..60 seconds. When you drop his ashes, all nearby foes are Blinded for 3..8 seconds.', '(15...60 seconds.) Drop effect: inflicts Blindness condition (3...8 seconds) on all nearby foes.', '', '', ''),
(789, 'Grasping Was Kuurong', 'Hold Kuurong\'s ashes for up to 15..60 seconds. When you drop his ashes, all nearby foes are struck for 15..75 damage and knocked down.', '(15...60 seconds.) Drop effect: deal 15...75 damage and knocks-down all nearby foes.', '', '', ''),
(790, 'Vengeful Was Khanhei', 'Hold Khanhei\'s ashes for 5..11 seconds. Whenever a foe strikes you in combat while you are holding Khanhei\'s ashes, you steal 5..35 Health from that foe.', '(5...11 seconds.) You steal 5...35 Health from every foe that hits you with an attack.', '', '', ''),
(791, 'Flesh of My Flesh', 'Lose half your Health. Resurrect target party member with your current Health and 5..20% Energy.', 'Resurrect target party member (half your current Health and 5...20% Energy). <i>Lose half your Health.</i>', 'Flesh of My Flesh (PvP)', 'Lose half your Health. Resurrect target party member with your current Health and 5..20% Energy.', 'Resurrect target party member (half your current Health and 5...20% Energy). Lose half your Health.'),
(792, 'Splinter Weapon', 'For 20 seconds, target ally has a Splinter Weapon. Target ally\'s next 1..5 attack[s] deal 5..50 damage to up to 4 adjacent foes.', '(20 seconds.) Attacks deal 5...50 damage to 3 <sub>sic</sub> adjacent foes. <i>Ends after 1...5 attacks.</i>', 'Splinter Weapon (PvP)', 'For 20 seconds, target ally has a Splinter Weapon. Target ally\'s next 1..5 attack[s] deal 5..30 damage to up to 4 adjacent foes.', '(20 seconds.) Attacks deal 5...30 damage to 3 <sub>sic</sub> adjacent foes. <i>Ends after 1...5 attacks.</i>'),
(793, 'Weapon of Warding', 'For 3..8 seconds, target ally has a Weapon of Warding that grants target ally +2..4 Health regeneration and a 50% chance to block.', '(3...8 seconds.) +2...4 Health regeneration. 50% chance to block.', 'Weapon of Warding (PvP)', 'For 3..8 seconds, target ally has a Weapon of Warding that grants target ally +2..4 Health regeneration and a 50% chance to block. Weapon of Warding ends if that ally attacks.', '(3...8 seconds.) +2...4 Health regeneration. 50% chance to block. <i>Ends if target ally attacks.</i>'),
(794, 'Wailing Weapon', 'For 3..9 seconds, target ally has a Wailing Weapon. Whenever the Wailing Weapon strikes an attacking foe, that foe is interrupted.', '(3...9 seconds.) Attacks interrupt attacking foes.', '', '', ''),
(795, 'Nightmare Weapon', 'For 12 seconds, target ally has a Nightmare Weapon. Target ally\'s next 3 attacks are reduced by 10..50 damage and steal up to 10..50 Health.', '(12 seconds.) Target ally\'s attacks steal 10...50 Health but deal 10...50 less damage. <i>Ends after 3 attacks.</i>', '', '', ''),
(799, 'Beguiling Haze', 'Shadow Step to target foe. That foe becomes Dazed for 3..9 seconds.', 'You Shadow Step to this foe. Inflicts Dazed condition (3...9 seconds).', '', '', ''),
(800, 'Enduring Toxin', ' For 5 seconds, target foe suffers -1..5 Health degeneration. If that foe is moving when Enduring Toxin would end, Enduring Toxin is renewed for another 5 seconds.', '(5 seconds.) Causes -1...5 Health degeneration. Renewal: if the target foe is moving when this hex ends.', '', '', ''),
(801, 'Shroud of Silence', 'All of your spells are disabled for 15 seconds. For 1..3 second[s], target touched foe cannot cast spells.', 'Elite Touch Hex Spell. (1...3 seconds.) Target foe cannot cast spells. <i>Your spells are disabled for 15 seconds.</i>', '', '', ''),
(802, 'Expose Defenses', 'For 1..11 second[s], target foe cannot block your attacks.', '(1...11 seconds.) Target foe cannot block your attacks.', '', '', ''),
(803, 'Power Leech', 'If target foe is casting a spell or chant, that skill is interrupted and for 10 seconds, whenever that foe casts a spell, you steal up to 5..15 Energy from that foe.', 'Interrupt a spell or a chant. Interruption effect: steal 5...15 Energy whenever target foe casts a spell (10 seconds).', '', '', ''),
(804, 'Arcane Languor', 'For 1..10 second[s], all spells cast by the target foe cause 10 Overcast.', '(1...10 seconds.) Target foe\'s spells cause 10 Overcast.', '', '', ''),
(805, 'Animate Vampiric Horror', 'Exploit nearest corpse to animate a level 1..17 Vampiric Horror. Whenever a Vampiric Horror you control deals damage, you gain the same amount of Health.', 'Creates a level 1...17 vampiric horror. You gain Health equal to the damage it deals. <i>Exploits a fresh corpse.</i>', '', '', ''),
(806, 'Cultist\'s Fervor', 'For 5 seconds and 3 seconds longer for every rank of Soul Reaping, your Necromancer spells cost -1..6 Energy to cast but you suffer from Bleeding for 10 seconds each time you cast a Necromancer spell.', '(5 seconds plus 3 seconds more for every rank of Soul Reaping.) Your Necromancer spells cost 1...6 less Energy. <i>You suffer from Bleeding (10 seconds) each time you cast a Necromancer spell.</i>', '', '', ''),
(808, 'Reaper\'s Mark', 'For 30 seconds, target foe suffers -1..5 Health degeneration. If that foe dies while hexed with Reaper\'s Mark, you gain 5..15 Energy.', '(30 seconds.) Causes -1...5 Health degeneration. You gain 5...15 Energy if target foe dies while suffering from this hex.', '', '', ''),
(809, 'Shatterstone', 'Target foe is struck for 25..100 cold damage and is hexed with Shatterstone for 3 seconds. When Shatterstone ends, that foe and all nearby foes are struck for 25..100 cold damage.', '(3 seconds.) Initial effect: deals 25...100 cold damage. End effect: deals 25...100 cold damage to target and all nearby foes.', '', '', ''),
(810, 'Protector\'s Defense', 'For 5..11 seconds, allies adjacent to you have a 75% chance to block incoming attacks. Protector\'s Defense ends if you move.', '(5...11 seconds.) Allies adjacent to you have 75% chance to block. <i>Ends if you move.</i>', '', '', ''),
(811, 'Run as One', 'For 5..15 seconds, you and your pet run 25% faster.', '(5...15 seconds.) You and your pet move 25% faster.', '', '', ''),
(812, 'Defiant Was Xinrae', 'Hold Xinrae\'s ashes for up to 15..60 seconds. While you hold her ashes, you cannot lose more than 20% of your max Health from a single hit. When you drop her ashes, you steal 5..50 Health from all nearby foes.', '(15...60 seconds.) You cannot lose more than 20% of your max Health from a single hit. Drop effect: steal 5...50 Health from nearby foes.', '', '', ''),
(813, 'Lyssa\'s Aura', 'For 10 seconds, you have +0..5 Energy regeneration. This enchantment reapplies itself every time you cast a spell on a foe.', '(10 seconds.) You have +0...5 Energy regeneration. Renewal: every time you cast a spell on an enemy.', '', '', ''),
(814, 'Shadow Refuge', 'For 6 seconds, you gain 5..10 Health regeneration. When Shadow Refuge ends, you gain 40..100 Health if you are attacking.', '(6 seconds.) You have +5...10 Health regeneration. End effect: heals you for 40...100 if you are attacking.', '', '', ''),
(815, 'Scorpion Wire', ' For 8..20 seconds, the next time you and target foe are more than 100\' apart, you Shadow Step to that foe and that foe is knocked down. This spell has half the normal range.', 'Half Range Hex Spell. (8...20 seconds.) Shadow Step to target foe and cause knock-down the next time this foe is more than 100\' away from you.', '', '', ''),
(816, 'Mirrored Stance', 'For 10..35 seconds, whenever target foe enters a stance, you enter the same stance.', '(10...35 seconds.) You enter any stance used by target foe.', '', '', ''),
(817, 'Discord', 'If target foe is suffering from a condition and under the effects of a hex or an enchantment, that foe suffers 30..110 damage.', 'Deals 30...110 damage. <i>No effect unless target foe has a condition and is either hexed or enchanted.</i>', 'Discord (PvP)', 'If target foe is suffering from a condition and under the effects of a hex or an enchantment, that foe suffers 30..110 damage.', 'Deals 30...110 damage. <i>No effect unless target foe has a condition and is either hexed or enchanted.</i>'),
(818, 'Well of Weariness', 'Exploit target corpse to create a Well of Weariness for 10..55 seconds. Enemies within the Well of Weariness suffer -1 Energy degeneration.', '(10...55 seconds.) Foes in this well have -1 Energy degeneration. <i>Exploits a fresh corpse.</i>', '', '', ''),
(819, 'Vampiric Spirit', 'Steal up to 5..50 Health from target foe. For 10 seconds, you have +5..10 Health regeneration.', 'Steal 5...50 Health from target foe. You have +5...10 Health regeneration (10 seconds).', '', '', ''),
(820, 'Depravity', 'For 5..20 seconds, whenever target foe casts a spell, that foe and one nearby foe lose 1..5 Energy.', '(5...20 seconds.) Causes 1...5 Energy loss whenever target foe casts a spell. One foe near your target also loses Energy.', '', '', ''),
(821, 'Icy Veins', 'Target foe is struck for 10..90 cold damage. For 10..35 seconds, if target foe dies all nearby foes are struck for 20..110 cold damage.', '(10...35 seconds.) Deals 20...110 cold damage to nearby foes if target foe dies. Initial effect: deals 10...90 cold damage.', '', '', ''),
(822, 'Weaken Knees', 'For 1..16 second[s], target foe suffers -1..4 Health degeneration and takes 5..10 damage while moving.', '(1...16 seconds.) Target foe has -1...4 Health degeneration and takes 5...10 damage while moving.', '', '', ''),
(823, 'Burning Speed', 'For 7 seconds, you are set on fire and move 30..45% faster. When Burning Speed ends, all adjacent foes are set on fire for 3..9 seconds.', '(7 seconds.) You move 30...45% faster. <i>You suffer from Burning (7 seconds).</i> End effect: inflicts Burning condition (3...9 seconds) on adjacent foes.', '', '', ''),
(824, 'Lava Arrows', 'Lava Arrows fly toward up to 3 foes near your target and strike for 20..65 fire damage if they hit.', 'Projectile: deals 20...65 fire damage. Bonus effect: sends projectiles at 2 other foes near your target.', '', '', ''),
(825, 'Bed of Coals', 'Create a Bed of Coals at target foe\'s location. For 5 seconds, foes adjacent to target foe are struck for 5..29 fire damage each second. Any foe knocked down on the Bed of Coals is set on fire for 3..7 seconds.', 'Deals 5...29 fire damage each second (5 seconds) to location of target foe. Hits foes adjacent to your target. Inflicts Burning condition (3...7 seconds) on knocked down foes.', '', '', ''),
(826, 'Shadow Form', 'For 5..21 seconds, you cannot be the target of enemy spells, and you gain 5 damage reduction for each Assassin enchantment on you. You cannot deal more than 5..25 damage with a single skill or attack.', '(5...21 seconds.) Enemy spells cannot target you. Gain 5 damage reduction for each Assassin enchantment on you. <i>You cannot deal more than 5...25 damage with a single skill or attack.</i>', 'Shadow Form (PvP)', 'For 5..15 seconds, all hostile spells that target you fail and all attacks against you miss. When Shadow Form ends, lose all but 5..50 Health.', '(5...15 seconds.) Hostile spells targeting you fail, and attacks against you miss. grey'),
(827, 'Siphon Strength', 'For 5..20 seconds, target foe deals 5..50 less damage with attacks and all of your attacks against that foe have an additional 33% chance of being a critical hit.', '(5...20 seconds.) Target foe deals -5...50 attack damage. You have +33% chance to land a critical hit on this foe.', '', '', ''),
(828, 'Vile Miasma', 'Target foe is struck for 10..65 cold damage. If suffering from a condition, that foe is hexed with Vile Miasma and suffers -1..5 Health degeneration for 10 seconds.', 'Causes -1...5 Health degeneration (10 seconds). Initial effect: deals 10...65 cold damage. <i>Hex is only applied if target foe has a condition.</i>', '', '', ''),
(830, 'Ray of Judgment', 'Invoke a Ray of Judgment at target foe\'s location. For 5 seconds, target foe and all foes adjacent to this location take 5..45 holy damage each second and begin Burning for 1..3 second[s].', 'Deals 5...45 holy damage and inflicts Burning (1...3 seconds) every second (5 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(831, 'Primal Rage', 'For 1..15 second[s], you attack 33% faster and move 25% faster, but you take double damage.', '(1...15 seconds.) You attack 33% faster and move 25% faster. <i>You take double damage.</i>', '', '', ''),
(832, 'Animate Flesh Golem', 'Exploit nearest corpse to animate a level 3..25 Flesh Golem. The Flesh Golem leaves an exploitable corpse. You can have only one Flesh Golem at a time.', 'Creates a level 3...25 flesh golem which leaves a fresh corpse when it dies. <i>Exploits a fresh corpse. You can have only one flesh golem at a time.</i>', '', '', ''),
(834, 'Reckless Haste', 'For 6..12 seconds, target foe and all adjacent foes are hexed with Reckless Haste. While hexed, they attack 25% faster but have a 50% chance to miss with attacks.', 'Also hexes foes adjacent to your target (6...12 seconds). These foes have 50% chance to miss, but attack 25% faster.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(835, 'Blood Bond', 'For 3..12 seconds, target foe and all adjacent foes are hexed with Blood Bond. Whenever an ally hits one of them with an attack, that ally gains 5..20 Health. If one of these foes dies while hexed, all allies adjacent to that foe are healed for 20..100 Health.', 'Also hexes foes adjacent to your target (3...12 seconds). Allies hitting these foes gain 5...20 health. If any of these foes dies while hexed, adjacent allies are healed for 20...100.', '', '', ''),
(836, 'Ride the Lightning', 'You Ride the Lightning to target. All adjacent foes are Blinded for 1..5 second[s].  If your target is a foe, it is struck for 10..70 lightning damage. This spell has 25% armor penetration.', 'Deals 10...70 lightning damage. 25% armor penetration. Blinds all adjacent foes (1...5 seconds). You instantly move to your target. May target allies.', 'Ride the Lightning (PvP)', 'You Ride the Lightning to target. All adjacent foes are Blinded for 1..5 second[s].  If your target is a foe, it is struck for 10..70 lightning damage. This spell has 25% armor penetration.', 'Deals 10...70 lightning damage. 25% armor penetration. Blinds all adjacent foes (1...5 seconds). You instantly move to your target. May target allies.'),
(837, 'Energy Boon', 'For 36..60 seconds, Energy Boon raises the maximum Health of you and target ally by 1..3 for each point of your respective maximum Energy.  When this enchantment is first applied, you and your target gain 1..12 Energy.  You gain an additional 1 Energy for every 2 points you have in Energy Storage.', '(36...60 seconds.) Maximum Health for you and target ally is increased by 1...3 for each point of maximum Energy. Initial effect: Both gain 1...12 Energy. You gain +1 Energy for every 2 points of Energy Storage.', '', '', ''),
(838, 'Dwayna\'s Sorrow', 'For 30 seconds, target ally and all nearby allies are enchanted with Dwayna\'s Sorrow. If an ally dies while under the effects of Dwayna\'s Sorrow, your party is healed for 5..50.', 'Enchants allies near your target (30 seconds). Your party is healed for 5...50 whenever one of these allies dies.', '', '', ''),
(839, '"Retreat!"', 'If there are any dead allies within earshot, your party moves 33% faster for 5..11 seconds.', '(5...11 seconds.) Party members in earshot move 33% faster. <i>No effect unless there are dead allies within earshot.</i>', '', '', ''),
(840, 'Poisoned Heart', 'You and all adjacent foes are Poisoned for 5..15 seconds.', 'Inflicts Poisoned condition (5...15 seconds) to adjacent foes. <i>You are also Poisoned.</i>', '', '', ''),
(841, 'Fetid Ground', 'Target foe is struck for 15..65 cold damage. If that foe is knocked down, that foe becomes Poisoned for 5..20 seconds.', 'Deals 15...65 cold damage. Inflicts Poisoned condition (5...20 seconds) if target foe is knocked-down.', '', '', ''),
(842, 'Arc Lightning', 'Target foe is struck for 5..40 lightning damage. If you are Overcast, two foes near your target are struck for 15..85 lightning damage. Damage from Arc Lightning has 25% armor penetration.', 'Deals 5...40 lightning damage. Deals 15...85 lightning damage to two nearby foes if you are Overcast. 25% armor penetration.', '', '', ''),
(843, 'Gust', 'For 5..11 seconds, both you and target ally move 33% faster.  When you cast this spell, all foes near you and your target take 15..70 cold damage. Foes struck by Gust while attacking or moving are knocked down.', '(5...11 seconds.) You and target ally move 33% faster. Initial effect: Foes near you and target ally are struck for 15...70 cold damage. Attacking or moving foes are knocked down.', '', '', ''),
(844, 'Churning Earth', 'Create Churning Earth at target foe\'s location. For the next 5 seconds, Churning Earth strikes foes near that location for 10..40 earth damage each second. Any foe moving faster than normal when struck by Churning Earth is knocked down.', 'Deals 10...40 earth damage each second (5 seconds). Hits foes near target\'s initial location. Causes knock-down to foes moving faster than normal.', '', '', ''),
(845, 'Liquid Flame', 'Target foe is struck for 7..112 fire damage. If that foe is attacking or casting a spell, nearby foes are also struck for 7..112 fire damage.', 'Deals 7...112 fire damage. Deals 7...112 fire damage to nearby foes if target was attacking or casting.', '', '', ''),
(846, 'Steam', ' Target foe is struck for 20..60 cold damage. If target foe is on fire, Steam Blinds that foe for 5..10 seconds.', 'Deals 20...60 cold damage. Inflicts Blindness condition (5...10 seconds) if target foe is Burning.', '', '', ''),
(847, 'Boon Signet', 'Heal target ally for 20..80 Health. Your next Healing or Protection Prayer  spell that targets an ally heals for an additional 20..80 Health.', 'Heals for 20...80. Your next Healing or Protection Prayer <sub>sic</sub> spell that targets an ally heals for +20...80 Health.', '', '', ''),
(848, 'Reverse Hex', 'Remove one hex from target ally. If a hex was removed in this way, for 5..10 seconds, the next time target ally would take damage, that damage is reduced by 5..50.', '(5...10 seconds.) Removes one hex from target ally. The next damage this ally takes is reduced by 5...50. <i>No effect unless this ally is hexed.</i>', '', '', ''),
(849, 'Lacerating Chop', 'If Lacerating Chop hits, you deal  +5..20 damage. If it strikes a knocked down foe your target suffers from Bleeding for 5..20 seconds.', 'Deals +5...20 damage. Inflicts Bleeding condition (5...20 seconds) if target foe is knocked-down.', '', '', ''),
(850, 'Fierce Blow', 'If Fierce Blow hits, you strike for +5..20 damage. If target foe was  suffering from Weakness, that foe also suffers from a Deep Wound for 1..8 second[s].', 'Deals +5...20 damage. Inflicts Deep Wound (1...8 seconds) if target foe is Weakened.', '', '', ''),
(851, 'Sun and Moon Slash', 'Attack target foe twice. These attacks cannot be blocked.', 'You attack target foe twice. Unblockable.', '', '', ''),
(852, 'Splinter Shot', 'If Splinter Shot hits, you deal +3..15 damage. If Splinter Shot is blocked, all foes adjacent to your target take 5..65 damage.', 'Deals +3...15 damage. Deals 5...65 damage to adjacent foes if blocked.', '', '', ''),
(853, 'Melandru\'s Shot', 'If this attack hits, your target bleeds for 5..25 seconds. If it hits a foe that is moving or knocked down, that foe takes +10..25 damage and is crippled for 5..15 seconds.', 'Inflicts Bleeding (5...25 seconds). Deals +10...25 damage and inflicts Crippled (5...15 seconds) if target foe was moving or knocked down.', '', '', ''),
(854, 'Snare', 'When Snare is triggered, all nearby foes become Crippled for 3..15 seconds. Snare ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Inflicts Crippled condition (3...15 seconds). <i>Easily interrupted.</i>', '', '', ''),
(858, 'Dancing Daggers', 'Send out three Dancing Daggers at target foe, each striking for 5..35 earth damage if they hit. Dancing Daggers has half the normal range. This skill counts as a lead attack.', 'Half Range Spell. Three projectiles: each deals 5...35 earth damage. Counts as a lead attack.', '', '', ''),
(859, 'Conjure Nightmare', ' For 2..16 seconds, target foe suffers -8 Health degeneration.', '(2...16 seconds.) Causes -8 Health degeneration.', '', '', ''),
(860, 'Signet of Disruption', 'If target foe is casting a spell, the spell is interrupted and that foe suffers 10..51 damage. If that foe is hexed, Signet of Disruption can interrupt any non-spell skills.', 'Interrupts a spell. Can interrupt any skill if target foe is hexed. Interruption effect: deals 10...51 damage.', '', '', ''),
(862, 'Ravenous Gaze', 'Deal 15..30 damage and steal 15..30 Health from target foe and all nearby foes.', 'Deals 15...30 damage and steals 15...30 Health from target and nearby foes.', '', '', ''),
(863, 'Order of Apostasy', 'For 5 seconds, whenever a party member hits a foe with physical damage, that foe loses one enchantment. For each Monk enchantment removed, you lose 25..15% maximum Health.', 'Enchants all party members (5 seconds). These party members remove one enchantment when they deal physical damage. <i>Removal cost: for each Monk enchantment, you lose 25...15% maximum Health.</i>', '', '', ''),
(864, 'Oppressive Gaze', 'Target foe and adjacent foes take 10..30 shadow damage. Foes already suffering from a condition are Poisoned and Weakened for 3..12 seconds.', 'Deals 10...30 damage to target and adjacent foes. Inflicts Poison and Weakness (3...12 second) on foes suffering from a condition.', '', '', ''),
(865, 'Lightning Hammer', 'Target foe is struck for 10..100 lightning damage and applies Cracked Armor for 5..20 seconds. Lightning Hammer has 25% armor penetration.', 'Deals 10...100 lightning damage. Applies Cracked Armor (5...20 seconds). 25% armor penetration.', 'Lightning Hammer (PvP)', 'Target foe is struck for 10..100 lightning damage. Lightning Hammer has 25% armor penetration.', 'Deals 10...100 lightning damage. 25% armor penetration.'),
(866, 'Vapor Blade', ' Target foe is struck for 15..135 cold damage. Vapor Blade deals half damage if that foe has any enchantments on them.', 'Deals 15...135 cold damage. Half damage if target foe is enchanted.', '', '', ''),
(867, 'Healing Light', 'Heal target ally for 40..100 Health. If your target has an enchantment, you gain 1..3 Energy.', 'Heals for 40...100. You gain 1...3 Energy if target ally is enchanted.', '', '', ''),
(869, '"Coward!"', 'If target foe is moving, that foe is knocked down.', 'Causes knock-down if target foe is moving.', '', '', ''),
(870, 'Pestilence', 'Create a level 1..10 spirit. When any creature within its range dies, conditions on that creature spread to any creature in the area already suffering from a condition. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit (30...90 second lifespan). When any creature in range dies, conditions on this creature spread to any creature in the area with a condition. Spirits are not affected.', '', '', ''),
(871, 'Shadowsong', 'Create a level 1..10 spirit. The spirit\'s attacks deal 5..20 damage and cause Blindness for 1..6 second[s]. This spirit dies after 30 second[s]. ', 'Creates a level 1...10 spirit (30-second lifespan). Its attacks deal 5...20 damage and inflict Blindness condition (1...6 seconds).', 'Shadowsong (PvP)', 'Create a level 1..6 spirit. The spirit\'s attacks deal 5..20 damage and cause Blindness for 1..6 second[s]. This spirit dies after 30 second[s]. ', 'Creates a level 1...6 spirit (30-second lifespan). Its attacks deal 5...20 damage and inflict Blindness condition (1...6 seconds).'),
(876, 'Signet of Shadows', 'Target foe takes 5..35 damage. If your target was Blinded, that foe suffers an additional 15..60 damage.', 'Deals 5...35 damage. Deals 15...60 more damage if target foe is Blinded.', '', '', ''),
(877, 'Lyssa\'s Balance', 'Target foe loses one Enchantment. If you have more Enchantments than target foe, this skill has no effect.', 'Removes one enchantment from target foe. <i>No effect if you have more enchantments than this foe.</i>', '', '', ''),
(878, 'Visions of Regret', 'For 10 seconds, target foe and adjacent foes take 15..45 damage whenever they use a skill and 5..50 additional damage if not under the effects of another Mesmer hex.', 'Also hexes foes adjacent to target (10 seconds). These foes take 15...45 damage whenever they use a skill and 5...50 additional damage if not under the effects of another Mesmer hex.', 'Visions of Regret (PvP)', 'For 10 seconds, target foe and adjacent foes take 10..30 damage whenever they use a skill and 5..40 additional damage if not under the effects of another Mesmer hex.', 'Also hexes foes adjacent to target (10 seconds). These foes take 10...30 damage whenever they use a skill and 5...40 additional damage if not under the effects of another Mesmer hex.'),
(879, 'Illusion of Pain', 'For 8 seconds, target foe has -3..10 Health degeneration and takes 3..10 damage each second. When Illusion of Pain ends, that foe is healed for 36..120.', '(8 seconds.) Causes -3...10 Health degeneration and target foe takes 3...10 damage each second. End effect: that foe is healed for 36...120.', 'Illusion of Pain (PvP)', 'For 8 seconds, target foe has -3..10 Health degeneration and takes 3..10 damage each second. When Illusion of Pain ends or is reapplied, that foe is healed for 36..120.', '(8 seconds.) Causes -3...10 Health degeneration and target foe takes 3...10 damage each second. End effect: that foe is healed for 36...120. <i>Ends if reapplied.</i>'),
(880, 'Stolen Speed', 'For 1..10 seconds, spells cast by the target foe and all adjacent foes take 100% longer to cast. Spells targeting these foes that are cast by you or your allies take 50% less time to cast.', 'Also hexes adjacent foes (1...10 seconds). Doubles spell casting time. Spells cast by you or your allies have -50% casting times.', 'Stolen Speed (PvP)', 'For 1..10 second[s], spells cast by the target foe and all adjacent foes take 100% longer to cast. Spells you cast that target these foes take 50% less time to cast.', 'Also hexes adjacent foes (1...10 seconds). Doubles spell casting time. Spells you cast on hexed foes have -50% casting time.'),
(881, 'Ether Signet', 'If you have less than 5..10 Energy, gain 10..20 Energy.', 'You gain 10...20 Energy. <i>No effect unless you have less than 5...10 Energy.</i>', '', '', ''),
(882, 'Signet of Disenchantment', ' Lose all Energy. Target foe loses one enchantment.', 'Removes one enchantment. <i>You lose all Energy.</i>', '', '', ''),
(883, 'Vocal Minority', 'For 5..20 seconds, target foe and all nearby foes cannot use shouts or chants.', 'Also hexes foes near target (5...20 seconds). These foes cannot use shouts or chants.', '', '', ''),
(884, 'Searing Flames', 'Target foe and all nearby foes are struck with Searing Flames. Foes already on fire when this skill is cast are struck for 10..100 fire damage. Foes not already on fire begin Burning for 1..7 seconds.', 'Hits foes near your target. Deals 10...100 fire damage to foes already Burning. Inflicts Burning condition (1...7 seconds) to foes not Burning.', '', '', ''),
(885, 'Shield Guardian', 'For 1..4 seconds, all party members in earshot have a 75% chance to block incoming attacks. If an attack is blocked, all allies in earshot are healed for 10..40 and Shield Guardian ends.', '(1...4 seconds). Party members in earshot have a 75% chance to block attacks. Block effect: Allies in earshot are healed for 10...40, and Shield Guardian ends.', '', '', ''),
(886, 'Restful Breeze', 'For 8..18 seconds, target ally has +10 Health regeneration. This enchantment ends if that ally attacks or uses a skill.', '(8...18 seconds.) +10 Health regeneration. <i>Ends if target ally attacks or uses a skill.</i>', '', '', ''),
(887, 'Signet of Rejuvenation', 'Heal target ally for 15..75. If target ally is casting a spell or attacking, that ally is healed for an additional 15..75 Health.', 'Heals for 15...75. Heals for 15...75 more if target ally is casting a spell or attacking.', '', '', ''),
(888, 'Whirling Axe', 'If Whirling Axe hits, you strike for +5..20 damage and any stance being used by your target ends. This attack cannot be blocked.', 'Deals +5...20 damage and removes a stance. Unblockable.', '', '', ''),
(889, 'Forceful Blow', 'If Forceful Blow hits, you strike for +10..30 damage and any stance being used by target foe ends. Target foe suffers from Weakness for 5..20 seconds. This attack cannot be blocked.', 'Deals +10...30 damage. Remove target foe\'s stance. Inflicts Weakness condition (5...20 seconds). Unblockable.', '', '', ''),
(891, '"None Shall Pass!"', 'All nearby foes that are moving are knocked down. This skill recharges 1..8 second[s] faster for each foe knocked down (maximum 25 seconds).', 'Knocks down all nearby moving foes. Recharges 1...8 seconds faster for each affected foe (maximum 25 seconds).', '', '', ''),
(892, 'Quivering Blade', 'If Quivering Blade hits, you strike for +10..40 damage. If this attack hits a moving foe, that foe is Dazed for 5 seconds.', 'Deals +10...40 damage. Inflicts Dazed condition (5 seconds) if target foe was moving.', '', '', ''),
(893, 'Seeking Arrows', 'For 3..14 seconds, your arrows cannot be blocked. Seeking Arrows ends if you fail to hit.', '(3...14 seconds.) Your arrows are unblockable. <i>Ends if you fail to hit.</i>', '', '', ''),
(898, 'Overload', 'For 5 seconds, target foe suffers -1..3 Health degeneration. If target foe is using a skill, then Overload deals 15..75 damage to that foe and all adjacent foes.', '(5 seconds.) Causes -1...3 Health degeneration. If target foe is using a skill, that foe and all adjacent foes take 15...75 damage.', '', '', ''),
(899, 'Images of Remorse', 'For 5..10 seconds, target foe suffers -1..3 Health degeneration. If that foe was attacking, that foe takes 10..52 damage.', '(5...10 seconds.) Causes -1...3 Health degeneration. Initial effect: 10...52 damage if target foe is attacking.', '', '', ''),
(900, 'Shared Burden', 'For 5..20 seconds, target foe and all nearby foes attack, cast spells, and move 50% slower.', 'Also hexes foes near your target (5...20 seconds). These foes attack, cast spells, and move 50% slower.', 'Shared Burden (PvP)', 'For 5..20 seconds, target foe and all nearby foes attack, cast spells, and move 33% slower.', 'Also hexes foes near your target (5...20 seconds). These foes attack, cast spells, and move 33% slower.'),
(901, 'Soul Bind', 'For 30 seconds, every time target foe is healed, the healer takes 20..80 damage. This hex ends if target is suffering from a Smiting Prayers hex.', '(30 seconds.) Every time target foe is healed, the healer takes 20...80 damage. <i>Ends if target is suffering from a Smiting Prayers hex.</i>', '', '', ''),
(902, 'Blood of the Aggressor', 'Steal up to 5..45 Health from target foe. If that foe was attacking, that foe suffers Weakness for 3..12 seconds.', 'Steal 5...45 Health. Inflicts Weakness (3...12 seconds) if target foe was attacking.', '', '', ''),
(903, 'Icy Prism', 'Target foe is struck for 15..75 cold damage. If that foe has a Water Magic hex, Icy Prism deals +15..75 cold damage to all other nearby foes.', 'Deals 15...75 cold damage. Deals +15...75 cold damage to other nearby foes if target has a Water Magic hex.', '', '', ''),
(904, 'Furious Axe', 'If Furious Axe hits, you strike for +5..35 damage. If it is blocked you gain 3 strikes worth of adrenaline.', 'Deals +5...35 damage. Gives you 3 strikes of adrenaline if blocked.', '', '', ''),
(905, 'Auspicious Blow', 'If Auspicious Blow hits, you strike for +5..20 damage and gain 3..8 Energy. If target foe is suffering from Weakness, this attack is unblockable.', 'Deals +5...20 damage and you gain 3...8 Energy. Unblockable if target foe is Weakened.', '', '', ''),
(906, '"On Your Knees!"', 'Lose all adrenaline. If any adjacent foes are knocked down, all of your stances are recharged.', 'Recharges all your stances if you are adjacent to a knocked-down foe. <i>You lose all adrenaline.</i>', '', '', ''),
(907, 'Dragon Slash', 'If Dragon Slash hits, you strike for +10..40 damage and gain 1..5 strike[s] of adrenaline.', 'Deals +10...40 damage. You gain 1...5 strikes of adrenaline if it hits.', '', '', ''),
(908, 'Marauder\'s Shot', 'If Marauder\'s Shot hits, you strike for +10..35 damage and all your non-attack skills are disabled for 10 seconds.', 'Deals +10...35 damage. <i>Your non-attack skills are disabled (10 seconds) if this attack hits.</i>', '', '', ''),
(909, 'Focused Shot', 'If Focused Shot hits, you strike for  +10..25 damage but all of your other attack skills are disabled for 5..3 seconds.', 'Deals +10...25 damage. <i>Your other attack skills are disabled (5...3 seconds) if this attack hits.</i>', '', '', ''),
(910, 'Spirit Rift', 'Open a Spirit Rift at target foe\'s location. After 3 seconds, all adjacent foes are struck for 25..125 lightning damage and suffer from Cracked Armor for 1..20 second[s].', 'After 3 seconds, affects foes adjacent to target\'s initial location. Deals 25...125 lightning damage and inflicts Cracked Armor (1...20 seconds).', '', '', ''),
(911, 'Union', 'Create a level 1..12 spirit. Whenever a non-spirit ally in its range takes damage or life steal, it is reduced by 15 and the spirit takes 15 damage. This spirit dies after 30..60 seconds.', 'Creates a level 1...12 spirit (30...60 second lifespan). Whenever a non-spirit ally within range takes damage or life steal, it is reduced by 15 and the spirit takes 15 damage.', 'Union (PvP)', 'Create a level 1..8 spirit. Whenever a non-spirit ally in its range takes damage or life steal, it is reduced by 15 and the spirit takes 15 damage. This spirit dies after 30..60 seconds.', 'Creates a level 1...8 spirit (30...60 second lifespan). Whenever a non-spirit ally within range takes damage or life steal, that damage is reduced by 15 and the spirit takes 15 damage.'),
(913, 'Tranquil Was Tanasen', 'Hold Tanasen\'s ashes for up to 5..20 seconds. While you hold his ashes, you have +10..25 armor and cannot be interrupted.', '(5...20 seconds.) You have +10...25 armor. You cannot be interrupted.', '', '', ''),
(914, 'Consume Soul', 'You steal 5..60 Health from target foe. All hostile summoned creatures in the area of that foe take 25..125 damage.', 'Steals 5...60 Health. Deal 25...125 damage to hostile summoned creatures in the area of target foe.', '', '', ''),
(915, 'Spirit Light', 'Target ally is healed for 60..180. If any spirits are within earshot, you don\'t sacrifice Health.', 'Heals for 60...180. You don\'t sacrifice Health if you are within earshot of any spirits.', '', '', ''),
(916, 'Lamentation', 'For 5..20 seconds, target foe and all nearby foes suffer -0..3 Health degeneration. When this hex is applied, these foes take 10..50 damage if you are within earshot of a spirit or corpse.', 'Also hexes foes near your target (5...20 seconds). Causes -0...3 Health degeneration. Initial effect: Deals 10...50 damage to these foes if you are within earshot of a spirit or corpse.', '', '', ''),
(917, 'Rupture Soul', 'Target allied spirit is destroyed. All nearby enemies are struck for 50..140 lightning damage and become blinded for 3..12 seconds.', 'Destroys target allied spirit. Deals 50...140 lightning damage and inflicts Blindness condition (3...12 seconds) to nearby foes.', '', '', ''),
(918, 'Spirit to Flesh', 'Target touched allied spirit is destroyed. All nearby allies are healed for 30..240.', 'Touch Spell. Destroys target touched allied spirit. Heals nearby allies for 30...240.', '', '', ''),
(919, 'Spirit Burn', 'Target foe is struck for 5..50 lightning damage. If any spirits are within earshot, Spirit Burn causes Burning for 1..5 second[s].', 'Deals 5...50 lightning damage. Inflicts Burning condition (1...5 seconds) if you are within earshot of a spirit.', '', '', ''),
(920, 'Destruction', 'Create a level 1..14 Spirit that dies after 30 seconds. When this Spirit dies, all foes in the area take 5..25 damage for each second the Spirit was alive (maximum 150 damage).', 'Create a level 1...14 Spirit that dies after 30 seconds. When this Spirit dies, all foes in the area take 5...25 damage for each second the Spirit was alive (maximum 150 damage).', 'Destruction (PvP)', 'Create a level 1..10 Spirit  that dies after 30 seconds. When this Spirit dies, all foes in the area take 1..10 damage for each second the Spirit was alive (maximum of 150 damage).', 'Creates a level 1...10 spirit (30 second lifespan). On death, it deals 1...10 damage for each second it was alive to all foes in the area (maximum 150 damage).'),
(921, 'Dissonance', 'Create a level 1..12 spirit. This spirit deals 5..20 damage and anyone struck by its attack is interrupted. This spirit dies after 10..25 seconds.', 'Creates a level 1...12 spirit (10...25 second lifespan). Its attacks deal 5...20 damage and interrupt actions.', 'Dissonance (PvP)', 'Create a level 1..12 spirit. This spirit deals 5..20 damage and anyone struck by its attack is interrupted. This spirit dies after 10..25 seconds.', 'Creates a level 1...8 spirit (10...25 second lifespan). Its attacks deal 5...20 damage and interrupt actions.'),
(923, 'Disenchantment', 'Create a level 1..12 spirit. This spirit deals 5..20 damage and anyone struck by its attack loses one enchantment. This spirit dies after 10..35 seconds.', 'Creates a level 1...12 spirit (10...35 second lifespan). Its attacks deal 5...20 damage and remove one enchantment.', 'Disenchantment (PvP)', 'Create a level 1..8 spirit. This spirit deals 5..20 damage and anyone struck by its attack loses one enchantment. This spirit dies after 10..35 seconds.', 'Creates a level 1...8 spirit (10...35 second lifespan). Its attacks deal 5...20 damage and remove one enchantment.'),
(925, 'Recall', 'While you maintain Recall, nothing happens.  When Recall ends, you Shadow Step to the ally you targeted when you activated this skill and all of your skills are disabled for 10 seconds.', 'End effect: Shadow Step to target ally. <i>Cannot self-target and disables all of your skills for 10 seconds when it ends.</i>', '', '', ''),
(926, 'Sharpen Daggers', 'For 5..30 seconds, your dagger attacks cause Bleeding for 5..15 seconds.', '(5...30 seconds.) Your dagger attacks inflict the Bleeding condition (5...15 seconds).', '', '', ''),
(927, 'Shameful Fear', ' For 10 seconds, target foe moves 10% faster than normal. For each second, if that foe is moving, that foe takes 5..20 damage.', '(10 seconds.) Target foe takes 5...20 damage each second while moving, but moves 10% faster.', '', '', ''),
(928, 'Shadow Shroud', 'For 3..9 seconds, target foe cannot be the target of enchantments.', '(3...9 seconds.) Target foe cannot be the target of enchantments.', '', '', ''),
(929, 'Shadow of Haste', 'For 10..40 seconds you move 15% faster than normal. When Shadow of Haste ends, you shadow step to your original location.', '(10...40 seconds.) You move 15% faster. End effect: return to your original location.', '', '', ''),
(930, 'Auspicious Incantation', 'For 20 seconds, the next spell you cast is disabled for an additional 10..5 seconds, and you gain 110..200% of that spell\'s Energy cost.', '(20 seconds.) Your next spell gives you 110...200% of its Energy cost. <i>That spell takes 10...5 seconds longer to recharge.</i>', '', '', ''),
(931, 'Power Return', 'If target foe is casting a spell or chant, that skill is interrupted and target foe gains 10..5 Energy.', 'Interrupts a spell or chant. Interruption effect: target foe gains 10...5 Energy.', '', '', ''),
(932, 'Complicate', 'If target foe is using a skill, that skill is interrupted and disabled for target foe and all foes in the area for an additional 5..12 seconds.', 'Interrupt a skill. Interruption effect: disables interrupted skill (+5...12 seconds) for target foe and all foes in the area.', '', '', ''),
(933, 'Shatter Storm', 'Target foe loses all enchantments. For each enchantment removed this way, Shatter Storm is disabled for an additional 7 seconds.', 'Removes all enchantments. <i>Removal cost: Shatter Storm is disabled for +7 seconds for each enchantment removed.</i>', '', '', ''),
(934, 'Unnatural Signet', 'Target foe takes 15..75 damage. If that foe is under the effects of a hex or enchantment, foes adjacent to your target take 5..50 damage.', 'Deals 15...75 damage. Deals 5...50 damage to other adjacent foes if the target is hexed or enchanted.', 'Unnatural Signet (PvP)', 'Target foe takes 10..60 damage. If that foe is under the effects of a hex or enchantment, foes adjacent to your target take 10..60 damage. All your non-Mesmer skills except signets are disabled for 10 seconds.', 'Deals 10...60 damage. Deals 10...60 damage to other adjacent foes if the target is hexed or enchanted. <i>Your non-Mesmer skills except signets are disabled (10 seconds).</i>'),
(935, 'Rising Bile', 'For 20 seconds, this hex does nothing.  When Rising Bile ends, that foe and all foes in the area take 1..6 damage for each second Rising Bile was in effect.', '(20 seconds.) End effect: deals 1...6 damage for each second Rising Bile was in effect. Also damages other foes in the area.', '', '', ''),
(936, 'Envenom Enchantments', 'Target foe loses one enchantment. For every remaining enchantment, target foe is poisoned for 3..10 seconds.', 'Removes one enchantment from target foe. Inflicts Poisoned condition (3...10 seconds for each remaining enchantment on that foe).', '', '', ''),
(937, 'Shockwave', 'All foes in the area take 15..60 earth damage and are Weakened for 1..10 second[s].  Nearby foes also take +15..60 earth damage and have Cracked Armor for 1..10 second[s].  Adjacent foes suffer the previous effects, take +15..60 earth damage, and are Blinded for 1..10 second[s].', 'Foes in the area take 15...60 earth damage and are Weakened (1...10 seconds). Nearby foes also take +15...60 earth damage and have Cracked Armor (1...10 seconds). Adjacent foes also take +15...60 earth damage and are Blinded (1...10 seconds).', '', '', ''),
(938, 'Ward of Stability', 'Create a Ward of Stability at your current location. For 10..25 seconds, non-spirit allies cannot be knocked down.', '(10...25 seconds.) Allies in this ward cannot be knocked-down. <i>Allied spirits are not affected.</i>', '', '', ''),
(939, 'Icy Shackles', 'For 1..10 second[s], target foe\'s movement speed is reduced by 66%. While under the effects of an enchantment, that foe\'s movement is reduced by 90%.', '(1...10 seconds.) Target foe moves 66% slower. This foe moves 90% slower if enchanted.', '', '', ''),
(941, 'Blessed Light', 'Heal target ally for 10..140 Health and remove one condition and one hex.', 'Heals for 10...140. Removes one condition and one hex.', '', '', ''),
(942, 'Withdraw Hexes', 'Remove all hexes from target ally and all adjacent allies. This spell takes an additional 20..5 seconds to recharge for each hex removed in this way.', 'Removes all hexes. Also affects adjacent allies. <i>Removal cost: +20...5 seconds recharge for each hex removed.</i>', '', '', ''),
(943, 'Extinguish', 'Remove one condition from each party member. Party members relieved of Burning are healed for 10..100 Health.', 'Affects all party members. Removes one condition. Party members relieved of Burning are healed for 10...100.', '', '', ''),
(944, 'Signet of Strength', 'Your next 1..16 attack[s] deal +5 damage.', 'Your attacks deal +5 damage. <i>Ends after 1...16 attacks.</i>', '', '', ''),
(946, 'Trapper\'s Focus', 'For 12..24 seconds, your trap skills are not easily interruptible and your Wilderness Survival attribute is increased by +0..2.', '(12...24 seconds.) Your trap skills are no longer easy to interrupt. You gain +0...2 to your Wilderness Survival attribute.', '', '', ''),
(947, 'Brambles', 'Create a level 1..10 Spirit. Non-Spirit creatures that are knocked down in its range take 5 damage and begin Bleeding for 5..20 seconds. This Spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Knocked-down creatures take 5 damage and begin Bleeding (5...20 seconds). Does not affect spirits.', '', '', ''),
(948, 'Desperate Strike', 'If you have less than 50..80% Health, you deal +15..60 damage.', 'Deals +15...60 damage if you have less than 50...80% Health.', '', '', ''),
(949, 'Way of the Fox', ' For 10..35 seconds, your next 1..6 attack[s] cannot be blocked.', '(10...35 seconds.) Your attacks are unblockable. <i>Ends after 1...6 attacks.</i>', '', '', ''),
(950, 'Shadowy Burden', ' For 3..15 seconds, target foe moves 25% slower and while target foe has no other hexes, that foe has 20 less armor against your attacks.', '(3...15 seconds.) Target foe moves 25% slower and has 20 less armor against your attacks. <i>Armor reduction only affects this foe while it has no other hexes.</i>', '', '', ''),
(951, 'Siphon Speed', 'For 5..15 seconds, target foe moves 33% slower and you move 33% faster. This spell has half the normal range. This spell recharges 50% faster if cast on a moving foe.', 'Half Range Hex Spell. (5...15 seconds.) Target foe moves 33% slower and you move 33% faster. Recharges 50% faster if cast on a moving foe.', '', '', ''),
(952, 'Death\'s Charge', 'Shadow Step to target foe. If that foe has more Health than you, you are healed for 65..200.', 'You Shadow Step to target foe. You are healed for 65...200 if this foe has more Health than you.', '', '', ''),
(953, 'Power Flux', 'If target foe is casting a spell or chant, that skill is interrupted and for 4..10 seconds, that foe has -2 Energy degeneration.', 'Interrupts a spell or chant. Interruption effect: -2 Energy degeneration (4...10 seconds).', '', '', ''),
(954, 'Expel Hexes', 'Remove up to 2 Hexes from target ally.', 'Removes 2 hexes from target ally.', '', '', ''),
(955, 'Rip Enchantment', 'Remove 1 enchantment from target foe. If an enchantment was removed, that foe suffers from Bleeding for 5..25 seconds.', 'Removes 1 enchantment. Removal effect: inflicts Bleeding (5...25 seconds).', '', '', ''),
(957, 'Spell Shield', 'For 5..20 seconds, while you are casting spells, foes cannot target you with spells. When Spell Shield ends, all your skills are disabled for 10..5 seconds.', '(5...20 seconds.) While casting spells, you cannot be the target of spells. <i>End effect: your skills are disabled (10...5 seconds).</i>', '', '', ''),
(958, 'Healing Whisper', 'Target other ally is healed for 40..100. This spell has half the normal range.', 'Half Range Spell. Heals for 40...100. <i>Cannot self-target.</i>', '', '', ''),
(959, 'Ethereal Light', 'Target ally is healed for 25..100. This spell is easily interrupted.', 'Heals for 25...100. <i>Easily interrupted.</i>', '', '', ''),
(960, 'Release Enchantments', 'Lose all enchantments. Each party member is healed for 5..35 Health for each Monk enchantment lost.', 'Removes all of your enchantments. Heals all party members for 5...35 for each Monk Enchantment removed.', '', '', ''),
(961, 'Lacerate', 'Create a level 1..10 spirit. Bleeding creatures within its range suffer -2 Health degeneration. When this spirit dies, all non-spirit creatures within its range that have less than 90% Health begin Bleeding for 5..25 seconds. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Bleeding creatures in range have -2 Health degeneration. End effect: Inflicts Bleeding condition (5...25 seconds) on creatures in range that have less than 90% health. Does not affect spirits.', '', '', ''),
(962, 'Spirit Transfer', 'The spirit nearest you loses 5..50 Health. Target ally is healed for 5 for each point of Health lost.', 'The spirit nearest you loses 5...50 Health. Heals target ally for 5 for each point of Health lost.', '', '', ''),
(963, 'Restoration', 'Create a level 1..14 spirit. When this spirit dies, all party members in the area are resurrected with 5..50% Health and zero Energy. This spirit dies after 30 seconds.', 'Creates a level 1...14 spirit (30 second lifespan). End effect: resurrects party members in the area with 5...50% Health and zero Energy.', 'Restoration (PvP)', 'Create a level 1..10 spirit. When this spirit dies, all party members in the area are resurrected with 5..50% Health and zero Energy. This spirit dies after 30 seconds.', 'Creates a level 1...10 spirit (30 second lifespan). End effect: resurrects party members in the area with 5...50% Health and zero Energy.'),
(964, 'Vengeful Weapon', 'For 8 seconds, the next time target ally takes damage or life steal from a foe, that ally steals up to 15..60 Health from that foe.', '(8 seconds.) Steals 15...60 Health from the next foe that deals damage or life steal to target ally.', '', '', ''),
(973, 'Blinding Powder', 'Must follow an off-hand attack. Target foe and all adjacent foes become Blinded for 3..15 seconds.', 'Inflicts Blindness condition (3...15 seconds) on target and adjacent foes. <i>Must follow an off-hand attack.</i>', '', '', ''),
(974, 'Mantis Touch', 'Must follow a lead attack. Target foe becomes Crippled for 5..20 seconds. This skill counts as an off-hand attack.', 'Inflicts Crippled condition (5...20 seconds). This skill counts as an off-hand attack. <i>Must follow a lead attack.</i>', '', '', ''),
(975, 'Exhausting Assault', 'Must follow a lead attack. Target foe\'s action is interrupted. If that action was casting a spell, target foe suffers 10 Overcast.', 'Interrupts an action. Inflicts 10 Overcast if the interrupted action was a spell. <i>Must follow a lead attack.</i>', '', '', ''),
(976, 'Repeating Strike', 'Must follow an off-hand attack. If it hits, this attack strikes for +10..30 damage. If it misses, it takes an additional 15 seconds to recharge.', 'Deals +10...30 damage. <i>This skill has +15 second recharge time if it misses. Must follow an off-hand attack.</i>', '', '', ''),
(977, 'Way of the Lotus', ' For 20 seconds, the next time you hit with a dual attack skill, you gain 5..20 Energy.', '(20 seconds.) You gain 5...20 Energy the next time you hit with a dual attack.', '', '', ''),
(978, 'Mark of Instability', 'For 20 seconds, the next time you hit target foe with a dual attack skill that foe is knocked down.', '(20 seconds.) Causes knock-down the next time you hit target foe with a dual attack.', '', '', ''),
(979, 'Mistrust', 'For 6 seconds, the next spell that target foe casts on one of your allies fails and deals 10..100 damage to that foe and all nearby foes.', '(6 seconds.) The next spell that target foe casts on one of your allies fails and deals 10...100 damage to target and nearby foes.', 'Mistrust (PvP)', 'For 6 seconds, the next spell that target foe casts on one of your allies fails and deals 10..75 damage to that foe and all nearby foes.', '(6 seconds.) The next spell that target foe casts on one of your allies fails and deals 10...75 damage to target and nearby foes.'),
(980, 'Feast of Souls', 'Destroy all nearby allies\' spirits. For each spirit destroyed in this way, all party members are healed for 50..100 Health.', 'Heals all party members for 50...100 for each nearby allied spirit. <i>All nearby allied spirits are destroyed.</i>', '', '', ''),
(981, 'Recuperation', 'Create a level 1..14 spirit. Non-spirit allies within its range gain +1..3 Health regeneration. This spirit dies after 15..45 seconds.', 'Creates a level 1...14 spirit (15...45 second lifespan). Non-spirit allies within range have +1...3 Health regeneration.', 'Recuperation (PvP)', 'Create a level 1..10 spirit. Non-spirit allies within its range gain +1..3 Health regeneration. This spirit dies after 15..45 seconds.', 'Creates a level 1...10 spirit (15...45 second lifespan). Non-spirit allies within range have +1...3 Health regeneration.'),
(982, 'Shelter', 'Create a level 1..12 spirit. Non-spirit allies within its range cannot lose more than 10% maximum Health from a single attack. When this spirit prevents damage, it loses 75..45 Health. This spirit lasts 30..60 seconds.', 'Creates a level 1...12 spirit (30...60 second lifespan). Non-spirit allies within range cannot lose more than 10% maximum Health from a single attack. <i>Damage prevention cost: this spirit loses 75...45 Health.</i>', 'Shelter (PvP)', 'Create a level 1..8 spirit. Non-spirit allies within its range cannot lose more than 10% maximum Health from a single attack. When this spirit prevents damage, it loses 75..45 Health. This spirit lasts 30..60 seconds.', 'Creates a level 1...8 spirit (30...60 second lifespan). Non-spirit allies within range cannot lose more than 10% maximum Health from a single attack. <i>Damage prevention cost: this spirit loses 75...45 Health.</i>'),
(983, 'Weapon of Shadow', 'For 1..7 second[s], target ally has a Weapon of Shadow. Whenever that ally is struck by an attack, that ally\'s attacker becomes Blinded for 5 seconds. The next 1..3 times  that ally hits with an attack, his target is Blinded for 5 seconds.', '(1...7 seconds.) Inflicts Blindness condition (5 seconds) on anyone who attacks target ally. Target ally\'s next 1...3 attacks inflict Blindness (5 seconds) if they hit.', '', '', ''),
(985, 'Caltrops', 'Target foe and all foes adjacent to your target are Crippled for 5..15 seconds. Caltrops has half the normal range.', 'Half Range Spell. Inflicts Crippled condition (5...15 seconds) on target and adjacent foes.', '', '', ''),
(986, 'Nine Tail Strike', 'Must follow an off-hand attack. Nine Tail Strike cannot be blocked and strikes for +15..40 damage if it hits.', 'Deals +15...40 damage. Unblockable. <i>Must follow an off-hand attack.</i>', '', '', ''),
(987, 'Way of the Empty Palm', 'For 5..20 seconds, off-hand and dual attacks cost no Energy.', '(5...20 seconds.) Your off-hand and dual attacks cost no Energy.', '', '', ''),
(988, 'Temple Strike', 'Must follow a lead attack. If this attack hits, target foe is Dazed and Blinded for 1..10 seconds, and if target foe is casting a spell, that foe is interrupted.', 'Interrupts a spell. Inflicts Dazed and Blindness conditions (1...10 seconds). <i>Must follow a lead attack.</i>', '', '', ''),
(989, 'Golden Phoenix Strike', 'If you are not under the effects of an enchantment, this skill misses. If it hits, Golden Phoenix Strike deals +10..30 damage and all adjacent foes take 10..30 damage.', 'Deals +10...30 damage to target and deals 10...30 damage to adjacent foes. <i>Fails if you are not enchanted.</i>', '', '', ''),
(990, 'Expunge Enchantments', 'Target foe loses 1 enchantment. All of your other non-attack skills are disabled for 10..5 seconds. For each skill disabled in this way, target touched foe loses 1 additional enchantment.', 'Touch Skill. Removes one enchantment for each non-attack skill you have. <i>All your non-attack skills are disabled (10...5 seconds).</i>', '', '', ''),
(991, 'Deny Hexes', 'Remove one hex from target ally and one additional hex for each recharging Divine Favor skill you have.', 'Removes one hex from target ally and one additional hex for each recharging Divine Favor skill you have.', '', '', ''),
(992, 'Triple Chop', 'Attack target foe and adjacent foes. Each attack that hits deals +10..40 damage.', 'Deals +10...40 damage. Also hits adjacent foes.', '', '', ''),
(993, 'Enraged Smash', 'If Enraged Smash hits, you gain 1..4 strike[s] of adrenaline. If you hit a moving foe, you strike for +10..40 damage, and target foe is knocked down.', 'Gives you 1...4 strikes of adrenaline if you hit. Deals +10...40 damage and causes knockdown if target foe was moving.', 'Enraged Smash (PvP)', 'If Enraged Smash hits, you gain 1..4 strike[s] of adrenaline. If you hit a moving foe, you strike for +10..40 damage, and target foe is knocked down.', 'Gives you 1...4 strikes of adrenaline if you hit. Deals +10...40 damage and causes knockdown if target foe was moving.'),
(994, 'Renewing Smash', ' If Renewing Smash hits, it deals +10..40 damage.  If you hit a knocked-down foe, you gain 3 Energy and this attack recharges instantly.', 'Deals +10...40 damage. You gain 3 Energy and this attack recharges instantly if target foe was knocked down.', 'Renewing Smash (PvP)', ' If Renewing Smash hits, it deals +5..20 damage. If it hits a knocked-down foe, you gain 3 Energy and this attack recharges instantly.', 'Deals +5...20 damage. You gain 3 energy and this attack recharges instantly if target foe was knocked down.'),
(995, 'Tiger Stance', ' For 4..10 seconds, you attack 33% faster. Tiger Stance ends if any of your attacks fail to hit.', '(4...10 seconds.) You attack 33% faster. <i>Ends if you fail to hit.</i>', '', '', ''),
(996, 'Standing Slash', 'If it hits, Standing Slash deals +5..20 damage plus an additional 5..20 damage if you are in a stance.', 'Deals +5...20 damage. Deals 5...20 more damage if you are in a stance.', '', '', ''),
(997, 'Famine', 'Create a level 1..10 spirit. Whenever a non-spirit creature in its range reaches 0 Energy, that creature takes 10..35 damage. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit (30...90 lifespan). Deals 10...35 damage to creatures in range that reach 0 energy. Does not affect spirits.', '', '', ''),
(1018, 'Critical Eye', 'For 10..35 seconds, you have an additional 3..15% chance to land a critical hit when attacking. You gain 1 Energy whenever you score a critical hit.', '(10...35 seconds.) You have +3...15% chance to land a critical hit. You gain 1 Energy whenever you land a critical hit.', '', '', ''),
(1019, 'Critical Strike', 'Must follow an off-hand attack. If it hits, this attack strikes for +10..30 damage, results in a critical hit, and you gain 1..3 Energy.', 'Deals +10...30 damage. Automatic critical hit. You gain 1...3 Energy. <i>Must follow an off-hand attack.</i>', '', '', ''),
(1020, 'Blades of Steel', 'Must follow an off-hand attack. If it hits, this attack strikes for +5..16 damage (maximum bonus 60) for each recharging dagger attack.', 'Deals +5...16 damage (maximum 60) for each recharging dagger attack. <i>Must follow an off-hand attack.</i>', '', '', ''),
(1021, 'Jungle Strike', 'Must follow a lead attack. If it hits, this attack strikes for +10..25 damage. If it hits a foe that was Crippled, that foe and all adjacent foes take +1..31 damage.', 'Deals +10...25 damage. Deals +1...31 damage to target and adjacent foes if target foe is Crippled. <i>Must follow a lead attack.</i>', '', '', ''),
(1022, 'Wild Strike', 'Must follow a lead attack. If it hits, this attack strikes for +10..35 damage and any stance being used by target foe ends. This attack cannot be blocked.', 'Deals +10...35 damage. Removes target foe\'s stance. Unblockable. <i>Must follow a lead attack.</i>', 'Wild Strike (PvP)', 'Must follow a lead attack. If it hits, this attack strikes for +10..35 damage and any stance being used by target foe ends. This attack cannot be blocked.', 'Deals +10...35 damage. Removes target foe\'s stance. Unblockable. <i>Must follow a lead attack.</i>'),
(1023, 'Leaping Mantis Sting', 'If Mantis Sting hits, target foe takes +5..15 damage. If this attack strikes a moving foe, that foe is Crippled for 3..15 seconds.', 'Deals +5...15 damage. Inflicts Crippled condition (3...15 seconds) if target foe is moving.', '', '', ''),
(1024, 'Black Mantis Thrust', 'If this attack hits, you strike for +8..20 damage. If target foe is suffering from a Hex, that foe is Crippled for 3..15 seconds.', 'Deals +8...20 damage. Inflicts Crippled condition (3...15 seconds) if target foe is hexed.', '', '', ''),
(1025, 'Disrupting Stab', 'If this attack hits, it interrupts target foe\'s action. If that action was a spell, it is disabled for 3..10 seconds.', 'Interrupts an action. If the interrupted action was a spell, it is disabled (3...10 seconds).', '', '', ''),
(1026, 'Golden Lotus Strike', 'If it hits, this attack strikes for +5..20 damage. If you are under the effects of an Enchantment, you gain 5..8 Energy.', 'Deals +5...20 damage. You gain 5...8 Energy if you are enchanted.', '', '', ''),
(1027, 'Critical Defenses', 'For 4..10 seconds, you have a 75% chance to block. Critical Defenses refreshes every time you land a critical hit.', '(4...10 seconds.) You have a 75% chance to block. Renewal: every time you land a critical hit.', '', '', ''),
(1028, 'Way of Perfection', ' For 60 seconds, whenever you successfully land a critical hit, you gain 10..40 Health.', '(60 seconds.) Your critical hits heal you for 10...40.', '', '', ''),
(1029, 'Dark Apostasy', 'For 3..17 seconds, every time you successfully land a critical hit, you remove one enchantment from your target. If you remove an enchantment in this way, you lose 10..4 Energy or Dark Apostasy ends.', '(3...17 seconds.) Your critical hits remove an enchantment. <i>Removal cost: lose 10...4 Energy or Dark Apostasy ends.</i>', '', '', ''),
(1030, 'Locust\'s Fury', 'For 10..35 seconds, you have an additional 50% chance to double strike while using daggers.', '(10...35 seconds.) You have +50% chance to double strike. <i>No effect unless you are wielding daggers.</i>', '', '', ''),
(1031, 'Shroud of Distress', ' For 30..60 seconds, if you are below 50% Health, you have 3..8 health regeneration and a 75% chance to block attacks.', '(30...60 seconds.) You have 3...8 health regeneration and a 75% chance to block. <i>No effect unless your Health is below 50%.</i>', 'Shroud of Distress (PvP)', ' For 30..60 seconds, if you are below 50% Health, you have a 75% chance to block attacks.', '(30...60 seconds.) You have 75% chance to block. No effect unless your Health is below 50%.'),
(1032, 'Heart of Shadow', 'You are healed for 30..150. Shadow Step to a nearby location directly away from your target.', 'You are healed for 30...150 and you Shadow Step to a nearby location directly away from your target.', '', '', ''),
(1033, 'Impale', 'Must follow a dual attack. Target foe is struck for 25..100 earth damage and suffers from a Deep Wound for 5..20 seconds.', 'Deals 25...100 earth damage. Inflicts Deep Wound condition (5...20 seconds). <i>Must follow a dual attack.</i>', '', '', ''),
(1034, 'Seeping Wound', 'For 1..7 second[s], target foe moves 33% slower. That foe takes 5..25 damage each second while suffering from a condition. This spell has half the normal range.', 'Elite Half Range Hex Spell. (1...7 seconds.) Target foe moves 33% slower. This foe takes 5...25 damage each second while suffering from a condition.', '', '', ''),
(1035, 'Assassin\'s Promise', 'For 5..15 seconds, if target foe dies, you gain 5..20 Energy and all your skills are recharged.', '(5...15 seconds.) You gain 5...20 Energy and all your skills recharge if target foe dies.', '', '', ''),
(1036, 'Signet of Malice', 'For each condition suffered by target foe, you lose one condition.', 'You lose one condition for each condition on target foe.', '', '', ''),
(1037, 'Dark Escape', 'For 5..15 seconds, you move 25% faster and take half damage. Dark Escape ends if you successfully hit with an attack.', '(5...15 seconds.) You move 25% faster and take half damage. <i>Ends if you hit with an attack.</i>', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1038, 'Crippling Dagger', 'Send out a Crippling Dagger at target foe. Crippling Dagger strikes for 15..60 earth damage if it hits, and Cripples moving foes for 3..15 seconds. This spell has half the normal range.', 'Half Range Spell. Projectile: deals 15...60 earth damage. Inflicts Crippled condition (3...15 seconds) if target foe is moving.', '', '', ''),
(1040, 'Spirit Walk', 'Shadow Step to target spirit.', 'Shadow Step to target Spirit. <sub>sic</sub>', '', '', ''),
(1041, 'Unseen Fury', 'All adjacent foes are Blinded for 3..10 seconds. For 10..30 seconds, you cannot be blocked by Blinded foes.', 'Inflicts Blindness condition on adjacent foes (3...10). You cannot be blocked by Blinded foes for 10...30 seconds.', 'Unseen Fury (PvP)', 'For 15..60 seconds, you cannot be blocked by Blinded foes.', '(15...60 seconds.) You cannot be blocked by Blinded foes'),
(1042, 'Flashing Blades', 'For 5..30 seconds, you have a 75% chance to block incoming attacks while attacking. If you block an attack in this way, your attacker takes 5..20 damage.', '(5...30 seconds.) You have 75% chance to block while attacking. Block effect: 5...20 damage to your attacker.', '', '', ''),
(1043, 'Dash', 'For 3 seconds, you run 50% faster.', '(3 seconds.) You move 50% faster.', '', '', ''),
(1044, 'Dark Prison', 'Shadow Step to target foe. For 1..6 seconds, that foe moves 33% slower.', 'Shadow Step to target foe. This foe moves 33% slower (1...6 seconds).', '', '', ''),
(1045, 'Palm Strike', 'Target touched foe takes 10..65 damage and is Crippled for 1..5 second[s]. This skill counts as an off-hand attack.', 'Elite Touch Skill. Deals 10...65 damage and inflicts Crippled condition (1...5 seconds). This skill counts as an off-hand attack.', '', '', ''),
(1048, 'Revealed Enchantment', 'Remove an enchantment from target foe and gain 3..15 Energy. For 20 seconds, Revealed Enchantment is replaced with the enchantment removed from target foe.', 'Removes an enchantment from target foe. Removal effects: you gain 3...15 Energy; this spell is replaced with that enchantment (20 seconds).', '', '', ''),
(1049, 'Revealed Hex', 'Remove a hex from target ally and gain 4..10 Energy. For 20 seconds, Revealed Hex is replaced with the hex that was removed.', 'Removes a hex from target ally. Removal effects: you gain 4...10 Energy; this spell is replaced with that hex (20 seconds).', '', '', ''),
(1052, 'Accumulated Pain', 'Target foe takes  15..75 damage. If target foe is suffering from 2 or more hexes, that foe suffers a Deep Wound for 5..20 seconds.', 'Deals 15...75 damage. Inflicts Deep Wound condition (5...20 seconds) if target foe has 2 or more hexes.', 'Accumulated Pain (PvP)', 'Target foe takes  10..35 damage. If target foe is suffering from 2 or more hexes, that foe suffers a Deep Wound for 5..20 seconds.', 'Deals 10...35 damage. Inflicts Deep Wound condition (5...20 seconds) if target foe has 2 or more hexes.'),
(1053, 'Psychic Distraction', 'All of your other skills are disabled for 8 seconds. If target foe is using a skill, that skill is interrupted and disabled for an additional 5..12 seconds.', 'Interrupts a skill. Interruption effect: disables interrupted skill (+5...12 seconds). <i>Your other skills are disabled (8 seconds).</i>', '', '', ''),
(1054, 'Ancestor\'s Visage', 'For 4..10 seconds, whenever target ally is hit by a melee attack, all adjacent foes lose all adrenaline and 3 Energy.', '(4...10 seconds.) All adjacent foes lose all adrenaline and 3 Energy whenever a melee attack hits target ally.', '', '', ''),
(1055, 'Recurring Insecurity', 'For 10 seconds, target foe suffers from -1..5 Health degeneration. If that foe has another hex when Recurring Insecurity would end, it is reapplied.', '(10 seconds.) Causes -1...5 Health degeneration. Renewal: if target foe has another hex when Recurring Insecurity would end.', '', '', ''),
(1056, 'Kitah\'s Burden', 'For 10 seconds, target foe moves 50% slower. When Kitah\'s Burden ends you gain 10..18 Energy.', '(10 seconds.) Target foe moves 50% slower. End effect: you gain 10...18 Energy.', '', '', ''),
(1057, 'Psychic Instability', 'Interrupt the target foe\'s action. If that action was a skill, that foe and nearby foes are knocked down for 2..4 seconds. (50% failure chance with Fast Casting 4 or less.)', 'Interrupts an action. Interruption effect: if the action is a skill, cause knockdown for 2...4 seconds on target foe and all nearby foes. <i>50% failure chance unless Fast Casting 5 or higher.</i>', 'Psychic Instability (PvP)', 'Interrupts the target foe\'s action. If that action is a skill, that foe and all adjacent foes are knocked down for 2..3 seconds. (50% failure chance with Fast Casting 4 or less.)', 'Interrupts an action. Interruption effect: if the action is a skill, causes knockdown for 2...3 seconds on target foe and all adjacent foes. <i>50% failure chance unless Fast Casting 5 or higher.</i>'),
(1059, 'Hex Eater Signet', 'Target touched ally and up to 2..5 adjacent allies each lose one hex. You gain 1..4 Energy for each hex removed this way.', 'Touch Signet. Removes a hex from target and 2...5 adjacent allies. Removal effect: you gain 1...4 Energy for each hex removed.', '', '', ''),
(1061, 'Feedback', 'Target foe loses one enchantment. If an enchantment is removed in this way, that foe also loses 4..10 Energy.', 'Removes one enchantment. Removal effect: target foe loses 4...10 Energy.', '', '', ''),
(1062, 'Arcane Larceny', 'For 5..35 seconds, one random spell is disabled for target foe and Arcane Larceny is replaced by that spell.', '(5...35 seconds.) Disables one random spell. This skill becomes that spell.', '', '', ''),
(1066, 'Spoil Victor', 'For 3..15 seconds, whenever target foe attacks or casts a spell on a creature with less Health, that foe loses 25..100 Health.', '(3...15 seconds.) Causes 25...100 Health loss whenever target foe attacks or casts spells on a creature with less Health.', 'Spoil Victor (PvP)', 'For 3..15 seconds, whenever target foe attacks or casts a spell on a creature with less Health that foe loses 15..75 Health.', '(3...15 seconds.) Causes 15...75 Health loss whenever target foe attacks or casts spells on a creature with less Health.'),
(1067, 'Lifebane Strike', 'Target foe takes 12..48 shadow damage. If that foe\'s Health is above 50%, you steal up to 12..48 Health.', 'Deals 12...48 damage. Steals 12...48 Health if target foe\'s Health is above 50%.', '', '', ''),
(1068, 'Bitter Chill', 'Target foe is struck for 15..60 cold damage.  If that foe had more Health than you, Bitter Chill recharges instantly.', 'Deals 15...60 cold damage. Recharges instantly if target foe had more Health than you.', '', '', ''),
(1069, 'Taste of Pain', 'If target foe is below 50% Health, you gain 30..150 Health.', 'Heals you for 30...150. <i>No effect unless target foe is below 50% Health.</i>', '', '', ''),
(1070, 'Defile Enchantments', 'Target foe and all nearby foes take 6..60 shadow damage and 4..20 shadow damage for each enchantment on them.', 'Deals 6...60 damage to target and nearby foes. Deals 4...20 more damage for each enchantment on them.', '', '', ''),
(1071, 'Shivers of Dread', 'For 10..40 seconds, whenever target foe is struck for cold damage while using a skill, that foe is interrupted and you lose 10..5 Energy or Shivers of Dread ends.', '(10...40 seconds.) Cold damage interrupts target foe\'s actions. <i>Interruption cost: you lose 10...5 Energy or this hex ends.</i>', '', '', ''),
(1075, 'Vampiric Swarm', 'Vampiric Swarm steals up to 15..60 Health from up to three foes in the area.', 'Steals 15...60 Health. Hits 2 additional foes in the area.', '', '', ''),
(1076, 'Blood Drinker', 'If your Health is above 50%, you begin Bleeding for 10 seconds. Steal up to 20..65 Health from target foe.', 'Steals 20...65 Health. <i>You begin Bleeding (10 seconds) if your Health is above 50%.</i>', '', '', ''),
(1077, 'Vampiric Bite', 'Touch target foe to steal up to 29..74 Health.', 'Touch Skill. Steals 29...74 Health.', '', '', ''),
(1078, 'Wallow\'s Bite', 'Target touched foe takes 20..58 damage. ', 'Touch Skill. Deals 20...58 damage. <sub>sic</sub>', '', '', ''),
(1079, 'Enfeebling Touch', 'Target touched foe loses 5..50 Health and suffers from Weakness for 5..20 seconds.', 'Touch Skill. Causes 5...50 Health loss. Inflicts Weakness condition (5...20 seconds).', '', '', ''),
(1081, 'Teinai\'s Wind', 'Target foe and all adjacent foes take 10..40 cold damage. Burning foes struck by Teinai\'s Wind take an additional 40..80 damage and are interrupted.', 'Deals 10...40 cold damage. Burning foes take +40...80 damage and are interrupted. Also strikes adjacent.', '', '', ''),
(1082, 'Shock Arrow', 'Send out a shocking arrow that flies swiftly toward target foe, striking for 5..50 lightning damage. If Shock Arrow strikes a foe suffering from Cracked Armor, you gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage. Shock Arrow has 25% armor penetration.', 'Rapid projectile: deals 5...50 lightning damage. Gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage if you hit a foe suffering from Cracked Armor. 25% armor penetration.', '', '', ''),
(1083, 'Unsteady Ground', 'You create Unsteady Ground at target foe\'s location. For 5 seconds, nearby foes take 10..40 earth damage each second. Attacking foes struck by Unsteady Ground are knocked down.', 'Deals 10...40 earth damage each second (5 seconds) and causes knock-down to attacking foes. Hits foes near target\'s initial location.', '', '', ''),
(1084, 'Sliver Armor', 'For 5..11 seconds, you have 25..50% chance to block attacks and whenever you are the target of a hostile spell or attack one nearby foe is struck for 5..35 earth damage.', '(5...11 seconds.) You have 25...50% chance to block. Deals 5...35 earth damage to one nearby foe whenever you are the target of a hostile spell or attack.', '', '', ''),
(1085, 'Ash Blast', 'Target and adjacent foes are struck for 35..65 earth damage. Burning foes struck by Ash Blast are hexed for 5 seconds and have a 20..75% chance to miss with attacks.', 'Deals 35...65 earth damage. Burning foes are hexed for 5 seconds and miss 20...75% of attacks. Also strikes adjacent.', '', '', ''),
(1086, 'Dragon\'s Stomp', 'You invoke a Dragon\'s Stomp at target foe\'s location. All foes near this location are knocked down and are struck for 26..100 earth damage.', 'Deals 26...100 earth damage. Causes knock-down. Also hits foes near this foe.', '', '', ''),
(1088, 'Second Wind', 'You gain 1 Energy and 5 Health for each point of Energy restricted by Overcast. You lose all enchantments.', 'You gain 1 Energy and 5 Health for each point of Overcast. You lose all enchantments.', '', '', ''),
(1090, 'Smoldering Embers', 'Target foe is struck for 10..70 fire damage. If you are Overcast, that foe is hexed with Smoldering Embers for 3 seconds and takes 5..25 additional fire damage each second.', 'Deals 10...70 fire damage to target. If you are Overcast, foe is hexed for 3 seconds, taking 5...25 fire damage each second.', '', '', ''),
(1091, 'Double Dragon', 'Invoke the power of the Dragon. For 8 seconds, you and target ally are enchanted with Double Dragon. Adjacent foes are dealt 5..30 fire damage each second. Additionally, when you or your ally use skills that target a foe, that foe is set on fire for 0..3 second[s].', '(8 seconds.) Enchants you and target ally. Adjacent foes take 5...30 fire damage each second. Skills that target a foe also inflict Burning (0...3 seconds).', '', '', ''),
(1093, 'Teinai\'s Heat', 'Place a ward of Teinai\'s Heat at your location for 10..15 seconds. Foes within the ward suffer 2..5 health  degeneration. Weakened foes attack 33% slower. This skill is disabled for 20 seconds.', '(10...15 seconds.) Causes -2...5 health <sub>sic</sub> degeneration. Weakened foes in the ward attack 33% slower. Disabled for 20 seconds.', '', '', ''),
(1094, 'Breath of Fire', 'Create Breath of Fire at target foe\'s current location.  For 5 seconds, foes adjacent to that location are struck for 10..40 fire damage each second.', 'Deals 10...40 fire damage each second (5 seconds). Hits foes adjacent to target\'s initial location.', '', '', ''),
(1095, 'Star Burst', 'Target touched foe and all foes in the area are struck for 7..112 fire damage and set on fire for 1..4 second[s]. For each foe you hit, gain 2 Energy.', 'Elite Touch Spell. Deals 7...112 fire damage. Inflicts Burning (1...4 seconds). <sub>sic</sub> Gain 2 Energy for each foe struck. Also hits foes in the area.', '', '', ''),
(1096, 'Glyph of Essence', ' For 15 seconds, your next spell casts instantly but causes you to lose all Energy.', '(15 seconds.) Your next spell casts instantly. <i>You lose all Energy.</i>', '', '', ''),
(1097, 'Teinai\'s Prison', ' For 1..6 second[s], target foe\'s legs are encased in ice, causing the foe to move 66% slower. If that foe has Cracked Armor, the chill seeps through causing 5..9 Health degeneration.', '(1...6 seconds.) Target foe moves 66% slower. Foes with Cracked Armor have 5...9 Health degeneration.', '', '', ''),
(1098, 'Mirror of Ice', 'Shatter a Mirror of Ice. All foes near you and target ally take 15..70 cold damage and move 66% slower for 2..6 seconds. If you strike a foe hexed with Water Magic, Mirror of Ice recharges 50% faster.', 'Deals 15...70 cold damage and slows foes by 66% (2...6 seconds). Hits foes near you and target ally. Recharges 50% faster if it hits a foe hexed with Water Magic.', '', '', ''),
(1099, 'Teinai\'s Crystals', 'Target foe takes 20..40 damage. If target foe has a Water Magic hex, this spell strikes target and nearby foes for an additional 20..40 damage, and nearby foes also suffer Cracked Armor for 5..15 seconds.', 'Deals 20...40 damage to target. Deals +20...40 more <sub>sic</sub> damage and causes Cracked Armor (5...15 seconds) nearby if that foe had a Water Magic hex.', '', '', ''),
(1113, 'Kirin\'s Wrath', 'For 5 seconds, foes adjacent to the location in which the spell was cast take 8..32 holy damage each second.', 'Deals 8...32 holy damage each second (5 seconds). Hits foes adjacent to your initial location.', '', '', ''),
(1114, 'Spirit Bond', 'For 8 seconds, whenever target ally takes more than 50 damage from the next 10 attacks or spells, that ally is healed for 30..90 Health.', '(8 seconds.) Heals for 30...90 whenever target ally takes more than 50 damage. <i>Ends after this ally takes damage from 10 attacks or spells.</i>', 'Spirit Bond (PvP)', 'For 8 seconds, whenever target ally takes more than 50 damage from the next 10 attacks or spells, that ally is healed for 30..90 Health.', '(8 seconds.) Heals for 30...90 whenever target ally takes more than 50 damage. <i>Ends after this ally takes damage from 10 attacks or spells.</i>'),
(1115, 'Air of Enchantment', 'For 4..10 seconds, enchantments cast on target other ally cost 5 less Energy (minimum 1 Energy).', '(4...10 seconds.) Enchantments cast on target ally cost 5 less Energy (minimum 1 Energy). <i>Cannot self-target.</i>', '', '', ''),
(1117, 'Heaven\'s Delight', 'Heals you and party members within earshot for 15..60 points.', 'Heals you and party members within earshot for 15...60 points.', '', '', ''),
(1118, 'Healing Burst', 'Target ally is healed for 10..160. All party members in earshot of your target gain Health equal to the Divine Favor bonus from this spell. Your Smiting Prayers are disabled for 20 seconds.', 'Heals for 10...160. Party members in earshot of your target gain Health equal to the Divine Favor bonus. <i>Disables your Smiting Prayers (20 seconds).</i>', '', '', ''),
(1119, 'Karei\'s Healing Circle', 'Heal yourself and all adjacent creatures for 30..180 points.', 'Heals you for 30...180. Also heals adjacent creatures.', '', '', ''),
(1120, 'Jamei\'s Gaze', 'Heal target other ally for 35..180 Health.', 'Heals for 35...180. <i>Cannot self-target.</i>', '', '', ''),
(1121, 'Gift of Health', 'All of your other Healing Prayers skills are disabled for 10..5 seconds. Target other ally is healed for 15..150 Health.', 'Heals for 15...150. <i>Disables your other Healing Prayers skills (10...5 seconds). Cannot self-target.</i>', '', '', ''),
(1123, 'Life Sheath', 'Remove 0..2 condition[s] from target ally. For 8 seconds, the next time that ally would take damage or life steal, that ally gains that amount of Health instead (maximum 20..100).', '(8 seconds.) Converts the next incoming damage or life steal (maximum 20...100) to healing. Initial effect: Removes 0...2 conditions.', '', '', ''),
(1126, 'Empathic Removal', 'You and target other ally lose 1 condition and 1 hex, and are healed for 50.', 'Removes one condition and hex from target ally and yourself, and heals for 50. <i>Cannot self-target.</i>', '', '', ''),
(1128, 'Resurrection Chant', 'Resurrect target party member with up to your current Health and 5..35% Energy. This spell has half the normal range.', 'Half Range Spell. Resurrects target party member at your current Health and with 5...35% Energy.', '', '', ''),
(1129, 'Word of Censure', 'Target foe takes 15..75 holy damage. If your target was below 33% Health, Word of Censure takes 20 additional seconds to recharge.', 'Deals 15...75 holy damage. <i>+20 recharge time if target foe is below 33% Health.</i>', '', '', ''),
(1130, 'Spear of Light', 'Spear of Light flies toward target foe and deals 26..56 holy damage if it hits. Spear of Light deals +15..60 damage if it hits an attacking foe.', 'Projectile: deals 26...56 holy damage. Deals 15...60 more damage if target foe is attacking.', '', '', ''),
(1131, 'Stonesoul Strike', 'Touched target foe takes 10..55 holy damage. If knocked down, your target takes an additional 10..55 holy damage.', 'Touch Skill. Deals 10...55 holy damage. Deals 10...55 more holy damage if target is knocked down.', '', '', ''),
(1133, 'Drunken Blow', 'If this attack hits, you strike for +10..40 damage and your target suffers from one of the following conditions: Deep Wound (for 20 seconds), Weakness (for 20 seconds), Bleeding (for 25 seconds), or Crippled (for 15 seconds). After making a Drunken Blow, you are knocked down.', 'Deals +10...40 damage. Inflicts one of the following random conditions: Deep Wound (20 seconds), Weakness (20 seconds), Bleeding (25 seconds), or Crippled (15 seconds). <i>You are knocked-down.</i>', '', '', ''),
(1134, 'Leviathan\'s Sweep', 'If this attack hits, you strike for +5..20 damage. If this attack is blocked, your target is knocked down and suffers 10..34 damage.', 'Deals +5...20 damage. Causes knock-down and deals 10...34 damage if blocked.', '', '', ''),
(1135, 'Jaizhenju Strike', 'If Jaizhenju Strike hits, you strike for +1..30 damage. If you are not using a stance, Jaizhenju Strike cannot be blocked.', 'Deals +1...30 damage. Unblockable unless you are in a stance.', '', '', ''),
(1136, 'Penetrating Chop', 'If this attack hits, you strike for +5..20 damage. This axe attack has 20% armor penetration.', 'Deals +5...20 damage. 20% armor penetration.', '', '', ''),
(1137, 'Yeti Smash', 'Lose all adrenaline. Attack all adjacent foes. If this attack strikes a foe suffering from a condition, that foe is knocked down. (50% failure chance with Hammer Mastery 4 or less.)', 'Attack all adjacent foes. Knocks down foes suffering from a condition. <i>You lose all adrenaline. 50% failure chance unless Hammer Mastery is 5 or more.</i>', '', '', ''),
(1141, '"You Will Die!"', 'If target foe is below 50% Health, you gain 1..3 strike[s] of adrenaline.', 'You gain 1...3 strikes of adrenaline. <i>No effect unless target foe is under 50% Health.</i>', '', '', ''),
(1142, 'Auspicious Parry', 'For 8 seconds, the next attack against you is blocked. You gain 1..4 strike[s] of adrenaline when this stance ends.', '(8 seconds.) Blocks one attack. End effect: you gain 1...4 strikes of adrenaline.', '', '', ''),
(1144, 'Silverwing Slash', 'This attack strikes for +1..40 damage if it hits.', 'Deals +1...40 damage.', '', '', ''),
(1146, 'Shove', 'Target touched foe is knocked down. If that foe was moving, that foe\'s stance ends and that foe takes 15..75 damage before being knocked down.', 'Elite Touch Skill. Causes knockdown. Initial effect: ends foe\'s stance and deals 15...75 damage if target foe is moving.', '', '', ''),
(1191, 'Sundering Attack', 'If Sundering Attack hits, you strike for +5..25 damage and this attack has 10% armor penetration.', 'Deals +5...25 damage. 10% armor penetration.', 'Sundering Attack (PvP)', 'If Sundering Attack hits, you strike for +3..10 damage and this attack has 10% armor penetration.', 'Deals +3...10 damage. 10% armor penetration.'),
(1192, 'Zojun\'s Shot', 'Shoot an arrow that has half the normal range, but strikes for +10..40 damage.', 'Half Range Bow Attack. Deals +10...40 damage.', '', '', ''),
(1194, 'Predatory Bond', 'For 5..20 seconds, your animal companion attacks 25% faster and heals you for 1..31 Health with each successful attack.', '(5...20 seconds.) Your pet attacks 25% faster and you gain 1...31 Health whenever your pet makes a successful attack.', 'Predatory Bond (PvP)', 'For 5..20 seconds, attacks by your animal companion heal you for 1..31 Health.', '(5...20 seconds.) You gain 1...31 Health whenever your pet makes a successful attack.'),
(1195, 'Heal as One', 'For 15 seconds, your animal companion steals 1..20 Health whenever it hits with an attack. You and your companion are both healed for 20..104 Health. If your companion is dead, it\'s resurrected with 50% Health. If you have this skill equipped, your companion will travel with you.', '(15 seconds.) Your pet\'s attacks steal 1...20 Health. Initial effect: heals you and your pet for 20...104; resurrects your pet (50% Health) if dead. If you have this equipped, your pet will travel with you.', 'Heal as One (PvP)', 'If you or your animal companion are below 75% Health, you are both healed for 25..145 Health. If your companion is dead, it is resurrected with 50% Health.', 'Heals you and your pet for 25...145. If your pet is dead, it is resurrected with 50% Health. <i>No effect if either you nor your pet are below 75% Health.</i>'),
(1196, 'Zojun\'s Haste', 'For 5..11 seconds, you move 33% faster and have a 27..75% chance to block incoming projectiles. Zojun\'s Haste ends if you attack.', '(5...11 seconds.) You move 33% faster and have 27...75% chance to block projectiles. <i>Ends if you attack.</i>', '', '', ''),
(1197, 'Needling Shot', 'Needling Shot strikes for only 10..30 damage and moves faster than normal.  If Needling Shot strikes a foe below 50% Health, Needling Shot recharges instantly. Your other attack skills are disabled for 2 seconds.', 'Fast-moving arrow. Deals 10...30 damage. Recharges instantly if target foe is below 50% Health. <i>Your other attack skills are disabled (2 seconds).</i>', '', '', ''),
(1198, 'Broad Head Arrow', 'You shoot a broad head arrow that moves slower than normal. If it hits, target foe is Dazed for 5..20 seconds, and if target foe is casting a spell that spell is interrupted.', 'Slow moving arrow. Interrupts a spell. Inflicts Dazed condition (5...20 seconds).', '', '', ''),
(1199, 'Glass Arrows', 'For 10..35 seconds, your arrows strike for +5..20 damage if they hit and cause Bleeding for 10..20 seconds if they are blocked.', '(10...35 seconds.) Your arrows deal +5...20 damage. Inflicts Bleeding condition if blocked (10...20 seconds).', 'Glass Arrows (PvP)', 'For 10..35 seconds, your arrows strike for +5..12 damage if they hit and cause Bleeding for 10..20 seconds if they are blocked.', '(10...35 seconds.) Your arrows deal +5...12 damage. Inflicts Bleeding condition if blocked (10...20 seconds).'),
(1200, 'Archer\'s Signet', 'For 1..24 second[s], conditions you apply while wielding a bow last 100% longer.', '(1...24 seconds.) Conditions you apply while wielding a bow last 100% longer.', '', '', ''),
(1201, 'Savage Pounce', 'Your animal companion attempts a Savage Pounce that deals +5..20 damage. If the attack strikes a foe who is casting a spell, that foe is knocked down.', 'Deals +5...20 damage. Causes knock-down if target foe is casting a spell.', '', '', ''),
(1202, 'Enraged Lunge', 'Your animal companion attempts an Enraged Lunge that applies a Deep Wound to target foe for 5..20 seconds and deals +10..50 damage.', 'Inflicts Deep Wound condition (5...20 seconds) and deals +10...50 damage.', 'Enraged Lunge (PvP)', 'Your animal companion attempts an Enraged Lunge that deals +3..23 damage (maximum bonus 80) for each recharging Beast Mastery skill.', 'Deals +3...23 damage (maximum 80) for each of your recharging Beast Mastery skills.'),
(1203, 'Bestial Mauling', 'Your animal companion attempts a Bestial Mauling that deals  +5..20 damage. If the attack strikes a knocked-down foe, that foe is Dazed for 4..10 seconds.', 'Deals +5...20 damage. Inflicts Dazed condition (4...10 seconds) if target foe is knocked-down.', '', '', ''),
(1205, 'Poisonous Bite', 'Your animal companion attempts a Poisonous Bite that Poisons target foe for 5..20 seconds.', 'Inflicts Poisoned condition (5...20 seconds).', '', '', ''),
(1206, 'Pounce', 'Your animal companion\'s next attack is a Pounce that deals +5..20 damage. If the attack strikes a moving foe, that foe is knocked down.', 'Deals +5...20 damage. Causes knock-down if target foe is moving.', '', '', ''),
(1209, 'Bestial Fury', ' All your non-attack skills are disabled for 5 seconds. For 5..11 seconds, you attack 25% faster.', '(5...11 seconds.) You attack 25% faster. <i>Your non-attack skills are disabled (5 seconds).</i>', '', '', ''),
(1211, 'Viper\'s Nest', 'Create a Viper\'s Nest. When it is triggered, all nearby foes are struck for 5..35 piercing damage and become Poisoned for 5..20 seconds. Viper\'s Nest expires after 90 seconds. This Trap is easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 5...35 piercing damage. Inflicts Poisoned condition (5...20 seconds). <i>Easily interrupted.</i>', '', '', ''),
(1212, 'Equinox', 'Create a level 1..10 spirit. Spells cast within its range that cause Overcast cause an additional 10 Overcast. This spirit dies after 30..150 seconds.', 'Creates a level 1...10 spirit (30...150 second lifespan). Overcast-causing spells cast within range cause an additional 10 Overcast.', '', '', ''),
(1213, 'Tranquility', 'Create a level 1..10 spirit. Enchantments cast by non-spirit creatures within its range expire 20..50% faster.  This spirit dies after 15..60 seconds.', 'Creates a level 1...10 spirit (15...60 second lifespan). Enchantments expire 20...50% faster on creatures in range. Does not affect spirits.', '', '', ''),
(1215, 'Clamor of Souls', 'Target foe and all nearby foes take 10..65 lightning damage. If you are within earshot of a spirit or holding a bundle item, you gain 10 Energy.', 'Deals 10...65 lightning damage to target and nearby foes. You gain 10 Energy if you are within earshot of a spirit or holding a bundle item.', '', '', ''),
(1217, 'Ritual Lord', 'For 5..35 seconds, your Ritualist attributes are boosted by 2..4 for your next skill. If that skill is a Binding Ritual, it recharges 10..60% faster and Ritual Lord recharges instantly.', '(5...35 seconds.) You have +2...4 to all Ritualist attributes for your next skill. If that skill is a Binding Ritual, it recharges 10...60% faster and Ritual Lord recharges instantly.', '', '', ''),
(1218, 'Cruel Was Daoshen', 'Hold Daoshen\'s ashes for up to 15..60 seconds. While you hold his ashes, all Ritualist skills have 10% armor penetration. When you drop his ashes, all nearby foes are struck for 15..85 lightning damage.', '(15...60 seconds.) Your Ritualist skills have 10% armor penetration. Drop effect: deals 15...85 lightning damage to all nearby foes.', '', '', ''),
(1219, 'Protective Was Kaolai', 'Hold Kaolai\'s ashes for up to 15..60 seconds. While you hold his ashes, you gain 10 armor. When you drop his ashes, all party members are healed for 10..85 Health.', '(15...60 seconds.) You have +10 armor. Drop effect: all party members are healed for 10...85.', '', '', ''),
(1220, 'Attuned Was Songkai', 'Hold Songkai\'s ashes for up to 45 seconds. While you hold her ashes, your spells and binding rituals cost -5..50% of the base Energy to cast.', '(45 seconds.) Your spells and binding rituals cost -5...50% of the base Energy.', '', '', ''),
(1221, 'Resilient Was Xiko', 'Hold Xiko\'s ashes for up to 5..20 seconds. For each hex or condition you are suffering from while holding her ashes, you gain +3 Health regeneration. When you drop her ashes, you lose 1..4 conditions.', '(5...20 seconds.) You have +3 Health regeneration for each hex or condition on you. Drop effect: you lose 1...4 conditions.', '', '', ''),
(1222, 'Lively Was Naomei', 'Hold Naomei\'s ashes for up to 45 seconds. When you drop her ashes, all party members in the area are resurrected with 15..75% Health and zero Energy.', '(45 seconds.) Drop effect: resurrects party members in the area (15...75% Health and zero Energy).', '', '', ''),
(1223, 'Anguished Was Lingwah', 'Hold Lingwah\'s ashes for up to 10..60 seconds. While you hold her ashes, your Ritualist hexes cost 1..5 less energy and last 33% longer. When you drop her ashes all your Ritualist hexes are recharged.', '(10...60 seconds.) Your Ritualist hexes cost 1...5 less energy and last 33% longer. Drop effect: all your Ritualist hexes are recharged.', '', '', ''),
(1224, 'Draw Spirit', 'Teleport target allied spirit to your location.', 'Teleports target allied spirit to your location.', '', '', ''),
(1225, 'Channeled Strike', 'Target foe is struck for 5..95 lightning damage. That foe is struck for an additional 5..35 lightning damage if you are holding an item.', 'Deals 5...95 lightning damage. Deals 5...35 additional lightning damage if you are holding an item.', '', '', ''),
(1226, 'Spirit Boon Strike', 'Target foe is struck for 20..65 lightning damage, and all spirits you control within earshot gain 20..65 Health.', 'Deals 20...65 lightning damage. Spirits you control within earshot gain 20...65 Health.', '', '', ''),
(1227, 'Essence Strike', 'Target foe is struck for 15..60 lightning damage. If any spirits are within earshot, you gain 1..9 Energy.', 'Deals 15...60 lightning damage. You gain 1...9 Energy if any spirits are within earshot.', '', '', ''),
(1228, 'Spirit Siphon', 'The spirit nearest you loses all Energy. You gain 15..50% of that Energy.', 'The spirit nearest you loses all Energy. You gain 15...50% of that Energy.', '', '', ''),
(1229, 'Explosive Growth', 'For 15..60 seconds, whenever you create a creature, up to 5 foes near that creature are struck for 20..65 lightning damage.', '(15...60 seconds.) Deals 20...65 lightning damage each time you create a creature to 5 foes near that creature.', '', '', ''),
(1230, 'Boon of Creation', 'For 15..60 seconds, whenever you create a creature, you gain 5..50 Health and 1..6 Energy.', '(15...60 seconds.) You gain 5...50 Health and 1...6 Energy whenever you create a creature.', '', '', ''),
(1231, 'Spirit Channeling', 'For 12 seconds, you have +1..6 Energy regeneration. When you cast this spell, you gain 3..12 Energy if you are within earshot of a spirit.', '(12 seconds.) You have +1...6 Energy regeneration. Initial effect: you gain 3...12 Energy if you are within earshot of a spirit.', '', '', ''),
(1232, 'Armor of Unfeeling', 'For 10..35 seconds, your spirits within earshot take 50% less damage and are immune to critical attacks.', '(10...35 seconds.) Your spirits within earshot take 50% less damage and are immune to critical attacks.', 'Armor of Unfeeling (PvP)', 'For 10..35 seconds, you have 10 base damage reduction while casting Binding Rituals.', '(10...35 seconds.) You have 10 base damage reduction while casting binding rituals.'),
(1233, 'Soothing Memories', 'Target ally is healed for 10..100 Health. If you are holding an item, you gain 3 Energy.', 'Heals for 10...100. You gain 3 Energy if you are holding an item.', '', '', ''),
(1234, 'Mend Body and Soul', 'Target ally is healed for 20..115 Health. That ally loses one condition for each spirit within earshot.', 'Heals for 20...115. Removes one condition for each spirit within earshot.', '', '', ''),
(1235, 'Dulled Weapon', 'For 5..15 seconds, target foe and all adjacent foes cannot achieve a critical hit and deal 1..15 less damage.', 'Also hexes foes adjacent to your target (5...15 seconds). Removes ability to land a critical hit. Reduces damage by 1...15.', '', '', ''),
(1236, 'Binding Chains', 'For 3 seconds, target foe and all nearby foes move 90% slower and take 1..30 damage each second while moving.', 'Also hexes foes near your target. (3 seconds.) These foes move 90% slower and takes 1...30 damage each second while moving.', '', '', ''),
(1237, 'Painful Bond', 'For 10..20 seconds, target foe and all nearby foes are hexed with Painful Bond and take 8..20 damage whenever hit by a spirit\'s attack.', 'Also hexes foes near your target (10...20 seconds). Spirits do 8...20 more damage against these foes.', '', '', ''),
(1238, 'Signet of Creation', 'You gain 4 Energy for every summoned creature you control within earshot (maximum 3..12 Energy).', 'Gain 4 Energy (maximum 3...12 Energy) for each summoned creature you control within earshot.', '', '', ''),
(1239, 'Signet of Spirits', 'Create 3 level 1..12 spirits. These spirits deal 5..20 damage with attacks and die after 60 seconds.', 'Creates 3 level 1...12 spirits (60 second lifespan). These spirits deal 5...20 damage with attacks.', 'Signet of Spirits (PvP)', 'You gain 3..12 Energy if you are within earshot of a spirit.', 'You gain 3...12 Energy. <i>No effect unless you are within earshot of a spirit.</i>'),
(1240, 'Soul Twisting', 'For 5..45 seconds, your Binding Rituals cost 15 less Energy (minimum 10) and recharge instantly. Soul Twisting ends after 1..3 Binding Ritual[s].', '(5...45 seconds.) Your Binding Rituals cost 15 less Energy (minimum 10) and recharge instantly. <i>Ends after 1...3 Binding Rituals.</i>', '', '', ''),
(1244, 'Ghostly Haste', 'For 5..20 seconds, spells you cast while within earshot of a spirit recharge 25% faster.', '(5...20 seconds.) Spells you cast recharge 25% faster. <i>No effect unless you are within earshot of a spirit.</i>', '', '', ''),
(1245, 'Gaze from Beyond', 'Target foe is struck for 20..65 lightning damage. If you are within earshot of a spirit, that foe is Blinded for 2..6 seconds.', 'Deals 20...65 lightning damage. Inflicts Blindness condition for 2...6 seconds if you are within earshot of a spirit.', '', '', ''),
(1246, 'Ancestors\' Rage', 'For 1 second, nothing happens. When this skill ends, all foes adjacent to target ally are struck for 5..110 lightning damage.', '(1 second.) End Effect: deals 5...110 lightning damage to foes adjacent to target ally.', 'Ancestors\' Rage (PvP)', 'For 3 seconds all foes adjacent to target ally are struck for 1..41 lightning damage each second.', '(3 seconds.) Deals 1...41 lightning damage to foes adjacent to target ally each second.'),
(1247, 'Pain', 'Create a level 1..12 spirit. This spirit\'s attacks deal 5..30 damage. This spirit dies after 30..150 seconds.', 'Creates a level 1...12 spirit (30...150 second lifespan). Its attacks deal 5...30 damage.', 'Pain (PvP)', 'Create a level 1..8 spirit. This spirit\'s attacks deal 5..30 damage. This spirit dies after 30..150 seconds.', 'Creates a level 1...8 spirit (30...150 second lifespan). Its attacks deal 5...30 damage.'),
(1249, 'Displacement', 'Create a level 1..14 spirit. All non-spirit allies within its range have a 75% chance to block incoming attacks. Every time an attack is blocked in this way, this spirit takes 60 damage. This spirit dies after 30..60 seconds.', 'Creates a level 1...14 spirit (30...60 second lifespan). Non-spirit allies within range have 75% chance to block. <i>Block effect: this spirit takes 60 damage.</i>', 'Displacement (PvP)', 'Create a level 1..10 spirit. All non-spirit allies within its range have a 75% chance to block incoming attacks. Every time an attack is blocked in this way, this spirit takes 60 damage. This spirit dies after 30..60 seconds.', 'Creates a level 1...10 spirit (30...60 second lifespan). Non-spirit allies within range have 75% chance to block. <i>Block effect: this spirit takes 60 damage.</i>'),
(1250, 'Preservation', 'Create a level 1..14 spirit. Every 4 seconds, this spirit heals one non-spirit ally in the area for 10..115 Health. This spirit dies after 90 seconds.', 'Creates a level 1...14 spirit (90 second lifespan). Every 4 seconds this spirit heals one non-spirit ally for 10...115.', 'Preservation (PvP)', 'Create a level 1..10 spirit. Every 4 seconds, this spirit heals one non-spirit ally in the area for 10..115 Health. This spirit dies after 90 seconds.', 'Creates a level 1...10 Spirit (90 second lifespan). Every 4 seconds this spirit heals one non-spirit ally for 10...115.'),
(1251, 'Life', 'Create a level 1..14 spirit. When this spirit dies, all non-spirit allies within its range are healed for 1..7 Health for each second this spirit was alive. This spirit dies after 20 seconds.', 'Creates a level 1...14 spirit (20 second lifespan). Affects non-spirit allies within range. End effect: heals for 1...7 for each second this spirit was alive.', 'Life (PvP)', 'Create a level 1..10 spirit. When this spirit dies, all non-spirit allies within its range are healed for 1..7 Health for each second this spirit was alive. This spirit dies after 20 seconds.', 'Creates a level 1...10 spirit (20 second lifespan). Affects non-spirit allies within range. End effect: heals for 1...7 for each second this spirit was alive.'),
(1252, 'Earthbind', 'Create a level 1..14 spirit. All non-spirit foes knocked down within its range are knocked down for at least 3 seconds. Whenever this happens, this spirit loses 50..25 Health. This spirit dies after 15..45 seconds.', 'Creates a level 1...14 spirit (15...45 second lifespan). Any time non-spirit foes within range are knocked down, they are knocked down for at least 3 seconds. <i>Knock-down cost: this spirit loses 50...25 Health.</i>', 'Earthbind (PvP)', 'Create a level 1..10 spirit. All non-spirit foes knocked down within its range are knocked down for at least 3 seconds. Whenever this happens, this spirit loses 50..25 Health. This spirit dies after 15..45 seconds.', 'Creates a level 1...10 spirit (15...45 second lifespan). Any time non-spirit foes within range are knocked down, they are knocked down for at least 3 seconds. <i>Knock-down cost: this spirit loses 50...25 Health.</i>'),
(1253, 'Bloodsong', 'Create a level 1..12 spirit who dies after 30..150 seconds. Attacks by that spirit steal up to 5..25 Health.', 'Creates a level 1...12 spirit (30...150 second lifespan). Its attacks steal 5...25 Health.', 'Bloodsong (PvP)', 'Create a level 1..8 spirit who dies after 30..150 seconds. Attacks by that spirit steal up to 5..25 Health.', 'Creates a level 1...8 spirit (30...150 second lifespan). Its attacks steal 5...25 Health.'),
(1255, 'Wanderlust', 'Create a level 1..12 spirit. Whenever this spirit\'s attack hits a stationary foe, that foe is knocked down and the spirit loses 70..50 Health. This spirit dies after 30..60 seconds.', 'Creates a level 1...12 spirit (30...60 second lifespan). Its attacks against stationary foes cause knock-down. <i>Knock-down cost: this spirit loses 70...50 Health.</i>', 'Wanderlust (PvP)', 'Create a level 1..8 spirit. Whenever this spirit\'s attack hits a stationary foe, that foe is knocked down and the spirit loses 70..50 Health. This spirit dies after 30..60 seconds.', 'Creates a level 1...8 spirit (30...60 second lifespan). Its attacks against stationary foes cause knock-down. <i>Knock-down cost: this spirit loses 70...50 Health.</i>'),
(1257, 'Spirit Light Weapon', 'For 10 seconds, target ally gains 1..15 Health per second and an additional 1..15 Health per second if that ally is within earshot of a spirit.', '(10 seconds.) Target ally gains 1...15 Health each second. 1...15 more healing per second while within earshot of a spirit.', '', '', ''),
(1258, 'Brutal Weapon', 'Give target ally a Brutal Weapon for 10..40 seconds. The bearer\'s weapon strikes for +5..15 damage as long as the bearer is under no enchantments.', '(10...40 seconds.) Attacks deal +5...15 damage. grey', '', '', ''),
(1259, 'Guided Weapon', 'For 4..10 seconds, target ally\'s attacks cannot be blocked.', '(4...10 seconds.) Attacks are unblockable.', '', '', ''),
(1260, 'Meekness', 'For 5..30 seconds, target foe and all foes in the area attack 50% slower.', 'Also hexes foes in the area of target (5...30 seconds). These foes attack 50% slower.', '', '', ''),
(1261, 'Frigid Armor', 'For 10..25 seconds, you have +10..40 armor against physical damage and cannot be set on fire.', '(10...25 seconds.) You have +10...40 armor against physical damage and immunity to Burning.', '', '', ''),
(1262, 'Healing Ring', 'Heal adjacent creatures for 30..180 Health. The caster is not healed.', 'Heals adjacent allies and foes for 30...180. <i>The caster is not healed.</i>', '', '', ''),
(1263, 'Renew Life', 'Resurrect target touched dead target  party member with 50% Health and 5..20% Energy. That party member and all allies within earshot are healed for 55..130 Health.', 'Touch Spell. Resurrects target party member (50% Health and 5...20% Energy). Heals allies within earshot for 55...130.', '', '', ''),
(1264, 'Doom', 'Strike target foe for 10..40 lightning (maximum 135) damage for every recharging binding ritual you have.', 'Deals 10...40 lightning damage (maximum 135) for each of your recharging binding rituals.', '', '', ''),
(1265, 'Wielder\'s Boon', 'Heal target ally for 15..60 points. If that ally is under the effects of a weapon spell, Wielder\'s Boon heals for an additional 15..75 Health.', 'Heals for 15...60. Heals for 15...75 more if this ally is under a Weapon <sub>sic</sub> spell.', '', '', ''),
(1266, 'Soothing', 'Create a level 1..12 spirit. All foes within its range take twice as long to build adrenaline. This spirit dies after 15..45 seconds.', 'Creates a level 1...12 spirit (15...45 second lifespan). Building adrenaline takes twice as long for foes within range.', 'Soothing (PvP)', 'Create a level 1..8 spirit. All foes within its range take twice as long to build adrenaline. This spirit dies after 15..45 seconds.', 'Creates a level 1...8 spirit (15...45 second lifespan). Building adrenaline takes twice as long for foes within range.'),
(1267, 'Vital Weapon', 'For 5..30 seconds, target ally has a Vital Weapon and has +40..175 maximum Health.', '(5...30 seconds.) +40...175 maximum Health.', '', '', ''),
(1268, 'Weapon of Quickening', 'For 5..25 seconds, target ally has a Weapon of Quickening, and spells and binding rituals recharge 33% faster.', '(5...25 seconds.) Spells and binding rituals recharge 33% faster.', '', '', ''),
(1269, 'Signet of Rage', 'Target foe takes 5..50 holy damage and +5..10 holy damage for each adrenaline skill that foe has.', 'Deals 5...50 holy damage. Deals 5...10 more holy damage for each of target foe\'s adrenaline skills.', '', '', ''),
(1333, 'Extend Conditions', 'Spread all conditions from target foe to foes near your target. The durations of those conditions are increased by 5..100% (maximum 30 seconds).', 'Spread all conditions from target foe to foes near your target. Those <sub>sic</sub> durations of those conditions are increased by 5...100% (maximum 30 seconds).', '', '', ''),
(1334, 'Hypochondria', 'Transfer all conditions from all foes in the area to target foe.', 'Transfer all conditions from foes in the area to target foe.', '', '', ''),
(1335, 'Wastrel\'s Demise', 'For 5 seconds, target foe is hexed with Wastrel\'s Demise. Each second while hexed, target foe and all foes adjacent to that foe take 1..10 damage and an additional 1..10 damage for each second this spell is in effect. This hex ends prematurely if target foe uses a skill.', '(5 seconds.) Each second while hexed, target foe and all foes adjacent to that foe take 1...10 damage. Foes take +1...10 damage each second this hex is in effect. <i>Ends early if target foe uses a skill.</i>', '', '', ''),
(1336, 'Spiritual Pain', 'Target foe takes 15..75 damage. All hostile summoned creatures in the area of that foe take 25..125 damage.', 'Deals 15...75 damage. Deals 25...125 damage to hostile summoned creatures in the area of your target foe.', 'Spiritual Pain (PvP)', 'Target foe takes 5..40 damage. All hostile summoned creatures in the area of that foe take 25..125 damage.', 'Deals 5...40 damage. Deals 25...125 damage to hostile summoned creatures in the area of your target foe.'),
(1337, 'Drain Delusions', 'Remove one Mesmer hex from target foe. If a hex was removed in this way, that foe loses 1..5 Energy and you gain 4 Energy for each point lost.', 'Removes one Mesmer hex from target foe. Causes 1...5 Energy loss. You gain 4 Energy for each point lost. <i>No effect unless a hex was removed.</i>', '', '', ''),
(1338, 'Persistence of Memory', 'For 5..20 seconds, whenever a spell you cast is interrupted, that spell is instantly recharged.', '(5...20 seconds.) Your interrupted spells recharge instantly.', '', '', ''),
(1339, 'Symbols of Inspiration', 'For 1..31 seconds, this skill becomes the Elite of target foe. Elite spells you cast use your Fast Casting attribute instead of their normal attributes.', '(1...31 seconds.) This skill becomes the Elite of target foe. Elite spells you cast use your Fast Casting attribute instead of their normal attributes.', '', '', ''),
(1340, 'Symbolic Celerity', 'For 36..60 seconds, all of your signets use your Fast Casting attribute instead of their normal attributes.', '(36...60 seconds.) Your signets use your Fast Casting attribute.', '', '', ''),
(1341, 'Frustration', 'For 5..20 seconds, target foe casts spells 50% slower and takes 5..50 damage whenever interrupted. If a skill is interrupted, Frustration deals double damage.', '(5...20 seconds.) Causes 50% slower spell casting. Target foe takes 5...50 damage whenever interrupted. Deals double damage on skill interrupt.', 'Frustration (PvP)', 'For 5..20 seconds, target foe casts spells 50% slower and takes 5..50 damage whenever interrupted.', '(5...20 seconds.) Causes 50% slower spell casting. Target foe takes 5...50 damage whenever interrupted.'),
(1342, 'Tease', 'If target foe is using a skill, that foe and other foes in the area are interrupted and you steal 0..5 Energy from all foes in the area.', 'Interrupts a skill. Interruption effect: also interrupts other foes in the area, and you steal 0...5 Energy from all foes in the area.', '', '', ''),
(1343, 'Ether Phantom', 'For 10 seconds, target foe has -1 Energy degeneration. If this hex is removed prematurely, that foe loses 1..5 Energy.', '(10 seconds.) Causes -1 Energy degeneration. Causes 1...5 Energy loss if this hex ends early.', '', '', ''),
(1344, 'Web of Disruption', 'Interrupt target foe. For 10 seconds, target foe is hexed with Web of Disruption.  When this hex ends, that foe is interrupted again.', '(10 seconds.) Initial effect: interrupts a skill. End effect: interrupts a skill.', 'Web of Disruption (PvP)', 'Interrupt target foe. For 10 seconds, target foe is hexed with Web of Disruption.  When this hex ends, that foe is interrupted again.', '(10 seconds.) Initial effect: interrupts a skill. End effect: interrupts a skill.'),
(1345, 'Enchanter\'s Conundrum', 'For 10 seconds, target foe casts enchantments 100..200% slower. If target foe is not under the effects of an enchantment when this hex is applied, that foe and all adjacent foes take 10..100 damage.', 'Causes 100...200% slower enchantment casting (10 seconds). Initial effect: deals 10...100 damage to target and adjacent foes if target foe is not enchanted.', 'Enchanter\'s Conundrum (PvP)', 'For 10 seconds, target foe casts enchantments 100..200% slower. If target foe is not under the effects of an enchantment when this hex is applied, that foe and all adjacent foes take 10..100 damage.', 'Causes 100...200% slower enchantment casting (10 seconds). Initial effect: deals 10...100 damage to target and adjacent foes if target foe is not enchanted.'),
(1346, 'Signet of Illusions', 'Your next 1..3 spell[s] use your Illusion attribute instead of its normal attribute.', 'Your next 1...3 spells use your Illusion attribute instead of its normal attribute.', '', '', ''),
(1347, 'Discharge Enchantment', 'Remove one enchantment from target foe. If that foe is hexed, this skill recharges 20..50% faster.', 'Removes one enchantment from target foe. 20...50% faster recharge if that foe was hexed.', '', '', ''),
(1348, 'Hex Eater Vortex', 'Remove a hex from target ally. If a hex is removed in this way, foes near that ally take 30..90 damage and lose one enchantment.', 'Removes one hex from target ally. Removal effect: deals 30...90 damage and removes one enchantment from foes near this ally.', '', '', ''),
(1349, 'Mirror of Disenchantment', 'Remove one enchantment from target foe. All of that foe\'s party members also lose that same enchantment.', 'Removes one enchantment from target foe. That foe\'s party members also lose this enchantment.', 'Mirror of Disenchantment (PvP)', 'Remove one enchantment from target foe. All of that foe\'s party members also lose that same enchantment.', 'Removes one enchantment from target foe. That foe\'s party members also lose this enchantment.'),
(1350, 'Simple Thievery', 'Interrupt target foe\'s action. If that action was a skill, that skill is disabled for 5..20 seconds, and Simple Thievery is replaced by that skill.', 'Interrupts an action. Interruption effect: If a skill was interrupted, that skill is disabled and Simple Thievery becomes that skill (5...20 seconds).', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1351, 'Animate Shambling Horror', 'Exploit nearest corpse to create a level 1..17 shambling horror. When the shambling horror dies, it is replaced by a level 0..15 jagged horror that causes Bleeding with each of its attacks.', 'Creates a level 1...17 shambling horror. When the shambling horror dies, it is replaced by a level 0...15 jagged horror that causes Bleeding with each of its attacks. <i>Exploits a fresh corpse.</i>', '', '', ''),
(1352, 'Order of Undeath', 'For 5 seconds, your minions deal +3..16 damage, but you lose 2% of your maximum Health whenever one of your minions hits with an attack.', '(5 seconds.) Your undead servants deal +3...16 damage. <i>You lose 2% of your maximum Health whenever your servants hit.</i>', '', '', ''),
(1353, 'Putrid Flesh', 'Destroy one of your target animated undead minions. All foes near that creature are Diseased for 5..15 seconds.', 'Destroys one of your undead servants. Inflicts Diseased condition (5...15 seconds) to foes near this servant.', '', '', ''),
(1354, 'Feast for the Dead', 'Destroy target animated undead ally. All of your other animated undead allies are healed for 10..100 Health.', 'Destroys one of your undead servants. Heals your servants for 10...100.', '', '', ''),
(1355, 'Jagged Bones', 'For 30 seconds, whenever target undead servant dies, it is replaced by a level 0..15 jagged horror that causes Bleeding with each of its attacks.', '(30 seconds.) When target undead servant dies, it is replaced by a level 0...15 jagged horror that inflicts Bleeding with attacks.', '', '', ''),
(1356, 'Contagion', 'For 60 seconds, whenever you suffer from a new condition, all foes in the area suffer from that same condition and you sacrifice 10..5% maximum Health.', '(60 seconds.) Whenever you gain a condition, all foes in the area gain that same condition. <i>You sacrifice 10...5% maximum Health each time this happens.</i>', '', '', ''),
(1358, 'Ulcerous Lungs', 'For 10..25 seconds, target foe and all nearby foes suffer from -4 Health degeneration when Bleeding, and whenever they use a shout or chant, they Bleed for 3..15 seconds.', 'Also hexes foes near your target (10...25 seconds). Causes -4 Health degeneration to any of these foes that are Bleeding. Inflicts Bleeding (3...15 seconds) whenever these foes use a shout or chant.', '', '', ''),
(1359, 'Pain of Disenchantment', 'Target foe loses 1..3 enchantment[s]. If an enchantment was lost in this way, that foe and all adjacent foes lose 10..100 Health.', 'Target foe loses 1...3 enchantments. Removal effect: that foe and all adjacent foes lose 10...100 Health.', '', '', ''),
(1360, 'Mark of Fury', 'For 5 seconds, allies hitting target foe gain 0..2 strike[s] of adrenaline. When this hex ends, that foe suffers from Cracked Armor for 1..15 second[s].', '(5 seconds.) Allies hitting target foe gain 0...2 strikes of adrenaline. End effect: inflicts Cracked Armor (1...15 seconds.)', '', '', ''),
(1362, 'Corrupt Enchantment', 'Remove one enchantment from target foe. If an enchantment is removed in this way, that foe suffers from -1..8 Health degeneration for 10 seconds.', 'Removes one enchantment from target foe. Removal effect: -1...8 Health degeneration (10 seconds).', '', '', ''),
(1363, 'Signet of Sorrow', 'Target foe takes 15..75 damage. If target foe is near a corpse or has a dead pet, this skill recharges instantly.', 'Deals 15...75 damage. Recharges instantly if target foe is near a corpse or has a dead pet.', '', '', ''),
(1364, 'Signet of Suffering', 'You suffer from Bleeding for 6 seconds. The next Necromancer skill that targets a foe causes Bleeding for 2..16 seconds.', 'You Bleed for 6 seconds. Applies Bleeding (2...16 seconds) to the target of your next Necromancer skill.', '', '', ''),
(1365, 'Signet of Lost Souls', 'If target foe is below 50% Health, you gain 10..100 Health and 1..10 Energy.', 'You gain 10...100 Health and 1...10 Energy if target foe is below 50% Health.', '', '', ''),
(1366, 'Well of Darkness', 'Exploit nearest corpse to create a Well of Darkness for 5..50 seconds. Hexed foes within the Well of Darkness miss 50% of the time.', '(5...50 seconds.) Hexed foes in this well have 50% chance to miss. <i>Exploits a fresh corpse</i>.', '', '', ''),
(1367, 'Blinding Surge', 'Target foe is struck for 5..50 lightning damage. That foe and all adjacent foes are Blinded for 3..8 seconds. This spell has 25% armor penetration. If this spell strikes an attacking foe, all adjacent foes are also struck and this spell deals 50% more damage.', 'Deals 5...50 lightning damage. Inflicts Blindness condition (3...8 seconds) on target and adjacent foes. 25% armor penetration. If target was attacking, also hits adjacent foes and deals 50% more damage.', '', '', ''),
(1368, 'Chilling Winds', 'Target foe and all adjacent foes are struck for 30..60 cold damage. For 10 seconds, the next Water Magic hex targeting a hexed foe lasts 25..100% longer.', '(10 seconds.) The next water hex on target foe lasts 25...100% longer. Initial effect: 30...60 cold damage. Also strikes adjacent.', '', '', ''),
(1369, 'Lightning Bolt', 'Send out a Lightning Bolt that strikes for 5..50 lightning damage if it hits. If Lightning Bolt strikes a moving foe, that foe is struck for 5..50 additional lightning damage. This spell has 25% armor penetration.', 'Projectile: deals 5...50 lightning damage. Deals 5...50 more lightning damage if target foe is moving. 25% armor penetration.', '', '', ''),
(1370, 'Storm Djinn\'s Haste', 'For 10..25 seconds, you move 25% faster. Each second that you are moving, you lose 1 Energy.', '(10...25 seconds.) You move 25% faster. <i>Lose 1 Energy each second while moving.</i>', '', '', ''),
(1371, 'Stone Striker', ' For 5..30 seconds, whenever you take or deal elemental or physical damage, that damage is converted to earth damage.', '(5...30 seconds.) Converts elemental and physical damage you take or deal to earth damage.', '', '', ''),
(1372, 'Sandstorm', 'Create a Sandstorm at target foe\'s location. For 10 seconds, nearby foes are struck for 10..30 earth damage each second and attacking foes are struck for an additional 10..30 earth damage each second.', 'Deals 10...30 earth damage each second (10 seconds). Hits foes near target foe\'s initial location. Hits attacking foes for 10...30 more earth damage each second.', '', '', ''),
(1373, 'Stone Sheath', 'For 5..20 seconds, you and target ally have +1..30 armor and are immune to critical hits. When you cast this spell, all foes near you and your target take 15..70 earth damage and are Weakened for 5..20 seconds.', '(5...20 seconds.) Gives you and target ally +1...30 armor and immunity to critical hits. Initial effect: Foes near you and target ally are struck for 15...70 earth damage and are Weakened (5...20 seconds).', '', '', ''),
(1374, 'Ebon Hawk', 'Send a projectile that strikes target foe for 10..85 earth damage and causes Weakness for 5..15 seconds if it hits.', 'Projectile: deals 10...85 earth damage and inflicts Weakness condition (5...15 seconds).', '', '', ''),
(1375, 'Stoneflesh Aura', 'For 5..15 seconds, damage you receive is reduced by 1..31, and you are immune to critical attacks.', '(5...15 seconds.) Reduces damage you take by 1...31, and you are immune to critical hits.', '', '', ''),
(1376, 'Glyph of Restoration', 'For 15 seconds, your next  2 spells heal you for 30..105 Health, and you are healed for 150..400% of the Energy cost of each spell.', '(15 seconds.) Your next 2 spells heal you for 30...105, and you are healed for 150...400% of the Energy cost of each spell.', '', '', ''),
(1377, 'Ether Prism', 'For 3 seconds, all damage you take is reduced by 75%. When Ether Prism ends, you gain 5..20 Energy.', '(3 seconds.) All damage you take is reduced by 75%. End effect: gain 5...20 Energy.', '', '', ''),
(1378, 'Master of Magic', 'For 1..61 second[s], all of your elemental attributes are set to 8..14 and your elemental spells return 30% of their Energy cost.', '(1...61 seconds). Your elemental attributes are set to 8...14 and elemental spells return 30% of their Energy cost.', '', '', ''),
(1379, 'Glowing Gaze', 'Target foe takes 5..50 fire damage. If that foe is on Fire, you gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage.', 'Deals 5...50 fire damage. You gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage if target foe is Burning.', '', '', ''),
(1380, 'Savannah Heat', 'You create Savannah Heat at target foe\'s location. For 5 seconds, all nearby foes take 5..20 fire damage for each second this spell has been in effect.', 'Deals 5...20 fire damage for each second since casting this spell (5 seconds). <i>Hits foes near target\'s initial location.</i>', 'Savannah Heat (PvP)', 'You create Savannah Heat at target foe\'s location. For 5 seconds, all nearby foes take 5..20 fire damage for each second this spell has been in effect.', 'Deals 5...20 fire damage for each second since casting this spell (5 seconds). Hits foes near target\'s initial location.'),
(1381, 'Flame Djinn\'s Haste', 'All adjacent foes are struck for 15..120 fire damage. For 8..14 seconds, you move 25% faster. Flame Djinn\'s Haste recharges 50% faster if a foe was struck by this spell.', '(8...14 seconds.) You move 25% faster. Initial effect: deals 15...120 fire damage to foes adjacent to you. If you damage a foe with this spell, it recharges 50% faster.', '', '', ''),
(1382, 'Freezing Gust', 'If target foe is under the effect of a Water Magic hex, that foe is struck for 20..80 cold damage. Otherwise, that foe moves 66% slower for 1..5 second[s].', 'Deals 20...80 cold damage if target foe is hexed with Water Magic. Otherwise, this foe moves 66% slower (1...5 seconds).', '', '', ''),
(1390, 'Judge\'s Intervention', 'For 10 seconds, the next time target ally receives damage that would be fatal, the damage is negated and one nearby foe takes 30..180 holy damage.', '(10 seconds.) Negates the next fatal damage. Negation effect: deals 30...180 holy damage to one foe near target ally.', '', '', ''),
(1391, 'Supportive Spirit', 'For 5..23 seconds, whenever target ally takes damage while knocked down, that ally is healed for 5..35 Health.', '(5...23 seconds.) Heals for 5...35 whenever target ally takes damage while knocked-down.', '', '', ''),
(1392, 'Watchful Healing', 'For 10 seconds, target ally gains +1..4 Health regeneration. If this skill ends prematurely, that ally gains 30..120 Health.', '(10 seconds.) Target ally has +1...4 Health regeneration and gains 30...120 Health if this enchantment ends early.', '', '', ''),
(1393, 'Healer\'s Boon', 'For 10..55 seconds.  Healing Prayers spells cast 50% faster and heal for 50% more Health.', '(10...55 seconds.) Healing Prayers spells cast 50% faster and heal for 50% more.', '', '', ''),
(1394, 'Healer\'s Covenant', 'While you maintain this enchantment, your Healing Prayers spells heal for 25% less Health, but cost -1..3 Energy.', 'Your Healing Prayers spells cost 1...3 less Energy. <i>These spells heal for 25% less.</i>', '', '', ''),
(1395, 'Balthazar\'s Pendulum', 'For 5..10 seconds, the next time target ally would be knocked down by a foe, that foe is knocked down instead.', '(5...10 seconds.) Causes knock-down to the next foe attempting to knock-down target ally.', '', '', ''),
(1396, 'Words of Comfort', 'Target ally is healed for 15..60 Health and an additional 15..45 Health if that ally is suffering from a condition.', 'Heals for 15...60. Heals for 15...45 more if target ally has a condition.', '', '', ''),
(1397, 'Light of Deliverance', 'All party members are healed for 5..70 Health.', 'Heals entire party for 5...70.', 'Light of Deliverance (PvP)', 'All party members are healed for 5..70 Health. All your Smiting Prayers are disabled for 20 seconds.', 'Heals entire party for 5...70. <i>Disables your Smiting Prayers (20 seconds).</i>'),
(1398, 'Scourge Enchantment', 'For 30 seconds, each time target foe is the target of an enchantment, the caster of that enchantment takes 15..75 damage.', '(30 seconds.) Deals 15...75 damage to anyone casting an enchantment on target foe.', '', '', ''),
(1399, 'Shield of Absorption', 'For 3..7 seconds, damage received by target ally is reduced by 5 each time that ally is hit while under the effects of this enchantment.', '(3...7 seconds.) Reduces incoming damage by 5 each time target ally takes damage.', '', '', ''),
(1400, 'Reversal of Damage', 'For 8 seconds, the next time target ally would take damage, the foe dealing the damage takes that damage instead (maximum 5..75).', '(8 seconds.) Negates the next damage and hits the source for that same amount (maximum 5...75).', '', '', ''),
(1401, 'Mending Touch', 'Touched ally loses two conditions and is healed for 15..60 Health for each condition removed in this way.', 'Touch Spell. Removes two conditions. Heals for 15...60 for each condition removed.', '', '', ''),
(1402, 'Critical Chop', 'If this attack hits, you inflict +5..20 damage. If this attack results in a critical hit, target foe is interrupted.', 'Deals +5...20 damage. Interrupts an action if you land a critical hit.', '', '', ''),
(1403, 'Agonizing Chop', 'When this attack hits, you deal +5..20 damage. If target foe is suffering from a Deep Wound, you interrupt that foe\'s action.', 'Deals +5...20 damage. Interrupts an action if target foe has a Deep Wound.', '', '', ''),
(1404, 'Flail', 'For 1..15 second[s], you attack 33% faster but move 33% slower.', '(1...15 seconds.) You attack 33% faster. <i>You move 33% slower.</i>', '', '', ''),
(1405, 'Charging Strike', 'For 1..10 second[s], you run 33% faster. Your next successful melee hit does +10..40 damage and this stance ends. This stance ends if you use a skill.', '(1...10 seconds.) You move 33% faster and deal +10...40 damage with your next melee hit. <i>Ends when you hit or if you use a skill.</i>', '', '', ''),
(1406, 'Headbutt', 'Target touched foe takes 40..100 damage. You are Dazed for 5..20 seconds.', 'Elite Touch Skill. Deals 40...100 damage. <i>You are Dazed (5...20 seconds).</i>', '', '', ''),
(1407, 'Lion\'s Comfort', 'All of your signets are disabled for 12 seconds. You are healed for 50..110 Health, and gain 0..3 strike[s] of adrenaline.', 'You are healed for 50...110 and gain 0...3 strikes of adrenaline. <i>Disables your signets (12 seconds).</i>', '', '', ''),
(1408, 'Rage of the Ntouka', 'Gain 1..7 strike[s] of adrenaline. For 10 seconds, whenever you use an adrenal skill, that skill recharges for 5 seconds.', 'You gain 1...7 adrenaline. <i>For 10 seconds, adrenal skills have a 5 second recharge when used.</i>', '', '', ''),
(1409, 'Mokele Smash', 'If this attack hits, you strike for +5..20 damage and gain 2 strikes of adrenaline.', 'Deals +5...20 damage and you gain 2 strikes of adrenaline.', '', '', ''),
(1410, 'Overbearing Smash', 'If this attack hits, you deal +1..20 damage. If target foe is knocked down, that foe is Dazed for 1..8 second[s].', 'Deals +1...20 damage. If target foe is knocked down, they are Dazed (1...8 seconds).', '', '', ''),
(1411, 'Signet of Stamina', ' You have +50..300 maximum Health. This signet ends if you successfully hit with an attack.', 'You have +50...300 maximum Health. <i>Ends if you hit with an attack.</i>', '', '', ''),
(1412, '"You\'re All Alone!"', 'If target foe isn\'t near an ally, that foe suffers from Cripple and Weakness for 8 seconds.', 'Inflicts Cripple and Weakness conditions (8 seconds). <i>No effect if target foe is near one of its allies.</i>', '', '', ''),
(1413, 'Burst of Aggression', ' For 2..10 seconds, you attack 33% faster. When this stance ends, you lose all adrenaline.', '(2...10 seconds.) You attack 33% faster. <i>End effect: lose all adrenaline.</i>', '', '', ''),
(1414, 'Enraging Charge', 'For 5..15 seconds, you move 25% faster. Enraging Charge ends when you successfully strike a target, at which point you gain 0..3 strike[s] of adrenaline if you hit with a melee attack.', '(5...15 seconds.) You move 25% faster. Gain +0...3 adrenaline on your next melee attack. <i>Ends after your next hit.</i>', '', '', ''),
(1415, 'Crippling Slash', 'If this attack hits, target foe is Crippled for 5..15 seconds and begins Bleeding for 10..25 seconds.', 'Inflicts Crippled condition (5...15 seconds) and Bleeding condition (10...25 seconds).', '', '', ''),
(1416, 'Barbarous Slice', 'If this attack hits, you deal +5..30 damage. If you are currently not in a stance, you also inflict Bleeding for 5..15 seconds.', 'Deals +5...30 damage. Inflicts Bleeding condition (5...15 seconds) if you are not in a stance.', '', '', ''),
(1465, 'Prepared Shot', 'If this attack hits, you strike for +10..25 damage. If you are under the effects of a preparation, you gain 1..9 Energy.', 'Deals +10...25 damage. You gain 1...9 Energy if you have a preparation active.', '', '', ''),
(1466, 'Burning Arrow', 'If this attack hits, you strike for +10..30 damage and cause Burning for 1..7 seconds.', 'Deals +10...30 damage. Inflicts Burning condition (1...7 seconds).', '', '', ''),
(1467, 'Arcing Shot', 'If this arrow hits, it strikes for +10..25 damage.  This arrow cannot be blocked, but it moves 50% slower.', 'Deals +10...25 damage. Unblockable. <i>This arrow moves 50% slower.</i>', '', '', ''),
(1468, 'Strike as One', 'Your animal companion instantly moves to your target and causes Bleeding for 5..15 seconds with its next attack. The next time you hit with an attack, target foe is Crippled for 5..15 seconds.', 'Your animal companion instantly moves to your target and inflicts Bleeding (5...15 seconds) with its next attack. The next time you hit with an attack, you inflict Crippled condition (5...15 seconds).', '', '', ''),
(1469, 'Crossfire', 'If this attack hits target foe, it deals +5..20 damage. If that foe is near any of your allies, this attack cannot be blocked.', 'Deals +5...20 damage. Unblockable if target foe is near any of your allies.', '', '', ''),
(1470, 'Barbed Arrows', 'For 24 seconds, your arrows cause Bleeding for 3..15 seconds.  You have -40 armor while activating this skill.', '(24 seconds.) Your arrows inflict Bleeding condition (3...15 seconds). <i>You have -40 armor while activating this skill.</i>', '', '', ''),
(1471, 'Scavenger\'s Focus', 'For 10 seconds if you strike a foe who is suffering from a condition you gain 3..12 Energy.', '(10 seconds). You gain 3...12 Energy if you strike a foe who has a condition.', '', '', ''),
(1472, 'Toxicity', 'Create a level 1..10 spirit. Poisoned or Diseased creatures within its range suffer -2 Health degeneration. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit (30...90 second lifespan.) Poisoned or Diseased creatures within range have -2 Health degeneration.', '', '', ''),
(1473, 'Quicksand', 'Create a level 1..10 spirit. All non-spirit creatures within its range lose 1 Energy each time they attack or use a skill. This spirit dies after 30..90 seconds.', 'Creates a level 1...10 spirit (30...90 second lifespan). Creatures lose 1 Energy each time they attack or use a skill. Does not affect spirits.', '', '', ''),
(1474, 'Storm\'s Embrace', 'For 10 seconds, you move 25% faster.  This stance is refreshed whenever you take Elemental damage.', '(10 seconds.) You move 25% faster. Renewal: whenever you take elemental damage.', '', '', ''),
(1475, 'Trapper\'s Speed', 'For 5..30 seconds, your Traps recharge 25% faster and activate 25% faster. This Stance ends if you successfully hit with an attack.', '(5...30 seconds.) Your traps recharge 25% faster and activate 25% faster. <i>Ends if you hit with an attack.</i>', '', '', ''),
(1476, 'Tripwire', 'When Tripwire is triggered, all nearby foes are struck for 5..20 piercing damage. Any Crippled foes are knocked down. Tripwire ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Deals 5...20 piercing damage to nearby foes. Causes knock-down to Crippled foes. <i>Easily interrupted.</i>', '', '', ''),
(1478, 'Renewing Surge', 'For 8 seconds, target foe suffers 2..12 damage each second. When this hex ends, you gain 1..8 Energy.', '(8 seconds.) Deals 2...12 damage each second. End effect: You gain 1...8 energy.', '', '', ''),
(1479, 'Offering of Spirit', 'Gain 8..17 Energy. If any spirits are within earshot, you do not sacrifice Health.', 'You gain 8...17 Energy. You do not sacrifice Health if any spirits are within earshot.', '', '', ''),
(1480, 'Spirit\'s Gift', 'For 60 seconds, whenever you create a creature, all allies near that creature gain 5..50 Health and lose 1 condition.', '(60 seconds.) Whenever you create a creature, all nearby allies gain 5...50 Health and lose one condition.', '', '', ''),
(1481, 'Death Pact Signet', 'Resurrect target party member with your current Health and 15..100% Energy. The next time that ally dies within 120 seconds, so do you.', 'Resurrects target party member with your current Health and 15...100% maximum Energy. <i>For 120 seconds, you die the next time this party member dies.</i>', 'Death Pact Signet (PvP)', 'Resurrect target party member with your current Health and 15..100% Energy. The next time that ally dies within 120 seconds, so do you.', 'Resurrects target party member with your current Health and 15...100% maximum Energy. For 120 seconds, you die the next time this party member dies.'),
(1482, 'Reclaim Essence', 'All of your Spirits die. You gain 5..20 Energy and all of your Binding Rituals are recharged if a Spirit died in this way.', 'All of your Spirits die. If a Spirit dies in this way, you gain 5...20 Energy and all of your Binding Rituals are recharged.', '', '', ''),
(1483, 'Banishing Strike', 'If it hits, this attack deals 10..60 holy damage. It deals double damage to summoned creatures.', 'Deals 10...60 holy damage. Deals double damage to summoned creatures.', 'Banishing Strike (PvP)', 'If it hits, this attack deals 10..60 holy damage. It deals double damage to summoned creatures.', 'Deals 10...60 holy damage. Deals double damage to summoned creatures.'),
(1484, 'Mystic Sweep', 'If this attack hits, you deal +3..12 damage. If you are enchanted, this attack deals an additional +3..12 damage.', 'Deals +3...12 damage. Deals an additional +3...12 damage if you are enchanted.', '', '', ''),
(1485, 'Eremite\'s Attack', 'If it hits, this attack deals +1..10 damage and removes a Dervish enchantment. If an enchantment is removed, you do an additional +1..10 damage and strike all adjacent foes.', 'Deals +1...10 damage and removes a Dervish enchantment. Removal Effect: Deals +1...10 damage and strikes all adjacent foes.', '', '', ''),
(1486, 'Reap Impurities', 'If this attack hits, you deal +3..15 damage. Each foe you hit loses 1 condition. For each foe who loses a condition, all foes adjacent to that target foe take 10..40 holy damage.', 'Deals +3...15 damage. Struck foes lose 1 condition. Removal Effect: all foes adjacent to those struck take 10...40 holy damage.', '', '', ''),
(1487, 'Twin Moon Sweep', 'You lose 1 Dervish enchantment and gain 10..50 Health. If an enchantment is lost in this way, you cannot be blocked, you strike twice, and you gain an additional 10..70 Health.', 'Remove 1 of your Dervish enchantments. Gain 10...50 Health. Removal effect: unblockable, attack twice, and gain 10...70 more Health.', 'Twin Moon Sweep (PvP)', 'You lose 1 Dervish enchantment and gain 10..20 Health. If an enchantment is lost in this way, you cannot be blocked, you strike twice, with each strike doing 25% less damage than normal, and you gain an additional 10..30 Health.', 'Remove 1 of your Dervish enchantments. Gain 10...20 Health. Removal effect: Unblockable, attack twice, each strike doing 25% less damage, and gain 10...30 more Health.'),
(1488, 'Victorious Sweep', 'If this attack hits, you deal +5..25 damage. If target foe has less Health than you, you gain 30..80 Health.', 'Deals +5...25 damage. You gain 30...80 Health for each foe you hit that has less Health than you.', '', '', ''),
(1489, 'Irresistible Sweep', 'Deal +3..15 damage and lose 1 Dervish enchantment. If you lose an enchantment in this way, Irresistible Sweep cannot be blocked, removes a stance, and deals +3..15 additional damage.', 'Deals +3...15. Remove one of your Dervish enchantments. Removal effect: unblockable, removes a stance, deals +3...15 additional damage.', 'Irresistible Sweep (PvP)', 'Deal +3..15 damage and lose 1 Dervish enchantment. If you lose an enchantment in this way, Irresistible Sweep cannot be blocked and removes a stance.', 'Deals +3...15 damage. Remove 1 of your Dervish enchantments. Removal effect: unblockable, removes a stance.'),
(1490, 'Pious Assault', 'If it hits, this attack deals +10..20 damage and removes 1 Dervish enchantment. If a Dervish enchantment was removed, this skill recharges 75% faster and adjacent foes take 10..30 damage.', 'Deals +10...20 damage. Removes 1 of your Dervish enchantments. Removal Effect: this skill recharges 75% faster and adjacent foes take 10...30 damage.', 'Pious Assault (PvP)', 'You remove 1 Dervish enchantment. If a Dervish enchantment was removed, this skill recharges 75% faster and adjacent foes take 10..30 damage.', 'Removes 1 of your Dervish enchantments. Removal effect: this skill recharges 75% faster and adjacent foes take 10...30 damage.'),
(1491, 'Mystic Twister', 'Deals 10..60 cold damage to all nearby foes. If you are enchanted, this spell deals an additional 10..60 cold damage.', 'Deals 10...60 cold damage to all nearby foes. Deals 10...60 more cold damage if you are enchanted.', '', '', ''),
(1493, 'Grenth\'s Fingers', 'All nearby foes are struck for 10..40 cold damage. For 30 seconds, your attacks deal cold damage. When this enchantment ends, you transfer  1..2 condition[s] to all nearby foes. ', 'Flash Enchantment Spell. (30 seconds.) Your attacks deal cold damage. Initial effect: hits nearby foes for 10...40 cold damage. End effect: transfers 1...2 conditions to all nearby foes.', '', '', ''),
(1495, 'Aura of Thorns', 'All nearby foes begin Bleeding for 5..15 seconds. For 30 seconds, this enchantment does nothing. When this enchantment ends, all nearby foes are Crippled for 3..8 seconds.', 'Flash Enchantment Spell. (30 seconds.) Initial effect: inflicts Bleeding condition (5...15 seconds) on nearby foes. End effect: inflicts Crippled condition (3...8 seconds) on nearby foes.', 'Aura of Thorns (PvP)', 'All nearby foes begin Bleeding for 5..15 seconds. For 30 seconds, this enchantment does nothing. When this enchantment ends, all nearby foes are Crippled for 3..8 seconds.', 'Flash Enchantment Spell. (30 seconds.) Initial effect: inflicts Bleeding condition (5...15 seconds) on nearby foes. End effect: inflicts Crippled condition (3...8 seconds) on nearby foes.'),
(1496, 'Balthazar\'s Rage', ' All nearby foes are set on fire for 1..3 second[s]. For 20 seconds afterward, this enchantment does nothing. When this enchantment ends, you gain 1..2 strike[s] of adrenaline if any foes are within earshot.', 'Flash Enchantment Spell. (20 seconds.) Initial Effect: Inflicts burning (1...3 seconds) on nearby foes. End effect: gain 1...2 strikes of adrenaline if any foes are within earshot.', '', '', ''),
(1497, 'Dust Cloak', ' All adjacent foes are struck for 10..40 earth damage. For 30 seconds, your attacks deal earth damage. When this enchantment ends, all adjacent foes are Blinded for 1..4 second[s].', 'Flash Enchantment Spell. (30 seconds.) Your attacks deal earth damage. Initial effect: deals 10...40 earth damage to adjacent foes. End effect: inflicts Blindness condition (1...4 seconds) on adjacent foes.', 'Dust Cloak (PvP)', ' All adjacent foes are struck for 10..40 earth damage. For 30 seconds, your attacks deal earth damage. When this enchantment ends, all adjacent foes are Blinded for 1..4 second[s].', 'Flash Enchantment Spell. (30 seconds.) Your attacks deal earth damage. Initial effect: deals 10...40 earth damage to adjacent foes. End effect: inflicts Blindness condition (1...4 seconds) on adjacent foes.'),
(1498, 'Staggering Force', 'All nearby foes are struck for 10..40 earth damage. For 30 seconds, your attacks deal earth damage. When this enchantment ends, all nearby foes have Cracked Armor for 1..10 second[s].', 'Flash Enchantment Spell. (30 seconds.) Your attacks deal earth damage. Initial effect: deals 10...40 earth damage to nearby foes. End effect: inflicts Cracked Armor condition (1...10 seconds) on nearby foes.', '', '', ''),
(1499, 'Pious Renewal', 'For 8 seconds, nothing happens. When this enchantment ends, Pious Renewal is recharged and you gain 0..5 Energy and 0..30 Health.', 'Elite Flash Enchantment Spell. (8 seconds.) End Effect: recharges itself and you gain 0...5 Energy and 0...30 Health.', '', '', ''),
(1500, 'Mirage Cloak', 'For 1..7 second[s], you have a 40..80% chance to block incoming attacks. When you cast this enchantment, all nearby foes are struck for 10..40 earth damage.', 'Flash Enchantment Spell. (1...7 seconds.) You have 40...80% chance to block. Initial Effect: deals 10...40 earth damage to nearby foes.', '', '', ''),
(1502, 'Arcane Zeal', 'For 10 seconds, whenever you cast a spell, you gain 1 Energy for each enchantment on you (maximum 1..7 Energy).', '(10 seconds.) You gain 1 Energy (maximum 1...7) for each enchantment on you whenever you cast a spell.', '', '', ''),
(1503, 'Mystic Vigor', 'For 20 seconds, every time you successfully hit with an attack, you gain 3..15 Health for each enchantment on you (maximum 25 Health).', '(20 seconds.) You gain 3...15 Health (maximum 25) for each enchantment on you whenever you hit with an attack.', '', '', ''),
(1504, 'Watchful Intervention', ' For 60 seconds, the next time target ally\'s Health drops below 25%, that ally is healed for 50..200 Health.', '(60 seconds.) Heals for 50...200 the next time damage drops target ally\'s Health below 25%.', '', '', ''),
(1505, 'Vow of Piety', 'For 20 seconds, you have +24 armor and +1..4 Health regeneration. Vow of Piety renews whenever an enchantment on you ends.', '(20 seconds) +24 armor and +1...4 Health regeneration. Renewal: Whenever an enchantment on you ends.', '', '', ''),
(1506, 'Vital Boon', 'For 20 seconds, you have +40..100 maximum Health. When this enchantment ends, you are healed for 75..200 Health.', '(20 seconds.) You have +40...100 maximum Health. End effect: Heals you for 75...200.', '', '', ''),
(1507, 'Heart of Holy Flame', 'All nearby foes take 5..30 holy damage. For 30 seconds, your attacks deal holy damage. When this enchantment ends, all nearby foes are set on fire for 2..5 seconds.', 'Flash Enchantment Spell. (30 seconds.) Your attacks deal holy damage. Initial effect: deals 5...30 holy damage to nearby foes. End effect: inflicts Burning condition (2...5 seconds) on nearby foes.', '', '', ''),
(1508, 'Extend Enchantments', 'For 10 seconds, your next Dervish enchantment lasts 10..150% longer.', '(10 seconds.) Your next Dervish enchantment lasts 10...150% longer.', '', '', ''),
(1509, 'Faithful Intervention', 'If damage drops your Health below 50%, Faithful Intervention ends. When Faithful Intervention ends, you are healed for 30..150 Health.', 'You gain 30...150 Health the next time damage drops your Health below 50%.', '', '', ''),
(1510, 'Sand Shards', 'For 30 seconds, the next 1..5 time[s] you hit with a scythe, all other adjacent foes take 10..60 earth damage.', 'Flash Enchantment Spell. (30 seconds.) Deal 10...60 earth damage to all other adjacent foes whenever you hit with your scythe. <i>Ends after 1...5 hits.</i>', '', '', ''),
(1512, 'Lyssa\'s Haste', 'For 3..15 seconds, your Dervish enchantments recharge 33% faster. When you activate this enchantment, all adjacent foes are interrupted. When this enchantment ends, all adjacent foes are interrupted. 50% failure chance unless Wind Prayers 5 or higher.', 'Flash Enchantment Spell. (3...15 seconds.) Your Dervish enchantments recharge 33% faster. Initial Effect: interrupts all adjacent foes. End Effect: interrupts all adjacent foes. <i>50% failure chance unless Wind Prayers 5 or higher.</i>', 'Lyssa\'s Haste (PvP)', 'For 3..15 seconds, your Dervish enchantments recharge 33% faster. When you activate this enchantment, all adjacent foes are interrupted. When this enchantment ends, all adjacent foes are interrupted. 50% failure chance unless Wind Prayers 5 or higher.', 'Flash Enchantment Spell. (3...15 seconds.) Your Dervish enchantments recharge 33% faster. Initial Effect: interrupts all adjacent foes. End Effect: interrupts all adjacent foes. <i>50% failure chance unless Wind Prayers 5 or higher.</i>'),
(1513, 'Guiding Hands', 'For 20 seconds, your next 0..3 attack[s] cannot be blocked. When activated, this skill removes the Blindness condition.', '(20 seconds.) Your next 0...3 attacks cannot be blocked. Initial effect: removes Blindness.', 'Guiding Hands (PvP)', 'For 20 seconds, your next 0..3 attack[s] cannot be blocked. When activated, this skill removes the Blindness condition.', '(20 seconds.) Your next 0...3 attacks cannot be blocked. Initial effect: removes Blindness.'),
(1514, 'Fleeting Stability', 'For 2..6 seconds, you cannot be knocked down and move 25% faster. This enchantment ends prematurely if it prevents a knockdown.', 'Flash Enchantment Spell. (2...6 seconds.) You cannot be knocked down and move 25% faster. Ends if knockdown prevented.', '', '', ''),
(1515, 'Armor of Sanctity', 'All adjacent foes suffer from Weakness for 5..15 seconds.  For 15 seconds, you take 5..20 less damage from foes suffering from a condition.', 'Inflicts Weakness condition on all adjacent foes (5...15). You take 5...20 less damage from foes with a condition. (15 seconds.)', '', '', ''),
(1516, 'Mystic Regeneration', ' For 5..20 seconds, you have  +1..4 Health regeneration for each enchantment (maximum of 8) on you.', '(5...20 seconds.) You have +1...4 Health regeneration for each enchantment (maximum of 8) on you.', 'Mystic Regeneration (PvP)', ' For 5..20 seconds, you have  +1..4 Health regeneration for each enchantment (maximum of 3) on you.', '(5...20 seconds.) You have +1...4 Health regeneration for each enchantment (maximum of 3) on you.'),
(1517, 'Vow of Silence', 'For 5..10 seconds, you cannot be the target of spells, and you cannot cast spells.', '(5...10 seconds.) Spells cannot target you. <i>You cannot cast spells.</i>', '', '', ''),
(1518, 'Avatar of Balthazar', 'For 10..90 seconds, you gain +20 armor against physical damage, you gain adrenaline 25% faster, your attacks deal holy damage, and whenever you lose a Dervish enchantment, nearby foes are set on fire for 1..3 second[s]. This skill is disabled for 45 seconds.', '(10...90 seconds.) You gain +20 armor against physical damage, you gain adrenaline 25% faster, your attacks deal holy damage, you inflict Burning (1...3 seconds) on nearby foes whenever you lose a Dervish enchantment. This skill is disabled for 45 seconds.', '', '', ''),
(1519, 'Avatar of Dwayna', 'For 10..90 seconds, your attacks deal holy damage. Whenever you use a Dervish attack skill, you lose 1 hex. Whenever you lose a Dervish enchantment, all allies in earshot are healed for 5..50 Health. This skill is disabled for 45 seconds.', '(10...90 seconds.) You deal holy damage. Whenever you use a Dervish attack skill, you lose 1 hex. Heal allies in earshot for 5...50 Health when you lose a Dervish enchantment. <i>This skill is disabled for 45 seconds.</i>', 'Avatar of Dwayna (PvP)', 'For 10..90 seconds, your attacks deal holy damage. Whenever you use a Dervish attack skill, you lose 1 hex. Whenever you lose a Dervish enchantment, you are healed for 5..50 Health. This skill is disabled for 45 seconds.', '(10...90 seconds.) Deal holy damage. You lose 1 hex whenever you use a Dervish attack skill. Heal yourself for 5...50 Health whenever you lose a Dervish enchantment. This skill is disabled for 45 seconds.'),
(1520, 'Avatar of Grenth', 'For 10..90 seconds, your scythe attacks deal dark damage, and steal 0..12 Health. You are immune to Disease, and inflict Disease on all adjacent foes for 3 seconds whenever you lose a Dervish enchantment. This skill is disabled for 45 seconds.', '(10...90 seconds.) Your scythe attacks deal dark damage and steal 0...12 Health. You are immune to Disease. Apply Disease to all adjacent foes (3 seconds) when you lose a Dervish enchantment. This skill is disabled for 45 seconds.', '', '', ''),
(1521, 'Avatar of Lyssa', 'For 10..90 seconds, your Dervish enchantments recharge 50% faster and your attacks deal chaos damage. Whenever you lose a Dervish enchantment, steal 1 Energy from all nearby foes. This skill is disabled for 45 seconds.', '(10...90 seconds.) Your Dervish enchantments recharge 50% faster and deal chaos damage with attacks. Steal 1 Energy from nearby foes when you lose a Dervish enchantment. This skill is disabled for 45 seconds.', '', '', ''),
(1522, 'Avatar of Melandru', 'For 10..90 seconds, you have +150 Health, +30 elemental armor, and your attacks deal earth damage. Whenever you lose a Dervish enchantment, all party members in earshot lose 1 condition. This skill is disabled for 45 seconds.', '(10...90 seconds.) You have +150 maximum Health, +30 elemental armor, and your attacks deal earth damage. Cure 1 condition from all party members in earshot whenever you lose a Dervish enchantment. <i>This skill is disabled for 45 seconds.</i>', 'Avatar of Melandru (PvP)', 'For 10..90 seconds, you have +100 Health, +30 elemental armor, and your attacks deal earth damage. Whenever you lose a Dervish enchantment you lose 2 conditions. This skill is disabled for 45 seconds.', '(10...90 seconds.) You have +100 maximum Health, +30 elemental armor, and your attacks deal earth damage. Cure 2 conditions from yourself whenever you lose a Dervish enchantment. This skill is disabled for 45 seconds.'),
(1523, 'Meditation', 'Lose all adrenaline. For 20 seconds, you gain 1..4 Energy every time an enchantment on you ends.', '(20 seconds.) <i>Lose all adrenaline.</i> Gain 1...4 Energy whenever an enchantment on you ends.', '', '', ''),
(1524, 'Eremite\'s Zeal', 'You gain 1..3 Energy for each foe within earshot, maximum 8 Energy. For 10 seconds afterward, this enchantment does nothing. When this enchantment ends, you gain 1..3 Energy for each foe within earshot, maximum 8 Energy.', '(10 seconds.) Initial effect: you gain 1...3 Energy (maximum 8) for each foe in earshot. End effect: you gain 1...3 Energy (maximum 8) for each foe in earshot.', '', '', ''),
(1525, 'Natural Healing', 'You are healed for 50..170 Health. If you are not enchanted, this spell activates 50% faster.', 'Heals you for 50...170. This skill activates 50% faster if you are not enchanted.', '', '', ''),
(1526, 'Imbue Health', 'Target other ally is healed for 5..50% of your current Health (maximum 300 Health).', 'Heals for 5...50% of your current Health (maximum 300). <i>Cannot self-target.</i>', '', '', ''),
(1527, 'Mystic Healing', 'You are healed for 5..65 Health. Also heals all enchanted party members for 5..65 Health.', 'Heals you for 5...65. Heals all enchanted party members for 5...65 Health.', 'Mystic Healing (PvP)', 'You are healed for 5..65 Health. Also heals all enchanted party members for 5..65 Health.', 'Heals you for 5...65. Heals all enchanted party members for 5...65 Health.'),
(1528, 'Dwayna\'s Touch', 'Target touched ally is healed for 15..60 Health for each Enchantment on you (maximum 150).', 'Touch Spell. Heals for 15...60 (maximum 150) for each enchantment on you.', '', '', ''),
(1529, 'Pious Restoration', 'You gain 80..150 Health and remove 1 Dervish enchantment. If an enchantment was removed in this way, you also lose 1..2 hex[es].', 'Gain 80...150 Health and remove 1 Dervish enchantment. Removal effect: lose 1...2 hexes.', '', '', ''),
(1530, 'Signet of Pious Light', 'Remove 1 Dervish enchantment. Target ally is healed for 30..150 Health. If an enchantment was removed in this way, this signet recharges 75% faster.', 'Heals for 30...150. Removes one of your Dervish enchantments. Removal effect: recharges 75% faster.', '', '', ''),
(1531, 'Intimidating Aura', 'For 60 seconds, you have +40..100 max Health and take 1..10 less damage from foes with less Health than you.', '(60 seconds.) You have +40...100 max Health and take -1...10 damage from foes with less Health than you.', '', '', ''),
(1532, 'Mystic Sandstorm', 'Create a sandstorm at your location that lasts 3 seconds. Each second, foes nearby this location take 10..20 earth damage. Attacking foes take an additional 10..20 damage. If you are enchanted when you cast this spell, it lasts twice as long.', '(3 seconds.) Deals 10...20 earth damage each second. Deals an additional 10...20 damage to attacking foes. Hits foes nearby your initial location. Lasts twice as long if you are enchanted.', '', '', ''),
(1533, 'Winds of Disenchantment', ' Lose 1 Dervish enchantment.  If a Dervish enchantment was removed in this way, all nearby foes lose 1 enchantment and take 20..80 cold damage.', 'Remove one of your Dervish enchantments. Removal effect: all nearby foes lose 1 enchantment and take 20...80 cold damage.', '', '', ''),
(1534, 'Rending Touch', 'Deals 15..65 cold damage to target foe. You lose one Dervish enchantment. If an enchantment was removed, target foe loses 1 enchantment and you gain 1 strike of adrenaline.', 'Touch Spell. Deals 15...65 cold damage. Lose 1 Dervish enchantment. Removal effect: target foe loses 1 enchantment and you gain 1 strike of adrenaline.', '', '', ''),
(1535, 'Crippling Sweep', 'If this attack hits a foe, that foe is Crippled for 3..12 seconds. This skill deals +3..15 extra damage if that foe is moving.', '(3...12 seconds.) Inflicts Cripple <sub>sic</sub> condition. Deals +3...15 damage to moving foes.', '', '', ''),
(1536, 'Wounding Strike', 'If this attack hits, you do +5..20 damage,  target foe suffers from Bleeding for 5..20 seconds, and you lose 1 Dervish enchantment. If an enchantment is removed, target foe also suffers from a Deep Wound for 5..20 seconds.', 'Deals +5...20 damage. Inflict Bleeding condition (5...20 seconds). Removes 1 Dervish enchantment. Also inflicts Deep Wound condition (5...20 seconds) if an enchantment is removed.', 'Wounding Strike (PvP)', 'If this attack hits, target foe suffers from Bleeding for 5..20 seconds, and you lose 1 Dervish enchantment. If an enchantment is removed, target foe also suffers from a Deep Wound for 5..20 seconds.', 'Inflicts Bleeding condition (5...20 seconds). Removes 1 Dervish enchantment. Also inflicts Deep Wound condition (5...20 seconds) if an enchantment is removed.'),
(1537, 'Wearying Strike', 'You remove 1 Dervish enchantment. If an enchantment was removed, you inflict a Deep Wound for 3..10 seconds. You suffer from Weakness for 10 seconds if an enchantment is not removed.', 'Remove 1 Dervish Enchantment. Removal Effect: Inflicts Deep Wound condition (3...10 seconds). <i>You are Weakened (10 seconds) if an enchantment is not lost.</i>', '', '', ''),
(1538, 'Lyssa\'s Assault', 'This attack interrupts an action if it hits. If you are enchanted, any skill you interrupt is disabled for additional 1..10 second[s]. This attack does 50% normal damage.', 'Interrupts an action. If you are enchanted, the interrupted skill is disabled for an additional 1...10 seconds. This attack does 50% of normal damage.', '', '', ''),
(1539, 'Chilling Victory', 'If it hits, this attack strikes for +3..15 damage. For each foe hit who has less Health than you, that foe and all adjacent foes are struck for 10..30 cold damage.', 'Deals +3...15 damage. Deals 10...30 cold damage to each foe hit who has less Health than you and foes adjacent to those targets.', '', '', ''),
(1540, 'Conviction', 'For 10 seconds, you gain +10 armor if you have a condition and gain 1..3 Health regen  for each condition you are suffering from. When this enchantment ends, you are cured of 1..2 condition[s].', 'Flash Enchantment Spell. (10 seconds.) Gain +10 armor while conditioned. Gain 1...3 Health regeneration for each condition you have. End Effect: Remove 1...2 conditions.', '', '', ''),
(1541, 'Enchanted Haste', 'For 7 seconds, you move 25% faster. If this enchantment ends prematurely, you lose 1 condition.', 'Flash Enchantment Spell. (7 seconds). You move 25% faster. Lose 1 condition if this enchantment ends prematurely.', '', '', ''),
(1542, 'Pious Concentration', 'For 5..20 seconds, you cannot be interrupted, but each time you would have been interrupted, you lose 1 Dervish enchantment or Pious Concentration ends.', '(5...20 seconds.) Prevents interrupts. <i>Prevention cost: you lose one Dervish enchantment or Pious Concentration ends.</i>', '', '', ''),
(1543, 'Pious Haste', 'You remove 1 Dervish enchantment and for 1..7 second[s] you move 25% faster. If an enchantment was removed you move 50% faster instead.', '(1...7 seconds.) Lose 1 Dervish enchantment and move 25% faster. Removal Effect: Run 50% faster instead.', '', '', ''),
(1544, 'Whirling Charge', 'For 1..6 second[s], you move 33% faster than normal. The next time you strike a foe, all other nearby foes take 10..60 cold damage and this enchantment ends.', 'Flash Enchantment Spell. (1...6 seconds.) You move 33% faster. Deal 10...60 cold damage to all other nearby foes the next time you hit a foe and this enchantment ends.', '', '', ''),
(1545, 'Test of Faith', 'Deals 15..65 cold damage and takes 1 enchantment from target foe. If that foe was not enchanted, that foe is Dazed for 1..4 second[s].', 'Touch Spell. Deals 15...65 cold damage and removes 1 enchantment. Target foe is Dazed for 1...4 seconds if that foe was not enchanted.', '', '', ''),
(1546, 'Blazing Spear', 'If this attack hits, it deals +5..25 damage and sets target foe on Fire  for 1..3 second[s].', 'Deals +5...25 damage. Inflicts Burning condition (1...3 seconds).', '', '', ''),
(1547, 'Mighty Throw', 'Your spear moves three times faster. If it hits, you deal +10..40 damage.', 'Deals +10...40 damage. This spear moves three times faster.', '', '', ''),
(1548, 'Cruel Spear', 'If this attack hits, you deal +1..31 damage. If it hits a non-moving target, you inflict a Deep Wound for 5..20 seconds.', 'Deals +1...31 damage. Inflicts Deep Wound condition (5...20 seconds) if target is not moving.', '', '', ''),
(1549, 'Harrier\'s Toss', 'If this attack hits, you deal +5..20 damage. If this attack hits a moving foe, it deals an additional 5..30 damage.', 'Deals +5...20 damage. Deals 5...30 more damage if target is moving.', 'Harrier\'s Toss (PvP)', 'If this attack hits, you deal +5..20 damage. If this attack hits a moving foe, it deals an additional 5..30 damage.', 'Deals +5...20 damage. Deals 5...30 more damage if target is moving.'),
(1550, 'Unblockable Throw', 'If this attack hits, you deal +10..40 damage. This attack cannot be blocked.', 'Deals +10...40 damage. Unblockable.', '', '', ''),
(1551, 'Spear of Lightning', 'If this attack hits, it deals +8..20 lightning damage. This attack has 25% armor penetration.', 'Deals +8...20 lightning damage. 25% armor penetration.', '', '', ''),
(1552, 'Wearying Spear', 'If this attack hits, you deal +10..40 damage. You are Weakened for 5 seconds.', 'Deals +10...40 damage. <i>You are Weakened (5 seconds).</i>', '', '', ''),
(1553, 'Anthem of Fury', 'For 10 seconds, all party members within earshot gain 1..4 strikes of adrenaline the next time they use an attack skill.', '(10 seconds.) Party members in earshot gain 1...4 adrenaline with their next attack skill.', '', '', ''),
(1554, 'Crippling Anthem', 'For 10 seconds, the next attack skill used by each ally within earshot causes Crippling for 5..15 seconds.', '(10 seconds.) Allies in earshot inflict Crippled condition (5...15 seconds) with their next attack skill.', '', '', ''),
(1555, 'Defensive Anthem', 'For 4..10 seconds, each party member within earshot has a 50% chance to block incoming attacks. This chant ends if that party member hits with an attack skill.', '(4...10 seconds.) Party members in earshot have 50% chance to block. <i>Ends when hitting with an attack skill.</i>', 'Defensive Anthem (PvP)', 'For 4..10 seconds, each party member within earshot has a 50% chance to block incoming attacks. This chant ends if that party member hits with an attack skill.', '(4...10 seconds.) Party members in earshot have 50% chance to block. <i>Ends when hitting with an attack skill.</i>'),
(1556, 'Godspeed', 'For 5..20 seconds, all allies within earshot move 25% faster while under the effects of an enchantment.', '(5...20 seconds.) Allies in earshot move 25% faster while enchanted.', '', '', ''),
(1557, 'Anthem of Flame', 'For 10 seconds, the next attack skill used by each party member within earshot also causes Burning for 1..3 second[s].', '(10 seconds.) Party members in earshot inflict Burning condition (1...3 seconds) with their next attack skill.', '', '', ''),
(1558, '"Go for the Eyes!"', 'For 10 seconds, the next time each ally within earshot makes an attack, that attack has an additional 30..100% chance to critical.', '(10 seconds.) Allies in earshot have +30...100% to land a critical hit with their next attack.', '"Go for the Eyes!" (PvP)', 'For 10 seconds, the next time each ally within earshot makes an attack, that attack has an additional 30..75% chance to critical.', '(10 seconds.) Allies in earshot have +30...75% to land a critical hit with their next attack.'),
(1559, 'Anthem of Envy', 'For 10 seconds, the next attack skill used by each ally within earshot deals +10..25 damage against foes with more than 50% Health.', '(10 seconds.) Allies in earshot do +10...25 damage with their next attack skill. <i>Damage bonus only applies to foes with more than 50% Health.</i>', 'Anthem of Envy (PvP)', 'For 10 seconds, the next attack skill used by each ally within earshot deals +10..20 damage against foes with more than 50% Health.', '(10 seconds.) Allies in earshot do +10...20 damage with their next attack skill. <i>Damage bonus only applies to foes with more than 50% Health.</i>');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1560, 'Song of Power', 'For 5..20 seconds, each ally within earshot gains 4 Energy regeneration until that ally uses a Skill.', '(5...20 seconds.) Allies in earshot gain +4 Energy regeneration. <i>Ends when using a skill.</i>', '', '', ''),
(1561, 'Zealous Anthem', 'For 10 seconds, the next time each ally within earshot uses an attack skill, that ally gains 1..8 Energy.', '(10 seconds.) Allies in earshot gain 1...8 Energy with their next attack skill.', '', '', ''),
(1562, 'Aria of Zeal', 'For 10 seconds, the next time each ally within earshot uses a Spell, that ally gains 1..6 Energy.', '(10 seconds.) Allies in earshot gain 1...6 Energy with their next spell.', '', '', ''),
(1563, 'Lyric of Zeal', 'For 10 seconds, the next time each ally within earshot uses a signet, that ally gains 1..8 Energy.', '(10 seconds.) Allies in earshot gain 1...8 Energy with their next signet.', '', '', ''),
(1564, 'Ballad of Restoration', 'For 10 seconds, the next time each party member within earshot takes damage, that party member gains 15..75 Health.', '(10 seconds.) Party members in earshot gain 15...75 Health the next time they take damage.', 'Ballad of Restoration (PvP)', 'For 10 seconds, the next time each party member within earshot takes damage, that party member gains 15..75 Health.', '(10 seconds.) Party members in earshot gain 15...75 Health the next time they take damage.'),
(1565, 'Chorus of Restoration', 'For 10 seconds, the next time each ally within earshot uses a shout or chant, that ally is healed for 30..90 Health.', '(10 seconds.) Allies in earshot are healed for 30...90 with their next shout or chant.', '', '', ''),
(1566, 'Aria of Restoration', 'For 10 seconds, the next time each party member within earshot uses a spell, that party member gains 30..90 Health.', '(10 seconds.) Party members in earshot gain 30...90 Health with their next spell.', '', '', ''),
(1567, 'Song of Concentration', 'For 10 seconds, the next skill used by each ally within earshot cannot be interrupted.', '(10 seconds.) Allies in earshot are uninterruptible with their next skill.', '', '', ''),
(1568, 'Anthem of Guidance', 'For 10 seconds, the next attack skill used by each party member within earshot cannot be blocked.', '(10 seconds.) Party members in earshot are unblockable with their next attack skill.', '', '', ''),
(1569, 'Energizing Chorus', 'For 10 seconds, the next Shout or Chant used by each ally within earshot costs 3..7 less Energy.', '(10 seconds.) The next shout or chant costs 3...7 less Energy for allies within earshot.', '', '', ''),
(1570, 'Song of Purification', 'For 20 seconds, the next 1..3 skill[s] used by each ally within earshot remove 1 condition from that ally.', '(20 seconds.) Allies in earshot lose one condition with their next 1...3 skills.', '', '', ''),
(1571, 'Hexbreaker Aria', 'For 10 seconds, the next time each ally within earshot casts a spell, that ally loses 1 hex.', '(10 seconds.) Allies in earshot lose one hex with their next spell.', '', '', ''),
(1572, '"Brace Yourself!"', 'For 5..15 seconds, the next time target other ally would be knocked down, all nearby foes take 15..75 damage instead.', '(5...15 seconds.) Prevents the next knock-down and deals 15...75 damage to all foes near target ally. <i>Cannot self-target.</i>', '"Brace Yourself!" (PvP)', 'For 5..15 seconds, the next time target other ally would be knocked down, 1 nearby foe takes 15..75 damage instead.', '(5...15 seconds.) Prevents the next knock-down and deals 15...75 damage to one foe near target ally. <i>Cannot self-target.</i>'),
(1573, 'Awe', ' If this skill hits a knocked-down foe, that foe becomes Dazed for 5..15 seconds. Awe has half the normal range.', 'Half Range Skill. Inflicts Dazed condition (5...15 seconds). <i>No effect unless target is knocked-down.</i>', '', '', ''),
(1574, 'Enduring Harmony', 'For 10..35 seconds, chants and shouts last 50% longer on target non-spirit ally.', '(10...35 seconds.) Chants and shouts last 50% longer on target ally. <i>Cannot target spirits.</i>', '', '', ''),
(1575, 'Blazing Finale', 'For 10..35 seconds, whenever a chant or shout ends on target non-spirit ally, all foes adjacent to that ally are set on Fire for 1..7 second[s].', '(10...35 seconds.) Inflicts Burning condition (1...7 seconds) to adjacent foes whenever a chant or shout ends on target ally. <i>Cannot target spirits.</i>', 'Blazing Finale (PvP)', 'For 10..35 seconds, whenever a chant or shout ends on target non-spirit ally, all foes adjacent to that ally are set on Fire for 1..3 second[s].', '(10...35 seconds.) Inflicts Burning condition (1...3 seconds) to adjacent foes whenever a chant or shout ends on target ally. Cannot target spirits.'),
(1576, 'Burning Refrain', 'For 20 seconds, if target non-Spirit ally hits a foe with more Health than that ally, that foe is set on Fire for 1..3 seconds. This Echo is reapplied every time a Chant or Shout ends on that ally.', '(20 seconds.) Inflicts Burning condition (1...3 seconds) if target ally hits a foe with more Health. Renewal: Whenever a chant or shout ends on that ally. <i>Cannot target spirits.</i>', '', '', ''),
(1577, 'Finale of Restoration', 'For 10..35 seconds, whenever a chant or shout ends on target non-spirit ally, that ally is healed for 15..75 Health.', '(10...35 seconds.) Target ally gains <sub>sic</sub> 15...75 Health whenever a shout or chant ends on that ally. <i>Cannot target spirits.</i>', 'Finale of Restoration (PvP)', 'For 10..35 seconds, the next 5 times that a chant or shout ends on target non-spirit ally, that ally is healed for 15..75 Health.', '(10...35 seconds.) Target ally gains <sub>sic</sub> 15...75 Health the next 5 times a shout or chant ends on that ally. <i>Cannot target spirits.</i>'),
(1578, 'Mending Refrain', 'For 15 seconds, target non-spirit ally has +2..3 Health regeneration. This echo is reapplied every time a chant or shout ends on that ally.', '(15 seconds.) Target ally has +2...3 Health regeneration. Renewal: whenever a chant or shout ends on that ally. <i>Cannot target spirits.</i>', 'Mending Refrain (PvP)', 'For 15 seconds, you have +2..3 Health regeneration. This echo is reapplied every time a chant or shout ends on you.', '(15 seconds.) You have +2...3 Health regeneration. Renewal: whenever a chant or shout ends on you.'),
(1579, 'Purifying Finale', 'For 10..35 seconds, target non-spirit ally loses 1 condition whenever a chant or shout ends on that ally.', '(10...35 seconds.) Target ally loses one condition whenever a chant or shout ends on that ally. <i>Cannot target spirits.</i>', '', '', ''),
(1580, 'Bladeturn Refrain', 'For 20 seconds, target non-spirit ally has a 5..20% chance to block incoming attacks. This echo is reapplied every time a chant or shout ends on that ally.', '(20 seconds.) Target ally has 5...20% chance to block. Renewal: Whenever a chant or shout ends on that ally. <i>Cannot target spirits.</i>', 'Bladeturn Refrain (PvP)', 'For 20 seconds, target non-spirit ally has +10..40 armor against slashing damage. This echo is reapplied every time a chant or shout ends on that ally.', '(20 seconds.) Target ally has +10...40 armor against slashing damage. Renewal: Whenever a chant or shout ends on that ally. <i>Cannot target spirits.</i>'),
(1581, 'Glowing Signet', 'If target foe is Burning, you gain 5..15 Energy.', 'You gain 5...15 Energy if target foe is Burning.', '', '', ''),
(1583, 'Leader\'s Zeal', 'For each nearby ally, you gain 2 Energy (maximum 8..12 Energy).', 'You gain 2 Energy (maximum 8...12 Energy) for each nearby ally.', '', '', ''),
(1584, 'Leader\'s Comfort', 'You gain 30..75 Health. For each ally within earshot, you also gain +10..20 Health (maximum 140 Health).', 'You gain 30...75 Health. You also gain 10...20 Health (maximum 140) for each ally in earshot.', '', '', ''),
(1585, 'Signet of Synergy', 'Target other ally is healed for 40..100 Health. If you are not under the effects of an enchantment, you are also healed for 40..100 Health.', 'Heal target ally for 40...100. You are also healed for 40...100 if you are not enchanted. <i>Cannot self-target.</i>', '', '', ''),
(1586, 'Angelic Protection', 'For 10 seconds, any time target other ally takes more than 250..100 damage per second, that ally is healed for any damage over that amount.', '(10 seconds.) Each second that target ally takes damage over 250...100, that ally is healed for any damage over that amount. <i>Cannot self target.</i>', '', '', ''),
(1587, 'Angelic Bond', 'For 10 seconds, the next time an ally within earshot would take fatal damage, that damage is negated and that ally is healed for 20..200 Health. Angelic Bond ends on all other allies.', '(10 seconds.) The next time an ally within earshot would take fatal damage, that damage is negated and that ally is healed for 20...200. <i>Ends on other allies.</i>', '', '', ''),
(1588, 'Cautery Signet', 'All party members lose all conditions. You are set on Fire   for 1 second for each condition removed in this way.', 'All party members lose all conditions. <i>You begin Burning (one second for each condition removed).</i>', '', '', ''),
(1589, '"Stand Your Ground!"', 'For 5..20 seconds, all party members within earshot receive +24 armor when not moving.', '(5...20 seconds.) Party members in earshot gain +24 armor when not moving.', '"Stand Your Ground!" (PvP)', 'For 5..20 seconds, all party members within earshot receive +24 armor when not moving. This effect ends on an ally affected by this shout when that ally attacks.', '(5...20 seconds.) Party members in earshot gain +24 armor when not moving. Ends for an ally if that ally attacks.'),
(1590, '"Lead the Way!"', 'Target ally moves 25% faster for 1..5 seconds for each ally within earshot (maximum of 20 seconds).', 'Target ally moves 25% faster for 1...5 seconds (maximum of 20 seconds) for each ally in earshot.', '', '', ''),
(1591, '"Make Haste!"', 'For 5..20 seconds, target other ally moves 33% faster.  This skill ends if that ally successfully hits with an attack.', '(5...20 seconds.) Target ally moves 33% faster. <i>Ends if target ally hits with an attack. Cannot self-target.</i>', '', '', ''),
(1592, '"We Shall Return!"', 'All party members in earshot are resurrected with 25..50% Health and 5..20% Energy.', 'All party members in earshot are resurrected (25...50% Health and 5...20% Energy).', '"We Shall Return!" (PvP)', 'For 10 seconds, whenever party members within earshot use a resurrection skill, their fallen allies return with 10..75% more Health and 5..50% more Energy.', '(10 seconds.) Whenever party members in earshot use a resurrection skill, their fallen allies return with 10...75% more Health and 5...50% more Energy.'),
(1593, '"Never Give Up!"', 'All allies within earshot and below 75% Health gain 1..10 Energy.', 'Allies in earshot gain 1...10 Energy. <i>Only affects allies below 75% Health.</i>', '"Never Give Up!" (PvP)', 'All allies within earshot and below 75% Health gain 1..10 Energy.', 'Allies in earshot gain 1...10 Energy. <i>Only affects allies below 75% Health.</i>'),
(1594, '"Help Me!"', 'You gain 15..90 Health. For 1..10 seconds, other allies\' spells targeting you cast 50% faster.', '(1...10 seconds.) Other allies\' spells targeting you cast 50% faster. You gain 15...90 Health.', '"Help Me!" (PvP)', 'You gain 5..50 Health. For 1..10 seconds, other allies\' spells targeting you cast 50% faster.', '(1...10 seconds.) Other allies\' spells targeting you cast 50% faster. You gain 5...50 Health.'),
(1595, '"Fall Back!"', 'For 4..10 seconds, all allies within earshot gain 5..15 Health per second while moving and move 33% faster.  "Fall Back!" ends on an ally affected by this shout when that ally successfully hits with an attack.', '(4...10 seconds.) Allies in earshot gain 5...15 Health per second while moving and move 33% faster. <i>Ends for an ally if that ally hits with an attack.</i>', '"Fall Back!" (PvP)', 'For 4..10 seconds, all allies within earshot gain 5..15 Health per second while moving and move 33% faster. "Fall Back!" ends on an ally affected by this shout when that ally successfully hits with an attack.', '(4...10 seconds.) Allies in earshot gain 5...15 Health per second while moving and move 33% faster. <i>Ends for an ally if that ally hits with an attack.</i>'),
(1596, '"Incoming!"', 'For 4..10 seconds, all allies within earshot move 33% faster, and gain 5..15 Health while moving.', '(4...10 seconds) Allies in earshot move 33% faster and gain 5...15 Health while moving.', '"Incoming!" (PvP)', 'For 4..10 seconds, all allies within earshot move 33% faster, and gain 5..15 Health while moving.', '(4...10 seconds) Allies in earshot move 33% faster and gain 5...15 Health while moving.'),
(1597, '"They\'re on Fire!"', 'For 10 seconds, party members within earshot take 5..35% less damage from foes suffering from Burning.', '(10 seconds.) Party members in earshot take 5...35% less damage from Burning foes.', '', '', ''),
(1598, '"Never Surrender!"', 'For 15 seconds, all party members within earshot and below 75% Health gain +1..5 Health regeneration.', '(15 seconds.) Party members in earshot gain +1...5 Health regeneration. <i>Only affects party members below 75% Health.</i>', '"Never Surrender!" (PvP)', 'For 10 seconds, all party members within earshot and below 75% Health gain +1..5 Health regeneration.', '(10 seconds.) Party members in earshot gain +1...5 Health regeneration. <i>Only affects party members below 75% Health.</i>'),
(1599, '"It\'s Just a Flesh Wound."', 'Target other ally loses all conditions. If a condition was removed in this way, that ally moves 25% faster for 1..10 second[s].', 'Remove all conditions from target ally. That ally moves 25% faster (1...10 seconds) if a condition was removed. <i>Cannot self-target.</i>', '', '', ''),
(1600, 'Barbed Spear', 'If this attack hits, your target begins Bleeding for 5..20 seconds.', 'Inflicts Bleeding condition (5...20 seconds).', '', '', ''),
(1601, 'Vicious Attack', 'If this attack hits, you deal +5..20 damage. If you land a critical hit with this attack, target foe suffers from a Deep Wound for 5..15 seconds.', 'Deals +5...20 damage. Inflicts Deep Wound condition (5...15 seconds) with a critical hit.', '', '', ''),
(1602, 'Stunning Strike', 'If this attack hits, you deal +5..30 damage. If it hits a foe suffering from a condition, that foe is also Dazed for 4..10 seconds.', 'Deals +5...30 damage. Inflicts Dazed condition (4...10 seconds) if target has a condition.', '', '', ''),
(1603, 'Merciless Spear', 'If this attack hits a foe with less than 50% Health, that foe suffers from a Deep Wound for 5..20 seconds.', 'Inflicts Deep Wound condition (5...20 seconds). <i>No effect unless target has less than 50% Health.</i>', '', '', ''),
(1604, 'Disrupting Throw', 'If this attack hits a foe suffering from a condition, that foe is interrupted.', 'Interrupts actions. <i>No effect unless target has a condition.</i>', '', '', ''),
(1605, 'Wild Throw', 'If this attack hits, it deals +5..20 damage, and any stance being used by your target ends. This attack cannot be blocked. All of your non-spear attack skills are disabled for 3 seconds.', 'Deals +5...20 damage. Unblockable. Ends target\'s stance.gray', '', '', ''),
(1633, 'Malicious Strike', 'If this attack hits a foe suffering from a condition, you deal +10..30 damage and this attack results in a critical hit.', 'If target foe has a condition, this attack deals +10...30 damage and is a critical hit.', '', '', ''),
(1634, 'Shattering Assault', 'Must follow an off-hand attack. If it hits, you deal 5..50 damage and target foe loses one enchantment. This attack cannot be blocked.', 'Deals 5...50 damage. Removes one enchantment. Unblockable. <i>Must follow an off-hand attack.</i>', '', '', ''),
(1635, 'Golden Skull Strike', 'If you are under the effects of an enchantment and this attack hits, target foe is Dazed 1..5 seconds.', 'Inflicts Dazed condition (1...5 seconds). <i>No effect unless you are enchanted. </i>', '', '', ''),
(1636, 'Black Spider Strike', 'Must strike a hexed foe. If it hits, this attack strikes for +5..20 damage and target foe is Poisoned for 5..20 seconds.', 'Deals +5...20 damage. Inflicts Poisoned condition (5...20 seconds). <i>Must strike a hexed foe.</i>', '', '', ''),
(1637, 'Golden Fox Strike', 'If this attack hits, target foe takes +10..30 damage. If you are under the effects of an Enchantment, this attack cannot be blocked.', 'Deals +10...30 damage. Unblockable if you are Enchanted.', '', '', ''),
(1638, 'Deadly Haste', 'For 10..35 seconds, half-ranged spells cast 5..50% faster and recharge 5..50% faster.', '(10...35 seconds.) Your half-ranged spells cast 5...50% faster and recharge 5...50% faster.', '', '', ''),
(1639, 'Assassin\'s Remedy', 'For 30 seconds, the next 1..10 attack skills you use remove one condition.', '(30 seconds.) Your next 1...10 attack skills each remove one condition.', 'Assassin\'s Remedy (PvP)', 'For 30 seconds, the next 1..10 attack skills you use remove one condition.', '(30 seconds.) Your next 1...10 attack skills each remove one condition.'),
(1640, 'Fox\'s Promise', 'For 5..20 seconds, your dagger attacks cannot be blocked. This enchantment ends the next time you fail to hit.', '(5...20 seconds.) Your dagger attacks are unblockable. <i>Ends the next time you fail to hit.</i>', '', '', ''),
(1641, 'Feigned Neutrality', 'For 4..10 seconds, you have +7 Health regeneration and +80 armor. This enchantment ends if you successfully hit with an attack or use a skill.', '(4...10 seconds.) You have +7 Health regeneration and +80 armor. <i>Ends if you hit with an attack or use a skill.</i>', '', '', ''),
(1642, 'Hidden Caltrops', 'Your non-Assassin skills are disabled for 10 seconds. For 1..10 seconds, target foe moves 50% slower. When this hex ends that foe is Crippled for 1..15 seconds.', '(1...10 seconds.) Causes 50% slower movement. End effect: inflicts Crippled condition (1...15 seconds). Your non-Assassin skills are disabled (10 seconds.)', '', '', ''),
(1643, 'Assault Enchantments', 'Must follow a dual attack. Target foe loses all enchantments.', 'Removes all enchantments. <i>Must follow a dual attack.</i>', '', '', ''),
(1644, 'Wastrel\'s Collapse', 'Shadow Step to target foe. If target foe is not using a skill, that foe is knocked down. All of your non-dagger attack skills are disabled for 10 seconds.', 'Shadow Step to target foe. Causes knock-down if target foe is not using a skill. <i>Disables your non-dagger attack skills (10 seconds).</i>', '', '', ''),
(1645, 'Lift Enchantment', ' If target touched foe is knocked down, that foe loses one enchantment.', 'Touch Skill. Removes one enchantment.gray', '', '', ''),
(1646, 'Augury of Death', ' For 5..35 seconds, the next time damage would drop target foe\'s Health below 50%, you inflict a Deep Wound for 5..20 seconds and Shadow Step to that foe. This spell has half the normal range.', 'Half Range Hex Spell. (5...35 seconds.) Inflict Deep Wound condition (5...20 seconds) and Shadow Step to target foe the next time damage would drop this foe\'s Health below 50%.', '', '', ''),
(1647, 'Signet of Toxic Shock', 'If target foe is suffering from Poison, that foe takes 10..100 damage.', 'Deals 10...100 damage. <i>No effect unless target foe is Poisoned</i>', '', '', ''),
(1648, 'Signet of Twilight', 'For each hex on target foe, that foe loses one enchantment.', 'Removes one enchantment for each hex on target foe.', '', '', ''),
(1649, 'Way of the Assassin', 'For the next 20 seconds while wielding daggers, you attack 5..20% faster and have a +5..35% chance to land a critical hit.', '(20 seconds.) While wielding daggers, you attack 5...20% faster and have +5...35% chance to land a critical hit.', '', '', ''),
(1650, 'Shadow Walk', 'Shadow Step to target foe. For 15 seconds nothing happens. Your attack skills are disabled for 1 second, and your stances and enchantments are disabled for 10 seconds. When this stance ends, you return to your original location.', '(15 seconds.) Shadow Step to target foe. End effect: return to your original location. <i>Disables your attack skills for 1 second. Disables your stances and enchantments for 10 seconds.</i>', '', '', ''),
(1651, 'Death\'s Retreat', 'Shadow Step to target ally. If you have less Health than that ally, you gain 40..130 Health.', 'Gain 40...130 Health if you have less Health than target ally. Initial effect: Shadow Step to this ally. <i>Cannot self-target.</i>', '', '', ''),
(1652, 'Shadow Prison', 'Shadow Step to target foe. For 1..7 seconds, that foe moves 66% slower.', 'Shadow Step to target foe. This foe moves 66% slower (1...7 seconds).', '', '', ''),
(1653, 'Swap', ' You and target summoned creature Shadow Step to each other\'s location.', 'You and target summoned creature Shadow Step to each other\'s locations.', '', '', ''),
(1654, 'Shadow Meld', 'Shadow Step to target other ally. When you stop maintaining this Enchantment, you return to your original location.', 'Shadow Step to target ally. End effect: return to your original location. <i>Cannot self-target.</i>', '', '', ''),
(1655, 'Price of Pride', 'For 5..20 seconds, the next time target foe uses an elite skill, that foe loses 1..7 Energy.', '(5...20 seconds.) Causes 1...7 Energy loss the next time target foe uses an elite skill.', '', '', ''),
(1656, 'Air of Disenchantment', 'Remove one enchantment from target foe and all nearby foes. For 5..20 seconds, enchantments expire 150..300% faster on those foes.', 'Also hexes foes near your target (5...20 seconds). Remove one enchantment from target and nearby foes. Enchantments expire 150...300% faster on those foes.', '', '', ''),
(1657, 'Signet of Clumsiness', 'If target foe is attacking, that foe and all adjacent foes are interrupted and take 15..60 damage. Any foes using attack skills are knocked down.', 'Interrupts an attack for target foe and all adjacent foes. Interruption effect: deals 15...60 damage; knocks down foes using attack skills.', 'Signet of Clumsiness (PvP)', 'If target foe is attacking, that foe and all adjacent foes are interrupted and take 15..60 damage. Any foes using attack skills are knocked down.', 'Interrupts an attack for target foe and all adjacent foes. Interruption effect: deals 15...60 damage; knocks down foes using attack skills.'),
(1658, 'Symbolic Posture', 'For 5..20 seconds, the next signet you activate recharges 20..80% faster.', '(5...20 seconds.) Your next signet recharges 20...80% faster.', '', '', ''),
(1659, 'Toxic Chill', 'Target foe is struck for 15..75 cold damage. If that foe is under the effects of a hex or enchantment, that foe becomes Poisoned for 10..25 seconds.', 'Deals 15...75 cold damage. Inflicts Poisoned condition (10...25 seconds) if target foe is hexed or enchanted.', '', '', ''),
(1660, 'Well of Silence', 'Exploit target corpse to create a Well of Silence for 10..30 seconds. Foes within the well cannot use shouts or chants and suffer -1..4 Health degeneration.', '(10...30 seconds.) Foes in this well cannot use shouts and chants and have -1...4 Health degeneration. <i>Exploits a fresh corpse.</i>', '', '', ''),
(1661, 'Glowstone', 'Send a projectile that strikes for 5..50 earth damage if it hits. If this spell hits a weakened  foe, you gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage.', 'Projectile: deals 5...50 earth damage. You gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage if target foe is Weakened.', '', '', ''),
(1662, 'Mind Blast', 'Target foe is struck for 15..60 fire damage. If you have more Energy than target foe, you gain 1..8 Energy.', 'Deals 15...60 fire damage. You gain 1...8 Energy if you have more Energy than target foe.', '', '', ''),
(1663, 'Elemental Flame', ' For 10..35 seconds, whenever you apply an Elemental hex to a foe, that foe is set on fire for 1..5 second[s].', '(10...35 seconds.) Inflicts Burning condition (1...5 seconds) whenever you apply an Elemental hex to a target.', 'Elemental Flame (PvP)', 'For 5..20 seconds, whenever you apply an Elemental hex to a foe, that foe is set on fire for 1..2 second[s].', '(5...20 seconds.) Inflicts Burning condition (1...2 seconds) whenever you apply an Elemental hex to a target.'),
(1664, 'Invoke Lightning', 'Target foe and up to two other foes near your target are struck for 10..90 lightning damage. This spell has 25% armor penetration.', 'Deals 10...90 lightning damage. Hits two foes near target. 25% armor penetration.', '', '', ''),
(1683, 'Pensive Guardian', 'For 5..11 seconds, target ally has a 50% chance to block attacks from enchanted foes.', '(5...11 seconds.) 50% chance to block enchanted foes.', '', '', ''),
(1684, 'Scribe\'s Insight', 'For 10..35 seconds, you gain 3 Energy whenever you use a Signet.', '(10...35 seconds.) You gain 3 Energy whenever you use a signet.', '', '', ''),
(1685, 'Holy Haste', 'For 1..60 second[s], your Healing Prayers spells cast 50% faster. This enchantment ends if you cast another enchantment.', '(1...60 seconds.) Your Healing Prayers spells cast 50% faster. <i>Ends if you cast an enchantment.</i>', '', '', ''),
(1686, 'Glimmer of Light', 'Heal target ally for 10..115 Health.', 'Heals for 10...115.', '', '', ''),
(1687, 'Zealous Benediction', 'Heal target ally for 30..180 Health. If target was below 50% Health, you gain 7 Energy.', 'Heals for 30...180. You gain 7 Energy if target ally was below 50% Health.', '', '', ''),
(1688, 'Defender\'s Zeal', 'For 5..25 seconds, whenever target foe hits with an attack, you gain 2 Energy.', '(5...25 seconds.) You gain 2 Energy whenever target foe hits with an attack.', '', '', ''),
(1689, 'Signet of Mystic Wrath', 'Target foe takes 5..35 holy damage for each enchantment on you (maximum 100 holy damage).', 'Deals 5...35 holy damage for each enchantment on you (maximum 100 holy damage).', '', '', ''),
(1690, 'Signet of Removal', 'If target ally is under the effects of an enchantment, that ally loses one hex and one condition.', 'Removes one hex and one condition. <i>No effect unless target ally is enchanted.</i>', '', '', ''),
(1691, 'Dismiss Condition', 'Remove one condition from target ally. If that ally is under the effects of an enchantment, that ally is healed for 15..75 Health.', 'Removes one condition. Heals for 15...75 if target ally is enchanted.', '', '', ''),
(1692, 'Divert Hexes', 'Remove up to 1..3 hex[es] from target ally. For each hex removed in this way, that ally loses one condition and gains 15..75 Health.', 'Removes 1...3 hexes. For each hex removed, target ally loses one condition and gains 15...75 Health.', '', '', ''),
(1693, 'Counterattack', 'If this attack hits, you strike for +5..35 damage. If you hit an attacking foe, you gain 2..6 Energy.', 'Deals +5...35 damage. You gain 2...6 Energy if target foe is attacking.', '', '', ''),
(1694, 'Magehunter Strike', 'If this attack hits, you strike for +5..20 damage. If your target is under the effects of an enchantment, this attack cannot be blocked.', 'Deals +5...20 damage. Unblockable if target foe is enchanted.', '', '', ''),
(1695, 'Soldier\'s Strike', 'If this attack hits, you deal +10..40 more damage. If you are under the effects of a chant or shout, this attack cannot be blocked.', 'Deals +10...40 damage. Unblockable if you are under the effects of a chant or shout.', '', '', ''),
(1696, 'Decapitate', 'You lose all adrenaline and all Energy. If this attack hits, you deal +5..50 damage and cause a Deep Wound for 5..20 seconds. This attack always results in a critical hit.', 'Deals +5...50 damage. Inflicts Deep Wound condition (5...20 seconds). Automatic critical hit. <i>You lose all adrenaline and Energy.</i>', '', '', ''),
(1697, 'Magehunter\'s Smash', 'If this attack hits, target foe is knocked down. If your target is under the effects of an enchantment, this attack cannot be blocked.', 'Causes knock-down. Unblockable if target foe is enchanted.', '', '', ''),
(1698, 'Soldier\'s Stance', 'For 5..15 seconds, you have a 75% chance to block. You attack 33% faster while under the effects of a shout or chant.', '(5...15 seconds.) You have a 75% chance to block. You attack 33% faster while under the effects of a chant or shout.', 'Soldier\'s Stance (PvP)', 'For 4..10 seconds, you attack 33% faster while under the effects of a shout or chant.', '(4...10 seconds.) You attack 33% faster while under the effects of a shout or chant.'),
(1699, 'Soldier\'s Defense', 'For 1..6 second[s], you have a 75% chance to block attacks while under the effects of a shout or chant. You gain 1 strike of adrenaline for each blocked attack.', '(1...6 seconds.) You have 75% chance to block while under the effects of a shout or chant. Block effect: gain 1 adrenaline.', '', '', ''),
(1700, 'Frenzied Defense', 'For 8 seconds, you have a 75% chance to block incoming attacks, but take double damage.', '(8 seconds.) You have 75% chance to block. <i>You take double damage.</i>', '', '', ''),
(1701, 'Steady Stance', 'For 10 seconds, the next time you would be knocked down, you gain 1..3 strike[s] of adrenaline and 1..7 Energy instead.', '(10 seconds.) The next time you would be knocked-down, you gain 1...3 adrenaline and 1...7 Energy instead.', '', '', ''),
(1702, 'Steelfang Slash', ' If this attack hits, you deal +1..31 damage. If you hit a foe that is knocked down, you gain 1..5 adrenaline.', 'Deals +1...31 damage. You gain 1...5 adrenaline if target foe is knocked down.', '', '', ''),
(1719, 'Screaming Shot', 'If this attack hits, you deal +10..25 damage. If your target is within earshot, that foe begins Bleeding for 5..20 seconds.', 'Deals +10...25 damage. Inflicts Bleeding condition (5...20 seconds) if target foe is within earshot.', '', '', ''),
(1720, 'Keen Arrow', 'If this attack hits, you strike for +5..20 damage. If you land a critical hit, you deal an additional +5..25 damage.', 'Deals +5...20 damage. Deals +5...25 more damage if you land a critical hit.', 'Keen Arrow (PvP)', 'If this attack hits, you strike for +1..5 damage. If you land a critical hit, you deal an additional +5..30 damage.', 'Deals +1...5 damage. Deals +5...30 more damage if you land a critical hit.'),
(1721, 'Rampage as One', 'For 3..15 seconds, both you and your animal companion attack 33% faster and run 25% faster.', '(3...15 seconds.) You and your pet attack 33% faster and move 25% faster. <i>No effect unless your pet is alive.</i>', '', '', ''),
(1722, 'Forked Arrow', 'Shoot two arrows simultaneously at target foe. If you are under the effects of an enchantment or hex, you shoot only one arrow.', 'You shoot two arrows simultaneously. <i>Shoot only one arrow if you are enchanted or hexed.</i>', '', '', ''),
(1723, 'Disrupting Accuracy', ' For 36 seconds, whenever your arrows critical, they also interrupt your target.', '(36 seconds.) Interrupts an action whenever your arrows critical.', '', '', ''),
(1724, 'Expert\'s Dexterity', 'For 1..20 second[s], you attack 33% faster and your Marksmanship attribute is increased by 2.', '(1...20 seconds.) You attack 33% faster and you gain +2 to your Marksmanship attribute.', 'Expert\'s Dexterity (PvP)', 'For 1..20 second[s], you attack 15% faster and your Marksmanship attribute is increased by 1.', '(1...20 seconds.) You attack 15% faster and you gain +1 to your Marksmanship attribute.'),
(1725, 'Roaring Winds', 'Create a level 1..10 spirit. Chants and shouts cost 1..5 more Energy. This spirit dies after 30..60 seconds.', 'Creates a level 1...10 spirit (30...60 second lifespan). Chants and shouts cost 1...5 more Energy for creatures in range.', '', '', ''),
(1726, 'Magebane Shot', 'If this attack hits, it interrupts target foe\'s action. If that action was a spell, it is disabled for an additional 10 seconds. This attack cannot be blocked.', 'Interrupts an action. Interruption effect: an interrupted spell is disabled for +10 seconds. Unblockable.', '', '', ''),
(1727, 'Natural Stride', ' For 1..8 second[s], you run 33% faster and have a 50% chance to block incoming attacks. Natural Stride ends if you become hexed or enchanted.', '(1...8 seconds.) You move 33% faster and have 50% chance to block. <i>Ends if you become hexed or enchanted.</i>', '', '', ''),
(1728, 'Heket\'s Rampage', 'For 5..11 seconds, you attack 33% faster. This stance ends if you use an attack skill.', '(5...11 seconds). You attack 33% faster. <i>Ends if you use an attack skill.</i>', '', '', ''),
(1729, 'Smoke Trap', 'When Smoke Trap is triggered, nearby foes are Blinded and Dazed for 5..10 seconds. Smoke Trap ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Inflicts Blinded and Dazed conditions (5...10 seconds) to nearby foes. <i>Easily interrupted.</i>', '', '', ''),
(1730, 'Infuriating Heat', 'Create a level 1..10 spirit. Non-spirit creatures within its range gain adrenaline twice as fast. This spirit dies after 30..60 seconds.', 'Creates a level 1...10 spirit (30...60 second lifespan). Doubles adrenaline gain for creatures in range.', '', '', ''),
(1731, 'Vocal Was Sogolon', 'For 60 seconds, all shouts and chants you use last 20..50% longer.', '(60 seconds.) Shouts and chants you use last 20...50% longer.', '', '', ''),
(1732, 'Destructive Was Glaive', 'Hold Glaive\'s ashes for up to 30..60 seconds. While you hold her ashes, all Ritualist skills have 20% armor penetration. When you drop her ashes, all foes in the area are struck for 15..85 lightning damage.', '(30...60 seconds.) Your Ritualist skills have 20% armor penetration. Drop effect: deals 15...85 lightning damage to all foes in the area.', 'Destructive Was Glaive (PvP)', 'Hold Glaive\'s ashes for up to 30..60 seconds. While you hold her ashes, all Ritualist skills have 10% armor penetration. When you drop her ashes, all foes in the area are struck for 15..85 lightning damage.', '(30...60 seconds.) Your Ritualist skills have 10% armor penetration. Drop effect: deals 15...85 lightning damage to all foes in the area.'),
(1733, 'Wielder\'s Strike', 'Target foe is struck for 5..50 lightning damage. If you are under the effects of a weapon spell, you deal an additional 10..40 lightning damage.', 'Deals 5...50 lightning damage. Deals 10...40 more lightning damage if you are under a weapon spell.', '', '', ''),
(1734, 'Gaze of Fury', 'Destroy target spirit and create a level 1..14 Spirit of Fury. This spirit\'s attacks deal 5..20 damage. This spirit dies after 30..60 seconds.', 'Destroys a spirit. Creates a level 1...14 spirit (30...60 second lifespan). Its attacks deal 5...20 damage.', 'Gaze of Fury (PvP)', 'Destroy target allied spirit and create a level 1..10 Spirit of Fury. This spirit\'s attacks deal 5..20 damage. This spirit dies after 30..60 seconds.', 'Destroys an allied spirit. Creates a level 1...10 spirit (30...60 second lifespan). Its attacks deal 5...20 damage.'),
(1736, 'Spirit\'s Strength', 'For 15..60 seconds, your attacks deal 5..35 more damage while under the effects of a weapon spell.', '(15...60 seconds.) Your attacks deal +5...35 damage if you are under a weapon spell.', '', '', ''),
(1737, 'Wielder\'s Zeal', 'For 10..30 seconds, whenever you cast a Weapon Spell on an ally, you gain 1..5 Energy.', '(10...30 seconds.) You gain 1...5 Energy whenever you cast a weapon spell.', '', '', ''),
(1738, 'Sight Beyond Sight', 'For 8..20 seconds, you cannot be Blinded.', '(8...20 seconds). You are immune to Blindness.', '', '', ''),
(1739, 'Renewing Memories', 'For 5..20 seconds, while holding an item, any weapon and item Spells you cast cost 5..35% less Energy.', '(5...20 seconds.) Your weapon and item spells cost 5...35% less Energy. <i>No effect unless holding an item.</i>', '', '', ''),
(1740, 'Wielder\'s Remedy', 'For 10..30 seconds, whenever you cast a weapon Spell on an ally, that ally loses 1 Condition.', '(10...30 seconds.) Whenever you cast a weapon spell, the targeted ally loses one condition.', '', '', ''),
(1741, 'Ghostmirror Light', 'Target other ally is healed for 15..90 Health. If you are within earshot of a spirit, you are also healed for 15..90 Health.', 'Heals for 15...90. You gain 15...90 Health if you are within earshot of a spirit. <i>Cannot self-target.</i>', '', '', ''),
(1742, 'Signet of Ghostly Might', 'For 5..20 seconds, all spirits you control within earshot attack 33% faster and deal 5..10 additional damage.', '(5...20 seconds.) All spirits you control within earshot attack 33% faster and deal +5...10 damage.', 'Signet of Ghostly Might (PvP)', 'Target allied summoned creature\'s attacks deal 5..35 more damage. After 10 seconds, that creature is destroyed.', 'Target allied summoned creature deals +5...35 damage. <i>That creature is destroyed after 10 seconds.</i>'),
(1743, 'Signet of Binding', 'You lose 200..50 Health and take control of target enemy-controlled spirit. (50% failure chance with Spawning Power 4 or less.)', 'You lose 200...50 Health. Gain control of target enemy-controlled spirit. <i>50% failure chance unless Spawning Power 5 or more.</i>', '', '', ''),
(1744, 'Caretaker\'s Charge', 'Target foe is struck for 20..75 lightning damage. If you are holding an item, you gain 5 Energy and 5..50 Health.', 'Deals 20...75 lightning damage. You gain 5 Energy and 5...50 Health if you are holding an item.', '', '', ''),
(1745, 'Anguish', 'Create a level 1..11 spirit. This spirit\'s attacks deal 5..20 damage and deal double damage to hexed foes. This spirit dies after 15..45 seconds.', 'Creates a level 1...11 spirit (15...45 second lifespan). Its attacks deal 5...20 damage. Double damage to hexed foes.', 'Anguish (PvP)', 'Create a level 1..7 spirit. This spirit\'s attacks deal 5..20 damage and deal double damage to hexed foes. This spirit dies after 15..45 seconds.', 'Creates a level 1...7 spirit (15...45 second lifespan). Its attacks deal 5...20 damage. Double damage to hexed foes.'),
(1747, 'Empowerment', 'Create a level 1..14 spirit. All allies within its range holding an item gain 15..45 maximum Health and 10 maximum Energy. This spirit dies after 15..60 seconds.', 'Creates a level 1...14 spirit (15...60 second lifespan). Allies in range holding an item gain 15...45 maximum Health and 10 maximum Energy.', 'Empowerment (PvP)', 'Create a level 1..10 spirit. All allies within its range holding an item gain 15..45 maximum Health and 10 maximum Energy. This spirit dies after 15..60 seconds.', 'Creates a level 1...10 spirit (15...60 second lifespan). Allies in range holding an item gain 15...45 maximum Health and 10 maximum Energy.'),
(1748, 'Recovery', 'Create a level 1..14 spirit. Conditions on allies within range of this spirit expire 20..50% faster. This spirit dies after 30..60 seconds.', 'Creates a level 1...14 spirit (30...60 second lifespan). Conditions on allies in range expire 20...50% faster.', 'Recovery (PvP)', 'Create a level 1..10 spirit. Conditions on allies within range of this spirit expire 20..50% faster. This spirit dies after 30..60 seconds.', 'Creates a level 1...10 spirit (30...60 second lifespan). Conditions on allies in range expire 20...50% faster.'),
(1749, 'Weapon of Fury', 'For 5..20 seconds, target ally gains 5..50% more adrenaline and 1 Energy whenever that ally successfully hits with an attack.', '(5...20 seconds.) 5...50% more adrenaline gain and +1 Energy whenever target ally hits with an attack.', '', '', ''),
(1750, 'Xinrae\'s Weapon', 'For 8 seconds, the next time target ally takes damage from a foe that damage is limited to 5% of that ally\'s max Health and that ally steals up to 20..80 Health from that foe.', '(8 seconds). The next time target ally takes damage from a foe, that damage is limited to 5% of target ally\'s max Health and that ally steals 20...80 Health from that foe.', '', '', ''),
(1751, 'Warmonger\'s Weapon', 'For 3..13 seconds, if target ally attacks a foe who is not attacking, that foe is interrupted.', '(3...13 seconds.) Attacks interrupt an action. Does not interrupt attacking foes.', '', '', ''),
(1752, 'Weapon of Remedy', 'For 8 seconds, the next time target ally takes damage or life steal from a foe, that ally steals up to 15..75 Health from that foe and loses 1 condition.', '(8 seconds.) The next time target ally takes damage or life steal from a foe, this ally steals 15...75 Health from that foe and loses 1 condition.', '', '', ''),
(1753, 'Rending Sweep', 'You deal +5..20 and lose 1 Dervish enchantment. If an enchantment was lost, you remove an enchantment from each foe you hit.', 'Deals +5...20 damage. You lose 1 Dervish enchantment. Removal effect: struck foes lose an enchantment.', '', '', ''),
(1754, 'Onslaught', 'For 3..15 seconds, you attack, move and gain adrenaline 25% faster.', 'Elite Flash Enchantment Spell. (3...15 seconds.) You attack, move and gain adrenaline 25% faster.', 'Onslaught (PvP)', 'For 2..8 seconds, you attack and gain adrenaline 25% faster.', 'Elite Flash Enchantment Spell. (2...8 seconds.) You attack and gain adrenaline 25% faster.'),
(1755, 'Mystic Corruption', 'All adjacent foes suffer from Disease for 1..2 second[s]. For 20 seconds, nothing happens. Disease duration is doubled if you are enchanted when you activate this skill. When this enchantment ends, all party members in earshot are cured of Disease.', 'Flash Enchantment Spell. (20 seconds.) Initial Effect: all adjacent foes are Diseased (1...2 seconds.) Double duration if you are already enchanted. End Effect: party members in earshot lose Disease.', '', '', ''),
(1756, 'Grenth\'s Grasp', 'For 20 seconds, if you are wielding a cold weapon, your attack skills also Cripple that  foe for 1..11 second[s] and you transfer 1 condition from yourself to that foe.', 'Elite Flash Enchantment Spell. (20 seconds.) Inflicts Crippled condition (1...11 seconds) with your attack skills and transfer 1 condition. <i>No effect unless wielding a cold weapon.</i>', '', '', ''),
(1757, 'Veil of Thorns', 'When you cast this enchantment, all nearby foes are struck for 5..50 piercing damage. For 5..25 seconds you take 5..35% less damage from spells.', 'Flash Enchantment Spell. (5...25 seconds.) Spell damage is reduced by 5...35%. Initial Effect: nearby foes are struck for 5...50 piercing damage.', '', '', ''),
(1758, 'Harrier\'s Grasp', 'For 5..20 seconds, attacks against moving foes also Cripple that foe for 2..9 seconds. This enchantment ends after you apply Cripple 1..3 times.  When you cast this spell, you lose Cripple and 1 other condition.', 'Flash Enchantment Spell. (5...20 seconds.) Attacks on moving foes also Cripple them. <sub>sic</sub> gray', '', '', ''),
(1759, 'Vow of Strength', 'For 15 seconds, whenever you attack a foe with your scythe, you deal 10..25 slashing damage to all adjacent foes.', '(15 seconds.) When you attack with a scythe, deals 10...25 slashing damage to adjacent foes.', '', '', ''),
(1760, 'Ebon Dust Aura', 'When you cast this enchantment, all nearby foes are Blinded for 1..7 second[s]. For 30 seconds, if you are wielding an earth weapon, your melee attacks deal +3..15 earth damage. When this enchantment ends, you are cured of Blindness.', 'Elite Flash Enchantment Spell. (30 seconds.) Deal +3...15 earth damage with your melee attacks. Initial Effect: Blinds nearby foes for 1...7 seconds. End Effect: removes Blindness. <i>No effect unless wielding an earth weapon.</i>', '', '', ''),
(1761, 'Zealous Vow', 'For 20 seconds, you have -3 Energy regeneration, and you gain 1..6 Energy every time you hit with an attack.', '(20 seconds.) You gain 1...6 Energy each time you hit with an attack. <i>You have -3 Energy regeneration.</i>', '', '', ''),
(1762, 'Heart of Fury', ' For 2..10 seconds, you attack 25% faster.', '(2...10 seconds.) You attack 25% faster.', 'Heart of Fury (PvP)', ' For 2..10 seconds, you attack 25% faster.', '(2...10 seconds.) You attack 25% faster.'),
(1763, 'Zealous Renewal', 'All nearby foes take 5..30 holy damage. For 5..25 seconds, you have -1 Energy regeneration, and gain 1 Energy whenever you hit a foe. If this enchantment ends prematurely, you gain 1..5 Energy.', 'Flash Enchantment Spell. (5...25 seconds.) Initial effect: deals 5...30 holy damage to nearby foes. You have -1 Energy regeneration and gain 1 Energy when you hit. Gain 1...5 Energy if this enchantment ends early.', '', '', ''),
(1764, 'Attacker\'s Insight', 'For 3..10 seconds you have a 50% chance to block while attacking. When this enchantment ends, you inflict Weakness on all adjacent foes for 3..15 seconds.', 'Flash Enchantment Spell. (3...10 seconds.) Gives 50% block chance while attacking. End Effect: Causes Weakness 3...15 seconds to adjacent foes.', '', '', ''),
(1765, 'Rending Aura', 'When you cast this enchantment, all nearby foes take 10..40 cold damage. For  30  seconds, your attack skills remove enchantments from knocked-down foes. When this enchantment ends, nearby foes are affected by Cracked Armor for 1..10 second[s].', 'Flash Enchantment Spell. (30 seconds.) Your attack skills remove enchantments from knocked-down foes. Initial effect: deals 10...40 cold damage to all nearby foes. End effect: nearby foes have Cracked Armor for 1...10 seconds.', '', '', ''),
(1766, 'Featherfoot Grace', 'For 5..20 seconds, you move 25% faster, and conditions expire 25% faster.', '(5...20 seconds.) You move 25% faster, and conditions expire 25% faster.', '', '', ''),
(1767, 'Reaper\'s Sweep', 'If this attack hits, you Cripple your target for 3..15 seconds. You lose 1 Dervish enchantment. If an enchantment was removed, this attack knocks down for 2..3 seconds.', '(3...15 seconds.) Cause Cripple. Lose 1 Dervish enchantment. Removal Effect: cause knockdown for 2...3 seconds.', '', '', ''),
(1768, 'Harrier\'s Haste', 'For 2..8 seconds, you move 25% faster and deal +3..12 more damage against moving foes.', 'Flash Enchantment Spell. (2...8 seconds.) You move 25% faster and deal +3...12 damage to moving foes.', '', '', ''),
(1769, 'Focused Anger', 'For 45 seconds, you gain 0..150% more adrenaline.', '(45 seconds.) You gain 0...150% more adrenaline.', '', '', ''),
(1770, 'Natural Temper', 'For 4..10 seconds, you gain 33% more adrenaline while not under the effects of an Enchantment.', '(4...10 seconds.) You gain 33% more adrenaline. <i>No effect if you are enchanted.</i>', '', '', ''),
(1771, 'Song of Restoration', 'For 10 seconds, the next time each party member within earshot uses a skill, that party member gains 45..110 Health.', '(10 seconds.) Party members in earshot gain 45...110 Health with their next skill.', 'Song of Restoration (PvP)', 'For 10 seconds, the next time each party member within earshot uses a skill, that party member gains 45..110 Health.', '(10 seconds.) Party members in earshot gain 45...110 Health with their next skill.'),
(1772, 'Lyric of Purification', 'For 5..20 seconds, the next time each ally within earshot uses a Signet, that ally loses 1 Condition.', '(5...20 seconds.) Allies in earshot lose one condition with their next signet.', '', '', ''),
(1773, 'Soldier\'s Fury', 'For 10..35 seconds, if you are under the effects of a chant or a shout, you attack 33% faster and gain 33% more adrenaline, but you have -20 armor.', '(10...35 seconds.) You attack 33% faster and gain 33% more adrenaline if under the effects of a shout or chant. <i>You have -20 armor.</i>', '', '', ''),
(1774, 'Aggressive Refrain', 'For 5..25 seconds, you attack 25% faster but have -20 armor. This echo is reapplied every time a chant or shout ends on you.', '(5...25 seconds.) You attack 25% faster. Renewal: whenever a chant or shout ends on you. <i>You have -20 armor.</i>', '', '', ''),
(1775, 'Energizing Finale', 'For 10..35 seconds, whenever a shout or chant ends on target non-spirit ally, that ally gains 1 Energy.', '(10...35 seconds.) Target ally gains 1 Energy whenever a shout or chant ends on that ally. <i>Cannot target spirits.</i>', '', '', ''),
(1776, 'Signet of Aggression', 'If you are under the effects of a shout or chant, you gain 2 strikes of adrenaline.', 'You gain 2 adrenaline if you are under the effects of a shout or chant.', '', '', ''),
(1777, 'Remedy Signet', 'You lose 1 condition.', 'You lose one condition.', '', '', ''),
(1778, 'Signet of Return', 'Resurrect target party member with 5..15% Health and 1..4% Energy for each party member within earshot.', 'Resurrects target party member (5...15% Health and 1...4% Energy for each party member in earshot).', 'Signet of Return (PvP)', 'Resurrect target party member with 5..15% Health and 1..4% Energy for each party member within earshot.', 'Resurrects target party member (5...15% Health and 1...4% Energy for each party member in earshot).'),
(1779, '"Make Your Time!"', 'You gain 1 strike of adrenaline for each party member within earshot (maximum 1..5 adrenaline).', 'You gain one adrenaline (maximum 1...5) for each party member in earshot.', '', '', ''),
(1780, '"Can\'t Touch This!"', 'For 20 seconds, the next 1..5 touch skill[s] used against allies within earshot fail[s].', '(20 seconds.) The next 1...5 touch-range skills used against allies <sub>sic</sub> within earshot fails.', '"Can\'t Touch This!" (PvP)', 'For 20 seconds, the next 1..5 touch skill[s] used against you fail[s].', '(20 seconds.) The next 1...5 touch-range skills used against you fails.'),
(1781, '"Find Their Weakness!"', 'For 5..20 seconds, the next time target ally attacks, that ally deals +5..50 damage and inflicts a Deep Wound for 5..20 seconds.', '(5...20 seconds.) Target ally deals +5...50 damage and inflicts Deep Wound condition (5...20 seconds) with the next attack.', '"Find Their Weakness!" (PvP)', 'For 5..20 seconds, the next time target ally criticals, that ally also inflicts a Deep Wound for 5..20 seconds.', '(5...20 seconds.) Target ally inflicts Deep Wound condition (5...20 seconds) with the next critical hit.'),
(1782, '"The Power Is Yours!"', 'For 3 seconds, all allies within earshot gain 0..1 Energy regeneration.', '(3 seconds.) Allies within earshot gain 0...1 Energy regeneration.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(1783, 'Slayer\'s Spear', 'If this attack hits, you deal +5..25 damage. If that foe has more Health than you, that foe suffers from a Deep Wound for 5..20 seconds.', 'Deals +5...25 damage. Inflicts Deep Wound condition (5...20 seconds) if target has more Health than you.', '', '', ''),
(1784, 'Swift Javelin', 'If this attack hits, you deal +5..20 damage. If you are under the effects of an enchantment, this spear flies twice as fast and cannot be blocked.', 'Deals +5...20 damage. This spear moves twice as fast and is unblockable if you are enchanted.', '', '', ''),
(1814, 'Lightbringer\'s Gaze', 'Target demonic servant of Abaddon takes 100 holy damage and is interrupted. This skill strikes one additional foe in the area for each rank of Lightbringer you have attained. This skill is disabled for 15 seconds after it is used.', 'Target demonic servant of Abaddon takes 100 holy damage and is interrupted. Hits one additional foe in the area for each rank of Lightbringer you have attained. <i>This skill is disabled (15 seconds).</i>', '', '', ''),
(1815, 'Lightbringer Signet', 'If you are within the area of a demonic servant of Abaddon, you gain 4..5 strikes of adrenaline and 22..24 Energy.', 'You gain 4...5 strikes of adrenaline and 22...24 Energy if you are within the area of a demonic servant of Abaddon.', '', '', ''),
(1816, 'Sunspear Rebirth Signet', 'Resurrect target dead party member at your location with full Health and 10% Energy for each Sunspear rank you have attained. This signet can only be used once per mission unless recharged by a morale boost.', 'Resurrect target dead ally <sub>sic</sub> at your location (full Health and 10% Energy for each Sunspear Rank attained). <i>This signet only recharges when you gain a morale boost.</i>', '', '', ''),
(1948, 'Shadow Sanctuary (Luxon)', 'You are Blinded for 5 seconds. For 10 seconds, you gain +7..10 Health regeneration and +40 armor.', '(10 seconds.) You gain +7...10 Health regeneration and +40 armor. <i>You are Blind (5 seconds).</i>', '', '', ''),
(1949, 'Ether Nightmare (Luxon)', 'Target foe loses 5..8 Energy. For each point of Energy lost in this way, that foe and all foes in the area suffer -1 Health degeneration for 10 seconds.', 'Causes 5...8 Energy loss. Target and foes in the area have -1 Health degeneration for each point of Energy lost (10 seconds).', '', '', ''),
(1950, 'Signet of Corruption (Luxon)', 'Target foe and all nearby foes take 20..30 damage. For each affected foe suffering from a condition or hex, you gain 2 Energy (maximum 12..20 Energy).', 'Deals 20...30 damage to target and nearby foes. You gain 2 Energy (maximum 12...20) for each of these foes with a condition or hex.', '', '', ''),
(1951, 'Elemental Lord (Luxon)', 'For 40..60 seconds, your elemental attributes are boosted by 1. Each time you cast a spell, you gain 1 Energy for every 10 ranks of Energy Storage and are healed for 100..300% of the spell\'s Energy cost.', '(40...60 seconds.) Your elemental attributes are boosted by 1. Each time you cast a spell, you gain 1 energy for every 10 ranks of Energy Storage and are healed for 100...300% of the spell\'s Energy cost.', '', '', ''),
(1952, 'Selfless Spirit (Luxon)', 'For 15..20 seconds, spells you cast that target another ally cost 3 less energy.', '(15...20 seconds.) Spells you cast that target another ally cost 3 less Energy.', '', '', ''),
(1953, 'Triple Shot (Luxon)', 'Shoot 3 arrows simultaneously at target foe. These arrows deal 40..25% less damage.', 'Shoot 3 arrows simultaneously at target foe. <i>These arrows deal 40...25% less damage</i>', '', '', ''),
(1954, '"Save Yourselves!" (Luxon)', 'For 4..6 seconds, all other party members gain +100 armor.', '(4...6 seconds.) All other party members gain +100 armor.', '', '', ''),
(1955, 'Aura of Holy Might (Luxon)', 'For 45 seconds, you deal 20..25 holy damage to all adjacent foes whenever you lose a Dervish enchantment.', '(45 seconds.) Whenever you lose a Dervish enchantment, adjacent foes take 20...25 holy damage.', '', '', ''),
(1957, 'Spear of Fury (Luxon)', 'Deals +30..40 damage. If this attack hits a foe suffering from a Condition, you gain 3..6 strikes of adrenaline.', 'Deals +30...40 damage. Gain 3...6 strikes of adrenaline if you hit a foe with a condition.', '', '', ''),
(1986, 'Vampiric Assault', 'Must follow an off-hand attack. If this attack hits, you steal 10..40 Health.', 'Steals 10...40 Health if this attack hits. <i>Must follow an off-hand attack.</i>', '', '', ''),
(1987, 'Lotus Strike', 'Must follow a lead attack. If it hits, this attack strikes for +10..25 damage and you gain 5..20 Energy.', 'Deals +10...25 damage; you gain 5...20 Energy. <i>Must follow a lead attack.</i>', '', '', ''),
(1988, 'Golden Fang Strike', 'Must follow a lead attack. If you are under the effects of an enchantment and this attack hits, target foe suffers from a Deep Wound for 5..20 seconds.', 'Inflicts Deep Wound condition (5...20 seconds) if you are enchanted. <i>Must follow a lead attack.</i>', '', '', ''),
(1990, 'Falling Lotus Strike', 'Must strike a knocked-down foe. If it hits, you strike for +15..35 damage and gain 1..12 Energy.', 'Deals +15...35 damage; you gain 1...12 Energy. <i>No effect unless target foe is knocked-down.</i>', '', '', ''),
(1991, 'Sadist\'s Signet', 'You gain 10..40 Health for each condition on target foe.', 'You gain 10...40 Health for each condition on target foe.', '', '', ''),
(1992, 'Signet of Distraction', 'If target foe is casting a spell, that spell is interrupted and disabled for 1..5 seconds for each signet you have equipped.', 'Interrupts a spell. Interruption effect: target foe\'s spell is disabled for 1...5 seconds for each signet you have equipped.', '', '', ''),
(1993, 'Signet of Recall', 'For 10 seconds, you have -4 Energy regeneration. When this effect ends, you gain 13..20 Energy.', '(10 seconds.) You have -4 Energy regeneration. End effect: you gain 13...20 Energy.', '', '', ''),
(1994, 'Power Lock', 'If target foe is casting a spell or chant, that skill is interrupted and disabled for an additional 5..13 seconds.', 'Interrupts a spell or chant. Interruption effect: interrupted spell or chant is disabled for +5...13 seconds.', '', '', ''),
(1995, 'Waste Not, Want Not', 'If target foe is not casting a spell or attacking, you gain 8..13 Energy.', 'You gain 8...13 Energy if target foe is not attacking or casting a spell.', '', '', ''),
(1996, 'Sum of All Fears', 'For 1..10 second[s], target foe moves, attacks, and casts spells 33% slower.', '(1...10 seconds.) Target foe moves, attacks, and casts spells 33% slower.', '', '', ''),
(1997, 'Withering Aura', 'For 5..20 seconds, target ally\'s melee attacks cause Weakness for 5..20 seconds.', '(5...20 seconds.) Target ally\'s melee attacks cause Weakness condition (5...20 seconds.)', '', '', ''),
(1998, 'Cacophony', 'For 10 seconds, whenever target foe uses a shout or chant, that foe takes 35..105 damage.', '(10 seconds.) Deals 35...105 damage whenever target foe uses a shout or chant.', '', '', ''),
(1999, 'Winter\'s Embrace', 'For 2..6 seconds, target foe moves 66% slower and takes 5..15 damage while moving.', '(2...6 seconds.) Target foe moves 66% slower and takes 5...15 damage while moving.', '', '', ''),
(2000, 'Earthen Shackles', 'For 3 seconds, target and all nearby foes move 90% slower. When Earthen Shackles ends, it applies Weakness for 5..20 seconds.', '(3 seconds.) Target and nearby foes move 90% slower. Applies Weakness for 5...20 seconds when it ends.', '', '', ''),
(2001, 'Ward of Weakness', 'You create a Ward of Weakness at your current location. For 5..20 seconds, foes in this area become Weakened for 5..20 seconds whenever they take elemental damage.', '(5...20 seconds). Inflicts Weakened condition (5...20 seconds) to any foes that take elemental damage in this ward.', '', '', ''),
(2002, 'Glyph of Swiftness', 'For 15 seconds, your next 1..5 spell[s] recharge 25% faster and projectiles from the affected spells move 200% faster.', '(15 seconds.) Your next 1...5 spells recharge 25% faster and projectiles from them move 200% faster.', '', '', ''),
(2003, 'Cure Hex', 'Remove one Hex from target ally. If a Hex was removed, that ally is healed for 30..120 Health.', 'Removes a Hex. Removal effect: Heals for 30...120.', '', '', ''),
(2004, 'Smite Condition', 'Remove one condition from target ally. If a condition was removed, foes in the area take 10..60 holy damage.', 'Removes a condition. Removal effect: deals 10...60 holy damage to foes in the area of target ally.', '', '', ''),
(2005, 'Smiter\'s Boon', 'For 30 seconds, your Smiting Prayers have double the Divine Favor bonus.', '(30 seconds.) Your Smiting Prayers have double Divine Favor healing bonus.', 'Smiter\'s Boon (PvP)', 'For 5 seconds, your Smiting Prayers have double the Divine Favor bonus.', '(5 seconds.) Your Smiting Prayers have double Divine Favor healing bonus.'),
(2006, 'Castigation Signet', 'Target foe takes 26..56 holy damage. If that foe was attacking, you gain 1..10 Energy.', 'Deals 26...56 holy damage. You gain 1...10 Energy if target foe is attacking.', '', '', ''),
(2007, 'Purifying Veil', 'While you maintain this enchantment, conditions expire 5..50% faster on target ally. When this enchantment ends, one condition is removed from that ally.', 'Conditions expire 5...50% faster on target ally. End effect: removes a condition.', '', '', ''),
(2008, 'Pulverizing Smash', 'If you hit a knocked-down foe, that foe suffers from Weakness and a Deep Wound for 5..20 seconds.', 'Inflicts Weakness and Deep Wound conditions (5...20 seconds) if target foe is knocked-down.', '', '', ''),
(2009, 'Keen Chop', 'If it hits, this attack always results in a critical hit.', 'Always a critical hit.', '', '', ''),
(2010, 'Knee Cutter', 'If this attack hits a Crippled foe, you gain 2..7 Energy and 1..3 strikes  of adrenaline.', 'You gain 2...7 Energy and 1...3 adrenaline if target foe is Crippled.', '', '', ''),
(2011, 'Grapple', 'You and target touched foe are knocked down. You lose your current stance.', 'Touch Skill. Causes knockdown. You are also knocked down. <i>Your current stance ends.</i>', '', '', ''),
(2012, 'Radiant Scythe', 'This attack strikes for +1 damage for each point of Energy you currently have, maximum 5..30. You gain 1..7 Energy if this attack hits.', 'Deals +1 damage (maximum 5...30) for each point of Energy you have. Gain 1...7 Energy.', '', '', ''),
(2013, 'Grenth\'s Aura', 'For 20 seconds, you deal 5..25 less damage and steal 5..25 Health whenever you hit with a scythe attack. When you cast this enchantment you steal 5..25 Health from all adjacent foes.', 'Flash Enchantment Spell. (20 seconds.) You deal 5...25 less damage and steal 5...25 Health when you hit with a scythe. Initial effect: steal 5...25 Health from all adjacent foes.', '', '', ''),
(2014, 'Signet of Pious Restraint', 'Remove one Dervish enchantment. Target foe is Crippled for 5..15 seconds. If an enchantment was removed, all foes nearby your target are also Crippled and this signet recharges 75% faster.', 'Inflicts Crippled condition (5...15 seconds). Remove one of your Dervish enchantments. Removal effect: also causes Cripple to foes nearby your target and recharges 75% faster.', 'Signet of Pious Restraint (PvP)', 'Lose 1 Dervish enchantment and Cripple target foe 5..15 seconds. If an enchantment was removed, this skill recharges 75% faster.', '(5...15 seconds.) Cripples target foe and removes 1 of your Dervish enchantments. Removal effect: recharges 75% faster.'),
(2015, 'Farmer\'s Scythe', 'If this attack hits, you deal +5..20 damage. If you hit more than one foe, this attack recharges instantly.', 'Deals +5...20 damage. Instant recharge if you hit more than one foe.', '', '', ''),
(2016, 'Energetic Was Lee Sa', 'Hold Lee Sa\'s ashes for 5..15 seconds. While you hold her ashes, you gain +2 Energy regeneration. When you drop her ashes, you gain +1..10 Energy.', '(5...15 seconds.) You have +2 Energy regeneration. Drop effect: you gain +1...10 Energy.', '', '', ''),
(2017, 'Anthem of Weariness', 'For 8 seconds, the next attack skill used by each ally within earshot also causes Weakness for 1..16 second[s].', '(8 seconds.) Allies in earshot inflict Weakness (1...16 seconds) with their next attack skill.', '', '', ''),
(2018, 'Anthem of Disruption', 'For 1..10 seconds, the next attack skill used by each ally within earshot also interrupts an action.', '(1...10 seconds.) Allies in earshot interrupt an action with their next attack skill.', 'Anthem of Disruption (PvP)', 'For 1..10 seconds, the next attack skill used by each ally within earshot also interrupts an action.', '(1...10 seconds.) Allies in earshot interrupt an action with their next attack skill.'),
(2051, 'Summon Spirits (Luxon)', 'All spirits you control Shadow Step to your location, and gain 60..100 Health.', 'Spirits you control Shadow Step to your location and gain 60...100 Health.', '', '', ''),
(2052, 'Shadow Fang', 'Shadow Step to target foe. For 10 seconds, this hex does nothing. When this hex ends, you return to your original location and that foe suffers from a Deep Wound for 5..20 seconds.', 'Shadow Step to target foe. End effect after 10 seconds: inflicts Deep Wound condition (5...20 seconds); you return to your original location.', '', '', ''),
(2053, 'Calculated Risk', 'For 3..24 seconds, target foe\'s attacks do +10 damage, but there is a 50% chance that the damage from each attack will be done to that foe instead. (Maximum 15..100 damage.)', 'Target foe does +10 damage with attacks (3...24 seconds). There is a 50% chance that the damage from each attack (maximum 15...100) will be done to that foe instead.', 'Calculated Risk (PvP)', 'For 3..12 seconds, target foe\'s attacks do +10 damage, but there is a 50% chance that the damage from each attack will be done to that foe instead. (Maximum 10..60 damage.)', 'Target foe does +10 damage with attacks (3...12 seconds). There is a 50% chance that the damage from each attack (maximum 10...60) will be done to that foe instead.'),
(2054, 'Shrinking Armor', 'For 10 seconds, target foe suffers from -1..4 Health degeneration. When this hex ends, that foe has Cracked Armor for 5..20 seconds.', '(10 seconds.) Causes -1...4 Health degeneration. End effect: inflicts Cracked Armor condition (5...20 seconds).', '', '', ''),
(2055, 'Aneurysm', 'Target foe regains all Energy. For each point of Energy gained in this way, that foe takes 1..3 damage and all adjacent foes lose 1 Energy. (Maximum 1..30).', 'Target foe regains all Energy. For each point of Energy gained, target takes 1...3 damage and all adjacent foes lose 1 Energy (maximum 1...30).', '', '', ''),
(2056, 'Wandering Eye', 'For 4 seconds, the next time target foe attacks, that attack is interrupted and all nearby foes take 30..110 damage.', '(4 seconds.) Interrupts target foe\'s next attack. Interruption effect: 30...110 damage to nearby foes.', 'Wandering Eye (PvP)', 'For 4 seconds, the next time target foe attacks, that attack is interrupted and all nearby foes take 10..92 damage.', '(4 seconds.) Interrupts target foe\'s next attack. Interruption effect: 10...92 damage to nearby foes.'),
(2057, 'Foul Feast', 'All conditions are transferred from target other ally to yourself. For each condition acquired in this way, you gain 0..45 Health and 0..2 Energy. This skill recharges twice as fast if you remove Disease from your target.', 'Transfers all conditions from target ally to yourself. You gain 0...45 Health and 0...2 Energy for each condition transferred. Half recharge if you remove Disease. <i>Cannot self-target.</i>', '', '', ''),
(2058, 'Putrid Bile', 'For 5..20 seconds, target foe suffers -1..3 Health degeneration. If that foe dies while under the effects of this hex, all nearby foes take 25..85 damage.', '(5...20 seconds.) Causes -1...3 Health degeneration. Deals 25...85 damage to all nearby foes if target foe dies.', '', '', ''),
(2059, 'Shell Shock', 'Target foe is struck for 10..30 lightning damage and has Cracked Armor for 5..20 seconds. This spell has 25% armor penetration. If you are Overcast, this spell strikes adjacent foes.', 'Deals 10...30 lightning damage. Inflicts Cracked Armor condition (5...20 seconds). 25% armor penetration. If Overcast, strikes adjacent.', '', '', ''),
(2060, 'Glyph of Immolation', 'For 15 seconds, your next 1..4 spell[s] that target[s] a foe also cause[s] Burning for 1..4 second[s].', '(15 seconds.) Your next 1...4 spells that targets a foe also inflicts Burning condition (1...4 seconds).', '', '', ''),
(2061, 'Patient Spirit', 'For 2 seconds, target ally is enchanted with Patient Spirit. Unless this enchantment ends prematurely, that ally is healed for 30..120 Health when the enchantment ends.', '(2 seconds.) End effect: heals for 30...120. <i>No effect if ends early.</i>', '', '', ''),
(2062, 'Healing Ribbon', 'Target other ally is healed for 20..110 Health. Up to 2 additional allies near target ally are healed for 10..100 Health.', 'Heals for 20...110. Heals two additional allies near target ally for 10...100. <i>Cannot self-target.</i>', '', '', ''),
(2063, 'Aura of Stability', 'For 3..8 seconds, target other ally cannot be knocked down.', '(3...8 seconds.) Target ally cannot be knocked-down. <i>Cannot self-target.</i>', '', '', ''),
(2064, 'Spotless Mind', 'For 1..15 seconds, target other ally loses a hex every 5 seconds.', '(1...15 seconds.) Removes a hex every 5 seconds. <i>Cannot self-target.</i>', '', '', ''),
(2065, 'Spotless Soul', 'For 1..15 seconds, target other ally loses a condition every 3 seconds.', '(1...15 seconds.) Removes a condition every 3 seconds. <i>Cannot self-target.</i>', '', '', ''),
(2066, 'Disarm', 'Interrupt target foe\'s action.  If that action was an attack, all of that foe\'s attack skills are disabled for 0..3 second[s].', 'Interrupts target foe. Interruption effect: disables this foe\'s attack skills (0...3 seconds) if that action was an attack.', '', '', ''),
(2067, '"I Meant to Do That!"', 'If you are knocked down, you gain 1..5 strikes of adrenaline.', 'You gain 1...5 strikes of adrenaline if you are knocked-down.', '', '', ''),
(2068, 'Rapid Fire', 'For 5..25 seconds, you attack 33% faster while wielding a bow.', '(5...25 seconds.) You attack 33% faster while wielding a bow.', '', '', ''),
(2069, 'Sloth Hunter\'s Shot', 'If this attack hits, target foe takes +10..25 damage. If that foe is not using a skill, Sloth Hunter\'s Shot does an additional +10..35 damage.', 'Deals +10...25 damage. Deals +10...35 more damage if target foe is not using a skill.', 'Sloth Hunter\'s Shot (PvP)', 'If this attack hits, target foe takes +5..20 damage. If that foe is not using a skill, Sloth Hunter\'s Shot does an additional +5..15 damage.', 'Deals +5...20 damage. Deals +5...15 more damage if target foe is not using a skill.'),
(2070, 'Aura Slicer', 'If this attack hits, you inflict Bleeding for 5..15 seconds. If you are enchanted, you also inflict Cracked Armor for 1..10 second[s].', '(5...15 seconds.)Inflicts <sub>sic</sub> Bleeding condition. Also inflicts Cracked Armor (1...10 seconds) if you are enchanted.', '', '', ''),
(2071, 'Zealous Sweep', 'If this attack hits, you deal +5..20 damage. You gain 3 Energy and 1 adrenaline for each foe you hit.', 'Deals +5...20 damage. You gain 3 Energy and 1 adrenaline for each foe you hit.', '', '', ''),
(2072, 'Pure Was Li Ming', 'Hold Li Ming\'s ashes for 5..20 seconds. While you hold her ashes, conditions on you expire 10..50% faster. When you drop her ashes, all allies within earshot lose 1..4 condition[s].', '(5...20 seconds.) Conditions on you expire 10...50% faster. Drop effect: removes 1...4 conditions from allies in earshot.', '', '', ''),
(2073, 'Weapon of Aggression', 'For 5..15 seconds, you attack 25% faster.', '(5...15 seconds.) You attack 25% faster.', '', '', ''),
(2074, 'Chest Thumper', 'If this attack hits a foe with Cracked Armor, that foe suffers a Deep Wound for 5..20 seconds.', 'Inflicts Deep Wound condition (5...20 seconds) if target foe has Cracked Armor.', '', '', ''),
(2075, 'Hasty Refrain', 'For 3..11 seconds, target ally moves 25% faster. This echo is reapplied every time a chant or shout ends on that ally.', '(3...11 seconds.) Target ally moves 25% faster. Renewal: every time a chant or shout ends on this ally.', '', '', ''),
(2091, 'Shadow Sanctuary (Kurzick)', 'You are Blinded for 5 seconds. For 10 seconds, you gain +7..10 Health regeneration and +40 armor.', '(10 seconds.) You gain +7...10 Health regeneration and +40 armor. <i>You are Blind (5 seconds).</i>', '', '', ''),
(2092, 'Ether Nightmare (Kurzick)', 'Target foe loses 5..8 Energy. For each point of Energy lost in this way, that foe and all foes in the area suffer -1 Health degeneration for 10 seconds.', 'Causes 5...8 Energy loss. Target and foes in the area have -1 Health degeneration for each point of Energy lost (10 seconds).', '', '', ''),
(2093, 'Signet of Corruption (Kurzick)', 'Target foe and all nearby foes take 20..30 damage. For each affected foe suffering from a condition or hex, you gain 2 Energy (maximum 12..20 Energy).', 'Deals 20...30 damage to target and nearby foes. You gain 2 Energy (maximum 12...20) for each of these foes with a condition or hex.', '', '', ''),
(2094, 'Elemental Lord (Kurzick)', 'For 40..60 seconds, your elemental attributes are boosted by 1. Each time you cast a spell, you gain 1 Energy for every 10 ranks of Energy Storage and are healed for 100..300% of the spell\'s Energy cost.', '(40...60 seconds.) Your elemental attributes are boosted by 1. Each time you cast a spell, you gain 1 energy for every 10 ranks of Energy Storage and are healed for 100...300% of the spell\'s Energy cost.', '', '', ''),
(2095, 'Selfless Spirit (Kurzick)', 'For 15..20 seconds, spells you cast that target another ally cost 3 less energy.', '(15...20 seconds.) Spells you cast that target another ally cost 3 less Energy.', '', '', ''),
(2096, 'Triple Shot (Kurzick)', 'Shoot 3 arrows simultaneously at target foe. These arrows deal 40..25% less damage.', 'Shoot 3 arrows simultaneously at target foe. <i>These arrows deal 40...25% less damage</i>', '', '', ''),
(2097, '"Save Yourselves!" (Kurzick)', 'For 4..6 seconds, all other party members gain +100 armor.', '(4...6 seconds.) All other party members gain +100 armor.', '', '', ''),
(2098, 'Aura of Holy Might (Kurzick)', 'For 45 seconds, you deal 20..25 holy damage to all adjacent foes whenever you lose a Dervish enchantment.', '(45 seconds.) Whenever you lose a Dervish enchantment, adjacent foes take 20...25 holy damage.', '', '', ''),
(2099, 'Spear of Fury (Kurzick)', 'Deals +30..40 damage. If this attack hits a foe suffering from a Condition, you gain 3..6 strikes of adrenaline.', 'Deals +30...40 damage. Gain 3...6 strikes of adrenaline if you hit a foe with a condition.', '', '', ''),
(2100, 'Summon Spirits (Kurzick)', 'All spirits you control Shadow Step to your location, and gain 60..100 Health.', 'Spirits you control Shadow Step to your location and gain 60...100 Health.', '', '', ''),
(2101, 'Critical Agility', 'For 4 seconds and 1 second for each rank of Critical Strikes, you attack 33% faster and gain 15..25 armor. This skill reapplies itself every time you land a critical hit.', '(4 seconds plus 1 second for each rank of Critical Strikes.) You attack 33% faster and gain +15...25 armor. Renewal: every time you land a critical hit.', '', '', ''),
(2102, 'Cry of Pain', 'Interrupt target foe\'s skill. If that foe was suffering from a Mesmer hex, that foe and all foes in the area take 25..50 damage and have 3..5 Health degeneration for 10 seconds.', 'Interrupts a skill. If target foe had a Mesmer hex, deals 25...50 damage to target and foes in the area and causes 3...5 Health degeneration (10 seconds).', '', '', ''),
(2103, 'Necrosis', 'If target foe is suffering from a condition or hex, that foe suffers 60..90 damage.', 'Deals 60...90 damage if target foe has a condition or hex.', '', '', ''),
(2104, 'Intensity', 'For 10 seconds, the next time you deal elemental damage with a spell to a target, you deal 60..70% of that damage to all other foes in the area.', '(10 seconds.) The next time you deal elemental damage with a spell, other targets in the area take 60...70% of that damage.', '', '', ''),
(2105, 'Seed of Life', 'For 2..5 seconds, whenever target other ally takes damage, all party members are healed for 2 Health for each rank in Divine Favor.', '(2...5) seconds. Heals party members for 2 for each rank in Divine Favor whenever target takes damage. Cannot self target.', '', '', ''),
(2107, 'Whirlwind Attack', 'Attack target and adjacent foes. Each attack that hits deals +13..20 damage.', 'Deals +13...20 damage to target and adjacent foes.', '', '', ''),
(2108, 'Never Rampage Alone', 'For 18..25 seconds, you and your pet attack 25% faster and have 1..3 Health regeneration.', '(18...25 seconds.) You and your pet attack 25% faster and have +1...3 Health regeneration.', '', '', ''),
(2109, 'Eternal Aura', 'You have +100 max Health. When this enchantment ends, all party members in the area are resurrected with 40..50% Health and 20..30% Energy.', 'You have +100 max Health. End effect: all party members in the area are resurrected with 40...50% Health and 20...30% Energy.', '', '', ''),
(2110, 'Vampirism', 'Create a level 4..14 spirit that dies after 75..150 seconds. Attacks by this spirit steal up to 10..20 Health, and you are healed for 10..20 Health.', 'Creates a level 4...14 spirit (lifespan 75...150 seconds). Its attacks steal 10...20 Health, and you are healed for 10...20.', '', '', ''),
(2112, '"There\'s Nothing to Fear!"', 'For 4 seconds and 1 second for every 2 ranks in Leadership, all party members within earshot take 20..35% less damage. Affected party members are healed for 35..60 Health when this shout ends.', '(4 seconds plus 1 second for every 2 ranks in Leadership.) Reduces damage by 20...35% for party members in earshot. End effect: heals for 35...60 Health.', '', '', ''),
(2116, 'Sneak Attack', 'If this attack hits, target foe is Blinded for 5..8 seconds. This attack counts as a lead attack.', 'Inflicts Blindness (5...8 seconds). <i>Counts as a lead attack.</i>', '', '', ''),
(2135, 'Trampling Ox', 'Must follow an off-hand attack. If it hits, you deal +5..20 damage. If you a  hit a Crippled foe, that foe is knocked down.', 'Deals +5...20 damage; causes knock-down if target foe is Crippled. <i>Must follow an off-hand attack.</i>', '', '', ''),
(2136, 'Smoke Powder Defense', 'For 8 seconds, the next time you are struck, you take half damage and all adjacent foes are Blinded for 2..6 seconds.', '(8 seconds.) The next time you are struck, you take half damage and inflict Blindness condition (2...6 seconds) on adjacent foes.', '', '', ''),
(2137, 'Confusing Images', 'For 2..10 seconds, target foe takes twice as long to activate non-attack skills.', '(2...10 seconds). Target foe takes twice as long to activate non-attack skills.', '', '', ''),
(2138, 'Hexer\'s Vigor', 'For 10 seconds, you have +1..8 Health regeneration. Hexer\'s Vigor ends if you cast a non-hex skill.', '(10 seconds.) You have +1...8 Health regeneration. <i>Ends if you use a non-hex skill.</i>', '', '', ''),
(2139, 'Masochism', 'For 10..40 seconds, you have +2 to your Death Magic and Soul Reaping attributes and sacrifice 5..3% of your maximum Health when you cast a spell.', '(10...40 seconds.) You have +2 Death Magic and Soul Reaping. Sacrifice 5...3% Health when you cast a spell.', 'Masochism (PvP)', 'For 30 seconds, you gain 1..3 Energy whenever you sacrifice Health.', '(30 seconds.) You gain 1...3 Energy whenever you sacrifice Health.'),
(2140, 'Piercing Trap', 'When Piercing Trap is triggered, all nearby foes are struck for 5..50 piercing damage. Any foes with Cracked Armor are struck for an additional 15..60 damage. Piercing Trap ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 5...50 piercing damage. Deals 15...60 more damage to any foes with Cracked Armor. <i>Easily interrupted.</i>', '', '', ''),
(2141, 'Companionship', 'If you have less Health than your pet, you are healed for 30..120 Health. If your pet has less Health than you, it is healed for 30..120 Health.', 'Heals you for 30...120 if you have less Health than your pet. Heals your pet for 30...120 if it has less Health than you.', '', '', ''),
(2142, 'Feral Aggression', 'For 5..20 seconds, your pet attacks 33% faster and deals +3..10 additional damage.', '(5...20 seconds.) Your pet attacks 33% faster and deals +3...10 damage.', '', '', ''),
(2143, 'Disrupting Shot', 'If this attack hits, target foe\'s action is interrupted. If that action was a Skill, you strike for +10..40 damage.', 'Interrupts an action. Interruption effect: +10...40 damage if you interrupt a skill.', '', '', ''),
(2144, 'Volley', 'All your Preparations are removed. Shoot arrows at target foe and up to 3 foes adjacent to your target. These arrows strike for +1..10 damage if they hit.', 'Deals +1...10 damage. Hits up to 3 foes adjacent to your target. <i>All your preparations are removed.</i>', '', '', ''),
(2145, 'Expert Focus', 'For 24 seconds, your bow attack skills cost 1..2 less Energy and deal 1..10 extra damage.', '(24 seconds.) Your bow attack skills cost 1...2 less Energy and do +1...10 damage.', '', '', ''),
(2146, 'Pious Fury', 'Remove one Dervish enchantment. For 1..7 second[s], you attack 25% faster. If an enchantment was removed, this stance lasts twice as long.', '(1...7 seconds.)You <sub>sic</sub> attack 25% faster and remove 1 of your Dervish enchantments. Removal effect: this stance lasts twice as long.', 'Pious Fury (PvP)', 'Remove one Dervish enchantment. For 1..5 second[s], you attack 25% faster. If an enchantment was removed, this stance lasts twice as long.', '(1...5 seconds.)You <sub>sic</sub> attack 25% faster and remove 1 of your Dervish enchantments. Removal effect: this stance lasts twice as long.'),
(2147, 'Crippling Victory', 'If this attack hits a foe, that foe is Crippled for 3..8 seconds. If you have more Health than target foe, all adjacent foes take 10..30 earth damage.', '(3...8 seconds) Cripples target foe. If your health is greater than target foe\'s, all adjacent foes take 10...30 earth damage.', '', '', ''),
(2148, 'Sundering Weapon', 'For 4..10 seconds, target ally\'s next 3 attacks have 10% armor penetration and cause Cracked Armor for 5..20 seconds.', '(4...10 seconds.) Target ally\'s next 3 attacks inflict Cracked Armor condition (5...20 seconds) and have 10% armor penetration.', '', '', ''),
(2149, 'Weapon of Renewal', 'For 4..10 seconds, the next time target ally hits with an attack skill, that ally gains 1..7 Energy.', '(4...10 seconds.) Target ally gains 1...7 Energy the next time this ally hits with an attack skill.', '', '', ''),
(2150, 'Maiming Spear', 'If your attack hits a Bleeding foe, that foe is Crippled for 5..20 seconds.', 'Inflicts Crippled condition (5...20 seconds) if target foe is Bleeding.', '', '', ''),
(2186, 'Signet of Deadly Corruption', 'Target foe takes 5..35 damage for each condition on that foe (maximum 130 damage).', 'Deals 5...35 damage (maximum 130) for each condition on target foe.', 'Signet of Deadly Corruption (PvP)', 'Must follow a dual attack. Target foe takes 5..35 damage for each condition on that foe (maximum 130 damage).', 'Deals 5...35 damage (maximum 130) for each condition on target foe. <i>Must follow a dual attack.</i>'),
(2187, 'Way of the Master', 'For 60 seconds, while holding a non-dagger weapon, you have an additional 3..33% chance to land a critical hit.', '(60 seconds.) While holding a non-dagger weapon, you have +3...33% chance to land a critical hit.', '', '', ''),
(2188, 'Defile Defenses', 'For 5..20 seconds, the next time target foe blocks, that foe takes 30..120 damage.', '(5...20 seconds.) Deals 30...120 damage the next time target foe blocks.', '', '', ''),
(2189, 'Angorodon\'s Gaze', 'Steal 10..50 Health from target foe. If you are suffering from a condition, you gain 3..12 Energy.', 'Steals 10...50 Health. You gain 3...12 Energy if you have a condition.', '', '', ''),
(2190, 'Magnetic Surge', 'Target foe takes 15..75 damage. If you are Overcast, Magnetic Surge enchants all allies in earshot for 1..5 second[s] to block the next attack against them.', 'Deals 15...75 damage. If you are Overcast, allies in earshot are enchanted for 1...5 <sub>sic</sub> and block the next attack against them.', '', '', ''),
(2191, 'Slippery Ground', 'If target or adjacent foes are Blind or moving, these foes are knocked down. There is a 50% chance of failure with Water Magic less than 5.', 'Causes knock-down if this foe is Blind or moving. Affects foes adjacent to your target. <i>50% failure chance unless Water Magic greater than 4.</i>', 'Slippery Ground (PvP)', 'If target foe is Blind or moving, that foe is knocked down. (50% failure chance with Water Magic 4 or less.)', 'Causes knock-down if this foe is Blind or moving. <i>50% failure chance unless Water Magic 5 or higher.</i>'),
(2192, 'Glowing Ice', 'Target foe is struck for 5..50 cold damage. If that foe is under the effects of a Water Magic hex, you gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage.', 'Deals 5...50 cold damage. You gain 5 Energy plus 1 Energy for every 2 ranks of Energy Storage if target foe is hexed with Water Magic.', '', '', ''),
(2193, 'Energy Blast', 'Target foe takes 1..2 damage for each point of Energy you have (maximum 130 damage).', 'Deals 1...2 damage (maximum 130) for each point of Energy you have.', '', '', ''),
(2194, 'Distracting Strike', 'If Distracting Strike hits, it deals no damage and interrupts target foe\'s action. If target foe has Cracked Armor, that skill is disabled for 20 seconds.', 'Interrupts an action. Interruption effect: Disables interrupted skill (20 seconds) if target foe has Cracked Armor. <i>Deals no damage.</i>', '', '', ''),
(2195, 'Symbolic Strike', 'If this attack hits, you deal +12 damage for each signet you have equipped (maximum 70 damage).', 'Deals +12 damage (maximum 70) for each signet you have equipped.', '', '', ''),
(2196, 'Soldier\'s Speed', 'For 3..18 seconds, you move 15% faster. You move an additional 15% faster while under the effects of a chant or shout.', '(3...18 seconds.) You move 15% faster and an additional 15% while affected by a chant or shout.', '', '', ''),
(2197, 'Body Blow', 'If this attack hits, target foe takes +10..40 damage. If target foe has Cracked Armor, that foe suffers from a Deep Wound for 0..15 second[s].', 'Deals +10...40 damage. Inflicts Deep Wound (0...15 seconds) if target foe has Cracked Armor.', '', '', ''),
(2198, 'Body Shot', 'If this attack hits, you deal +5..20 damage. If it hits a foe with Cracked Armor, you gain 5..10 Energy.', 'Deals +5...20 damage. You gain 5...10 Energy if target foe has Cracked Armor.', '', '', ''),
(2199, 'Poison Tip Signet', 'For 60 seconds, your next attack also inflicts Poison for 8..15 seconds.', '(60 seconds.) Inflicts Poisoned condition (8...15 seconds) with your next attack.', '', '', ''),
(2200, 'Signet of Mystic Speed', 'For 30 seconds, your next 1..3 self-targeting enchantment[s] cast instantly. Flash enchantments do not consume uses of this skill.', '(30 seconds.) Your next 1...3 self-targeted enchantments <sub>sic</sub> cast instantly. <i>Flash enchantments do not consume uses of this skill.</i>', '', '', ''),
(2201, 'Shield of Force', 'For 1..16 second[s], blocks the next 1 attack against you. If an attack is blocked, all adjacent attacking foes are knocked down and suffer from Weakness for 5..20 seconds.', 'Flash Enchantment Spell. (1...16 seconds.) Blocks the next 1 attack against you. Knocks down and inflicts Weakness (5...20 seconds) on all adjacent attacking foes.', '', '', ''),
(2202, 'Mending Grip', 'Target ally is healed for 15..75 Health. If that ally is under the effects of a weapon spell, that ally loses one condition.', 'Heals for 15...75. Removes one condition if target ally is under a Weapon <sub>sic</sub> spell.', '', '', ''),
(2203, 'Spiritleech Aura', 'For 5..20 seconds, all of your spirits within earshot deal 5..20 less damage and steal 5..20 Health when they attack.', '(5...20 seconds.) All of your spirits within earshot deal 5...20 less damage and steal 5...20 Health when they attack.', '', '', ''),
(2204, 'Rejuvenation', 'Create a level 1..20 spirit. This spirit heals all party members within earshot for 3..10 Health each second. This spirit loses 3..10 Health for each party member healed in this way. This spirit dies after 30..90 seconds.', 'Creates a level 1...20 spirit (30...90 second lifespan). Heals party members in earshot for 3...10 each second. <i>Healing cost: this spirit loses 3...10 Health.</i>', 'Rejuvenation (PvP)', 'Create a level 1..16 spirit. This spirit heals all party members within earshot for 3..10 Health each second. This spirit loses 3..10 Health for each party member healed in this way. This spirit dies after 30..90 seconds.', 'Creates a level 1...16 spirit (30...90 second lifespan). Heals party members in earshot for 3...10 each second. <i>Healing cost: this spirit loses 3...10 Health.</i>'),
(2205, 'Agony', 'Create a level 1..12 spirit. This spirit causes 3..10 Health loss each second to foes within earshot. This spirit loses 3..10 Health for each foe hurt in this way. This spirit dies after 30..90 seconds.', 'Creates a level 1...12 spirit (30...90 second lifespan). Causes 3...10 Health loss each second to foes in earshot. <i>This spirit loses 3...10 Health for each foe that loses Health.</i>', 'Agony (PvP)', 'Create a level 1..8 spirit. This spirit causes 3..10 Health loss each second to foes within earshot. This spirit loses 3..10 Health for each foe hurt in this way. This spirit dies after 30..90 seconds.', 'Creates a level 1...8 spirit (30...90 second lifespan). Causes 3...10 Health loss each second to foes in earshot. <i>This spirit loses 3...10 Health for each foe that loses Health.</i>'),
(2206, 'Ghostly Weapon', 'For 5..20 seconds, target other ally\'s next attack cannot be blocked.', '(5...20 seconds.) Target ally\'s next attack is unblockable. <i>Cannot self-target.</i>', '', '', ''),
(2207, 'Inspirational Speech', 'You lose all adrenaline and target other ally gains 1..4 strikes of adrenaline.', 'Target ally gains 1...4 strikes of adrenaline. <i>Cannot self-target. You lose all adrenaline.</i>', '', '', ''),
(2208, 'Burning Shield', 'For 3..9 seconds, while wielding a shield, the next attack skill used against you is blocked. If it was a melee attack, your attacker is set on fire for 1..6 seconds.', '(3...9 seconds.) Blocks the next attack skill against you. Inflicts Burning condition (1...6 seconds) if it was a melee attack. <i>No effect unless you are wielding a shield.</i>', '', '', ''),
(2209, 'Holy Spear', 'If this attack hits, you deal +5..20 damage. If it hits a summoned creature, all nearby foes take 15..90 holy damage, and are set on fire for 3 seconds.', 'Deals +5...20 damage. Deals 15...90 holy damage and inflicts Burning condition (3 seconds) to nearby foes if you hit a summoned creature.', '', '', ''),
(2210, 'Spear Swipe', 'If this attack hits, you deal +5..20 damage and target foe is Dazed for 4..10 seconds. This attack has melee range.', 'Spear Melee Attack. Deals +5...20 damage and inflicts Dazed condition (4...10 seconds). This attack has melee range.', '', '', ''),
(2211, 'Alkar\'s Alchemical Acid', 'Throw Alkar\'s Alchemical Acid at target foe. Target and adjacent foes are struck for 40..50 damage. Destroyers take an additional 45..70 damage and they have Cracked Armor for 14..20 seconds.', 'Projectile: deals 40...50 damage to target and adjacent foes. Deals 45...70 more damage and inflicts Cracked Armor condition (14...20 seconds) to Destroyers.', '', '', ''),
(2212, 'Light of Deldrimor', 'All foes in the area are struck for 55..80 holy damage. The location of hidden objects are briefly indicated on your Compass. Any hidden objects in the area are revealed.', 'Deals 55...80 holy damage to foes in the area. Pings hidden objects within the area on the compass.', '', '', ''),
(2213, 'Ear Bite', 'Target touched foe takes 50..70 piercing damage and begins Bleeding for 15..25 seconds.', 'Touch Skill. Deals 50...70 piercing damage and inflicts Bleeding condition (15...25 seconds).', '', '', ''),
(2214, 'Low Blow', 'Strike target foe for 45..70 damage. If target foe is knocked down, that foe takes an additional 30..50 damage and suffers from Cracked Armor for 14..20 seconds.', 'Touch Skill. Deals 45...70 damage. Inflicts 30...50 damage and Cracked Armor (14...20 seconds) if target foe is knocked down.', '', '', ''),
(2215, 'Brawling Headbutt', 'You headbutt target touched foe dealing 45..70 damage and knocking down that foe if it hits.', 'Touch Skill. Deals 45...70 damage; causes knock-down.', '', '', ''),
(2216, '"Don\'t Trip!"', 'For 3..5 seconds, party members within earshot cannot be knocked down.', '(3...5 seconds.) Prevents knock-down; affects party members within earshot.', '', '', ''),
(2217, '"By Ural\'s Hammer!"', 'For 30 seconds, all dead party members in earshot are resurrected with full Health and Energy. Resurrected party members deal 25..33% more damage. When this effect ends, affected party members die. This skill does not incur a death penalty.', '(30 seconds.) Resurrect all dead party members in earshot with full Health and Energy. Affected party members deal 25...33% more damage. Affected party members die when it ends, but do not receive a death penalty.', '', '', ''),
(2218, 'Drunken Master', 'For 72..90 seconds, your movement and attack speeds are increased by 10..15%. If you are drunk while activating this skill, your movement and attack speeds are increased by 25..33% instead.', '(72...90 seconds.) You move and attack 10...15% faster if you are not drunk. You move and attack 25...33% faster if you are drunk.', '', '', ''),
(2219, 'Great Dwarf Weapon', 'For 20 seconds, target other ally\'s weapon strikes for +15..20 damage and has a 28..40% chance to cause knock down.', '(20 seconds.) +15...20 weapon damage and 28...40% chance to cause knock-down with attacks. <i>Cannot self-target.</i>', '', '', ''),
(2220, 'Great Dwarf Armor', 'For 22..40 seconds, target ally has +24 armor, +60 maximum Health, and an additional +24 armor against Destroyers.', '(22...40 seconds.) +24 armor and +60 maximum Health. Additional +24 armor against Destroyers.', '', '', ''),
(2221, 'Breath of the Great Dwarf', 'All party members are relieved of Burning and are healed for 50..60 Health.', 'Removes burning and heals for 50...60 Health. Affects party members.', '', '', ''),
(2222, 'Snow Storm', 'Create a Snow Storm at target foe\'s location. For 5 seconds, foes adjacent to that location are struck for 30..40 cold damage each second.', 'Deals 30...40 cold damage each second (5 seconds). Hits foes adjacent to target foe\'s initial location.', '', '', ''),
(2223, 'Black Powder Mine', 'When Black Powder Mine is triggered, all nearby foes are struck for 20..30 damage, become Blinded, and begin Bleeding for 7..10 seconds. Black Powder Mine ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 20...30 damage. Inflicts Blindness and Bleeding conditions (7...10 seconds). <i>Easily interrupted.</i>', '', '', ''),
(2224, 'Summon Mursaat', 'Summon a level 14..20 Mursaat that lives for 40..60 seconds and has Enervating Charge. Only 1 Asura Summon can be active at a time.', 'Summon a level 14...20 Mursaat (40...60 lifespan) that has Enervating Charge. Only 1 Asura Summon can be active a time.', '', '', ''),
(2225, 'Summon Ruby Djinn', 'Summon a level 14..20 Ruby Djinn that lives for 40..60 seconds and has Immolate. Only 1 Asura Summon can be active at a time.', 'Summon a level 14...20 Ruby Djinn (40...60 lifespan) that has Immolate. Only 1 Asura Summon can be active a time.', '', '', ''),
(2226, 'Summon Ice Imp', 'Summon a level 14..20 Ice Imp that lives for 40..60 seconds and has Ice Spikes. Only 1 Asura Summon can be active a time.', 'Summon a level 14...20 Ice Imp (40...60 lifespan) that has Ice Spikes. Only 1 Asura Summon can be active a time.', '', '', ''),
(2227, 'Summon Naga Shaman', 'Summon a level 14..20 Naga Shaman that lives for 40..60 seconds and has Stoning. Only 1 Asura Summon can be active a time.', 'Summon a level 14...20 Naga Shaman (40...60 lifespan) that has Stoning. Only 1 Asura Summon can be active a time.', '', '', ''),
(2228, 'Deft Strike', 'If this attack hits, target foe takes 18..30 damage. If that foe has Cracked Armor, it begins Bleeding for 18..30 seconds.', 'Deals 18...30 damage. Inflicts Bleeding condition (18...30 seconds) if target foe has Cracked Armor.', '', '', ''),
(2229, 'Signet of Infection', 'If target foe is Bleeding, that foe is Diseased for 13..20 seconds.', 'Inflicts Diseased condition 13...20 seconds if target foe is Bleeding.', '', '', ''),
(2230, 'Tryptophan Signet', 'For 14..20 seconds, target foe and all adjacent foes move and attack 23..40% slower.', '(14...20 seconds.) Target and adjacent foes move and attack 23...40% slower.', '', '', ''),
(2231, 'Ebon Battle Standard of Courage', 'You plant an Ebon Battle Standard of Courage at your current location. For 14..20 seconds, non-spirit allies in this area gain +24 armor and an additional +24 armor against Charr.', '(14...20 seconds.) Allies in this ward have +24 armor and +24 more armor against Charr. <i>Spirits are unaffected.</i>', '', '', ''),
(2232, 'Ebon Battle Standard of Wisdom', 'You plant an Ebon Battle Standard of Wisdom at your current location. For 14..20 seconds, non-spirit allies in this area have a 44..60% chance to halve skill recharge of spells they cast.', '(14...20 seconds.) Spells that allies in this ward cast have a 44...60% chance to recharge 50% faster. <i>Spirits are unaffected.</i>', '', '', ''),
(2233, 'Ebon Battle Standard of Honor', 'You plant an Ebon Battle Standard of Honor at your current location. For 14..20 seconds, non-spirit allies in this area strike for +8..15 damage and an additional +7..10 vs. Charr.', '(14...20 seconds.) Allies in this ward deal +8...15 damage and +7...10 more damage against Charr. <i>Spirits are unaffected.</i>', '', '', ''),
(2234, 'Ebon Vanguard Sniper Support', 'Target foe is struck for 54..90 piercing damage and begins Bleeding for 5..25 seconds. This attack has a 10% chance of doing an additional +540..900 piercing damage. If this attack hits a Charr it has a 25% chance of doing an additional +540..900 piercing damage.', 'Deals 54...90 piercing damage and inflicts Bleeding condition (5...25 seconds). 10% chance of +540...900 piercing damage. 25% chance of +540...900 piercing damage if target foe is a Charr.', '', '', ''),
(2235, 'Ebon Vanguard Assassin Support', 'Summon a level 14..20 Ebon Vanguard Assassin that has Iron Palm, Fox Fangs, and Nine Tail Strike. This summoned Assassin Shadow Steps to target foe. If that foe is a Charr, the Assassin lives for 24..30 seconds. If target foe is not a Charr, the Assassin lives for 12..15 seconds.', 'Summon a level 14...20 assassin that has Iron Palm, Fox Fangs, and Nine Tail Strike; it Shadow Steps to this foe. If this foe is a Charr, the assassin lives for 24...30 seconds. If target foe is not a Charr, the Assassin lives for 12...15 seconds.', '', '', ''),
(2236, 'Well of Ruin', 'Exploit nearest corpse to create a Well of Ruin at its location. For 5..30 seconds, whenever a foe in the well takes physical damage, that foe has Cracked Armor for 5..20 seconds.', '(5...30 seconds.) Inflicts Cracked Armor condition (5...20 seconds) to any foe in the well that takes physical damage. <i>Exploits a fresh corpse.</i>', '', '', ''),
(2237, 'Atrophy', 'For 3..7 seconds, target foe\'s primary attribute is reduced to 0.', '(3...7 seconds.) Reduces this foe\'s primary attribute to 0.', '', '', ''),
(2238, 'Spear of Redemption', 'If this attack hits, you deal +5..20 damage. If it fails to hit, you lose one condition.', 'Deals +5...20 damage. If it fails to hit, you lose one condition.', '', '', ''),
(2353, '"Finish Him!"', 'If target foe has less than 50% Health, that foe takes 44..80 damage and suffers from Cracked Armor and a Deep Wound for 12..20 seconds.', 'Deals 44...80 damage and inflicts Cracked Armor and Deep Wound conditions (12...20 seconds). <i>No effect unless target foe has less than 50% Health.</i>', '', '', ''),
(2354, '"Dodge This!"', 'For 16..20 seconds, your next attack cannot be blocked and does +14..20 damage.', '(16...20 seconds.) Your next attack is unblockable and deals +14...20 damage.', '', '', ''),
(2355, '"I Am the Strongest!"', 'Your next 5..8 attacks do an additional +14..20 damage.', 'Your next 5...8 attacks deal +14...20 damage.', '', '', ''),
(2356, '"I Am Unstoppable!"', 'For 16..20 seconds, you have +24 armor and cannot be knocked down or Crippled.', '(16...20 seconds.) You have +24 armor and cannot be knocked-down or Crippled.', '', '', ''),
(2357, 'A Touch of Guile', 'Target touched foe takes 44..80 damage. If target foe was knocked down, that foe cannot attack for 5..8 seconds.', 'Touch Hex Spell. Deals 44...80 damage. Target foe cannot attack (5...8 seconds) if it was knocked-down.', '', '', ''),
(2358, '"You Move Like a Dwarf!"', 'Target foe is knocked down and takes 44..80 damage. That foe is also Crippled for 8..15 seconds.', 'Deals 44...80 damage, causes knock-down, and inflicts Crippled condition (8...15 seconds).', '', '', ''),
(2359, '"You Are All Weaklings!"', 'Target foe and foes adjacent to target are Weakened for 8..12 seconds.', 'Inflicts Weakness condition (8...12 seconds). Also affects adjacent foes.', '', '', ''),
(2360, 'Feel No Pain', 'For 30 seconds you have +2..3 Health regeneration. If you are drunk when activating this skill, you also have +200..300 maximum Health.', '(30 seconds.) You have +2...3 Health regeneration. You have +200...300 maximum Health if you are drunk when activating this skill.', '', '', '');
INSERT INTO `gw1_skilldesc_en` (`id`, `pve_name`, `pve_desc`, `pve_desc_short`, `pvp_name`, `pvp_desc`, `pvp_desc_short`) VALUES
(2361, 'Club of a Thousand Bears', 'If this attack hits, it deals +6..9 damage for each adjacent foe (maximum 60 damage). If target foe is non-human it is knocked down.', 'Deals +6...9 damage for each adjacent foe (maximum 60 damage). Causes knock-down if target foe is non-human.', '', '', ''),
(2374, 'Ursan Blessing', 'You lose all effects and take on the aspect of the bear. For 60 seconds, you have 100 armor and 750..800 Health. All your attributes are set to 0 and bear attacks replace your skills.', 'You lose all effects and take on the aspect of the bear (60 seconds). All your attributes are set to 0 and bear attacks replace your skills, and you have 100 armor and 750...800 Health.', '', '', ''),
(2379, 'Volfen Blessing', 'You lose all effects and take on the aspect of the wolf. For 60 seconds, you have 80 armor, 660..700 Health, and 2..4 Health regeneration. All your attributes are set to 0 and wolf attacks replace your skills.', 'You lose all effects and take on the aspect of the wolf (60 seconds). All your attributes are set to 0 and wolf attacks replace your skills, and you have 80 armor, 660...700 Health and 2...4 Health regeneration.', '', '', ''),
(2384, 'Raven Blessing', 'You lose all effects and take on the aspect of the raven. For 60 seconds, you have 80 armor, 660..700 Health, and a 20..30% chance to block, All your attributes are set to 0 and raven attacks replace your skills.', 'You lose all effects and take on the aspect of the Raven (60 seconds). All your attributes are set to 0 and raven attacks replace your skills, and you have 80 armor, 660...700 Health, and a 20...30% block chance.', '', '', ''),
(2411, 'Mindbender', 'For 10..16 seconds, you move 20..33% faster and your Spells take 20% less time to cast.', '(10...16 seconds.) You move 20...33% faster and cast Spells 20% faster.', '', '', ''),
(2412, 'Smooth Criminal', 'For 10..20 seconds, one random spell is disabled for target foe and Smooth Criminal is replaced by that spell. You gain 5..10 Energy.', '(10...20 seconds.) Disables one Spell. This skill becomes that Spell. You gain 5...10 Energy.', '', '', ''),
(2413, 'Technobabble', 'Target foe and all adjacent foes are struck for 30..40 damage. If target foe is not a boss, that foe and all adjacent foes are Dazed for 3..5 seconds.', 'Deals 30...40 damage to target and adjacent foes. Inflicts daze condition (3...5 seconds) on these foes if target was not a boss.', '', '', ''),
(2414, 'Radiation Field', 'For 5 seconds, foes in the area have -4..6 Health degeneration. When the portal ends, foes in the area are Diseased for 12..20 seconds.', '(5 seconds.) Causes -4...6 Health degeneration to foes in the area. End effect: inflicts Disease condition (12...20 seconds) to foes in the area.', '', '', ''),
(2415, 'Asuran Scan', 'For 9..12 seconds your attacks cannot miss target foe. Killing a foe hexed with Asuran Scan removes 5% Death Penalty.', '(9...12 seconds.) You cannot miss target foe. If you kill this foe, you lose 5% Death Penalty.', '', '', ''),
(2416, 'Air of Superiority', 'For 20..30 seconds you gain a random Asura benefit every time you earn experience from killing an enemy.', '(20...30 seconds). Gain a random Asura benefit every time you earn experience from killing an enemy.', '', '', ''),
(2417, 'Mental Block', 'For 5..11 seconds you have a 50% chance to block attacks. This Enchantment is reapplied every time an enemy strikes you.', '(5...11 seconds.) You have a 50% chance to block. Renewal: every time an enemy hits you.', '', '', ''),
(2418, 'Pain Inverter', 'For 6..10 seconds, every time target foe deals damage that foe takes 100..140% of the damage it causes (maximum 80 damage).', '(6...10 seconds.) Deals 100...140% of the attack damage (maximum 80) back to target foe every time it does damage.', '', '', ''),
(2420, 'Ebon Escape', 'Shadow Step to target other ally. You and that other ally are healed for 70..110 health.', 'Heals you and target ally for 70...110. Initial effect: Shadow Step to this ally. <i>Cannot self-target.</i>', '', '', ''),
(2421, 'Weakness Trap', 'When Weakness Trap is triggered, all nearby foes are Weakened for 10..20 seconds and take 24..50 lightning damage. All Charr are knocked down. Weakness Trap ends after 90 seconds. While activating this skill, you are easily interrupted.', '(90 seconds.) Affects nearby foes. Deals 24...50 lightning damage. Inflicts Weakness condition (10...20 seconds). Knocks-down Charr. <i>Easily interrupted.</i>', '', '', ''),
(2422, 'Winds', 'Create a level 4..10 Spirit. All foes within its range have 15% chance to miss with ranged attacks. This spirit dies after 54..90 seconds.', 'Creates a level 4...10 spirit (54...90 second lifespan.) Affects foes within range. 15% chance to miss with ranged attacks.', '', '', ''),
(2423, 'Dwarven Stability', 'For 24..30 seconds, your stances last 55..100% longer. If you activated this skill while drunk, you cannot be knocked down.', '(24...30 seconds.) Your stances last 55...100% longer. You cannot be knocked-down if you activated this skill while drunk . <sub>sic</sub>', '', '', '');
